INSERT INTO [t10490] VALUES (1658,'ضرار بن الأزور أسدي أسد بن خزيمة، واسم الأزور مالك بن أوس بن خزيمة بن سعد بن مالك بن ثعلبة بن ذودان بن أسد بن خزيمة بن مدركة بن إلياس بن مضر، وهو قاتل مالك بن نويرة أخي متمم بن نويرة، وقيل: قتل يوم مسيلمة، وقيل: بل قتل في خلافة عمر حين بعثه مع خالد','5100','1657','0');
INSERT INTO [t10490] VALUES (1659,'ضرار بن الخطاب ليس له حديث، له ذكر فيما حكاه عنه عمر بن الخطاب ذكره بعض المتأخرين، ولم يذكره أحد في الصحابة، ولا فيمن أسلم غيره','5106','1657','0');
