INSERT INTO [t10490] VALUES (1670,'الضحاك بن عرفجة أصيب أنفه يوم الكلاب ذكره بعض المتأخرين، وقال: قاله ابن عرادة، عن عبد الرحمن بن طرفة بن عرفجة، وقال: عبد الرحمن بن الضحاك بن عرفجة، وهو وهم، وصوابه: عرفجة بن أسعد','5130','1657','0');
