INSERT INTO [t10490] VALUES (1732,'عبد الله بن إسحاق الأعرج ذكره بعض المتأخرين، وزعم أنه جد حاجب بن أبان الأعرج، وأصيبت رجله مع رسول الله صلى الله عليه وسلم','5278','1725','0');
INSERT INTO [t10490] VALUES (1733,'عبد الله بن الأسود السدوسي أحد الوفد من بني سدوس حديثه عند أولاده، وقال قتادة فيما روى عنه مسلم بن إبراهيم، عن الصعق بن حزن قال: هاجر أربعة من ربيعة: بشير بن الخصاصية، وعبد الله بن الأسود، وفرات بن حيان، وعمرو بن تغلب','5280','1725','0');
INSERT INTO [t10490] VALUES (1734,'عبد الله بن أبي أمية المخزومي أخو أم سلمة، إسلامه عام الفتح، وقيل: يوم الطائف، واسم أبي أمية: حذيفة بن المغيرة، أمه: عاتكة بنت عبد المطلب، رمي يوم الطائف رمية فمات شهيدا','5282','1725','0');
INSERT INTO [t10490] VALUES (1735,'عبد الله بن أنس أخو فاطمة، تقدم حديثه في باب الألف','5284','1725','0');
INSERT INTO [t10490] VALUES (1736,'عبد الله بن الأسقع الليثي ذكره بعض المتأخرين، روى حديثه أبو شهاب عن المغيرة بن زياد، عن مكحول مرسلا','5285','1725','0');
INSERT INTO [t10490] VALUES (1737,'عبد الله ابن أم حرام بنت ملحان أبو أبي ابن امرأة عبادة بن الصامت، صلى القبلتين مع النبي صلى الله عليه وسلم وهو عبد الله بن أبي، هكذا قال البخاري , روى عنه: إبراهيم بن أبي عبلة، وقيل: هو عبد الله بن عمرو بن قيس بن زيد بن سواد بن مالك بن عمرو بن النجار','5286','1725','0');
INSERT INTO [t10490] VALUES (1738,'عبد الله بن أبي حبيبة الأشهلي الأنصاري واسم أبي حبيبة: الأدرع بن الأزعر بن زيد بن العطاف بن ضبيعة، شهد بيعة الرضوان، وأبو حبيبة شهد بدرا، والشاهد فيما قاله عبد الله بن أبي داود، حديثه عند محمد بن إسماعيل بن مجمع','5290','1725','0');
INSERT INTO [t10490] VALUES (1739,'عبد الله بن أبي أحمد بن جحش أتي به النبي صلى الله عليه وسلم لما ولد سماه عبد الله، له ولابنه معاوية رؤية، حديثه عند حسين بن أبي لبابة','5292','1725','0');
INSERT INTO [t10490] VALUES (1740,'عبد الله بن أبي أوفى الأسلمي من أصحاب الشجرة، غزا مع النبي صلى الله عليه وسلم ست غزوات، وأصابته يوم حنين ضربة في ذراعه، يكنى: أبا معاوية، كان يصبغ لحيته ورأسه بالحناء، وله ضفيرتان، كف بصره في آخر عمره، توفي سنة ست وثمانين، وقيل: سبع وثمانين','5294','1725','0');
INSERT INTO [t10490] VALUES (1741,'باب الباء من باب العين','5300','1723','0');
INSERT INTO [t10490] VALUES (1742,'عبد الله بن بسر أبو صفوان السلمي المازني يكنى: أبا بسر، وقيل: أبو صفوان، آخر من مات بالشام من الصحابة، وصلى القبلتين، وضع النبي صلى الله عليه وسلم يده على رأسه وبرك عليه ودعا له، صحب هو وأبوه وأمه وأخوه عطية، وأخته الصماء النبي صلى الله عليه وسلم','5301','1741','0');
INSERT INTO [t10490] VALUES (1743,'عبد الله بن بدر الجهني أبو بعجة يعد في المدنيين، حديثه عند ابنه بعجة، ومعاذ بن عبد الله بن خبيب','5309','1741','0');
INSERT INTO [t10490] VALUES (1744,'عبد الله بن أبي بكر الصديق رضي الله عنهما','5312','1741','0');
INSERT INTO [t10490] VALUES (1745,'ذكر بعض المتأخرين: أبا هند الداري، وقال: اسمه: عبد الله بن البر واسمه برير بن عبد الله نذكره في الكنى','5314','1741','0');
INSERT INTO [t10490] VALUES (1746,'عبد الله بن بدر غير منسوب ذكره سليمان بن أحمد في المعجم، والحضرمي في المفاريد','5315','1741','0');
INSERT INTO [t10490] VALUES (1747,'عبد الله بن برير بن ربيعة روى عنه: أبو عبد الرحمن الجبلي، عداده في المصريين، وأحال به على أبي سعيد بن عبد الأعلى','5317','1741','0');
INSERT INTO [t10490] VALUES (1748,'وعبد الله بن بديل بن ورقاء','5318','1741','0');
INSERT INTO [t10490] VALUES (1749,'وعبد الله بن بغيل الكناني لا يعرف له صحبة , وذكر عبد الله بن بديل في المسح على الخفين، وهو بديل ليس عبد الله','5319','1741','0');
INSERT INTO [t10490] VALUES (1750,'باب التاء مهمل','5320','1723','0');
INSERT INTO [t10490] VALUES (1751,'باب الثاء من باب العين','5321','1723','0');
INSERT INTO [t10490] VALUES (1752,'عبد الله بن ثابت الظفري الأنصاري أبو الربيع ذكره في حديث جابر بن عتيك','5322','1751','0');
INSERT INTO [t10490] VALUES (1753,'عبد الله بن ثابت الأنصاري حديثه عند الشعبي، يعد في الكوفيين','5324','1751','0');
INSERT INTO [t10490] VALUES (1754,'عبد الله بن ثابت الأنصاري يكنى: أبا أسيد، ذكره بعض المتأخرين حاكيا عن يحيى بن صاعد، وهو عندي المتقدم','5326','1751','0');
INSERT INTO [t10490] VALUES (1755,'عبد الله بن ثعلبة بن خزمة الأنصاري شهد بدرا من الخزرج','5328','1751','0');
INSERT INTO [t10490] VALUES (1756,'عبد الله بن ثعلبة بن صعير وقيل: ابن أبي صعير بن عمر بن زيد بن سنان بن المهتجر بن سلامان بن عدي بن صغير بن الخزاز بن كاهل بن عدي بن الشاعر، يكنى: أبا محمد حليف بني زهرة من بني عذرة مات سنة تسع وثمانين، روى عنه: ابنه ومحمد بن مسلم الزهري، رأى النبي','5330','1751','0');
INSERT INTO [t10490] VALUES (1757,'عبد الله بن ثوب أبو مسلم الخولاني، قيل: إن إسلامه كان في عهد رسول الله صلى الله عليه وسلم، ولم يره فيما ذكره بعض المتأخرين، توفي في زمن معاوية رضي الله عنه، روى عنه: محمد بن زياد الألهاني، وشرحبيل بن مسلم، ومكحول، كان مولده يوم حنين، وهو الصحيح','5335','1751','0');
INSERT INTO [t10490] VALUES (1758,'باب الجيم من باب العين','5337','1723','0');
INSERT INTO [t10490] VALUES (1759,'عبد الله بن جعفر بن أبي طالب أبو جعفر الهاشمي، أمه: أسماء بنت عميس بن النعمان بن كعب بن مالك بن قحافة بن خثعم، مولده بأرض الحبشة، يكنى: أبا جعفر بايع هو وعبد الله بن الزبير رسول الله صلى الله عليه وسلم، وهو ابن سبع سنين، مختلف في وفاته، توفي','5338','1758','0');
INSERT INTO [t10490] VALUES (1760,'عبد الله بن جحش أبو محمد الأسدي شهد بدرا، واستشهد بأحد، من مهاجرة الحبشة، وهو من أسد بن خزيمة حليف بني أمية، أخو أبي أحمد، سأل ربه عز وجل الشهادة فاستجيبت دعوته، وهو أول أمير أمره رسول الله صلى الله عليه وسلم، فغنم من المشركين غنيمة، فبعثه إلى','5347','1758','0');
INSERT INTO [t10490] VALUES (1761,'عبد الله بن جبير الأنصاري الأوسي عقبي بدري أحدي، أخو خوات بن جبير، أمره النبي صلى الله عليه وسلم على الرماة يوم أحد، فاستشهد به، حديثه عند البراء بن عازب، وقيل: عبد ربه، لا عقب له','5352','1758','0');
INSERT INTO [t10490] VALUES (1762,'عبد الله بن جبير الخزاعي أبو عبد الرحمن مختلف في صحبته، حديثه عند سماك بن حرب','5356','1758','0');
INSERT INTO [t10490] VALUES (1763,'عبد الله بن جابر العبدي أحد الوفد من عبد القيس كان مع أبيه حين وفد على النبي صلى الله عليه وسلم، روى حديثه الحارث بن مرة','5358','1758','0');
INSERT INTO [t10490] VALUES (1764,'عبد الله بن جابر البياضي وقيل: إنه أنصاري غير العبدي المتقدم، ذكره الإمام أحمد بن حنبل في الصحابة، حديثه عند ابن عقيل','5360','1758','0');
INSERT INTO [t10490] VALUES (1765,'عبد الله بن جهيم الأنصاري يكنى: أبا جهم، وهو ابن الحارث بن الصمة بن زيد مناة بن حبيب، وقيل: ابن الصمة بن عمرو بن الجموح، حديثه عند بسر بن سعيد، وعمير مولى ابن عباس، وقال أحمد بن رشدين: أبو جهيم بن الحارث بن الصمة بن عمرو بن الجموح بن حذام بن غنم','5363','1758','0');
INSERT INTO [t10490] VALUES (1766,'عبد الله بن جراد الخفاجي وخفاجة من بني عقيل، عداده في أهل الطائف حديثه عند ابن أخيه يعلى بن الأشدق','5366','1758','0');
INSERT INTO [t10490] VALUES (1767,'عبد الله بن الجد بن قيس بن صخر ابن خنساء الأنصاري من الخزرج من بني عبيد بن عدي شهد بدرا','5370','1758','0');
INSERT INTO [t10490] VALUES (1768,'عبد الله بن جزء بن أنس بن عامر بن علي السلمي، يعد في البصريين، ذكره بعض المتأخرين وزعم أنه جد نائل بن مطرف','5372','1758','0');
INSERT INTO [t10490] VALUES (1769,'عبد الله بن أبي الجدعاء عداده في البصريين , حديثه عند عبد الله بن شقيق، وقال بعضهم: ابن أبي الحمساء قال أبو حاتم الرازي: هم ثلاثة لا يروي عنهم إلا عبد الله بن شقيق','5374','1758','0');
INSERT INTO [t10490] VALUES (1770,'باب الحاء من باب العين','5377','1723','0');
INSERT INTO [t10490] VALUES (1771,'عبد الله بن حذافة السهمي وهو عبد الله بن حذافة بن قيس بن عدي بن سعيد بن سهم بن عمرو بن هصيص بن كعب بن لؤي، لم يذكره عروة ولا ابن شهاب ولا ابن إسحاق في البدريين، وقال ابن إسحاق: هو من مهاجرة الحبشة ولم يتابع عليه، وروي في بعض الأخبار أنه من أهل بدر،','5378','1770','0');
INSERT INTO [t10490] VALUES (1772,'عبد الله بن الحارث بن نوفل بن الحارث بن عبد المطلب، له ولأبيه صحبة، وقيل: إن له إدراكا، ولأبيه صحبة , حديثه عند أبي سلمة، وابنه عبد الله','5386','1770','0');
INSERT INTO [t10490] VALUES (1773,'عبد الله بن الحارث بن أبي ضرار','5388','1770','0');
INSERT INTO [t10490] VALUES (1774,'عبد الله بن الحارث بن جزء الزبيدي وزبيد من مذحج، ومذحج من كهلان حليف بني سهم بن عمرو، سكن مصر، وبها مات، وقيل: عبد الله بن الحارث بن جزء بن معدي كرب بن عمرو بن عصر بن عمرو بن عويج بن عمرو بن زبيد توفي سنة ست وثمانين، وذكر بعض المتأخرين أنه شهد بدرا','5390','1770','0');
INSERT INTO [t10490] VALUES (1775,'عبد الله بن الحارث بن قيس بن عدي السهمي أخو السائب قتل يوم الطائف شهيدا من مهاجرة الحبشة','5396','1770','0');
INSERT INTO [t10490] VALUES (1776,'عبد الله بن الحارث أبو رفاعة العدوي، ذكره بعض المتأخرين، وأبو رفاعة اسمه: تميم بن أسيد، وقيل: ابن إياس، وقيل: عبد الله بن عبد الحارث','5398','1770','0');
INSERT INTO [t10490] VALUES (1777,'عبد الله بن الحارث بن عويمر الأنصاري وقيل: المزني، ذكره بعض المتأخرين، وقال: روى عنه: محمد بن نافع بن عجير، وأخرج عنه هذا الحديث من غير رواية، ذكره عن عبد الله بن سعد الزهري، عن عمه يعقوب، عن أبيه','5400','1770','0');
INSERT INTO [t10490] VALUES (1778,'عبد الله بن حوالة الأزدي يكنى: أبا حوالة سكن الأردن، حديثه عند أبي إدريس الخولاني وجبير بن نفير، وربيعة بن لقيط، وعبد الله بن زغب، وسلمان بن شمير، وبسر بن عبيد الله، وعبد الله بن شقيق','5402','1770','0');
INSERT INTO [t10490] VALUES (1779,'عبد الله بن حبشي الخثعمي روى عنه: سعيد بن محمد بن جبير بن مطعم، وعبيد بن عمير','5405','1770','0');
INSERT INTO [t10490] VALUES (1780,'عبد الله بن حبيب مجهول حديثه عند عبيد بن عمير ذكره بعض المتأخرين، ولا صحبة له','5408','1770','0');
INSERT INTO [t10490] VALUES (1781,'عبد الله بن حارثة بن النعمان يعد في المدنيين، حديثه عند أولاده','5410','1770','0');
INSERT INTO [t10490] VALUES (1782,'عبد الله بن أبي حدرد الأسلمي واسم أبي حدرد: سلامة كلم النبي صلى الله عليه وسلم كعب بن مالك فيه حين تقاضاه أن يضع عنه شطر دينه، يكنى: أبا محمد بعثه النبي صلى الله عليه وسلم في سرية أضم إلى عامر بن الأضبط، توفي سنة إحدى وسبعين، وهو ابن إحدى وثمانين','5412','1770','0');
INSERT INTO [t10490] VALUES (1783,'عبد الله بن أبي الحمساء عداده في البصريين روى حديثه شقيق بن سلمة أبو عبد الله، وقيل: أبو الحسماء، وقيل: ابن أبي الجدعاء','5415','1770','0');
INSERT INTO [t10490] VALUES (1784,'عبد الله بن حرملة مجهول، وذكر بعض المتأخرين أنه المدلجي روى عنه: أبو بكر بن عبد الرحمن بن الحارث بن هشام','5417','1770','0');
INSERT INTO [t10490] VALUES (1785,'عبد الله يلقب بحمار له صحبة كان صاحب ضحك ومزاح، له ذكر في حديث عمر بن الخطاب رضي الله عنه','5419','1770','0');
INSERT INTO [t10490] VALUES (1786,'عبد الله بن حزابة وعبد الله بن حكل ذكرهما بعض المتأخرين، وقال: ذكرا في الصحابة، وهما من تابعي أهل الشام وروى عنهما: خالد بن معدان، ولم يزد عليه','5421','1770','0');
INSERT INTO [t10490] VALUES (1787,'باب الخاء من باب العين','5422','1723','0');
INSERT INTO [t10490] VALUES (1788,'عبد الله بن خبيب الجهني الأنصاري حديثه عند ابنه معاذ، يعد في المدنيين','5423','1787','0');
INSERT INTO [t10490] VALUES (1789,'عبد الله بن خمير من بني عبيد بن عدي بن غنم بن كعب بن سلمة، شهد بدرا أخو خارجة بن خمير','5427','1787','0');
INSERT INTO [t10490] VALUES (1790,'عبد الله بن خباب بن الأرت أدرك النبي صلى الله عليه وسلم، مختلف في صحبته، له رؤية ولأبيه صحبة، قتلته الخوارج، روى عن أبيه وعن أبي بن كعب','5429','1787','0');
INSERT INTO [t10490] VALUES (1791,'عبد الله بن خالد بن أسيد المخزومي في صحبته ورؤيته نظر، وهو من ولد أبي العيص بن أمية، وخالد هو أخو عتاب بن أسيد، روى عنه ابنه عبد العزيز','5431','1787','0');
INSERT INTO [t10490] VALUES (1792,'عبد الله بن خازم وهو ابن أسماء بن الصلت ابن أخي عمرو بن أسماء بن الصلت، ولي خراسان من قبل عبد الملك بن مروان فبعث برأس ابن الزبير إليه، ولا صحبة له، وفتح على يده سرخس، ذكر بعض المتأخرين أنه أدرك النبي صلى الله عليه وسلم، ولا يعتد بقوله','5434','1787','0');
INSERT INTO [t10490] VALUES (1793,'عبد الله بن خريت البكري من بني بكر بن معاوية أدرك الجاهلية يعد في الحجازيين، لا يسند شيئا، ولا تصح له صحبة، ولا رؤية، روى عنه عبد الله بن عبيد بن عمير، ذكره بعض المتأخرين','5435','1787','0');
INSERT INTO [t10490] VALUES (1794,'باب الدال من باب العين','5437','1723','0');
INSERT INTO [t10490] VALUES (1795,'عبد الله بن دارة مولى عثمان بن عفان ذكره بعض المتأخرين، وزعم أنه كان في وقت النبي صلى الله عليه وسلم، ولم يذكره أحد في الصحابة، روايته عن حمران، عن عثمان بن عفان، عن النبي صلى الله عليه وسلم، وقيل: عن عثمان، وروى عن كعب الأحبار، حديثه عند محمد','5438','1794','0');
INSERT INTO [t10490] VALUES (1796,'باب الذال من باب العين','5441','1723','0');
INSERT INTO [t10490] VALUES (1797,'عبد الله ذو البجادين المزني له ذكر في حديث عبد الله بن مسعود، وعمرو بن عوف، نزل النبي صلى الله عليه وسلم في قبره، ودفنه وأثنى عليه وقال: " كان أواها تلاء للقرآن " وأمر بني سلمة أن يزوجوه، فزوجوه، وسمي ذا البجادين، لأن عمه نزع منه كل ما كان له،','5442','1796','0');
INSERT INTO [t10490] VALUES (1798,'باب الراء من باب العين','5445','1723','0');
INSERT INTO [t10490] VALUES (1799,'عبد الله بن رواحة بن امرئ القيس بن ثعلبة بن عبد بن عمرو بن امرئ القيس بن ثعلبة بن كعب بن الخزرج، عقبي بدري، أمره النبي صلى الله عليه وسلم بعد جعفر سنة ثمان، استشهد يوم مؤتة أحد النقباء، له في الإسلام المناقب المذكورة، والأيام المشهورة كان حارس النبي','5446','1798','0');
INSERT INTO [t10490] VALUES (1800,'عبد الله بن ربيعة السلمي روى عنه ابن أبي ليلى، كان من أعمام منصور بن المعتمر، قيل: إنه خال عمرو بن عتبة بن فرقد','5456','1798','0');
INSERT INTO [t10490] VALUES (1801,'عبد الله بن ربيعة بن مسروح بن معاوية وقيل: ابن ربيعة بن عامر بن صعصعة بن بكر حجازي حديثه عند ابنه فضالة، ذكره بعض المتأخرين','5458','1798','0');
INSERT INTO [t10490] VALUES (1802,'عبد الله بن ربيعة النميري أبو يزيد ذكره الحضرمي في الوحدان','5460','1798','0');
INSERT INTO [t10490] VALUES (1803,'عبد الله بن ربيعة بن الحارثة بن المطلب القرشي روى عنه عروة بن الزبير والفضل بن الحسن الضمري','5462','1798','0');
INSERT INTO [t10490] VALUES (1804,'عبد الله بن رفاعة بن رافع الزرقي ذكره الحسن بن سفيان في الوحدان، وتابعه بعض المتأخرين','5464','1798','0');
INSERT INTO [t10490] VALUES (1805,'عبد الله بن الربيع بن قيس من بني الأبجر بن عوف، وهم بنو خدرة بن الحارث بن الخزرج، شهد بدرا، قاله عروة، وابن إسحاق','5466','1798','0');
INSERT INTO [t10490] VALUES (1806,'عبد الله بن أبي ربيعة الثقفي أبو سفيان بن عبد الله حديثه عند ابن سفيان بن عبد الله','5468','1798','0');
INSERT INTO [t10490] VALUES (1807,'عبد الله بن أبي ربيعة المخزومي واسم أبي ربيعة: عمرو بن المغيرة بن عبد الله بن عمر بن مخزوم وأمه ثقفية','5470','1798','0');
INSERT INTO [t10490] VALUES (1808,'عبد الله بن رزق المخزومي ذكره بعض المتأخرين، وقال: ذكر في الصحابة ولا نعرف له صحبة ولا رؤية','5473','1798','0');
INSERT INTO [t10490] VALUES (1809,'باب الزاي من باب العين','5475','1723','0');
INSERT INTO [t10490] VALUES (1810,'عبد الله بن الزبير بن العوام أبو بكر، وقيل: أبو خبيب أبوه حواري رسول الله صلى الله عليه وسلم، وأمه أسماء بنت أبي بكر الصديق، وخالته عائشة زوجة رسول الله صلى الله عليه وسلم، وجده أبو بكر الصديق، وعمته خديجة زوجة رسول الله صلى الله عليه وسلم، وجدته','5476','1809','0');
INSERT INTO [t10490] VALUES (1811,'ما أسند عبد الله بن الزبير بن العوام رضي الله عنهما','5492','1809','0');
INSERT INTO [t10490] VALUES (1812,'عبد الله بن زمعة بن الأسود بن المطلب بن أسد بن عبد العزى بن قصي بن كلاب بن مرة بن كعب بن لؤي بن غالب، أمه قريبة بنت أبي أمية بن المغيرة بن عبد الله بن عمر','5500','1809','0');
INSERT INTO [t10490] VALUES (1813,'عبد الله بن زيد بن ثعلبة بن عبد ربه بن زيد بن الحارث بن الخزرج أبو محمد عقبي بدري، له ولأبيه صحبة، روى عنه ابنه محمد وسعيد بن المسيب، وأبو بكر بن عمرو بن حزم، وعبد الرحمن بن أبي ليلى، توفي بالمدينة سنة اثنتين وثلاثين وهو ابن أربع وستين، وصلى عليه','5502','1809','0');
INSERT INTO [t10490] VALUES (1814,'عبد الله بن زيد بن عاصم المازني الأنصاري الخزرجي من بني النجار، شهد بدرا، وقتل يوم الحرة سنة ثلاث وستين يكنى أبا محمد قال الواقدي: هو ابن أم عبادة حديثه عند عباد بن تميم، ويحيى بن عمارة بن أبي حسن وواسع بن حبان وغيرهم','5506','1809','0');
INSERT INTO [t10490] VALUES (1815,'عبد الله بن زيد وهو ابن أبي طلحة واسم أبي طلحة: زيد بن سهل بن الأسود بن حرام الأنصاري النجاري، استشهد بفارس، وقيل: توفي بالمدينة في خلافة الوليد بن عبد الملك، يكنى: أبا يحيى، أمه: أم سليم أم أنس بن مالك سماه النبي صلى الله عليه وسلم عبد الله،','5510','1809','0');
INSERT INTO [t10490] VALUES (1816,'عبد الله بن زيد بن عمرو بن مازن ذكره بعض المتأخرين، وقال: كان على ثقل النبي صلى الله عليه وسلم، وحكى به عن يونس بن بكير، عن ابن إسحاق قال: أقبل رسول الله صلى الله عليه وسلم قافلا إلى المدينة حمل معه الثقل الذي أصاب، وجعل على الثقل عبد الله بن زيد','5513','1809','0');
INSERT INTO [t10490] VALUES (1817,'عبد الله بن زيد الجهني ذكره بعض المتأخرين، وقال في إسناد حديثه نظر','5514','1809','0');
INSERT INTO [t10490] VALUES (1818,'عبد الله بن زائدة وهو ابن أم مكتوم الأعمى مختلف في اسمه، فقيل: عمرو، وقيل: عبد الله، وقال ابن إسحاق: هو عبد الله بن عمرو بن شريح بن قيس بن زائدة بن الأصم من بني عامر بن لؤي، وقيل: عمرو بن قيس بن شريح بن مالك، وقال مصعب الزبيري: وأم مكتوم اسمها','5516','1809','0');
INSERT INTO [t10490] VALUES (1819,'عبد الله بن زمل الجهني حديثه عند أبي مشجعة بن ربعي','5519','1809','0');
INSERT INTO [t10490] VALUES (1820,'عبد الله بن الزبعرى كان شاعرا جدلا، يناضل عن قريش ويهاجي المسلمين وهو مشرك، ثم أسلم بعد الفتح، وحسن إسلامه فيما ذكره محمد بن إسحاق وهو عبد الله بن الزبعرى بن قيس بن عدي بن سهم القرشي','5521','1809','0');
INSERT INTO [t10490] VALUES (1821,'عبد الله أبو زهير ذكره بعض المتأخرين، وقال: روى عنه ابنه، ولا يصح وفي إسناده اختلاف، وأخرج له هذا الحديث عن علي بن عاصم، عن عطاء بن السائب، عن زهير بن عبد الله، عن أبيه وصوابه','5523','1809','0');
INSERT INTO [t10490] VALUES (1822,'عبد الله بن زغب الإيادي مختلف في صحبته، يعد في تابعي أهل حمص، روى عنه عبد الرحمن بن عائذ، وضمرة بن حبيب','5525','1809','0');
INSERT INTO [t10490] VALUES (1823,'باب السين من باب العين','5527','1723','0');
INSERT INTO [t10490] VALUES (1824,'عبد الله بن سلام بن الحارث الخزرجي من بني قينقاع يكنى أبا يوسف، سماه النبي صلى الله عليه وسلم عبد الله، وكان اسمه حصينا، توفي بالمدينة سنة ثلاث وأربعين، روى عنه أبو هريرة، وأنس بن مالك، وعبد الله بن مغفل، ويوسف ومحمد ابناه، وحبيش الغفاري، وقيس','5528','1823','0');
INSERT INTO [t10490] VALUES (1825,'عبد الله بن سهل بن زيد الأنصاري قتيل اليهود بخيبر حكم رسول الله صلى الله عليه وسلم في قتله بالقسامة','5530','1823','0');
INSERT INTO [t10490] VALUES (1826,'عبد الله بن سهل بن حنيف بن واهب بن حكيم ويقال: عكيم بن ثعلبة من بني صبيعة بن زيد بن مالك، ذكره بعض المتأخرين أنه ولد في عهد النبي صلى الله عليه وسلم، أمه أميمة التي كانت تحت حسان بن الدحداح، وفيها نزلت إذا جاءك المؤمنات يبايعنك، ذكره عن ابن وهب، عن','5533','1823','0');
INSERT INTO [t10490] VALUES (1827,'عبد الله بن سهل بن رافع الأنصاري من الأوس من بني النبيت، ثم من بني عبد الأشهل، شهد بدرا','5534','1823','0');
INSERT INTO [t10490] VALUES (1828,'عبد الله بن سهيل بن عمرو بن عبد شمس أخو أبي جندل بن سهيل من بني عامر بن لؤي، شهد بدرا مع أبيه والمشركين، ثم فر من أبيه إلى رسول الله صلى الله عليه وسلم، فشهدها مسلما','5536','1823','0');
INSERT INTO [t10490] VALUES (1829,'عبد الله بن سعد بن خيثمة من بني عمرو بن عبد عوف، من الأوس، له ولأبيه صحبة، شهد هو وأبوه بدرا، ولا يختلف في أبيه، قتل سعد ببدر، وخيثمة بأحد','5539','1823','0');
INSERT INTO [t10490] VALUES (1830,'عبد الله بن سعد الأنصاري عم حرام بن معاوية، وقيل: حرام بن حكيم حديثه عند ابن أخيه حرام، وخالد بن معدان، يعد في الشاميين','5541','1823','0');
INSERT INTO [t10490] VALUES (1831,'عبد الله بن سعد بن أبي سرح بن حبيب بن الحارث بن جذيمة، وقيل: ابن حذيفة بن نصر بن مالك بن حسل بن عامر بن لؤي القرشي من بني معيص بن عامر، ثم من بني عامر بن لؤي، يكنى أبا يحيى، كان يكتب الوحي فارتد، وكان أخا عثمان بن عفان من الرضاعة، فاستأمنه يوم فتح','5544','1823','0');
INSERT INTO [t10490] VALUES (1832,'عبد الله بن سعيد الأموي كان اسمه الحكم فسماه النبي صلى الله عليه وسلم عبد الله','5547','1823','0');
INSERT INTO [t10490] VALUES (1833,'عبد الله بن السعدي القرشي كان مسترضعا في بني سعد، فهو يدعى ابن السعدي، وهو عبد الله بن عبد بن وقدان بن عبد شمس بن عبد ود بن نصر بن مالك بن حسل بن عامر بن لؤي بن غالب، ويقال: عبد الله بن سعد، ويقال: عبد الله بن وقدان','5549','1823','0');
INSERT INTO [t10490] VALUES (1834,'عبد الله بن سراقة بن المعتمر بن أنس بن أداة بن رزاح بن عدي بن كعب بن لؤي أخو عمرو، شهد بدرا هو وأخوه','5553','1823','0');
INSERT INTO [t10490] VALUES (1835,'عبد الله بن السائب بن أبي السائب المخزومي العائذي القارئ، من بني قارة، يكنى أبا عبد الرحمن، كان شريك النبي صلى الله عليه وسلم فأثنى عليه، وهو عبد الله بن السائب بن عائذ بن عبد الله بن عمر بن مخزوم، سكن مكة، مات في زمن عبد الله بن الزبير','5559','1823','0');
INSERT INTO [t10490] VALUES (1836,'عبد الله بن سلمة بن مالك بن الحارث بن عدي بن العجلان شهد بدرا، واستشهد يوم أحد، أمه أنيسة بنت عدي','5564','1823','0');
INSERT INTO [t10490] VALUES (1837,'عبد الله بن سرجس المزني أكل مع النبي صلى الله عليه وسلم خبزا ولحما، واستغفر له، عداده في البصريين حديثه عند قتادة، وعاصم الأحول، ومسلم بن أبي مريم','5568','1823','0');
INSERT INTO [t10490] VALUES (1838,'عبد الله بن سفيان الأزدي روى عنه عثامة بن قيس، حديثه عند الحمصيين','5573','1823','0');
INSERT INTO [t10490] VALUES (1839,'عبد الله بن سفيان بن عبد الأسد بن هلال بن عبد الله بن عمر بن مخزوم، أخو هبار بن سفيان فيما قاله بعض المتأخرين عن محمد بن إسحاق، وهو يزعم أبي سلمة بن عبد الأسد، من مهاجرة الحبشة، وقتل يوم اليرموك فيما قاله الزهري','5575','1823','0');
INSERT INTO [t10490] VALUES (1840,'عبد الله بن سبرة الجهني','5578','1823','0');
INSERT INTO [t10490] VALUES (1841,'عبد الله بن سبرة الهمداني مجهول ذكره بعض المتأخرين، وحكى ابن أبي خيثمة ذكره في الصحابة، وهو عندي الأول','5580','1823','0');
INSERT INTO [t10490] VALUES (1842,'عبد الله بن سيلان يعد في الكوفيين، حديثه عند قيس وسماه أبو علي النيسابوري الحافظ','5582','1823','0');
INSERT INTO [t10490] VALUES (1843,'عبد الله بن سليم بن أكيمة الليثي الحجازي ذكره بعض المتأخرين','5584','1823','0');
INSERT INTO [t10490] VALUES (1844,'عبد الله بن أبي سفيان بن عبد المطلب القرشي ذكر في الصحابة، ولا تصح له رؤية ولا صحبة','5586','1823','0');
INSERT INTO [t10490] VALUES (1845,'عبد الله بن سويد الحارثي أنصاري له صحبة، عداده في المدنيين، حديثه عند ثعلبة بن أبي مالك القرظي','5588','1823','0');
INSERT INTO [t10490] VALUES (1846,'عبد الله بن ساعدة الأنصاري روى عنه مسلم بن جندب','5590','1823','0');
INSERT INTO [t10490] VALUES (1847,'عبد الله بن سندر الجذامي له ولأبيه صحبة، روى عنه ابنه، وأبو الخير، وربيعة بن لقيط، وكان أبوه سندر مولى لزنباع بن سلامة، يكنى أبا الأسود','5592','1823','0');
INSERT INTO [t10490] VALUES (1848,'عبد الله بن سالم روى عنه عبادة بن نسي، مختلف فيه','5594','1823','0');
INSERT INTO [t10490] VALUES (1849,'باب الشين من باب العين','5596','1723','0');
INSERT INTO [t10490] VALUES (1850,'عبد الله بن الشخير العامري وهو عبد الله بن الشخير بن عوف بن كعب بن وقدان بن الحريش بن معاوية بن كعب بن ربيعة بن عامر بن صعصعة، وفد إلى النبي صلى الله عليه وسلم، سكن البصرة','5597','1849','0');
INSERT INTO [t10490] VALUES (1851,'عبد الله بن أبي شديدة يعد في أهل الطائف، روى عنه المغيرة بن سعيد الطائفي، لا تصح له صحبة','5603','1849','0');
INSERT INTO [t10490] VALUES (1852,'عبد الله بن الشياب يعد في أهل حمص، سماه ابن أبي داود، حديثه عند عبد الله بن أبي بلال','5605','1849','0');
INSERT INTO [t10490] VALUES (1853,'عبد الله بن شبل الأنصاري ذكره ابن أبي عاصم في الآحاد وقيل: إنه أخو عبد الرحمن بن شبل','5607','1849','0');
INSERT INTO [t10490] VALUES (1854,'عبد الله بن شمر الخولاني يعد في التابعين، ذكره بعض المتأخرين أن له صحبة وشهد فتح مصر وأحال بصحبته على أبي سعيد بن يونس بن عبد الأعلى','5609','1849','0');
INSERT INTO [t10490] VALUES (1855,'عبد الله بن شرحبيل أبو علقمة ذكره المتأخر وقال: نسبه يحيى بن يونس الشيرازي، ذكره في الصحابة، وعداده في التابعين','5610','1849','0');
INSERT INTO [t10490] VALUES (1856,'باب الصاد من باب العين','5611','1723','0');
INSERT INTO [t10490] VALUES (1857,'عبد الله بن صفوان بن قدامة أخو عبد الرحمن، له ولأخيه وأبيه صحبة','5612','1856','0');
INSERT INTO [t10490] VALUES (1858,'عبد الله بن صفوان الأنصاري يعد في الكوفيين، حديثه في اصطياد الأرنبين عند الشعبي، مختلف فيه، فقيل: محمد بن صفوان، وقيل: عبد الله، وقيل: خالد، وقيل له: صفوان، وقيل: صفوان بن محمد، تقدم حديثه فيمن اسمه محمد','5614','1856','0');
INSERT INTO [t10490] VALUES (1859,'عبد الله بن صفوان الخزاعي ذكره بعض المتأخرين، وزعم أن له صحبة، ولم يسند عنه شيئا، وذكره في باب الصاد فقال: صفوان بن عبد الله من حديث حماد بن سلمة، عن أبي سنان عن يعلى بن شداد أن عبد الله بن صفوان أوصى أن تشق أكفانه مما يلي الأرض ويهال عليه التراب','5615','1856','0');
INSERT INTO [t10490] VALUES (1860,'عبد الله الصنابحي حديثه عند عطاء بن يسار، مختلف فيه، قال ابن أبي خيثمة، عن يحيى بن معين أن اسمه عبد الله، وقيل: أبو عبد الله، وخالفه غيره فقال: هذا غير أبي عبد الله، اسمه عبد الرحمن، وهذا اسمه عبد الله','5616','1856','0');
INSERT INTO [t10490] VALUES (1861,'باب الضاد من باب العين','5618','1723','0');
INSERT INTO [t10490] VALUES (1862,'عبد الله بن ضمرة بن مالك بن سلمة بن عبد العزى البجلي، عداده في البصريين','5619','1861','0');
INSERT INTO [t10490] VALUES (1863,'باب الطاء من باب العين','5621','1723','0');
INSERT INTO [t10490] VALUES (1864,'عبد الله بن طارق الظفري شهد بدرا، قاله الزهري، وقال عروة بن الزبير: عبد الله بن طارق البلوي حليف الأنصار','5622','1863','0');
INSERT INTO [t10490] VALUES (1865,'عبد الله بن طهفة الغفاري مختلف في حديثه','5625','1863','0');
INSERT INTO [t10490] VALUES (1866,'باب العين من باب العين','5627','1723','0');
INSERT INTO [t10490] VALUES (1867,'عبد الله بن عثمان بن عامر بن عمرو بن كعب بن سعد بن تيم القرشي أبو بكر الصديق رضي الله عنه، له ولأبيه أبي قحافة ولابنيه محمد وعبد الرحمن ولابن ابنه أبي عتيق صحبة، تقدم ذكره في العشرة وفي أول باب العين','5628','1866','0');
INSERT INTO [t10490] VALUES (1868,'عبد الله بن عبد الله بن أبي بن مالك بن الحارث بن عبيد بن مالك، وسلول امرأة وهي أم أبي لأن اسمه الحباب فسماه رسول الله صلى الله عليه وسلم عبد الله الأنصاري الخزرجي، شهد بدرا من بني عمرو بن عوف، ذكره بعض المتأخرين فقال: عبد الله بن عبد الله بن أبي مالك','5629','1866','0');
INSERT INTO [t10490] VALUES (1869,'عبد الله بن أبي بكر الصديق وهو عبد الله بن عبد الله بن عثمان بن عامر، وهو الذي يأتي رسول الله صلى الله عليه وسلم وأباه إذ هما في الغار بزادهما وأخبار مكة كل ليلة، رمي يوم الطائف بسهم فلم يزل يتعاهده حتى مات بالمدينة في أول خلافة أبيه، ذكره بعض','5636','1866','0');
INSERT INTO [t10490] VALUES (1870,'عبد الله بن عبد الأسد بن هلال بن عبد الله بن عمر بن مخزوم ابن عمة رسول الله صلى الله عليه وسلم، أمه برة بنت عبد المطلب بن هاشم، أخو النبي صلى الله عليه وسلم من الرضاعة، يكنى أبا سلمة، ذو الهجرتين: هجرة الحبشة والمدينة، وولد له بالحبشة عمر بن أبي','5640','1866','0');
INSERT INTO [t10490] VALUES (1871,'عبد الله بن عبد هلال وقيل: ابن عبد الله بن هلال، أنصاري من أهل قباء حديثه عند بشر بن مروان القبائي','5647','1866','0');
INSERT INTO [t10490] VALUES (1872,'عبد الله بن عبد الثمالي أبو الحجاج له صحبة من الطبقة الثانية، من الشاميين، عداده في أهل حمص حديثه عند عبد الرحمن بن أبي عوف الجرشي','5649','1866','0');
INSERT INTO [t10490] VALUES (1873,'عبد الله بن عائذ بن قرط وقيل: ابن قريط نسب إلى الصحابة','5651','1866','0');
INSERT INTO [t10490] VALUES (1874,'عبد الله بن عباس بن عبد المطلب بن هاشم أبو العباس، أمه لبابة بنت الحارث بن حزن بن بجية بن الهزم بن روبية بن عبد الله بن هلال بن عامر بن صعصعة الهلالي، كان يسمى الحبر والبحر لكثرة علمه، وحدة فهمه، وحبر الأمة وفقيهها، ولسان العشيرة ومنطيقها، محنك','5653','1866','0');
INSERT INTO [t10490] VALUES (1875,'عبد الله بن عمر بن الخطاب أبو عبد الرحمن العدوي خال المؤمنين، من أملك شباب قريش عن الدنيا، أمه وأم أخته حفصة زوج النبي صلى الله عليه وسلم: زينب بنت مظعون بن حبيب بن وهب بن حذافة بن جمح، هاجر مع أبيه عمر رضي الله عنهما، كان آدم طوالا له جمة مفروقة','5692','1866','0');
INSERT INTO [t10490] VALUES (1876,'ما أسند عبد الله بن عمر رضي الله عنهما روى من المتون سوى الطرق نيفا وسبع مائة حديث','5724','1866','0');
INSERT INTO [t10490] VALUES (1877,'عبد الله بن عثمان بن عفان ولد بأرض الحبشة، أمه رقية بنت رسول الله صلى الله عليه وسلم وبه كان يكني رسول الله صلى الله عليه وسلم عثمان بن عفان: أبا عبد الله، توفي في حياة رسول الله صلى الله عليه وسلم وهو ابن ست سنين، ودخل في قبره، ذكره بعض المتأخرين','5742','1866','0');
INSERT INTO [t10490] VALUES (1878,'عبد الله بن عمرو بن حرام الأنصاري ثم السلمي أبو جابر، شهد العقبة وبدرا، من النقباء، استشهد يوم أحد، ودفن مع صفيه ووديده عمرو بن الجموح، كلم الله روحه بالكفاح، وأظلت الملائكة جسمه بالجناح، قاتل المشركين بالجد والثبات فقتلوه محتسبا عن تسع من البنات','5747','1866','0');
INSERT INTO [t10490] VALUES (1879,'عبد الله بن عمرو بن وهب بن ثعلبة بن وقش بن ثعلبة بن طريف، استشهد بأحد من رهط سعد بن عبادة','5760','1866','0');
INSERT INTO [t10490] VALUES (1880,'عبد الله بن عمرو بن العاص بن وائل بن هشام بن سعيد بن سعد بن سهم بن عمرو بن هصيص بن كعب بن لؤي بن غالب، يكنى أبا محمد وقيل: أبو نصير، وقيل: أبو عبد الرحمن، استأذن النبي صلى الله عليه وسلم في الكتابة عنه في حال الغضب والرضا، فأذن له حفظ عن النبي صلى','5763','1866','0');
INSERT INTO [t10490] VALUES (1881,'عبد الله المزني أبو علقمة وهو عبد الله بن عمرو بن هلال، وقيل: ابن شرحبيل والد بكر وعلقمة، حديثه عند ابنه','5780','1866','0');
INSERT INTO [t10490] VALUES (1882,'عبد الله بن عمرو ابن أم حرام الأنصاري وقيل: عبد الله ابن أم حرام، وهو ابن امرأة عبادة بن الصامت','5784','1866','0');
INSERT INTO [t10490] VALUES (1883,'عبد الله بن عمرو بن حزم أخو عمارة بن حزم، ذكره بعض المتأخرين وقال: له ذكر في المغازي، ولا تعرف له رواية، فيما قال نظر','5786','1866','0');
INSERT INTO [t10490] VALUES (1884,'عبد الله بن عامر وقيل: عمرو بن لؤيم، يعد في الصحابة','5787','1866','0');
INSERT INTO [t10490] VALUES (1885,'عبد الله بن عمرو بن حلحلة ذكره بعض المتأخرين، وقال: ذكر في الصحابة، وهو وهم','5789','1866','0');
INSERT INTO [t10490] VALUES (1886,'عبد الله بن عتيك الأنصاري أخو جابر من الأوس من بني مالك بن معاوية أحد قتلة ابن أبي الحقيق وقال ابن داود: هو أبو جابر وجبر ابنا عتيك حديثه عند ابنه وعبد الله، وعبد الرحمن بن كعب بن مالك، قتل باليمامة سنة اثنتي عشرة','5791','1866','0');
INSERT INTO [t10490] VALUES (1887,'عبد الله بن عدي بن الخيار بن عدي بن نوفل بن عبد مناف الأنصاري','5795','1866','0');
INSERT INTO [t10490] VALUES (1888,'عبد الله بن عدي بن الحمراء الزهري أبو عمرو يعد في الحجازيين حديثه عند أبي سلمة','5797','1866','0');
INSERT INTO [t10490] VALUES (1889,'عبد الله بن عامر بن ربيعة العدوي العنزي - وعنزة من اليمن - حليف بني عدي أدرك النبي صلى الله عليه وسلم ورآه، توفي النبي وهو ابن خمس أو أربع سنين، وقال الهيثم بن عدي: هلك زمن الوليد بن عبد الملك','5799','1866','0');
INSERT INTO [t10490] VALUES (1890,'عبد الله بن عامر بن كريز بن حبيب بن عبد شمس القرشي، توفي النبي صلى الله عليه وسلم وله ثلاث عشرة سنة، وتوفي سنة ستين حنكه النبي صلى الله عليه وسلم وهو ابن ثلاث سنين وهو الذي فتح نيسابور','5805','1866','0');
INSERT INTO [t10490] VALUES (1891,'عبد الله بن عامر بن أنيس بن المنتفق بن عامر، الوافد على رسول الله صلى الله عليه وسلم، روى عنه عبد الله بن جراد، والمغيرة بن عبد الله اليشكري','5807','1866','0');
INSERT INTO [t10490] VALUES (1892,'عبد الله بن عمير الأنصاري شهد بدرا','5810','1866','0');
INSERT INTO [t10490] VALUES (1893,'عبد الله بن عمير السدوسي','5814','1866','0');
INSERT INTO [t10490] VALUES (1894,'عبد الله بن عمير الخطمي إمام مسجد بني خطمة جاهد مع النبي صلى الله عليه وسلم وهو أعمى','5816','1866','0');
INSERT INTO [t10490] VALUES (1895,'عبد الله بن عمير الأشجعي','5818','1866','0');
INSERT INTO [t10490] VALUES (1896,'عبد الله بن عميرة أدرك الجاهلية، وكان قائد الأعشى في الجاهلية، لا تصح له صحبة ولا رؤية، ذكره بعض المتأخرين','5820','1866','0');
INSERT INTO [t10490] VALUES (1897,'عبد الله بن عتبة بن مسعود ابن أخي عبد الله بن مسعود الهذلي حجازي حديثه عند ابنه عبيد الله','5822','1866','0');
INSERT INTO [t10490] VALUES (1898,'عبد الله بن عرفجة الأنصاري السالمي من الأوس، شهد بدرا، سماه بعض المتأخرين عبد الله، ولم يسمه الزهري، ولا ابن إسحاق','5825','1866','0');
INSERT INTO [t10490] VALUES (1899,'عبد الله بن عبس وقيل: عبيس الأنصاري الخزرجي شهد بدرا','5828','1866','0');
INSERT INTO [t10490] VALUES (1900,'عبد الله بن عرفطة الأنصاري الخدري شهد بدرا','5831','1866','0');
INSERT INTO [t10490] VALUES (1901,'عبد الله بن عبد مناف بن النعمان بن سنان الأنصاري، شهد بدرا','5835','1866','0');
INSERT INTO [t10490] VALUES (1902,'عبد الله بن عياش بن أبي ربيعة المخزومي','5839','1866','0');
INSERT INTO [t10490] VALUES (1903,'عبد الله بن عوف','5843','1866','0');
INSERT INTO [t10490] VALUES (1904,'عبد الله بن عكيم أبو معبد الجهني من الكوفة، أدرك النبي صلى الله عليه وسلم ولم يره، روى عنه زيد بن وهب، وعبد الرحمن بن أبي ليلى، وعيسى ابنه، وهلال الوزان، والقاسم بن مخيمرة','5846','1866','0');
INSERT INTO [t10490] VALUES (1905,'عبد الله بن عرابة الجهني حديثه عند معاذ بن عبد الله بن خبيب، وقيل: عبد الرحمن بن عرابة، والصحيح رفاعة بن عرابة','5854','1866','0');
INSERT INTO [t10490] VALUES (1906,'عبد الله بن عكبرة قوله: التخليل من السنة، مجهول، ذكره بعض المتأخرين، قوله هذا من حديث أبي أحمد الزبيري، عن حنظلة بن عبد الحميد، عن عبد الكريم البصري، عن مجاهد، عن عبد الله بن عكبرة، ولم يزد عليه','5856','1866','0');
INSERT INTO [t10490] VALUES (1907,'عبد الله بن عنبة أبو عنبة الخولاني سماه الطبراني في المعجم يعد في الشاميين حديثه عند بكر بن زرعة وأبي الزاهرية، ومحمد بن زياد الألهاني','5858','1866','0');
INSERT INTO [t10490] VALUES (1908,'عبد الله بن عديس البلوي أخو عبد الرحمن، شهد فتح مصر لا تعرف له رواية','5860','1866','0');
INSERT INTO [t10490] VALUES (1909,'وعبد الله بن عنمة المزني شهد فتح مصر له ذكر في الصحابة، ذكرهما بعض المتأخرين، ولم يخرج عنهما شيئا وأحال بذكرهما إلى أبي سعيد بن عبد الأعلى','5861','1866','0');
INSERT INTO [t10490] VALUES (1910,'عبد الله بن عويم بن ساعدة بن صلعجة بن عمرو بن حارثة بن أوس بن مالك، شهد عويم بدرا والعقبة، ذكره المنيعي في الصحابة','5862','1866','0');
INSERT INTO [t10490] VALUES (1911,'باب الغين من باب العين','5864','1723','0');
INSERT INTO [t10490] VALUES (1912,'عبد الله بن غنام له صحبة، وقيل: عنام بدري: وهو ابن أوس بن عمرو بن مالك بن بياضة','5865','1911','0');
INSERT INTO [t10490] VALUES (1913,'عبد الله بن الغسيل مجهول حديثه عند عامر بن عبد الأسد يعد في أعراب البصرة','5867','1911','0');
INSERT INTO [t10490] VALUES (1914,'عبد الله الغفاري','5869','1911','0');
INSERT INTO [t10490] VALUES (1915,'باب الفاء من باب العين','5870','1723','0');
INSERT INTO [t10490] VALUES (1916,'عبد الله بن فضالة الليثي لم يذكر سماعا من النبي صلى الله عليه وسلم، ولا تصح له صحبة، عداده في التابعين، ذكره بعض المتأخرين في الصحابة','5871','1915','0');
INSERT INTO [t10490] VALUES (1917,'باب القاف من باب العين','5872','1723','0');
INSERT INTO [t10490] VALUES (1918,'عبد الله بن قيس أبو موسى الأشعري حليف آل عتبة بن ربيعة بن عبد شمس أسلم بمكة، وهاجر إلى الحبشة، ذو الهجرتين، هجرة الحبشة والمدينة، فبقي بالحبشة مع جعفر بن أبي طالب حتى قدم معه زمن خيبر، مختلف في وفاته وقبره، فقيل: توفي في سنة اثنتين وخمسين، ودفن','5873','1917','0');
INSERT INTO [t10490] VALUES (1919,'عبد الله بن قيس الخزاعي','5893','1917','0');
INSERT INTO [t10490] VALUES (1920,'عبد الله بن قيس الأنصاري استشهد في بعض بعوث النبي صلى الله عليه وسلم، ذكره في حديث ابن عباس','5895','1917','0');
INSERT INTO [t10490] VALUES (1921,'عبد الله بن قيس الأسلمي','5897','1917','0');
INSERT INTO [t10490] VALUES (1922,'عبد الله بن قيس بن مخرمة بن المطلب روى عنه عبد الله بن أبي بكر بن محمد بن عمرو بن حزم، عن أبيه، عنه، ذكره بعض المتأخرين، وذكر أن في صحبته نظرا إسماعيل بن أبان، عن أبي أويس، عن عبد الله بن أبي بكر بن محمد بن عمرو بن حزم، عن أبيه عن عبد الله بن قيس','5899','1917','0');
INSERT INTO [t10490] VALUES (1923,'عبد الله بن قيس بن صخر بن حرام بن ربيعة بن عدي بن غنم بن كعب بن سلمة الأنصاري، شهد بدرا، قاله عروة بن الزبير حدثنا حبيب بن الحسن، ثنا محمد بن يحيى، ثنا أحمد بن محمد بن أيوب، ثنا إبراهيم بن سعد، عن محمد بن إسحاق، في تسمية من شهد بدرا من الأنصار من','5900','1917','0');
INSERT INTO [t10490] VALUES (1924,'عبد الله بن قيس بن خالد بن خلدة بن الحارث بن سواد بن مالك بن غنم، شهد بدرا حدثنا فاروق الخطابي، قال: ثنا زياد بن الخليل، ثنا إبراهيم بن المنذر، ثنا محمد بن فليح، ثنا موسى بن عقبة، عن ابن شهاب، في تسمية من شهد بدرا من الأنصار من الخزرج: عبد الله','5901','1917','0');
INSERT INTO [t10490] VALUES (1925,'عبد الله بن قيس العتقي له صحبة، وشهد فتح مصر والعراق، له رواية، ذكره المحيل على أبي سعيد بن عبد الأعلى','5902','1917','0');
INSERT INTO [t10490] VALUES (1926,'عبد الله بن قرط الأزدي الثمالي كان اسمه شيطانا، فسماه النبي صلى الله عليه وسلم عبد الله، له ولأخيه عبد الرحمن صحبة، عداده في الشاميين، كان أميرا على حمص من قبل معاوية، قتل بأرض الروم غازيا سنة ست وخمسين','5903','1917','0');
INSERT INTO [t10490] VALUES (1927,'عبد الله بن قارب الثقفي','5907','1917','0');
INSERT INTO [t10490] VALUES (1928,'عبد الله بن قمامة أخو وقاص بن قمامة السلميان، كتب لهما النبي صلى الله عليه وسلم كتابا له، ذكر في حديث عمرو بن حزم','5909','1917','0');
INSERT INTO [t10490] VALUES (1929,'باب الكاف من باب العين','5911','1723','0');
INSERT INTO [t10490] VALUES (1930,'عبد الله بن كرز الليثي له ذكر في حديث عائشة، وأخبرت عن شعره','5912','1929','0');
INSERT INTO [t10490] VALUES (1931,'عبد الله بن كعب بن عمرو بن عوف بن مبذول بن عمرو بن غنم بن مازن بن النجار، شهد بدرا حدثنا فاروق الخطابي، ثنا زياد بن الخليل، ثنا إبراهيم بن المنذر، ثنا محمد بن فليح، ثنا موسى بن عقبة، عن ابن شهاب، في تسمية من شهد بدرا من الأنصار من الخزرج، ثم من','5914','1929','0');
INSERT INTO [t10490] VALUES (1932,'عبد الله بن كعب بن زيد بن عاصم الأنصاري يكنى أبا الحارث، بدري، ولاه النبي صلى الله عليه وسلم حفظ الأنفال يوم بدر، من بني مازن بن النجار، وقيل: عبد الله بن كعب بن عاصم، وأراد المتقدم حدثنا حبيب بن الحسن، ثنا محمد بن يحيى، ثنا أحمد بن محمد بن أيوب','5915','1929','0');
INSERT INTO [t10490] VALUES (1933,'عبد الله بن اللتبية استعمله النبي صلى الله عليه وسلم ساعيا على بعض الصدقات، ذكره في حديث أبي حميد الساعدي','5916','1929','0');
INSERT INTO [t10490] VALUES (1934,'باب الميم من باب العين','5917','1723','0');
INSERT INTO [t10490] VALUES (1935,'عبد الله بن مسعود ابن عاقل بن حبيب بن فار بن شمخ بن مخزوم بن صاهلة بن كاهل بن الحارث بن تميم بن سعد بن هذيل بن مدركة بن إلياس بن مضر، قاله شباب فيما حدثناه محمد بن علي، ثنا عمر بن أحمد بن إسحاق، عنه وقال محمد بن إسحاق: عبد الله بن مسعود بن الحارث بن','5918','1934','0');
INSERT INTO [t10490] VALUES (1936,'عبد الله بن مالك، ابن بحينة الأزدي وهو عبد الله بن مالك بن سعد بن القشب، من أزد شنوءة حليف بني عبد المطلب بن عبد مناف بن قصي، وبحينة بنت الحارث بن المطلب بن عبد مناف، روى عنه عبد الرحمن الأعرج، وعطاء بن يسار، ومحمد بن عبد الرحمن بن ثوبان، وابنه','5952','1934','0');
INSERT INTO [t10490] VALUES (1937,'عبد الله بن مالك الأوسي حجازي، حديثه عند شبل بن خليد','5956','1934','0');
INSERT INTO [t10490] VALUES (1938,'عبد الله بن مالك الغافقي أبو موسى عداده في أهل مصر، روى عنه، ثعلبة بن أبي الكنود','5959','1934','0');
INSERT INTO [t10490] VALUES (1939,'عبد الله بن مالك الأحمسي أبو كاهل، وقيل: قيس بن عائذ','5961','1934','0');
INSERT INTO [t10490] VALUES (1940,'عبد الله بن مالك بن أبي القين الخزرجي أخو كعب بن مالك له ذكر عند ابن أخيه عبد الله بن كعب، لا تعرف له رواية','5963','1934','0');
INSERT INTO [t10490] VALUES (1941,'عبد الله بن مالك من بني المعتمر وهو ابن قضيعة بن عبس له صحبة، عقد له النبي صلى الله عليه وسلم لواء أبيض في رهط بعثهم له شهد فتح القادسية، وكان على أحد المجنبتين، لا تعرف له رواية ذكره بعض المتأخرين','5964','1934','0');
INSERT INTO [t10490] VALUES (1942,'عبد الله بن مغفل المزني وهو عبد الله بن مغفل بن عبد نهم بن عفيف بن أسحيم بن ربيعة بن عدي بن ذؤيب بن سعد بن عداد بن عثمان بن عمرو بن أد بن طابخة بن إلياس بن مضر، يكنى أبا سعيد، وقيل: أبو زياد، أمه العيلة بنت معاوية بن معاوية، من مزينة، سكن البصرة،','5965','1934','0');
INSERT INTO [t10490] VALUES (1943,'عبد الله بن مطيع بن الأسود القرشي من العبلات من بني عدي، ولد في عهد النبي صلى الله عليه وسلم وحنكه، توفي زمن ابن الزبير سكن المدينة','5973','1934','0');
INSERT INTO [t10490] VALUES (1944,'عبد الله بن مطر أبو ريحانة مختلف في اسمه، فقيل: هو شمعون، وهو من الأزد، وكان يقص بأيليا، له كرامات، وآيات، حديثه عند شهر بن حوشب، وثوبان بن شهر، وكريب، وأبرهة، وعبادة بن نسي، والهيثم بن شفي','5977','1934','0');
INSERT INTO [t10490] VALUES (1945,'عبد الله بن مقرن المزني ذكره بعض المتأخرين في الصحابة، وقال: روى عنه ابن سيرين، وعبد الملك بن عمير، ولم يخرج عنه شيئا','5980','1934','0');
INSERT INTO [t10490] VALUES (1946,'عبد الله بن مظعون الجمحي أخو عثمان بن مظعون، شهد بدرا، حدثنا فاروق، ثنا زياد بن الخليل، ثنا إبراهيم بن المنذر، ثنا محمد بن فليح، ثنا موسى بن عقبة، عن ابن شهاب، في تسمية من شهد بدرا مع رسول الله صلى الله عليه وسلم من بني جمح: عبد الله بن مظعون','5981','1934','0');
