INSERT INTO [t10490] VALUES (1961,'باب النون من باب العين','6009','1723','0');
INSERT INTO [t10490] VALUES (1962,'عبد الله بن نضلة من بني عدي بن كعب، من مهاجرة الحبشة، ذكره بعض المتأخرين، عن محمد بن شعيب، عن عثمان بن عطاء، عن ابن عباس، فيمن هاجر مع جعفر بن أبي طالب: عبد الله بن نضلة، وهو وهم لا يختلف أحد من أهل المغازي، الزهري، وابن إسحاق في كل الروايات','6010','1961','0');
