INSERT INTO [t10490] VALUES (1982,'باب الياء من باب العين','6042','1723','0');
INSERT INTO [t10490] VALUES (1983,'عبد الله بن يزيد الخطمي الأنصاري سكن الكوفة، وله بها دار، توفي زمن ابن الزبير، نسبه محمد بن عمر الواقدي، فقال: هو عبد الله بن يزيد بن زيد بن حصين بن عمرو بن الحارث بن خطمة، يكنى أبا موسى، شهد الحديبية، وهو ابن سبع عشرة سنة، استعمله ابن الزبير','6043','1982','0');
INSERT INTO [t10490] VALUES (1984,'عبد الله بن يزيد القارئ له ذكر في حديث عائشة','6047','1982','0');
INSERT INTO [t10490] VALUES (1985,'عبد الله اليربوعي','6049','1982','0');
INSERT INTO [t10490] VALUES (1986,'عبد الله الثمالي غير منسوب، له صحبة، روى عنه ثور بن يزيد، وعبد الرحمن بن أبي عوف، ذكره يعقوب بن سفيان في الصحابة، وهو من التابعين، وقيل: هو عبد الله بن عبد الثمالي المتقدم ذكره','6051','1982','0');
INSERT INTO [t10490] VALUES (1987,'عبد الله، غير منسوب غير النبي صلى الله عليه وسلم اسمه، فكان اسمه نعما، فسماه عبد الله','6054','1982','0');
INSERT INTO [t10490] VALUES (1988,'عبد الله أبو يزيد المدني وقيل: عبد حديثه عند ابنه يزيد','6056','1982','0');
INSERT INTO [t10490] VALUES (1989,'عبد الله أبو قابوس غير منسوب، يعد في أهل الكوفة، مختلف في اسمه، ذكره بعض المتأخرين، وأبو قابوس اسمه المخارق','6058','1982','0');
INSERT INTO [t10490] VALUES (1990,'عبد الله البكري مجهول، سأل النبي صلى الله عليه وسلم عن أفضل الأعمال، حديثه عند محمد بن عمرو بن جبلة، عن بنت الشماخ، قالت: حدثتني بهية بنت محمد بن عبد الله البكرية، عن أبيها، عن النبي صلى الله عليه وسلم بهذا، ذكره بعض المتأخرين هكذا، ولم يزد عليه','6060','1982','0');
INSERT INTO [t10490] VALUES (1991,'عبد الله بن سفيان الثقفي روى حديثه هشام بن عروة، عن أبيه، عن سفيان بن عبد الله الثقفي، عن أبيه، ولا يصح قوله: عن أبيه، والحديث الصحيح بسفيان بن عبد الله من دون أبيه','6061','1982','0');
INSERT INTO [t10490] VALUES (1992,'عبد الله الداري أخو أبي هند، كان اسمه الطيب، تقدم ذكره، وكرره بعض المتأخرين بعد أن ذكره في باب الباء، فقال: عبد الله','6062','1982','0');
INSERT INTO [t10490] VALUES (1993,'عبد الله الخولاني والد أبي إدريس الخولاني، حكى بعض المتأخرين أن البخاري ذكره في الصحابة، وأن ابنه روى عنه، ولا تصح له صحبة','6063','1982','0');
INSERT INTO [t10490] VALUES (1994,'عبد الله أبو خالد عداده في أهل الشام، وقيل: عبيد الله، عداده في الشاميين، روى حديثه عبد الوهاب بن الضحاك','6064','1982','0');
INSERT INTO [t10490] VALUES (1995,'عبد الله المزني، غير منسوب ذكره بعض المتأخرين، وقال: يقال: إنه ابن مغفل، وقال: روى حديثه أبو معمر، عن عبد الوارث، عن حسين المعلم','6066','1982','0');
INSERT INTO [t10490] VALUES (1996,'عبد الله أبو محمد روى عن النبي صلى الله عليه وسلم في مدمني الخمر، ذكره بعض المتأخرين، وقال: رواه سهيل بن أبي صالح، عن محمد بن عبد الله، عن أبيه، وصحيحه سهيل، عن أبيه','6068','1982','0');
INSERT INTO [t10490] VALUES (1997,'عبد الله أبو مالك الخثعمي له ذكر في حديث حبيب بن مسلمة، قد تقدم','6069','1982','0');
INSERT INTO [t10490] VALUES (1998,'من اسمه عبد الرحمن','6071','1723','0');
INSERT INTO [t10490] VALUES (1999,'عبد الرحمن بن عوف تقدم ذكره في العشرة، وهو أبو محمد عبد الرحمن بن عوف بن عبد عوف بن عبد الحارث بن زهرة بن كلاب بن مرة بن كعب بن لؤي، ذو الهجرتين، شهد بدرا والمشاهد كلها، أحد أصحاب الشورى، بعثه النبي صلى الله عليه وسلم في سرية وعممه، وعقد له اللواء','6072','1998','0');
INSERT INTO [t10490] VALUES (2000,'عبد الرحمن بن جبر الأنصاري يكنى أبا عبس، شهد بدرا، كان اسمه في الجاهلية عبد العزى، فسماه رسول الله صلى الله عليه وسلم عبد الرحمن، وهو عبد الرحمن بن جبر بن عمرو بن زيد بن حارثة بن الحارث بن الخزرج بن عمرو بن مالك بن الأوس، وقيل: ابن عمرو بن زيد بن','6076','1998','0');
INSERT INTO [t10490] VALUES (2001,'عبد الرحمن بن سعد بن المنذر بن مالك بن خالد بن ثعلبة بن حارثة بن عمرو بن الخزرج بن ساعدة، نسبه الواقدي، وقال: شهد أحدا والخندق والمشاهد، انقرض أولاده، فلم يبق له عقب، وقيل: ابن المنذر بن حميد الساعدي، وقيل: إن اسمه المنذر، روى عنه جابر بن عبد','6079','1998','0');
INSERT INTO [t10490] VALUES (2002,'عبد الرحمن ابن حسنة بن المطاع بن عبد الله بن الغطريف بن عبد العزى بن ختامة بن مائل بن ملادم بن مالك بن رهم بن يشكر بن مبشر بن الغوث بن مرة، أخو تميم بن مر، ويقال: إنه من كندة أخو شرحبيل ابن حسنة، وحسنة أمهما، سكن الكوفة','6082','1998','0');
INSERT INTO [t10490] VALUES (2003,'عبد الرحمن بن أبي بكر الصديق يكنى أبا عثمان، وقيل: أبو عبد الله، كان اسمه في الجاهلية عبد العزى، فسماه النبي صلى الله عليه وسلم عبد الرحمن، أمه أم رومان بنت عبد دهمان، أحد بني فراس بن غنم بن مالك بن كنانة، وهو أخو عائشة لأبيها وأمها، كان أسن ولد','6085','1998','0');
INSERT INTO [t10490] VALUES (2004,'عبد الرحمن بن سمرة بن حبيب بن عبد شمس بن عبد مناف القرشي، يكنى أبا سعيد، أمه بنت أبي فرعة بن كعب بن عمرو بن طريف بن خزيمة بن علقمة بن غنم بن مالك بن كنانة، سكن البصرة، ومات بها، وابنه عبيد الله بن عبد الرحمن غلب على البصرة أيام ابن الأشعث روى عنه','6088','1998','0');
INSERT INTO [t10490] VALUES (2005,'عبد الرحمن بن عمر بن الخطاب أخو عبد الله، وحفصة لأبيهما وأمهما، أمهم زينب بنت مظعون بن حبيب بن وهب بن حذافة بن جمح، كناه النبي صلى الله عليه وسلم أبا عيسى فيما وهم فيه بعض المتأخرين، وعده في الصحابة؛ لأن النبي صلى الله عليه وسلم كنى المغيرة بها لا','6091','1998','0');
INSERT INTO [t10490] VALUES (2006,'عبد الرحمن بن زيد بن الخطاب أخو عمر أبو عبد الحميد، ولد في عهد النبي صلى الله عليه وسلم، ومات النبي صلى الله عليه وسلم، وهو ابن ست سنين','6092','1998','0');
INSERT INTO [t10490] VALUES (2007,'عبد الرحمن بن ثابت بن قيس بن شماس الأنصاري، له ولأبيه صحبة فيما ذكره بعض المتأخرين','6094','1998','0');
INSERT INTO [t10490] VALUES (2008,'عبد الرحمن بن أزهر بن عبد عوف بن عبد الحارث بن زهرة بن كلاب بن مرة بن كعب بن لؤي بن غالب بن فهر، سكن مكة يستدل على رحل خالد بن الوليد، أمه بنت عبد يزيد بن هاشم بن عبد المطلب بن عبد مناف، شهد حنينا مع النبي صلى الله عليه وسلم، وكان يسعى بين يدي رسول','6096','1998','0');
INSERT INTO [t10490] VALUES (2009,'عبد الرحمن بن عثمان التيمي وهو ابن عبيد الله بن عثمان بن عمرو بن كعب بن سعد بن تميم بن مرة بن كعب ابن أخي طلحة بن عبيد الله، شهد اليرموك مع أبي عبيدة، وأصيب مع ابن الزبير، فدفن في المسجد الحرام، وأخفي مكان قبره عن أهل الشام، حديثه عند سعيد بن المسيب','6100','1998','0');
INSERT INTO [t10490] VALUES (2010,'عبد الرحمن بن معاذ التيمي له صحبة، حديثه عند محمد بن إبراهيم التيمي','6105','1998','0');
INSERT INTO [t10490] VALUES (2011,'عبد الرحمن بن صفوان بن قدامة الجمحي وقيل: القرشي، ويقال: صفوان بن عبد الرحمن بن أمية بن خلف، حديثه عند مجاهد، وقيل: ابن قدامة الجمحي','6107','1998','0');
INSERT INTO [t10490] VALUES (2012,'عبد الرحمن بن صفوان بن قتادة له ولأبيه صحبة','6110','1998','0');
INSERT INTO [t10490] VALUES (2013,'عبد الرحمن بن أبزى الخزاعي سكن الكوفة، مولى نافع بن الحارث','6113','1998','0');
INSERT INTO [t10490] VALUES (2014,'عبد الرحمن بن أم النحام وقيل: ابن النحام ذكره في حديث كعب بن مرة','6116','1998','0');
INSERT INTO [t10490] VALUES (2015,'عبد الرحمن بن زمعة بن الأسود ابن المطلب بن عبد العزى بن قصي، أمه بنت أبي أمية بن المغيرة بن عبد الله بن عمر بن مخزوم، وأمها عاتكة بنت عبد المطلب','6118','1998','0');
INSERT INTO [t10490] VALUES (2016,'عبد الرحمن بن شبل الأنصاري من فقهائهم، سكن الشام','6120','1998','0');
INSERT INTO [t10490] VALUES (2017,'عبد الرحمن بن أبي صعصعة ذكره بعض المتأخرين، وقال: هو ابن عمرو بن زيد بن عوف بن المنذر بن عمرو بن غنم بن مازن بن النجار، أخو قيس بن عبد الله بن عبد الرحمن بن أبي صعصعة، عن أبيه، عن جده','6122','1998','0');
INSERT INTO [t10490] VALUES (2018,'عبد الرحمن بن أشيم الأنصاري قال محمد بن إسماعيل: له صحبة، يعد في المدنيين','6124','1998','0');
INSERT INTO [t10490] VALUES (2019,'عبد الرحمن بن سميرة وقيل: ابن سمير، ذكره بعض المتأخرين في الصحابة، وقال: لا يصح، وأخرج له هذا الحديث من حديث قبيصة','6126','1998','0');
INSERT INTO [t10490] VALUES (2020,'عبد الرحمن بن يزيد بن جارية الأنصاري أخو مجمع، قال محمد بن إسماعيل: عداده في التابعين، سماه غيره في الصحابة، حديثه عند القاسم بن محمد','6128','1998','0');
