INSERT INTO [t10490] VALUES (2120,'عبد غنم الدوسي أبو هريرة وقال شعبة: اسمه عبد شمس، وقيل: عبد نهم، وقال بكر بن بكار: اسمه عمرو بن عبد غنم وقيل: عامر بن عبد شمس، وقيل: عبد ياليل وقيل عبد العزى، وقيل: عامر بن عبد شمس، وقال أبو عبيد: اسمه عامر بن عمير، وقال هشام بن محمد: اسمه','6345','2108','0');
