INSERT INTO [t10490] VALUES (2180,'عباد بن شرحبيل الغبري يعد في البصريين، حديثه عند أبي بشر جعفر بن أبي وحشية','6512','2177','0');
INSERT INTO [t10490] VALUES (2181,'عباد بن بشر بن قيظي الأنصاري قيل: هو المتقدم من بني عبد الأشهل، وقيل: غيره، فرقه بعض المتأخرين فأخرج له هذا الحديث، وهو الذي تقدم حديثه','6515','2177','0');
INSERT INTO [t10490] VALUES (2182,'عباد بن عمرو الديلي وقيل: الليثي، يعد في الكوفيين','6518','2177','0');
INSERT INTO [t10490] VALUES (2183,'عباد أبو ثعلبة العبدي سكن الكوفة، حديثه عند الأسود بن قيس','6520','2177','0');
INSERT INTO [t10490] VALUES (2184,'عباد بن المطلب ذكره بعض المتأخرين في الصحابة، وزعم أنه له ذكر في المهاجرين، ولا يعرف له رواية، فذكر له، عن شيخه، عن العطاردي، عن يونس بن بكير، عن ابن إسحاق في هجرة الصحابة: ونزل عبيدة بن الحارث، وعباد بن المطلب، وجماعة سماهم، وهو وهم شنيع،','6522','2177','0');
INSERT INTO [t10490] VALUES (2185,'عباد بن مرة، وقيل: مرة بن عباد عداده في الشاميين، فيما ذكره بعض المتأخرين','6524','2177','0');
INSERT INTO [t10490] VALUES (2186,'عباد بن سهل الأنصاري الأشهلي استشهد بأحد','6526','2177','0');
INSERT INTO [t10490] VALUES (2187,'عباد العدوي ذكره البخاري في الصحابة فيما حكى عنه بعض المتأخرين وقال: خالفه غيره','6529','2177','0');
INSERT INTO [t10490] VALUES (2188,'عباد بن سنان، وقيل: شيبان أبو إبراهيم السلمي حليف قريش، خطب إلى النبي صلى الله عليه وسلم أميمة بنت ربيعة بن الحارث','6531','2177','0');
INSERT INTO [t10490] VALUES (2189,'عباد بن عمرو وقيل: عياد بن عمرو، وكان يخدم النبي صلى الله عليه وسلم','6534','2177','0');
INSERT INTO [t10490] VALUES (2190,'عباد بن أخضر وقيل: ابن أحمر','6536','2177','0');
INSERT INTO [t10490] VALUES (2191,'عباد بن شيبان أبو يحيى، روى عنه ابنه يحيى بن عباد ذكره بعض المتأخرين في الصحابة فأسقط جده شيبان فقال: يحيى بن عباد، عن أبيه، عن النبي صلى الله عليه وسلم في أكل السحور حديث أشعث بن سوار','6538','2177','0');
INSERT INTO [t10490] VALUES (2192,'عباد بن سحيم الضبي ذكره ابن أبي عاصم في الصحابة فيما حكاه عنه بعض المتأخرين، وقال البخاري: هو تابعي، ولم يذكر له شيئا','6540','2177','0');
INSERT INTO [t10490] VALUES (2193,'عباد بن جعفر المخزومي روى عنه ابنه محمد ذكره بعض المتأخرين وقال: ذكر في الصحابة ولا يعرف له رواية ولا صحبة، ولم يخرج له شيئا','6541','2177','0');
INSERT INTO [t10490] VALUES (2194,'من اسمه عمر','6542','1723','0');
INSERT INTO [t10490] VALUES (2195,'عمر بن الخطاب العدوي تقدم ذكره في العشرة','6543','2194','0');
INSERT INTO [t10490] VALUES (2196,'عمر بن أبي سلمة بن عبد الأسد ربيب رسول الله صلى الله عليه وسلم وهو عمر بن أبي سلمة بن عبد الأسد بن هلال بن عبد الله بن عمر بن مخزوم أمه: أم سلمة زوجة النبي صلى الله عليه وسلم، واسمها هند بنت أبي أمية، واسم أبي أمية: حذيفة بن المغيرة بن عبد الله بن','6547','2194','0');
INSERT INTO [t10490] VALUES (2197,'عمر بن سعد أبو كبشة الأنماري يعد في الشاميين، له صحبة، مختلف في اسمه، فقالوا: عمير بن سعد، وقالوا: سعد بن عمرو، وقالوا: عمرو بن سعد، نذكر حديثه في الكنى إن شاء الله','6563','2194','0');
INSERT INTO [t10490] VALUES (2198,'عمر بن عوف النخعي ذكره بعض المتأخرين وقال: قيل: عمر، وزعم أن محمد بن إسماعيل ذكره في الصحابة فيمن اسمه عمر، وفيما ذكره عنه نظر، وأخرج له هذا الحديث','6564','2194','0');
INSERT INTO [t10490] VALUES (2199,'عمر بن الحكم السلمي أتى النبي صلى الله عليه وسلم، وهم فيه مالك بن أنس، وصوابه: معاوية بن الحكم','6567','2194','0');
INSERT INTO [t10490] VALUES (2200,'عمر بن يزيد الكعبي جالس النبي صلى الله عليه وسلم','6569','2194','0');
INSERT INTO [t10490] VALUES (2201,'عمر بن الجمعي وصوابه عمرو بن الحمق','6571','2194','0');
INSERT INTO [t10490] VALUES (2202,'عمر بن معاوية الغاضري غاضرة قيس، ذكره بعض المتأخرين، وقال: مختلف في حديثه','6573','2194','0');
INSERT INTO [t10490] VALUES (2203,'عمر بن مالك الأنصاري كان ينزل مصر، ذكره الطبراني في معجمه','6575','2194','0');
INSERT INTO [t10490] VALUES (2204,'عمر بن عامر السلمي سأل النبي صلى الله عليه وسلم، روى عنه سلمة أبو عبد الحميد، ذكره بعض المتأخرين فأخرج له هذا الحديث بعينه، من حديث يحيى بن الورد، ووهم فيه، فإنما هو عمرو بن عبسة السلمي','6577','2194','0');
INSERT INTO [t10490] VALUES (2205,'عمر بن سعد السلمي ذكره الحضرمي في الوحدان، فيه نظر','6579','2194','0');
INSERT INTO [t10490] VALUES (2206,'عمر الأسلمي وقيل: الجهني، غير منسوب، ذكره الحضرمي في الوحدان','6581','2194','0');
INSERT INTO [t10490] VALUES (2207,'عمر بن سالم الخزاعي ذكره بعض المتأخرين وقال: وقيل: عمرو، وافد خزاعة إلى النبي صلى الله عليه وسلم، ولم يختلف فيه أنه عمر بن سالم','6584','2194','0');
INSERT INTO [t10490] VALUES (2208,'عمر بن غزية الخزاعي أتى النبي صلى الله عليه وسلم وبايعه، ذكره بعض المتأخرين، وهو عمرو بن غزية الأنصاري، عقبي، وأخرج له','6586','2194','0');
INSERT INTO [t10490] VALUES (2209,'عمر بن لاحق صاحب النبي صلى الله عليه وسلم فيما قال بعض المتأخرين','6588','2194','0');
INSERT INTO [t10490] VALUES (2210,'عمر بن عمرو الليثي وقيل: عبيد بن عمرو، حديثه عند قرة بن خالد، عن سهل بن علي النميري، عن عبيد بن عمرو','6590','2194','0');
INSERT INTO [t10490] VALUES (2211,'عمر بن عبيد الله بن أبي زياد','6591','2194','0');
INSERT INTO [t10490] VALUES (2212,'من اسمه عثمان','6593','1723','0');
INSERT INTO [t10490] VALUES (2213,'عثمان بن عفان بن أبي العاص بن أمية بن عبد شمس يكنى أبا عبد الله، وقيل: أبو عمرو، ذو النورين مهاجري، ذو الهجرتين، بدري بسهمه وأجره أمه أروى بنت كريز بن حبيب بن عبد شمس بن عبد مناف، وأم أروى أم حكيم بنت عبد المطلب بن هاشم، اسمها البيضاء كان أول من','6594','2212','0');
INSERT INTO [t10490] VALUES (2214,'عثمان بن عامر بن عمرو بن كعب بن سعد بن تيم بن مرة أبو قحافة والد أبي بكر الصديق، أمه أمينة بنت عبد العزى بن حرثان بن عوف بن عبيد بن عويج بن عدي بن كعب، أسلم يوم الفتح، توفي سنة أربع عشرة وهو ابن سبع وتسعين سنة، ورث ابنه أبا بكر، وعاش بعد ابنه أبي','6596','2212','0');
INSERT INTO [t10490] VALUES (2215,'عثمان بن مظعون بن حبيب بن وهب بن حذافة بن جمح بن عمرو بن مصيص بن كعب بن لؤي، يكنى أبا السائب، من مهاجرة الحبشة في الهجرة الأولى، وأميرهم، فقدم مكة قبل الهجرة، فهاجر فيها إلى المدينة فشهد بدرا، كان من رهبان المهاجرين ونساكهم، يقوم الليل ويصوم','6599','2212','0');
INSERT INTO [t10490] VALUES (2216,'عثمان بن حنيف الأنصاري وهو عثمان بن حنيف بن واهب بن العكيم، وقيل: حكيم بن ثعلبة بن مجدعة بن الحارث بن عمرو بن خلاس بن عمرو بن عوف بن مالك بن عمرو بن مالك بن الأوس، استعمله عمر بن الخطاب على العراق، وعمر إلى أيام معاوية، ولاه عمر الكوفة وأمره أن يمسح','6611','2212','0');
INSERT INTO [t10490] VALUES (2217,'عثمان بن طلحة بن أبي طلحة بن عبد العزى بن عثمان بن عبد الله بن عبد الدار بن قصي الحجبي أمه أم سعيد بنت شهيد من بني عمرو بن عوف، أسلم قبل الفتح، كان بالحبشة هو وخالد بن الوليد وعمرو بن العاص فقدموا المدينة، فأسلموا في صفر سنة ثمان من الهجرة فاستبشر','6615','2212','0');
INSERT INTO [t10490] VALUES (2218,'عثمان بن أبي العاص الثقفي وهو عثمان بن أبي العاص بن بشر بن عبيد بن دهمان بن عبد الله بن همام بن أبان بن يسار بن مالك بن حطيط بن جشم بن قسي بن منبه بن بكر بن هوازن بن منصور بن عكرمة بن خصفة بن قيس بن غيلان بن مضر، وفد إلى رسول الله صلى الله عليه وسلم وهو','6618','2212','0');
INSERT INTO [t10490] VALUES (2219,'عثمان بن الأزرق ذكره سليمان بن أحمد في معجمه في الصحابة','6624','2212','0');
INSERT INTO [t10490] VALUES (2220,'عثمان بن عمرو الأنصاري ذكره سليمان في المعجم فيما أرى، وهو النعمان بن عمرو بن رفاعة','6626','2212','0');
INSERT INTO [t10490] VALUES (2221,'عثمان بن عثمان الثقفي ذكره بعض المتأخرين، وقال: عداده في الحمصيين، كان أميرا على صنعاء الشام، فروى له هذا الحديث','6628','2212','0');
INSERT INTO [t10490] VALUES (2222,'عثمان بن شماس بن لبيد مهاجري، بدري، قتل يوم أحد كذا ذكره بعض المتأخرين عن ابن إسحاق، فذكر في ترجمة ابن لبيد، وعن ابن إسحاق: عثمان بن شماس بن الشريد، فإنه خرج مع مصعب بن عمير، وعثمان بن مظعون مهاجرا، وهو وهم فاحش؛ فإنه شماس بن عثمان بعد أن ذكره','6630','2212','0');
INSERT INTO [t10490] VALUES (2223,'عثمان بن قيس بن أبي العاص بن قيس بن عدي له صحبة، شهد فتح مصر مع أبيه ذكر بعض المتأخرين عن أبي سعيد بن عبد الأعلى، وذكر أنه كان على قضاء مصر في زمن عثمان، وقيل: في زمن عمر، وذكر له هذه الحكاية عن الليث بن سعد','6633','2212','0');
INSERT INTO [t10490] VALUES (2224,'عثمان بن عمرو ذكره بعض المتأخرين، وزعم أن له ذكرا في حديث أنس بن مالك','6635','2212','0');
INSERT INTO [t10490] VALUES (2225,'عثمان بن عبيد الله بن الهدير القرشي ذكره بعض المتأخرين، وقال: ذكر فيمن ولد على عهد النبي صلى الله عليه وسلم، ولم يزد عليه','6637','2212','0');
INSERT INTO [t10490] VALUES (2226,'من اسمه علي','6638','1723','0');
INSERT INTO [t10490] VALUES (2227,'علي بن أبي طالب بن عبد المطلب بن هاشم سيد الأصفياء، وعلم الأتقياء، وزين الخلفاء، تقدم ذكره في العشرة، قتل بالكوفة في رمضان سنة أربعين، وقتل وهو ابن ثلاث وستين، شهد بدرا وهو ابن عشرين سنة، ذكرنا سنه، ووفاته، ونسبته، وأولاده في العشرة، قتله عدو','6639','2226','0');
INSERT INTO [t10490] VALUES (2228,'علي بن أبي العاص بن الربيع أمه زينب بنت رسول الله صلى الله عليه وسلم، توفي في عهد النبي صلى الله عليه وسلم، له ذكر، وليس له حديث','6645','2226','0');
INSERT INTO [t10490] VALUES (2229,'علي بن شيبان بن محرز ابن عمرو بن عبد الله بن عمرو بن عبد العزى بن سحيم بن مرة بن الدول بن حنيفة، من ساكني اليمامة، وفد على النبي صلى الله عليه وسلم','6646','2226','0');
INSERT INTO [t10490] VALUES (2230,'علي بن طلق بن المنذر بن قيس بن عمرو بن عبد الله بن عبد العزى بن سحيم بن مرة بن الدول بن حنيفة','6651','2226','0');
INSERT INTO [t10490] VALUES (2231,'علي أبو سدرة السلمي وقيل: علي بن أبي علي، يكنى أبا سدرة، حديثه عند عبد الله بن كثير','6654','2226','0');
INSERT INTO [t10490] VALUES (2232,'علي بن الحكم السلمي يقال: إنه أخو معاوية، حديثه عند يعقوب بن محمد الزهري','6656','2226','0');
INSERT INTO [t10490] VALUES (2233,'علي بن هبار ذكره بعض المتأخرين، وقال: في إسناده نظر، وأخرج له حديث هشيم، عن أبي معشر','6658','2226','0');
INSERT INTO [t10490] VALUES (2234,'علي بن ركانة ذكره بعض المتأخرين، وقال: لا تصح له صحبة، وأخرج له هذا الحديث','6660','2226','0');
INSERT INTO [t10490] VALUES (2235,'علي أبو علي الهلالي ذكره سليمان بن أحمد في الصحابة','6662','2226','0');
INSERT INTO [t10490] VALUES (2236,'من اسمه عمرو','6664','1723','0');
INSERT INTO [t10490] VALUES (2237,'عمرو بن ثابت بن وقش وقيل: ابن أقيش، أصيرم بني عبد الأشهل، استشهد بأحد، حديثه عند أبي هريرة','6665','2236','0');
INSERT INTO [t10490] VALUES (2238,'عمرو بن عوف حليف بني عامر بن لؤي، يقال: إنه مولى سهيل بن عمرو، شهد بدرا','6669','2236','0');
INSERT INTO [t10490] VALUES (2239,'عمرو بن سعيد بن العاص بن أمية بن عبد شمس من مهاجرة الحبشة، استعمله رسول الله صلى الله عليه وسلم على تيماء وخيبر، قتل بأجنادين من أرض الشام في خلافة أبي بكر الصديق رضي الله عنه قال ابن إسحاق: لا عقب له، أخو خالد بن سعيد، ولما أن أسلما قال فيهما','6671','2236','0');
INSERT INTO [t10490] VALUES (2240,'عمرو بن حزم بن زيد بن لوذان الأنصاري من بني مالك بن النجار، أحد عمال النبي صلى الله عليه وسلم على اليمن، سكن المدينة، توفي في خلافة عمر بن الخطاب رضي الله عنه، وقيل: توفي سنة أربع وخمسين، يكنى أبا الضحاك، شهد الخندق هو وزيد بن ثابت، وكان أول مشهد','6674','2236','0');
INSERT INTO [t10490] VALUES (2241,'عمرو بن عبسة السلمي أبو نجيح، قدم مكة على النبي صلى الله عليه وسلم فلقيه بعكاظ ورآه مستخفيا من قريش في أول الدعوة، وهو يقول: أنا رابع الإسلام، ثم رجع إلى أرضه وقومه بني سليم مقيما حتى مضى بدر وأحد والخندق، ثم قدم المدينة فنزلها، وكان قبل أن يسلم','6680','2236','0');
INSERT INTO [t10490] VALUES (2242,'عمرو بن الجموح بن زيد بن حرام السلمي استشهد بأحد فدفن هو وعبد الله بن عمرو بن حرام في قبر واحد، يكنى أبا معاذ، وقال فيه النبي صلى الله عليه وسلم: " سيدكم الأبيض الجعد عمرو بن الجموح " وكان أعرج فقال صلى الله عليه وسلم: " كأني أنظر إليك تمشي برجلك هذه','6687','2236','0');
INSERT INTO [t10490] VALUES (2243,'عمرو بن العاص بن وائل بن هاشم بن سعيد بن سهم بن عمرو بن هصيص بن كعب بن لؤي بن غالب، يكنى أبا عبد الله أمه النابغة من بني عنزة بن أسيد بن ربيعة بن نزار، كان يخضب بالسواد، وخرج إلى الحبشة إلى النجاشي بعد الأحزاب، فأسلم عنده بالحبشة، فأخذه أصحابه','6694','2236','0');
INSERT INTO [t10490] VALUES (2244,'عمرو بن أبي الفغواء الخزاعي حديثه عند ابنه عبد الله، أخو علقمة بن أبي الفغواء بن عمرو بن مازن بن عدي بن عمرو بن ربيعة بن كعب','6707','2236','0');
INSERT INTO [t10490] VALUES (2245,'عمرو بن عمير الأنصاري سكن المدينة، وقيل: عمير بن عمرو وقيل: عامر بن عمير','6709','2236','0');
INSERT INTO [t10490] VALUES (2246,'عمرو بن أمية الضمري وهو عمرو بن أمية بن خويلد بن عبد الله بن إياس بن عبد بن ناشرة بن كعب بن جدي بن ضمرة بن بكر بن عبد مناة بن كنانة بن خزيمة بن مدركة بن إلياس بن مضر، يكنى أبا أمية، بعثه رسول الله صلى الله عليه وسلم وحده عينا إلى قريش فحل حبيب بن عدي','6711','2236','0');
INSERT INTO [t10490] VALUES (2247,'عمرو بن عبد الله القارئ أبو عياض، يعد في الحجازيين، حديثه عند ابنه عياض','6715','2236','0');
INSERT INTO [t10490] VALUES (2248,'عمرو بن أبي عمرو المزني أبو رافع، روى عنه ابنه رافع','6717','2236','0');
INSERT INTO [t10490] VALUES (2249,'عمرو بن شأس الأسلمي','6719','2236','0');
INSERT INTO [t10490] VALUES (2250,'عمرو بن يثربي الضمري حجازي','6721','2236','0');
INSERT INTO [t10490] VALUES (2251,'عمرو بن ثعلبة الجهني حديثه عند وهب بن عطاء بن يزيد الجهني، يعد في الحجازيين','6723','2236','0');
INSERT INTO [t10490] VALUES (2252,'عمرو بن أبي عمرو العجلاني أبو عبد الرحمن، حديثه عند ابنه عبد الرحمن','6725','2236','0');
INSERT INTO [t10490] VALUES (2253,'عمرو بن زائدة بن الأصم ابن أم مكتوم وقيل: عبد الله بن عمرو، وقيل: عمرو بن قيس بن شريح بن مالك، وأم مكتوم اسمها عاتكة','6727','2236','0');
INSERT INTO [t10490] VALUES (2254,'عمرو بن عتبة بن نوفل عداده في الحجازيين','6732','2236','0');
INSERT INTO [t10490] VALUES (2255,'عمرو بن أخطب أبو زيد الأنصاري، سكن البصرة، روى عنه أنس بن سيرين، وعلباء بن أحمر، ومعاوية بن قرة، وأبو قلابة، وأبو نهيك القاسم','6734','2236','0');
INSERT INTO [t10490] VALUES (2256,'عمرو بن حريث بن عمرو بن عثمان بن عبد الله بن عمر المخزومي، سكن الكوفة، يكنى أبا سعيد، مات سنة خمس وثمانين حملت أمه عام بدر، وقيل: بل توفي النبي صلى الله عليه وسلم وله اثنا عشر سنة، مسح النبي صلى الله عليه وسلم رأسه، ودعا له بالبركة في صفقته وبيعته','6738','2236','0');
INSERT INTO [t10490] VALUES (2257,'عمرو بن الحارث بن المصطلق الخزاعي أخو جويرية أم المؤمنين، يعد في الكوفيين','6743','2236','0');
INSERT INTO [t10490] VALUES (2258,'عمرو بن الأحوص أبو سليمان الجشمي، حديثه عند ابنه سليمان','6746','2236','0');
INSERT INTO [t10490] VALUES (2259,'عمرو بن سراقة بن المعتمر ابن أنس بن أداة بن رزاح بن عدي بن كعب بن لؤي، أخو عبد الله بن سراقة، شهد بدرا، ووهم بعض المتأخرين، فقال: عمرو بن سراقة الأنصاري، واختلط فإنما هو عدوي من بني عدي بن كعب من رهط عمر بن الخطاب، ذكره في حديث عامر بن ربيعة العدوي','6749','2236','0');
INSERT INTO [t10490] VALUES (2260,'عمرو بن سنان الخدري ذكره في حديث أبي سعيد الخدري','6753','2236','0');
INSERT INTO [t10490] VALUES (2261,'عمرو بن تغلب النمري وقيل: العبدي، هو من النمر بن قاسط بن ربيعة، وقيل: هو من بكر بن وائل روى عنه الحسن بن الحسن، سكن البصرة وكله رسول الله صلى الله عليه وسلم إلى ما جعل الله في قلبه من الإيمان','6755','2236','0');
INSERT INTO [t10490] VALUES (2262,'عمرو بن الحمق الخزاعي وهو عمرو بن الحمق بن الكاهن بن حبيب بن عمرو بن ربيعة بن كعب الخزاعي، سكن الكوفة، ثم انتقل إلى مصر، روى عنه رفاعة القتباني، وجبير بن نفير، وغيرهما، كان أول رأس أهدي في الإسلام رأس عمرو بن الحمق، أصابته لدغة فتوفي فخافت الرسل','6758','2236','0');
INSERT INTO [t10490] VALUES (2263,'عمرو بن خارجة الأشعري وقيل: الأنصاري، حليف أبي سفيان بن حرب بن أمية بن عبد شمس، وقيل: خارجة بن عمرو، والصحيح: عمرو بن خارجة، يعد في الشاميين، حديثه عند عبد الرحمن بن غنم','6763','2236','0');
INSERT INTO [t10490] VALUES (2264,'عمرو بن عوف بن زيد بن ملحة المزني يكنى أبا عبد الله، وكان قديم الإسلام، سكن المدينة، أدرك معاوية، وتوفي في ولايته','6766','2236','0');
INSERT INTO [t10490] VALUES (2265,'عمرو بن مرة الجهني وقيل: الأسدي، يكنى أبا مريم، سكن فلسطين، حدث عنه عيسى بن طلحة، والربيع بن سبرة، وأبو الحسن الجزري','6772','2236','0');
INSERT INTO [t10490] VALUES (2266,'عمرو بن سالم الخزاعي الكلبي الشاعر، رسول بني خزاعة إلى رسول الله صلى الله عليه وسلم؛ لاستنصارهم به على قريش حين أخفروا ذمته، ذكره في حديث المسور بن مخرمة، ومروان، وغيرهم','6777','2236','0');
INSERT INTO [t10490] VALUES (2267,'عمرو بن عامر بن مالك بن خنساء بن مبذول بن عمرو بن تميم بن مازن بن النجار أبو داود المازني، شهد بدرا، نسبه محمد بن يحيى الذهلي فيما حكى عنه بعض المتأخرين، وقال محمد بن إسحاق: اسمه عمير','6779','2236','0');
INSERT INTO [t10490] VALUES (2268,'عمرو بن معاذ بن النعمان بن امرئ القيس، أخو سعد بن معاذ، شهد بدرا واستشهد بأحد','6781','2236','0');
INSERT INTO [t10490] VALUES (2269,'عمرو بن سعد بن معاذ الأشهلي أبو واقد حكاه عبد الله بن أبي داود السجستاني، فيما أخبرنيه محمد بن محمد بن يعقوب الحجاجي إجازة عنه، قال: ومن بني عبد الأشهل: سعد بن معاذ، وولده عبد الله، وعمرو، هكذا في كتاب ابن القداح، قال: ورأيت سعدا في النوم فقلت','6783','2236','0');
INSERT INTO [t10490] VALUES (2270,'عمرو بن أبي سفيان سمع النبي صلى الله عليه وسلم نهى عن أن يشرب من ثلمة القدح ذكره بعض المتأخرين، وقال: روى حديثه روح بن عبادة، عن ابن جريج، عن عبد الملك بن عبد الله بن أبي سفيان، عن عمه عمرو بن أبي سفيان هذا، ولم يزد عليه','6785','2236','0');
INSERT INTO [t10490] VALUES (2271,'عمرو بن سفيان الثقفي شهد حنينا مع المشركين، يعد في الشاميين، حديثه عند القاسم أبي عبد الرحمن، كذا ذكره القاضي أبو أحمد أنه شهد حنينا مع المشركين، أسلم بعد حنين','6787','2236','0');
INSERT INTO [t10490] VALUES (2272,'عمرو بن بلال بن بليل أبو ليلى الأنصاري، مختلف في اسمه، وقيل: اسمه داود، وقيل: سفيان، وقيل: يسار، وقيل: أوس، وقيل: بلال. روى عنه ابنه عبد الرحمن ابن أبي ليلى، تقدم حديثه، وقيل: عمرو بن بليل بن أحيحة بن الجلاح بن الحريش بن جحجبا، شهد أحدا،','6790','2236','0');
INSERT INTO [t10490] VALUES (2273,'عمرو بن كعب اليامي وقيل: كعب بن عمرو اليامي جد طلحة بن مصرف','6791','2236','0');
INSERT INTO [t10490] VALUES (2274,'عمرو بن معدي كرب بن عبد الله بن عمرو بن عصم بن عمرو بن زبيد أبو ثور الزبيدي، له الوقائع المذكورة في الجاهلية، وأدرك الإسلام فقدم على النبي صلى الله عليه وسلم وعلمه التلبية وله في الإسلام بالقادسية بلاء حسن حين بعثه عمر بن الخطاب إلى سعد بن أبي وقاص،','6793','2236','0');
INSERT INTO [t10490] VALUES (2275,'عمرو بن سفيان أبو الأعور السلمي وهو عمرو بن سفيان بن عبد شمس بن سعيد بن الأوقص بن مرة بن هلال بن فالح بن ذكوان بن ثعلبة بن بهثة بن سليم، وأمه قريبة بنت قيس بن عبد قيس بن عدي بن سعيد بن سهم بن عمرو بن هصيص، حليف بني أمية','6795','2236','0');
INSERT INTO [t10490] VALUES (2276,'عمرو بن عبيد الله الحضرمي قيل: إنه رأى النبي صلى الله عليه وسلم، ولا يصح حديثه أبو بكر بن خزيمة: لا أدري أمن المدينة هو أم من غيرها؟','6798','2236','0');
INSERT INTO [t10490] VALUES (2277,'عمرو بن سفيان المحاربي يعد في البصريين، وقيل: عمرو بن شفي المحاربي','6800','2236','0');
INSERT INTO [t10490] VALUES (2278,'عمرو بن أبي أراكة ذكره بعض المتأخرين، وزعم أنه سمع النبي صلى الله عليه وسلم ينهى عن المثلة. قال محمد بن إسماعيل: عمرو بن أراكة سكن البصرة، روى عن النبي صلى الله عليه وسلم','6802','2236','0');
INSERT INTO [t10490] VALUES (2279,'عمرو بن أوس الثقفي نزل الطائف قدم على رسول الله صلى الله عليه وسلم، روى عنه ابنه عثمان، وقيل: عن عثمان بن عبد الله بن أوس، عن عمرو بن أوس، عن أبيه، وقد تقدم فيما ذكره، ولم يزد عليه، والصواب: عمرو بن أوس، عن أبيه','6804','2236','0');
INSERT INTO [t10490] VALUES (2280,'عمرو بن سلمة بن نفيع بن لائم بن قدامة بن جرم الجرمي أبو بريد، حديثه عند أبي قلابة، وأيوب، وعاصم إمام بني جرم على عهد رسول الله صلى الله عليه وسلم، أمهم سبع سنين في حياته، فلم يزل إمامهم في المكتوبة، وفي جنائزهم إلى أن مات، وهو أول من كسي بالأمانة','6806','2236','0');
INSERT INTO [t10490] VALUES (2281,'عمرو بن يعلى الثقفي ذكر أنه حضر الصلاة مع النبي صلى الله عليه وسلم روى عنه عمرو بن دينار، ذكره بعض المتأخرين، وقال: ذكر في الصحابة، ولا يصح، ولم يزد عليه','6813','2236','0');
INSERT INTO [t10490] VALUES (2282,'عمرو بن محصن الأسدي من المهاجرين الأولين من بني غنم بن دودان','6815','2236','0');
INSERT INTO [t10490] VALUES (2283,'عمرو بن مطرف بن عمرو وقيل: ابن علقمة الأنصاري من بني عمرو بن مبذول، استشهد بأحد مع رسول الله صلى الله عليه وسلم','6817','2236','0');
INSERT INTO [t10490] VALUES (2284,'عمرو بن غزية بن عمرو بن ثعلبة بن خنساء بن مبذول بن عمرو بن غنم بن مازن بن النجار، شهد العقبة، نزلت فيه: أقم الصلاة طرفي النهار','6820','2236','0');
INSERT INTO [t10490] VALUES (2285,'عمرو بن النعمان بن مقرن وقيل: النعمان بن عمرو، ذكره بعض المتأخرين، وقال: روى حديثه بكر بن خلف، عن العلاء بن عبد الجبار، عن عبد الواحد بن زياد، عن الأعمش، عن أبي خالد الوالبي، عن عمرو بن النعمان، قال بكر بن خلف: وله صحبة، ولم يتابع عليه، ولم','6822','2236','0');
INSERT INTO [t10490] VALUES (2286,'عمرو بن سفيان البكالي سكن الشام، قيل: له صحبة، واختلف فيه، وقال حفص بن غياث: كان قد رأى النبي صلى الله عليه وسلم، وكذا ذا فقه، روى عنه أبو تميمة الهجيمي، ومعدان بن أبي طلحة','6824','2236','0');
INSERT INTO [t10490] VALUES (2287,'عمرو أبو فراس الليثي روى عنه أبو الطفيل عامر بن واثلة','6828','2236','0');
INSERT INTO [t10490] VALUES (2288,'عمرو بن الطفيل روى عنه أبو أمامة الباهلي','6830','2236','0');
INSERT INTO [t10490] VALUES (2289,'عمرو بن مخزوم الغاضري أدرك النبي صلى الله عليه وسلم، ودخل ناحية أصبهان وأرجان، في ولاية عمر بن الخطاب له ذكر، ولا رواية له، وهو الذي يسمى عقبة مارت بهذا الاسم، شق عليه صعودها، فقال لدليله الذي أخذ به عليها: ما أردت؟ فسمى العقبة عقبة مارت، كذا','6832','2236','0');
INSERT INTO [t10490] VALUES (2290,'عمرو بن ثعلبة بن وهب بن عدي بن مالك بن النجار الأنصاري، شهد بدرا، يكنى أبا الحكم، وقيل: أبو حكيمة','6833','2236','0');
INSERT INTO [t10490] VALUES (2291,'عمرو بن قيس بن سواد النجاري شهد بدرا واستشهد بأحد، أبو قيس','6837','2236','0');
INSERT INTO [t10490] VALUES (2292,'عمرو بن خارجة بن قيس الأنصاري النجاري، شهد بدرا','6839','2236','0');
INSERT INTO [t10490] VALUES (2293,'عمرو بن إياس من بني لوذان بن غنم، حليف لهم','6841','2236','0');
INSERT INTO [t10490] VALUES (2294,'عمرو بن أبي سرح الفهري شهد بدرا، وهو عمرو بن أبي سرح بن ربيعة بن هلال بن أهيب بن ضبة بن الحارث بن فهر','6844','2236','0');
INSERT INTO [t10490] VALUES (2295,'عمرو بن مازن من بني خنساء بن مبذول، شهد بدرا، قاله محمد بن إسحاق، فيما حكاه عنه بعض المتأخرين، وقال: لا يعرف له رواية، ووهم فيه؛ لأن عمرو بن غنم بن مازن جد خنساء الذي ينتسب إليه بنو خنساء بن مبذول بن عمرو بن غنم بن مازن، هكذا قاله ابن إسحاق،','6846','2236','0');
INSERT INTO [t10490] VALUES (2296,'عمرو بن سهل، وقيل: سهيل الأنصاري سمع النبي صلى الله عليه وسلم يحث على صلة القرابة، روى حديثه حنان بن سدير، عن عبد الرحمن بن الغسيل، عنه مرسلا، فيما ذكره بعض المتأخرين، ولم يزد عليه','6847','2236','0');
INSERT INTO [t10490] VALUES (2297,'عمرو بن غيلان الثقفي يعد في الشاميين، حديثه عند أبي عبيد الله مسلم بن مشكم، مختلف في صحبته','6848','2236','0');
INSERT INTO [t10490] VALUES (2298,'عمرو بن جدعان روى عنه أبو هريرة','6850','2236','0');
INSERT INTO [t10490] VALUES (2299,'عمرو الثمالي وقيل: اليماني، حديثه عند شهر بن حوشب','6853','2236','0');
INSERT INTO [t10490] VALUES (2300,'عمرو بن صليع من محارب خصفة، سمع حذيفة، وعائشة، ذكر بعض المتأخرين أن له صحبة','6855','2236','0');
INSERT INTO [t10490] VALUES (2301,'عمرو ذو النور وهو ابن الطفيل الدوسي، نسبه موسى بن سهل الرملي، كان النبي صلى الله عليه وسلم دعا له، واستشهد يوم اليرموك، وذو النور هو أبوه الطفيل بن عمرو الدوسي، ذكرنا ذلك في قصة الطفيل، وابنه عمرو مختلف في صحبته','6857','2236','0');
INSERT INTO [t10490] VALUES (2302,'عمرو بن عطية غير منسوب، ذكره الطبراني في الصحابة في معجمه','6859','2236','0');
INSERT INTO [t10490] VALUES (2303,'عمرو بن مالك الرواسي ذكره سليمان','6861','2236','0');
INSERT INTO [t10490] VALUES (2304,'عمرو بن مالك الأشجعي ذكره ابن أبي شيبة في المقلين','6863','2236','0');
INSERT INTO [t10490] VALUES (2305,'عمرو بن أبي خزاعة اختصم إلى النبي صلى الله عليه وسلم في قتيل، ذكره بعض المتأخرين','6865','2236','0');
INSERT INTO [t10490] VALUES (2306,'عمرو بن معاذ الأنصاري تفل رسول الله صلى الله عليه وسلم على رجله لما قطع فبرأ، وقيل: إنه المتقدم أخو سعد بن معاذ','6867','2236','0');
INSERT INTO [t10490] VALUES (2307,'عمرو بن حزابة بن نعيم ولد على عهد النبي صلى الله عليه وسلم، ذكره بعض المتأخرين','6869','2236','0');
INSERT INTO [t10490] VALUES (2308,'عمرو بن نضلة روى معاذ بن رفاعة، عن أبي عبيد الحاجب، عن عمرو بن نضلة، ورواه الأوزاعي، عن أبي عبيد، عن عبيد بن نضلة، وهو الصحيح','6871','2236','0');
INSERT INTO [t10490] VALUES (2309,'عمرو بن مرداس السلمي له ذكر في جملة المؤلفة قلوبهم، ذكره بعض المتأخرين من حديث محمد بن مروان، عن الكلبي، عن أبي صالح، عن ابن عباس، ووهم؛ لأنه العباس بن مرداس، لا يختلف فيه','6872','2236','0');
INSERT INTO [t10490] VALUES (2310,'عمرو بن الأهتم التميمي يذكر في المؤلفة','6874','2236','0');
INSERT INTO [t10490] VALUES (2311,'عمرو أبو سعيد الأنصاري شهد بدرا فيما قاله بعض المتأخرين','6877','2236','0');
INSERT INTO [t10490] VALUES (2312,'عمرو العجلاني الأنصاري ذكره ابن أبي عاصم، وسليمان الطبراني','6879','2236','0');
INSERT INTO [t10490] VALUES (2313,'عمرو بن حماس الليثي غير محفوظ، لا يصح له صحبة، ذكره بعض المتأخرين، وقيل: أبو عمرو بن خماس، وهو المشهور','6881','2236','0');
INSERT INTO [t10490] VALUES (2314,'عمرو بن حبيب وقيل: ابن أبي حبيب، وقيل: ابن أبي جندب، عداده في الشاميين، ذكره الحسن بن سفيان في الوحدان','6883','2236','0');
INSERT INTO [t10490] VALUES (2315,'عمرو بن شغوى اليافعي وقيل: شعوى، شهد فتح مصر يعد في الصحابة، أخرج عنه سليمان، وغيره من المتأخرين','6885','2236','0');
INSERT INTO [t10490] VALUES (2316,'عمرو أبو زرعة','6887','2236','0');
INSERT INTO [t10490] VALUES (2317,'عمرو بن أبي الأسد ذكره الحسن بن سفيان، والمنيعي في الوحدان','6889','2236','0');
INSERT INTO [t10490] VALUES (2318,'أبو السنابل بن بعكك قيل: إن اسمه عمرو، نذكر حديثه في الكنى إن شاء الله','6891','2236','0');
INSERT INTO [t10490] VALUES (2319,'عمرو بن حنة الأنصاري ذكره سليمان بن أحمد في معجمه','6892','2236','0');
INSERT INTO [t10490] VALUES (2320,'عمرو بن سعيد الهذلي أبو سعيد','6894','2236','0');
INSERT INTO [t10490] VALUES (2321,'عمرو أبو عطية السعدي روى حديثه ابنه عطية','6896','2236','0');
INSERT INTO [t10490] VALUES (2322,'عمرو بن قرة سأل النبي صلى الله عليه وسلم عن كسبه','6898','2236','0');
INSERT INTO [t10490] VALUES (2323,'عمرو بن شراحيل ذكره سليمان في معجمه','6900','2236','0');
INSERT INTO [t10490] VALUES (2324,'عمرو الجني ذكره سليمان في معجمه، في إسناده نظر في سجوده مع النبي صلى الله عليه وسلم في سورة والنجم','6902','2236','0');
INSERT INTO [t10490] VALUES (2325,'عمرو بن سمرة بن حبيب بن عبد شمس قيل: عمرو بن حبيب الأقطع، له ذكر في حديث ثعلبة أبي عبد الرحمن حدثناه أبو عمرو بن حمدان، قال: ثنا الحسن بن سفيان، ثنا حرملة بن يحيى، ثنا عبد الله بن وهب، أخبرني ابن لهيعة، عن يزيد بن أبي حبيب، عن عبد الرحمن بن','6903','2236','0');
INSERT INTO [t10490] VALUES (2326,'عمرو بن سفيان العوفي وقيل: عمرو بن سليمان، ذكره ابن أبي عاصم في الآحاد، وقال البخاري: هو تابعي روى عنه بشر بن عبد الله، لا يعرف له صحبة','6904','2236','0');
INSERT INTO [t10490] VALUES (2327,'عمرو بن بداح القيسي له ذكر في حديث المشمرج بن خالد، ذكره بعض المتأخرين','6906','2236','0');
INSERT INTO [t10490] VALUES (2328,'عمرو بن ميمون الأودي أدرك الجاهلية، وأسلم في حياة النبي صلى الله عليه وسلم، وكان قد حج مائة حج وعمرة، قال حجاج بن محمد، عن شعبة، عن عمرو بن مرة، قال: أدرك عمرو بن ميمون النبي صلى الله عليه وسلم','6908','2236','0');
INSERT INTO [t10490] VALUES (2329,'عمرو بن مالك ملاعب الأسنة التمس من رسول الله صلى الله عليه وسلم دواء، حديثه عند خشرم بن حسان فيما رواه بعض المتأخرين من حديث الزبيري، عن مسعر عنه، والصحيح: رواية الأشجعي، وعفان بن سيار، وغيرهما، عن مسعر، عن خشرم، عن عامر بن مالك، فذكره فيمن','6914','2236','0');
INSERT INTO [t10490] VALUES (2330,'عمرو بن زرارة أمره النبي صلى الله عليه وسلم برفع إزاره','6915','2236','0');
INSERT INTO [t10490] VALUES (2331,'من اسمه عامر','6917','1723','0');
INSERT INTO [t10490] VALUES (2332,'عامر بن عبد الله بن الجراح ابن هلال بن وهيب بن ضبة بن الحارث بن فهر بن مالك بن النضر بن كنانة، يكنى أبا عبيدة، أمه أم غنم، وقيل: أميمة بنت غنم بن جابر بن عبد العزيز بن عامر بن عميرة بن وديعة بن الحارث بن فهر، مهاجري ذو الهجرتين، شهد بدرا، شهد له','6918','2331','0');
INSERT INTO [t10490] VALUES (2333,'عامر بن ربيعة العدوي حليف لهم، ذو الهجرتين، شهد بدرا، مختلف في نسبته، فقيل: من عنز بن وائل، وقيل: من اليمن، يكنى أبا عبد الله، توفي سنة اثنين وثلاثين حين نشبوا في الفتنة على عثمان، فمن نسبه إلى عنز بن وائل، قال: هو فيما','6920','2331','0');
INSERT INTO [t10490] VALUES (2334,'عامر بن فهيرة مولى أبي بكر الصديق رضي الله عنه من المهاجرين الأولين هاجر مع رسول الله صلى الله عليه وسلم، وأبي بكر رضي الله عنه من مكة، بدري، استشهد ببئر معونة روى عنه عائشة، وعبد الرحمن بن عوف، وجابر بن عبد الله','6928','2331','0');
INSERT INTO [t10490] VALUES (2335,'عامر بن الأكوع عم سلمة بن عمرو، واسم الأكوع: سنان بن عبد الله بن بشير بن خزيمة بن مالك بن سلامان بن أسلم بن أفصى، قاله محمد بن سعد الواقدي، استشهد مع النبي صلى الله عليه وسلم بخيبر، كان شاعرا يحدو بمن مع رسول الله صلى الله عليه وسلم في غزوة خيبر','6938','2331','0');
INSERT INTO [t10490] VALUES (2336,'عامر بن شهر الهمداني يكنى أبا الكنود البكيلي، كوفي، حديثه عند الشعبي','6945','2331','0');
INSERT INTO [t10490] VALUES (2337,'عامر بن قيس الأشعري أبو بردة، أخو أبي موسى. قال مسلم بن الحجاج: اسمه عامر، وله صحبة','6947','2331','0');
INSERT INTO [t10490] VALUES (2338,'أبو حبة البدري قيل: إن اسمه عامر بن عمرو بن ثابت، وقيل: عامر بن عبد عمرو بن ثابت بن كلفة بن ثعلبة بن مالك بن عمرو بن عوف بن مالك بن الأوس فيما ذكره بعض المتأخرين، أخو سعد بن خيثمة لأمه، أمهما هند بنت أوس بن عدي بن أمية بن عامر بن خطمة، شهد بدرا،','6950','2331','0');
INSERT INTO [t10490] VALUES (2339,'عامر أبو هشام الأنصاري استشهد بأحد مع النبي صلى الله عليه وسلم','6952','2331','0');
INSERT INTO [t10490] VALUES (2340,'عامر بن حذيفة أبو جهم، وقيل: عبيد، تقدم ذكره','6956','2331','0');
INSERT INTO [t10490] VALUES (2341,'عامر بن البكير بن عبد ياليل بن ناشب بن عنزة بن سعد بن ليث، أخو عاقل، وخالد، وإياس، حليف بني عدي، شهد بدرا','6957','2331','0');
INSERT INTO [t10490] VALUES (2342,'عامر بن الحارث الفهري من بني الحارث بن فهر بن مالك، شهد بدرا، ذكره بعض المتأخرين، عن يونس بن بكير، عن ابن إسحاق، وقال إبراهيم بن سعد، عن إسحاق: هو عامر بن عبد الله بن الجراح أبو عبيدة، وقال: موسى بن عقبة، عن ابن شهاب: هو ابن عمرو بن عامر بن','6960','2331','0');
INSERT INTO [t10490] VALUES (2343,'عامر بن مخلد بن الحارث من بني سواد بن مالك، شهد بدرا، وقتل بأحد، لا عقب له','6961','2331','0');
INSERT INTO [t10490] VALUES (2344,'عامر بن سلمة الأنصاري شهد بدرا، من بني عدي بن مالك','6964','2331','0');
INSERT INTO [t10490] VALUES (2345,'عامر بن أمية بن زيد بن الخشخاش لا عقب له، شهد بدرا من بني عدي بن النجار','6967','2331','0');
INSERT INTO [t10490] VALUES (2346,'عامر بن عوف بن حارثة بن عمرو بن الخزرج الأنصاري، شهد بدرا','6970','2331','0');
INSERT INTO [t10490] VALUES (2347,'عامر بن مالك ملاعب الأسنة، حديثه عند خشرم بن حسان الجعفري','6972','2331','0');
INSERT INTO [t10490] VALUES (2348,'عامر بن عمير النميري شهد حجة الوداع مع رسول الله صلى الله عليه وسلم، يعد في الكوفيين','6975','2331','0');
INSERT INTO [t10490] VALUES (2349,'عامر بن أبي أمية أخو أم سلمة، ذكره بعض المتأخرين، وزعم أنه أدرك النبي صلى الله عليه وسلم','6978','2331','0');
INSERT INTO [t10490] VALUES (2350,'عامر بن عبد الله البدري ذكره سليمان بن أحمد في معجمه','6980','2331','0');
INSERT INTO [t10490] VALUES (2351,'عامر الرام أخو الخضر، حي من محارب','6982','2331','0');
INSERT INTO [t10490] VALUES (2352,'عامر بن عبدة الرقاشي عم أبي حرة الرقاشي، روى حديثه واصل بن عبد الرحمن، عن أبي حرة، عن عمه، مختلف في اسمه، فيما ذكره بعض المتأخرين','6984','2331','0');
INSERT INTO [t10490] VALUES (2353,'عامر بن مسعود الجمحي عداده في الكوفيين، وهو عامر بن مسعود بن أمية بن خلف بن وهب بن حذافة بن جمح، ابن أخي صفوان بن أمية، وهو والد إبراهيم بن عامر، كان يلي لعبد الله بن الزبير على أهل الكوفة، ثم عزله بعبد الله بن يزيد الخطمي، مختلف في صحبته، حديثه','6985','2331','0');
INSERT INTO [t10490] VALUES (2354,'عامر المزني أبو هلال','6987','2331','0');
INSERT INTO [t10490] VALUES (2355,'عامر بن لقيط العامري ذكره سليمان في معجمه','6990','2331','0');
INSERT INTO [t10490] VALUES (2356,'عامر بن واثلة البكري يكنى أبا الطفيل، وهو عامر بن واثلة بن عبد الله بن حميس بن جدي بن سعد بن ليث، وقال بعضهم بدل حميس بن جدي بن عمير بن جابر، مولده عام أحد، أدرك من زمان النبي صلى الله عليه وسلم ثمان سنين، وقال: كنت في حياة النبي صلى الله عليه وسلم','6992','2331','0');
INSERT INTO [t10490] VALUES (2357,'عامر بن الحارث بن كلثوم الأشعري يكنى أبا مالك، قدم على رسول الله صلى الله عليه وسلم في السفينة، ذكره بعض المتأخرين، وزعم أنه ممن قدم مصر. حدث عنه من المصريين: إبراهيم بن مقسم مولى هذيل، ومن الشاميين: عبد الرحمن بن غنم، وأبو سلام الحبشي، وحال بكل','6999','2331','0');
INSERT INTO [t10490] VALUES (2358,'عامر بن لدين الأشعري مختلف في صحبته، وهو معدود في تابعي أهل الشام','7000','2331','0');
INSERT INTO [t10490] VALUES (2359,'عامر بن مطر الشيباني ذكره سليمان في معجمه، مختلف في صحبته','7001','2331','0');
INSERT INTO [t10490] VALUES (2360,'عامر بن عمرو بن حذافة التجيبي أبو بلال','7003','2331','0');
INSERT INTO [t10490] VALUES (2361,'وعامر بن عبد الله بن جهم الخولاني','7004','2331','0');
INSERT INTO [t10490] VALUES (2362,'وعامر بن الحارث بن ثوبان ذكرهم المحيل بذكرهم على أبي سعيد بن عبد الأعلى، وحكى عنه أنه قال: شهدوا فتح مصر ولا نعرف لواحد منهم رواية','7005','2331','0');
INSERT INTO [t10490] VALUES (2363,'عمار بن ياسر حليف بني مخزوم، وقيل: هو مولاهم، وهو عمار بن ياسر بن مالك بن حصين بن ثعلبة بن مالك بن آدد، وقال ابن الكلبي: هو من عبس بن زيد بن مذحج، لم يشهد بدرا ابن مؤمنين غير عمار، من السابقين الأولين، والمعذبين في الله، ذو الهجرتين، مختلف في','7006','2331','0');
INSERT INTO [t10490] VALUES (2364,'*','7022','1723','0');
INSERT INTO [t10490] VALUES (2365,'عمار بن سعد القرظ المؤذن له رؤية فيما ذكره بعض المتأخرين، وأخرج له هذا الحديث من حديث عبد الرحمن بن سعد','7022','2364','0');
INSERT INTO [t10490] VALUES (2366,'عمار بن عبيد الخثعمي وقيل: عمارة، وهو المشهور الأصح، يعد في الشاميين، حديثه عند داود بن أبي هند في الفتن، نذكره فيمن اسمه عمارة','7025','2364','0');
INSERT INTO [t10490] VALUES (2367,'عمار بن معاذ بن زرارة بن عمرو بن غنم بن عدي بن الحارث بن مرة الظفري أبو نملة، كذا نسبه ابن أبي داود، وسماه فيما حكى عنه بعض المتأخرين، وخالف غيره، روى عنه ابنه نملة. روى يونس، وشعيب، ومعمر، وغيرهم، عن الزهري، عن نملة بن أبي نملة، عن أبيه، عن','7026','2364','0');
INSERT INTO [t10490] VALUES (2368,'عمارة بن حزم الأنصاري عقبي، بدري، شهد بدرا، استشهد باليمامة سنة إحدى عشرة، أمه وأم أخويه عمرو ومعمر: خالدة بنت أنس بن شيبان بن وهب بن لوذان','7027','2364','0');
INSERT INTO [t10490] VALUES (2369,'عمارة بن ثابت الأنصاري أخو خزيمة بن ثابت، روى عنه عمارة بن خزيمة بن ثابت','7034','2364','0');
INSERT INTO [t10490] VALUES (2370,'عمارة بن رؤيبة الثقفي حديثه عند ابنه أبي بكر، والشعبي، وحصين، وزياد، عن علاقة، وابن إسحاق، وعبد الملك بن عمير','7037','2364','0');
INSERT INTO [t10490] VALUES (2371,'عمارة بن أوس بن خالد بن عبيد بن أمية بن عامر بن خطمة الأنصاري، أحد من صلى القبلتين','7042','2364','0');
INSERT INTO [t10490] VALUES (2372,'عمارة بن عقبة بن حارثة بن عقار بن مليل بن ضمرة الغفاري، استشهد مع رسول الله صلى الله عليه وسلم بخيبر','7044','2364','0');
INSERT INTO [t10490] VALUES (2373,'عمارة بن الوليد بن عقبة ابن أبي معيط، أبو مدرك القرشي، روى عنه ابنه مدرك، يعد في الكوفيين','7046','2364','0');
INSERT INTO [t10490] VALUES (2374,'عمارة بن زياد بن السكن بن امرئ القيس الأنصاري الأشهلي، استشهد بأحد','7048','2364','0');
INSERT INTO [t10490] VALUES (2375,'عمارة بن مخلد بن الحارث وقيل: عامر، استشهد بأحد','7051','2364','0');
INSERT INTO [t10490] VALUES (2376,'عمارة بن عبيد الخثعمي وقيل: عمار بن عبيدة، مختلف في صحبته','7053','2364','0');
INSERT INTO [t10490] VALUES (2377,'عمارة بن زعكرة يعد في الشاميين، حديثه عند عبد الرحمن بن عائذ','7056','2364','0');
INSERT INTO [t10490] VALUES (2378,'عمارة بن أحمر المازني يعد في البصريين، ذكره البخاري في الصحابة في الوحدان','7058','2364','0');
INSERT INTO [t10490] VALUES (2379,'عمارة بن أبي حسن المازني الأنصاري يعد في المدنيين، ذكره بعض المتأخرين، وقال أبو أحمد في تاريخه: له صحبة، عقبي، بدري، وفيه نظر، حديثه عند ابنه يحيى، رواه إسماعيل بن أبي أويس، عن أخيه','7060','2364','0');
INSERT INTO [t10490] VALUES (2380,'عمارة بن شبيب السيائي الأنصاري وقيل: عمار، روى عنه أبو عبد الرحمن الجبلي، عداده في المصريين، ذكره المحيل عن أبي سعيد بن عبد الأعلى، ولم يزد عليه','7062','2364','0');
INSERT INTO [t10490] VALUES (2381,'من اسمه عمير','7063','1723','0');
INSERT INTO [t10490] VALUES (2382,'عمير بن أبي وقاص الزهري أخو سعد، مهاجري أولي، استشهد ببدر مع رسول الله صلى الله عليه وسلم، استصغره النبي صلى الله عليه وسلم عن بدر فبكى، ثم أجازه وعقد عليه حمائل سيفه','7064','2381','0');
INSERT INTO [t10490] VALUES (2383,'عمير بن عامر بن مالك بن خنساء بن مبذول الأنصاري أبو داود المازني، من بني مازن بن النجار، شهد بدرا','7069','2381','0');
INSERT INTO [t10490] VALUES (2384,'عمير بن سعد الأنصاري يقال له: نسيج وحده، استعمله عمر بن الخطاب رضي الله عنه على حمص، قال الواقدي: هو عمير بن سعد بن عبيد بن النعمان بن قيس بن عمرو بن عوف، وكان أبوه سعد شهد بدرا، وهو سعد القارئ، الذي جمع القرآن على عهد رسول الله صلى الله عليه وسلم','7074','2381','0');
INSERT INTO [t10490] VALUES (2385,'عمير الأنصاري وقيل: ابن نيار، ابن أخي أبي بردة بن نيار، شهد بدرا فيما ذكره بعض المتأخرين، عن صالح بن محمد جزرة، مختلف في حديثه، حديثه عند ابنه سعيد','7076','2381','0');
INSERT INTO [t10490] VALUES (2386,'عمير بن حبيب الخطمي الأنصاري وهو عمير بن حبيب بن خماشة بن جويبر بن عبيد بن عنان بن عامر بن خطمة، قاله محمد بن سعد الواقدي، بايع تحت الشجرة، جد أبي جعفر الخطمي','7078','2381','0');
INSERT INTO [t10490] VALUES (2387,'عمير بن سلمة الضمري يعد في الحجازيين، مختلف في حديثه، روى عنه عيسى بن طلحة بن عبيد الله وهو عمير بن سلمة بن منتاب بن طلحة بن جري بن ضمرة بن بكر بن عبد مناة بن كنانة بن خزيمة بن مدركة بن إلياس بن مضر','7081','2381','0');
INSERT INTO [t10490] VALUES (2388,'عمير بن الحارث بن ثعلبة بن الحارث بن حرام الأنصاري من بني جشم من الخزرج، ثم من بني سلمة بن سعد، شهد بدرا','7083','2381','0');
INSERT INTO [t10490] VALUES (2389,'عمير بن الحمام بن الجموح الأنصاري شهد بدرا','7086','2381','0');
INSERT INTO [t10490] VALUES (2390,'عمير بن عمرو الليثي من مسلمة الفتح، وقيل عمر','7089','2381','0');
INSERT INTO [t10490] VALUES (2391,'عمير بن قتادة يكنى أبا عبيد، وهو عمير بن قتادة بن سعيد بن عامر بن جندع بن ليث بن بكر بن عبد مناة بن كنانة بن خزيمة بن مدركة بن إلياس بن مضر','7091','2381','0');
INSERT INTO [t10490] VALUES (2392,'عمير ذي مران الهمداني أبو سعيد، أدرك النبي صلى الله عليه وسلم وكتب إليه رسول الله صلى الله عليه وسلم كتاب عهد وأمان','7095','2381','0');
INSERT INTO [t10490] VALUES (2393,'عمير بن سعد بن فهد العبدي أبو الأشعث','7097','2381','0');
INSERT INTO [t10490] VALUES (2394,'عمير بن وهب الجمحي شهد بدرا كافرا ثم أسلم بعد بدر بالمدينة، قدمها ليقتل النبي صلى الله عليه وسلم فهداه الله فأسلم، ثم رجع إلى مكة مسلما','7099','2381','0');
INSERT INTO [t10490] VALUES (2395,'عمير بن جابر بن غاضرة بن أشرس الكندي ذكره المنيعي','7101','2381','0');
INSERT INTO [t10490] VALUES (2396,'عمير القارئ وهو عمير بن عدي بن خرشة بن أمية بن عامر بن خطمة، أمه أمامة بنت الواهب بن عبد الله من بني حوار، وكان عمير ضريرا، وهو الذي سماه رسول الله صلى الله عليه وسلم البصير، وكان يزوره في بني واقف، وكان من الذين كسروا أصنام بني خطمة، وقتل عمير','7103','2381','0');
INSERT INTO [t10490] VALUES (2397,'عمير أبو أبي بكر بن عمير','7105','2381','0');
INSERT INTO [t10490] VALUES (2398,'عمير مولى آبي اللحم مولى بني غفار، شهد خيبر ولم يسهم له النبي صلى الله عليه وسلم ورضخ له وأعطاه سيفا تقلده، واسم آبي اللحم: عبد الله بن عبد مالك بن عبد الله بن غفار، وكان شاعرا عريفا، وسمي آبي اللحم؛ لأنه كان يمتنع من أكل اللحم، روى عن عمير بن','7107','2381','0');
INSERT INTO [t10490] VALUES (2399,'عمير رجل من الصحابة ذكره في حديث الزهري، عن أنس','7113','2381','0');
INSERT INTO [t10490] VALUES (2400,'عمير بن أمية','7115','2381','0');
INSERT INTO [t10490] VALUES (2401,'عمير المزني ذكره سليمان، ولم يخرج عنه شيئا','7117','2381','0');
INSERT INTO [t10490] VALUES (2402,'عمير جد معرف بن واصل سكن الكوفة، ذكره المنيعي في الصحابة','7118','2381','0');
INSERT INTO [t10490] VALUES (2403,'عمير بن عبيد الأنصاري وقال ابن إسحاق: هو عمير بن سعد','7120','2381','0');
INSERT INTO [t10490] VALUES (2404,'من اسمه عويمر','7123','1723','0');
INSERT INTO [t10490] VALUES (2405,'عويمر مولى أم الفضل ذكره بعض المتأخرين، ولا صحبة له','7124','2404','0');
INSERT INTO [t10490] VALUES (2406,'عويمر بن عامر أبو الدرداء وقيل: عويمر بن ثعلبة بن عامر بن زيد بن قيس بن أمية بن مالك بن عامر بن عدي بن كعب بن الخزرج بن الحارث بن الخزرج، وأمه: محبة بنت واقد بن عمرو بن الأطنابة بن عامر بن زيد مناة بن مالك بن ثعلبة بن كعب، واختلف في اسم أبي الدرداء،','7125','2404','0');
INSERT INTO [t10490] VALUES (2407,'عويمر بن أشقر الأنصاري شهد بدرا، سكن المدينة، حديثه عند عباد بن تميم','7138','2404','0');
INSERT INTO [t10490] VALUES (2408,'عويمر أبو تميم له ذكر في الصحابة، سأل النبي صلى الله عليه وسلم عن الصيد، روى حديثه: عمرو بن تميم، عن عويمر، عن أبيه، عن جده، ذكره بعض المتأخرين، ولم يزد عليه','7140','2404','0');
INSERT INTO [t10490] VALUES (2409,'عويمر العجلاني وقيل: عويمر بن أبيض، سكن الكوفة سأل النبي صلى الله عليه وسلم عن رجل وجد مع امرأته رجلا، فنزلت آية اللعان، وقيل: إنه عويمر بن أشقر المتقدم ذكره','7141','2404','0');
INSERT INTO [t10490] VALUES (2410,'من اسمه عمران','7143','1723','0');
INSERT INTO [t10490] VALUES (2411,'عمران بن حصين أبو نجيد الخزاعي سكن البصرة، وهو عمران بن حصين بن عبيد بن خلف بن عبد نهم بن حذيفة بن جهمة بن غاضرة بن خشينة بن كعب بن عمرو بن خزاعة، أسلم وغزا مع رسول الله صلى الله عليه وسلم غزوات، وعقبه بالبصرة، وكان أبيض الرأس واللحية، كف نفسه عن','7144','2410','0');
INSERT INTO [t10490] VALUES (2412,'عمران بن عويمر وقيل: ابن عويمر الهذلي السجاع، له ذكر في حديث أسامة الهذلي','7156','2410','0');
INSERT INTO [t10490] VALUES (2413,'عمران أبو نصر الضبعي روى عنه ابنه أبو حمزة نصر بن عمران','7158','2410','0');
INSERT INTO [t10490] VALUES (2414,'عمران بن طلحة بن عبيد الله التيمي أمه: حمنة بنت جحش، قيل: إنه ولد على عهد النبي صلى الله عليه وسلم وسماه رسول الله صلى الله عليه وسلم عمران، وقدم عمران البصرة على علي بن أبي طالب رضي الله عنه بعد الجمل، فرد عليه أموال أبيه','7160','2410','0');
INSERT INTO [t10490] VALUES (2415,'عمران بن حجاج ذكره بعض المتأخرين أن محمد بن إسماعيل البخاري ذكره في الصحابة ولم يذكر له حديثا','7162','2410','0');
INSERT INTO [t10490] VALUES (2416,'عمران بن ملحان وقيل: ابن تيم العطاردي أبو رجاء من الخضارمة، أدرك الجاهلية والإسلام، أسلم في حياة النبي صلى الله عليه وسلم ولم يره، توفي سنة خمس ومائة، وقيل: ثمان ومائة، وعاش مائة وخمسا وثلاثين سنة، وقيل: مائة وسبعا وعشرين سنة، كان يخضب رأسه','7163','2410','0');
INSERT INTO [t10490] VALUES (2417,'*','7170','1723','0');
INSERT INTO [t10490] VALUES (2418,'عويم بن ساعدة الأنصاري الأوسي قيل: من بني زيد بن أمية، وقيل: من بني عمرو بن عوف، وقيل: حليف لبني عمرو بن عوف','7170','2417','0');
INSERT INTO [t10490] VALUES (2419,'عويم بن ساعدة الهذلي أبو تميم، من بني سعد بن هذيل، روى حديثه: عمرو بن تميم بن عويم، عن أبيه، عن جده','7178','2417','0');
INSERT INTO [t10490] VALUES (2420,'من اسمه العباس','7180','1723','0');
INSERT INTO [t10490] VALUES (2421,'العباس بن عبد المطلب بن هاشم يكنى أبا الفضل، عم رسول الله صلى الله عليه وسلم وصنو أبيه، أمه نثيلة أم الربيع، وقيل: نثلة بنت حباب بن كليب بن مالك بن عمرو بن زيد مناة، وقال القاسم بن معن: اسمها نثيلة بنت كليب بن مالك بن حباب بن حطائط بن النمر بن قاسط','7181','2420','0');
INSERT INTO [t10490] VALUES (2422,'عباس بن مرداس السلمي يكنى أبا الهيثم، كان له صنم يدعى الضماد، فسمع صائحا منه: يا عباس، شعر: قل للقبائل من سليم كلها هلك الضماد وفاز أهل المسجد هلك الضماد وكان يعبد مرة قبل الصلاة على النبي محمد فجاء في ثلاثمائة ركب من قومه، فأسلم هو وقومه، ثم','7186','2420','0');
INSERT INTO [t10490] VALUES (2423,'عباس بن عبادة بن نضلة بن مالك بن العجلاني من أهل العقبة، واستشهد بأحد، من بني سالم بن عوف، وهو القائل ليلة العقبة لرسول الله صلى الله عليه وسلم: لئن شئت لنميلن بأسيافنا غدا، وشد للعقد في البيعة لرسول الله صلى الله عليه وسلم','7188','2420','0');
INSERT INTO [t10490] VALUES (2424,'عباس مولى بني هاشم قديم أدرك النبي صلى الله عليه وسلم فيما ذكره بعض المتأخرين، عن قيس بن الربيع، عن عاصم بن سليمان، إن كان محفوظا','7191','2420','0');
INSERT INTO [t10490] VALUES (2425,'من اسمه عتبة','7193','1723','0');
INSERT INTO [t10490] VALUES (2426,'عتبة بن غزوان يعرف بالسلمي، وقيل: المازني مازن سليم، بدري، مهاجري، حليف بني نوفل بن عبد مناف، يكنى أبا عبد الله، وقيل: أبو غزوان، كان طويلا جميلا، وهو عتبة بن غزوان بن جابر بن وهب بن نسيب بن مالك بن الحارث بن مازن بن منصور بن عكرمة بن حفصة بن','7194','2425','0');
INSERT INTO [t10490] VALUES (2427,'عتبة بن ربيع بن رافع الأنصاري الخدري استشهد يوم أحد مع رسول الله صلى الله عليه وسلم','7203','2425','0');
INSERT INTO [t10490] VALUES (2428,'عتبة بن مسعود الهذلي أخو عبد الله توفي قبل عبد الله في زمن عمر بن الخطاب، وصلى عليه عمر رضي الله عنه، وقيل: توفي في سنة أربع وأربعين','7205','2425','0');
INSERT INTO [t10490] VALUES (2429,'عتبة بن عويم الأنصاري قال ابن أبي داود: شهد بيعة الرضوان والمشاهد','7211','2425','0');
INSERT INTO [t10490] VALUES (2430,'عتبة بن أسيد بن جارية الثقفي يكنى أبا بصير، كان من المحبوسين بمكة، فانفلت منهم في الهدنة بعد القضية، فأتى النبي صلى الله عليه وسلم بالمدينة، وسماه مسعر بن حرب، فكتب فيه الأخنس بن شريق، وأزهر بن عبد عوف، فرده رسول الله صلى الله عليه وسلم، فقتل أبو','7213','2425','0');
INSERT INTO [t10490] VALUES (2431,'عتبة بن عبد السلمي كان اسمه عتلة، فسماه النبي صلى الله عليه وسلم عتبة، يكنى أبا الوليد، حديثه عند شريح بن عبيد، ولقمان بن عامر، وكثير بن مرة الحضرمي، وخالد بن معدان، وعبد الله بن ناسج، وعقيل بن مدرك، وحبيب بن عبيد الرحبي، وراشد بن سعد، وغيرهم','7215','2425','0');
INSERT INTO [t10490] VALUES (2432,'عتبة بن الندر السلمي حديثه عند علي بن رباح، وخالد بن معدان','7220','2425','0');
INSERT INTO [t10490] VALUES (2433,'عتبة بن فرقد السلمي سكن الكوفة، قال محمد بن سعد: هو عتبة بن فرقد بن يربوع بن حبيب بن مالك بن أسعد بن رفاعة بن ربيعة بن رواعة بن الحارث بن بهثة بن سليم، كان شريفا، وأولاده بالكوفة يقال لهم: الفراقدة','7224','2425','0');
INSERT INTO [t10490] VALUES (2434,'عتبة بن طويع ذكره بعض المتأخرين، وقال: ذكر في الصحابة، ولا يثبت','7228','2425','0');
INSERT INTO [t10490] VALUES (2435,'عتبة بن نيار بعثه النبي صلى الله عليه وسلم إلى زرعة بن سيف، ذكره بعض المتأخرين في الصحابة','7230','2425','0');
INSERT INTO [t10490] VALUES (2436,'عتبة بن أبي وقاص أخو سعد، ذكره بعض المتأخرين في الصحابة، واستشهد بحديث الزهري','7233','2425','0');
INSERT INTO [t10490] VALUES (2437,'من اسمه عاصم','7237','1723','0');
INSERT INTO [t10490] VALUES (2438,'عاصم بن عدي الأنصاري بدري بسهمه، خرج مع النبي صلى الله عليه وسلم إلى بدر، فرده من الطريق واستخلفه على العالية، وقيل: إنه عاش مائة وخمس عشرة سنة، وهو عاصم بن عدي بن الجد بن عجلان بن ضبيعة، وهو من بلي، حليف لبني عبيد بن زيد بن مالك بن عوف بن عمرو بن','7238','2437','0');
INSERT INTO [t10490] VALUES (2439,'عاصم بن ثابت بن الأقلح وقيل: أبي الأقلح، واسم أبي الأقلح: قيس جد عاصم بن عمر بن الخطاب لأمه، شهد بدرا، قتل بالرجيع على عهد النبي صلى الله عليه وسلم، وهو الذي أراد المشركون رأسه، فحمته الدبر عن المشركين، وهو عاصم بن ثابت بن الأقلح بن عصمة بن مالك','7246','2437','0');
INSERT INTO [t10490] VALUES (2440,'عاصم بن سفيان الثقفي سكن المدينة','7249','2437','0');
INSERT INTO [t10490] VALUES (2441,'عاصم بن قيس البدري','7251','2437','0');
INSERT INTO [t10490] VALUES (2442,'عاصم أبو النصر الليثي','7253','2437','0');
INSERT INTO [t10490] VALUES (2443,'عاصم بن عمر بن الخطاب ولد في عهد رسول الله صلى الله عليه وسلم وصغر عن صحبته','7255','2437','0');
INSERT INTO [t10490] VALUES (2444,'عاصم بن حدرد، وقيل: خدرة الأنصاري','7257','2437','0');
INSERT INTO [t10490] VALUES (2445,'عاصم أبو هاشم الأسلمي','7259','2437','0');
INSERT INTO [t10490] VALUES (2446,'*','7261','1723','0');
INSERT INTO [t10490] VALUES (2447,'عصام المزني سكن المدينة','7261','2446','0');
INSERT INTO [t10490] VALUES (2448,'عصمة بن مالك الخطمي وهو ابن أمية بن ضبيعة بن يزيد بن مالك بن عوف بن عمرو بن عوف، روى حديثه عبيد الله بن موهب','7263','2446','0');
INSERT INTO [t10490] VALUES (2449,'عصمة بن قيس السلمي وقيل: الهوزني، كان اسمه عصية، فسماه رسول الله صلى الله عليه وسلم: عصمة','7267','2446','0');
INSERT INTO [t10490] VALUES (2450,'عصمة، وقيل: عصيمة شهد بدرا من بني أسد بن خزيمة، حليف بني مازن بن النجار','7271','2446','0');
INSERT INTO [t10490] VALUES (2451,'عصمة بن مدرك ذكره بعض المتأخرين، وقال: ذكر في الصحابة ولا يثبت','7274','2446','0');
INSERT INTO [t10490] VALUES (2452,'عقبة بن عمرو أبو مسعود البدري الأنصاري وهو ابن ثعلبة بن يسيرة، وقيل: أسيرة بن عسيرة بن جدارة بن عوف بن الحارث بن الخزرج، يكنى أبا مسعود، يعرف بالبدري نسبه أهل الكوفة إلى أنه بدري، ولم يذكره أهل المدينة في البدريين، شهد العقبة، استخلفه علي رضي الله','7276','2446','0');
INSERT INTO [t10490] VALUES (2453,'عقبة بن عامر بن عبس بن عمرو بن عدي بن عمرو بن رفاعة بن مودعة بن عدي بن غنم بن ربعة بن رشدان بن قيس بن جهينة، ويكنى أبا حماد، سكن مصر، وقيل: أبو أسد، وقيل: أبو عمرو، وقيل: أبو عبس، ولي الجيش لمعاوية بعد موت عتبة بن أبي سفيان، توفي بمصر آخر خلافة','7284','2446','0');
INSERT INTO [t10490] VALUES (2454,'عقبة بن الحارث بن عامر بن نوفل بن عبد مناف أبو سروعة، أمه خزاعية، سكن مكة، حديثه عند ابن أبي مليكة، وعبيد بن أبي مريم، شرب هو وعبد الرحمن بن عمر الخمر بمصر','7294','2446','0');
INSERT INTO [t10490] VALUES (2455,'عقبة بن مالك الليثي يعد في البصريين، حديثه عند بشر بن عاصم الليثي','7296','2446','0');
INSERT INTO [t10490] VALUES (2456,'عقبة بن وهب بن ربيعة بن أسد بن صهيب بن مالك بن كثير بن غنم بن دودان بن أسد بن خزيمة الأسدي، أخو شجاع بن وهب الأزدي، يكنى: أبا سنان بن وهب، من بني غنم بن دودان، شهد بدرا هاجر إلى المدينة مع رسول الله صلى الله عليه وسلم','7298','2446','0');
INSERT INTO [t10490] VALUES (2457,'عقبة بن عامر السلمي حديثه عند زيد بن أسلم','7303','2446','0');
INSERT INTO [t10490] VALUES (2458,'عقبة أبو عبد الرحمن الجهني','7305','2446','0');
INSERT INTO [t10490] VALUES (2459,'عقبة بن رافع وقيل: هو عصمة بن رافع بن عبد القيس بن لقيط بن عامر بن أمية بن الحارث بن عامر بن الفهر القرشي، شهد فتح مصر، وولي الإمرة على المغرب، واستشهد بإفريقية، وبنى قيروان إفريقية، وأنزلها المسلمين، قتلته البربر بالمغرب سنة ثلاث وستين، وولده','7307','2446','0');
INSERT INTO [t10490] VALUES (2460,'عقبة أبو عبد الرحمن مولى جبر بن عتيك الأنصاري شهد أحدا مع مولاه','7311','2446','0');
INSERT INTO [t10490] VALUES (2461,'عقبة أبو سعد الزرقي سمع النبي صلى الله عليه وسلم فيما ذكره بعض المتأخرين','7313','2446','0');
INSERT INTO [t10490] VALUES (2462,'عقبة بن كديم بن عدي بن جارية بن زيد بن مناة بن عدي بن عمرو بن مالك بن النجار، شهد فتح مصر، وعقبة بمصر، لا يعرف له رواية، ذكره المحيل على أبي سعيد بن عبد الأعلى','7315','2446','0');
INSERT INTO [t10490] VALUES (2463,'عياض بن غنم الفهري وقيل: الأشعري، سكن الشام، وهو عياض بن غنم بن زهير بن أبي شداد بن ربيعة بن هلال بن أهيب بن ضبة بن الحارث بن فهر بن مالك بن النضر بن كنانة، أسلم قبل الحديبية، وكان بالشام مع ابن عمه أبي عبيدة بن الجراح، فلما حضرت أبا عبيدة الوفاة','7316','2446','0');
INSERT INTO [t10490] VALUES (2464,'عياض بن حمار المجاشعي سكن البصرة، لقي النبي صلى الله عليه وسلم بمكة، حديثه عند مطرف والحسن حدثنا سليمان بن أحمد، حدثنا أحمد بن عبد الرحمن البرقي، ثنا عبد الملك بن هشام، عن أبي عبيدة معمر بن المثنى، ثنا عياض بن حمار بن عرفجة بن ناجية بن سفيان بن','7323','2446','0');
INSERT INTO [t10490] VALUES (2465,'عياض بن عبد الله أبو عبد الله الثقفي حديثه عند عبد الله بن عبد الرحمن بن يعلى الطائفي','7329','2446','0');
INSERT INTO [t10490] VALUES (2466,'عياض بن عمرو الأشعري سكن الكوفة، حديثه عند الشعبي، وسماك','7332','2446','0');
INSERT INTO [t10490] VALUES (2467,'عياض الأنصاري له صحبة','7336','2446','0');
INSERT INTO [t10490] VALUES (2468,'عياض بن زيد العبدي ذكره سليمان بن أحمد في معجمه','7339','2446','0');
INSERT INTO [t10490] VALUES (2469,'عياض بن عبد الله بن أبي ذناب المدني ذكره بعض المتأخرين، وعده من الصحابة','7341','2446','0');
INSERT INTO [t10490] VALUES (2470,'عياض بن مرثد العامري مختلف في صحبته، ذكره سليمان بن أحمد في المعجم','7343','2446','0');
INSERT INTO [t10490] VALUES (2471,'عياض بن الحارث التيمي رأى النبي صلى الله عليه وسلم يوم أحد، وقد مثل بحمزة، روى حديثه الواقدي، عن عبد الرحمن بن عبد العزيز، عن محمد بن إبراهيم بن الحارث، عن عمه عياض بن الحارث، ذكره بعض المتأخرين، ولم يزد عليه','7345','2446','0');
INSERT INTO [t10490] VALUES (2472,'عياض بن سعد بن جبر بن عوف الأزدي الجحدري شهد فتح مصر له ذكر، ولا يعرف له رواية، حكي لنا عن أبي سعيد بن عبد الأعلى','7346','2446','0');
INSERT INTO [t10490] VALUES (2473,'عكرمة بن أبي جهل ابن هشام بن المغيرة بن عبد الله بن عمر بن مخزوم، أمه أم خالد امرأة من بني هلال، أسلم عام الفتح، واستشهد في خلافة عمر بن الخطاب باليرموك، وقيل: بأجنادين كان إذا اجتهد في اليمين قال: والذي نجاني يوم بدر، وكان يضع المصحف على وجهه،','7347','2446','0');
INSERT INTO [t10490] VALUES (2474,'عكرمة بن عبيد الخولاني ذكره بعض المتأخرين في الصحابة، وقال: لا يعرف له رواية، حكي عن أبي سعيد بن عبد الأعلى أنه ممن شهد فتح مصر وله إدراك','7351','2446','0');
INSERT INTO [t10490] VALUES (2475,'من اسمه علقمة','7352','1723','0');
INSERT INTO [t10490] VALUES (2476,'علقمة بن رمثة البلوي كان ممن بايع تحت الشجرة، وشهد فتح مصر حديثه عند زهير بن قيس البلوي','7353','2475','0');
INSERT INTO [t10490] VALUES (2477,'علقمة بن فغواء الخزاعي أبو عبد الله، وقيل: ابن أبي الفغواء، سكن المدينة بعثه النبي صلى الله عليه وسلم بمال إلى أبي سفيان بن حرب ليقسمه في فقراء قريش، وكان دليل النبي صلى الله عليه وسلم إلى تبوك','7355','2475','0');
INSERT INTO [t10490] VALUES (2478,'علقمة بن ناجية بن الحارث أبو كلثوم الخزاعي ثم المصطلقي','7358','2475','0');
INSERT INTO [t10490] VALUES (2479,'علقمة بن الحويرث الغفاري له صحبة، حديثه عند محمد بن مطرف الأيلي','7362','2475','0');
INSERT INTO [t10490] VALUES (2480,'علقمة بن سفيان بن عبد الله بن ربيعة الثقفي سكن البصرة، حديثه عند ابنه سفيان، وغيره','7364','2475','0');
INSERT INTO [t10490] VALUES (2481,'علقمة الأسلمي يكنى أبا أوفى، والد عبد الله، ذكره في حديث ابنه عبد الله','7366','2475','0');
INSERT INTO [t10490] VALUES (2482,'علقمة بن علاثة العامري قسم النبي صلى الله عليه وسلم ذهبة، بعث بها علي رضي الله عنه من اليمن بينه وبين علقمة بن علاثة، وعيينة، والأقرع، وزيد الخيل، ذكره في حديث ابن عمر، وأبي سعيد، وأنس، كان من المؤلفة، ثم حسن إسلامه، وكان من الفقهاء المؤمنين','7368','2475','0');
INSERT INTO [t10490] VALUES (2483,'علقمة بن الأعور السلمي يعد في المدنيين، ذكره في حديث ابن عباس','7374','2475','0');
INSERT INTO [t10490] VALUES (2484,'علقمة بن نضلة الكناني','7377','2475','0');
INSERT INTO [t10490] VALUES (2485,'علقمة بن وقاص الليثي ذكره بعض المتأخرين في الصحابة، وذكره القاضي أبو أحمد والناس في التابعين، سمع عمر، عائشة، مات بالمدينة في ولاية عبد الملك','7379','2475','0');
INSERT INTO [t10490] VALUES (2486,'علقمة بن مجزز المدلجي أحد عمال النبي صلى الله عليه وسلم، ذكره في حديث أبي سعيد الخدري','7381','2475','0');
INSERT INTO [t10490] VALUES (2487,'علقمة بن يزيد بن عمرو بن سلمة بن منبه بن ذهل بن غطيف بن عبد الله بن ناجية بن مراد المرادي، وفد على النبي صلى الله عليه وسلم، ورجع إلى اليمن، شهد فتح مصر ولاه عتبة بن أبي سفيان الإسكندرية في خلافة معاوية، رواه أبو قبيل، وحكى عنه فيما قاله أبو سعيد بن','7384','2475','0');
INSERT INTO [t10490] VALUES (2488,'علقمة بن جنادة بن عبد الله بن قيس له صحبة شهد فتح مصر وولي البحر لمعاوية، توفي سنة تسع وخمسين، قاله أبو سعيد بن عبد الأعلى','7385','2475','0');
INSERT INTO [t10490] VALUES (2489,'علقمة بن سمي الخولاني صحابي شهد فتح مصر ولا يعرف له رواية، قاله أبو سعيد بن عبد الأعلى','7386','2475','0');
INSERT INTO [t10490] VALUES (2490,'من اسمه عروة','7387','1723','0');
INSERT INTO [t10490] VALUES (2491,'عروة بن مضرس بن أوس بن حارثة بن لام الطائي شهد حجة الوداع مع النبي صلى الله عليه وسلم، حديثه عند الشعبي','7388','2490','0');
INSERT INTO [t10490] VALUES (2492,'عروة بن أبي الجعد البارقي وقيل: ابن الجعد الأزدي، سكن الكوفة، حديثه عند الشعبي، وأبي إسحاق السبيعي، والعيزار بن حريث، وشبيب بن غرقدة، وعائذ بن نصيب، ونعيم بن أبي هند، وسماك بن حرب، وشريح بن هانئ، وغيرهم، وشريح بن هانئ، وغيرهم','7390','2490','0');
INSERT INTO [t10490] VALUES (2493,'عروة أبو غاضرة الفقيمي','7401','2490','0');
INSERT INTO [t10490] VALUES (2494,'عروة بن أسماء بن الصلت السلمي استشهد ببئر معونة','7403','2490','0');
INSERT INTO [t10490] VALUES (2495,'عروة بن مسعود الثقفي عم المغيرة بن شعبة بعثه رسول الله صلى الله عليه وسلم داعيا إلى الطائف يدعوهم إلى الإسلام، فقتلوه بعد مقفله من حنين، وكان صاحب قريش يوم الحديبية، وحضر النبي صلى الله عليه وسلم، فلما رجع إلى قريش، قال: إن هذا الذي عرض عليكم خطة','7405','2490','0');
INSERT INTO [t10490] VALUES (2496,'عروة بن مغيث الأنصاري مختلف في صحبته، وقال البخاري: عداده في التابعين، وهو الصحيح، وقال ابن أبي خيثمة: هو صحابي','7409','2490','0');
INSERT INTO [t10490] VALUES (2497,'من اسمه عدي','7411','1723','0');
INSERT INTO [t10490] VALUES (2498,'عدي بن حاتم الطائي وهو حاتم طيئ حاتم بن عبد الله بن سعد بن الحشرج بن امرئ القيس بن عدي بن أخزم، يكنى أبا طريف، نسبه أبو عبيد القاسم بن سلام، فيما حدثناه سليمان بن أحمد، ثنا علي بن عبد العزيز عنه، كان يسكن الكوفة، ومات بها زمن المختار فيما ذكره','7412','2497','0');
INSERT INTO [t10490] VALUES (2499,'عدي بن عميرة بن فروة بن زرارة بن الأرقم الكندي يكنى أبا زرارة، توفى بالرها، سكن مصر، حديثه عن قيس بن أبي حازم','7418','2497','0');
INSERT INTO [t10490] VALUES (2500,'عدي بن عميرة الكندي أخو العرس بن عميرة الكندي، روى حديثه ابنه عدي بن عدي، وهو عندي المتقدم، وفصله بعض المتأخرين','7420','2497','0');
INSERT INTO [t10490] VALUES (2501,'عدي بن زيد الجذامي عداده في الحجازيين، مختلف في حديثه، روى حديثه عبد الله بن أبي سفيان','7424','2497','0');
INSERT INTO [t10490] VALUES (2502,'عدي بن أبي الزغباء الجهني من بني غنم، وقيل: من بني عائذ بن ثعلبة، وقيل: هو حليف بني مالك بن النجار، بعثه النبي صلى الله عليه وسلم مع بسبس بن عمرو عينا إلى عير أبي سفيان وتجار قريش، فرجعا إلى النبي صلى الله عليه وسلم فأخبراه، فاستنفر المسلمين وخرج','7427','2497','0');
INSERT INTO [t10490] VALUES (2503,'عدي بن بداء فيه وفي تميم الداري نزلت: يا أيها الذين آمنوا شهادة بينكم. .، كان عدي نصرانيا هو وتميم، ولا يعرف لعدي إسلام، ذكره بعض المتأخرين، سكن الكوفة','7431','2497','0');
INSERT INTO [t10490] VALUES (2504,'عدي بن ربيعة بن سواءة بن جشم الجشمي ذكرناه فيمن اسمه محمد في أول الكتاب','7433','2497','0');
INSERT INTO [t10490] VALUES (2505,'من اسمه العلاء','7434','1723','0');
INSERT INTO [t10490] VALUES (2506,'العلاء الحضرمي والحضرمي، اسمه: عبد الله بن عباد بن أكبر، وقيل: ابن صدقة بن ربيعة بن مالك بن عريف بن مالك بن الخزرج بن الصدف، وكان الحضرمي حليف حرب بن أمية، كان عامل رسول الله صلى الله عليه وسلم على البحرين توفي النبي صلى الله عليه وسلم وهو عليها،','7435','2505','0');
INSERT INTO [t10490] VALUES (2507,'العلاء بن خارجة من ساكني المدينة','7440','2505','0');
INSERT INTO [t10490] VALUES (2508,'العلاء بن خباب سكن الكوفة، حديثه عند عبد الرحمن بن عابس، وعند ابنه عبد الله بن العلاء','7442','2505','0');
INSERT INTO [t10490] VALUES (2509,'العلاء بن جارية الثقفي كان من المؤلفة','7444','2505','0');
INSERT INTO [t10490] VALUES (2510,'العلاء بن مسروح حجازي، ذكره بعض المتأخرين','7447','2505','0');
INSERT INTO [t10490] VALUES (2511,'العلاء بن وهب بن محمد بن وهبان بن خباب بن حجير بن عبد بن معيص بن عامر بن لؤي شهد القادسية، وكتب عثمان إلى معاوية رضي الله عنهما يأمره أن يستعمله على الجزيرة، فولاه وتزوج زينب بنت عقبة بن أبي معيط، وهو من مسلمة الفتح، وأقام أميرا بالرقة، فيما حكاه','7449','2505','0');
INSERT INTO [t10490] VALUES (2512,'العلاء بن يزيد بن أنيس الفهري رأى النبي صلى الله عليه وسلم وقدم مصر بعد أن فتحت، وعقبه بها، وهو جد أبي الحارث أحمد بن سعيد الفهري، قاله أبو سعيد بن عبد الأعلى فيما حكاه المحيل عليه','7450','2505','0');
INSERT INTO [t10490] VALUES (2513,'العلاء بن سعد الساعدي شهد الفتح مع النبي صلى الله عليه وسلم، حديثه عند ابنه عبد الرحمن، ذكره بعض المتأخرين','7451','2505','0');
INSERT INTO [t10490] VALUES (2514,'من اسمه عوف','7453','1723','0');
INSERT INTO [t10490] VALUES (2515,'عوف بن مالك الأشجعي يكنى: أبا عبد الرحمن، سكن الشام، وقدم مصر، وقيل: أبا عبد الله، وتوفي سنة ثلاث وسبعين، حدث عنه من الصحابة: أبو أيوب الأنصاري، وأبو هريرة، والمقداد بن معدي كرب، ومن كبار التابعين: أبو مسلم، وأبو إدريس الخولانيان، وجبير بن','7454','2514','0');
INSERT INTO [t10490] VALUES (2516,'عوف أبو سلمة الأنصاري وقيل: هو ابن سلمة بن سلامة، حديثه عند ابنه سلمة','7458','2514','0');
INSERT INTO [t10490] VALUES (2517,'عوف بن الحارث البجلي والد قيس بن أبي حازم، وقيل: عبد عوف بن عبد الحارث، وقيل: عمرو بن عوف، وقيل: صخر، مختلف فيه','7460','2514','0');
INSERT INTO [t10490] VALUES (2518,'عوف بن النعمان الشيباني أدرك النبي صلى الله عليه وسلم فيما قاله بعض المتأخرين','7462','2514','0');
INSERT INTO [t10490] VALUES (2519,'عوف بن القعقاع بن معبد بن زرارة بن عدس بن زيد، عن عبد الله بن دارم','7464','2514','0');
INSERT INTO [t10490] VALUES (2520,'عوف بن الحارث بن سواد بن مالك بن غنم بن مالك، وهو ابن عفراء، أخو معاذ، ومعوذ، استشهد ببدر مع رسول الله صلى الله عليه وسلم روى عنه عبد الله بن عباس رضي الله عنهما','7466','2514','0');
INSERT INTO [t10490] VALUES (2521,'عوف بن ربيع بن جارية بن ساعدة بن جذيمة بن مالك بن نصر بن معين بن الحارث بن ثعلبة بن دودان بن أسد، ذو الخيار، وفد على رسول الله صلى الله عليه وسلم ونزل الرقة، وعقبه بها، ذكره بعض المتأخرين، عن علي بن أحمد الحراني، ثنا محمود بن محمد الأديب بهذا، ولم','7468','2514','0');
INSERT INTO [t10490] VALUES (2522,'عوف بن أثاثة وهو مسطح المذكور في قصة الإفك، وهو الذي آلى أبو بكر الصديق فيه أن لا ينفعه بنافعة، وكان قريبا له','7469','2514','0');
INSERT INTO [t10490] VALUES (2523,'عوف بن حصيرة أدرك النبي صلى الله عليه وسلم، روى عنه الشعبي فيما ذكره بعض المتأخرين','7472','2514','0');
INSERT INTO [t10490] VALUES (2524,'عوف أبو شبيل أدرك النبي صلى الله عليه وسلم فيما ذكره بعض المتأخرين روى عنه ابنه شبيل بن عوف','7474','2514','0');
INSERT INTO [t10490] VALUES (2525,'عوف الخثعمي والد حصين بن عوف، تقدم ذكره مع ابنه حصين في حرف الحاء','7475','2514','0');
INSERT INTO [t10490] VALUES (2526,'عوف بن سراقة الضمري أخو جعيل بن سراقة، لهما صحبة فيما ذكره بعض المتأخرين روى عنه ابنه عبد الواحد','7476','2514','0');
INSERT INTO [t10490] VALUES (2527,'عوف بن دلهم ذكره بعض المتأخرين أن له ذكرا في الصحابة،','7478','2514','0');
INSERT INTO [t10490] VALUES (2528,'عوف بن نجوة شهد فتح مصر ولا يعرف له رواية فيما حكي عن أبي سعيد بن عبد الأعلى','7480','2514','0');
INSERT INTO [t10490] VALUES (2529,'من اسمه عطاء','7481','1723','0');
INSERT INTO [t10490] VALUES (2530,'عطاء الشيبي تفرد بالرواية عنه فطر بن خليفة، وهو عطاء بن النضر بن الحارث بن علقمة بن كلدة بن عبد مناف بن عبد الدار بن قصي بن كلاب، سمعت أبا بكر الطلحي ينسبه، وقال غيره: هو عطاء بن عبيد الله','7482','2529','0');
INSERT INTO [t10490] VALUES (2531,'عطاء بن إبراهيم وقيل: إبراهيم بن عطاء، مختلف فيه، من ثقيف','7484','2529','0');
INSERT INTO [t10490] VALUES (2532,'عطاء أبو عبد الله، غير منسوب','7486','2529','0');
INSERT INTO [t10490] VALUES (2533,'عطاء المزني ذكره بعض المتأخرين، وقال: هو وهم، وذكر حديث ابن عيينة، عن عبد الملك بن نوفل، عن ابن عصام المزني، عن أبيه في بعث النبي صلى الله عليه وسلم السرية، فقال ابن عطاء: وإنما هو ابن عصام','7488','2529','0');
INSERT INTO [t10490] VALUES (2534,'من اسمه عطية','7489','1723','0');
INSERT INTO [t10490] VALUES (2535,'عطية القرظي رأى النبي صلى الله عليه وسلم وسمع منه، سكن الكوفة، حديثه عند عبد الملك بن عمير، ومجاهد بن جبر، وكثير بن السائب','7490','2534','0');
INSERT INTO [t10490] VALUES (2536,'عطية السعدي من بني جشم بن سعد، قيل: هو عطية بن سعد، وقيل: عطية بن عمرو بن عرورة، وقيل: عطية بن عروة حديثه عند أولاده','7494','2534','0');
INSERT INTO [t10490] VALUES (2537,'عطية بن بسر المازني أخو عبد الله، حديثه عند غضيف بن الحارث','7498','2534','0');
INSERT INTO [t10490] VALUES (2538,'عطية بن عفيف له ذكر في حديث عائشة','7501','2534','0');
INSERT INTO [t10490] VALUES (2539,'عطية بن سفيان بن عبد الله بن ربيعة الثقفي حجازي، مختلف في حديثه','7503','2534','0');
INSERT INTO [t10490] VALUES (2540,'عطية بن عامر يعد في الشاميين، ذكره بعض المتأخرين، وقال: يعد في الشاميين','7505','2534','0');
INSERT INTO [t10490] VALUES (2541,'*','7507','1723','0');
INSERT INTO [t10490] VALUES (2542,'عفيف الكندي أخو الأشعث بن قيس لأمه، وقال بعض المتأخرين: عفيف بن قيس، ووهم؛ لأنه عفيف بن معدي كرب الكندي','7507','2541','0');
INSERT INTO [t10490] VALUES (2543,'عفيف بن الحارث اليماني ذكره سليمان بن أحمد الطبراني في معجمه، في الصحابة','7509','2541','0');
INSERT INTO [t10490] VALUES (2544,'عرزب الكندي يعد في الشاميين، روى عنه أبو عفيف الجذامي','7511','2541','0');
INSERT INTO [t10490] VALUES (2545,'عائذ بن عمرو المزني سكن البصرة، وهو عائذ بن عمرو بن هلال بن عبيد بن يزيد بن رواحة بن زبيبة بن عدي بن عامر بن عبد الله بن ثعلبة بن ثور بن هرمز بن لاطم بن عثمان بن عمرو، وعمرو هو مزينة، سمي باسمه ابن أد بن طابخة بن إلياس بن مضر، يكنى أبا هبيرة، له دار','7513','2541','0');
INSERT INTO [t10490] VALUES (2546,'عائذ بن سعيد الجسري حي من عنزة من ربيعة، وهو عائذ بن سعيد بن زيد بن جندب بن جابر بن زيد بن عبد الحارث بن معيص، قتل مع علي رضي الله عنهما بصفين سنة سبع وثلاثين، وكان فيمن وفد على النبي صلى الله عليه وسلم','7515','2541','0');
INSERT INTO [t10490] VALUES (2547,'عائذ بن أبي عائذ الجعفي روى عن النبي صلى الله عليه وسلم','7517','2541','0');
INSERT INTO [t10490] VALUES (2548,'عائذ بن ثعلبة بن وبر البلوي له صحبة، وشهد فتح مصر، قتله الروم ببرلس سنة ثلاث وخمسين، قاله: أبو سعيد بن يونس بن عبد الأعلى','7519','2541','0');
INSERT INTO [t10490] VALUES (2549,'عائذ بن قرط حديثه عند عمرو بن قيس السكوني، وموسى بن أبي حبيب','7520','2541','0');
INSERT INTO [t10490] VALUES (2550,'عائذ بن عبد عمرو الأزدي عداده في البصريين، توفي بعد عثمان رضي الله عنه، ذكره البخاري في الوحدان، ولم يذكر له حديثا فيما حكاه عنه بعض المتأخرين','7522','2541','0');
INSERT INTO [t10490] VALUES (2551,'عتاب بن أسيد بن أبي العيص بن أمية بن عبد شمس بن عبد مناف، أمه زينب بنت عمرو بن أمية بن عبد شمس، استعمله النبي صلى الله عليه وسلم على مكة، وتوفي رسول الله صلى الله عليه وسلم وعتاب عامله على مكة','7523','2541','0');
INSERT INTO [t10490] VALUES (2552,'عتاب بن شمير الضبي له صحبة، حديثه عند ابنه مجمع','7526','2541','0');
INSERT INTO [t10490] VALUES (2553,'عتبان بن مالك الأنصاري الخزرجي شهد بدرا، وكان إمام قومه في عهد النبي صلى الله عليه وسلم وهو أعمى في بني سالم','7528','2541','0');
INSERT INTO [t10490] VALUES (2554,'عياش بن أبي ربيعة بن المغيرة بن عبد الله بن عمر بن مخزوم، من المهاجرين الأولين، ذو الهجرتين، ولد له بالحبشة ابنه عبد الله، ثم هاجر هو وعمر بن الخطاب إلى المدينة، كان أخا لأبي جهل بن هشام لأمه، خرج أبو جهل، والحارث ابنا هشام إلى المدينة فتلطفا له،','7533','2541','0');
INSERT INTO [t10490] VALUES (2555,'عرفجة بن ضريح الأشجعي وقيل: عرفجة بن شراحيل، وقيل: عرفجة بن شريك، وقال بعض المتأخرين: عرفجة بن شريح، وهو تصحيف، وقع في ضريح، يعد في الكوفيين روى عنه: قطبة بن مالك، وزياد بن علاقة، والشعبي، وأبو يعفور، وأبو حازم الأشجعي، وأبو عون الثقفي،','7538','2541','0');
INSERT INTO [t10490] VALUES (2556,'عرفجة بن أسعد بن كرب التميمي حديثه عند أخيه طرفة بن أسعد','7541','2541','0');
INSERT INTO [t10490] VALUES (2557,'عون بن جعفر بن أبي طالب أخو عبد الله، أشبه النبي صلى الله عليه وسلم خلقا وخلقا، رأى النبي صلى الله عليه وسلم ومسح رأسه','7543','2541','0');
INSERT INTO [t10490] VALUES (2558,'عابس الغفاري وقيل: عبس بن عابس، روى عنه: أبو أمامة الباهلي، وعليم الكندي، وزاذان أبو عمر','7545','2541','0');
INSERT INTO [t10490] VALUES (2559,'عابس بن ربيعة والد عبد الرحمن بن عابس، ذكره ابن أبي داود في الصحابة فيما حكاه عنه بعض المتأخرين','7550','2541','0');
INSERT INTO [t10490] VALUES (2560,'عابس مولى حويطب بن عبد العزى نزلت فيه وفي أصحابه: ومن الناس من يشري نفسه ابتغاء مرضات الله والله رءوف بالعباد','7552','2541','0');
INSERT INTO [t10490] VALUES (2561,'عنبسة بن أبي سفيان أدرك النبي صلى الله عليه وسلم ولم يصح له صحبة، ولا رواية، روى عنه أبو أمامة الباهلي، والنعمان بن سالم فيما ذكره بعض المتأخرين، ولم يزد عليه، واتفق متقدمو أئمتنا أنه من التابعين','7554','2541','0');
INSERT INTO [t10490] VALUES (2562,'عياذ بن عبد عمرو الأزدي أتى النبي صلى الله عليه وسلم وخدمه، حديثه عند أبي عاصم','7555','2541','0');
INSERT INTO [t10490] VALUES (2563,'العرباض بن سارية السلمي أبو نجيح، روى عنه: عبد الرحمن بن عمرو، وجبير بن نفير، وخالد بن معدان، وأبو رهم السماعي، وحجر بن حجر، وحبيب بن عبيد، ويحيى بن أبي المطاع، والمهاجر بن حبيب، وعبد الرحمن بن أبي بلال الخزاعي، وعبد الأعلى بن هلال السلمي،','7557','2541','0');
INSERT INTO [t10490] VALUES (2564,'عازب أبو البراء بن عازب وهو عازب بن الحارث بن عدي بن جشم بن مجدعة بن حارثة بن الحارث، نسبه شباب، ذكره في حديث ابنه البراء','7562','2541','0');
INSERT INTO [t10490] VALUES (2565,'عكاشة بن محصن الأسدي شهد بدرا مع رسول الله صلى الله عليه وسلم، هو السابق بدعوة النبي صلى الله عليه وسلم أن يجعله من الذين يدخلون الجنة بغير حساب، قاتل يوم بدر بسيفه حتى انقطع، فأعطاه النبي صلى الله عليه وسلم جزلا من حطب، فعاد في يده سيفا أبيض الحديدة','7564','2541','0');
INSERT INTO [t10490] VALUES (2566,'عباية أبو قيس حدث عن النبي صلى الله عليه وسلم في الصوم، ذكره بعض المتأخرين في الصحابة، وقال: لا يصح روى حديثه الجريري، عن قيس بن عباية، عن أبيه، ولم يزد عليه','7568','2541','0');
INSERT INTO [t10490] VALUES (2567,'عاقل بن البكير بن عبد ياليل بن ناشب بن عنزة بن سعد بن ليث، أخو ياسر وخالد وعاصم، حلفاء بني عدي بن كعب','7569','2541','0');
INSERT INTO [t10490] VALUES (2568,'عرس بن عميرة الكندي روى حديثه، عدي بن عدي، وزهدم بن الحارث','7572','2541','0');
INSERT INTO [t10490] VALUES (2569,'عكراش بن ذؤيب التميمي وهو ابن حرقوص بن جعدة بن عمرو بن الغزال بن مرة بن عبيد، صحب النبي صلى الله عليه وسلم وواكله وسمع منه، أتى النبي صلى الله عليه وسلم بصدقات قومه بني مرة، حديثه عند ابنه عبيد الله','7575','2541','0');
INSERT INTO [t10490] VALUES (2570,'عتيك بن التيهان الأشهلي أخو أبي الهيثم من بني النبيت، شهد بدرا، ذكره بعض المتأخرين عتيكا عن الزهري، وابن إسحاق، وفي نسختي عبيد بن التيهان بالدال، عن الزهري، وابن إسحاق','7578','2541','0');
INSERT INTO [t10490] VALUES (2571,'عرفطة بن خباب القرشي حليف بني أمية، استشهد بالطائف وله عقب ولا يعرف له رواية','7581','2541','0');
INSERT INTO [t10490] VALUES (2572,'عتير العذري استقطع النبي صلى الله عليه وسلم أرضا بوادي القرى','7583','2541','0');
INSERT INTO [t10490] VALUES (2573,'عتير، له صحبة روى حديثه معلى بن الفضل، عن سليمان بن عبد الرحمن، عن شيخ يقال له: عتير، صحب النبي صلى الله عليه وسلم أنه قال: " إذا زفت المرأة إلى زوجها، شيعها سبعون ألف ملك " ذكره بعض المتأخرين، ولم يزد عليه','7585','2541','0');
INSERT INTO [t10490] VALUES (2574,'عنترة الشيباني أبو هارون','7587','2541','0');
INSERT INTO [t10490] VALUES (2575,'العداء بن خالد بن هوذة بن خالد بن ربيعة بن عمرو بن عامر بن صعصعة بن معاوية بن بكر بن هوازن بن منصور بن معاوية بن حفصة بن قيس بن غيلان بن مضر " حدثنا بنسبه سليمان بن أحمد، ثنا أحمد بن عبد العزيز الجوهري، ثنا زكريا بن يحيى المنقري، ثنا الأصمعي، به روى','7589','2541','0');
INSERT INTO [t10490] VALUES (2576,'عكاف بن وداعة الهلالي له ذكر في حديث عطية بن بسر','7595','2541','0');
INSERT INTO [t10490] VALUES (2577,'عيينة بن حصن بن حذيفة بن بدر الفزاري من صناديد العرب، استألفه النبي صلى الله عليه وسلم على الإسلام، وكان من المؤلفة، ذكره في حديث أبي سعيد الخدري، وعمرو بن عبسة السلمي','7598','2541','0');
INSERT INTO [t10490] VALUES (2578,'علباء اليشكري، وقيل: السلمي حديثه عند عبد الحميد بن جعفر، عن أبيه عنه','7602','2541','0');
INSERT INTO [t10490] VALUES (2579,'علباء بن أصمع القيسي وفد على النبي صلى الله عليه وسلم روى حديثه عباد بن جمهور','7605','2541','0');
INSERT INTO [t10490] VALUES (2580,'عتيبة الأنصاري غير منسوب، له ذكر في حديث أبي ثعلبة الخشني','7607','2541','0');
INSERT INTO [t10490] VALUES (2581,'علية بن زيد الأنصاري الحارثي أبو محمد، له صحبة، المتصدق بعرضه على الناس','7609','2541','0');
INSERT INTO [t10490] VALUES (2582,'عريب أبو عبد الله المليكي يعد في الشاميين','7611','2541','0');
INSERT INTO [t10490] VALUES (2583,'عقيبة بن رقيبة وقيل: رقيبة بن عقيبة، تقدم ذكره، أخرجه بعض المتأخرين، ولم يزد عليه','7615','2541','0');
INSERT INTO [t10490] VALUES (2584,'عقربة الجهني قتل يوم أحد، روى عنه ابنه بشير','7616','2541','0');
INSERT INTO [t10490] VALUES (2585,'عطارد بن حاجب التميمي له صحبة، صاحب الحلة التي اشتراها منه عمر بن الخطاب رضي الله عنه','7618','2541','0');
INSERT INTO [t10490] VALUES (2586,'علاقة بن صحار السليطي عم خارجة بن الصلت، كذا ذكره ابن أبي خيثمة، عن أبي عبيد القاسم بن سلام','7621','2541','0');
INSERT INTO [t10490] VALUES (2587,'عيسى بن عقيل وقيل: ابن معقل، حديثه عند زياد بن علاقة','7623','2541','0');
INSERT INTO [t10490] VALUES (2588,'عفير بن أبي عفير روى عنه عبد الرحمن بن أبي بكر','7625','2541','0');
INSERT INTO [t10490] VALUES (2589,'عوسجة بن حرملة الجهني سكن فلسطين، ذكره البخاري في الصحابة','7627','2541','0');
INSERT INTO [t10490] VALUES (2590,'عنمة أبو إبراهيم الجهني حديثه عند أولاده','7629','2541','0');
INSERT INTO [t10490] VALUES (2591,'عجوز بن نمير له من النبي صلى الله عليه وسلم رؤية وسماع، والصحيح عجوز من بني نمير','7631','2541','0');
INSERT INTO [t10490] VALUES (2592,'عقيل بن أبي طالب يكنى أبا يزيد، حضر فتح خيبر، وقسم له النبي صلى الله عليه وسلم من خيبر، روى عنه الحسن، وموسى بن طلحة، وعبد الله بن حنين','7633','2541','0');
INSERT INTO [t10490] VALUES (2593,'عسعس بن سلامة أبو شقرة ذكره في الصحابة، ولا يصح صحبته، حديثه عند الأزرق بن قيس، والحسن، وسعيد ابني أبي الحسن','7636','2541','0');
INSERT INTO [t10490] VALUES (2594,'عجير بن يزيد بن عبد العزى ذكره البخاري في الصحابة، سكن مكة حدثناه سليمان بن أحمد، عن ابن صدقة، عن البخاري، ولم يذكر له شيئا','7639','2541','0');
INSERT INTO [t10490] VALUES (2595,'عثامة بن قيس وقيل: عسامة، قاله البخاري','7640','2541','0');
INSERT INTO [t10490] VALUES (2596,'العاص بن هشام أبو خالد المخزومي، جد عكرمة بن خالد، سكن مكة، ذكره سليمان بن أحمد في الصحابة','7643','2541','0');
INSERT INTO [t10490] VALUES (2597,'عداس النينوي مولى عتبة وشيبة ابني ربيعة','7645','2541','0');
INSERT INTO [t10490] VALUES (2598,'عتريس بن عرقوب ذكر فيمن أدرك النبي صلى الله عليه وسلم، روى عنه طارق بن شهاب، وهو من أصحاب عبد الله بن مسعود، ولا يصح له صحبة، ذكره بعض المتأخرين','7647','2541','0');
INSERT INTO [t10490] VALUES (2599,'عنبس بن ثعلبة البلوي له صحبة، شهد فتح مصر لا يعرف له رواية','7648','2541','0');
INSERT INTO [t10490] VALUES (2600,'علسة بن عدي البلوي ممن بايع تحت الشجرة، وشهد فتح مصر روى عنه موسى بن الأشعث، وابنه الوليد بن علسة','7649','2541','0');
INSERT INTO [t10490] VALUES (2601,'عجري بن ماتع السكسكي شهد فتح مصر، لا يعرف له رواية، كل هؤلاء ذكرهم المحيل بذكرهم على أبي سعيد بن عبد الأعلى، ولم يزد على ما حكيته عنه','7650','2541','0');
INSERT INTO [t10490] VALUES (2602,'باب الغين','7651','18','0');
INSERT INTO [t10490] VALUES (2603,'غالب بن أبجر المزني وقيل: غالب بن ذيخ، وقيل: ذريح، سكن الكوفة، روى عنه عبد الرحمن بن مقرن','7652','2602','0');
INSERT INTO [t10490] VALUES (2604,'غالب بن عبد الله الليثي حجازي، بعثه النبي صلى الله عليه وسلم عام الفتح ليسهل لهم الطريق','7655','2602','0');
INSERT INTO [t10490] VALUES (2605,'غرفة بن الحارث الكندي وقيل: الأزدي، روى عنه كعب بن علقمة، وعبد الله بن الحارث','7658','2602','0');
INSERT INTO [t10490] VALUES (2606,'غزية بن الحارث الأنصاري له صحبة، يعد في المدنيين','7661','2602','0');
INSERT INTO [t10490] VALUES (2607,'غزية بن عمرو بن عطية الأنصاري من بني مازن بن النجار، شهد العقبة وبايع','7664','2602','0');
INSERT INTO [t10490] VALUES (2608,'غيلان بن سلمة الثقفي، حجازي ذكره في حديث عبد الله بن عمر، روى عنه بشر بن عاصم الثقفي، ومولاه نافع أبو السائب، وعروة بن غيلان','7666','2602','0');
INSERT INTO [t10490] VALUES (2609,'غيلان بن عمرو له ذكر في حديث أسامة الهذلي','7674','2602','0');
INSERT INTO [t10490] VALUES (2610,'غطيف أبو عياض سمع النبي صلى الله عليه وسلم، روى حديثه معاوية بن عياض بن غطيف، عن أبيه، عن جده، وهو من كندة، له صحبة','7676','2602','0');
INSERT INTO [t10490] VALUES (2611,'غطيف بن أبي سفيان حدث عن النبي صلى الله عليه وسلم، ذكره الحسن بن سفيان وغيره في الصحابة ولا يصح، هو تابعي من أهل مكة، يروي عن يعقوب، ونافع ابني عاصم','7678','2602','0');
INSERT INTO [t10490] VALUES (2612,'غضيف أو أبو غضيف له صحبة، وقال بعض المتأخرين: غطيف أو أبو غطيف بالطاء ورواه من حديث علي بن عبد العزيز، عن أبي غسان','7680','2602','0');
INSERT INTO [t10490] VALUES (2613,'غضيف بن الحارث الكندي وقيل: الأزدي، هو ابن زنيم الثمالي، عداده في الحمصيين، يكنى أبا أسماء، قاله عبد الصمد بن سعيد الحمصي','7682','2602','0');
INSERT INTO [t10490] VALUES (2614,'غسان أبو يحيى وفد على النبي صلى الله عليه وسلم، روى عنه ابنه يحيى','7686','2602','0');
INSERT INTO [t10490] VALUES (2615,'غنام أبو عبد الرحمن حديثه عند ابنه عبد الرحمن، سكن المدينة','7688','2602','0');
INSERT INTO [t10490] VALUES (2616,'غنيم بن قيس المازني روى عنه ابنه جناح، أدرك عهد النبي صلى الله عليه وسلم، ولا يصح له رؤية ولا صحبة، ذكره بعض المتأخرين، ولم يزد عليه','7690','2602','0');
INSERT INTO [t10490] VALUES (2617,'غرقدة أبو شبيب ذكره بعض المتأخرين، وقال: ذكر في الصحابة، ولا يصح','7691','2602','0');
INSERT INTO [t10490] VALUES (2618,'غني بن قطيب شهد فتح مصر، ذكر في الصحابة، ولا يعرف له رواية، قاله المحيل بذكره على أبي سعيد بن عبد الأعلى','7692','2602','0');
INSERT INTO [t10490] VALUES (2619,'باب الفاء','7693','18','0');
INSERT INTO [t10490] VALUES (2620,'الفضل بن العباس بن عبد المطلب أول ولد العباس وبكره، كان يكنى العباس أبا الفضل، أمه لبابة بنت الحارث، وكانت تكنى: بأم الفضل، وهي بنت الحارث بن حزن بن بجير بن الهذم بن رؤيبة بن عبد الله بن هلال بن عامر بن صعصعة بن معاوية بن بكر بن هوازن بن منصور بن','7694','2619','0');
INSERT INTO [t10490] VALUES (2621,'الفضل بن يحيى بن قيوم الأزدي ذكره بعض المتأخرين، وقال: اختلف في صحبته، عداده في أهل فلسطين ووهم؛ فإن الفضل يروي عن أبيه، عن جده قيوم الذي سماه النبي صلى الله عليه وسلم عبد القيوم، ذكره من حديث أبي بشر الدولابي، ثنا موسى بن سهل، قال: والفضل','7701','2619','0');
INSERT INTO [t10490] VALUES (2622,'فضالة بن عبيد الأنصاري هو ابن نافذ بن قيس بن صهيبة بن أصرم بن جحجبي بن كلفة بن عوف، قيل: إنه كان فيمن بايع تحت الشجرة، وقيل: إنه شهد أحدا والخندق، وشهد فتح مصر، توفي بدمشق سنة ثلاث وخمسين، ولي القضاء بها لمعاوية، وعقبه بالشام، وأمه: غفرة بنت','7702','2619','0');
INSERT INTO [t10490] VALUES (2623,'فضالة الليثي ويعرف بالزهراني أبو عبد الله، غير منسوب، حديثه عند ابنه عبد الله','7708','2619','0');
INSERT INTO [t10490] VALUES (2624,'فضالة بن هند الأسلمي ذكره بعض المتأخرين وزعم أن له صحبة، عداده في المدنيين، وأخرج له هذا الحديث','7710','2619','0');
INSERT INTO [t10490] VALUES (2625,'فاكه بن سعد أبو عقبة الأنصاري له صحبة، حديثه عند أبي جعفر الخطمي','7712','2619','0');
INSERT INTO [t10490] VALUES (2626,'فروة بن مسيك المرادي وهو ابن الحارث بن سلمة بن الحارث الغطيفي، قدم على النبي صلى الله عليه وسلم في سنة عشر، فأسلم، فبعثه على مراد، وزبير، ومذحج روى عنه يحيى بن هانئ، وأبو سبرة والشعبي، وسعيد بن أبيض بن حمال، والبراء بن عبد الرحمن، وابن عابس،','7715','2619','0');
INSERT INTO [t10490] VALUES (2627,'فروة بن عامر الجذامي وقيل: ابن نعامة، وقيل: ابن نباتة، وقيل: ابن نفاثة المهدي إلى النبي صلى الله عليه وسلم بغلته البيضاء، سكن عمان الشام','7718','2619','0');
INSERT INTO [t10490] VALUES (2628,'فروة بن عمرو الأنصاري ثم البياضي، عقبي بدري، وهو الذي أتى بعرق التمر، فأعطاه النبي صلى الله عليه وسلم المتظاهر من امرأته سلمة بن صخر','7720','2619','0');
INSERT INTO [t10490] VALUES (2629,'فروة بن قيس أدرك النبي صلى الله عليه وسلم، ولا يعرف له رواية، فيما قاله بعض المتأخرين، وأخرج له هذا الحديث','7724','2619','0');
INSERT INTO [t10490] VALUES (2630,'فروة، له صحبة ذكره البخاري غير منسوب، روى حديثه معاوية بن صالح، عن أبي عمرو، عن بشير، كذا ذكره بعض المتأخرين عنه','7726','2619','0');
INSERT INTO [t10490] VALUES (2631,'فروة روى عنه حسان بن عطية مرسلا. ذكره بعض المتأخرين، وقال: هو مجهول قال الشيخ: والذي روى عنه حسان هو فروة بن نوفل','7727','2619','0');
INSERT INTO [t10490] VALUES (2632,'فجيع بن عبد الله العامري من ساكني الكوفة، كتب له النبي صلى الله عليه وسلم كتابا، وسأل النبي صلى الله عليه وسلم عما يحل له ويحرم','7728','2619','0');
INSERT INTO [t10490] VALUES (2633,'الفلتان بن عاصم الجرمي روى حديثه كليب الجرمي، وهو خال عاصم بن كليب، روى غير حديث','7732','2619','0');
INSERT INTO [t10490] VALUES (2634,'فرات بن حيان العجلي وهو فرات بن حيان بن ثعلبة بن عبد العزى بن حبيب بن حية بن ربيعة بن سعد بن عجل، حليف بني سهم، كان دليلا هاديا للطرق، بعث رسول الله صلى الله عليه وسلم سرية لزيد بن حارثة في مائة راكب ليعترضوا لعير قريش، وكان دليلهم فرات بن حيان،','7734','2619','0');
INSERT INTO [t10490] VALUES (2635,'فرات النجراني غير منسوب، عداده في أهل الشام، أدرك النبي صلى الله عليه وسلم، ولا تصح له رؤية، كذا ذكره بعض المتأخرين، وأخرج له حديث الزبيدي هذا وهو خطأ إنما هو فرات البهراني لا النجراني، وروى عن أبي عامر الأشعري، عن النبي صلى الله عليه وسلم، هذا','7738','2619','0');
INSERT INTO [t10490] VALUES (2636,'فديك أبو بشير أتى النبي صلى الله عليه وسلم فسأله عن الهجرة','7740','2619','0');
INSERT INTO [t10490] VALUES (2637,'فيروز الديلمي ابن أخت النجاشي، قاتل الأسود العنسي المتنبئ، خدم النبي صلى الله عليه وسلم وسأله عن الأشربة، سكن الشام، روى عن النبي صلى الله عليه وسلم غير حديث، يكنى أبا عبد الرحمن، روى عنه ابناه الضحاك، وعبد الله، وكثير بن مرة، وعروة بن رويم','7743','2619','0');
INSERT INTO [t10490] VALUES (2638,'فراس بن عمرو الليثي له رؤية، حمله أبوه إلى النبي صلى الله عليه وسلم، روى حديثه شريح بن مسلمة','7747','2619','0');
INSERT INTO [t10490] VALUES (2639,'الفراسي حديثه عند بكر بن سوادة، عن مسلم بن مخشي','7749','2619','0');
INSERT INTO [t10490] VALUES (2640,'فاتك بن عمرو الخطمي','7751','2619','0');
INSERT INTO [t10490] VALUES (2641,'فرقد له صحبة ذكره بعض المتأخرين','7753','2619','0');
INSERT INTO [t10490] VALUES (2642,'فرقد أكل على مائدة النبي صلى الله عليه وسلم ذكره بعض المتأخرين، وهو وهم','7755','2619','0');
INSERT INTO [t10490] VALUES (2643,'باب القاف','7757','18','0');
INSERT INTO [t10490] VALUES (2644,'*','7758','2643','0');
INSERT INTO [t10490] VALUES (2645,'قيس بن عاصم المنقري وهو قيس بن عاصم بن سنان بن خالد بن منقر بن عبيد بن مقاعس، واسمه الحارث، وسمي مقاعسا؛ لتقاعسه عن حلف بني سعد، وهو الحارث بن عمرو بن كعب بن سعد بن زيد مناة بن تميم بن مر بن أد بن طابخة بن إلياس بن مضر بن نزار أمه أم أصفر بنت خليفة','7758','2644','0');
INSERT INTO [t10490] VALUES (2646,'قيس بن مخرمة بن المطلب بن عبد مناف بن قصي بن كلاب بن مرة بن كعب بن لؤي بن غالب، لدة النبي صلى الله عليه وسلم، روى عنه ابناه عبد الله ومحمد','7764','2644','0');
INSERT INTO [t10490] VALUES (2647,'قيس بن أبي صعصعة الأنصاري عقبي، بدري من الخزرج، من بني مازن بن النجار، حديثه عند واسع بن حبان، جعله النبي صلى الله عليه وسلم على الساقة في مخرجه إلى بدر وقيل: إن اسم أبي صعصعة: عمرو بن زيد بن عوف بن مبذول بن عمرو بن غنم بن مازن بن النجار، قاله','7768','2644','0');
INSERT INTO [t10490] VALUES (2648,'قيس بن النعمان السكوني وقيل: القيسي، وفد على النبي صلى الله عليه وسلم، حديثه في الكوفيين، والبصريين فيما قاله البخاري، روى عنه: إياد بن لقيط، وزيد بن علي أبو القموص','7772','2644','0');
INSERT INTO [t10490] VALUES (2649,'قيس بن سعد بن عبادة بن دليم الأنصاري الخزرجي خادم النبي صلى الله عليه وسلم وحاجبه، وصاحب لوائه، كان من دهاة العرب المذكورين بالدهاء، روى عنه: أنس بن مالك، والشعبي، وميمون بن أبي شبيب، وعمرو بن شرحبيل، ولاه علي بن أبي طالب رضي الله عنهما مصر فاختط','7774','2644','0');
INSERT INTO [t10490] VALUES (2650,'قيس بن أبي غرزة الغفاري ويقال: البجلي، وقيل: الجهني، سكن الكوفة، حديثه عند شقيق أبي وائل','7783','2644','0');
INSERT INTO [t10490] VALUES (2651,'قيس بن قهد الأنصاري مختلف في اسم أبيه، فقيل: قيس بن عمرو، وقيل: قيس بن سهل، وقيل: ابن قهد، وهو جد يحيى بن سعيد الأنصاري، وهو قيس بن سهل بن ثعلبة بن الحارث بن زيد بن ثعلبة بن عبيد بن ثعلبة بن غنم بن مالك بن النجار، روى عنه: ابنه سعيد، وعطاء بن','7785','2644','0');
INSERT INTO [t10490] VALUES (2652,'قيس بن عائذ أبو كاهل الأحمسي، مختلف في اسمه، فقيل: عبد الله بن مالك، قاله البخاري، حديثه عند إسماعيل، والأشعث ابني أبي خالد، ونفيع أبي داود','7787','2644','0');
INSERT INTO [t10490] VALUES (2653,'قيس بن الحارث بن جدار وقيل: ابن عميرة الأسدي، وقيل: الحارث بن قيس، حديثه عند حميضة بن الشمردل، وعائذ بن نصيب، وقال قيس بن الربيع: هو جدي، كانت العرب تتحاكم إليه في الجاهلية','7789','2644','0');
INSERT INTO [t10490] VALUES (2654,'قيس بن السكن بن قيس بن زعوراء بن حرام من بني عدي بن النجار أبو زيد القارئ، مختلف في اسمه، فقيل: سعد بن عمير بن النعمان، وقيل: ثابت أحد من جمع القرآن في عهد النبي صلى الله عليه وسلم، شهد بدرا واستشهد يوم جسر أبي عبيد','7792','2644','0');
INSERT INTO [t10490] VALUES (2655,'قيس بن مخلد بن ثعلبة الأنصاري شهد بدرا','7795','2644','0');
INSERT INTO [t10490] VALUES (2656,'قيس بن عبد المنذر الأنصاري قتل ببدر، نزلت فيه وفي أصحابه: ولا تقولوا لمن يقتل في سبيل الله أموات بل أحياء الآية','7798','2644','0');
INSERT INTO [t10490] VALUES (2657,'قيس بن عبد الله أبو ليلى الجعدي نابغة بني جعدة، هو قيس بن عبد الله بن عدس بن ربيعة بن جعدة بن كعب بن ربيعة بن عامر بن صعصعة','7800','2644','0');
INSERT INTO [t10490] VALUES (2658,'قيس بن سلع الأنصاري من أهل المدينة، حديثه عند نافع مولى حمنة','7803','2644','0');
INSERT INTO [t10490] VALUES (2659,'قيس بن السائب بن عائذ بن عبد الله بن عمر بن مخزوم، شريك النبي صلى الله عليه وسلم في الجاهلية','7805','2644','0');
INSERT INTO [t10490] VALUES (2660,'قيس بن كلاب الكلابي من أهل اليمن، حديثه عند عبد الله بن حكيم الكناني','7808','2644','0');
INSERT INTO [t10490] VALUES (2661,'قيس بن مالك الأرحبي كاتبه النبي صلى الله عليه وسلم وأسلم بعد أن كتب إليه','7810','2644','0');
INSERT INTO [t10490] VALUES (2662,'قيس بن خرشة القيسي أتى النبي صلى الله عليه وسلم فبايعه على أن يقول بالحق','7812','2644','0');
INSERT INTO [t10490] VALUES (2663,'قيس بن الهيثم السلمي جد عبد القاهر بن السري، يروي عنه عطية الدعاء، ذكره البخاري في الوحدان من الصحابة، ولم يذكر له حديثا، ذكره بعض المتأخرين مقتصرا على اسمه، وأخرجه القاضي أبو أحمد في التابعين، وقال: يعد في البصريين','7815','2644','0');
INSERT INTO [t10490] VALUES (2664,'قيس التميمي حديثه عند المغيرة بن شبيل الأحمسي','7816','2644','0');
INSERT INTO [t10490] VALUES (2665,'قيس بن زيد الجهني وقيل: ابن يزيد، يعد في الكوفيين، روى عنه الشعبي','7818','2644','0');
INSERT INTO [t10490] VALUES (2666,'قيس بن زيد مجهول، غير المتقدم، حديثه عند أبي عمران الجوني، لا يصح له صحبة ولا رؤية','7820','2644','0');
INSERT INTO [t10490] VALUES (2667,'قيس بن عبد العزى روى عنه أنس بن مالك','7822','2644','0');
INSERT INTO [t10490] VALUES (2668,'قيس الجذامي وقيل: قيس بن زيد بن جبا الجذامي','7824','2644','0');
INSERT INTO [t10490] VALUES (2669,'قيس بن الخشخاش العنبري سكن البصرة، أخو مالك وعبيد، كتب لهم النبي صلى الله عليه وسلم كتابا تقدم ذكره في ترجمة عبيد','7828','2644','0');
INSERT INTO [t10490] VALUES (2670,'قيس أبو غنيم أدرك النبي صلى الله عليه وسلم، سكن البصرة','7830','2644','0');
INSERT INTO [t10490] VALUES (2671,'قيس أبو ثابت الأنصاري جد عدي بن ثابت، روى عن النبي صلى الله عليه وسلم في المستحاضة، وقيل: هو قيس بن دينار','7832','2644','0');
INSERT INTO [t10490] VALUES (2672,'قيس أبو يعيش الغفاري','7835','2644','0');
INSERT INTO [t10490] VALUES (2673,'قيس أبو محمد غير منسوب','7837','2644','0');
INSERT INTO [t10490] VALUES (2674,'قيس بن خارجة ذكره الحضرمي والمنيعي في الوحدان من الصحابة','7839','2644','0');
INSERT INTO [t10490] VALUES (2675,'قيس بن عبد الله من بني أسد بن خزيمة، أبو آمنة بنت قيس، هاجر إلى أرض الحبشة مع امرأته بركة مولاة أبي سفيان بن حرب','7841','2644','0');
INSERT INTO [t10490] VALUES (2676,'وقيس بن حذافة بن قيس بن عدي بن سعيد بن سهم، من مهاجرة الحبشة، هاجر في الهجرة الأخيرة " حدثنا بذلك، حبيب بن الحسن، ثنا محمد بن يحيى، ثنا أحمد بن محمد، ثنا إبراهيم بن سعد، عن محمد بن إسحاق، بذلك','7842','2644','0');
INSERT INTO [t10490] VALUES (2677,'وقيس بن معبد الحنفي أخو يزيد بن معبد، له ذكر في حديث أخيه','7843','2644','0');
INSERT INTO [t10490] VALUES (2678,'قيس بن أبي العاص بن قيس بن عدي بن سعيد بن سهم، شهد فتح مصر، واختط بها دارا، ولي بمصر القضاء لعمر بن الخطاب، قاله ابن لهيعة، عن يزيد بن أبي حبيب','7844','2644','0');
INSERT INTO [t10490] VALUES (2679,'قيس بن عباد روى عن النبي صلى الله عليه وسلم في قاتل نفسه، يعد في الشاميين، حديثه عند أبي أيوب الدمشقي سليمان بن عبد الرحمن، عن الوليد بن مسلم، عن حفص بن غيلان، عن العباس بن ميمون، عنه ولا يصح له صحبة ولا رؤية','7845','2644','0');
INSERT INTO [t10490] VALUES (2680,'قيس بن أبي حازم أدرك النبي صلى الله عليه وسلم، ولا يصح له رؤية، رأى أبا بكر الصديق، والعشرة، وروى عنهم','7846','2644','0');
INSERT INTO [t10490] VALUES (2681,'قبيصة بن مخارق بن عبد الله بن شداد بن أبي ربيعة بن نهيك بن هلال بن عامر بن صعصعة روى عنه كنانة بن نعيم، وقطن بن قبيصة، وأبو عثمان النهدي','7848','2644','0');
INSERT INTO [t10490] VALUES (2682,'قبيصة بن وقاص السلمي له صحبة، يعد في البصريين','7853','2644','0');
INSERT INTO [t10490] VALUES (2683,'قبيصة البجلي روى عن النبي صلى الله عليه وسلم في الكسوف، ذكره بعض المتأخرين، وهو عندي قبيصة بن مخارق الهلالي المتقدم، والبجلي وهم','7855','2644','0');
INSERT INTO [t10490] VALUES (2684,'قبيصة بن برمة الأسدي له صحبة، يروي عن عبد الله بن مسعود، والمغيرة بن شعبة، روى عنه واصل بن حبان الأحدب','7857','2644','0');
INSERT INTO [t10490] VALUES (2685,'قبيصة، غير منسوب أتى النبي صلى الله عليه وسلم فسأله، كذا ذكره بعض المتأخرين، وقال: غير منسوب وجعله ترجمة، وأخرج له حديث عطاء، عن ابن عباس من رواية محمد بن الفضل بن عطية، عن عطاء، ولم ينسب قبيصة ورواه نافع بن عبد الله، عن عطاء، عن ابن عباس فنسبه','7860','2644','0');
INSERT INTO [t10490] VALUES (2686,'قبيصة بن البراء ذكره بعض المتأخرين، وقال: ذكر في الصحابة، ولا يثبت وأخرج له هذا الحديث','7862','2644','0');
INSERT INTO [t10490] VALUES (2687,'من اسمه قتادة','7864','2643','0');
INSERT INTO [t10490] VALUES (2688,'قتادة بن النعمان بن زيد بن عامر بن سواد بن كعب، واسم كعب: ظفر بن الخزرج الظفري الأنصاري أخو أبي سعيد الخدري لأمه، يكنى أبا عثمان، وقيل: أبو عمر، شهد بدرا، وأحدا، والمشاهد، وأصيبت عيناه، فسقطت حدقتاه، فردهما النبي صلى الله عليه وسلم وبصق فيهما','7865','2687','0');
INSERT INTO [t10490] VALUES (2689,'قتادة بن ملحان القيسي من بني قيس بن ثعلبة، مسح النبي صلى الله عليه وسلم وجهه، روى عنه ابنه، وحيان بن عميرة','7874','2687','0');
INSERT INTO [t10490] VALUES (2690,'قتادة بن الأعور أبو جون بن قتادة التميمي، ذكره المنيعي في الوحدان، وقال: قال محمد بن سعد: قتادة بن الأعور بن ساعدة بن عون بن كعب بن عبد شمس بن سعد بن زيد مناة بن تميم، صحب النبي صلى الله عليه وسلم قبل الوفد، وكتب له النبي صلى الله عليه وسلم كتابا','7877','2687','0');
INSERT INTO [t10490] VALUES (2691,'قتادة بن عياش أبو هشام الرهاوي، حديثه عند أولاده','7878','2687','0');
INSERT INTO [t10490] VALUES (2692,'قتادة بن قيس بن حبشي الصدفي لا يعرف له رواية، شهد فتح مصر، ذكره المحيل على أبي سعيد بن عبد الأعلى','7880','2687','0');
INSERT INTO [t10490] VALUES (2693,'قتادة بن أوفى ذكره المنيعي، عن ابن سعد الواقدي، وقال: قتادة بن أوفي بن مولة بن عتبة بن ملادس بن قتادة، من عبد شمس بن سعد بن زيد مناة بن تميم، له صحبة، وهو أبو إياس بن قتادة، وأم إياس بن قتادة: الفارعة بنت حمير بن عبادة بن النزال بن مرة، ولا أعلم','7881','2687','0');
INSERT INTO [t10490] VALUES (2694,'*','7882','2643','0');
INSERT INTO [t10490] VALUES (2695,'قطبة بن مالك الثعلبي عم زياد بن علاقة، يعد في الكوفيين','7882','2694','0');
INSERT INTO [t10490] VALUES (2696,'قطبة بن قتادة السدوسي بايع النبي صلى الله عليه وسلم فكناه بأبي الحوصلة، عداده في البصريين، حديثه عند مقاتل السدوسي، وقيل: قطبة بن قتادة بن جرير بن إساف من ولد الخصاصية','7886','2694','0');
INSERT INTO [t10490] VALUES (2697,'قطبة بن عامر بن حديدة بن عمرو بن سواد السلمي الأنصاري شهد بدرا والعقبة، نزلت فيه: ليس البر','7889','2694','0');
INSERT INTO [t10490] VALUES (2698,'قدامة بن مظعون الجمحي القرشي أخو عثمان، خال حفصة، وعبد الله ابني عمر، شهد بدرا، روى عن أخيه عثمان بن مظعون، وروى عنه عبد الله بن عمر، وعبد الله بن عامر بن ربيعة، استعمله عمر بن الخطاب على البحرين، وهو المجلود في الشراب','7893','2694','0');
INSERT INTO [t10490] VALUES (2699,'قدامة بن عبد الله بن عمار الكلابي حديثه عند أيمن بن نابل، وحميد بن كلاب، عداده في الحجازيين، له صحبة، سكن مكة','7898','2694','0');
INSERT INTO [t10490] VALUES (2700,'قدامة بن حنظلة الثقفي عداده في الحمصيين، روى عنه غضيف بن الحارث، ذكره بعض المتأخرين','7906','2694','0');
INSERT INTO [t10490] VALUES (2701,'قدامة بن مالك بن خارجة بن عمرو بن مالك بن زيد بن مرة من ولد سعد العشيرة وفد على النبي صلى الله عليه وسلم، وشهد فتح مصر، وقال سعيد بن عفير: قيل: إن الذي كان بمصر مالك بن قدامة بن مالك ابنه، قاله المحيل بذكره على أبي سعيد بن يونس بن عبد الأعلى','7908','2694','0');
INSERT INTO [t10490] VALUES (2702,'قرة بن إياس بن هلال ابن رئاب بن عبيد بن سواءة بن دينار بن ثعلبة بن سليم بن أوس بن عمير بن أد بن طابخة المزني، ومزينة امرأة يقال لها: مزينة بنت كلب بن وبرة، سكن البصرة، يكنى أبا معاوية، روى عنه ابنه معاوية وقتل قرة قتلا، وكان معاوية بن قرة يقول:','7909','2694','0');
INSERT INTO [t10490] VALUES (2703,'قرة بن دعموص بن ربيعة بن عوف بن معاوية بن قريع بن الحارث بن نمير بن عامر النميري، وفد على رسول الله صلى الله عليه وسلم مع أعمامه، وقيس بن عاصم، والحارث بن شريح، وكتب له كتابا','7916','2694','0');
INSERT INTO [t10490] VALUES (2704,'قرة بن هبيرة القشيري له صحبة، حديثه عند الشاميين','7920','2694','0');
INSERT INTO [t10490] VALUES (2705,'القاسم ابن رسول الله صلى الله عليه وسلم ذكره بعض المتأخرين، وأخرج حديث الحكم، عن مقسم، عن ابن عباس، قال: " ولدت خديجة غلامين لرسول الله صلى الله عليه وسلم: القاسم، وعبد الله " ويقول معمر، عن الزهري: " لبث رسول الله صلى الله عليه وسلم مع خديجة','7922','2694','0');
INSERT INTO [t10490] VALUES (2706,'والقاسم الأنصاري غلام من الأنصار، له ذكر في حديث جابر، ولا يصح له حديث، ولا يعرف له رواية، ذكره بعض المتأخرين، وقال: رواه روح بن القاسم، وابن عيينة، عن أبي الزبير، عن جابر، ووهم، فإنما روياه عن ابن المنكدر، عن جابر','7923','2694','0');
INSERT INTO [t10490] VALUES (2707,'القاسم مولى أبي بكر الصديق حديثه عند مطرف بن طريف، عن أبي الجهم، مولى البراء عنه، ذكره المنيعي في الصحابة، ولم يتابع عليه','7928','2694','0');
INSERT INTO [t10490] VALUES (2708,'أبو العاص بن الربيع ختن رسول الله صلى الله عليه وسلم بابنته زينب، قيل: إن اسمه القاسم، وقيل: لقيط، مختلف فيه، ذكره المنيعي، عن الزبير بن بكار','7929','2694','0');
INSERT INTO [t10490] VALUES (2709,'قثم بن العباس بن عبد المطلب رديف النبي صلى الله عليه وسلم، روى عنه عبد الله بن عباس، وعبد الله بن جعفر','7931','2694','0');
INSERT INTO [t10490] VALUES (2710,'قباث بن أشيم بن عامر بن الملوح بن يعمر بن عوف بن كعب بن عامر بن ليث بن بكر بن عبد مناة بن كنانة بن خزيمة بن مدركة بن إلياس بن مضر، شهد بدرا مع المشركين على رسول الله صلى الله عليه وسلم، ثم أسلم فحسن إسلامه، كان قديما أدرك أمية بن عبد شمس، وعقل الفيل','7934','2694','0');
INSERT INTO [t10490] VALUES (2711,'قرظة بن كعب بن عمرو بن عامر بن زيد بن مناة بن مالك بن الأبجر، شهد أحدا وما بعده من المشاهد، أمه جنيدة بنت ثابت بن سنان بن عبيد بن الأبجر، وأخوه لأمه عبد الله بن إياس، بعثه عمر بن الخطاب رضي الله عنهم في أصحاب له إلى الكوفة يقرئهم القرآن، وأوصاهم','7940','2694','0');
INSERT INTO [t10490] VALUES (2712,'قهيد بن مطرف الغفاري سكن الحجاز، حديثه عند المطلب بن عبد الله بن حنطب','7944','2694','0');
INSERT INTO [t10490] VALUES (2713,'قارب الثقفي روى عن النبي صلى الله عليه وسلم في الترحم على المحلقين، حديثه عند أولاده','7946','2694','0');
INSERT INTO [t10490] VALUES (2714,'القعقاع بن أبي حدرد الأسلمي','7948','2694','0');
INSERT INTO [t10490] VALUES (2715,'القعقاع بن معبد بن زرارة التميمي وفد على النبي صلى الله عليه وسلم','7950','2694','0');
INSERT INTO [t10490] VALUES (2716,'قيوم أبو يحيى الأزدي وفد على النبي صلى الله عليه وسلم مع أبي راشد في وفد اليمن فسماه عبد القيوم تقدم في حرف العين','7952','2694','0');
INSERT INTO [t10490] VALUES (2717,'قين الأشجعي له ذكر في حديث أبي هريرة حين قال له: فكيف بالمهراس؟ قال: أعوذ بالله من شرك يا قين " رواه يحيى بن أبي كثير، عن أبي سلمة، عن أبي هريرة. ذكره بعض المتأخرين في الصحابة، ولا حقيقة لصحبته','7953','2694','0');
INSERT INTO [t10490] VALUES (2718,'قيظي بن قيس ذكره المنيعي، عن محمد بن سعد، كاتب الواقدي فنسبه، قال: قيظي بن قيس بن لوذان بن ثعلبة بن عدي بن مجدعة بن حارثة، أمه: لبنى بنت رافع بن عدي بن زيد بن جشم بن حارثة، شهد قيظي أحدا هو وثلاثة من أولاده: عقبة، وعبد الله، وعبد الرحمن بنو','7954','2694','0');
INSERT INTO [t10490] VALUES (2719,'أبو إسرائيل الذي نذر أن يصوم ولا يتكلم، ويقوم في الشمس، اسمه قشير ذكره المنيعي فسماه قشيرا','7955','2694','0');
INSERT INTO [t10490] VALUES (2720,'قاطع بن سارق أبو صفرة كناه النبي صلى الله عليه وسلم أبا صفرة','7956','2694','0');
INSERT INTO [t10490] VALUES (2721,'قيسبة بن كلثوم بن حباشة وفد على النبي صلى الله عليه وسلم، وشهد فتح مصر، له ذكر، ولا رواية له، قاله أبو سعيد بن يونس بن عبد الأعلى فيما حكاه عنه المحيل عليه','7958','2694','0');
INSERT INTO [t10490] VALUES (2722,'قفيز غلام النبي صلى الله عليه وسلم','7959','2694','0');
INSERT INTO [t10490] VALUES (2723,'قسامة بن حنظلة الطائي قدم على النبي صلى الله عليه وسلم، له ذكر في حديث طلحة بن عبيد الله، فيما ذكره بعض المتأخرين','7961','2694','0');
INSERT INTO [t10490] VALUES (2724,'باب الكاف','7962','18','0');
INSERT INTO [t10490] VALUES (2725,'كعب بن مالك بن أبي كعب بن القين بن كعب بن سواد بن غنم بن كعب بن سلمة السلمي الأنصاري الخزرجي الشاعر، شهد بيعة العقبة مع السبعين، يكنى أبا عبد الله، وقيل: أبو عبد الرحمن، كانت كنيته أبا بشير في الجاهلية، أحد المخلفين من الثلاثة الذين خلفوا فتيب','7963','2724','0');
INSERT INTO [t10490] VALUES (2726,'كعب بن عمرو أبو اليسر الخزرجي عقبي بدري وهو كعب بن عمرو بن عباد بن عمرو بن غنم بن سواد بن غنم بن كعب بن سلمة بن علي، وهو الذي أسر العباس بن عبد المطلب يوم بدر، وكان رجلا قصيرا دحداحا، آخر من مات بالمدينة ممن شهد بدرا سنة خمس وخمسين، روى عنه ابنه عمار','7971','2724','0');
INSERT INTO [t10490] VALUES (2727,'كعب بن عجرة أبو محمد الأنصاري السلمي قال محمد بن سعد الواقدي: لم يوجد نسبه في كتاب نسب الأنصار وقال محمد بن هشام الكلبي: هو كعب بن عجرة بن أمية بن عدي بن عبيد بن الحارث بن عمرو بن عوف بن غنم بن سويد بن مري بن أراشة بن عامر بن عبيلة بن قشميل بن قران بن','7979','2724','0');
INSERT INTO [t10490] VALUES (2728,'كعب بن عاصم الأشعري يكنى: أبا مالك، كان من أصحاب السفينة، سكن مصر، وقيل: الشام، روى عنه جابر بن عبد الله، وأم الدرداء، وعبد الرحمن بن غنم، وخالد بن أبي مريم','7986','2724','0');
INSERT INTO [t10490] VALUES (2729,'كعب بن عياض الأشعري يعد في الشاميين، حديثه عند جبير بن نفير الحضرمي','7988','2724','0');
INSERT INTO [t10490] VALUES (2730,'كعب بن مرة البهزي وقيل: مرة بن كعب، مختلف فيه، حديثه عند شرحبيل بن السمط، وسالم بن أبي الجعد','7990','2724','0');
INSERT INTO [t10490] VALUES (2731,'كعب العبادي وهو كعب بن عدي بن حنظلة بن عدي بن عمرو بن ثعلبة بن عدي بن ملكان بن عوف بن عذرة بن زيد اللات، وهو الذي يقال له: التنوخي، وحالف بنو ملكان بن عوف تنوخا فنسب إليهم، وهو العبادي من عباد الحيرة، وفد مع وفد الحيرة على رسول الله صلى الله عليه','7994','2724','0');
INSERT INTO [t10490] VALUES (2732,'كعب بن عمرو اليامي جد طلحة بن مصرف','7996','2724','0');
INSERT INTO [t10490] VALUES (2733,'كعب بن زهير بن أبي سلمى الشاعر واسم أبي سلمى: ربيعة بن رباح بن قرط بن الحارث بن مازن بن ثعلبة بن ثور بن هذمة بن لاطم بن عثمان بن مزينة، أسلم بعد الفتح بعد منصرف النبي صلى الله عليه وسلم من الطائف، كاتبه أخوه بجير بن زهير يحثه على الإسلام، أو يحترز من','7998','2724','0');
INSERT INTO [t10490] VALUES (2734,'كعب بن زيد بن قيس بن مالك الأنصاري النجاري، شهد بدرا','8000','2724','0');
INSERT INTO [t10490] VALUES (2735,'كعب بن جماز بن ثعلبة الجهني وقيل: الغساني، حليف بني ساعدة بن كعب بن الخزرج شهد بدرا وقيل: حليف بني طريف بن الخزرج','8002','2724','0');
INSERT INTO [t10490] VALUES (2736,'كعب بن زيد بن قيس الأنصاري من بني دينار بن النجار، شهد بدرا، وأسند عن النبي صلى الله عليه وسلم','8005','2724','0');
INSERT INTO [t10490] VALUES (2737,'كعب بن سليم القرظي والد محمد بن كعب، قيل: إنه كان ممن لم ينبت يوم قريظة فترك، ذكر بعض المتأخرين قال: روى حديثه حاتم بن إسماعيل، عن الجعيد بن عبد الرحمن، عن موسى بن عبد الرحمن، عن محمد بن كعب القرظي، عن أبيه، ووهم، فإن قوله: عن أبيه، ليس هو','8008','2724','0');
INSERT INTO [t10490] VALUES (2738,'كعب بن يسار بن ضنة بن ربيعة بن قزعة بن عبد الله بن مخزوم بن غالب بن قطيعة، ذكره بعض المتأخرين، عن أبي سعيد بن عبد الأعلى: أنه من الصحابة، وشهد فتح مصر، واختط بها , وولي القضاء، وقال سعيد بن عفير: هو أول قاض استقضي بها في الإسلام، وكان قاضيا في','8009','2724','0');
INSERT INTO [t10490] VALUES (2739,'كعب: غير منسوب روى عنه علقمة بن نضلة فيما قاله بعض المتأخرين، وأخرج له حديث أبي عاصم','8011','2724','0');
INSERT INTO [t10490] VALUES (2740,'كعب: له صحبة فيما ذكره بعض المتأخرين , وروى له هذا الحديث في صلاة الخوف موقوفا، وقال: عداده في أهل مصر، روى عنه: زياد بن نافع','8013','2724','0');
INSERT INTO [t10490] VALUES (2741,'كعب بن الخدارية له ذكر في حديث أبي رزين العقيلي','8016','2724','0');
INSERT INTO [t10490] VALUES (2742,'كعب بن قطبة له ذكر في حديث حكاه بعض المتأخرين , ولم يزد عليه','8017','2724','0');
INSERT INTO [t10490] VALUES (2743,'كعب بن الخزرج الأنصاري من بلحارث , ذكره البخاري فيما حكاه عنه بعض المتأخرين','8018','2724','0');
INSERT INTO [t10490] VALUES (2744,'كعب بن سور الأزدي قتل يوم الجمل، قيل: إن له إدراكا من النبي صلى الله عليه وسلم، وكان قاضيا في بني لقيط بالبصرة، بعثه عمر قاضيا عليها، روى له محمد بن سيرين أحكاما وأخبارا','8020','2724','0');
INSERT INTO [t10490] VALUES (2745,'كعب بن ماتع الحبر أبو إسحاق أدرك عهد النبي صلى الله عليه وسلم ولم يره، كان إسلامه في خلافة عمر رضي الله عنه','8022','2724','0');
INSERT INTO [t10490] VALUES (2746,'كناز بن الحصين أبو مرثد الغنوي، وقيل: ابن حصن بن يربوع بن عمرو بن يربوع بن خرشة بن سعد بن طريف بن حلان بن غنم بن غني بن يعصر بن سعد بن قيس بن غيلان بن مضر، حليف حمزة بن عبد المطلب رضي الله عنهما، شهد بدرا مع رسول الله صلى الله عليه وسلم مات سنة اثنتي','8024','2724','0');
INSERT INTO [t10490] VALUES (2747,'كلثوم بن الحصين أبو رهم الغفاري، وهو كلثوم بن الحصين بن عبيد بن خلف بن قيس بن أحمس بن غفار بن مقبل بن ضمرة بن بكر بن عبد مناة بن كنانة بن مدركة بن إلياس بن مضر، بايع تحت الشجرة، استخلفه النبي صلى الله عليه وسلم على المدينة في بعض مخارجه عام الفتح،','8027','2724','0');
INSERT INTO [t10490] VALUES (2748,'كلثوم الخزاعي سكن الكوفة , روى عنه جامع بن شداد , والزبير بن عدي، ذكره الحضرمي في الوحدان ولا يصح له صحبة','8030','2724','0');
INSERT INTO [t10490] VALUES (2749,'كلثوم بن علقمة بن ناجية بن الحارث المصطلقي، روى عنه ابنه الحضرمي، ذكره بعض المتأخرين، وأخرج له حديث يعقوب بن حميد، عن عيسى بن الحضرمي، وهو وهم , إنما الصحبة لأبيه علقمة','8032','2724','0');
INSERT INTO [t10490] VALUES (2750,'كلثوم بن هدم أخي بني عمرو بن عوف، وقيل: كان أحد بني زيد بن مالك، وقيل: أحد بني عبيد، كان يسكن قباء , وعليه نزل رسول الله صلى الله عليه وسلم لما قدم المدينة بقباء في بني عمرو بن عوف','8034','2724','0');
INSERT INTO [t10490] VALUES (2751,'كثير بن العباس بن عبد المطلب ولد قبل وفاة النبي صلى الله عليه وسلم في عشر من الهجرة','8038','2724','0');
INSERT INTO [t10490] VALUES (2752,'كثير بن أبي كثير له صحبة، يعد في المصريين، حديثه عند عقبة بن مسلم التجيبي','8040','2724','0');
INSERT INTO [t10490] VALUES (2753,'كثير الهاشمي أفرده بعض المتأخرين، وهو كثير بن العباس المتقدم، وأخرج له هذا الحديث','8042','2724','0');
INSERT INTO [t10490] VALUES (2754,'كثير بن الصلت كان اسمه قليلا , فسماه النبي صلى الله عليه وسلم كثيرا','8045','2724','0');
INSERT INTO [t10490] VALUES (2755,'كثير بن شهاب ذكره بعض المتأخرين، وقال: عداده في الكوفيين، وقال: روى عنه عدي بن حاتم - إن كان محفوظا - وأخرج له هذا الحديث','8047','2724','0');
INSERT INTO [t10490] VALUES (2756,'كثير خال البراء بن عازب، حديثه عند حازم البجلي','8049','2724','0');
INSERT INTO [t10490] VALUES (2757,'كثير بن السائب ذكره بعض المتأخرين أنه عرض على النبي صلى الله عليه وسلم , فجعله في الذرية، وأخرج له هذا الحديث','8051','2724','0');
INSERT INTO [t10490] VALUES (2758,'كثير: غير منسوب روى الحسن بن عبد الرحمن بن عوف، عن أبيه قال: قلت لكثير - وكان من الصحابة فيما ذكره بعض المتأخرين - ولم يزد عليه','8053','2724','0');
INSERT INTO [t10490] VALUES (2759,'كليب بن شهاب بن المجنون الجرمي أبو عاصم , حديثه عند ابنه عاصم , له في الصحابة ذكر','8054','2724','0');
INSERT INTO [t10490] VALUES (2760,'كليب أبو كثير الجهني حديثه عند أولاده','8056','2724','0');
INSERT INTO [t10490] VALUES (2761,'كليب أبو منفعة الحنفي روى عنه ابنه منفعة، ذكره بعض المتأخرين , وأخرج له هذا الحديث','8059','2724','0');
INSERT INTO [t10490] VALUES (2762,'كليب بن حزن وقيل: حزم، وقيل جزء بن معاوية بن خفاجة بن عمرو بن عقيل العقيلي','8061','2724','0');
INSERT INTO [t10490] VALUES (2763,'كيسان أبو عبد الرحمن مولى سلمة بن أسيد قاله سليمان، وقيل: كيسان بن عبد الله بن طارق، وقيل: ابن بشير الحجازي، نسبه بعض المتأخرين','8063','2724','0');
INSERT INTO [t10490] VALUES (2764,'كيسان والد نافع ابن كيسان، ويكنى: أبا نافع، أفرده سليمان بن أحمد، عن كيسان أبي عبد الرحمن , وقال: كيسان أبو نافع , غير المتقدم، وجعلهما بعض المتأخرين واحدا','8066','2724','0');
INSERT INTO [t10490] VALUES (2765,'كيسان مولى رسول الله صلى الله عليه وسلم مختلف فيه , فقيل: مهران، وقيل: طهمان، وقيل: هرمز، حديثه عند عطاء بن السائب، عن أم كلثوم بنت علي , عنه','8070','2724','0');
INSERT INTO [t10490] VALUES (2766,'كيسان مولى الأنصار استشهد بأحد','8072','2724','0');
INSERT INTO [t10490] VALUES (2767,'كيسان مولى عتاب ابن أبي أسيد، ذكره بعض المتأخرين أنه أدرك النبي صلى الله عليه وسلم','8074','2724','0');
INSERT INTO [t10490] VALUES (2768,'كردم بن سفيان الثقفي أبو ميمونة، حجازي، حديثه عند ابنته ميمونة، وعبد الله بن عمرو بن العاص','8076','2724','0');
INSERT INTO [t10490] VALUES (2769,'كردم بن قيس الخشني روى عنه: إبراهيم بن عمرو، فرق الطبراني بينه وبين الأول، وكذلك أبو حاتم الرازي أفرده عن المتقدم','8081','2724','0');
INSERT INTO [t10490] VALUES (2770,'كردم بن أبي السائب الأنصاري وقيل: الثقفي، حديثه عند عبد الرحمن بن إسحاق المزني، عن أبيه , عنه','8083','2724','0');
INSERT INTO [t10490] VALUES (2771,'كرز بن عبد وقيل: ابن عبيد العكي سكن فلسطين , حديثه عند أولاده','8085','2724','0');
INSERT INTO [t10490] VALUES (2772,'كهمس الهلالي له صحبة، سكن البصرة، حديثه عند معاوية بن قرة','8087','2724','0');
INSERT INTO [t10490] VALUES (2773,'كلدة بن حنبل الغساني حليف بني جمح، سكن مكة، وهو كلدة بن حنبل بن مالك بن عاتقة بن كلدة، أمه أنيسة بنت معمر بن حبيب بن وهب بن حذافة بن جمح، وهو أخو صفوان بن أمية لأمه , حالف أباه معمرا , وأنكحه ابنته','8089','2724','0');
INSERT INTO [t10490] VALUES (2774,'كرز بن علقمة الخزاعي له صحبة، حديثه عند عروة بن سليمان بن حبيب','8091','2724','0');
INSERT INTO [t10490] VALUES (2775,'كرز بن جابر الفهري بعثه النبي صلى الله عليه وسلم فيمن بعثهم خلف العرنيين أميرا، استشهد يوم الفتح','8094','2724','0');
INSERT INTO [t10490] VALUES (2776,'كرز التميمي غير منسوب، ذكره أبو حاتم، والحضرمي، وغيرهما في الصحابة','8098','2724','0');
INSERT INTO [t10490] VALUES (2777,'كرز بن سامة من بني عامر، وفد إلى النبي صلى الله عليه وسلم، حديثه عند أولاده','8100','2724','0');
INSERT INTO [t10490] VALUES (2778,'كريم بن الحارث جد زرارة، يعد في البصريين، ذكره محمد بن إسماعيل البخاري في الصحابة فيما حكاه عنه بعض المتأخرين، ولم يخرج له شيئا','8103','2724','0');
INSERT INTO [t10490] VALUES (2779,'كريم بن جزيء أتى النبي صلى الله عليه وسلم فسأله عن خشاش الأرض، وهو وهم , وتصحيف، إنما هو خزيمة بن جزيء، حديثه عند أخيه خالد بن جزيء، ذكره المتأخر ولم يخرج له شيئا','8104','2724','0');
INSERT INTO [t10490] VALUES (2780,'كدير الضبي مختلف في صحبته، سكن الكوفة، وقال المنيعي: هو كدير بن قتادة','8105','2724','0');
INSERT INTO [t10490] VALUES (2781,'كندير بن سعيد بن حيدة بن قشير القشيري وقيل: المزني، مختلف في صحبته، وقيل: عن أبيه سعيد، وهو الصحيح، تقدم فيمن اسمه سعيد، حديثه عند العباس بن عبد الرحمن الهاشمي','8107','2724','0');
INSERT INTO [t10490] VALUES (2782,'كردوس بن عمرو وقيل: ابن هانئ، روى عنه أبو وائل شقيق، ذكره ابن أبي داود والحسن بن سفيان في الصحابة , وخالفهما غيرهما','8109','2724','0');
INSERT INTO [t10490] VALUES (2783,'كهيل الأزدي غير منسوب، ذكره الحسن بن سفيان في الوحدان','8113','2724','0');
INSERT INTO [t10490] VALUES (2784,'كشد الجهني رأى النبي صلى الله عليه وسلم، ذكره بعض المتأخرين وقال: روى حديثه الواقدي، عن عبد العزيز بن عمران، عن واقد بن عبد العزيز، ولم يزد عليه إن كان محفوظا','8115','2724','0');
INSERT INTO [t10490] VALUES (2785,'كبيش بن هوذة أحد بني الحارث بن شروس، أتى النبي صلى الله عليه وسلم وكتب له كتابا','8116','2724','0');
INSERT INTO [t10490] VALUES (2786,'كركرة له ذكر في حديث ذكره بعض المتأخرين','8118','2724','0');
INSERT INTO [t10490] VALUES (2787,'باب اللام','8120','18','0');
INSERT INTO [t10490] VALUES (2788,'لقيط بن الربيع وهو أبو العاص بن الربيع بن عبد العزى بن عبد الشمس بن عبد مناف، أمه هالة أخت خديجة بنت خويلد بن أسد بن عبد العزى بن قصي، ختن النبي صلى الله عليه وسلم بابنته زينب، أسلم وهاجر، أثنى عليه النبي صلى الله عليه وسلم وقال: " إنه حدثني ,','8121','2787','0');
INSERT INTO [t10490] VALUES (2789,'لقيط بن عامر بن المنتفق بن عامر بن عقيل أبو رزين العقيلي، له صحبة ووفادة، كان النبي صلى الله عليه وسلم يكره المسائل , فإذا سأله أبو رزين أعجبه أكثر رواياته مسائل سأل عنها النبي صلى الله عليه وسلم في التوحيد والأصول','8125','2787','0');
INSERT INTO [t10490] VALUES (2790,'لقيط بن صبرة أبو عاصم، روى عنه ابنه عاصم، سكن مكة، وقيل: أنه كان من بني المنتفق، من بني عقيل','8130','2787','0');
INSERT INTO [t10490] VALUES (2791,'لقيط بن أرطأة السكوني عداده في الشاميين','8132','2787','0');
INSERT INTO [t10490] VALUES (2792,'لقيط بن عدي اللخمي جد سويد بن حيان، له ذكر في الصحابة، روى عنه سويد، ولا يعرف له مسند، عداده في المصريين قاله أبو سعيد بن عبد الأعلى','8135','2787','0');
INSERT INTO [t10490] VALUES (2793,'لبيد بن ربيعة بن عامر بن مالك الشاعر حكت عنه عائشة، رضي الله عنها، وترحمت عليه، وصدقه النبي صلى الله عليه وسلم في بيت قاله. قال مالك بن أنس: عاش لبيد مائة وستين سنة، ذكره بعض المتأخرين في الصحابة','8136','2787','0');
INSERT INTO [t10490] VALUES (2794,'لبيد بن سهل الأنصاري له ذكر في حديث قتادة بن النعمان في قصة ابن أبيرق لما نسبوا سرقتهم إلى لبيد، فأنزل الله تعالى فيه: ومن يكسب خطيئة أو إثما ثم يرم به بريئا فبرأه الله مما تقولت عليه بنو أبيرق','8139','2787','0');
INSERT INTO [t10490] VALUES (2795,'لبي بن لبا الأسدي حديثه عند الواسطيين، حدث عنه أبو بلج: جارية بن بلج التميمي','8141','2787','0');
INSERT INTO [t10490] VALUES (2796,'اللجلاج أبو العلاء سكن دمشق , روى عنه، ابناه: العلاء، وخالد، أسلم وهو ابن خمسين , ومات وهو ابن عشرين ومائة سنة','8143','2787','0');
INSERT INTO [t10490] VALUES (2797,'اللجلاج بن حكيم السلمي أخو جحاف بن حكيم السلمي، سماه بعض المتأخرين اللجلاج وقال: عداده في الجزريين','8146','2787','0');
INSERT INTO [t10490] VALUES (2798,'لبيبة الأنصاري أبو عبد الرحمن، ذكره بعض المتأخرين، وأخرج له هذا الحديث وغيره، وقيل: أبو لبيبة، وقيل: يحيى بن عبد الرحمن بن محمد بن لبيبة','8148','2787','0');
INSERT INTO [t10490] VALUES (2799,'لبدة بن كعب أبو تريس يعد في المصريين، ذكره بعض المتأخرين، وزعم أنه أدرك النبي صلى الله عليه وسلم وأكل الدم في الجاهلية','8152','2787','0');
INSERT INTO [t10490] VALUES (2800,'لميس بن سلمى عداده في أعراب البصرة، روى حديثه عمرو بن جبلة','8154','2787','0');
INSERT INTO [t10490] VALUES (2801,'لهيب بن مالك اللهبي قال: حضرت مع النبي صلى الله عليه وسلم فذكرت له الكهانة رواه عبد الله بن محمد العدوي بإسناد لا يثبت','8155','2787','0');
INSERT INTO [t10490] VALUES (2802,'ليشرح بن لحي بن مخمر أبو مخمر الرعيني، شهد فتح مصر، له ذكر في الصحابة، ولا يعرف له رواية','8156','2787','0');
INSERT INTO [t10490] VALUES (2803,'لصيت بن خثيم بن حرملة له ذكر في الصحابة، وشهد فتح مصر، لا يعرف له رواية , أحال بذكره , وذكر ليشرح المحيل على أبي سعيد بن عبد الأعلى','8157','2787','0');
INSERT INTO [t10490] VALUES (2804,'لقس بن سلمان مولى كعب بن عجرة، أدرك النبي صلى الله عليه وسلم، وروى عن كعب، روى حديثه أبو ضمرة، عن سعد بن إسحاق بن كعب، عن أبيه، هؤلاء ذكرهم المتأخر، ولم يزد على ذكره عنه، ولم يتابع عليه أحد من أهل المسانيد ولا التواريخ','8158','2787','0');
INSERT INTO [t10490] VALUES (2805,'لهيعة الحضرمي ذكره أبو زرعة في الصحابة','8159','2787','0');
INSERT INTO [t10490] VALUES (2806,'باب الميم','8161','18','0');
INSERT INTO [t10490] VALUES (2807,'من اسمه معاذ','8162','2806','0');
INSERT INTO [t10490] VALUES (2808,'معاذ بن جبل الأنصاري ثم الخزرجي، شهد العقبة وبدرا والمشاهد، إمام الفقهاء، وكبير العلماء بعثه النبي صلى الله عليه وسلم عاملا على اليمن وقال: " نعم الرجل معاذ " بعثه ليجبره من دينه , يكنى أبا عبد الرحمن، أسلم وهو ابن ثماني عشرة سنة، وتوفي وهو ابن','8163','2807','0');
INSERT INTO [t10490] VALUES (2809,'معاذ بن الحارث الأنصاري عقبي بدري، يعرف بابن عفراء، وعفراء أمه، وهو الذي شارك معاذ بن عمرو بن الجموح في قتل أبي جهل، وهو معاذ بن الحارث بن رفاعة بن سواد بن مالك بن غنم بن مالك بن النجار، وعفراء أمه بنت عبيد بن ثعلبة بن غنم بن مالك بن النجار، أمه أم','8187','2807','0');
INSERT INTO [t10490] VALUES (2810,'معاذ بن عمرو بن الجموح الخزرجي قاتل أبي جهل، عقبي بدري، شهد له النبي صلى الله عليه وسلم فقال: " نعم الرجل معاذ بن عمرو " روى عنه ابن عباس، عاش إلى زمن عثمان بن عفان','8189','2807','0');
INSERT INTO [t10490] VALUES (2811,'معاذ أبو زهير الثقفي روى عنه ابنه أبو بكر، سماه البخاري، ومسلم بن الحجاج: معاذا، وقيل: معاذ بن رباح','8194','2807','0');
INSERT INTO [t10490] VALUES (2812,'معاذ بن أنس الجهني حديثه عند ابنه سهل','8196','2807','0');
INSERT INTO [t10490] VALUES (2813,'معاذ بن ماعص وقيل ابن ناعص بن قيس بن خلدة الأنصاري , ثم الخزرجي، شهد بدرا','8198','2807','0');
INSERT INTO [t10490] VALUES (2814,'معاذ القارئ قيل: إنه أبو حليمة القارئ الأنصاري، سكن المدينة، توفي قبل زيد بن ثابت، روى عنه عمران بن أبي أنس، ونافع مولى ابن عمر، والمقبري، وقيل: هو معاذ بن الحارث بن سواد بن مالك بن غنم القارئ','8201','2807','0');
INSERT INTO [t10490] VALUES (2815,'معاذ بن سعد أو سعد بن معاذ الأنصاري ذكره مالك بن أنس في الموطأ على الشك، أخرج له بعض المتأخرين هذا الحديث، وعده في الصحابة','8204','2807','0');
INSERT INTO [t10490] VALUES (2816,'معاذ بن عثمان بن معاذ التيمي من رهط محمد بن إبراهيم بن الحارث التيمي، ذكره بعض المتأخرين أن له ذكرا في حديث إن صح','8206','2807','0');
INSERT INTO [t10490] VALUES (2817,'من اسمه مالك','8207','2806','0');
INSERT INTO [t10490] VALUES (2818,'مالك بن التيهان أبو الهيثم الأنصاري، عقبي بدري، شهد العقبة الأولى والمشاهد بعدها، أول من بايع بالعقبة على الإسلام، وهو نقيب القوم وخطيبهم، صاحب الضيافة، أضاف النبي صلى الله عليه وسلم، وأبا بكر، وعمر، حدث عنه أبو هريرة، وابن عباس، وابن عمر،','8208','2817','0');
INSERT INTO [t10490] VALUES (2819,'مالك بن ربيعة أبو أسيد الساعدي، من بني ساعدة بن كعب بن الخزرج، شهد بدرا، توفي سنة ثلاثين، وله ثنتان وتسعون سنة، وذكر بعض المتأخرين: أنه توفي سنة ستين , ووهم وهو مالك بن ربيعة بن البدن بن عامر بن عوف بن حارثة بن عمرو بن الخزرج بن ساعدة، أصيب ببصره','8216','2817','0');
INSERT INTO [t10490] VALUES (2820,'مالك بن صعصعة الأنصاري روى عنه أنس بن مالك','8222','2817','0');
INSERT INTO [t10490] VALUES (2821,'مالك بن ربيعة السلولي يكنى أبا مريم، والد بريد، شهد الشجرة، سكن الكوفة، له غير حديث عند ابنه بريد','8224','2817','0');
INSERT INTO [t10490] VALUES (2822,'مالك بن قيس أبو صرمة الأنصاري، نذكره في الكنى، حدث عنه يحيى بن سعيد، عن محمد بن يحيى بن حيان، عن لؤلؤة، عن أبي صرمة، ذكره بعض المتأخرين، عن أحمد بن حنبل: أن اسمه مالك بن قيس','8228','2817','0');
INSERT INTO [t10490] VALUES (2823,'مالك بن كعب الأنصاري ذكره بعض المتأخرين، وقال: مختلف فيه، والصواب: كعب بن مالك، وأخرج له هذا الحديث','8229','2817','0');
INSERT INTO [t10490] VALUES (2824,'مالك بن سنان الخدري والد أبي سعيد، قيل: إنه استشهد بأحد , قتله غراب بن سفيان الكناني، وهو مالك بن سنان بن ثعلبة بن عبيد بن الأبجر، حديثه عند ابنه أبي سعيد، وصحف بعض المتأخرين في ابن الأبجر فقال: ابن الأغر','8231','2817','0');
INSERT INTO [t10490] VALUES (2825,'مالك بن مسعود الأنصاري من بني ساعدة، شهد بدرا','8234','2817','0');
INSERT INTO [t10490] VALUES (2826,'مالك بن رافع الزرقي الأنصاري شهدا بدرا، أخو رفاعة بن رافع','8237','2817','0');
INSERT INTO [t10490] VALUES (2827,'مالك بن قدامة الأنصاري من بني غنم بن سلم بن مالك بن الأوس بن حارثة','8239','2817','0');
INSERT INTO [t10490] VALUES (2828,'مالك بن نضلة أبو أبي الأحوص الجشمي، سكن الكوفة','8242','2817','0');
INSERT INTO [t10490] VALUES (2829,'مالك بن الحويرث أبو سليمان الليثي، سكن البصرة، قدم على النبي صلى الله عليه وسلم , فأقام عليه في شيبة من قومه , فعلمهم الصلاة، وأمرهم بتعليمهم القوم إذا رجعوا إليهم، حدث عنه أبو قلابة، ونصر بن عاصم، وسوار الجرمي، وابنه الحسن بن مالك بن الحويرث','8244','2817','0');
INSERT INTO [t10490] VALUES (2830,'مالك الرؤاسي أتى النبي صلى الله عليه وسلم , فاسترضاه فرضي عنه، حديثه عند ابنه عمرو بن مالك','8247','2817','0');
INSERT INTO [t10490] VALUES (2831,'مالك بن عبد الله الخزاعي صلى خلف النبي صلى الله عليه وسلم , وغزا معه، حديثه عند منصور بن حيان','8249','2817','0');
INSERT INTO [t10490] VALUES (2832,'مالك بن الخشخاش العنبري أخو عبيد وقيس','8251','2817','0');
INSERT INTO [t10490] VALUES (2833,'مالك بن عبد الله الخثعمي له صحبة، صاحب السرايا','8253','2817','0');
INSERT INTO [t10490] VALUES (2834,'مالك بن الدخشم من بني عامر بن عوف، شهد بدرا، وهو الذي ذب عنه رسول الله صلى الله عليه وسلم يوم حضر دار عتبان بن مالك','8255','2817','0');
INSERT INTO [t10490] VALUES (2835,'مالك بن عبادة وقيل: ابن عبد الله أبو موسى الغافقي','8259','2817','0');
INSERT INTO [t10490] VALUES (2836,'مالك بن مرارة الرهاوي وقيل: مرة، حكى عنه النبي صلى الله عليه وسلم في قصة زرعة بن سيف بن ذي يزن، وروى ابن عون، عن عمرو بن سعيد، عن حميد بن عبد الرحمن، عن ابن مسعود قال: أتيت النبي صلى الله عليه وسلم وعنده مالك بن مرارة','8261','2817','0');
INSERT INTO [t10490] VALUES (2837,'مالك بن عبادة وقيل: عبدة، له ذكر في كتاب زرعة بن سيف بن ذي يزن الذي كتب النبي صلى الله عليه وسلم إليه يوصيه بمعاذ عبد الله بن زيد، ومالك بن عبادة، وعقبة بن عمرو خيرا','8263','2817','0');
INSERT INTO [t10490] VALUES (2838,'مالك بن عبد وقيل: ابن عبد الله المعافري','8264','2817','0');
INSERT INTO [t10490] VALUES (2839,'مالك بن هبيرة السكوني يعد في المصريين، حديثه عند أبي الخير اليزني','8266','2817','0');
INSERT INTO [t10490] VALUES (2840,'مالك بن عتاهية يعد في المصريين','8268','2817','0');
INSERT INTO [t10490] VALUES (2841,'مالك بن أحيمر الجذامي وقيل اليماني، حديثه عند أبي رزين الباهلي','8271','2817','0');
INSERT INTO [t10490] VALUES (2842,'مالك بن يخامر ذكر في الصحابة , ولا يثبت','8273','2817','0');
INSERT INTO [t10490] VALUES (2843,'مالك بن عمرو من بني غنم بن ذودان، له ذكر في المهاجرة','8275','2817','0');
INSERT INTO [t10490] VALUES (2844,'مالك المري والد أبي غطفان المري، حكى بعض المتأخرين عن البخاري أن له صحبة، وله حديث ثابت ولم يزد عليه','8277','2817','0');
INSERT INTO [t10490] VALUES (2845,'مالك بن عمرو القشيري مختلف فيه , فقيل: عمرو بن مالك، وقيل: أبي بن مالك، تقدم اختلاف حديثه','8278','2817','0');
INSERT INTO [t10490] VALUES (2846,'مالك بن حيدة القشيري ذكره في حديث أخيه معاوية، يعد في البصريين، حديثه عند سويد بن حجير أبي قزعة','8280','2817','0');
INSERT INTO [t10490] VALUES (2847,'مالك بن قهطم الدارمي أبو أبي العشراء , قاله علي بن المديني، وقيل: اسمه عطارد بن بدر , قاله أحمد، وأبو خيثمة','8282','2817','0');
INSERT INTO [t10490] VALUES (2848,'مالك بن عوف النصري يكنى أبا علي، كان رئيسا مقداما، كان على النبي صلى الله عليه وسلم يوم هوازن، وهو رئيس المشركين يومئذ، ثم أسلم , وشهد القادسية مسلما مع سعد بن أبي وقاص، وهو: مالك بن عوف بن مالك بن سعد بن ربيعة بن يربوع بن واثلة بن دهمان بن نصر بن','8284','2817','0');
INSERT INTO [t10490] VALUES (2849,'مالك بن يسار السكوني ثم العوفي، روى عنه أبو بحرية، يعد في الشاميين، صحف بعض المتأخرين فقال: روى عنه أبو نجدة السكوني وهو تصحيف','8286','2817','0');
INSERT INTO [t10490] VALUES (2850,'مالك بن أزهر وقيل: ابن أبي زاهر، أدرك النبي صلى الله عليه وسلم فيما ذكره بعض المتأخرين، روى حديثه سعيد بن أبي مريم، عن ابن لهيعة، عن بكر بن سوادة، عن سعيد بن أبي شمر السباني , حدثه أنه رأى مالك بن أزهر وكان قد أدرك رسول الله صلى الله عليه وسلم ينقي','8288','2817','0');
INSERT INTO [t10490] VALUES (2851,'مالك ابن بحينة روى حديثه سعد بن إبراهيم، عن حفص بن عاصم بن مالك ابن بحينة، وصوابه عبد الله بن مالك ابن بحينة','8289','2817','0');
INSERT INTO [t10490] VALUES (2852,'مالك بن ضمرة الضمري عداده في الكوفيين فيما ذكره بعض المتأخرين','8292','2817','0');
INSERT INTO [t10490] VALUES (2853,'مالك بن عمير السلمي شهد مع النبي صلى الله عليه وسلم حنينا والطائف، عداده في أهل المدينة، روى عنه إسماعيل بن سميع , والمنهال بن عمرو','8294','2817','0');
INSERT INTO [t10490] VALUES (2854,'مالك بن أبي خولي شهد بدرا من حلفاء بني عدي بن كعب','8296','2817','0');
INSERT INTO [t10490] VALUES (2855,'مالك بن عمرو أخو ثقف بن عمرو، وهم من بني حجر إلى بني سليم من حلفاء بني عبد شمس','8298','2817','0');
INSERT INTO [t10490] VALUES (2856,'مالك بن نميلة المزني شهد بدرا، من حلفاء بني معاوية بن مالك بن عوف','8300','2817','0');
INSERT INTO [t10490] VALUES (2857,'مالك بن سعد مجهول، عداده في أعراب البصرة','8302','2817','0');
INSERT INTO [t10490] VALUES (2858,'مالك بن أوس بن الحدثان ذكره محمد بن إسحاق بن خزيمة، في الصحابة، فيما حكاه عنه بعض المتأخرين','8304','2817','0');
INSERT INTO [t10490] VALUES (2859,'مالك الأنصاري سمع النبي صلى الله عليه وسلم يقول: " أعطوا المجالس حقها "، ذكره بعض المتأخرين، وقال: لا يعرف، وقال: روى حديثه عبيد الله بن موسى، عن موسى بن عبيدة، عن أيوب بن خالد، عن مالك - رجل من الأنصار - بهذا','8307','2817','0');
INSERT INTO [t10490] VALUES (2860,'مالك بن الحارث ذكره المنيعي، عن محمد بن ميمون الخياط، عن ابن عيينة، عن زكريا، عن الشعبي، ووهم فيه، والصواب: الحارث بن مالك','8308','2817','0');
INSERT INTO [t10490] VALUES (2861,'مالك بن عمير الحنفي أدرك الجاهلية , ولا يعرف له رؤية ولا صحبة، روى عنه إسماعيل بن سميع، والمنهال بن عمرو، ذكره الحسن بن سفيان في الوحدان','8309','2817','0');
INSERT INTO [t10490] VALUES (2862,'مالك بن الحارث الذهلي يلقب خمخاما , من بني بكر بن وائل، وفد على النبي صلى الله عليه وسلم، وعقبه بهراة، ذكره بعض المتأخرين حدثنا عبد الله بن الحسن بن بالويه، ثنا محمد بن محمد بن أبي خراسان، ح، وأنبئنا عن محمد بن أحمد السلمي المروزي، ثنا عبد الرحمن','8312','2817','0');
