INSERT INTO [t10490] VALUES (2883,'أبو غادية الجهني مختلف في اسمه، قيل: اسمه مسلم , نذكر حديثه في الكنى إن شاء الله','8352','2868','0');
INSERT INTO [t10490] VALUES (2884,'من اسمه: مسلمة','8353','2806','0');
INSERT INTO [t10490] VALUES (2885,'مسلمة بن مخلد بن خالد الأنصاري الزرقي حديثه عند علي بن رباح، ومجمع بن كعب، ومعاوية بن خديج , وجبلة بن عطية، ومكحول، وهو مسلمة بن مخلد بن صامت بن لوذان بن عبد ود بن زيد بن ثعلبة بن الخزرج بن ساعدة بن كعب بن الخزرج، مولده مقدم النبي صلى الله عليه وسلم','8354','2884','0');
