INSERT INTO [t10490] VALUES (2887,'من اسمه: معاوية','8361','2806','0');
INSERT INTO [t10490] VALUES (2888,'معاوية بن أبي سفيان واسم أبي سفيان: صخر بن حرب بن أمية بن عبد شمس بن عبد مناف، يكنى: أبا عبد الرحمن، وأمه: هند بنت عتبة بن ربيعة بن عبد شمس، وأمها: صعبة بنت أمية بن حارثة بن الأوقص، من بني سليم، وأمها: بنت نوفل بن عبد مناف، كان من الكتبة','8362','2887','0');
INSERT INTO [t10490] VALUES (2889,'معاوية بن الحكم السلمي سكن المدينة، روى عنه أبو سلمة بن عبد الرحمن، وعطاء بن يسار','8368','2887','0');
INSERT INTO [t10490] VALUES (2890,'معاوية الليثي سكن البصرة','8372','2887','0');
INSERT INTO [t10490] VALUES (2891,'معاوية بن حديج بن جفنة السكوني وقيل: الخولاني، وقيل: من تجيب، كان من عمال معاوية، روى عن النبي صلى الله عليه وسلم غير حديث , روى عنه سويد بن قيس','8374','2887','0');
INSERT INTO [t10490] VALUES (2892,'معاوية بن حيدة القشيري جد بهز بن حكيم، سكن البصرة، حديثه عند ابنه حكيم','8377','2887','0');
