INSERT INTO [t10490] VALUES (2895,'معاوية الهذلي غير منسوب، حديثه عند سليم بن عامر الحمصي، ذكره المنيعي في الوحدان','8384','2887','0');
INSERT INTO [t10490] VALUES (2896,'معاوية بن معاوية المزني توفي على عهد النبي صلى الله عليه وسلم , ذكره في حديث أنس بن مالك','8386','2887','0');
INSERT INTO [t10490] VALUES (2897,'معاوية بن نفيع ذكره بعض المتأخرين، وقال: له صحبة: وقال: روى محمد بن جابر، عن أشعث بن أبي الشعثاء، عن الصلت البكري، عن معاوية بن نفيع، وكانت له صحبة قال: اجتمعنا إليه يوم عيد في السواد , فصلى بنا','8390','2887','0');
INSERT INTO [t10490] VALUES (2898,'معاوية بن قرمل ذكره بعض المتأخرين، وقال: يقال: إن له صحبة','8391','2887','0');
INSERT INTO [t10490] VALUES (2899,'معاوية أبو نوفل الديلي ذكره الطبراني في الصحابة','8393','2887','0');
INSERT INTO [t10490] VALUES (2900,'معاوية بن سويد بن مقرن ذكره الحسن بن سفيان والمنيعي في الوحدان','8395','2887','0');
INSERT INTO [t10490] VALUES (2901,'من اسمه معقل','8397','2806','0');
INSERT INTO [t10490] VALUES (2902,'معقل بن سنان الأشجعي أبو سنان وقيل: أبو محمد، سكن الكوفة، وقتل يوم الحرة في ذي الحجة سنة ثلاث وستين وهو معقل بن سنان بن مطهر بن قينان بن سبيع بن بكر بن أشجع، شهد فتح مكة، روى عنه علقمة، ومسروق , ونافع بن جبير، والحسن بن أبي الحسن، قتله مسلم بن','8398','2901','0');
INSERT INTO [t10490] VALUES (2903,'معقل بن يسار المزني أبو علي وهو معقل بن يسار بن عبد الله بن معين بن حراق بن لأي بن كعب بن عبد بن ثور بن هذمة بن لاطم بن عثمان بن عمرو بن أد بن طابخة، وعمرو بن أد هو من مزينة، نسب إلى أمه مزينة , وهي بنت كلب بن وبرة، شهد الحديبية، ورفع أغصان الشجرة -','8401','2901','0');
INSERT INTO [t10490] VALUES (2904,'معقل بن أبي معقل ويقال: ابن أبي الهيثم الأسدي، حديثه عند أبي يزيد مولاه، وأبي سلمة بن عبد الرحمن، وأم معقل','8405','2901','0');
INSERT INTO [t10490] VALUES (2905,'معقل بن المنذر بن سرح من بني خناس بن سنان بن عبيد بن عدي بن غنم بن كعب بن سلمة الأنصاري الخزرجي، شهد بدرا','8408','2901','0');
