INSERT INTO [t10490] VALUES (2919,'منذر بن عبد اليشكري له ذكر في المغازي، لا يعرف له رواية، كذا حكاه بعض المتأخرين، ولم يزد عليه','8437','2908','0');
INSERT INTO [t10490] VALUES (2920,'محمود بن مسلمة بن سلمة بن خالد بن عدي بن مجدعة، أخو محمد بن مسلمة الأنصاري، شهد أحدا والحديبية، واستشهد بخيبر، دلى عليه مرحب اليهودي رحى , فهشمت البيضة رأسه، وسقطت جلدة جبينه على وجهه، فعصبه رسول الله صلى الله عليه وسلم بثوب , فمات يوم الثالث،','8438','2908','0');
