INSERT INTO [t10490] VALUES (2922,'محمود بن لبيد الأنصاري أدرك النبي صلى الله عليه وسلم، وولد في حياته , قاله أحمد بن حنبل، وابن أبي خيثمة، سكن المدينة','8445','2908','0');
