INSERT INTO [t10490] VALUES (2926,'معبد بن نباتة الأسدي ذكره بعض المتأخرين، وإنما هو منقذ بن نباتة، وأخرج له هذا، عن ابن إسحاق','8454','2908','0');
INSERT INTO [t10490] VALUES (2927,'معبد بن قيس وقيل: ابن وهب بن صخر الأنصاري الخزرجي، شهد بدرا','8456','2908','0');
INSERT INTO [t10490] VALUES (2928,'معبد أبو حميضة وقيل: معبد بن عباد بن قشير، شهد بدرا','8458','2908','0');
INSERT INTO [t10490] VALUES (2929,'معبد بن أبي معبد الكعبي الخزاعي وهو ابن أم معبد، مر به النبي صلى الله عليه وسلم مهاجرا إلى المدينة وهو صغير','8461','2908','0');
INSERT INTO [t10490] VALUES (2930,'معبد بن أكثم الكعبي ذكره في حديث جابر','8464','2908','0');
INSERT INTO [t10490] VALUES (2931,'معبد بن وهب العبدي ذكره المنيعي في الوحدان','8466','2908','0');
INSERT INTO [t10490] VALUES (2932,'معبد القرشي ذكره الطبراني في الصحابة','8468','2908','0');
INSERT INTO [t10490] VALUES (2933,'مسعود ابن العجماء والعجماء اسم أمه، وهي بنت عامر، وهو مسعود بن الأسود بن حارثة بن نضلة بن عوف بن عبيد بن عويج بن عدي بن كعب , قتل أباه يوم بدر كافرا، وله أخ يقال له: ثوبان بن الأسود، قتل بالإسكندرية , فيما قاله أبو سعيد بن عبد الأعلى , واستشهد مسعود','8470','2908','0');
INSERT INTO [t10490] VALUES (2934,'مسعود بن الضحاك اللخمي','8472','2908','0');
INSERT INTO [t10490] VALUES (2935,'مسعود غلام فروة الأسلمي وقيل: هو مسعود بن هنيدة، شهد المريسيع مع رسول الله صلى الله عليه وسلم، فأعطاه عشرا من الإبل قاله الواقدي','8474','2908','0');
INSERT INTO [t10490] VALUES (2936,'مسعود بن ربيعة بن عمر بن عبد القارئ حليف بني زهرة , لا يعرف له رواية، شهد بدرا','8476','2908','0');
INSERT INTO [t10490] VALUES (2937,'مسعود بن أوس بن أصرم بن زيد بن ثعلبة بن غنم بن مالك بن النجار يكنى أبا محمد صحب النبي صلى الله عليه وسلم , وشهد فتح مصر، وله حديث رواه ابن لهيعة، عن يزيد المعافري، عن مولى لرويفع، عن أبي محمد الأنصاري، حكاه بعض المتأخرين، عن أبي سعيد بن عبد الأعلى','8478','2908','0');
INSERT INTO [t10490] VALUES (2938,'مسعود بن عمرو وقيل: ابن الربيع حديثه عند سعيد بن زيد، مختلف في صحبته، سكن المدينة، وقال إسحاق بن عيسى: عن أبي معشر سعيد بن الربيع: شهد بدرا وهو حليف بني زهرة، وهو المتقدم','8480','2908','0');
INSERT INTO [t10490] VALUES (2939,'مسعود بن خالد الزرقي شهد بدرا، وقيل: مسعود بن سعد بن خالد','8482','2908','0');
