INSERT INTO [t10490] VALUES (3038,'مدرك أبو الطفيل الغفاري حديثه عند أولاده، بعثه النبي صلى الله عليه وسلم إلى مكة في حمل ابنته منها إليه','8744','2953','0');
INSERT INTO [t10490] VALUES (3039,'مدلج الأنصاري بعثه النبي صلى الله عليه وسلم إلى عمر بن الخطاب رضي الله عنه ليدعو إليه، غير منسوب، ذكره ابن عباس في حديثه','8747','2953','0');
INSERT INTO [t10490] VALUES (3040,'مجاعة بن مرارة بن سليم السلمي وقيل: سلمى: سكن اليمامة، وفد هو وأبوه على رسول الله صلى الله عليه وسلم، حديثه عند أولاده','8749','2953','0');
INSERT INTO [t10490] VALUES (3041,'مرارة بن سلمى اليمامي روى عنه ابنه مجاعة، له ولابنه وفادة، ذكره بعض المتأخرين، وأفرده عن مجاعة، وذكر له هذا الحديث','8752','2953','0');
INSERT INTO [t10490] VALUES (3042,'مجزأة بن ثور بن عفير بن زهير بن كعب بن عمرو بن سدوس السدوسي، قتل في عهد عمر، ذكره بعض المتأخرين أن البخاري ذكره في الصحابة، ولا يثبت، وروايته عن عبد الرحمن بن أبي بكر، وهو أخو منجوف بن ثور','8755','2953','0');
INSERT INTO [t10490] VALUES (3043,'مطرف بن بهصل بن كعب بن قشع بن ادلف بن الهضم بن عبد الله بن حرماز الحرمازي، ذكره البخاري في الصحابة فيما ذكره عند بعض المتأخرين , وقال: وله ذكر في حديث نضلة بن بهصل , ولم يزد عليه','8756','2953','0');
INSERT INTO [t10490] VALUES (3044,'محلم بن جثامة الليثي أخو الصعب، ذكره في حديث عبد الله بن حذافة','8757','2953','0');
