INSERT INTO [t10490] VALUES (3047,'محمية بن جزء الزبيدي كان رسول الله صلى الله عليه وسلم استعمله على الأخماس، وهو عم عبد الله بن الحارث بن جزء','8765','2953','0');
INSERT INTO [t10490] VALUES (3048,'مخول بن يزيد البهزي ثم السلمي حديثه عند ابنه القاسم، أتى النبي صلى الله عليه وسلم بالأبواء متحاكما إليه','8767','2953','0');
