INSERT INTO [t10490] VALUES (3058,'منبعث كان اسمه المضطجع، فسماه رسول الله صلى الله عليه وسلم منبعثا، أسلم يوم حاصر أهل الطائف، من آل عثمان بن عامر بن معتب','8791','2953','0');
INSERT INTO [t10490] VALUES (3059,'مسروح أبو بكرة مولى الحارث بن كلدة، أسلم يوم الطائف، وكناه النبي صلى الله عليه وسلم أبا بكرة، وقيل: اسمه نفيع، نذكر من حديثه في حرف النون','8793','2953','0');
INSERT INTO [t10490] VALUES (3060,'معافى بن زيد الجرشي له ذكر في حديث حدثناه محمد، ثنا إبراهيم بن أحمد القنطري البغدادي، ثنا شبيب بن يحيى بن شبيب، ثنا محمد بن تمام بن عياش، عن عبد العزيز بن قيس، عن حميد، عن أنس، قال: أتى رسول الله صلى الله عليه وسلم رجل من تهامة , يقال له: معافى','8795','2953','0');
INSERT INTO [t10490] VALUES (3061,'مليل بن وبرة بن عبد الكريم بن خالد بن عجلان الأنصاري، شهد بدرا','8796','2953','0');
