INSERT INTO [t10490] VALUES (3073,'مدلوك أبو سفيان يعد في الشاميين، أصابته مسحة من النبي صلى الله عليه وسلم في رأسه فاسود شعره، حديثه عند مطر بن العلاء','8820','2953','0');
INSERT INTO [t10490] VALUES (3074,'محدوج بن زيد الهذلي مختلف في صحبته','8822','2953','0');
INSERT INTO [t10490] VALUES (3075,'معدان أبو خالد بن معدان له صحبة فيما قاله الطبراني','8824','2953','0');
INSERT INTO [t10490] VALUES (3076,'معدان أبو الخير يعرف بجفشيش، وهو المتحاكم إلى النبي صلى الله عليه وسلم مع الأشعث بن قيس في أرض تخاصما فيها، تقدم ذكره','8826','2953','0');
INSERT INTO [t10490] VALUES (3077,'مقوقس صاحب الإسكندرية المهدي إلى النبي صلى الله عليه وسلم، حديثه عند عبيد الله بن عبد الله بن عتبة','8828','2953','0');
