INSERT INTO [t10490] VALUES (3083,'المنتشر أبو محمد الهمداني سكن الكوفة','8840','2953','0');
INSERT INTO [t10490] VALUES (3084,'مضرج بن جدالة أتى النبي صلى الله عليه وسلم فقال: كيف فضل أمتك على سائر الأمم، ذكره في حديث رواه عاصم بن عبيد الله المروزي، عن إسماعيل بن أبي زياد، عن ليث، عن الضحاك , عن ابن عباس، ذكره بعض المتأخرين','8842','2953','0');
INSERT INTO [t10490] VALUES (3085,'مبرح بن شهاب اليافعي شهد فتح مصر، وهو ابن شهاب بن الحارث بن ربيعة بن سخيت بن شرحبيل، وكان فيمن وفد على النبي صلى الله عليه وسلم فيما حكاه عنه المتأخر قاله: أبو سعيد بن عبد الأعلى، عن سعيد بن عفير','8843','2953','0');
INSERT INTO [t10490] VALUES (3086,'باب النون','8844','18','0');
INSERT INTO [t10490] VALUES (3087,'النعمان بن مقرن وقيل: ابن عمر بن المقرن المزني استشهد يوم فتح نهاوند، سنة إحدى وعشرين من الهجرة، استعمله عمر على جيش نهاوند روى حديثه معقل بن يسار، ومسلم بن الهيصم، وأبو خالد الوالبي وغيرهم','8845','3086','0');
