INSERT INTO [t10490] VALUES (3090,'النعمان بن غصن بن الحارث البلوي حليف الأنصار، شهد بدرا','8857','3086','0');
INSERT INTO [t10490] VALUES (3091,'النعمان بن سنان من بني عبيد بن عدي بن معبد بن قيس بن صخر بن حرام، حليف لهم، شهد بدرا','8859','3086','0');
INSERT INTO [t10490] VALUES (3092,'النعمان بن عمرو بن رفاعة بن الحارث بن سواد النجاري الأنصاري، شهد بدرا','8861','3086','0');
INSERT INTO [t10490] VALUES (3093,'النعمان بن عبد عمرو بن مسعود الأنصاري من بني دينار بن النجار، شهد بدرا','8863','3086','0');
INSERT INTO [t10490] VALUES (3094,'النعمان بن الرازية اللهبي وقال المنيعي: نعمان بن راذبة عريف الأزد وصاحب رايتهم، يعد في الحمصيين قاله البخاري، روى عنه صالح بن شريح فيما ذكره عنه بعض المتأخرين','8866','3086','0');
INSERT INTO [t10490] VALUES (3095,'النعمان بن عدي بن نضلة بن عبد العزى بن حرثان بن عوف بن عبيد بن عويج بن عدي بن كعب العدوي، هاجر هو وأبوه إلى الحبشة، مات أبوه بالحبشة، وقدم النعمان مع من قدم من المسلمين، فبقي إلى خلافة عمر بن الخطاب، فاستعمله على ميسان، فقال الأبيات، فعزله عمر "','8868','3086','0');
INSERT INTO [t10490] VALUES (3096,'النعمان بن بشير ابن سعد بن ثعلبة بن خلاس بن زيد بن مالك بن ثعلبة بن كعب بن الخزرج الأنصاري، كان أول مولود للأنصار بعد الهجرة، أمه عمرة بنت رواحة، له ولأبويه صحبة، توفي النبي صلى الله عليه وسلم، وله ثمان سنين وسبعة أشهر، كان أمير الكوفة في عهد','8869','3086','0');
INSERT INTO [t10490] VALUES (3097,'النعمان بن أبي فاطمة الأنصاري ذكره سليمان بن أحمد في معجمه','8876','3086','0');
INSERT INTO [t10490] VALUES (3098,'النعمان بن أشيم الأشجعي أبو هند، وقيل: اسمه رافع، له صحبة، أخرج له بعض المتأخرين هذا الحديث، عن علي بن عبد العزيز، عن أبي نعيم','8878','3086','0');
INSERT INTO [t10490] VALUES (3099,'النعمان بن قيس أدرك النبي صلى الله عليه وسلم , وحدث عنه، وعن أبي بكر، حديثه عند عبيد الله بن إياد بن لقيط، عن أبيه، عن النعمان بن قيس','8880','3086','0');
INSERT INTO [t10490] VALUES (3100,'النعمان بن حارثة الأنصاري شهد العقبة مبايعا للنبي صلى الله عليه وسلم ذكره في حديث عقيل بن أبي طالب','8881','3086','0');
INSERT INTO [t10490] VALUES (3101,'النعمان بن عجلان دخل عليه النبي صلى الله عليه وسلم عائدا له، ذكره بعض المتأخرين','8883','3086','0');
INSERT INTO [t10490] VALUES (3102,'النعمان بن شريك الشيباني لقي النبي صلى الله عليه وسلم بمنى مع صاحبيه مفروق بن عمرو، وهانئ بن قبيصة، والمثنى بن حارثة حين دعاهم إلى دين الله وتوحيده، وأخبرهم أن الله تعالى مانح بلاد الفرس، وأموالهم العرب ذكرهم في حديث أبان بن تغلب، عن عكرمة، عن ابن','8885','3086','0');
INSERT INTO [t10490] VALUES (3103,'النعمان بن برزج أدرك الجاهلية، ولا يعرف له إسلام، ذكره المتأخر','8887','3086','0');
INSERT INTO [t10490] VALUES (3104,'النعمان بن مرة ذكره بعض المتأخرين أنه أخرج في الصحابة، وهو تابعي، ذكره يحيى بن سعيد الأنصاري','8888','3086','0');
INSERT INTO [t10490] VALUES (3105,'النعمان بن جزء بن النعمان بن قيس بن مالك بن سعد بن ذهل الغطيفي، وفد على النبي صلى الله عليه وسلم , وشهد فتح مصر قاله أبو سعيد بن عبد الأعلى','8889','3086','0');
INSERT INTO [t10490] VALUES (3106,'النعيمان صاحب سويبط بن حرملة، ورفيقه، ذكر في أهل بدر، ذكرهما في حديث أم سلمة','8890','3086','0');
INSERT INTO [t10490] VALUES (3107,'نعيم بن النحام وهو نعيم بن عبد الله بن خالد بن أسيد بن عبد عوف بن عبيد بن عويج بن عدي بن كعب: سمي النحام لأن النبي صلى الله عليه وسلم قال: " دخلت الجنة فسمعت نحمة أمامي , فإذا هو نعيم "، والنحم الصوت، كان إسلامه بمكة قبل هجرة الحبشة، وكان يكتم','8893','3086','0');
INSERT INTO [t10490] VALUES (3108,'نعيم أبو يزيد الأسلمي وقيل: نعيم بن هزال الأسلمي، حديثه عند ابنه يزيد','8897','3086','0');
INSERT INTO [t10490] VALUES (3109,'نعيم بن مسعود بن عامر بن أنيف بن ثعلبة بن قنفذ بن هلال بن حلاوة بن أشجع بن ريث بن غطفان الأشجعي أبو سلمة، أمره النبي صلى الله عليه وسلم يوم الأحزاب أن يخذل بين بني قريظة وأبي سفيان، حديثه عند ابنه سلمة','8899','3086','0');
INSERT INTO [t10490] VALUES (3110,'نعيم بن سلام وقيل: ابن سلامة، قدم على النبي صلى الله عليه وسلم، له ذكر في حديث أبي هريرة','8903','3086','0');
INSERT INTO [t10490] VALUES (3111,'نعيم بن همار مختلف في اسم أبيه، فقيل: هبار الغطفاني، ويقال: ابن حمار، وقيل: هدار، وقيل: حماد، سكن الشام، حديثه عند قيس الجذامي','8905','3086','0');
INSERT INTO [t10490] VALUES (3112,'نعيم بن قعنب ذكره محمد بن إسحاق بن خزيمة في الصحابة، وكان من ساكني الوادي','8908','3086','0');
INSERT INTO [t10490] VALUES (3113,'نعيم بن أوس أخو تميم الداري , له ذكر في حديث ذكره بعض المتأخرين','8910','3086','0');
INSERT INTO [t10490] VALUES (3114,'نعيم بن عبد الرحمن الأزدي بصري، روى عنه داود بن أبي هند ذكر في الصحابة ولا يصح','8911','3086','0');
INSERT INTO [t10490] VALUES (3115,'نعيم بن ربيعة بن كعب الأسلمي قال: كنت أخدم رسول الله صلى الله عليه وسلم، وهو ربيعة بن كعب، ونعيم بن ربيعة وهم، رواه إبراهيم بن سعد، عن محمد بن إسحاق، عن محمد بن عمرو بن عطاء، عن نعيم بن ربيعة بن كعب، وصوابه ربيعة بن كعب','8912','3086','0');
INSERT INTO [t10490] VALUES (3116,'نافع بن عتبة بن أبي وقاص الزهري ابن أخي سعد، له صحبة، روى عنه جابر بن سمرة، وكان اسم أمه زينب بنت جابر الكناني، كان عتبة أصاب دما في الجاهلية - من قريش , فاتخذ المدينة منزلا ومالا، فانتقل إليها قبل الهجرة، فمات , فأوصى إلى سعد بن أبي وقاص','8913','3086','0');
INSERT INTO [t10490] VALUES (3117,'نافع بن عبد الحارث الخزاعي وهو نافع بن عبد الحارث بن حبان بن عمرو بن عبشان بن عبد عمرو بن لؤي بن ملكان بن أفصى، سكن المدينة , واستعمله عمر على الطائف، روى عنه أبو سلمة، وخميل، وأبو الطفيل','8915','3086','0');
INSERT INTO [t10490] VALUES (3118,'نافع بن بديل بن ورقاء الخزاعي استشهد يوم بئر معونة','8919','3086','0');
INSERT INTO [t10490] VALUES (3119,'نافع مولى النبي صلى الله عليه وسلم','8921','3086','0');
INSERT INTO [t10490] VALUES (3120,'نافع أبو سليمان العبدي مولى المنذر بن ساوى، روى عنه، ابنه سليمان، وفد على النبي صلى الله عليه وسلم، كان ينزل حلب','8923','3086','0');
INSERT INTO [t10490] VALUES (3121,'نافع بن يزيد الثقفي ذكره بعض المتأخرين، وقال: له ذكر في الصحابة، ولا يثبت','8925','3086','0');
INSERT INTO [t10490] VALUES (3122,'نافع أبو السائب مولى غيلان بن سلمة ذكره بعض المتأخرين','8927','3086','0');
INSERT INTO [t10490] VALUES (3123,'نافع بن كيسان قال محمد بن سعد كاتب الواقدي: نافع بن كيسان، سكن دمشق , وروى عن النبي صلى الله عليه وسلم','8929','3086','0');
INSERT INTO [t10490] VALUES (3124,'نافع أبو طيبة الحجام مختلف في اسمه فقيل: نافع، وقيل: ميسرة، مولى محيصة بن مسعود الأنصاري','8931','3086','0');
INSERT INTO [t10490] VALUES (3125,'نافع بن أبي نافع الرواسي جد علقمة، روى حديثه حميد بن عبد الرحمن الرواسي أبو عوف، مختلف في حديثه','8933','3086','0');
INSERT INTO [t10490] VALUES (3126,'نافع بن الحارث أبو عبد الله الثقفي، سكن البصرة، روى عن النبي صلى الله عليه وسلم حديثا. قال محمد بن سعد الواقدي: هو نافع بن الحارث بن كلدة بن عمرو بن علاج، واسمه عمير بن أبي سلمة بن عبد العزى بن عوف بن ثقيف، وأم نافع سمية أم أبي بكرة، وزياد، وكان','8935','3086','0');
INSERT INTO [t10490] VALUES (3127,'نافع بن عجير سكن المدينة، روى عن النبي صلى الله عليه وسلم في طلاق ألبتة، ذكره المنيعي رحمه الله','8937','3086','0');
INSERT INTO [t10490] VALUES (3128,'نفيع أبو بكرة وقيل: مسروح قال أحمد بن حنبل: اسمه نفيع بن الحارث، وقال أبو خيثمة: نفيع بن مسروح، وقال محمد بن سعد: أبو بكرة اسمه نفيع بن مسروح , وأمه سمية، وهو أخو زياد بن أبي سفيان لأمه، كان عبدا لبعض أهل الطائف , فتدلى إلى رسول الله صلى الله','8939','3086','0');
INSERT INTO [t10490] VALUES (3129,'نضلة بن عبيد وقيل: ابن عبد الله، وقيل: ابن عائذ أبو برزة الأسلمي من بني سلامان بن أسلم، وقال هشام بن محمد الكلبي: هو نضلة بن عبد الله بن الحارث بن حبال بن ربيعة بن دعبل بن أنس بن خزيمة بن مالك بن سلامان بن أسلم بن أفصى، أسلم قديما، وشهد فتح مكة،','8946','3086','0');
INSERT INTO [t10490] VALUES (3130,'نضلة بن عمرو الغفاري سكن الحجاز، وفد على النبي صلى الله عليه وسلم , فأقطعه أرضا بالصفراء','8951','3086','0');
INSERT INTO [t10490] VALUES (3131,'نضلة بن بهصل الحرمازي','8954','3086','0');
INSERT INTO [t10490] VALUES (3132,'نضلة بن ماعز رأى أبا ذر يصلي الضحى، روى حديثه حسين المعلم , عن عبد الله بن بريدة، ذكره بعض المتأخرين، ولم يزد عليه','8956','3086','0');
INSERT INTO [t10490] VALUES (3133,'نوفل بن معاوية بن عروة وقيل: ابن عمرو الدؤلي، حجازي، حديثه عند أبي بكر بن عبد الرحمن، وعبد الرحمن بن مطيع، وعراك بن مالك','8957','3086','0');
INSERT INTO [t10490] VALUES (3134,'نوفل أبو فروة الأشجعي ذكر ابن سعد صاحب الطبقات: أنه أبو سحيم نوفل الأشجعي , حديثه عند ابنيه: فروة، وعبد الرحمن','8959','3086','0');
INSERT INTO [t10490] VALUES (3135,'نوفل بن الحارث بن عبد المطلب الهاشمي روى عنه عبد الله بن عباس','8962','3086','0');
INSERT INTO [t10490] VALUES (3136,'نوفل بن عبد الله بن نضلة الأنصاري شهد بدرا','8965','3086','0');
INSERT INTO [t10490] VALUES (3137,'نمير الخزاعي أبو مالك، له صحبة، حديثه عند ابنه مالك','8967','3086','0');
INSERT INTO [t10490] VALUES (3138,'نمير بن خرشة الثقفي ذكره البخاري في الصحابة','8969','3086','0');
INSERT INTO [t10490] VALUES (3139,'نفير بن جبير أبو جبير الحضرمي , روى عنه ابنه جبير، يعد في الشاميين','8971','3086','0');
INSERT INTO [t10490] VALUES (3140,'نفير بن مجيب ذكره بعض المتأخرين، وصحف إنما هو سفيان بن مجيب وقد ذكره على الصحة فيمن اسمه سفيان , هذا الحديث بعينه، عن يحيى بن أبي كثير','8974','3086','0');
INSERT INTO [t10490] VALUES (3141,'نهيك بن صريم السكوني سكن الشام، حديثه عند أبي إدريس الخولاني','8976','3086','0');
INSERT INTO [t10490] VALUES (3142,'نهيك بن أوس الخزرجي ذكره المنيعي قال: رأيت في كتاب محمد بن سعد: نهيك بن أوس بن خزيمة بن عدي بن أبي بن غنم بن عوف بن عمرو بن عوف بن الخزرج من القواقلة، شهد نهيك أحدا، والمشاهد كلها مع رسول الله صلى الله عليه وسلم، وأرسله إلى المدينة يبشرهم بفتح حنين','8978','3086','0');
INSERT INTO [t10490] VALUES (3143,'نهيك بن عاصم بن مالك بن المنتفق رفيق أبي رزين لقيط بن عامر، ذكره في حديثه','8979','3086','0');
INSERT INTO [t10490] VALUES (3144,'نهيك بن يساف وقيل: يساف بن نهيك، ذكره بعض المتأخرين، وروي له حديث ظهير بن رافع','8981','3086','0');
INSERT INTO [t10490] VALUES (3145,'نصر بن دهر الأسلمي عداده في أهل المدينة، حديثه عند ابنه أبي الهيثم','8983','3086','0');
INSERT INTO [t10490] VALUES (3146,'نصر بن الحارث الأوسي الظفري شهد بدرا','8987','3086','0');
INSERT INTO [t10490] VALUES (3147,'نصر بن وهب الخزاعي رأى النبي صلى الله عليه وسلم، روى عنه أبو المليح الهذلي','8989','3086','0');
INSERT INTO [t10490] VALUES (3148,'نصر بن حزن النصري أدرك النبي صلى الله عليه وسلم، وهم شعبة في اسمه فقال: نصر بن حزن قاله عنه ابن أبي عدي وقال أبو داود: عن شعبة، عن أبي إسحاق، عن بشر بن حزن، وقيل: عن أبي داود أيضا، عن شعبة، عن أبي إسحاق، عن عبدة بن حزن','8991','3086','0');
INSERT INTO [t10490] VALUES (3149,'نصر بن عوف بن قتادة ابن أخي صفوان بن قدامة، له ذكر في حديث صفوان , وقد تقدم','8992','3086','0');
INSERT INTO [t10490] VALUES (3150,'نصير: غير منسوب ذكره الحضرمي في الوحدان','8993','3086','0');
INSERT INTO [t10490] VALUES (3151,'النضر بن الحارث بن كلدة بن علقمة القرشي من بني عبد الدار، عداده في الحجازيين، شهد حنينا، وأعطاه النبي صلى الله عليه وسلم مائة من الإبل','8995','3086','0');
INSERT INTO [t10490] VALUES (3152,'النضر بن سلمة الهذلي سمع النبي صلى الله عليه وسلم، روى عنه أبو عبد الله القراظ','8998','3086','0');
INSERT INTO [t10490] VALUES (3153,'نجاب بن ثعلبة شهد بدرا','9000','3086','0');
INSERT INTO [t10490] VALUES (3154,'ناجية بن جندب بن كعب وقيل: ناجية بن كعب بن جندب بن عمير بن يعمر بن حازم الأسلمي , سائق بدن رسول الله صلى الله عليه وسلم، حجازي، حديثه عند عروة بن الزبير، وعبد الله بن عمر الأسلمي، وزاهر أبي مجزأة الأسلمي','9002','3086','0');
INSERT INTO [t10490] VALUES (3155,'ناجية بن الحارث الخزاعي روى حديثه، عيسى بن الحضرمي بن كلثوم، عن آبائه','9006','3086','0');
INSERT INTO [t10490] VALUES (3156,'ناجية الطفاوي قيل: كان يكتب المصاحف، مختلف في صحبته','9008','3086','0');
INSERT INTO [t10490] VALUES (3157,'ناجية بن عمرو حديثه عند عائذ بن شريح','9010','3086','0');
INSERT INTO [t10490] VALUES (3158,'ناجية بن خفاف أبو خفاف العنزي، ذكر في الصحابة، ولا يصح، روى عنه أبو إسحاق السبيعي، ذكره بعض المتأخرين , ولم يزد عليه','9012','3086','0');
INSERT INTO [t10490] VALUES (3159,'النواس بن سمعان الأنصاري الكلابي يعد في الشاميين، حديثه عند: جبير بن نفير، وأبي إدريس الخولاني، ويحيى بن جابر الطائي، ورجاء بن حيوة، وبشر بن عبيد الله , ومكحول','9013','3086','0');
INSERT INTO [t10490] VALUES (3160,'نقادة الأسدي أبو سعر روى عنه: ابنه سعر، والبراء السليطي','9016','3086','0');
INSERT INTO [t10490] VALUES (3161,'نبيشة الخير الهذلي وهو نبيشة بن عبد الله بن شيبان بن عتاب بن الحارث بن حصين بن الحارث بن عبد العزى بن وائل بن ليحان بن هذيل أبو طريف سكن البصرة، ابن عم سلمة بن المحبق، حديثه عند أبي المليح الهذلي','9018','3086','0');
INSERT INTO [t10490] VALUES (3162,'نبيشة: غير منسوب توفي قبل النبي صلى الله عليه وسلم، ذكره في حديث ابن عباس , أن النبي صلى الله عليه وسلم رأى رجلا يلبي عن نبيشة، فقال: " أيها الملبي عن نبيشة، حججت؟ " قال: لا','9021','3086','0');
INSERT INTO [t10490] VALUES (3163,'نبيط بن جابر الأنصاري ذكره المنيعي وقال: رأيت في أصل كتاب محمد بن سعد: نبيط بن جابر بن مالك بن عدي بن عمرو بن مالك بن النجار، شهد أحدا , وله عقب: زوجه النبي صلى الله عليه وسلم الفريعة بنت أبي أمامة أسعد بن زرارة، وكانت من المبايعات','9022','3086','0');
INSERT INTO [t10490] VALUES (3164,'نبيط بن شريط بن أنس حديثه عند ابنه سلمة بن نبيط، وأبي مالك الأشجعي','9023','3086','0');
INSERT INTO [t10490] VALUES (3165,'نيار بن ظالم الأسدي قاله المنيعي، عن محمد بن سعد، وقال: هو نيار بن ظالم بن عبس بن حرام بن جندب بن عامر بن غنم بن عدي بن النجار، شهد أحدا: أمه أم نيار بنت إياس بن عامر , من بلي، حلفاء بني حارثة، وهو أخو أبي الأعور بن ظالم الذي شهد بدرا','9025','3086','0');
INSERT INTO [t10490] VALUES (3166,'نيار بن مكرم الأسلمي له صحبة، روى عنه عروة بن الزبير','9026','3086','0');
INSERT INTO [t10490] VALUES (3167,'نابغة بن جعدة أبو ليلى مختلف في اسمه , فقيل: عبد الله بن قيس، وقيل قيس بن عبد الله، وقد تقدم ذكره','9028','3086','0');
INSERT INTO [t10490] VALUES (3168,'نبيه بن صؤاب الجهني وفد على النبي صلى الله عليه وسلم وشهد فتح مصر، وكان أحد الأربعة الذين أقاموا قبلة مصر، روى عنه يزيد بن أبي حبيب، وعبد الملك بن أبي رائطة، وعبد العزيز بن مليل قاله أبو سعيد بن عبد الأعلى , فيما حكاه المتأخر عنه','9029','3086','0');
INSERT INTO [t10490] VALUES (3169,'نوح بن مخلد الضبعي روى عنه أبو جمرة نصر بن عمران الضبعي','9031','3086','0');
INSERT INTO [t10490] VALUES (3170,'النمر بن تولب الشاعر كتب له النبي صلى الله عليه وسلم كتابا، حديثه عند مطرف بن عبد الله بن الشخير','9033','3086','0');
INSERT INTO [t10490] VALUES (3171,'نضرة بن أكثم الخزاعي وقيل: بصرة , روى عنه سعيد بن المسيب','9035','3086','0');
INSERT INTO [t10490] VALUES (3172,'نصيب مولى سرى بنت نبهان الغنوية، سأل النبي صلى الله عليه وسلم','9037','3086','0');
INSERT INTO [t10490] VALUES (3173,'نميلة بن عبد الله الكلبي صحب النبي صلى الله عليه وسلم، له ذكر في حديث','9039','3086','0');
INSERT INTO [t10490] VALUES (3174,'نقب بن فروة بن البدن الأنصاري من بني ساعدة، استشهد يوم أحد','9041','3086','0');
INSERT INTO [t10490] VALUES (3175,'نقيدة بن عمرو الكعبي الخزاعي روى عنه حزام بن هشام، ذكره بعض المتأخرين وقال: ذكر في الصحابة , ولا يثبت، روايته عن عمر بن الخطاب رضي الله عنه','9043','3086','0');
INSERT INTO [t10490] VALUES (3176,'نبهان التمار أبو مقبل , نزلت فيه والذين إذا فعلوا فاحشة , وأقم الصلاة طرفي النهار','9044','3086','0');
INSERT INTO [t10490] VALUES (3177,'ناشرة بن سويد الجهني روى عنه ابنه مريح، وعلي بن رباح، ذكره بعض المتأخرين','9046','3086','0');
INSERT INTO [t10490] VALUES (3178,'النباش بن زرارة له ذكر في المغازي، وله صحبة فيما ذكره بعض المتأخرين','9048','3086','0');
INSERT INTO [t10490] VALUES (3179,'باب الواو','9049','18','0');
INSERT INTO [t10490] VALUES (3180,'وائل بن حجر الكندي الحضرمي من أبناء أقيال اليمن، وهو وائل بن حجر بن سعد بن مسروق بن وائل بن ضمعج بن وائل بن ربيعة بن وائل بن النعمان بن زيد بن مالك بن زيد الحضرمي، وفد على النبي صلى الله عليه وسلم فأنزله، وأصعد به معه على منبره، وأقطعه القطائع , وكتب','9050','3179','0');
INSERT INTO [t10490] VALUES (3181,'وائل بن أبي القعيس أخو أفلح، مختلف في حديثه، له ذكر في حديث عائشة، ذكره بعض المتأخرين، ولا أعلم له صحبة ولا إسلاما','9059','3179','0');
INSERT INTO [t10490] VALUES (3182,'واثلة بن الأسقع الليثي من بني ليث بن بكر بن عبد مناة مختلف في كنيته، فقيل: أبو شداد، وقيل: أبو قرصافة، وقيل: أبو الأسقع، سكن بيت جبرين من الشام، قدم قبل مخرج النبي صلى الله عليه وسلم إلى تبوك بليال , فسكن الصفة، توفي سنة خمس وثمانين وله ثمان','9061','3179','0');
INSERT INTO [t10490] VALUES (3183,'واثلة الليثي أبو أبي الطفيل، رأى الحجر الأسود في الجاهلية أبيض مثل الصفا، ذكره المنيعي في الوحدان','9066','3179','0');
INSERT INTO [t10490] VALUES (3184,'واثلة بن الخطاب القرشي ذكره المنيعي، وقال: له حديث، ولم يخرج له شيئا','9067','3179','0');
INSERT INTO [t10490] VALUES (3185,'وهب بن زمعة أبو عبد الله وهو أخو عبد الله، له ذكر في حديث أم سلمة','9068','3179','0');
INSERT INTO [t10490] VALUES (3186,'وهب بن الأسود القرشي وقيل: الأسود بن وهب، ابن خال النبي صلى الله عليه وسلم، مختلف في صحبته','9070','3179','0');
INSERT INTO [t10490] VALUES (3187,'وهب بن حذيفة الحجازي حديثه عند واسع بن حبان','9072','3179','0');
INSERT INTO [t10490] VALUES (3188,'وهب بن قيس بن أبان الثقفي أخو سفيان، روت عنه أميمة بنت رقيقة','9074','3179','0');
INSERT INTO [t10490] VALUES (3189,'أبو سنان الأسدي: وهب بن عبد الله أول من بايع بيعة الرضوان تحت الشجرة، وهو ابن محصن بن حرثان، لا يعرف له رواية','9076','3179','0');
INSERT INTO [t10490] VALUES (3190,'وهب بن عمرو من بني غنم بن ذودان، من المهاجرين الأولين، ذكره بعض المتأخرين، عن يونس بن بكير، عن ابن إسحاق قال: قدم المهاجرون المدينة أرسالا منهم وهب بن عمرو - وصحف إنما هو ثقف بن عمرو','9078','3179','0');
INSERT INTO [t10490] VALUES (3191,'وهب بن عمير القرشي بعثه النبي صلى الله عليه وسلم إلى صفوان بن أمية يدعوه إلى الإسلام','9079','3179','0');
INSERT INTO [t10490] VALUES (3192,'وهب بن عبد الله بن قارب الثقفي حجازي , ذكره بعض المتأخرين وقال: حج مع أبيه، فرأى النبي صلى الله عليه وسلم. روى عنه إبراهيم بن ميسرة في الترحم على المحلقين، وهو وهم، فإن وهب بن عبد الله بن قارب روى، عن أبيه أنه قال: حججت مع أبي , فرأيت النبي صلى','9081','3179','0');
INSERT INTO [t10490] VALUES (3193,'وهب بن سعد بن أبي سرح من بني مالك، استشهد يوم مؤتة، لم يسند شيئا','9083','3179','0');
INSERT INTO [t10490] VALUES (3194,'وهب بن عبد الله أبو جحيفة السوائي، من بني عامر بن صعصعة، سكن الكوفة، كان على شرطة علي بن أبي طالب، وكان يقوم تحت منبره، استعمله على خمس المتاع الذي كان في حربه، توفي النبي صلى الله عليه وسلم تسليما، وأبو جحيفة لم يبلغ الحلم، وتوفي أبو جحيفة في','9085','3179','0');
INSERT INTO [t10490] VALUES (3195,'وهب بن جمرة وقيل حمزة، حديثه في الكوفيين','9091','3179','0');
INSERT INTO [t10490] VALUES (3196,'وهب بن خنبش وقيل: ابن هرم، وقيل: هرم، حديثه في الكوفيين','9093','3179','0');
INSERT INTO [t10490] VALUES (3197,'وهب بن معقل الغفاري نزل مصر، روى عنه أبو قبيل المعافري، ذكره المتأخر، عن أبي سعيد بن عبد الأعلى، وقال: له صحبة، ولم يزد عليه','9095','3179','0');
INSERT INTO [t10490] VALUES (3198,'وابصة بن معبد بن عتبة بن الحارث بن مالك بن الحارث بن قيس بن كعب بن سعد بن الحارث بن ثعلبة بن دودان بن أسد بن خزيمة بن مدركة، يكنى: أبا سالم، سكن الرقة','9096','3179','0');
INSERT INTO [t10490] VALUES (3199,'الوليد بن الوليد بن المغيرة المخزومي كان من المستضعفين، حبسه المشركون بمكة عن الهجرة فانفلت منهم، وبعد أن دعا له النبي صلى الله عليه وسلم في قنوته بالنجاة، فقدم المدينة، وتوفي بها، فكفنه رسول الله صلى الله عليه وسلم في قميصه، وكانت أم سلمة تندبه','9100','3179','0');
INSERT INTO [t10490] VALUES (3200,'الوليد بن عقبة بن أبي معيط ابن أبي عمرو بن أمية بن عبد شمس، يكنى أبا وهب وكان أخا لعثمان لأمه، أمهما أروى بنت كريز بن ربيعة بن حبيب بن عبد شمس، وأمها أم حكيم البيضاء بنت عبد المطلب، عمة النبي صلى الله عليه وسلم، بعثه النبي صلى الله عليه وسلم إلى بني','9105','3179','0');
INSERT INTO [t10490] VALUES (3201,'الوليد بن قيس العامري دعا له النبي صلى الله عليه وسلم','9108','3179','0');
INSERT INTO [t10490] VALUES (3202,'واقد بن عبد الله الحنظلي وقيل: اليربوعي، بعثه النبي صلى الله عليه وسلم في سرية عبد الله بن جحش إلى بطن نخلة','9110','3179','0');
INSERT INTO [t10490] VALUES (3203,'واقد مولى رسول الله صلى الله عليه وسلم','9112','3179','0');
INSERT INTO [t10490] VALUES (3204,'واقد بن الحارث يكنى: أبا الحارث ذكره بعض المتأخرين، وزعم أن له صحبة، روى حديثه قيس بن رافع، يعد في المصريين، روى حديثه عبد الله بن المبارك','9114','3179','0');
INSERT INTO [t10490] VALUES (3205,'وذكر بعض المتأخرين: واقد أبو مرواح الليثي وحكي أن أبا داود السجستاني قال: له صحبة','9116','3179','0');
INSERT INTO [t10490] VALUES (3206,'وبر بن مشهر الحنفي كان رسول مسيلمة، فقدم على النبي صلى الله عليه وسلم فأسلم، وأقام معه بالمدينة يتعلم القرآن إلى أن قبض صلى الله عليه وسلم','9117','3179','0');
INSERT INTO [t10490] VALUES (3207,'وبر بن يخنس سمع النبي صلى الله عليه وسلم فيما ذكره بعض المتأخرين، روى عنه النعمان بن بزرج، حديثه عند عبد الملك الزماري','9119','3179','0');
INSERT INTO [t10490] VALUES (3208,'ورقة بن نوفل الدؤلي، وقيل: الأنصاري','9121','3179','0');
INSERT INTO [t10490] VALUES (3209,'وهبان بن صيفي الغفاري وقيل: أهبان، حديثه عند زهدم بن الحارث','9123','3179','0');
INSERT INTO [t10490] VALUES (3210,'وحشي بن حرب الحبشي أبو دسمة مولى جبير بن مطعم بن عدي بن نوفل، قاتل حمزة، أسلم بعد الفتح، فقدم مع وفد ثقيف إلى المدينة، وشهد اليمامة ورمى مسيلمة الكذاب، وهو والأنصاري، وقتل مسيلمة من ضربتهما، ثم تحول إلى الشام فسكن حمصا، ومات بها، حديثه عند جعفر','9125','3179','0');
INSERT INTO [t10490] VALUES (3211,'وردان مولى النبي صلى الله عليه وسلم توفي على عهد رسول الله صلى الله عليه وسلم، ذكره في حديث ابن عباس رضي الله عنه','9130','3179','0');
INSERT INTO [t10490] VALUES (3212,'وردان التميمي قدم في سبي بني تميم على النبي صلى الله عليه وسلم، له ذكر في حديث لعائشة، ذكره بعض المتأخرين، وقال: وردان بن إسماعيل، وذكر فيما خرج له خلافه','9132','3179','0');
INSERT INTO [t10490] VALUES (3213,'وعلة بن يزيد عداده في البصريين، من أعرابهم، روت عنه ابنته أم يزيد','9134','3179','0');
INSERT INTO [t10490] VALUES (3214,'وديعة بن عمرو الجهني حليف الأنصار، من بني سواد بن عمرو، شهد بدرا','9136','3179','0');
INSERT INTO [t10490] VALUES (3215,'وداعة بن أبي وداعة السهمي وفد على النبي صلى الله عليه وسلم، في صحبته مقال، ذكره بعض المتأخرين','9139','3179','0');
INSERT INTO [t10490] VALUES (3216,'واسع بن حبان الأنصاري ذكره المنيعي في الوحدان، وقال: سكن المدينة، في صحبته مقال','9141','3179','0');
INSERT INTO [t10490] VALUES (3217,'ودفة بن إياس الأنصاري شهد بدرا، من بني عوف بن الخزرج','9143','3179','0');
INSERT INTO [t10490] VALUES (3218,'ودان بن زر الكلبي أتى النبي صلى الله عليه وسلم له ذكر في حديث فيما ذكره بعض المتأخرين من حديث محمد بن يحيى الأسدي، ثنا محمد بن يزيد بن زياد بن عبد الواسع بن علي بن الودان بن زر الكلبي، وكان الودان أتى النبي صلى الله عليه وسلم فيما ذكر، عن أبيه، عن','9146','3179','0');
INSERT INTO [t10490] VALUES (3219,'باب الهاء','9147','18','0');
INSERT INTO [t10490] VALUES (3220,'من اسمه هشام','9148','3219','0');
INSERT INTO [t10490] VALUES (3221,'هشام بن حكيم بن حزام بن أسد بن عبد العزى بن قصي بن كلاب بن مرة بن كعب بن لؤي بن غالب، استشهد بأجنادين من أرض الشام، توفي قبل أبيه حكيم، أمه أم هشام من بني فراس بن غنم، روى عنه عمر بن الخطاب، والمسور بن مخرمة، وعبد الرحمن بن قتادة السلمي، وعروة بن','9149','3220','0');
INSERT INTO [t10490] VALUES (3222,'هشام بن العاص بن وائل السهمي أخو عمرو بن العاص، صحب النبي صلى الله عليه وسلم، واستشهد بأجنادين، وقيل: باليرموك بالشام، شهد له النبي صلى الله عليه وسلم بالإيمان','9153','3220','0');
INSERT INTO [t10490] VALUES (3223,'هشام بن عامر الأنصاري له صحبة، سماه النبي صلى الله عليه وسلم هشاما، روى عنه حميد بن هلال، وأبو قلابة، وأبو قتادة العدوي، وابنه سعد، والحسن البصري، ومعاذة العدوية، سكن البصرة، أمه من بهز، شهد أبوه بدرا، واستشهد بأحد، وقال محمد بن سعد الواقدي:','9156','3220','0');
INSERT INTO [t10490] VALUES (3224,'هشام بن صبابة الليثي أخو مقيس بن صبابة الليثي، قتل خطأ فوداه النبي صلى الله عليه وسلم','9160','3220','0');
INSERT INTO [t10490] VALUES (3225,'هشام مولى رسول الله صلى الله عليه وسلم حديثه عند أبي الزبير','9162','3220','0');
INSERT INTO [t10490] VALUES (3226,'هشام بن عمرو العامري من المؤلفة','9164','3220','0');
INSERT INTO [t10490] VALUES (3227,'هشام بن عتبة بن ربيعة بن عبد شمس أبو حذيفة، قتل هو ومولاه سالم يوم اليمامة سنة إحدى عشرة، قاله الأصمعي، عن ابن أبي زياد، عن أبيه، وكان أبو حذيفة ممن شهد بدرا مع رسول الله صلى الله عليه وسلم، رواه الزهري، عن عروة، عن عائشة رضي الله عنها','9166','3220','0');
INSERT INTO [t10490] VALUES (3228,'هشام بن قتادة الرهاوي سكن الرها، روى عن النبي صلى الله عليه وسلم حديثا، ذكره المنيعي في الوحدان','9167','3220','0');
INSERT INTO [t10490] VALUES (3229,'هاشم بن عتبة بن أبي وقاص الزهري مختلف فيه، فقيل: هو نافع أبو هشام، حديثه عند يونس بن أبي إسحاق، عن عبد الملك بن عمير','9169','3220','0');
INSERT INTO [t10490] VALUES (3230,'*','9171','3219','0');
INSERT INTO [t10490] VALUES (3231,'هانئ بن عمرو أبو شريح الخزاعي مختلف في اسمه، ذكره سليمان فيمن اسمه هانئ','9171','3230','0');
INSERT INTO [t10490] VALUES (3232,'هانئ بن نيار بن عمرو بن عبيد بن عمرو بن كلاب بن دهمان بن غنم بن ذبيان بن هميم بن كاهل بن ذهل بن بلي بن عمرو بن الحاف بن قضاعة، عقبي بدري أحدي شجري، وهو خال البراء بن عازب، أبو بردة مختلف في اسمه فقيل اسمه: كثير','9172','3230','0');
INSERT INTO [t10490] VALUES (3233,'هانئ بن يزيد بن نهيك بن دريد بن سفيان بن ضباب من بني الحارث بن كعب النخعي الكوفي، له صحبة، كناه النبي صلى الله عليه وسلم أبا شريح','9177','3230','0');
INSERT INTO [t10490] VALUES (3234,'هانئ أبو مالك، جد خالد بن يزيد','9179','3230','0');
INSERT INTO [t10490] VALUES (3235,'هانئ بن فراس الأشجعي من أهل الكوفة، شهد الشجرة، اشتكى فجعل تحت ركبته وسادة، رواه إسرائيل، عن مجزأة بن زاهر، لا يعرف له حديث مسند، ذكره بعض المتأخرين','9181','3230','0');
INSERT INTO [t10490] VALUES (3236,'هانئ بن جزء بن النعمان بن قيس المرادي أخو نعمان الغطيفي، وفد على النبي صلى الله عليه وسلم، وشهد فتح مصر، لا يعرف له رواية، قاله أبو سعيد بن عبد الأعلى','9182','3230','0');
INSERT INTO [t10490] VALUES (3237,'هلال بن أمية الأنصاري الواقفي أحد الثلاثة الذين تاب الله عليهم، روى عنه ابن عباس، وجابر، وهو القاذف امرأته فلاعنها، بقي بعد النبي صلى الله عليه وسلم دهرا، وقال محمد بن سعد: هو هلال بن أمية بن عامر بن قيس بن عبد الأعلى بن عامر بن كعب بن واقف: وكان','9183','3230','0');
INSERT INTO [t10490] VALUES (3238,'هلال بن المعلى بن لوذان الأنصاري الزرقي، أخو رافع، شهد بدرا','9185','3230','0');
INSERT INTO [t10490] VALUES (3239,'هلال أبو أم بلال الأسلمي، سكن المدينة','9187','3230','0');
INSERT INTO [t10490] VALUES (3240,'هلال بن ربيعة ذكره بعض المتأخرين، وقال: له صحبة، في إسناد حديثه إرسال، وأسند له هذا الحديث: عن محمد بن إسحاق، عن عبد الله بن أبي بكر، فصحف، إنما هو مالك بن ربيعة أبو أسيد، فجعله هلال بن ربيعة','9189','3230','0');
INSERT INTO [t10490] VALUES (3241,'هلال بن مرة الأشجعي زوج بروع بنت واشق، تقدم ذكره فيمن اسمه الجراح','9191','3230','0');
INSERT INTO [t10490] VALUES (3242,'هند بن أبي هالة وهو هالة بن النباش بن زرارة بن وقدان بن حبيب بن سلامة بن غوي بن جروة بن أسيد بن عمرو بن تميم، أحد بني أسيد بن عمرو بن تميم، حليف بني عبد الدار، أمه خديجة بنت خويلد زوج النبي صلى الله عليه وسلم، كانت قبل رسول الله صلى الله عليه وسلم','9192','3230','0');
INSERT INTO [t10490] VALUES (3243,'هند بن هند بن أبي هالة','9194','3230','0');
INSERT INTO [t10490] VALUES (3244,'هند بن أسماء بن حارثة بن هند الأسلمي وقيل: هند بن حارثة بن سعيد، وقال محمد بن عمر الواقدي: قال أبو هريرة: وما كنت أرى أسماء، وهندا ابنا حارثة إلا خادمين لرسول الله صلى الله عليه وسلم، من طول لزومهما بابه، وخدمتهما إياه، وكانا محتاجين، ولهما عقب','9196','3230','0');
INSERT INTO [t10490] VALUES (3245,'هوذة بن قيس بن عبادة بن دهم بن عطية بن زيد بن قيس بن عامر بن مالك بن أوس، أنصاري، روى حديثه: عبد الرحمن بن النضر، عن أبيه، عن جده، مختلف في نسبه','9199','3230','0');
INSERT INTO [t10490] VALUES (3246,'هوذة أدرك النبي صلى الله عليه وسلم، غير منسوب، روى عنه الشعبي أنه قدم على معاوية فسأله: هل شهدت بدرا؟ فقال علي: لا، لي. . . . . . فذكر القصة، ذكره بعض المتأخرين، ولا تصح له صحبة، لأن إسلامه كان بعد وفاة النبي صلى الله عليه وسلم','9201','3230','0');
INSERT INTO [t10490] VALUES (3247,'هوذة بن عرفطة الحميري وفد على النبي صلى الله عليه وسلم، وشهد فتح مصر، لا يعرف له رواية، قاله المحيل على أبي سعيد بن عبد الأعلى','9202','3230','0');
INSERT INTO [t10490] VALUES (3248,'هرماس بن زياد بن عمرو بن عامر الباهلي، من قيس عيلان، يكنى: أبا جدير، سكن اليمامة، وقيل: اسمه شريح، حديثه عند هرماس، وحنبل، وعكرمة بن عمار','9203','3230','0');
INSERT INTO [t10490] VALUES (3249,'هلب الطائي أبو قبيصة مختلف في اسمه، فقيل: يزيد بن قنافة، ذكره البخاري، وقيل: سلام، ولا يصح، حديثه عند ابنه قبيصة، كان أقرع الرأس، فمسح النبي صلى الله عليه وسلم رأسه فنبت شعره، فسمي الهلب الطائي','9207','3230','0');
INSERT INTO [t10490] VALUES (3250,'هبيب بن مغفل الغفاري سكن مصر، وهو هبيب بن محمد بن عمرو بن المغفل بن الواقعة بن حرام بن غفار بن مليل بن ضمرة، وسمي مغفلا لأنه أغفل سمة إبله فلم يسمها، روى عنه أسلم أبو عمران','9211','3230','0');
INSERT INTO [t10490] VALUES (3251,'هبيرة بن شبل بن العجلان بن عتاب بن مالك بن كعب بن عمرو بن سعد بن عوف بن ثقيف','9215','3230','0');
INSERT INTO [t10490] VALUES (3252,'هنيدة بن خالد الخزاعي وقيل: النخعي، يعد في الكوفيين، مختلف في صحبته، كانت أمه تحت عمر بن الخطاب رضي الله عنه، حديثه عند: أبي إسحاق، وعدي بن ثابت','9217','3230','0');
INSERT INTO [t10490] VALUES (3253,'هزال بن يزيد الأسلمي روى عنه: ابنه نعيم','9220','3230','0');
INSERT INTO [t10490] VALUES (3254,'هداج الحنفي وكان قد أدرك الجاهلية','9223','3230','0');
INSERT INTO [t10490] VALUES (3255,'هدار صاحب النبي صلى الله عليه وسلم يعد في الحمصيين، حديثه عند: شقير مولى العباس','9225','3230','0');
INSERT INTO [t10490] VALUES (3256,'هبار بن سفيان بن عبد الأسد قتل بأجنادين بأرض الشام في خلافة أبي بكر، لا عقب له، من مهاجرة الحبشة','9227','3230','0');
INSERT INTO [t10490] VALUES (3257,'هبار بن الأسود بن المطلب بن أسد بن عبد العزى بن قصي، وهو الذي أمر النبي صلى الله عليه وسلم بإحراقه لما ضرب هودج زينب بنته , فروعها حتى أسقطت، ثم أسلم فحسن إسلامه','9229','3230','0');
INSERT INTO [t10490] VALUES (3258,'هرمز أو كيسان مولى النبي صلى الله عليه وسلم مختلف في اسمه','9233','3230','0');
INSERT INTO [t10490] VALUES (3259,'هرم بن خنبش','9236','3230','0');
INSERT INTO [t10490] VALUES (3260,'هرمي بن عبد الله الواقفي ذكره محمد بن سعد الواقدي , وقال: هرمي بن عبد الله بن رفاعة بن نجدة بن مجدعة بن عدي بن نمير بن واقف، كان قديم الإسلام وهو من البكائين الذين أتوا رسول الله صلى الله عليه وسلم، وهو يريد الخروج إلى مكة، فاستحملوه','9238','3230','0');
INSERT INTO [t10490] VALUES (3261,'الهيثم: أبو قيس استعمله النبي صلى الله عليه وسلم على صدقات قومه','9239','3230','0');
INSERT INTO [t10490] VALUES (3262,'الهيثم بن دهر','9241','3230','0');
INSERT INTO [t10490] VALUES (3263,'أبو معقل الأسدي قيل: إن اسمه الهيثم، نذكر حديثه في الكنى إن شاء الله','9243','3230','0');
INSERT INTO [t10490] VALUES (3264,'هيبان وقيل: هيفان - الأسلمي , حديثه عند: ابنه عبد الله','9244','3230','0');
INSERT INTO [t10490] VALUES (3265,'حرف لام ألف','9246','18','0');
INSERT INTO [t10490] VALUES (3266,'لاشر بن حمير وقيل: لاشومة بن جرثوم، وقيل: لاس بن جاهم أبو ثعلبة الخشني، مختلف في اسمه، وقيل: عرنوق بن ناشم، وقيل: ناشر، وقيل: ناشب بن عمرو، وقيل: جرثم بن ناشب، وقيل: جرهم بن ناشم، وقيل: جرثوم، تقدم ذكره في حرف الجيم، توفي سنة خمس وسبعين','9247','3265','0');
INSERT INTO [t10490] VALUES (3267,'لاحق بن ضميرة الباهلي وفد على النبي صلى الله عليه وسلم','9249','3265','0');
INSERT INTO [t10490] VALUES (3268,'باب الياء','9251','18','0');
INSERT INTO [t10490] VALUES (3269,'من اسمه يزيد','9252','3268','0');
INSERT INTO [t10490] VALUES (3270,'يزيد بن أبي سفيان صخر بن حرب بن أمية بن عبد شمس بعثه أبو بكر الصديق رضي الله عنه إلى الشام، فخرج مشيعا له ماشيا، وأقره عمر، توفي في الشام سنة ثماني عشرة، أحد أمراء الأجناد، يكنى أبا خالد، أمه: أم الحكم زينب بنت نوفل بن خلف من بني جلاس، ثم من بني','9253','3269','0');
INSERT INTO [t10490] VALUES (3271,'يزيد بن الأسود أبو جابر السوائي','9255','3269','0');
INSERT INTO [t10490] VALUES (3272,'يزيد بن عامر السوائي كان قد شهد حنينا مع المشركين , ثم أسلم، حديثه عند: السائب بن يسار، ونوح بن صعصعة','9257','3269','0');
INSERT INTO [t10490] VALUES (3273,'يزيد بن السكن الأنصاري شهد أحدا مع النبي صلى الله عليه وسلم','9260','3269','0');
INSERT INTO [t10490] VALUES (3274,'يزيد بن ثابت الأنصاري أخو زيد بن ثابت، حديثه عند ابن أخيه خارجة بن زيد','9262','3269','0');
INSERT INTO [t10490] VALUES (3275,'يزيد بن سلمة الجعفي يعد في الكوفيين، حديثه عند: وائل بن حجر , وعلقمة بن وائل , وابن أشوع','9264','3269','0');
INSERT INTO [t10490] VALUES (3276,'يزيد بن الحارث بن قيس بن مالك بن أحمر بن حارثة بن ثعلبة بن كعب بن الحارث بن الخزرج , يقال له: ابن فسحم، لا عقب له، شهد بدرا مع النبي صلى الله عليه وسلم','9267','3269','0');
INSERT INTO [t10490] VALUES (3277,'يزيد بن المنذر بن سرح بن خناس بن سنان الأنصاري شهد بدرا مع رسول الله صلى الله عليه وسلم','9270','3269','0');
INSERT INTO [t10490] VALUES (3278,'يزيد بن نعامة الضبي روى عنه: سعيد بن سلمان، مختلف في صحبته','9273','3269','0');
INSERT INTO [t10490] VALUES (3279,'يزيد بن أسد بن كرز القسري البجلي يكنى أبا الهيثم','9275','3269','0');
INSERT INTO [t10490] VALUES (3280,'يزيد بن شيبان وقيل: ابن سنان، مختلف في صحبته','9277','3269','0');
INSERT INTO [t10490] VALUES (3281,'يزيد بن الأخنس السلمي يكنى: أبا معن، وقال محمد بن سعد: هو يزيد بن الأخنس بن حبيب بن جرة بن زغبة بن مالك بن خفاف بن امرئ القيس بن بهثة بن سليم، سكن الكوفة، وقال غيره: يعد في الشاميين، روى عنه: كثير بن مرة، وجبير بن نفير، ذكره في حديث أبي أمامة','9279','3269','0');
INSERT INTO [t10490] VALUES (3282,'يزيد أبو عبد الرحمن قيل: إنه يزيد بن جارية الأنصاري من الأوس، وقيل: زيد بن جارية، روى حديثه: عاصم بن عبيد الله، عن عبد الرحمن بن يزيد','9282','3269','0');
INSERT INTO [t10490] VALUES (3283,'ابن مربع الأنصاري قيل: إن اسمه يزيد، وقيل: زيد، روى عنه يزيد بن شيبان، أنه أتاهم فقال: إني رسول رسول الله إليكم','9284','3269','0');
INSERT INTO [t10490] VALUES (3284,'يزيد بن شيبان الأزدي ذكره بعض المتأخرين، عن البخاري أن له رؤية، وهو الديلي، خال عمرو بن عبد الله بن صفوان، حديثه عند: عمرو بن دينار','9285','3269','0');
INSERT INTO [t10490] VALUES (3285,'يزيد بن الجراح أخو أبي عبيدة، ذكره بعض المتأخرين، وقال: له رؤية وصحبه، وذكر أنه لا يعرف له حديث مسند','9287','3269','0');
INSERT INTO [t10490] VALUES (3286,'يزيد أبو معن الجرمي وقيل: السلمي، له ولابنه ولأبيه صحبة، يعد في الكوفيين، وقيل: هو يزيد بن الأخنس','9288','3269','0');
INSERT INTO [t10490] VALUES (3287,'يزيد الطائي أبو قبيصة، قيل: ابن قنافة، وقيل: ابن قتادة، يعرف بهلب الطائي، أخرجه بعض المتأخرين، وقال: يزيد بن قنافة بعد أن ذكره في حرف الهاء: هلب الطائي','9290','3269','0');
INSERT INTO [t10490] VALUES (3288,'يزيد أبو السائب ابن أخت النمر بن قاسط الكندي، وهو يزيد بن عبد الله بن الأسود بن ثمامة بن يقظان بن الحارث بن عمرو بن معاوية بن الحارث، والنمر حليف لبني عامر بن صعصعة، وكان يزيد حليفا لأبي سفيان بن حرب، صحب النبي صلى الله عليه وسلم، فأمره على اليمامة','9292','3269','0');
INSERT INTO [t10490] VALUES (3289,'يزيد أبو السائب وهو عندي الأول، فرق بعض المتأخرين بينه وبين الأول فيما ذكره عن البخاري','9295','3269','0');
INSERT INTO [t10490] VALUES (3290,'يزيد بن بشر الضبعي أدرك الجاهلية، ذكره بعض المتأخرين في الصحابة، وقال: لا يثبت','9297','3269','0');
INSERT INTO [t10490] VALUES (3291,'يزيد بن ركانة بن عبد يزيد بن هاشم توفي في أيام معاوية رضي الله عنهما','9299','3269','0');
INSERT INTO [t10490] VALUES (3292,'يزيد بن أنيس بن عبد الله بن عمرو بن حبيب بن عمرو بن شيبان بن محارب بن فهر، يكنى أبا عبد الرحمن، شهد فتح مصر، له بها دار، ولا يعرف له رواية بمصر، حديثه عند أهل البصرة، كذا حكاه المتأخر عن أبي سعيد بن عبد الأعلى','9301','3269','0');
INSERT INTO [t10490] VALUES (3293,'يزيد بن سيف بن جارية اليربوعي عداده في أعراب البصرة','9303','3269','0');
INSERT INTO [t10490] VALUES (3294,'يزيد بن جارية أو خارجة ذكره المنيعي في الوحدان','9305','3269','0');
INSERT INTO [t10490] VALUES (3295,'يزيد أبو هانئ الحنفي أتى النبي صلى الله عليه وسلم، وتحاكم إليه , فاستوهبه عليه الصلاة والسلام جراحة أخيه , فوهبها له','9307','3269','0');
INSERT INTO [t10490] VALUES (3296,'يزيد بن حمزة بن عوف وفد مع أبيه على رسول الله صلى الله عليه وسلم وبايعه، حديثه عند أولاده','9309','3269','0');
INSERT INTO [t10490] VALUES (3297,'يزيد بن عباية بن بحير بن خالد بن جلاس بن مرة بن زيد بن مالك بن جنادة بن معن الباهلي: أتى النبي صلى الله عليه وسلم بصدقة، فمسح رأسه، ذكره بعض المتأخرين وقال: رواه إبراهيم بن المستمر، عن زياد بن قريع بن يزيد بن عباية، عن أبيه، عن جده يزيد، ولم يزد','9311','3269','0');
INSERT INTO [t10490] VALUES (3298,'يزيد بن معبد الحنفي وقيل: الدؤلي أبو معبد، وفد على النبي صلى الله عليه وسلم، له ولأخيه صحبة، حديثه عند ابنه معبد','9312','3269','0');
INSERT INTO [t10490] VALUES (3299,'يزيد أبو حكيم وقيل: حكيم بن أبي يزيد','9314','3269','0');
INSERT INTO [t10490] VALUES (3300,'يزيد بن زمعة بن المطلب القرشي الأسدي استشهد مع النبي صلى الله عليه وسلم يوم حنين','9316','3269','0');
INSERT INTO [t10490] VALUES (3301,'يزيد بن أبي زياد وقيل: يزيد بن زياد الأسلمي له ذكر في الصحابة، عداده في المصريين فيما حكاه المتأخر، عن أبي سعيد بن عبد الأعلى , وأنه لا يعرف له مسند، روى عنه: يزيد بن أبي حبيب','9318','3269','0');
INSERT INTO [t10490] VALUES (3302,'يزيد بن عامر بن حديدة الأنصاري الخزرجي من بني سواد بن غنم بن كعب بن سلمة، ثم من بني حديدة بن عمرو بن سواد، شهد بدرا، يكنى أبا المنذر','9320','3269','0');
INSERT INTO [t10490] VALUES (3303,'يزيد بن قيس من حلفاء قريش، استشهد يوم اليمامة','9322','3269','0');
INSERT INTO [t10490] VALUES (3304,'يزيد بن شجرة يعد في أهل الشام، وقيل: الرهاوي، حديثه عند مجاهد','9324','3269','0');
INSERT INTO [t10490] VALUES (3305,'يزيد بن مهارخسر يعد في أهل اليمن، فارسي الأصل، وفد على النبي صلى الله عليه وسلم وعليه ثياب بياض , فسماه زاهرا ذكره بعض المتأخرين، وقال: روى الوليد بن يزيد بن يعلى بن عياش بن يزيد بن شرحبيل بن يزيد بن مهارخسر، عن أبيه يزيد، عن أبيه يعلى، عن أبيه','9327','3269','0');
INSERT INTO [t10490] VALUES (3306,'يزيد أبو عبد الله مجهول','9328','3269','0');
INSERT INTO [t10490] VALUES (3307,'يزيد أبو الحجاج غير منسوب ذكره الحسن بن سفيان في الوحدان، وقال: يزيد القسملي','9330','3269','0');
INSERT INTO [t10490] VALUES (3308,'يزيد أبو عمر غير منسوب ذكره سليمان بن أحمد في الوحدان','9332','3269','0');
INSERT INTO [t10490] VALUES (3309,'يزيد بن حصين الشامي وقيل: ابن عمير، وقيل: ابن نمير، ذكره الحسن بن سفيان، وسليمان بن أحمد في الصحابة','9334','3269','0');
INSERT INTO [t10490] VALUES (3310,'يزيد بن عبد الله الكندي جد يزيد بن خصيفة، روى حديثه: يحيى بن يزيد النوفلي، عن أبيه، عن يزيد بن خصيفة قال: حدثني يزيد بن عبد الله الكندي، عن أبيه، عن جده','9336','3269','0');
INSERT INTO [t10490] VALUES (3311,'ويزيد بن الأسود الجرشي يكنى أبا الأسود، سكن الشام، ذكرهما المتأخر، وقال:، ذكرا في الصحابة، ولا يثبت، ولم يزد على ذكرهما شيئا','9337','3269','0');
INSERT INTO [t10490] VALUES (3312,'يزيد بن كعب البهزي أن عمير بن سلمة الضمري أهدى إلى النبي صلى الله عليه وسلم حمار وحش، ذكره المتأخر، وقال: رواه داود بن رشيد، ووهم فيه','9338','3269','0');
INSERT INTO [t10490] VALUES (3313,'يزيد بن الأصم ابن أخت ميمونة , أبو عوف، سكن الجزيرة، ذكره المتأخر وأخرج له هذا الحديث، وقال: عداده في التابعين','9339','3269','0');
INSERT INTO [t10490] VALUES (3314,'يزيد بن قتادة ذكره سليمان بن أحمد في الوحدان','9341','3269','0');
INSERT INTO [t10490] VALUES (3315,'من اسمه يعلى','9343','3268','0');
INSERT INTO [t10490] VALUES (3316,'يعلى بن أمية التميمي حليف بني نوفل بن عبد مناف، وهو يعلى بن أمية بن أبي عبيدة بن همام بن الحارث بن بكر بن زيد بن مالك بن زيد مناة بن تميم، أمه منية بنت غزوان السلمية , أخت عتبة بن غزوان، حديثه عند: ابنيه صفوان، وعثمان، ومجاهد، وخالد بن دريك،','9344','3315','0');
INSERT INTO [t10490] VALUES (3317,'يعلى بن مرة الثقفي قال محمد بن سعد: هو يعلى بن مرة بن وهب بن جابر بن عتاب بن مالك بن كعب بن عمرو بن سعد بن عوف بن ثقيف، أسلم وشهد مع النبي صلى الله عليه وسلم بيعة الرضوان، والحديبية، وخيبر والفتح، وهوازن، والطائف، كان من أفاضل الصحابة، أمره النبي','9347','3315','0');
INSERT INTO [t10490] VALUES (3318,'باب من اسمه يسار','9353','3268','0');
INSERT INTO [t10490] VALUES (3319,'يسار بن عبد أبو عزة الهذلي وقيل: يسار بن نمير، من بني لحيان بن هذيل، يعد في البصريين، حديثه عند: أبي المليح','9354','3318','0');
INSERT INTO [t10490] VALUES (3320,'يسار أبو ليلى الأنصاري مختلف في اسمه، فقيل: يسار، وقيل: سفيان، وقيل: داود بن بلال بن بليل بن أحيم بن الجلاح الأنصاري قال ابن نمير: اسمه يسار، استشهد بصفين هو وابنه عبد الله، عقبه وحديثه بالكوفة','9358','3318','0');
INSERT INTO [t10490] VALUES (3321,'يسار بن سبع أبو الغادية الجهني، وقيل: المزني حديثه عند كلثوم بن جبر','9361','3318','0');
INSERT INTO [t10490] VALUES (3322,'يسار جد محمد بن إسحاق','9364','3318','0');
INSERT INTO [t10490] VALUES (3323,'يسار أبو مسلم بن يسار وقيل: هو يسار بن سويد الجهني، سكن البصرة، وهو مولى فضالة بن هلال المزني','9366','3318','0');
INSERT INTO [t10490] VALUES (3324,'يسار مولى النبي صلى الله عليه وسلم بعثه راعيا لإبله , فقتله العرنيون وسملوا عينه، فحمل ميتا إلى قباء , فدفن بها','9369','3318','0');
INSERT INTO [t10490] VALUES (3325,'يسار الحبشي كان عبدا لعامر اليهودي , يرعى عليه غنمه، فأسلم لما نزل رسول الله صلى الله عليه وسلم على خيبر، فقاتل معه حتى استشهد بخيبر، فقال النبي صلى الله عليه وسلم: " لقد رأيت عند رأسه زوجتين من الحور العين "','9371','3318','0');
INSERT INTO [t10490] VALUES (3326,'يسار أبو هند حجم النبي صلى الله عليه وسلم، مختلف في اسمه','9373','3318','0');
INSERT INTO [t10490] VALUES (3327,'يسار بن أزيهر الجهني عداده في المدنيين، روت عنه ابنته عمرة، حديثه عند صيفي بن نافع أبي سعد مولى جهينة، عن عمرة بنت يسار بن أزيهر الجهني، عن أبيها قال: " مسح رسول الله صلى الله عليه وسلم على رأسي , وكساني بردين , وأعطاني سيفا "، قالت: فما شاب رأس','9375','3318','0');
INSERT INTO [t10490] VALUES (3328,'*','9376','3268','0');
INSERT INTO [t10490] VALUES (3329,'يسير بن عمرو السكوني الدرمكي من الخضارمة، سكن الكوفة، توفي النبي صلى الله عليه وسلم وله عشر سنين، مات سنة خمس وثمانين','9376','3328','0');
INSERT INTO [t10490] VALUES (3330,'يسير من أصحاب النبي صلى الله عليه وسلم وقيل: أسير، روى عن النبي صلى الله عليه وسلم أنه قال: " الحياء من الإيمان "، ذكره بعض المتأخرين، وقال: روى حديثه أبو عوانة، عن داود الأودي، عن حميد بن عبد الرحمن عنه','9379','3328','0');
INSERT INTO [t10490] VALUES (3331,'ياسر أبو عمار وهو ياسر بن عامر بن مالك بن كنانة بن قيس بن الحصين بن ذؤيب بن ثعلبة بن عوف بن حارثة بن عامر بن تام بن زيد بن مالك بن أدد بن زيد بن يشجب بن عريب بن زيد بن كهلان بن سبأ بن يشجب بن يعرب بن قحطان، نسبه محمد بن سعد الواقدي قال: وبنو مالك بن','9380','3328','0');
