INSERT INTO [t10490] VALUES (3432,'أبو حديدة الجهني، وقيل: ابن حديدة صاحب النبي صلى الله عليه وسلم، ذكره المتأخر، وقال: روى حديثه: ابن أبي ذئب، عن أبي حازم، عن أبي حديدة قال: بعثني عمي بالزوراء، ولم يزد عليه، وقال: قال محمد بن عمرو، عن أبي حازم، عن ابن حديدة، وهو الصواب','9573','3416','0');
