INSERT INTO [t10490] VALUES (4083,'منهن: صفية بنت عبد المطلب أم الزبير بن العوام، كان له صلوات الله عليه ست عمات: البيضاء وهي أم حكيم، وعاتكة، وأميمة، وأروى، وبرة، أمهن فاطمة بنت عمرو بن عائذ بن عمر بن مخزوم، وهي أم عبد الله، وأبي طالب، والزبير، وعبد الكعبة بني عبد المطلب','10931','4082','0');
INSERT INTO [t10490] VALUES (4084,'عاتكة بنت عبد المطلب عمة النبي صلى الله عليه وسلم، صاحبة الرؤيا الدالة على مصاب أهل بدر، روت عنها أم كلثوم بنت عقبة، ذكرها المتأخر في الصحابة','10933','4082','0');
INSERT INTO [t10490] VALUES (4085,'حليمة السعدية أم رسول الله صلى الله عليه وسلم التي أرضعته، وهي بنت أبي ذؤيب عبد الله بن الحارث بن حيان بن سعد بن بكر بن هوازن، وهي أم عبد الله وأنيسة أخوي رسول الله صلى الله عليه وسلم من الرضاعة، كانت تحت الحارث بن عبد العزى بن سعد بن بكر، روى عنها','10935','4082','0');
INSERT INTO [t10490] VALUES (4086,'سائر الصحابيات رضي الله عنهن','10936','4050','0');
INSERT INTO [t10490] VALUES (4087,'باب الألف','10936','4086','0');
INSERT INTO [t10490] VALUES (4088,'أسماء بنت أبي بكر الصديق أم عبد الله بن الزبير، كانت تعرف بذات النطاقين، كانت تحت الزبير بن العوام فولدت له عبد الله، وعروة، والمنذر، ثم طلقها، فكانت عند ابنها عبد الله، كانت أخت عائشة لأبيها، وكانت أسن من عائشة، ولدت قبل التأريخ بسبع وعشرين سنة','10937','4087','0');
INSERT INTO [t10490] VALUES (4089,'أسماء بنت عميس الخثعمية كانت من المهاجرات ممن لها هجرتان: هجرة الحبشة وهجرة بالمدينة، هاجرت مع زوجها جعفر بن أبي طالب، فولدت له بأرض الحبشة عبد الله، وعونا، ومحمدا ابني جعفر بن أبي طالب، ثم قتل عنها جعفر، فخلف عليها أبو بكر الصديق رضي الله عنه،','10946','4087','0');
INSERT INTO [t10490] VALUES (4090,'أسماء بنت يزيد بن السكن الأنصارية وهي بنت عم معاذ بن جبل، قتلت يوم اليرموك تسعة من الروم بعمود فسطاطها، حديثها عن شهر بن حوشب، ومجاهد، ومهاجر الأنصاري، وإسحاق بن راشد، ومحمود بن عمرو','10953','4087','0');
INSERT INTO [t10490] VALUES (4091,'أسماء بنت يزيد الأنصارية من بني عبد الأشهل، وافدة النساء، روى عنها، مسلم بن عبيد، ذكرها المتأخر وأفردها عن المتقدم، وهو عندي المتقدم','10959','4087','0');
INSERT INTO [t10490] VALUES (4092,'أسماء بنت عمرو بن عدي السلمية الأنصارية شهدت النبي صلى الله عليه وسلم بالعقبة','10961','4087','0');
INSERT INTO [t10490] VALUES (4093,'أسماء بنت مرشد أخت بني حارثة ذكرها المتأخر من حديث حرام بن عثمان','10963','4087','0');
INSERT INTO [t10490] VALUES (4094,'أسماء بنت مخرمة التيمية وهي أم الجلاس، وأم عياش، وعبد الله بني أبي ربيعة روى عنها عبد الله بن عباس، والربيع بنت معوذ ابن عفراء، ذكرها المتأخر','10965','4087','0');
INSERT INTO [t10490] VALUES (4095,'أسماء بنت زيد بن الخطاب العدوية لها رواية فيما ذكرها المتأخر، وقال: روى حديثها محمد بن إسحاق، عن محمد بن يحيى بن حيان، عن عبيد الله بن عبد الله بن عمر عنها، ولم يزد على ذلك','10967','4087','0');
INSERT INTO [t10490] VALUES (4096,'أميمة بنت رقيقة التيمية خالة فاطمة بنت النبي صلى الله عليه وسلم، قيل: إنها كانت أخت خديجة لأمها، روى عنها حكيمة ابنتها، ومحمد بن المنكدر، وهي أميمة بنت عبد بن بجاد بن عمير بن الحارث بن حارثة بن سعيد','10968','4087','0');
INSERT INTO [t10490] VALUES (4097,'أميمة بنت رقيقة بنت أبي صيفي بن هاشم بن عبد مناف ورقيقة هي أم مخرمة بن نوفل، صاحبة الرؤيا في استسقاء عبد المطلب مع النبي صلى الله عليه وسلم','10970','4087','0');
INSERT INTO [t10490] VALUES (4098,'أميمة مولاة رسول الله صلى الله عليه وسلم حديثها عن جبير بن نفير','10972','4087','0');
INSERT INTO [t10490] VALUES (4099,'أميمة بنت الحارث امرأة عبد الرحمن بن الزبير، لها ذكر في حديث ابن عباس','10974','4087','0');
INSERT INTO [t10490] VALUES (4100,'أميمة بنت بشر من بني عمرو بن عوف أم عبد الله بن سهل، امرأة سهل بن حنيف، كانت قبل ذلك تحت حسان بن دحداحة، وفيها نزلت: يا أيها الذين آمنوا إذا جاءكم المؤمنات مهاجرات ذكره ابن وهب، عن ابن لهيعة، عن يزيد بن أبي حبيب','10976','4087','0');
INSERT INTO [t10490] VALUES (4101,'أميمة بنت خلف بن أسعد بن عامر بن بياضة الخزاعية امرأة خالد بن سعيد بن العاص، هاجرت إلى أرض الحبشة مع زوجها، وولدت بها لخالد: سعيدا وأمه بنت خالد، ذكره المتأخر عن الزهري، وقال: أميمة، وإنما هي أمينة، أو همينة','10977','4087','0');
INSERT INTO [t10490] VALUES (4102,'أنيسة بنت خبيب بن يساف عمة خبيب بن عبد الرحمن، روى عنها، خبيب بن عبد الرحمن','10978','4087','0');
INSERT INTO [t10490] VALUES (4103,'أنيسة بنت عدي الأنصارية استأذنت النبي صلى الله عليه وسلم في نقل ابنها من أحد إلى المدينة فأذن لها','10980','4087','0');
INSERT INTO [t10490] VALUES (4104,'آسية بنت فرج الجرهمية نزلت الحجون من مكة، ذكرها في حديث عبد الله بن جراد العقيلي','10982','4087','0');
INSERT INTO [t10490] VALUES (4105,'آمة بنت خالد بن سعيد بن العاص الأموي تكنى أم خالد، امرأة الزبير بن العوام، وأمها همينة، وقيل: أمينة روى عنها: موسى بن عقبة، وكريب بن سليم الكندي، وسعيد بن عمرو بن العاص','10984','4087','0');
INSERT INTO [t10490] VALUES (4106,'أمامة بنت أبي العاص بن الربيع أمها زينب بنت رسول الله صلى الله عليه وسلم، تزوج بها علي بن أبي طالب رضي الله عنه بعد موت فاطمة، عن وصية فاطمة له بها، ذكره حجاج بن محمد، عن عبد الله بن جعفر المخرمي، عن جعفر بن محمد، عن أبيه','10987','4087','0');
INSERT INTO [t10490] VALUES (4107,'أمة الله بنت رزينة ذكرها المتأخر، وقال: كانت خادم رسول الله صلى الله عليه وسلم، وقال: رواه محمد بن موسى الجرشي، عن عليلة بنت الكميت ووهم فيه، فإن الصحبة لأمها رزينة، نذكر حديثها في حرف الراء إن شاء الله','10990','4087','0');
INSERT INTO [t10490] VALUES (4108,'أروى بنت ربيعة بن الحارث بن عبد المطلب روى حديثها عطاف بن خالد، عن أمه، عن أمها، وهي أروى، أنها أتت النبي صلى الله عليه وسلم وهي صبية، ذكرها المتأخر، عن عبيد بن محمد، عن عبد القدوس بن إبراهيم، عن عطاف','10991','4087','0');
INSERT INTO [t10490] VALUES (4109,'أروى بنت كريز بن عبد شمس أم عثمان بن عفان، ماتت في خلافة عثمان، لا يعرف لها حديث، ذكرها المتأخر ولم يزد عليه','10992','4087','0');
INSERT INTO [t10490] VALUES (4110,'أروى روت عن النبي صلى الله عليه وسلم في مس الفرج، وقيل: أم أروى، روى هشام بن زياد، عن هشام بن عروة','10993','4087','0');
INSERT INTO [t10490] VALUES (4111,'باب الباء','10995','4086','0');
INSERT INTO [t10490] VALUES (4112,'بسرة بنت صفوان بن أمية وقيل: صفوان بن نوفل بن أسد بن عبد العزى بن قصي، وقال مصعب الزبيري: كانت بسرة تحت المغيرة بن ثابت، وهي من المبايعات روى عنها عبد الله بن عمرو بن العاص، ومروان بن الحكم، وسعيد بن المسيب، وعروة بن الزبير، وأم كلثوم بنت عقبة،','10996','4111','0');
INSERT INTO [t10490] VALUES (4113,'بقيرة امرأة القعقاع بن أبي حدرد الأنصاري','11001','4111','0');
INSERT INTO [t10490] VALUES (4114,'بثينة بنت الضحاك الأنصارية أخت ثابت بن الضحاك، كان محمد بن مسلمة يخطبها، فاختبأ على إجار له فطاردها ببصره، وقيل: نبيتة','11004','4111','0');
INSERT INTO [t10490] VALUES (4115,'برة سماها النبي صلى الله عليه وسلم زينب بنت أبي سلمة بن عبد الأسد، ربيبة النبي صلى الله عليه وسلم','11006','4111','0');
INSERT INTO [t10490] VALUES (4116,'برة بنت أبي تجراة سمعت النبي صلى الله عليه وسلم في المسعى يقول: " كتب عليكم السعي " روت عنها: صفية بنت شيبة، وعميرة بنت عبد الله بن كعب بن مالك','11008','4111','0');
INSERT INTO [t10490] VALUES (4117,'بريرة مولاة عائشة روت عنها عائشة، وابن عباس، وعروة، وعبد الملك بن مروان، وعبد الله بن محيريز','11010','4111','0');
INSERT INTO [t10490] VALUES (4118,'بادية بنت غيلان الثقفية، وقيل: نادية ذكرها المتأخر وقال: نادية وهم، ذكرها في حديث القاسم، عن عائشة','11013','4111','0');
INSERT INTO [t10490] VALUES (4119,'بديلة بنت مسلم بن عميرة بن سليمان الحارثي أدركت النبي صلى الله عليه وسلم فيما ذكرها المتأخر، وقال: روى حديثها الواقدي من حديث جعفر بن محمود بن محمد بن مسلمة، عن جدته أم أبيه بديلة، قالت: جاءنا رجل يقال له: عباد بن بشر من بني حارثة، فقال: إن','11015','4111','0');
INSERT INTO [t10490] VALUES (4120,'بركة الحبشية ذكرها في حديث أميمة بنت رقيقة، هي التي شربت بول النبي صلى الله عليه وسلم، تقدم ذكرها في حديث أميمة','11016','4111','0');
INSERT INTO [t10490] VALUES (4121,'بركة بنت يسار امرأة قيس بن عبد الله الأسدي، مولاة أبي سفيان، هاجرت مع زوجها إلى أرض الحبشة','11017','4111','0');
INSERT INTO [t10490] VALUES (4122,'بهيسة أدركت النبي صلى الله عليه وسلم، حديثها عند سيار بن منظور','11019','4111','0');
INSERT INTO [t10490] VALUES (4123,'بروع بنت واشق الأشجعية لها ذكر في حديث معقل بن سنان في قضية رسول الله صلى الله عليه وسلم لها لما توفي عنها زوجها','11021','4111','0');
INSERT INTO [t10490] VALUES (4124,'البرصاء جدة عبد الرحمن بن أبي عمرة اسمها: كبيشة، وقيل: كبشة، وقيل: أم كبشة روى عنها عبد الرحمن بن أبي عمرة، ذكرها المتأخر','11023','4111','0');
INSERT INTO [t10490] VALUES (4125,'بهية بنت عبد الله البكرية وفدت مع أبيها إلى النبي صلى الله عليه وسلم','11026','4111','0');
INSERT INTO [t10490] VALUES (4126,'باب التاء','11028','4086','0');
INSERT INTO [t10490] VALUES (4127,'تملك الشيبية من بني شيبة بن عبد الدار','11029','4126','0');
INSERT INTO [t10490] VALUES (4128,'تميمة بنت وهب أبي عبيد القرظية مطلقة رفاعة القرظي','11031','4126','0');
INSERT INTO [t10490] VALUES (4129,'تويلة بنت أسلم بايعت النبي صلى الله عليه وسلم، قيل إنها أنصارية من بني حارثة','11033','4126','0');
INSERT INTO [t10490] VALUES (4130,'التوأمة بنت أمية بن خلف لها ذكر لا رؤية لها، ويقال: إنها بايعت النبي صلى الله عليه وسلم','11035','4126','0');
INSERT INTO [t10490] VALUES (4131,'باب الثاء','11037','4086','0');
INSERT INTO [t10490] VALUES (4132,'ثبيتة بنت النعمان بن عمرو بن النعمان بن خالد بن عمرو بن أمية أمها حبيبة بنت قيس من بني غالب بن فهر، تزوجها محمد بن عمرو بن حزم من بني النجار، وأسلمت ثبيتة، وبايعت النبي صلى الله عليه وسلم، فيما ذكره المتأخر، عن محمد بن سعد الواقدي','11038','4131','0');
INSERT INTO [t10490] VALUES (4133,'ثويبة مولاة أبي لهب أرضعت النبي صلى الله عليه وسلم، ذكرها المتأخر، وقال: اختلف في إسلامها. ولا أعلم أحدا أثبت إسلامها غير المتأخر','11039','4131','0');
INSERT INTO [t10490] VALUES (4134,'باب الجيم','11040','4086','0');
INSERT INTO [t10490] VALUES (4135,'جميلة، وقيل: جويرية بنت أبي جهل أدركت النبي صلى الله عليه وسلم، وروت عنه','11041','4134','0');
INSERT INTO [t10490] VALUES (4136,'جميلة بنت ثابت بن أبي الأقلح الأوسي امرأة عمر بن الخطاب، أم عاصم بن عمر','11043','4134','0');
INSERT INTO [t10490] VALUES (4137,'جميلة بنت أبي بن سلول أخت عبد الله، وقيل: ابنة عبد الله، وهو وهم، كانت تحت حنظلة بن أبي عامر، فقتل عنها يوم أحد، ثم خلف عليها ثابت بن قيس بن شماس، فاختلعت منه بحديقته، ثم خلف عليها مالك بن الدخشم من بني عوف بن الخزرج، ثم خلف عليها خبيب بن يساف','11046','4134','0');
INSERT INTO [t10490] VALUES (4138,'جذامة بنت وهب الأسدية روت عنها عائشة أم المؤمنين، وذكر المتأخر أن قيل: خذامة وذكر أن الأول أصح، وحكى وهم واهم فإنه لم يختلف فيه','11048','4134','0');
INSERT INTO [t10490] VALUES (4139,'جذامة بنت الحارث أخت حليمة السعدية، أم النبي صلى الله عليه وسلم، لقبها الشيماء، ذكرها المتأخر، وقال: لا يعرف لها رواية','11050','4134','0');
INSERT INTO [t10490] VALUES (4140,'جمرة بنت قحافة عدادها في الكوفيين، حديثها عند شبيب بن غرقدة','11051','4134','0');
INSERT INTO [t10490] VALUES (4141,'جمرة بنت عبد الله اليربوعية عدادها في أهل الكوفة','11053','4134','0');
INSERT INTO [t10490] VALUES (4142,'جمرة بنت النعمان العدوية','11055','4134','0');
INSERT INTO [t10490] VALUES (4143,'جهدمة امرأة بشير بن الخصاصية لها من النبي صلى الله عليه وسلم رؤية','11057','4134','0');
INSERT INTO [t10490] VALUES (4144,'جسرة بنت دجاجة ذكرها المتأخر، وقال: أدركت وفاة النبي صلى الله عليه وسلم، واستشهد بها','11059','4134','0');
INSERT INTO [t10490] VALUES (4145,'جميلة بنت سعد بن الربيع امرأة زيد بن ثابت روى عنها ثابت بن قيس، فيما ذكرها المتأخر من حديث مسعر، عن ثابت بن عبيد، قال: دخلت على بنت سعد بن الربيع يعني جميلة، وهي امرأة زيد بن ثابت، فقربت إلي رطبا أو تمرا، فقلت لها: أرى هذا أورثت عن أبيك؟ فقالت:','11061','4134','0');
INSERT INTO [t10490] VALUES (4146,'باب الحاء','11063','4086','0');
INSERT INTO [t10490] VALUES (4147,'حليمة بنت أبي ذؤيب السعدية واسم أبي ذؤيب: عبد الله بن الحارث بن حبان بن سعد بن بكر بن هوازن، وهي أم رسول الله صلى الله عليه وسلم التي أرضعته وفصلته، حديثها عند عبد الله بن جعفر بن أبي طالب','11064','4146','0');
INSERT INTO [t10490] VALUES (4148,'حسنة أم شرحبيل ولها ذكر فيمن هاجرت إلى الحبشة','11066','4146','0');
INSERT INTO [t10490] VALUES (4149,'حقة بنت عمرو صلت القبلتين مع النبي عليه السلام','11068','4146','0');
INSERT INTO [t10490] VALUES (4150,'حمنة بنت جحش بن رياب تكنى أم حبيبة، أخت زينب بنت جحش زوج النبي صلى الله عليه وسلم، كانت من المهاجرات، كانت يوم أحد تداوي الجرحى، وتسقي العطشى، كانت تحت طلحة بن عبيد الله، فولدت له عمران بن طلحة','11070','4146','0');
INSERT INTO [t10490] VALUES (4151,'حبيبة بنت سهل كان النبي صلى الله عليه وسلم عزم على تزويجها ثم تركها، فتزوجها ثابت بن قيس بن شماس، روت عنها عمرة','11072','4146','0');
INSERT INTO [t10490] VALUES (4152,'حبيبة بنت شريق أدركت النبي صلى الله عليه وسلم، روت عن بديل بن ورقاء','11075','4146','0');
INSERT INTO [t10490] VALUES (4153,'حبيبة بنت أبي تجراة العبدرية من بني عبد الدار، رأت النبي صلى الله عليه وسلم في المسعى','11077','4146','0');
INSERT INTO [t10490] VALUES (4154,'حبيبة خادمة عائشة حديثها عند محمد بن سيرين','11080','4146','0');
INSERT INTO [t10490] VALUES (4155,'حبيبة بنت زيد بن خارجة بن أبي زهير الخزرجي، زوجة أبي بكر الصديق، لها ذكر ولا يعرف لها رواية','11082','4146','0');
INSERT INTO [t10490] VALUES (4156,'حبيبة بنت أبي أمامة أسعد بن زرارة الأنصاري روت عنها زينب بنت نبيط','11084','4146','0');
INSERT INTO [t10490] VALUES (4157,'حبيبة بنت مليل بن وبرة بن خالد بن العجلان من بني عوف بن الخزرج، بايعت النبي صلى الله عليه وسلم، وتزوجها فروة بن عمرو بن ورقة بن عبيد بن عامر بن بياضة، فولدت له عبد الرحمن','11086','4146','0');
INSERT INTO [t10490] VALUES (4158,'حبيبة بنت مسعود من بني عامر بن زريق، بايعت النبي صلى الله عليه وسلم، لا يعرف لها رواية','11087','4146','0');
INSERT INTO [t10490] VALUES (4159,'حبيبة بنت عمرو بن حصن من بني عامر بن زريق، أسلمت، بايعت النبي صلى الله عليه وسلم، لا يعرف لها رواية، ذكرهن ثلاثتهن المتأخر عن محمد بن منصور البلخي، عن محمد بن سعد الواقدي من قوله، ولم يزد عليه','11088','4146','0');
INSERT INTO [t10490] VALUES (4160,'الحولاء بنت تويت بن أسد بن عبد العزى روت عنها عائشة','11089','4146','0');
INSERT INTO [t10490] VALUES (4161,'الحولاء امرأة عثمان بن مظعون لها ذكر في حديث، ولا يعرف لها رواية، ذكرها المتأخر ولم يزد عليه','11091','4146','0');
INSERT INTO [t10490] VALUES (4162,'حواء أم بجيد كانت من المبايعات من الأنصار، هي بنت زيد بن السكن بن كرز بن زعوراء من بني عبد الأشهل، قاله محمد بن إسحاق، عن عاصم بن عمر بن قتادة، أسلمت قبل زوجها قيس بن الحطيم، وهاجرت، وهي جدة بني بجيد وقيل: هي حواء بنت رافع بن امرئ القيس من بني عبد','11092','4146','0');
INSERT INTO [t10490] VALUES (4163,'حية بنت أبي حية رأت النبي صلى الله عليه وسلم فيما ذكره المتأخر','11099','4146','0');
INSERT INTO [t10490] VALUES (4164,'حميمة بنت صيفي بن صخر من بني كعب بن سلمة، تزوجها البراء بن معرور، ثم خلف عليها زيد بن حارثة، أسلمت وبايعت النبي صلى الله عليه وسلم فيما حكاه المتأخر عن محمد بن منصور، عن محمد بن سعد الواقدي','11101','4146','0');
INSERT INTO [t10490] VALUES (4165,'حزمة بنت قيس أخت فاطمة بنت قيس، امرأة سعيد بن زيد، لها ذكر في حديث الزهري، عن عبيد الله بن عبد الله، ذكرها المتأخر ولم يزد عليه','11102','4146','0');
INSERT INTO [t10490] VALUES (4166,'حبشية العدوية عدي خزاعة، زوجة سفيان بن معمر بن حبيب، من مهاجرة الحبشة، ذكرها المتأخر عن ابن لهيعة، عن أبي الأسود، عن عروة، وهو تصحيف، إنما هي حسنة امرأة سفيان بن معمر بن حبيب الجمحي، كما ذكره ابن إسحاق وغيره، قد تقدم','11103','4146','0');
INSERT INTO [t10490] VALUES (4167,'باب الخاء','11104','4086','0');
INSERT INTO [t10490] VALUES (4168,'خولة بنت قيس بن قهد بن ثعلبة الأنصارية أم محمد، وقيل: أم حبيبة، قتل عنها حمزة بن عبد المطلب، وخلف عليها النعمان بن عجلان الأنصاري، حديثها عند عبيد سنوطا وهو عبيد بن الوليد، ومحمود بن لبيد، ومعاذ بن رفاعة بن رافع، ومحمد بن يحيى بن حيان، وقال علي','11105','4167','0');
INSERT INTO [t10490] VALUES (4169,'خولة بنت قيس أم صبية الجهنية حديثها عند سالم ونافع ابني سرج والنعمان بن خربوذ','11110','4167','0');
INSERT INTO [t10490] VALUES (4170,'خولة بنت حكيم بن أمية بن حارثة بن الأوقص بن مرة بن هلال السلمية، امرأة عثمان بن مظعون، هي التي وهبت نفسها للنبي صلى الله عليه وسلم، روى عنها سعد بن أبي وقاص','11113','4167','0');
INSERT INTO [t10490] VALUES (4171,'خولة بنت حكيم الأنصارية','11118','4167','0');
INSERT INTO [t10490] VALUES (4172,'خولة بنت ثامر الأنصارية','11120','4167','0');
INSERT INTO [t10490] VALUES (4173,'خولة بنت اليمان العبسية، أخت حذيفة وقيل: فاطمة، روى عنها أبو سلمة، وامرأة ربعي بن حراش','11122','4167','0');
INSERT INTO [t10490] VALUES (4174,'خولة الأنصارية المظاهر منها، مختلف في اسمها ونسبها، فقيل: خولة، وقيل: خويلة بنت خويلد، وقيل: بنت مالك بن ثعلبة، وقيل: بنت ثعلبة بن مالك بن الدخشم، وقيل: بنت الصامت كانت تحت أوس بن الصامت أخي عبادة، فظاهر منها فأنزل الله عز وجل فيها حكم الظهار','11125','4167','0');
INSERT INTO [t10490] VALUES (4175,'خولة بنت عاصم امرأة هلال بن أمية، وهي التي قذفها زوجها، وفرق النبي صلى الله عليه وسلم بينهما، لها ذكر من دون الرواية فيما ذكر المتأخر','11130','4167','0');
INSERT INTO [t10490] VALUES (4176,'خولة بنت يسار استفتت النبي صلى الله عليه وسلم في طهارة ثيابها، لها ذكر في حديث أبي هريرة','11131','4167','0');
INSERT INTO [t10490] VALUES (4177,'خولة بنت عبد الله الأنصارية عدادها في البصريين','11134','4167','0');
INSERT INTO [t10490] VALUES (4178,'خولة خادمة رسول الله صلى الله عليه وسلم','11136','4167','0');
INSERT INTO [t10490] VALUES (4179,'خولة، غير منسوبة ذكرها سليمان، وقال: هي غير منسوبة، وخرج لها هذا الحديث وأراها امرأة حمزة','11138','4167','0');
INSERT INTO [t10490] VALUES (4180,'خولة بنت عمرو لها ذكر في حديث عائشة، بعث إليها النبي صلى الله عليه وسلم، استسلف منها','11140','4167','0');
INSERT INTO [t10490] VALUES (4181,'خولة بنت الأسود بن حذافة ومن مهاجرة الحبشة، وقيل: خويلة','11142','4167','0');
INSERT INTO [t10490] VALUES (4182,'خنساء بنت خدام بن خالد الأنصارية ذكرها في حديث أبي هريرة، روى عنها عبد الرحمن، ومجمع، ابنا يزيد','11144','4167','0');
INSERT INTO [t10490] VALUES (4183,'خدامة، وقيل: خذافة بنت جندل الأسدية قاله عروة في رواية المتأخر، عن أبي إسحاق: خذامة','11149','4167','0');
INSERT INTO [t10490] VALUES (4184,'خليدة بنت قعنب الضبية كانت من المبايعات','11151','4167','0');
INSERT INTO [t10490] VALUES (4185,'خالدة بنت أنس الساعدية أم بني حزم','11153','4167','0');
INSERT INTO [t10490] VALUES (4186,'خليسة جارية حفصة زوج النبي صلى الله عليه وسلم روت عنها عليلة بنت الكميت فيما ذكرها المتأخر من حديث جدتها، أن خليسة جارية حفصة حدثتها، أن حفصة وعائشة كانتا جالستين يتحدثان، فأقبلت سودة زوجة النبي صلى الله عليه وسلم، فقالت إحداهما للأخرى: ما ترى سودة','11155','4167','0');
INSERT INTO [t10490] VALUES (4187,'خضرة خادمة رسول الله صلى الله عليه وسلم','11156','4167','0');
INSERT INTO [t10490] VALUES (4188,'خيرة امرأة كعب بن مالك','11158','4167','0');
INSERT INTO [t10490] VALUES (4189,'خيرة بنت حدرد أم الدرداء وقيل: اسمها هجيمة، حديثها عند سهل بن معاذ، عن أبيه، وصفوان بن عبد الله، ويحنس أبي موسى، وعبد الله بن بابا','11160','4167','0');
INSERT INTO [t10490] VALUES (4190,'الخرقاء امرأة كانت سوداء تقم مسجد رسول الله صلى الله عليه وسلم، لها ذكر في حديث حماد بن زيد، عن ثابت، عن أنس','11162','4167','0');
INSERT INTO [t10490] VALUES (4191,'باب الدال','11163','4086','0');
INSERT INTO [t10490] VALUES (4192,'درة بنت أبي لهب بن عبد المطلب واسم أبي لهب: عبد العزى، قدمت المدينة مهاجرة، كانت تحت الحارث بن عبد الله بن نوفل، فولدت له عقبة، والوليد، وأبا مسلم، وقيل: كانت عند دحية بن خليفة الكلبي','11164','4191','0');
INSERT INTO [t10490] VALUES (4193,'درة بنت أبي سلمة بن عبد الأسد ربيبة رسول الله صلى الله عليه وسلم، لها ذكر في حديث أم حبيبة بنت أبي سفيان','11169','4191','0');
INSERT INTO [t10490] VALUES (4194,'دقرة أم ولد أذينة ذكرها سليمان بن أحمد، وقال: يقال لها صحبة، ولم يذكر لها شيئا','11172','4191','0');
INSERT INTO [t10490] VALUES (4195,'باب الذال','11173','4086','0');
INSERT INTO [t10490] VALUES (4196,'ذرة، امرأة من أصحاب النبي صلى الله عليه وسلم غير منسوبة، روى عنها محمد بن المنكدر، وزيد بن أسلم، فيما ذكرها المتأخر وأخرج له','11174','4195','0');
INSERT INTO [t10490] VALUES (4197,'باب الراء','11176','4086','0');
INSERT INTO [t10490] VALUES (4198,'رقية بنت أبي صيفي بن هاشم بن عبد مناف أم مخرمة، وكانت لدة عبد المطلب، ذكرها سليمان بن أحمد فيمن لها صحبة، وما أراها بقيت إلى البعثة والدعوة','11177','4197','0');
INSERT INTO [t10490] VALUES (4199,'رقيقة الثقفية','11179','4197','0');
INSERT INTO [t10490] VALUES (4200,'رائطة بنت سفيان بن الحارث الخزاعية','11181','4197','0');
INSERT INTO [t10490] VALUES (4201,'رائطة امرأة عبد الله بن مسعود وأم ولده، وقيل: إنها بنت عبد الله الثقفية','11183','4197','0');
INSERT INTO [t10490] VALUES (4202,'رائطة بنت منبه بن الحجاج السهمي، أم عبد الله بن عمرو بن العاص، أسلمت وبايعت، لها ذكر في حديث','11186','4197','0');
INSERT INTO [t10490] VALUES (4203,'الربيع بنت النضر أم حارثة عمة أنس بن مالك رضي الله عنهم','11187','4197','0');
INSERT INTO [t10490] VALUES (4204,'الربيع بنت معوذ بن عفراء الأنصارية روى عنها أبو عبيدة بن محمد بن عمار بن ياسر، وعبد الله بن محمد بن عقيل، وأبو سلمة بن عبد الرحمن، وخالد بن ذكوان، وسليمان بن يسار، ومحمد بن عبد الرحمن بن ثوبان، والنعمان بن سالم، أعطاها النبي صلى الله عليه وسلم كفا','11190','4197','0');
INSERT INTO [t10490] VALUES (4205,'الرميصاء، وقيل الغميصاء، وهي أم سليم أم أنس بن مالك، روت عنها عائشة، وأم سلمة، وخولة بنت حكيم، وأنس بن مالك، وأبو أمامة بن سهل','11194','4197','0');
INSERT INTO [t10490] VALUES (4206,'الرميصاء أو الغميصاء شكت زوجها إلى النبي صلى الله عليه وسلم','11196','4197','0');
INSERT INTO [t10490] VALUES (4207,'رميثة الأنصارية جدة عاصم بن عمر بن قتادة','11198','4197','0');
INSERT INTO [t10490] VALUES (4208,'رزينة مولاة صفية زوج النبي صلى الله عليه وسلم حديثها عند ابنتها أمة الله','11201','4197','0');
INSERT INTO [t10490] VALUES (4209,'روضة روت عنها شيبة بنت الأسود','11203','4197','0');
INSERT INTO [t10490] VALUES (4210,'رجاء، غير منسوبة رأت النبي صلى الله عليه وسلم، حديثها عند محمد بن سيرين','11205','4197','0');
INSERT INTO [t10490] VALUES (4211,'باب الزاي','11207','4086','0');
INSERT INTO [t10490] VALUES (4212,'زينب بنت جحش زوجة النبي صلى الله عليه وسلم قد تقدم ذكرها في أزواج النبي صلى الله عليه وسلم','11208','4211','0');
INSERT INTO [t10490] VALUES (4213,'وزينب بنت خزيمة أم المساكين تقدم ذكرها في أزواج النبي صلى الله عليه وسلم','11209','4211','0');
INSERT INTO [t10490] VALUES (4214,'زينب بنت أبي سلمة ابن عبد الأسد المخزومي، ربيبة النبي صلى الله عليه وسلم، كان اسمها برة، فسماها رسول الله صلى الله عليه وسلم زينبا، روى عنها علي بن الحسين، وأبو سلمة بن عبد الرحمن، وعروة بن الزبير','11210','4211','0');
INSERT INTO [t10490] VALUES (4215,'زينب بنت أبي معاوية وقيل: معاوية الثقفية، امرأة عبد الله بن مسعود، روى عنها عبد الله بن مسعود، وأبو هريرة، وأبو سعيد، وعائشة، وبسر بن سعيد','11213','4211','0');
INSERT INTO [t10490] VALUES (4216,'زينب بنت حميد أم عبد الله بن هشام','11218','4211','0');
INSERT INTO [t10490] VALUES (4217,'زينب بنت نبيط بن جابر امرأة أنس بن مالك، قيل: إنها كانت من الأنصار، وقيل: بل من أحمس','11220','4211','0');
INSERT INTO [t10490] VALUES (4218,'زينب بنت جحش مختلف في اسمها، فقيل: حبيبة، وقيل: أم حبيبة، كانت تحت عبد الرحمن بن عوف، ذكرها المتأخر وأخرج لها','11223','4211','0');
INSERT INTO [t10490] VALUES (4219,'زينب الأسدية','11226','4211','0');
INSERT INTO [t10490] VALUES (4220,'زينب بنت قيس بن مخرمة من بني عبد المطلب بن عبد مناف','11228','4211','0');
INSERT INTO [t10490] VALUES (4221,'زينب بنت أبي سفيان بن حرب بن أمية القرشي امرأة عروة بن مسعود الثقفي، روى عنها علقمة بن عبد الله فيما ذكرها المتأخر','11230','4211','0');
INSERT INTO [t10490] VALUES (4222,'زينب بنت أبي رافع ذكرها المتأخر','11232','4211','0');
INSERT INTO [t10490] VALUES (4223,'زنيرة الرومية أسلمت فأصيبت ببصرها، فعيرتها قريش بإسلامها، فرد الله عليها بصرها','11234','4211','0');
INSERT INTO [t10490] VALUES (4224,'باب السين','11236','4086','0');
INSERT INTO [t10490] VALUES (4225,'سهلة بنت سهيل بن عمرو امرأة أبي حذيفة بن عتبة بن ربيعة، ذكرها في حديث عائشة رضي الله عنها','11237','4224','0');
INSERT INTO [t10490] VALUES (4226,'سهلة بنت عاصم بن عدي الأنصارية ولدت يوم حنين فسماها رسول الله صلى الله عليه وسلم سهلة','11242','4224','0');
INSERT INTO [t10490] VALUES (4227,'سهلة بنت سعد الساعدي أخت سهل','11244','4224','0');
INSERT INTO [t10490] VALUES (4228,'سهلة بنت سهل ذكرها سليمان بن أحمد','11246','4224','0');
INSERT INTO [t10490] VALUES (4229,'سبيعة بنت الحارث الأسلمية لها ذكر في حديث أم سلمة، روى عنها ابن عمرو، وأبو سلمة بن عبد الرحمن، وعبد الله بن عتبة','11248','4224','0');
INSERT INTO [t10490] VALUES (4230,'سبيعة بنت أبي لهب ذكرها المتأخر، وصوابه: درة بنت أبي لهب','11251','4224','0');
INSERT INTO [t10490] VALUES (4231,'سبيعة القرشية غير منسوبة، ذكرها المتأخر، وقال: روت عنها عائشة','11252','4224','0');
INSERT INTO [t10490] VALUES (4232,'سبيعة بنت حبيب الضبعي أن رجلا مر بالنبي صلى الله عليه وسلم، فقال رجل: إني أحبه في الله عز وجل. ذكرها المتأخر، وقال: لها ذكر في حديث حماد بن سلمة، عن ثابت','11254','4224','0');
INSERT INTO [t10490] VALUES (4233,'سلمى بنت قيس أم المنذر الأنصارية إحدى خالات النبي صلى الله عليه وسلم، قيل: أو عمته، وقد صلت القبلتين، من المبايعات','11255','4224','0');
INSERT INTO [t10490] VALUES (4234,'سلمى مولاة النبي صلى الله عليه وسلم وخادمته ويقال: إنها مولاة صفية بنت عبد المطلب امرأة أبي رافع، روت عنها عائشة رضي الله عنهما','11257','4224','0');
INSERT INTO [t10490] VALUES (4235,'سلمى بنت عميس الخثعمية أخت أسماء بنت عميس امرأة شداد بن الهاد، إحدى الأخوات المؤمنات','11262','4224','0');
INSERT INTO [t10490] VALUES (4236,'سلمى غير منسوبة، قالت: قال لي النبي صلى الله عليه وسلم: " بعث الله أربعة آلاف نبي " في حديث طويل. رواه محمد بن عقبة، عن وهب بن عبد الله بن كعب، ذكرها المتأخر ولم يزد عليه','11266','4224','0');
INSERT INTO [t10490] VALUES (4237,'سلمى، غير منسوبة قال: ذكرها المتأخر، وقال: روى عنها عبيد الله بن علي، وهي عندي المتقدمة امرأة أبي رافع، وأخرج لها هذا الحديث','11267','4224','0');
INSERT INTO [t10490] VALUES (4238,'سلمى بنت نصر المحاربية ذكرها سليمان بن أحمد، وقال: يقال لها صحبة','11269','4224','0');
INSERT INTO [t10490] VALUES (4239,'سلامة بنت الحر الجعفية قيل: هي أخت خرشة بنت الحر، قاله أبو بكر بن أبي شيبة','11271','4224','0');
INSERT INTO [t10490] VALUES (4240,'سلامة بنت معقل الخزاعية','11274','4224','0');
INSERT INTO [t10490] VALUES (4241,'سلامة الوابشية وهي عندي المتقدمة بنت الحر أخت خرشة، أفردها المتأخر فسماها الوابشية','11276','4224','0');
INSERT INTO [t10490] VALUES (4242,'سلامة حاضنة إبراهيم ابن رسول الله صلى الله عليه وسلم، روى عنها: أنس بن مالك','11278','4224','0');
INSERT INTO [t10490] VALUES (4243,'سودة بنت مسرح، وقيل سوادة حضرت ولادة الحسن والحسين رضي الله عنهما','11280','4224','0');
INSERT INTO [t10490] VALUES (4244,'سهيمة المزنية امرأة ركانة بن عبد يزيد ذكرها المتأخر','11282','4224','0');
INSERT INTO [t10490] VALUES (4245,'سمية بنت خياط أم عمار بن ياسر، مولاة أبي حذيفة بن المغيرة بن عبد الله بن عمر بن مخزوم، كانت من المعذبين في الله، وأول شهيدة استشهدت في الإسلام','11284','4224','0');
INSERT INTO [t10490] VALUES (4246,'سفانة بنت حاتم الطائي أخت عدي بن حاتم الطائي، سبيت فقدم بها على رسول الله صلى الله عليه وسلم المدينة في سبايا من طيئ، فحبسها أياما ثم من عليها بالسلم، وأعطاها نفقة وكسوة وردها إلى مأمنها، وأشارت إلى أخيها عدي بن حاتم بالقدوم على رسول الله صلى الله','11287','4224','0');
INSERT INTO [t10490] VALUES (4247,'سعدى المرية امرأة طلحة بن عبيد الله، وهي سعدى بنت عوف بن خارجة بن سنان، وقيل: بنت عوف بن الحارث، روى عنها: يحيى بن طلحة، وطلحة بن يحيى، وزفر بن عقيل، ومحمد بن عمران بن طلحة','11289','4224','0');
