INSERT INTO [t10490] VALUES (4350,'ليلى بنت أبي حثمة بن حذيفة بن غانم بن عبد الله بن عبيد بن عويج بن عدي بن كعب، أم عبد الله بن عامر بن ربيعة، من المهاجرات الأول، هاجرت مع زوجها عامر بن ربيعة إلى الحبشة','11524','4345','0');
