INSERT INTO [t10490] VALUES (4355,'ميمونة مولاة رسول الله صلى الله عليه وسلم روى عنها: علي بن أبي طالب، وزياد بن أبي سودة، وهي عندي ميمونة بنت سعد، فأفردها المتأخر عن ميمونة بنت سعد','11533','4353','0');
