INSERT INTO [t10490] VALUES (4357,'ميمونة بنت أبي عسيب مولاة النبي صلى الله عليه وسلم، وقال المتأخر: بنت عنبسة، أو أبي عنبسة، وهو تصحيف','11541','4353','0');
