INSERT INTO [t10490] VALUES (4429,'أم الحارث بنت عياش بن أبي ربيعة لها من النبي صلى الله عليه وسلم رؤية، وسمعت بديل بن ورقاء ينادي عن رسول الله صلى الله عليه وسلم بمنى ناهيا عن صوم أيام التشريق','11688','4413','0');
