INSERT INTO [t10490] VALUES (4438,'أم ذر امرأة أبي ذر لها ذكر في وفاة أبي ذر، ذكرها المتأخر، ولم يزد عليه حدثناه أبو عمرو بن حمدان، ثنا الحسن بن سفيان، ثنا عباس بن الوليد، ثنا يحيى بن سليم، ثنا عبد الله بن عثمان بن خثيم، عن مجاهد، عن إبراهيم بن الأشتر، عن أبيه، عن أم ذر، قالت:','11710','4437','0');
INSERT INTO [t10490] VALUES (4439,'باب الراء','11711','4393','0');
