INSERT INTO [t10495] VALUES (1,'مقدمة المؤلف','1','0','0');
INSERT INTO [t10495] VALUES (2,'المهاجرون من الصحابة','66','0','0');
INSERT INTO [t10495] VALUES (3,'أبو بكر الصديق','66','2','0');
INSERT INTO [t10495] VALUES (4,'ومن مفاريد أقواله , لمراعاة أحواله','78','2','0');
INSERT INTO [t10495] VALUES (5,'عمر بن الخطاب قال الشيخ رحمه الله تعالى: وثاني القوم عمر الفاروق ذو المقام الثابت المأنوق، أعلن الله تعالى به دعوة الصادق المصدوق، وفرق به بين الفصل والهزل، وأيد بما قواه به من لوامع الطول، ومهد له من منائح الفضل شواهد التوحيد، وبدد به مواد التنديد','96','2','0');
INSERT INTO [t10495] VALUES (6,'كلماته في الزهد والورع ومن مفاريد أقواله الدالة على حقائق أحواله','131','5','0');
INSERT INTO [t10495] VALUES (7,'عثمان بن عفان وثالث القوم القانت ذو النورين، والخائف ذو الهجرتين، والمصلي إلى القبلتين، هو عثمان بن عفان رضي الله تعالى عنه. كان من الذين آمنوا وعملوا الصالحات ثم اتقوا وآمنوا ثم اتقوا وأحسنوا، فكان ممن هو قانت آناء الليل ساجدا وقائما يحذر الآخرة','163','2','0');
INSERT INTO [t10495] VALUES (8,'علي بن أبي طالب وسيد القوم، محب المشهود، ومحبوب المعبود، باب مدينة العلم والعلوم، ورأس المخاطبات، ومستنبط الإشارات، راية المهتدين، ونور المطيعين، وولي المتقين، وإمام العادلين، أقدمهم إجابة وإيمانا، وأقومهم قضية وإيقانا، وأعظمهم حلما، وأوفرهم','198','2','0');
INSERT INTO [t10495] VALUES (9,'وثيق عباراته ودقيق إشاراته قال أبو نعيم: ومما حفظ عنه من وثيق العبارات، ودقيق الإشارات','244','8','0');
INSERT INTO [t10495] VALUES (10,'وصيته لكميل بن زياد','261','8','0');
INSERT INTO [t10495] VALUES (11,'زهده وتعبده قال الشيخ رحمه الله: ذكر بعض ما نقل عنه من التقلل والتزهد، واشتهر به من الترهب والتعبد. قيل: " إن التصوف السلو عن الأعراض، بالسمو إلى الأغراض "','263','8','0');
INSERT INTO [t10495] VALUES (12,'وصفه في مجلس معاوية','280','8','0');
INSERT INTO [t10495] VALUES (13,'طلحة بن عبيد الله ومن الأعلام الشاهرة، صاحب الأحوال الزاهرة، الجواد بنفسه، الفياض بماله، طلحة بن عبيد الله. قضى نحبه، وأقرض ربه، كان في الشدة والقلة لنفسه بذولا، وفي الرخاء والسعة بماله وصولا. وقد قيل: " إن التصوف النزوح بالأحوال، والتخفف من','291','2','0');
INSERT INTO [t10495] VALUES (14,'الزبير بن العوام قال أبو نعيم: وقرينه الزبير بن العوام، الثابت القوام، صاحب السيف الصارم، والرأي الحازم، كان لمولاه مستكينا، وبه مستعينا، قاتل الأبطال، وباذل الأموال. وقد قيل: " إن التصوف الوفاء والثبات، والتسامح بالمال والجدات "','301','2','0');
INSERT INTO [t10495] VALUES (15,'سعد بن أبي وقاص قال أبو نعيم رحمه الله: وأما سعد بن أبي وقاص فقديم السبق، بدء أمره مقاساة للشدة، واحتمال الضيقة، وهو مع الرسول صلى الله عليه وسلم بمكة هون عليه تحمل الأثقال، ومفارقة العشيرة والمال، لما باشر قلبه من حلاوة الإقبال، ونصر على الأعداء','312','2','0');
INSERT INTO [t10495] VALUES (16,'سعيد بن زيد وأما سعيد بن زيد بن عمرو بن نفيل، فكان بالحق قوالا، ولماله بذالا، ولهواه قامعا وقتالا، ولم يكن ممن يخاف في الله لومة لائم، وكان مجاب الدعوة، سبق الإسلام قبل عمر بن الخطاب رضي الله عنهما، شهد بدرا بسهمه وأجره، رغب عن الولاية، وتشمر في','326','2','0');
INSERT INTO [t10495] VALUES (17,'عبد الرحمن بن عوف وأما عبد الرحمن بن عوف، فكان حاله فيما بسط له حال الأمناء والخزان، يفرقه في سبيل المنعم المنان، يستخير بالله من التفتين فيه والطغيان، وتتصل منه المناحة والأحزان خوف الانقطاع عن إخوته والأخدان، أدرك الودق، وسبق الرنق، كثير الأموال','334','2','0');
INSERT INTO [t10495] VALUES (18,'أبو عبيدة بن الجراح ومنهم الأمين الرشيد، والعامل الزهيد، أمين الأمة أبو عبيدة. كان للأجانب من المؤمنين وديدا، وعلى الأقارب من المشركين شديدا، فيه نزلت: لا تجد قوما يؤمنون بالله واليوم الآخر يوادون من حاد الله ورسوله الآية. صبر على الاقتصار على','347','2','0');
INSERT INTO [t10495] VALUES (19,'عثمان بن مظعون ومنهم المتقشف المحزون، الممتحن في عينه المطعون، ذو الهجرتين عثمان بن مظعون. كان إلى الاستجابة لله سابقا، وبمعالي الأحوال لاحقا، وفي العبادة ناسكا، وفي المحاربة فاتكا، لم تنقصه الدنيا، ولم تحطه عن العليا، تعجل إلى المحبوب، فتسلى','355','2','0');
INSERT INTO [t10495] VALUES (20,'مصعب بن عمير الداري ومنهم مصعب بن عمير الداري، المحب القاري، المستشهد بأحد. كان أول الدعاة، وسيد التقاة، سبق الركب، وقضى النحب، ورغب عن التزييف والتسويف، وغلب عليه الحنين والتخويف. وقد قيل: " إن التصوف طلب التأنيس في رياض التقديس ".','366','2','0');
INSERT INTO [t10495] VALUES (21,'عبد الله بن جحش ومنهم المقسم على ربه، المشمر لحبه، أول من عقدت له الراية في الإسلام عبد الله بن جحش. أمه عمة رسول الله صلى الله عليه وسلم أميمة بنت عبد المطلب، كان من مهاجرة الحبشة ممن شهد بدرا، صاهر رسول الله صلى الله عليه وسلم بأخته زينب بنت جحش.','373','2','0');
INSERT INTO [t10495] VALUES (22,'عامر بن فهيرة ومنهم المشروع رشده، المنزوع حسده، والمرفوع جسده، عامر بن فهيرة. سبق إلى الدعوة، وخدم الرسول صلى الله عليه وسلم وصحبه في الهجرة، وقد قيل: " إن التصوف استطابة الهلك، فيما يخطب من الملك "','377','2','0');
INSERT INTO [t10495] VALUES (23,'عاصم بن ثابت ومنهم الطاهر الزكي، العاهد الوفي، عاصم بن ثابت بن أبي الأقلح الأنصاري. وفى لله تعالى في حياته، فحماه الله تعالى من المشركين بعد وفاته. وقد قيل: " إن التصوف المفر من البينونة، إلى مقر الكينونة "','383','2','0');
INSERT INTO [t10495] VALUES (24,'خبيب بن عدي قال أبو نعيم: ومنهم خبيب بن عدي المصلوب، الثابت الصابر في ذات الله المحبوب. وقد قيل: " إن التصوف إقامة الدنف المعذب، على حفاظ الكلف المهذب "','386','2','0');
INSERT INTO [t10495] VALUES (25,'جعفر بن أبي طالب قال أبو نعيم: ومنهم الخطيب المقدام، السخي المطعام، خطيب العارفين، ومضيف المساكين، ومهاجر الهجرتين، ومصلي القبلتين، البطل الشجاع، الجواد الشعشاع جعفر بن أبي طالب عليه السلام. فارق الخلق، ورامق الحق وقد قيل: " إن التصوف الانفراد','390','2','0');
INSERT INTO [t10495] VALUES (26,'عبد الله بن رواحة الأنصاري ومنهم المتفكر عند نزول الآيات، والمتصبر عند تناول الرايات، عبد الله بن رواحة الأنصاري، استشهد بالبلقاء، زاهدا في البقاء، راغبا في اللقاء. وقد قيل: " إن التصوف الوطء على جمر الغضا، إلى منازل الأنس والرضا "','400','2','0');
INSERT INTO [t10495] VALUES (27,'أنس بن النضر ومنهم أنس بن النضر، المؤيد بالثبات والنصر، المستشهد بأحد بعد تغيبه عن بدر، تنسم بالروائح، فجاد بالجوارح، وفاز بالمنائح. وقد قيل: " إن التصوف استنشاق النسيم، والاشتياق إلى التسنيم','407','2','0');
INSERT INTO [t10495] VALUES (28,'عبد الله ذو البجادين ومنهم الأواه التالي، المتجرد من المعروض الخالي، عبد الله ذو البجادين، المؤاخي للعمرين، وضعه رسول الله صلى الله عليه وسلم في حفرته، وسفح عليه من عبرته','409','2','0');
INSERT INTO [t10495] VALUES (29,'القراء السبعون قال أبو نعيم: قد طوينا ذكر كثير من هذه الطبقة من النساك، والعارفين والعباد، الذين انقرضوا على عهد رسول الله صلى الله عليه وسلم ولم تكلمهم الدنيا، منهم من هو مسمى مذكور، كزيد بن الدثنة المقتول بالرجيع مع أصحابه، وكالمنذر بن عمرو بن','413','2','0');
INSERT INTO [t10495] VALUES (30,'عبد الله بن مسعود ومن طبقة السابقين المهاجرين، المعروفين بالنسك من المعمرين، القارئ الملقن، والغلام المعلم، والفقيه المفهم، صاحب السواد والسرار، والسباق والبدار، أقربهم وسيلة، وأرجحهم فضيلة، كان من الرفقاء، والنجباء، والوزراء، والرقباء. عبد','416','2','0');
INSERT INTO [t10495] VALUES (31,'من وصاياه ومواعظه','465','30','0');
INSERT INTO [t10495] VALUES (32,'عمار بن ياسر ومنهم عمار بن ياسر أبو اليقظان، الممتلئ من الإيمان، والمطمئن بالإيقان، والمتثبت حين المحنة والافتتان، والصابر على المذلة والهوان، من السابقين الأولين، سبق إلى قتال الطغاة زمن النبي صلى الله عليه وسلم، وبقي إلى طعان البغاة مع الوصي،','492','2','0');
INSERT INTO [t10495] VALUES (33,'خباب بن الأرت ومنهم السابق المفتن، المعذب الممتحن، خباب بن الأرت أبو عبد الله مولى بني زهرة. أسلم راغبا، وهاجر طائعا، وعاش مجاهدا، وثبت في إسلامه شاكرا، كان من النواحين البكائين، وكانت نياحته على اكتوائه لما ابتلي في جسمه، وبكاؤه لافتتانه لما','512','2','0');
INSERT INTO [t10495] VALUES (34,'بلال بن رباح ومنهم السيد المتعبد المتجرد، بلال بن رباح، عتيق الصديق ذي الفضل والسماح، علم الممتحنين في الدين والمعذبين، خازن الرسول الأمين محمد سيد المرسلين، السابق الوامق، والمتوكل الواثق. وقد قيل: " إن التصوف قطع العلائق، والأخذ بالوثائق "','529','2','0');
INSERT INTO [t10495] VALUES (35,'صهيب بن سنان بن مالك ومنهم السابق المهاجر، المطعم المتاجر، لماله بذول، ولنفسه قتول، ولدينه عقول، وبربه تعالى يجول ويصول، صهيب بن سنان بن مالك، أسرع الإجابة لله تعالى وللرسول. وقد قيل: " إن التصوف الأخذ بالأصول، والترك للفضول، والتشمير للوصول "','545','2','0');
INSERT INTO [t10495] VALUES (36,'أبو ذر الغفاري ومنهم العابد الزهيد، القانت الوحيد، رابع الإسلام، ورافض الأزلام، قبل نزول الشرع والأحكام، تعبد قبل الدعوة بالشهور والأعوام، وأول من حيا الرسول بتحية الإسلام، لم يكن تأخذه في الحق لائمة اللوام، ولا تفزعه سطوة الولاة والحكام، أول من','561','2','0');
INSERT INTO [t10495] VALUES (37,'مواعظه','598','36','0');
INSERT INTO [t10495] VALUES (38,'عتبة بن غزوان ومنهم الزاهد في الإمرة والسلطان، والتارك لولاية المدن والبلدان، سابع الإسلام والإيمان، أبو عبد الله عتبة بن غزوان، استعفى عن إمرة البصرة بعد أن بنى مسجدها، ونصب منبرها. توفي بالربذة، له الخطبة المشهورة في تولي الدنيا وتصرمها، وفي','632','2','0');
INSERT INTO [t10495] VALUES (39,'المقداد بن الأسود قال الشيخ رحمه الله: ومنهم المقداد بن الأسود، وهو المقداد بن عمرو بن ثعلبة، مولى الأسود بن عبد يغوث، السابق إلى الإسلام، والفارس يوم الحرب والإقدام، ظهرت له الدلائل والأعلام، حين عزم على إسقاء الرسول عليه السلام والإطعام. أعرض','635','2','0');
INSERT INTO [t10495] VALUES (40,'سالم مولى أبي حذيفة ومنهم الحافظ القاري، والإمام الجاري، سالم مولى أبي حذيفة. كان صبا وامقا، وبمودع الكتاب ناطقا، وفي العبادة مخلصا واثقا','649','2','0');
INSERT INTO [t10495] VALUES (41,'عامر بن ربيعة ومنهم أبو عبد الله عامر بن ربيعة، الزاهد في العطايا والقطيعة. شهد بدرا والمشاهد، وعمر بالذكر البقاع والمساجد، تحرز بما أيد به من الفطنة، عن الوقوع فيما امتحن به غيره من الفتنة، عاش كريما، ومضى سليما','656','2','0');
INSERT INTO [t10495] VALUES (42,'ثوبان مولى رسول الله صلى الله عليه وسلم ومنهم القنع العفيف، الوفي الظريف، أبو عبد الله ثوبان مولى رسول الرحمن، المضمون له بالكفالة والضمان، حلول ساحة الجنان، إذ ترك السؤال وإتيان السلطان','666','2','0');
INSERT INTO [t10495] VALUES (43,'رافع مولى النبي صلى الله عليه وسلم ومنهم الشانئ للزائل الدني، والمحب للباقي السني، رافع أبو البهي، مولى النبي، المنتخب الصفي صلى الله عليه وسلم','677','2','0');
INSERT INTO [t10495] VALUES (44,'أسلم أبو رافع ومنهم أسلم أبو رافع مولى رسول الله صلى الله عليه وسلم، أسلم قبل بدر، وكان يكتم إسلامه مع العباس، ثم قدم بكتاب قريش إلى المدينة على رسول الله صلى الله عليه وسلم، وأظهر إسلامه ليقيم بها، فرده رسول الله صلى الله عليه وسلم وقال: " إنا لا','680','2','0');
INSERT INTO [t10495] VALUES (45,'سلمان الفارسي ومنهم سابق الفرس، ورائق العرس، الكادح الذي لا يبرح، والزاخر الذي لا ينزح، الحكيم، والعابد العليم، أبو عبد الله سلمان ابن الإسلام، رافع الألوية والأعلام، أحد الرفقاء والنجباء، ومن إليه تشتاق الجنة من الغرباء، ثبت على القلة والشدائد','684','2','0');
INSERT INTO [t10495] VALUES (46,'أبو الدرداء ومنهم العارف المتفكر، العالم المتذكر، عرف المنعم والنعماء، وتفكر في صنائعه السراء والضراء، وامق العبادة، وفارق التجارة، داوم على العمل استباقا، وأحب اللقاء اشتياقا، تفرغ من الهموم، ففتح له الفهوم. أبو الدرداء صاحب الحكم والعلوم.','760','2','0');
INSERT INTO [t10495] VALUES (47,'معاذ بن جبل ومنهم أبو عبد الرحمن معاذ بن جبل، المحكم للعمل، التارك للجدل، مقدام العلماء، وإمام الحكماء، ومطعام الكرماء، القارئ القانت، المحب الثابت، السهل السري، السمح السخي، المولى المأمون، والوفي المصون، مؤتمن على العباد والأموال، ومصون من','873','2','0');
INSERT INTO [t10495] VALUES (48,'سعيد بن عامر ومنهم سعيد بن عامر بن جذيم الجمحي، زهد في الدنيا الفتانة السحارة، ونظر إلى طلابها بعين الحقارة، وسلك منهج السابقين بالحث والنذارة، ورغب عن الدنيا مع تقلده الولايات وقيامه فيها برعايته العهود والأمانات، وقد قيل: " إن التصوف مصابرة','925','2','0');
INSERT INTO [t10495] VALUES (49,'عمير بن سعد ومنهم عمير بن سعد الحافظ للعهد، الوافي بالوعد، اللقن الحفيظ، الخشن الغليظ، جمال الولاة، وحجة الله على الرعاة، يقال له: نسيج وحده','930','2','0');
INSERT INTO [t10495] VALUES (50,'أبي بن كعب ومنهم المنبئ إذا سئل عن الغامض الصعب، والمذري إذا سما من الشوق والكرب، سيد المسلمين أبي بن كعب.','936','2','0');
INSERT INTO [t10495] VALUES (51,'أبو موسى الأشعري ومنهم العامل المعلم صاحب القراءة والمزمار، الرابض نفسه بالسياحة في المضمار، الأشعري أبو موسى عبد الله بن قيس بن حضار، كان بالأحكام والأقضية عالما، وفي أودية المحبة والمشاهدة هائما، وبقراءة القرآن في الحنادس مترنما وقائما، وفي طول','962','2','0');
INSERT INTO [t10495] VALUES (52,'شداد بن أوس ومنهم ذو اللسان المزموم، والبيان المفهوم، صاحب الحذر والورع، والبكاء والضرع، أبو يعلى شداد بن أوس الأنصاري رضي الله تعالى عنه','994','2','0');
INSERT INTO [t10495] VALUES (53,'حذيفة بن اليمان ومنهم العارف بالمحن وأحوال القلوب، والمشرف على الفتن والآفات والعيوب، سأل عن الشر فاتقاه، وتحرى الخير فاقتناه، سكن عند الفاقة والعدم، وركن إلى الإنابة والندم، وسبق رتق الأيام والأزمان، أبو عبد الله حذيفة بن اليمان. وقد قيل: " إن','1010','2','0');
INSERT INTO [t10495] VALUES (54,'عبد الله بن عمرو بن العاص ومنهم القوي الخاشع، القارئ المتواضع، صاحب الصيام والقيام، عبد الله بن عمرو بن العاص، كان بالحقائق قائلا، وعن الأباطيل مائلا، يعانق العمل، ويفارق الجدل، يطعم الطعام، ويفشي السلام، ويطيب الكلام، وقد قيل: " التصوف','1074','2','0');
INSERT INTO [t10495] VALUES (55,'مواصلته البكاء حتى رمصت عيناه','1097','2','0');
INSERT INTO [t10495] VALUES (56,'اجتماع قراء أهل البصرة في الموسم عليه','1097','55','0');
INSERT INTO [t10495] VALUES (57,'عبد الله بن عمر بن الخطاب','1099','55','0');
INSERT INTO [t10495] VALUES (58,'تعداد المؤلف لمناقبه','1099','57','0');
INSERT INTO [t10495] VALUES (59,'أخباره المسندة في ابتعاده عن الفتنة وعن طلب الخلافة','1099','57','0');
INSERT INTO [t10495] VALUES (60,'كتابة الحجاج له في ذلك ورده عليه','1100','57','0');
INSERT INTO [t10495] VALUES (61,'خبر الحكمين معه في ذلك ورده عليهم','1100','57','0');
INSERT INTO [t10495] VALUES (62,'أخباره في الصدقات وإن ما كان يعجبه من ماله يتقرب به على الله','1101','57','0');
INSERT INTO [t10495] VALUES (63,'عتقه جاريته رميثة لحبه لها','1102','57','0');
INSERT INTO [t10495] VALUES (64,'صدقته في مجلس واحد اثنان وعشرين ألف دينار','1103','57','0');
INSERT INTO [t10495] VALUES (65,'تصدقه بما كان يشتهيه من الطعام','1104','57','0');
INSERT INTO [t10495] VALUES (66,'كان لا يأكل إلا وعلى خوانه مسكين أو يتيم','1106','57','0');
INSERT INTO [t10495] VALUES (67,'أخبار في الزهد في الطعام','1106','57','0');
INSERT INTO [t10495] VALUES (68,'خبر إبله التي استاقها أصحاب نجدة الحروري','1107','57','0');
INSERT INTO [t10495] VALUES (69,'خبره مع خباز بن عامر بن كريز','1108','57','0');
INSERT INTO [t10495] VALUES (70,'اختياره خشن الثياب','1109','57','0');
INSERT INTO [t10495] VALUES (71,'مواظبته على قيام الليل','1110','57','0');
INSERT INTO [t10495] VALUES (72,'عبد الله بن العباس ومنهم اللقن المعلم، والفطن المفهم، فخر الفخار، وبدر الأحبار، وقطب الأفلاك، وعنصر الأملاك، البحر الزخار، والعين الخرار، مفسر التنزيل، ومبين التأويل، المتفرس الحساس، والوضيء اللباس، مكرم الجلاس، ومطعم الأناس، عبد الله بن','1164','2','0');
INSERT INTO [t10495] VALUES (73,'عبد الله بن الزبير ومنهم الصائل بالحق، القائل بالصدق، المحنك بريق النبوة، المبجل لشرف الأمومة والأبوة، المشاهد في القيام، والمواصل للصيام، ذو السيف الصارم، والرأي الحازم، مبارز الشجعان، وحافظ القرآن، التزق بالنبي لزوقا، والتصق بالصديق لصوقا،','1222','2','0');
INSERT INTO [t10495] VALUES (74,'ذكر أهل الصفة','1252','0','0');
INSERT INTO [t10495] VALUES (75,'أوس بن أوس الثقفي وقيل: أوس بن حذيفة، ونسبه إلى أهل الصفة، وهو وهم، فإنه قدم وافدا مع وفد ثقيف على رسول الله صلى الله عليه وسلم في آخر عهده، وهو من المالكين مع الأحلاف الذين أنزلهم النبي صلى الله عليه وسلم القبة لا الصفة. روى عن رسول الله صلى الله','1286','74','0');
INSERT INTO [t10495] VALUES (76,'أسماء بن حارثة وذكر أسماء بن حارثة الأسلمي أخا هند، فكان أبو هريرة يقول: ما كنت أرى أسماء وهندا إلا خادمين لرسول الله صلى الله عليه وسلم من طول لزومهما بابه وخدمتهما له. قال بعض المتأخرين: هو من أهل الصفة','1289','74','0');
INSERT INTO [t10495] VALUES (77,'الأغر المزني وذكر الأغر المزني، ونسب إلى موسى بن عقبة من غير إسناد أنه من أهل الصفة','1292','74','0');
INSERT INTO [t10495] VALUES (78,'بلال بن رباح وذكر بلال بن رباح في أهل الصفة، وقد تقدم ذكرنا له، وأنه كان من السابقين المعذبين في الله عز وجل، خازن النبي صلى الله عليه وسلم','1295','74','0');
INSERT INTO [t10495] VALUES (79,'البراء بن مالك وذكر البراء بن مالك الأنصاري أخا أنس بن مالك، وحكى عن محمد بن إسحاق أنه من أهل الصفة، ولم يذكر إسناده. والبراء شهد أحدا فما دونه من المشاهد، استشهد يوم تستر، وكان طيب القلب، يميل إلى السماع، ويستلذ الترنم، أحد الشجعان والفرسان','1297','74','0');
INSERT INTO [t10495] VALUES (80,'‍‍ثابت بن الضحاك وذكر ثابت بن الضحاك الأنصاري أبا زيد الأشهلي، ونسبه إلى أهل الصفة، وهو من أهل الشجرة، أنصاري الدار، ليس من أهل الصفة بشيء','1303','74','0');
INSERT INTO [t10495] VALUES (81,'ثابت بن وديعة وذكر ثابت بن وديعة الأنصاري، ونسبه إلى أهل الصفة، وإنما نزل الكوفة لا الصفة، وروى له هذا الحديث','1306','74','0');
INSERT INTO [t10495] VALUES (82,'ثقيف بن عمرو وذكر ثقيف بن عمرو بن شميط الأسدي، من حلفاء بني أمية استشهد بخيبر، نسبه إلى أهل الصفة، حكاه عن خليفة بن خياط','1308','74','0');
INSERT INTO [t10495] VALUES (83,'جندب بن جنادة وذكر جندب بن جنادة أبا ذر الغفاري، وقد تقدم ذكرنا له ولحاله ولقدمه، وأنه رابع الإسلام، وأنه كان من قطان مسجد النبي صلى الله عليه وسلم لما قدم المدينة، فكان متوحدا متعبدا، فربما أحدث العهد بأهل الصفة مستأنسا بهم فذكر في جملتهم لهذا','1309','74','0');
INSERT INTO [t10495] VALUES (84,'جرهد بن خويلد وذكر جرهد بن خويلد، وقيل: ابن رزاح الأسلمي، سكن الصفة متطرقا، شهد الحديبية','1312','74','0');
INSERT INTO [t10495] VALUES (85,'جعيل بن سراقة وذكر جعيل بن سراقة الضمري، وسكن الصفة','1314','74','0');
INSERT INTO [t10495] VALUES (86,'جارية بن حميل وذكر جارية بن حميل بن شبة بن قرط، من أهل الصفة، حكاه عن الدارقطني، وذكره عن ابن جرير، أن له صحبة','1317','74','0');
INSERT INTO [t10495] VALUES (87,'حذيفة بن اليمان وذكر حذيفة بن اليمان، خالط أهل الصفة مدة فنسب إليهم، وهو وأبوه من المهاجرين، فخيره النبي صلى الله عليه وسلم بين الهجرة والنصرة، فاختار النصرة، وحالف الأنصار فعد في جملتهم. تقدم ذكرنا له ولأحواله في الطبقة الأولى كان بالفتن والآفات','1318','74','0');
INSERT INTO [t10495] VALUES (88,'حذيفة بن أسيد وذكر حذيفة بن أسيد أبا سريحة الغفاري، من أهل الصفة، شهد الشجرة','1321','74','0');
INSERT INTO [t10495] VALUES (89,'حبيب بن زيد','1324','74','0');
INSERT INTO [t10495] VALUES (90,'حارثة بن النعمان وذكر حارثة بن النعمان الأنصاري النجاري في أهل الصفة، وحكاه عن أبي عبد الرحمن النسائي، وكان من أهل بدر، وأحد الثمانين الذين ثبتوا يوم حنين ولم يفروا، وأصيب ببصره في آخر عمره','1326','74','0');
INSERT INTO [t10495] VALUES (91,'حازم بن حرملة وذكر حازم بن حرملة الأسلمي، ونسبه إلى الصفة، من قبل الحسن بن سفيان.','1329','74','0');
INSERT INTO [t10495] VALUES (92,'حنظلة بن أبي عامر وذكر حنظلة بن أبي عامر الراهب الأنصاري، ونسبه إلى أهل الصفة من قبل أبي موسى محمد بن المثنى، وهو غسيل الملائكة','1331','74','0');
INSERT INTO [t10495] VALUES (93,'حجاج بن عمرو وذكر حجاج بن عمرو الأسلمي، ونسبه إلى أهل الصفة، وأحال به على أبي عبد الله الحافظ، وهو وهم؛ لأن حجاجا الأسلمي هو حجاج بن مالك أبو حجاج بن حجاج، وحجاج بن عمرو هو المازني الأنصاري، ولا يعرف لواحد منهما ذكر في أهل الصفة، وأخرج له هذا','1333','74','0');
INSERT INTO [t10495] VALUES (94,'الحكم بن عمير وذكر الحكم بن عمير الثمالي، ونسبه إلى أهل الصفة سكن الشام.','1335','74','0');
INSERT INTO [t10495] VALUES (95,'حرملة بن إياس وذكر حرملة بن إياس في أهل الصفة، ونسبه إلى خليفة بن خياط. وقيل: هو حرملة بن عبد الله العنبري','1339','74','0');
INSERT INTO [t10495] VALUES (96,'خنيس بن حذافة وذكر خنيس بن حذافة السهمي في أهل الصفة، حكاه عن أبي طالب الحافظ، ومحمد بن إسحاق بن يسار. وخنيس من المهاجرين الأولين، زوجته حفصة بنت عمر، من مهاجرة الحبشة، وشهد بدرا، توفي بالمدينة في أول الإسلام، وتأيمت منه حفصة، وتزوجها رسول الله','1348','74','0');
INSERT INTO [t10495] VALUES (97,'خالد بن زيد وذكر خالد بن زيد أبا أيوب الأنصاري في أهل الصفة، وقال: قاله محمد بن جرير. وأبو أيوب هو صاحب الدار المشهورة التي نزل عليه العلم المنشور رسول الله صلى الله عليه وسلم حين قدم إلى المدينة إلى أن بنى المسجد والحجرة، وداره اليوم أيضا بالمدينة','1350','74','0');
INSERT INTO [t10495] VALUES (98,'خريم بن فاتك وذكر خريم بن فاتك الأسدي من أهل الصفة، ونسبه إلى أحمد بن سليمان المروزي، وخريم شهد بدرا، وهو الذي هتف به الهاتف حين جنه الليل بأبرق العزاف فقال: ويحك عذ بالله ذي الجلال والمجد والبقاء والإفضال واقرأ لآيات من الأنفال ووحد الله ولا تبالي','1355','74','0');
INSERT INTO [t10495] VALUES (99,'خريم بن أوس وذكر خريم بن أوس الطائي في أهل الصفة، ونسبه إلى أبي الحسن علي بن عمر الدارقطني وخريم من المهاجرين، وهو الذي لما أن أخبر النبي أصحابه أن الحيرة رفعت له فرأى الشيماء بنت بقيلة معتجرة بخمار أسود على بغلة شهباء، قال: يا رسول الله، إن نحن','1357','74','0');
INSERT INTO [t10495] VALUES (100,'خبيب بن يساف وذكر خبيب بن يساف بن عتبة أبا عبد الرحمن في أهل الصفة، حكاه عن أبي عبد الله الحافظ النيسابوري، وحكى عن أبي بكر بن أبي داود أنه من أهل بدر','1360','74','0');
INSERT INTO [t10495] VALUES (101,'دكين بن سعيد وذكر دكين بن سعيد المزني، وقيل: الخثعمي، من أهل الصفة، سكن الكوفة، قدم على النبي صلى الله عليه وسلم في أربعمائة نفر يستطعمونه، فأطعمهم وزودهم قال الشيخ رحمه الله: لا أعلم لاستيطانه الصفة ونزولها أثرا صحيحا','1362','74','0');
INSERT INTO [t10495] VALUES (102,'عبد الله ذو البجادين وذكر عبد الله ذا البجادين في أهل الصفة، حكاه عن علي بن المديني. تقدم ذكرنا له في جملة المهاجرين السابقين، وسمي ذا البجادين لأن عمه كان يلي عليه، وهو في حجره بكرمه، فلما أسلم نزع منه كل ما كان عليه، فأبى إلا الإسلام، فأعطته أمه','1364','74','0');
INSERT INTO [t10495] VALUES (103,'رفاعة أبو لبابة وذكر رفاعة أبا لبابة الأنصاري، وقيل: اسمه بشير بن عبد المنذر، من بني عمرو بن عوف، في أهل الصفة، نسبه إلى أبي عبد الله الحافظ النيسابوري. كان رفاعة بدريا بسهمه','1365','74','0');
INSERT INTO [t10495] VALUES (104,'أبو رزين وذكر أبا رزين في أهل الصفة، واستشهد بحديث رواه عمرو بن بكر السكسكي، عن محمد بن زيد، عن أبي سلمة بن عبد الرحمن، عن أبيه، عن النبي صلى الله عليه وسلم، أنه قال لرجل من أهل الصفة، يكنى أبا رزين: " يا أبا رزين، إذا خلوت فحرك لسانك بذكر الله','1367','74','0');
INSERT INTO [t10495] VALUES (105,'زيد بن الخطاب وذكر زيد بن الخطاب في أهل الصفة من قول أبي عبد الله الحافظ، وزيد قتل شهيدا يوم مسيلمة، وشهد بدرا، يكنى أبا عبد الرحمن','1369','74','0');
INSERT INTO [t10495] VALUES (106,'سلمان الفارسي وذكر سلمان الفارسي أبا عبد الله في أصل الصفة، وقد تقدم ذكرنا لبعض أحواله، وأنه كان أحد النجباء والسباق من الغرباء','1372','74','0');
INSERT INTO [t10495] VALUES (107,'سعد بن أبي وقاص وذكر سعد بن أبي وقاص في أهل الصفة مستدلا بقوله: فينا نزلت: ولا تطرد الذين يدعون ربهم بالغداة والعشي، الآية، وقد تقدم ذكرنا له في السابقين المهاجرين، يكنى أبا إسحاق، توفي بالمدينة بالعقيق','1375','74','0');
INSERT INTO [t10495] VALUES (108,'سعيد بن عامر وذكر سعيد بن عامر بن جذيم الجمحي في أهل الصفة، حكاه عن الواقدي، وأنه لا يعلم له دارا بالمدينة. تقدم ذكرنا لحاله وتجرده عن الدنيا، وإيثاره الفقر في جملة المهاجرين','1378','74','0');
INSERT INTO [t10495] VALUES (109,'سفينة أبو عبد الرحمن وذكر سفينة أبا عبد الرحمن مولى رسول الله صلى الله عليه وسلم في أهل الصفة، حكاه عن يحيى بن سعيد القطان، أعتقته أم سلمة على أن يخدم رسول الله صلى الله عليه وسلم ما عاش، فخدمه عشر سنين، وكان بهم خليطا، ولهم أليفا.','1379','74','0');
INSERT INTO [t10495] VALUES (110,'سعد بن مالك وذكر سعد بن مالك أبا سعيد الخدري في أهل الصفة، وقال: قاله أبو عبيد القاسم بن سلام، وحاله قريب من حال أهل الصفة، وإن كان أنصاري الدار لإيثاره التصبر، واختياره للفقر والتعفف','1384','74','0');
INSERT INTO [t10495] VALUES (111,'سالم مولى أبي حذيفة وذكر سالما مولى أبي حذيفة في أهل الصفة. وقد تقدم ذكرنا له، كان ممن استشهد باليمامة، أخذ اللواء بيمينه فقطعت، ثم تناوله بشماله فقطعت، ثم اعتنق اللواء وجعل يقرأ: وما محمد إلا رسول قد خلت من قبله الرسل، أفإن مات أو قتل انقلبتم على','1389','74','0');
INSERT INTO [t10495] VALUES (112,'سالم بن عبيد الأشجعي وذكر سالم بن عبيد الأشجعي، سكن الصفة ثم انتقل إلى الكوفة ونزلها','1391','74','0');
INSERT INTO [t10495] VALUES (113,'سالم بن عمير وذكر سالم بن عمير في أهل الصفة من قبل أبي عبد الله، شهد بدرا من الأوس من بني ثعلبة بن عمرو بن عوف، كان أحد التوابين، فيه وفي أصحابه نزلت: " تولوا وأعينهم تفيض من الدمع','1393','74','0');
INSERT INTO [t10495] VALUES (114,'السائب بن خلاد وذكر السائب بن خلاد في أهل الصفة من قبل أبي عبد الله الحافظ','1395','74','0');
INSERT INTO [t10495] VALUES (115,'شقران مولى رسول الله صلى الله عليه وسلم وذكر شقران مولى رسول الله صلى الله عليه وسلم في أهل الصفة، وقال: قاله جعفر بن محمد الصادق','1397','74','0');
INSERT INTO [t10495] VALUES (116,'شداد بن أسيد وذكر شداد بن أسيد في أهل الصفة، حكاه عمرو بن قيظي بن عامر بن شداد، عن أبيه، عن جده، أنه قدم على النبي صلى الله عليه وسلم فأسكنه الصفة','1399','74','0');
INSERT INTO [t10495] VALUES (117,'صهيب بن سنان وذكر صهيب بن سنان في أهل الصفة، وقال: قاله أبو هريرة. تقدم ذكرنا له في جملة السابقين الأولين','1401','74','0');
INSERT INTO [t10495] VALUES (118,'صفوان بن بيضاء وذكر صفوان بن بيضاء في أهل الصفة، حكاه عن أبي عبد الله الحافظ، وهو أحد بني فهر، شهد بدرا، بعثه النبي صلى الله عليه وسلم في سرية مع عبد الله بن جحش، فنزلت فيهم: إن الذين آمنوا والذين هاجروا وجاهدوا في سبيل الله أولئك يرجون رحمة الله','1403','74','0');
INSERT INTO [t10495] VALUES (119,'طخفة بن قيس وذكر طخفة بن قيس الغفاري في أهل الصفة، سكن المدينة ومات في الصفة','1404','74','0');
INSERT INTO [t10495] VALUES (120,'طلحة بن عمرو وذكر طلحة بن عمرو البصري، نزل الصفة وسكن البصرة','1406','74','0');
INSERT INTO [t10495] VALUES (121,'الطفاوي الدوسي وذكر الطفاوي الدوسي في أهل الصفة، قال: وقاله أبو نضرة','1408','74','0');
INSERT INTO [t10495] VALUES (122,'عبد الله بن مسعود وذكر عبد الله بن مسعود في أهل الصفة، وقال: قاله يحيى بن معين، وقد تقدم ذكرنا لأحواله وبعض أقواله في طبقة السابقين من المهاجرين، وكان سيد من يقول بالأخبار والخصوص، مع متابعته للآثار والنصوص، وكان من المحفوظين من أصحاب رسول الله صلى','1410','74','0');
INSERT INTO [t10495] VALUES (123,'أبو هريرة وذكر عبد شمس، وقيل: عبد الرحمن بن صخر أبا هريرة الدوسي، وهو أشهر من سكن الصفة واستوطنها طول عمر النبي صلى الله عليه وسلم، ولم ينتقل عنها، وكان عريف من سكن الصفة من القاطنين، ومن نزلها من الطارقين كان النبي صلى الله عليه وسلم إذا أراد أن','1415','74','0');
INSERT INTO [t10495] VALUES (124,'عبد الله بن عبد الأسد المخزومي وذكر عبد الله بن عبد الأسد أبا سلمة المخزومي في أهل الصفة وقال: قاله عبد الله بن المبارك، وهو ممن هاجر الهجرتين توفي بعد منصرفه من أحد، انتقض به جرح كان أصابه بأحد فقضى منه','1457','74','0');
INSERT INTO [t10495] VALUES (125,'عبد الله بن حوالة الأزدي وذكر عبد الله بن حوالة الأزدي في أهل الصفة وهو ممن سكن الشام حكاه عن أبي عيسى الترمذي','1459','74','0');
INSERT INTO [t10495] VALUES (126,'عبد الله ابن أم مكتوم وذكر عبد الله ابن أم مكتوم في أهل الصفة وقال: قاله أبو رزين قدم المدينة بعد بدر بيسير فنزل الصفة مع أهلها فأنزله النبي صلى الله عليه وسلم دار الغذاء وهي دار مخرمة بن نوفل وهو الذي نزل فيه: عبس وتولى أن جاءه الأعمى','1461','74','0');
INSERT INTO [t10495] VALUES (127,'عبد الله بن عمرو بن حرام الأنصاري وذكر عبد الله بن عمرو بن حرام الأنصاري السلمي أبا جابر في أهل الصفة وقال: قاله أحمد بن هلال الشطوي، وهو المستشهد بأحد الذي أحياه الله تعالى فكلمه كفاحا، عقبي بدري من النقباء','1463','74','0');
INSERT INTO [t10495] VALUES (128,'عبد الله بن أنيس وذكر عبد الله بن أنيس في أهل الصفة وقال: قاله أبو عبد الله الحافظ النيسابوري وكان من جهينة سكن البادية وكان ينزل في رمضان إلى المدينة ليلة فيسكن المسجد والصفة ليلته، صاحب المخصرة أعطاه النبي صلى الله عليه وسلم مخصرته ليلقاه بها يوم','1465','74','0');
INSERT INTO [t10495] VALUES (129,'عبد الله بن زيد الجهني وذكر عبد الله بن زيد الجهني في أهل الصفة من قبل الحافظ أبي عبد الله النيسابوري وقال الواقدي: كان أحد الأربعة الذين كانوا يحملون ألوية جهينة يوم الفتح توفي في زمن معاوية','1468','74','0');
INSERT INTO [t10495] VALUES (130,'عبد الله بن الحارث بن جزء الزبيدي وذكر عبد الله بن الحارث بن جزء الزبيدي في أهل الصفة انتقل إلى مصر وقيل: إنه ابن أخي محمية بن جزء الزبيدي عمي في آخر أيامه وكان مكفوفا اكتفى عن رؤية الأناس بالأنس بذكر الله وتقديسه','1470','74','0');
INSERT INTO [t10495] VALUES (131,'عبد الله بن عمر بن الخطاب وذكر عبد الله بن عمر بن الخطاب في أهل الصفة من قبل أبي عبد الله النيسابوري الحافظ وذكرنا بعض كلامه وأحواله وأنه كان من أحلاس المسجد يأوي إليه ويسكنه','1473','74','0');
INSERT INTO [t10495] VALUES (132,'عبد الرحمن بن قرط وذكر عبد الرحمن بن قرط عنه','1476','74','0');
INSERT INTO [t10495] VALUES (133,'عبد الرحمن بن جبر بن عمرو وذكر عبد الرحمن بن جبر بن عمرو أبا عبيس الأنصاري الحارثي في أهل الصفة من قبل أبي عبد الله النيسابوري الحافظ','1478','74','0');
INSERT INTO [t10495] VALUES (134,'عتبة بن غزوان وذكر عتبة بن غزوان من قبل محمد بن إسحاق، وعمار بن ياسر من قبل سعيد بن المسيب وعثمان بن مظعون من قبل أبي عيسى الترمذي ونسبهم إلى مساكنة الصفة وقد تقدم ذكرنا لهم ولبعض أحوالهم وأقوالهم في صدر الكتاب وثلاثتهم من سباق المهاجرين وكبرائهم','1480','74','0');
INSERT INTO [t10495] VALUES (135,'عقبة بن عامر الجهني وذكر عقبة بن عامر الجهني في أهل الصفة وكان ممن خالطهم سكن مصر وتوفي بها','1481','74','0');
INSERT INTO [t10495] VALUES (136,'عباد بن خالد الغفاري وذكر عباد بن خالد الغفاري في أهل الصفة حكاه عن الواقدي وقال: هو الذي نزل بالسهم في البئر يوم الحديبية','1486','74','0');
INSERT INTO [t10495] VALUES (137,'عمرو بن عوف المزني وذكر عمرو بن عوف المزني في أهل الصفة من قبل أبي عبد الله الحافظ','1488','74','0');
INSERT INTO [t10495] VALUES (138,'عمرو بن تغلب وذكر عمرو بن تغلب نزل الصفة وسكن البصرة','1492','74','0');
INSERT INTO [t10495] VALUES (139,'عويم بن ساعدة الأنصاري وذكر عويم بن ساعدة الأنصاري في أهل الصفة من قبل أبي عبد الله النيسابوري وهو ممن شهد بدرا من حلفاء بني عمرو بن عوف وقيل: من أنفسهم','1494','74','0');
INSERT INTO [t10495] VALUES (140,'عكاشة بن محصن الأسدي وذكر عكاشة بن محصن الأسدي في أهل الصفة من قبل أبي عبد الله الحافظ وعكاشة قتل يوم بزاخة قتله طليحة في أيام الردة','1500','74','0');
INSERT INTO [t10495] VALUES (141,'العرباض بن سارية وذكر العرباض بن سارية في أهل الصفة وكان من البكائين فيه وفي أصحابه نزلت تولوا وأعينهم تفيض من الدمع حزنا ألا يجدوا ما ينفقون','1502','74','0');
INSERT INTO [t10495] VALUES (142,'عبد الله بن حبشي الخثعمي عبد الله بن حبشي الخثعمي، ذكره أبو سعيد بن الأعرابي','1507','74','0');
INSERT INTO [t10495] VALUES (143,'عتبة بن عبد السلمي وعتبة بن عبد السلمي ذكره أبو سعيد بن الأعرابي في أهل الصفة','1509','74','0');
INSERT INTO [t10495] VALUES (144,'عتبة بن الندر السلمي وعتبة بن الندر السلمي ذكره أبو سعيد بن الأعرابي في أهل الصفة','1512','74','0');
INSERT INTO [t10495] VALUES (145,'عمرو بن عبسة السلمي وعمرو بن عبسة السلمي ذكره أبو سعيد الأعرابي في أهل الصفة','1514','74','0');
INSERT INTO [t10495] VALUES (146,'عبادة بن قرص وعبادة بن قرص وقيل قرط ذكره ابن الأعرابي في أهل الصفة','1517','74','0');
INSERT INTO [t10495] VALUES (147,'عياض بن حمار المجاشعي وعياض بن حمار المجاشعي ذكره أبو سعيد بن الأعرابي في أهل الصفة','1519','74','0');
INSERT INTO [t10495] VALUES (148,'فضالة بن عبيد الأنصاري وفضالة بن عبيد الأنصاري ذكره ابن الأعرابي في أهل الصفة','1522','74','0');
INSERT INTO [t10495] VALUES (149,'فرات بن حيان العجلي وفرات بن حيان العجلي ذكره أبو عبد الرحمن السلمي في أهل الصفة ونسبه إلى سفيان الثوري','1525','74','0');
INSERT INTO [t10495] VALUES (150,'أبو فراس الأسلمي وذكر أبا فراس الأسلمي في أهل الصفة وقال: قاله محمد بن عمرو بن عطاء','1527','74','0');
INSERT INTO [t10495] VALUES (151,'قرة بن إياس المزني وقرة بن إياس المزني أبو معاوية ذكره ابن الأعرابي في أهل الصفة','1529','74','0');
INSERT INTO [t10495] VALUES (152,'كناز بن الحصين وذكر كناز بن الحصين أبا مرثد الغنوي في أهل الصفة ذكره أبو عبد الرحمن السلمي وقال: قاله الواقدي، وأبو عبد الله الحافظ شهد بدرا حليف حمزة بن عبد المطلب','1531','74','0');
INSERT INTO [t10495] VALUES (153,'كعب بن عمرو وذكر كعب بن عمرو أبا اليسر الأنصاري في أهل الصفة من قبل أبي عبد الله الحافظ وهو ممن شهد بدرا','1533','74','0');
INSERT INTO [t10495] VALUES (154,'أبو كبشة مولى رسول الله صلى الله عليه وسلم وذكر أبا كبشة مولى رسول الله صلى الله عليه وسلم في أهل الصفة من قبل أبي عبد الله الحافظ','1536','74','0');
INSERT INTO [t10495] VALUES (155,'مسطح بن أثاثة أبو عباد وذكر مسطح بن أثاثة أبا عباد في أهل الصفة من قبل أبي عبد الله الحافظ، وله ذكر في حديث الإفك وهو الذي كان الصديق ينفق عليه لفقره وقرابته فلما خاض فيما خاض آلى أن لا ينفق عليه " فلما نزلت وليعفوا وليصفحوا ألا تحبون أن يغفر الله لكم','1539','74','0');
INSERT INTO [t10495] VALUES (156,'مسعود بن الربيع القاري وذكر مسعود بن الربيع القاري في أهل الصفة من قبل أبي عبد الله الحافظ','1540','74','0');
INSERT INTO [t10495] VALUES (157,'معاذ أبو حليمة القارئ وذكر معاذا أبا حليمة القارئ في أهل الصفة من قبل أبي عبد الله الحافظ','1542','74','0');
INSERT INTO [t10495] VALUES (158,'واثلة بن الأسقع وذكر واثلة بن الأسقع في أهل الصفة وكان من سكانها قاله الواقدي ويحيى بن معين وقال الواقدي: أسلم واثلة والنبي صلى الله عليه وسلم يتجهز إلى تبوك','1544','74','0');
INSERT INTO [t10495] VALUES (159,'وابصة بن معبد الجهني وذكر وابصة بن معبد الجهني في أهل الصفة قال أيوب بن مكرز: كان وابصة يجالس الفقراء ويقول: هم إخواني على عهد رسول الله صلى الله عليه وسلم ونزل وابصة الرقة وعقبه بها','1549','74','0');
INSERT INTO [t10495] VALUES (160,'هلال مولى المغيرة بن شعبة وذكر هلالا مولى المغيرة بن شعبة','1551','74','0');
INSERT INTO [t10495] VALUES (161,'يسار أبو فكيهة وذكر يسارا أبا فكيهة مولى صفوان بن أمية في أهل الصفة وقد قاله محمد بن إسحاق','1553','74','0');
INSERT INTO [t10495] VALUES (162,'ذكر جماعة من سكان الصفة وقطان المسجد ترك ذكرهم السلمي وابن الأعرابي فمنهم','1556','74','0');
INSERT INTO [t10495] VALUES (163,'بشير ابن الخصاصية وهو بشير بن معبد بن شراحيل بن سبع بن ضبار بن سدوس كان اسمه في الجاهلية نذيرا وقيل: زحم، هاجر إلى النبي صلى الله عليه وسلم فسماه بشيرا وأنزله الصفة','1557','74','0');
INSERT INTO [t10495] VALUES (164,'أبو مويهبة مولى رسول الله صلى الله عليه وسلم وأبو مويهبة مولى رسول الله صلى الله عليه وسلم كان يبيت في المسجد ويخالط أهل الصفة','1559','74','0');
INSERT INTO [t10495] VALUES (165,'أبو عسيب مولى رسول الله صلى الله عليه وسلم وأبو عسيب مولى رسول الله صلى الله عليه وسلم كان يبيت في المسجد ويخالط أهل الصفة','1561','74','0');
INSERT INTO [t10495] VALUES (166,'أبو ريحانة شمعون الأزدي وأبو ريحانة شمعون الأزدي وقيل الأنصاري، كان من الذابين المجتهدين معدود في أهل الصفة','1563','74','0');
INSERT INTO [t10495] VALUES (167,'أبو ثعلبة الخشني وأبو ثعلبة الخشني من عباد الصحابة، له في جملة أهل الصفة ذكر ومدخل','1568','74','0');
INSERT INTO [t10495] VALUES (168,'ربيعة بن كعب الأسلمي وربيعة بن كعب الأسلمي كان من أحلاس المسجد الملازمين لخدمة رسول الله صلى الله عليه وسلم له بأهل الصفة اتصال','1574','74','0');
INSERT INTO [t10495] VALUES (169,'أبو برزة الأسلمي وأبو برزة الأسلمي نضلة بن عبيد من المستهينين بالدنيا المشتهرين بالذكر دخل الصفة ولابس أهلها','1577','74','0');
INSERT INTO [t10495] VALUES (170,'معاوية بن الحكم السلمي ومعاوية بن الحكم السلمي نزل الصفة','1581','74','0');
INSERT INTO [t10495] VALUES (171,'الحسن بن علي فأما السيد المحبب والحكيم المقرب الحسن بن علي رضي الله تعالى عنهما فله في معاني المتصوفة الكلام المشرق المرتب، والمقام المؤنق المهذب وقيل: إن التصوف تنوير البيان وتطهير الأركان','1587','74','0');
INSERT INTO [t10495] VALUES (172,'النساء الصحابيات','1605','0','0');
INSERT INTO [t10495] VALUES (173,'فاطمة بنت رسول الله قال الشيخ رحمه الله: ومن ناسكات الأصفياء وصفيات الأتقياء فاطمة رضي الله تعالى عنها، السيدة البتول البضعة الشبيهة بالرسول ألوط أولاده بقلبه لصوقا وأولهم بعد وفاته به لحوقا كانت عن الدنيا ومتعتها عازفة، وبغوامض عيوب الدنيا وآفاتها','1605','172','0');
INSERT INTO [t10495] VALUES (174,'عائشة زوج رسول الله صلى الله عليه وسلم ومنهم الصديقة بنت الصديق العتيقة بنت العتيق حبيبة الحبيب وأليفة القريب سيد المرسلين محمد الخطيب المبرأة من العيوب المعراة من ارتياب القلوب لرؤيتها جبريل رسول علام الغيوب عائشة أم المؤمنين رضي الله تعالى عنها كانت','1621','172','0');
INSERT INTO [t10495] VALUES (175,'حفصة بنت عمر ومنهن القوامة الصوامة المزرية بنفسها اللوامة حفصة بنت عمر بن الخطاب وارثة الصحيفة الجامعة للكتاب رضي الله تعالى عنها','1651','172','0');
INSERT INTO [t10495] VALUES (176,'زينب بنت جحش ومنهن الخاشعة الراضية الأواهة الداعية زينب بنت جحش رضي الله تعالى عنها','1657','172','0');
INSERT INTO [t10495] VALUES (177,'صفية زوج النبي صلى الله عليه وسلم ومنهن التقية الزاكية ذات العين الباكية صفية الصافية زوجة النبي صلى الله عليه وسلم','1666','172','0');
INSERT INTO [t10495] VALUES (178,'أسماء بنت الصديق ومنهن الصادقة الذاكرة الصابرة الشاكرة أسماء بنت الصديق الشاقة نطاقها لمعصم قربة النبي صلى الله عليه وسلم وعلاقها','1669','172','0');
INSERT INTO [t10495] VALUES (179,'الرميصاء أم سليم ومنهن الرميصاء أم سليم المستسلمة لحكم المحبوب الطاعنة بالخناجر في الوقائع والحروب وقد قيل: إن التصوف مفارقة الدعة والاختيار، ومعانقة الدعة حين البلوى والاختيار','1677','172','0');
INSERT INTO [t10495] VALUES (180,'أم حرام بنت ملحان ومنهن حميدة البر شهيدة البحر التواقة إلى مشاهدة الجنان أم حرام بنت ملحان وقد قيل: إن التصوف البذل والإيثار، والتشرف بخدمة الأخيار','1693','172','0');
INSERT INTO [t10495] VALUES (181,'أم ورقة الأنصارية ومنهن الشهيدة القارئة أم ورقة الأنصارية كانت تؤم المؤمنات المهاجرات ويزورها النبي صلى الله عليه وسلم في الأحايين والأوقات','1698','172','0');
INSERT INTO [t10495] VALUES (182,'أم سليط الأنصارية ومنهن أم سليط الأنصارية الكادحة الغازية، شهدت مع النبي صلى الله عليه وسلم أحدا وكدحت فلم تخف دون الله أحدا','1700','172','0');
INSERT INTO [t10495] VALUES (183,'خولة بنت قيس ومنهن المرأة الصالحة خولة بنت قيس الناصحة','1702','172','0');
INSERT INTO [t10495] VALUES (184,'أم عمارة ومنهن أم عمارة المبايعة بالعقبة المحاربة عن الرجال والشيبة، كانت ذات جد واجتهاد وصوم ونسك واعتماد','1704','172','0');
INSERT INTO [t10495] VALUES (185,'الحولاء بنت تويت ومنهن الحولاء بنت تويت القانتة المهاجرة المتهجدة الثابتة','1708','172','0');
INSERT INTO [t10495] VALUES (186,'أم شريك الأسدية ومنهن أم شريك الأسدية ذات الأحوال المرضية، والآيات المكرمة السنية','1711','172','0');
INSERT INTO [t10495] VALUES (187,'أم أيمن ومنهن أم أيمن المهاجرة الماشية الصائمة الطاوية الناحبة الباكية سقيت من غير راوية شربة سماوية كانت لها شافية كافية','1713','172','0');
INSERT INTO [t10495] VALUES (188,'يسيرة ومنهن يسيرة المهاجرة المسبحة المهللة الذاكرة','1719','172','0');
INSERT INTO [t10495] VALUES (189,'زينب الثقفية ومنهن المتصدقة المصلية زينب الثقفية المتخلية من حليها المتقربة به إلى وليها','1721','172','0');
INSERT INTO [t10495] VALUES (190,'مارية ومنهن خادمة الرسول مارية المجاهدة المطاطية','1725','172','0');
INSERT INTO [t10495] VALUES (191,'عميرة بنت مسعود وأخواتها ومنهن عميرة بنت مسعود وأخواتها','1727','172','0');
INSERT INTO [t10495] VALUES (192,'السوداء ومنهن السوداء مستوطنة المساجد المبرأة عن الظنون في الأندية والمشاهد','1729','172','0');
INSERT INTO [t10495] VALUES (193,'الأنصارية ومنهن المستهينة بالمحن والمصائب المتسلية عن النوازل، والنوائب وقد قيل: إن التصوف الصبر على الرزايا والشكر على المنح والعطايا','1731','172','0');
INSERT INTO [t10495] VALUES (194,'السوداء ومنهن السوداء الممتحنة الصابرة بالبلوى مرتهنة','1733','172','0');
INSERT INTO [t10495] VALUES (195,'أم بجيد الحبيبية ومنهن أم بجيد الحبيبية البذولة المنفقة','1735','172','0');
INSERT INTO [t10495] VALUES (196,'أم فروة ومنهن أم فروة المبايعة المجتهدة المتابعة','1738','172','0');
INSERT INTO [t10495] VALUES (197,'أم إسحاق ومنهن المهاجرة أم إسحاق المثكلة بالوحدة والفراق','1740','172','0');
INSERT INTO [t10495] VALUES (198,'أسماء بنت عميس ومنهن مهاجرة الهجرتين ومصلية القبلتين أسماء بنت عميس الخثعمية المعروفة بالبحرية الحبشية أليفة النجائب وكريمة الحبائب، عقد عليها جعفر الطيار وخلف عليها بعده الصديق سابق الأخيار ومات عنها الوصي علي سيد الأبرار','1742','172','0');
INSERT INTO [t10495] VALUES (199,'أسماء بنت يزيد ومنهن الأنصارية أسماء بنت يزيد بن السكن النابذة لما يورث الغرور والفتن','1747','172','0');
INSERT INTO [t10495] VALUES (200,'أم هانئ الأنصارية ومنهن الأنصارية أم هانئ السائلة عن التزاور بعد التفاني','1751','172','0');
INSERT INTO [t10495] VALUES (201,'سلمى بنت قيس ومنهن المصلية للقبلتين المحافظة على البيعتين سلمى بنت قيس النجارية','1753','172','0');
INSERT INTO [t10495] VALUES (202,'فمن الطبقة الأولى من التابعين','1761','0','0');
INSERT INTO [t10495] VALUES (203,'أويس بن عامر القرني سيد العباد وعلم الأصفياء من الزهاد أويس بن عامر القرني بشر النبي صلى الله عليه وسلم به وأوصى به أصحابه','1762','202','0');
INSERT INTO [t10495] VALUES (204,'عامر بن عبد قيس ومنهم المضر بلذيذ العيش عامر بن عبد الله بن عبد قيس المراقب المستحي السالم المستضيء وقد قيل: إن التصوف انتصاب الارتقاء، وارتقاء الالتقاء','1781','202','0');
INSERT INTO [t10495] VALUES (205,'مسروق قال الشيخ رحمه الله تعالى: ومنهم العالم بربه الهائم بحبه الذاكر لذنبه، في العلم معروق وبالضمان موثوق، ولعباد الله معشوق أبو عائشة المسمى بمسروق، وهو مسروق بن عبد الرحمن الهمداني الكوفي وقيل: التصوف التشمر للورود، واللحوق والتبصر في الوجود','1809','202','0');
INSERT INTO [t10495] VALUES (206,'علقمة بن قيس النخعي ومنهم العالم الرباني علقمة بن قيس النخعي أبو شبل الهمداني، أوتي فقها وعبادة وحسن تلاوة وزهادة','1830','202','0');
INSERT INTO [t10495] VALUES (207,'الأسود بن يزيد النخعي ومنهم القارئ القوام الساري الصوام الفقيه الأثير، الفقير الأسير، الأسود بن يزيد النخعي','1860','202','0');
INSERT INTO [t10495] VALUES (208,'أبو يزيد الربيع بن خثيم ومنهم المخبت الورع المتثبت القنع الحافظ لسره، والضابط لجهره المعترف بذنبه المفتقر إلى ربه أبو يزيد الربيع بن خثيم أحد الثمانية من الزهاد وقد قيل: إن التصوف مشارفة السرائر ومصارفة الظواهر','1877','202','0');
INSERT INTO [t10495] VALUES (209,'هرم بن حيان ومنهم الهائم الحيران القائم العطشان هرم بن حيان عاش في حبه ولهان حرقا وعاد قبره حين دفن ريان غدقا وقد قيل: إن التصوف الاحتراق حذار الافتراق، والاشتياق لدار الاستباق','1946','202','0');
INSERT INTO [t10495] VALUES (210,'أبو مسلم الخولاني ومنهم المتخلي عن الهموم والكرب، المتسلي بالأوراد والنوب الخولاني أبو مسلم عبد الله بن ثوب حكيم الأمة وممثلها ومديم الخدمة ومحررها وقد قيل: إن التصوف التخلي عن المنقضي الفاني، والمتسلي بالمتحدي الباقي','1964','202','0');
INSERT INTO [t10495] VALUES (211,'الحسن البصري ومنهم حليف الخوف والحزن أليف الهم والشجن عديم النوم والوسن أبو سعيد الحسن بن أبي الحسن، الفقيه الزاهد المتشمر العابد، كان لفضول الدنيا وزينتها نابذا، ولشهوة النفس ونخوتها واقذا وقد قيل: إن التصوف التنقية من الدرن والتوقية من البدن','1997','202','0');
INSERT INTO [t10495] VALUES (212,'سعيد بن المسيب فأما أبو محمد سعيد بن المسيب بن حزن المخزومي كان من الممتحنين امتحن فلم تأخذه في الله لومة لائم صاحب عبادة وجماعة وعفة وقناعة وكان كاسمه بالطاعات سعيدا ومن المعاصي والجهالات بعيدا وقد قيل: إن التصوف التمكن من الخدمة والتحفظ للحرمة','2104','202','0');
INSERT INTO [t10495] VALUES (213,'عروة بن الزبير ومنهم المعطي ما تمنى حمل العلم عنه إذ فيه تعنى مكن من الطاعة فاكتسب وامتحن بالمحنة فاحتسب، عروة بن الزبير بن العوام المجتهد المتعبد الصوام وقد قيل: إن التصوف عرفان المنن وكتمان المحن','2178','202','0');
INSERT INTO [t10495] VALUES (214,'القاسم بن محمد بن أبي بكر ومنهم الفقيه الورع الشفيق الضرع نجل الصديق ذو الحسب العتيق القاسم بن محمد بن أبي بكر الصديق، كان لغوامض الأحكام فائقا وإلى محاسن الأخلاق سابقا قد قيل: إن التصوف الصفو للزيق والرقو للفيق','2212','202','0');
INSERT INTO [t10495] VALUES (215,'أبو بكر بن عبد الرحمن ومنهم الفقيه الوجيه العابد النبيه راهب قريش وعابدها أبو بكر بن عبد الرحمن بن الحارث بن هشام المخزومي، أكثر حديثه في الأقضية والأحكام','2231','202','0');
INSERT INTO [t10495] VALUES (216,'عبيد الله بن عتبة ومنهم عبيد الله بن عبد الله بن عتبة بن مسعود الهذلي أحد الأربعة من البحور، المواصل الرواح بالبكور المنابذ للدنيا خيفة الغرة والعثور','2236','202','0');
INSERT INTO [t10495] VALUES (217,'خارجة بن زيد ومنهم الفقيه ابن الفقيه خارجة بن زيد بن ثابت الأنصاري كان من عباد المدينة ممن تفقه ثم انفرد وآثر العزلة ولم ينشر عنه من كلامه كبير شيء عامة حديثه في الأقضية والأحكام فمما أسنده ما','2244','202','0');
INSERT INTO [t10495] VALUES (218,'سليمان بن يسار ومنهم العابد المجار المعصوم حين الفتنة من الفجار وأبو أيوب سليمان بن يسار','2247','202','0');
INSERT INTO [t10495] VALUES (219,'سالم بن عبد الله ومنهم الفقيه المتخشع الرهاب أبو عمر سالم بن عبد الله بن عمر بن الخطاب كان لله خاشعا وفي نفسه خاضعا وبما يدفع به وقته قانعا وقد قيل: إن للتصوف لزوم الخضوع والقنوع والتصبر من الجزوع والهلوع','2254','202','0');
INSERT INTO [t10495] VALUES (220,'مطرف بن عبد الله ومنهم المتعبد الشكير مطرف بن عبد الله بن الشخير، كان لنفسه مذلا ولذكر الله عز وجل مجلا وقد قيل: إن التصوف إدمان الإذلال والأعمال، وإيثار الإقلال والإخمال','2272','202','0');
INSERT INTO [t10495] VALUES (221,'يزيد بن عبد الله ومنهم أبو العلاء يزيد بن عبد الله بن الشخير أخو مطرف له في العبادة ذكر مشهور وكلامه إن قل مذكور فمما حفظ عنه قيل له: " ألا نسقف مسجدنا قال: أصلحوا قلوبكم يكفكم مسجدكم " وكان يقول: " إن صاحب النار الذي لا تمنعه مخافة الله من شيء خفي له','2349','202','0');
INSERT INTO [t10495] VALUES (222,'صفوان بن محرز ومنهم المتعبد البكاء المتوحد الدعاء صفوان بن محرز المازني','2355','202','0');
INSERT INTO [t10495] VALUES (223,'أبو العالية ومنهم ذو الأحوال السامية والأعمال الخافية رفيع أبو العالية كانت وصاياه في لزوم الاتباع، وعهوده في مجانبة الإحداث والابتداع، وقد قيل: إن التصوف الرضا بالقسمة والسخاء بالنعمة','2370','202','0');
INSERT INTO [t10495] VALUES (224,'بكر بن عبد الله المزني قال الشيخ رحمه الله: ومنهم الناصح الذكي الواثق الغني بكر بن عبد الله المزني','2405','202','0');
INSERT INTO [t10495] VALUES (225,'خليد بن عبد الله العصري ومنهم الذاكر الفكري خليد بن عبد الله العصري كان لمحبوبه ذاكرا وإلى مشاهدته ساهرا','2438','202','0');
INSERT INTO [t10495] VALUES (226,'مورق العجلي ومنهم المستسلم المتسلي مورق بن مشمرخ العجلي، كان بالحق عن الخلق ساليا، وبالشهود عن الصدود ساهيا','2450','202','0');
INSERT INTO [t10495] VALUES (227,'صلة بن أشيم العدوي ومنهم أبو الصهباء صلة بن أشيم العدوي، المنتصح بكتاب الله والمتحبب إلى عباد الله كان عند النوازل محتسبا صابرا، وفي الحنادس منتصبا ذاكرا وقد قيل: إن التصوف شدة الانتصاب، والاكتساب برؤية الاحتساب والارتقاب','2467','202','0');
INSERT INTO [t10495] VALUES (228,'العلاء بن زياد ومنهم المبشر المحزون المستتر المخزون تجرد من التلاد وتشمر للمهاد وقدم العتاد للميعاد واعتزل عن العباد العلاء بن زياد وقد قيل: إن التصوف الارتياد والاجتهاد لذل الانقياد في عز الاعتماد','2486','202','0');
INSERT INTO [t10495] VALUES (229,'أبو السوار العدوي ومنهم أبو السوار العدوي بالقلب، زوار وفي الوجه خوار وبالوصل فخار، وبالنفس ضرار. وقد قيل: إن التصوف الهيمان في الوجد والهيجان في الود','2510','202','0');
INSERT INTO [t10495] VALUES (230,'حميد بن هلال العدوي ومنهم حميد بن هلال العدوي تفقه واعتزل وعلم واشتغل، له في العلم الحظ الجزيل وفي التحقيق السمت الجميل','2521','202','0');
INSERT INTO [t10495] VALUES (231,'الأسود بن كلثوم ومنهم المستشهد الملثوم الأسود بن كلثوم خلصت دعوته فعجلت كرامته','2533','202','0');
INSERT INTO [t10495] VALUES (232,'شويس بن حياش ومن مشيخة بني عدي شويس بن حياش أبو الرقاد ولد عام الهجرة فأدرك عهد النبي صلى الله عليه وسلم وأخذ العطاء من عمر بن الخطاب رضي الله تعالى عنه','2535','202','0');
INSERT INTO [t10495] VALUES (233,'عبد الله بن غالب ومنهم العابد الرائب المتشمر الناحب المتشوق الطالب أبو فراس عبد الله بن غالب وقيل: إن التصوف الحذر من الدنيا والهرب، والرغب في العقبى والطلب','2540','202','0');
INSERT INTO [t10495] VALUES (234,'زرارة بن أوفى ومنهم الخائف المخفي زرارة بن أوفى رق فأوحى ورد إلى الملأ الأعلى وقيل: إن التصوف عويل حتى الرحيل وحويل إلى المقيل','2550','202','0');
INSERT INTO [t10495] VALUES (235,'عقبة بن عبد الغافر ومنهم الداعي الشاكر عقبة بن عبد الغافر، كان في الضراء ذاكرا وفي السراء شاكرا','2561','202','0');
INSERT INTO [t10495] VALUES (236,'ابن سيرين ومنهم ذو العقل الرصين والورع المتين، المطعم للإخوان والزائرين، ومعظم الرجاء للمذنبين والموحدين، أبو بكر محمد بن سيرين كان ذا ورع وأمانة وحيطة وصيانة، كان بالليل بكاء نائحا وبالنهار بساما سائحا يصوم يوما ويفطر يوما وقيل: إن التصوف البذل','2568','202','0');
INSERT INTO [t10495] VALUES (237,'عبد الله بن زيد الجرمي ومنهم اللبيب الناصح والخطيب الفاصح، كثر إشفاقه فكثر إنفاقه أبو قلابة عبد الله بن زيد الجرمي وقيل: إن التصوف النصح في الإشفاق، والفسح في الأخلاق','2682','202','0');
INSERT INTO [t10495] VALUES (238,'مسلم بن يسار ومنهم الشاهد المبصار المجاهد المحضار أبو عبد الله مسلم بن يسار وقيل: إن التصوف التمتع بالحضور والتتبع للخطور','2723','202','0');
INSERT INTO [t10495] VALUES (239,'معاوية بن قرة ومنهم البسام بالنهار والبكاء في الأسحار أبو إياس معاوية بن قرة','2764','202','0');
INSERT INTO [t10495] VALUES (240,'أبو رجاء العطاردي ومنهم ذو العمر المعمر، والحبر المحبر، والبر المبشر أبو رجاء العطاردي، أدرك أول دعوة الرسول فأجاب إلى التصديق والقبول وثبت على الإقبال والوصول وقيل: إن التصوف قبول الرسول للتوسل إلى الوصول','2792','202','0');
INSERT INTO [t10495] VALUES (241,'أبو عمران الجوني ومنهم الواعظ اليقظان موقظ الوسنان ومنفر الشيطان الجوني أبو عمران وقد قيل: إن التصوف التيقظ والانتباه والتبصر في دفع التوهم والاشتباه','2814','202','0');
INSERT INTO [t10495] VALUES (242,'ثابت البناني ومنهم المتعبد الناحل المتهجد الذابل أبو محمد ثابت بن أسلم البناني وقيل: إن التصوف محافظة الحرمة ومداومة الخدمة','2854','202','0');
INSERT INTO [t10495] VALUES (243,'قتادة بن دعامة ومنهم الحافظ الرغاب الواعظ الرهاب قتادة بن دعامة أبو الخطاب وقد قيل: إن التصوف المراعاة والاحتفاظ والمعاناة والاتعاظ','2926','202','0');
INSERT INTO [t10495] VALUES (244,'محمد بن واسع ومنهم العامل الخاشع والخامل الخاضع أبو عبد الله محمد بن واسع كان لله عاملا وفي نفسه خاملا وقيل: إن التصوف الخشوع والخمول والقنوع والذبول','2978','202','0');
INSERT INTO [t10495] VALUES (245,'مالك بن دينار ومنهم العارف النظار الخائف الجآر أبو يحيى مالك بن دينار، كان لشهوات الدنيا تاركا، وللنفس عند غلبتها مالكا. وقيل: إن التصوف تدلل وافتخار وتذلل وافتقار','3043','202','0');
INSERT INTO [t10495] VALUES (246,'أيوب السختياني ومنهم فتى الفتيان، سيد العباد والرهبان، المنور باليقين والإيمان، السختياني أيوب بن كيسان، فقيها محجاجا وناسكا حجاجا، عن الخلق آيسا وبالحق آنسا','3219','202','0');
INSERT INTO [t10495] VALUES (247,'يونس بن عبيد ومنهم الورع السديد والضرع الشديد ذو الكلام الموزون واللسان المخزون أبو عبد الله يونس بن عبيد','3296','202','0');
INSERT INTO [t10495] VALUES (248,'سليمان بن طرخان ومنهم المتعبد المتهجد المتثبت المتشدد أبو المعتمر سليمان بن طرخان. وقيل: إن التصوف اغتنام الوقت، التزام السمت','3358','202','0');
INSERT INTO [t10495] VALUES (249,'عبد الله بن عون ومنهم الحافظ للسانه، الضابط لأركانه، ذو القلب السليم، والطريق المستقيم، عبد الله بن عون، كان للقرآن تاليا، وللجماعة مواليا، وعن أعراض المسلمين عافيا، وقيل: إن التصوف بذل الندى وحمل الأذى','3409','202','0');
INSERT INTO [t10495] VALUES (250,'فرقد السبخي ومنهم المعرض عن الفاني الوبي، المقبل على الآتي البهي أبو يعقوب فرقد السبخي وقيل: إن التصوف طرح التلهي والتمني، والجد في اللحوق والتلقي','3452','202','0');
INSERT INTO [t10495] VALUES (251,'يزيد بن أبان الرقاشي ومنهم الصالح الباكي، والصائم الظامي، يزيد بن أبان الرقاشي. وقيل: إن التصوف تحمل للتخفف وتذيل للتشرف','3480','202','0');
INSERT INTO [t10495] VALUES (252,'هارون بن رباب الأسدي ومنهم المخفي لزهده والموفي بعهده هارون بن رباب الأسدي','3500','202','0');
INSERT INTO [t10495] VALUES (253,'منصور بن زاذان ومنهم زين القراء والفتيان، الميسر له تلاوة القرآن، منصور بن زاذان','3512','202','0');
INSERT INTO [t10495] VALUES (254,'بديل بن ميسرة ومنهم المخلص العابد المجتهد الزاهد بديل بن ميسرة العقيلي','3537','202','0');
INSERT INTO [t10495] VALUES (255,'طلق بن حبيب ومنهم الوفي النجيب، المتعبد اللبيب طلق بن حبيب','3544','202','0');
INSERT INTO [t10495] VALUES (256,'يحيى بن أبي كثير ومنهم الراوي الخبير، الواعي البصير، أبو نصر يحيى بن أبي كثير، كان ذا بصر وهدى واجتهاد وتقى','3558','202','0');
INSERT INTO [t10495] VALUES (257,'مطر الوراق ومنهم العالم المشفاق، والعامل المنفاق، أبو رجاء مطر الوراق','3609','202','0');
INSERT INTO [t10495] VALUES (258,'أوس بن عبد الله ومنهم المجانب للأهواء والآراء، المفارق للتلاعن والأسواء، أوس بن عبد الله أبو الجوزاء','3624','202','0');
INSERT INTO [t10495] VALUES (259,'يزيد بن حميد الضبعي ومنهم المتعبد السياح والمتعوذ الصياح يزيد بن حميد الضبعي أبو التياح','3644','202','0');
INSERT INTO [t10495] VALUES (260,'جابر بن زيد قال الشيخ: ومنهم المتخلي بعلمه عن الشبه، والظلماء، والمتسلي بذكره في الوعورة والوعثاء جابر بن زيد أبو الشعثاء، كان للعلم عينا معينا، وفي العبادة ركنا مكينا، وكان إلى الحق آيبا، ومن الخلق هاربا، تأخر ذكره عن طبقته وهو من قدماء التابعين','3653','202','0');
INSERT INTO [t10495] VALUES (261,'داود بن أبي هند ومنهم العالم المثبت، والزاهد المخبت، داود بن أبي هند','3689','202','0');
INSERT INTO [t10495] VALUES (262,'المنذر بن مالك ومنهم مفيض الدموع والعبرة، ومبيد البيوع والحبرة، المنذر بن مالك أبو نضرة من متقدمي طبقة أهل البصرة. وقيل: إن التصوف التحفظ من العثرة، والتيقظ من الفترة','3714','202','0');
INSERT INTO [t10495] VALUES (263,'بكر بن عمرو ومنهم الداعي بالتحقيق، الناجي أبو الصديق، اسمه بكر بن عمرو، كان في العبادة سابقا، وفي اللياذة صادقا','3731','202','0');
INSERT INTO [t10495] VALUES (264,'الفضيل بن زيد الرقاشي ومنهم حارس الأوقات، وغارس الأقوات بالتنصل من الحوبات، أبو حسان الفضيل بن زيد الرقاشي، من متقدمي التابعين، وعباد أهل البصرة، غزا في أيام عمر بن الخطاب غزوات.','3737','202','0');
INSERT INTO [t10495] VALUES (265,'قسامة بن زهير ومنهم المقتصر على الفلق والكسير، والمستتر بالخرق والحصير، أبو المنهال قسامة بن زهير. وقيل: إن التصوف انتقاض الدوير، واعتراض على الغوير.','3742','202','0');
INSERT INTO [t10495] VALUES (266,'أبو الحلال العتكي ومنهم العتكي أبو الحلال، المحفوظ من الكسل والملال، كان ذا قوة في العبادة، وتقشف وزهادة.','3749','202','0');
INSERT INTO [t10495] VALUES (267,'ميمون بن سياه ومنهم المعرض عن الشنآن والعصيان، المقبل على ذكر المنعم المحسان، ميمون بن سياه بن ميمون.','3756','202','0');
INSERT INTO [t10495] VALUES (268,'الحجاج بن الفرافصة ومنهم المأخوذ عن العاجلة، المردود إلى الآجلة، الحجاج بن الفرافصة.','3764','202','0');
INSERT INTO [t10495] VALUES (269,'إياس بن قتادة التميمي ومنهم المستقيل آثامه، المتدارك أيامه، المستأنس بوحدته، المعتبر بشيبته، إياس بن قتادة التميمي ابن أخت الأحنف بن قيس، كان قاضيا لبني تميم.','3776','202','0');
INSERT INTO [t10495] VALUES (270,'أبو الأبيض ومنهم المتبع للأوجب الأفرض، المفارق للأنزر الأرمض، العابد المكنى بأبي الأبيض','3779','202','0');
INSERT INTO [t10495] VALUES (271,'لاحق بن حميد ومنهم الفقيه السديد، العابد الرشيد، أبو مجلز لاحق بن حميد.','3782','202','0');
INSERT INTO [t10495] VALUES (272,'حسان بن أبي سنان ومنهم حافظ الطرف واللسان، رابط القلب والجنان، حسان بن أبي سنان.','3797','202','0');
INSERT INTO [t10495] VALUES (273,'عاصم بن سليمان الأحول ومنهم العابد الأفضل، عاصم بن سليمان الأحول.','3822','202','0');
INSERT INTO [t10495] VALUES (274,'إياس بن معاوية ومنهم صاحب الحكم والأحكام الماضية، المكنى أبا واثلة، إياس بن معاوية.','3832','202','0');
INSERT INTO [t10495] VALUES (275,'شميط بن عجلان ومنهم الوامق الولهان، الواعظ اليقظان، أبو همام شميط بن عجلان، وقيل: أبو عبيد الله.','3846','202','0');
INSERT INTO [t10495] VALUES (276,'زين العابدين علي بن الحسين فمن هذه الطبقة علي بن الحسين بن علي بن أبي طالب رضي الله تعالى عنهم، زين العابدين، ومنار القانتين، كان عابدا وفيا، جوادا حفيا. وقيل: إن التصوف حفظ الوفاء، وترك الجفاء.','3887','202','0');
INSERT INTO [t10495] VALUES (277,'محمد بن المنكدر ومنهم الناظر المعتبر، والذاكر المعتذر، أبو عبد الله محمد بن المنكدر، اعتذر فابتدر، واعتبر فانشمر وقيل: إن التصوف اعتبار في انشمار، واعتذار في ابتدار','3937','202','0');
INSERT INTO [t10495] VALUES (278,'صفوان بن سليم ومنهم المجتهد الوفي المتعبد السخي صفوان بن سليم الزهري، كان ذا جهد واغتناء، وورد واعتناء، وقيل: إن التصوف بذل الغنى لحفظ الوفا وحمل الضنا لترك الجفا','4000','202','0');
INSERT INTO [t10495] VALUES (279,'عامر بن عبد الله ومنهم الداعي العامل، الخافي العاقل، عامر بن عبد الله بن الزبير، كان لمشهوده عاملا، ولمشروعه عاقلا، وقيل: إن التصوف الإكباب على العمل، والإعراض عن العلل','4034','202','0');
INSERT INTO [t10495] VALUES (280,'سعد بن إبراهيم الزهري ومنهم فقيه العصر، وصائم الدهر، المتعبد القارئ، الكاسي العاري، سعد بن إبراهيم الزهري','4048','202','0');
INSERT INTO [t10495] VALUES (281,'محمد ابن الحنفية ومنهم الإمام اللبيب ذو اللسان، الخطيب، الشهاب الثاقب والنصاب العاقب، صاحب الإشارات الخفية والعبارات الجلية، أبو القاسم محمد ابن الحنفية','4069','202','0');
INSERT INTO [t10495] VALUES (282,'محمد بن علي الباقر ومنهم الحاضر الذاكر الخاشع الصابر، أبو جعفر محمد بن علي الباقر، كان من سلالة النبوة، وممن جمع حسب الدين والأبوة، تكلم في العوارض والخطرات، وسفح الدموع والعبرات، ونهى عن المراء والخصومات. وقيل: إن التصوف التعزز بالحضرة،','4091','202','0');
INSERT INTO [t10495] VALUES (283,'جعفر بن محمد الصادق ومنهم الإمام الناطق، ذو الزمام، السابق، أبو عبد الله جعفر بن محمد الصادق، أقبل على العبادة، والخضوع، وآثر العزلة والخشوع، ونهى عن الرئاسة والجموع، وقيل: إن التصوف انتفاع بالسبب، وارتفاع في النسب','4148','202','0');
INSERT INTO [t10495] VALUES (284,'علي بن عبد الله بن العباس ومنهم ناسك النساك وقمر الأفلاك وعنصر الأملاك علي بن عبد الله بن العباس','4199','202','0');
INSERT INTO [t10495] VALUES (285,'محمد بن كعب القرظي ومنهم المنفر عن دار الغرور والكرب، والمبشر بما يعقب تحمل النفور والصعب، والقرظي أبو حمزة محمد بن كعب','4213','202','0');
INSERT INTO [t10495] VALUES (286,'زيد بن أسلم ومنهم الحليم الأحلم، والسليم الأسلم أبو أسامة زيد بن أسلم، كان بالعدل قائلا، وبالفضل عاملا، وعن الجهل، عادلا','4249','202','0');
INSERT INTO [t10495] VALUES (287,'سلمة بن دينار ومنهم ذو الهم العازم، والخوف اللازم، سلمة بن دينار أبو حازم كان للغوامض فاتقا، وللعوراض رامقا، وبمعبوده عمن سواه واثقا. وقيل: إن التصوف وثوق بالمعبود، ومروق عن الصدود','4278','202','0');
INSERT INTO [t10495] VALUES (288,'ربيعة بن أبي عبد الرحمن ومنهم صاحب المعارف والبيان، والمحارف والقربان، ربيعة بن أبي عبد الرحمن أبو عثمان','4389','202','0');
INSERT INTO [t10495] VALUES (289,'عبيد بن عمير ومن تابعي أهل مكة الواعظ الصغير العابد الضمير، أبو عاصم عبيد بن عمير، كان بذكر الله لهجا، وبنعم الله عليه بهجا، وعن ذكر من سوى الله حرجا، وقيل: إن التصوف اغتنام الذكر، واكتتام السر','4417','202','0');
INSERT INTO [t10495] VALUES (290,'مجاهد بن جبر ومنهم العالم الحبر ذو الأحلام والصبر، أبو الحجاج مجاهد بن جبر، صاحب التأويل والتفسير، والأقاويل والتذكير. وقيل: إن التصوف إعلام عن أعلام، وإحكام لأحكام','4472','202','0');
INSERT INTO [t10495] VALUES (291,'عطاء بن أبي رباح ومنهم فقيه الحرم والبطاح، مفترش الجنبين والطراح، أبو محمد عطاء بن أبي رباح وقد قيل: إن التصوف سماح لرباح، واطراح لاستراح','4641','202','0');
INSERT INTO [t10495] VALUES (292,'عكرمة مولى ابن عباس ومنهم مفسر الآيات المحكمة، ومنور الروايات المبهمة، أبو عبد الله مولى ابن عباس عكرمة، كان في البلاد جوالا، ومن علمه للعباد بذالا وقيل: إن التصوف التحصيل للأصول، ثم التنبيه للعقول، والتعليم للجهول','4704','202','0');
INSERT INTO [t10495] VALUES (293,'أخباره في التفسير','4729','292','0');
INSERT INTO [t10495] VALUES (294,'عمرو بن دينار ومنهم الفقيه المتشدد، والمتعبد المتهجد، عمرو بن دينار أبو محمد.','4795','202','0');
INSERT INTO [t10495] VALUES (295,'عبد الله بن عبيد بن عمير ومنهم عبد الله بن عبيد بن عمير.','4827','202','0');
INSERT INTO [t10495] VALUES (296,'الزهري ومنهم العالم السوي، والراوي الروي، أبو بكر محمد بن مسلم بن شهاب الزهري، كان ذا عز وسناء، وفخر وسخاء. وقيل: إن التصوف دراية وصدق، وسخاوة وخلق.','4846','202','0');
INSERT INTO [t10495] VALUES (297,'طاوس بن كيسان ومنهم المتفقه اليقظان والمتعبد المحسان أبو عبد الرحمن طاوس بن كيسان أول الطبقة من أهل اليمن الذين قال فيهم النبي صلى الله عليه وسلم: " الإيمان يمان "','4936','202','0');
INSERT INTO [t10495] VALUES (298,'وهب بن منبه ومنهم الحكيم الدامغ للمشبه، الحليم الدافع للمتسفه: أبو عبد الله وهب بن منبه','5044','202','0');
INSERT INTO [t10495] VALUES (299,'ميمون بن مهران ومنهم الحكيم اليقظان أبو أيوب ميمون بن مهران إمام أهل الجزيرة، حميد السيرة، سديد السريرة. وقيل: إن التصوف اعتقال السريرة واحتمال الجريرة','5225','202','0');
INSERT INTO [t10495] VALUES (300,'يزيد بن الأصم ومنهم المنيب الأقوم يزيد بن الأصم','5317','202','0');
INSERT INTO [t10495] VALUES (301,'شقيق بن سلمة فمنهم الواله الذابل، المجتهد الناحل، شقيق بن سلمة، أبو وائل.','5335','202','0');
INSERT INTO [t10495] VALUES (302,'خيثمة بن عبد الرحمن ومنهم المطعم للإخوان، والمكرم للخلان: خيثمة بن عبد الرحمن. كان بالنعم واثقا، وللقائه تائقا. وقيل: إن التصوف الانتفاء من الأعراض للابتغاء من الأعواض','5390','202','0');
INSERT INTO [t10495] VALUES (303,'الحارث بن سويد ومنهم الحارث بن سويد التيمي أبو عائشة، كان على وقته شحيحا، وبالإغضاء عن اللاهين نجيحا','5452','202','0');
INSERT INTO [t10495] VALUES (304,'الحارث بن قيس الجعفي ومنهم الحارث بن قيس الجعفي.','5470','202','0');
INSERT INTO [t10495] VALUES (305,'شريح بن الحارث الكندي ومنهم شريح بن الحارث الكندي أبو أمية القاضي. كان من حاله التسليم والتراضي، والقيام على نفسه بالمحاسبة والتقاضي. وقيل: إن التصوف الحنين إلى الباقي، والأنين من الماضي','5472','202','0');
INSERT INTO [t10495] VALUES (306,'عمرو بن شرحبيل قال الشيخ رحمه الله: ومنهم العارف بالسبيل، العازم على الرحيل: أبو ميسرة عمرو بن شرحبيل','5508','202','0');
INSERT INTO [t10495] VALUES (307,'عمرو بن ميمون ومنهم عمرو بن ميمون الأودي، المتحمل للعناء، المتشوق للقاء، كان للحياة مستبقا، وللعبادة معتنقا','5533','202','0');
INSERT INTO [t10495] VALUES (308,'عمرو بن عتبة قال الشيخ رحمه الله تعالى: ومنهم المجاب المستشهد عمرو بن عتبة ابن فرقد، كان مظللا محروسا، وبالبلاء مكللا ممسوسا','5563','202','0');
INSERT INTO [t10495] VALUES (309,'معضد أبو زيد العجلي ومنهم المتعبد المجتهد الشاهد المستشهد أبو زيد العجلي معضد','5579','202','0');
INSERT INTO [t10495] VALUES (310,'شبيل بن عوف ومنهم أخيذ الحذر والخوف، وحفيظ النظر والجوف الأحمسي شبيل بن عوف','5585','202','0');
INSERT INTO [t10495] VALUES (311,'مرة بن شراحيل ومنهم المدمن للتعبد، والمواظب على التهجد، المنقبض عن الهزل والأباطيل، المحصن لسانه في الفتن عن الأقاويل: الطيب أبو إسماعيل مرة بن شراحيل حدثنا أبو حامد بن جبلة، ثنا محمد بن إسحاق، قال: سمعت العباس بن محمد يقول: سمعت يحيى بن معين','5592','202','0');
INSERT INTO [t10495] VALUES (312,'زيد بن وهب','5628','202','0');
INSERT INTO [t10495] VALUES (313,'سويد بن غفلة وأما أبو أمية سويد بن غفلة، فكان الأذان والصلاة عمله، وبلغ من أقصى السن أمله، ولم تخرج الفتن عقله ولا جهله','5643','202','0');
INSERT INTO [t10495] VALUES (314,'همام بن الحارث النخعي ومنهم المتعبد القوام، المتلذذ بالسهر للذكر همام، وهو همام بن الحارث النخعي','5660','202','0');
INSERT INTO [t10495] VALUES (315,'كردوس بن هانئ ومنهم كردوس بن هانئ قيل: ابن عياش الثعلبي وقيل: ابن عمرو يعرف بالقاص، كان يقص على التابعين','5668','202','0');
INSERT INTO [t10495] VALUES (316,'زر بن حبيش ومنهم الوافد الغادي، الذاكر في النادي، وفد ليتعلم، وغزا ليغنم: زر بن حبيش أبو مريم، تحمل الكلال طلبا للكمال، فحفظ من الملال، وثبت في الوصال. وقيل: إن التصوف التحمل للكلال، والتحرز من الملال، والترويح بالوصال','5674','202','0');
INSERT INTO [t10495] VALUES (317,'أبو عبد الرحمن السلمي ومنهم ذو الصيام والقيام، مقرئ الأئمة والأعلام على مدى السنين والأعوام، في التعبد لبيب، وفي التعليم أريب: أبو عبد الرحمن السلمي عبد الله بن حبيب','5710','202','0');
INSERT INTO [t10495] VALUES (318,'زياد بن جرير الأسلمي قال الشيخ رضى الله تعالى عنه: ومنهم معظم الأمانة، ومنظم الديانة، الفقيه التقي، العامل الوفي: زياد بن جرير الأسلمي','5723','202','0');
INSERT INTO [t10495] VALUES (319,'زاذان أبو عمرو الكندي قال الشيخ رحمه الله تعالى: ومنهم الناصح المجاب، والرابح المثاب: زاذان أبو عمرو الكندي','5739','202','0');
INSERT INTO [t10495] VALUES (320,'أبو عبيدة بن عبد الله بن مسعود قال الشيخ رحمه الله تعالى: ومنهم الذاكر الشاكر أبو عبيدة بن عبد الله بن مسعود','5760','202','0');
INSERT INTO [t10495] VALUES (321,'يزيد بن شريك التيمي وابنه إبراهيم ومنهم يزيد بن شريك التيمي وابنه إبراهيم','5786','202','0');
INSERT INTO [t10495] VALUES (322,'إبراهيم بن يزيد النخعي ومنهم التقي الحفي، الفقيه الرضي: إبراهيم بن يزيد النخعي. كان للعلوم جامعا، ومن نخوة النفوس واضعا، وعن المتواضعين رافعا. وقيل: إن التصوف الرفع للأذلاء والمتواضعين، والوضع من الأجلاء والمتكبرين','5831','202','0');
INSERT INTO [t10495] VALUES (323,'عون بن عبد الله بن عتبة قال الشيخ رحمه الله تعالى: ومنهم الراكن إلى ذكر الله، والساكن إلى ضمان الله، المفارق للمثرين والكبراء، المرافق للمساكين والفقراء، كان لمسير الأجل مبصرا، ولغرور الأمل محذرا، كان على نفسه نائحا، وإلى الحق رائحا، صاحب','5954','202','0');
INSERT INTO [t10495] VALUES (324,'سعيد بن جبير قال الشيخ رحمه الله تعالى: ومنهم الفقيه البكاء، والعالم الدعاء، السعيد الشهيد، السديد الحميد: أبو عبد الله جبير بن سعيد. وقيل: إن التصوف التحقق في التوكل، والتشوق في التنقل','6059','202','0');
INSERT INTO [t10495] VALUES (325,'آثاره في التفسير','6122','324','0');
INSERT INTO [t10495] VALUES (326,'عامر بن شراحيل الشعبي قال الشيخ رضي الله تعالى عنه: ومنهم الفقيه القوي، سالك السمت المرضي، بالعلم الواضح المضي والحال الزكي الوضي: أبو عمرو عامر بن شراحيل الشعبي. كان بالأوامر مكتفيا، وعن الزواجر منتهيا، تاركا لتكلف الأثقال، معتنقا لتحمل الواجب','6217','202','0');
INSERT INTO [t10495] VALUES (327,'عمرو بن عبد الله السبيعي قال الشيخ رضي الله تعالى عنه ومنهم المعمر الثابت، المشمر القانت، تبصر فعقل، وتصبر ففعل: أبو إسحاق عمرو بن عبد الله السبيعي. وقيل: إن التصوف تصبر واحتمال، وتشمر واعتمال','6334','202','0');
INSERT INTO [t10495] VALUES (328,'عبد الرحمن بن أبي ليلى قال الشيخ رضي الله تعالى عنه ومنهم الفقيه المرتضى، والحكم المبتلى: أبو عيسى عبد الرحمن بن أبي ليلى. امتحن بالحكم والقضاء، فابتلي بالندم والبكاء. وقيل: إن التصوف اصطبار في البلاء لانتظار الانجلاء.','6398','202','0');
INSERT INTO [t10495] VALUES (329,'عبد الله بن أبي الهذيل قال الشيخ رضي الله تعالى عنه: ومنهم مغتنم الساعات، ومكتتم الطاعات: عبد الله بن أبي الهذيل أبو المغيرة','6418','202','0');
INSERT INTO [t10495] VALUES (330,'أبو صالح الحنفي ماهان قال الشيخ رحمه الله تعالى ومنهم الكلف بالمحامد والأذكار، والمبتلى في إظهاره على الظلمة الإنكار: أبو صالح الحنفي ماهان وقيل: إن اسمه عبد الرحمن بن قيس أخو طليق','6446','202','0');
INSERT INTO [t10495] VALUES (331,'ربعي بن خراش قال الشيخ رحمه الله تعالى ومنهم المفارق للبزة والرياش، والمهاجر للوطاء والفراش، العابد العبسي: ربعي بن خراش','6462','202','0');
INSERT INTO [t10495] VALUES (332,'موسى بن طلحة التيمي قال الشيخ رحمه الله: ومنهم الفصيح الفقيه التقي: موسى بن طلحة بن عبيد الله التيمي، كان فقيها كاملا، وتقيا عاملا','6473','202','0');
INSERT INTO [t10495] VALUES (333,'ميمون بن أبي شبيب قال الشيخ رحمه الله تعالى: ومنهم العفيف اللبيب، الفقيه الأديب: أبو نصر ميمون بن أبي شبيب، قتل يوم الجماجم','6488','202','0');
INSERT INTO [t10495] VALUES (334,'سعيد بن فيروز أبو البختري قال الشيخ رحمه الله تعالى: ومنهم الطاعن عن الممتري، الخارج على المفتري: سعيد بن فيروز أبو البختري. خرج مع القراء على الحجاج المفتري، فقتل بدير الجماجم مع القراء يوم عبد الرحمن بن محمد بن الأشعث','6503','202','0');
INSERT INTO [t10495] VALUES (335,'محمد بن سوقة قال الشيخ رحمه الله تعالى ورضي الله عنه: ومنهم الخائف المعظم، العاطف المقدم، عرف فعظم، وعطف فقدم، أبو عبد الله بن سوقة. وقيل: إن التصوف تعظيم عن تخويف، وتقديم لتخفيف','6527','202','0');
INSERT INTO [t10495] VALUES (336,'طلحة بن مصرف قال الشيخ: ومنهم الورع الكلف، القارئ الدنف، أبو محمد طلحة بن مصرف، كان ذا صدق ووفاء، وخلق وصفاء، وقيل: إن التصوف صدق في الخفاء، وخلق للوفاء حدثنا أحمد بن جعفر بن حمدان، قال: ثنا عبد الله بن أحمد بن حنبل، قال: ثنا أبو سعيد الأشج،','6572','202','0');
INSERT INTO [t10495] VALUES (337,'زبيد بن الحارث الإيامي قال الشيخ رحمه الله: ومنهم ذو الخشية والمهابة، والتوكل والقناعة، كان بالدنيا وعروضها مستهينا، وللقرآن وفروضه مستبينا، أبو عبد الرحمن زبيد بن الحارث الأيامي. وقيل: إن التصوف العزم على التخشع والتذلل، واللزوم للتوقع والتوكل','6636','202','0');
INSERT INTO [t10495] VALUES (338,'منصور بن المعتمر قال الشيخ رحمه الله ومنهم حليف الصيام والقيام، خفيف التطعم والمنام، المتفكر المعتبر أبو غياث منصور بن المعتمر','6683','202','0');
INSERT INTO [t10495] VALUES (339,'سليمان الأعمش ومنهم الإمام المقرئ، الراوي المفتي، كان كثير العمل، قصير الأمل، من ربه راهبا ناسكا، ومع عباده لاعبا ضاحكا، سليمان بن مهران الأعمش، وقيل: إن التصوف موافقة الحق ومضاحكة الخلق','6718','202','0');
INSERT INTO [t10495] VALUES (340,'حبيب بن أبي ثابت قال الشيخ رحمه الله تعالى: ومنهم المتع‍بد المنفاق، المتوكل على المولى الرزاق، مطعم القراء، ومعلم السفهاء، حبيب بن أبي ثابت، تواضع فارتفع، وتطاوع فانتفع','6792','202','0');
INSERT INTO [t10495] VALUES (341,'عبد الرحمن بن أبي نعم قال الشيخ رحمه الله تعالى: ومنهم الوافد المواصل، العابد العامل، عبد الرحمن بن أبي نعم، واصل ليصل، وعامل ليقبل','6829','202','0');
INSERT INTO [t10495] VALUES (342,'خلف بن حوشب قال الشيخ: ومنهم ذو السمت المهذب، والكلام المحبب، أبو عبد الرحمن خلف بن حوشب','6844','202','0');
INSERT INTO [t10495] VALUES (343,'الربيع بن أبي راشد قال الشيخ رحمه الله: ومنهم الحاضر الشاهد الذاكر الواجد الربيع بن أبي راشد','6855','202','0');
INSERT INTO [t10495] VALUES (344,'ذكر جماعة من تابعي التابعين من أهل الكوفة والمعدودين فيهم','6872','202','0');
INSERT INTO [t10495] VALUES (345,'كرز بن وبرة الحارثي فمنهم كرز بن وبرة الحارثي، كان يسكن جرجان، كوفي الأصل، له الصيت البليغ، والمكان الرفيع في النسك والتعبد كما كان يغلب عليه المؤانسة والمشاهدات، فيشهده شتى الملاطفات، ويؤنسه خفي المخاطبات وقيل: إن التصوف النزوح بالاستيناس،','6873','202','0');
INSERT INTO [t10495] VALUES (346,'عبد الملك بن أبجر قال الشيخ رضي الله تعالى عنه: ومنهم المتقي الأنور، الباكي الأغزر، عبد الملك بن سعيد بن أبجر','6897','202','0');
INSERT INTO [t10495] VALUES (347,'عبد الأعلى التيمي قال الشيخ رحمه الله تعالى: ومنهم ذو الخشوع الغيبي، والدموع السيبي، عبد الأعلى التيمي. باطنه خاشع، وحاضره سامع، وناظره دامع','6914','202','0');
INSERT INTO [t10495] VALUES (348,'مجمع بن صمغان التيمي قال الشيخ رضي الله تعالى عنه: ومنهم الورع السخي، مجمع بن صمغان التيمي','6924','202','0');
INSERT INTO [t10495] VALUES (349,'ضرار بن مرة قال الشيخ رحمه الله تعالى: ومنهم الباكي اليقظان، ضرار بن مرة أبو سنان','6933','202','0');
INSERT INTO [t10495] VALUES (350,'عمرو بن مرة قال الشيخ رضي الله تعالى عنه: ومنهم الراوي الثابت، والراجي القانت عمرو بن مرة','6950','202','0');
INSERT INTO [t10495] VALUES (351,'عمرو بن قيس الملائي قال الشيخ رحمه الله تعالى: ومنهم القارئ الخاشع، والمسكين المتواضع عمرو بن قيس الملائي','6978','202','0');
INSERT INTO [t10495] VALUES (352,'أبو مسلم الخولاني قال الشيخ رحمه الله تعالى ورضي الله عنه: ذكر طبقة من تابعي أهل الشام. فمنهم حكيم الأمة وممثلها أبو مسلم الخولاني عبد الله بن ثوب، تقدم ذكره وبعض كلامه مع الزهاد الثمانية في صدر الكتاب، قيل: كان إسلامه عام حنين، وقدم المدينة في','7051','202','0');
INSERT INTO [t10495] VALUES (353,'أبو إدريس الخولاني قال الشيخ رضي الله تعالى عنه: ومنهم المعتبر النظار، والمتفكر الذكار، أبو إدريس الخولاني عائذ الله بن عبد الله','7060','202','0');
INSERT INTO [t10495] VALUES (354,'أبو عبد الله الصنابحي ومنهم المشمر المسابق، أبو عبد الله الصنابحي عبد الرحمن بن عسيلة','7086','202','0');
INSERT INTO [t10495] VALUES (355,'أيفع بن عبد الكلاعي ومنهم الواعظ الداعي، أيفع بن عبد الكلاعي','7095','202','0');
INSERT INTO [t10495] VALUES (356,'جبير بن نفير ومنهم المتواضع في نفسه العفير جبير بن نفير','7101','202','0');
INSERT INTO [t10495] VALUES (357,'ابن محيريز قال الشيخ رحمه الله: ومنهم الصابر للدين العزيز، المتواضع في نفسه عبد الله بن محيريز','7119','202','0');
INSERT INTO [t10495] VALUES (358,'عبد الله بن أبي زكريا قال الشيخ رحمه الله تعالى: ومنهم المستبق إلى ذكره كهلا وصبيا، المغتنم مسألته جهرا وخفيا، كان رضيا زكيا، ووليا تقيا، عبد الله بن أبي زكريا','7168','202','0');
INSERT INTO [t10495] VALUES (359,'أبو عطية المذبوح قال الشيخ رحمه الله: ومنهم المفزع المشروح، أبو عطية بن قيس المذبوح','7192','202','0');
INSERT INTO [t10495] VALUES (360,'مريج بن مسروق قال الشيخ رضي الله عنه: ومنهم القلق المخنوق، أبو الحسن مريج بن مسروق','7199','202','0');
INSERT INTO [t10495] VALUES (361,'عمرو بن الأسود ومنهم المتسمت بالسمت الأجود، العنسي عمرو بن الأسود','7204','202','0');
INSERT INTO [t10495] VALUES (362,'عمير بن هانئ قال الشيخ رحمه الله تعالى: ومنهم التارك للأماني والتواني، المثابر على المباني والمعاني، أبو الوليد عمير بن هاني','7211','202','0');
INSERT INTO [t10495] VALUES (363,'عبيدة بن مهاجر ومنهم الزاهد المفارق للمشاجر، والمسابق للمتاجر، أبو عبد رب عبيدة بن مهاجر','7220','202','0');
INSERT INTO [t10495] VALUES (364,'يزيد بن مرثد ومنهم البكاء الموجد، يزيد بن مرثد','7228','202','0');
INSERT INTO [t10495] VALUES (365,'شفي بن ماتع الأصبحي قال الشيخ رضي الله عنه: ومنهم العامل الخفي، شفي بن ماتع الأصبحي','7236','202','0');
INSERT INTO [t10495] VALUES (366,'رجاء بن حيوة ومنهم الفقيه المفهم المطعام، مشير الخلفاء والأمراء، رجاء بن حيوة أبو المقدام','7246','202','0');
INSERT INTO [t10495] VALUES (367,'مكحول الشامي ومنهم الإمام الفقيه الصائم المهزول إمام أهل الشام أبو عبد الله مكحول','7279','202','0');
INSERT INTO [t10495] VALUES (368,'عطاء بن ميسرة قال الشيخ رحمه الله تعالى: ومنهم المحث على التزود للآجلة، المنفر عن الاغترار بالعاجلة، أبو عثمان الخراساني عطاء بن ميسرة. كان فقيها كاملا، وواعظا عاملا، تزود للارتحال، تيقنا للانتقال وقيل: إن التصوف تبصر في الرشاد، وتشمر للمعاد،','7348','202','0');
INSERT INTO [t10495] VALUES (369,'خالد بن معدان ومنهم ذو البدن المجهود، والقلب الموجود، واللب المحمود، كان لقلبه واجدا، وبلبه وافدا، وفي وصله جاهدا، خالد بن معدان وقيل: إن التصوف بذل المجهود، لمشاهدة المعبود','7413','202','0');
INSERT INTO [t10495] VALUES (370,'بلال بن سعد ومنهم المتشمر في الوعظ، المتفكر في الوعد، بلال بن سعد. كان عقولا عن الله تعالى سميعا، حمولا في الخدمة رفيعا، بليغا في الموعظة ضليعا','7469','202','0');
INSERT INTO [t10495] VALUES (371,'يزيد بن ميسرة ومنهم البليغ في الوعظ والتذكرة، المصيب في الرأي والمشورة، أبو يوسف يزيد بن ميسرة','7525','202','0');
INSERT INTO [t10495] VALUES (372,'إبراهيم بن أبي عبلة ومنهم إبراهيم بن أبي عبلة، كان أمينا قارئا، كان في علمه وقراءته هنيا مريا، وفي مواعظه ونصائحه بليغا قويا، رحمة الله تعالى عليه','7564','202','0');
INSERT INTO [t10495] VALUES (373,'يونس بن ميسرة قال الشيخ رحمه الله: ومنهم الشهيد المحبس، يونس بن ميسرة بن حلبس، رضي الله تعالى عنه','7590','202','0');
INSERT INTO [t10495] VALUES (374,'عمر بن عبد العزيز قال الشيخ رحمه الله: ومنهم المحتصن الحريز، ذو الشجى والأزيز، المولى عمر بن عبد العزيز كان واحد أمته في الفضل، ونجيب عشيرته في العدل، جمع زهدا وعفافا، وورعا وكفافا، شغله آجل العيش عن عاجله، وألهاه إقامة العدل عن عاذله، كان','7606','202','0');
INSERT INTO [t10495] VALUES (375,'الرسالة','7931','374','0');
INSERT INTO [t10495] VALUES (376,'عبد الملك بن عمر بن عبد العزيز قال الشيخ رحمه الله: ومنهم الحذر الحرك، سليل عمر بن عبد الملك كان للحق نافذا، وللباطل واقذا. وقيل: إن التصوف الحذر من الأهاويل والنفر من الأباطيل','7933','202','0');
INSERT INTO [t10495] VALUES (377,'كعب الأحبار قال الشيخ رحمه الله: ومنهم الحبر صاحب الكتب والأسفار، المثير للمكتوم والأسرار، والمشير إلى المشاهد والآثار، أبو إسحاق كعب بن ماتع الأحبار وقيل: إن التصوف مفارقة الأشرار، ومصادقة الأخيار','7969','202','0');
INSERT INTO [t10495] VALUES (378,'تكملة كعب الأحبار','8076','377','0');
INSERT INTO [t10495] VALUES (379,'نوف البكالي ومنهم المرغب في المحاسن والمعالي نوف بن أبي فضالة البكالي، كان للكتب قاريا. وإلى المحامد داعيا وعن المحاذر ناهيا. وقيل إن التصوف الدعاء إلى الارتفاع والإيماء إلى الارتداع','8208','202','0');
INSERT INTO [t10495] VALUES (380,'حيلان بن فروة ومنهم الواعظ الجعد المعروف بالحفظ والسرد حيلان بن فروة أبو الجلد، كان للكتب المنزلة حافظا، وبمواعظ الأنبياء وأحوالهم واعظا، وبالأذكار لهجا لافظا، وقيل: إن التصوف الرعاية للعهود والكفاية بالمشهود','8231','202','0');
INSERT INTO [t10495] VALUES (381,'شهر بن حوشب ومنهم المعتبر بالشعر المشيب، والمنتظر للوارد المغيب شهر بن حوشب','8254','202','0');
INSERT INTO [t10495] VALUES (382,'مغيث بن سمى ومنهم الواعظ المحذر المذكر المبشر مغيث بن سمي رضي الله تعالى عنه','8286','202','0');
INSERT INTO [t10495] VALUES (383,'حسان بن عطية ومنهم المسارع إلى الأعمال الزكية، الذام للأقوال الردية الداعي بالأدعية المرضية أبو بكر حسان بن عطية، بصري الأصل من ناقلة الشام','8297','202','0');
INSERT INTO [t10495] VALUES (384,'القاسم بن مخيمرة ومنهم الرافض للفضول النافض للهموم أبو عروة القاسم بن مخيمرة رضي الله تعالى عنه. كوفي الأصل نزيل الشام','8351','202','0');
INSERT INTO [t10495] VALUES (385,'إسماعيل بن المهاجر ومنهم القارئ الصادق المثابر إسماعيل بن عبيد الله بن أبي المهاجر رضي الله تعالى عنه','8380','202','0');
INSERT INTO [t10495] VALUES (386,'سليمان الأشدق ومنهم الصدوق الأصدق الفقيه الأحذق سليمان بن موسى الأشدق رضي الله تعالى عنه','8387','202','0');
INSERT INTO [t10495] VALUES (387,'أبو بكر الغساني ومنهم المتعبد الرباني أبو بكر بن أبي مريم الغساني رضي الله تعالى عنه','8397','202','0');
INSERT INTO [t10495] VALUES (388,'علي بن أبي جملة، ورجاء بن أبي سلمة ومنهم القرينان العابدان الراويان العاملان علي بن أبي جملة، ورجاء بن أبي سلمة رضي الله تعالى عنهما','8409','202','0');
INSERT INTO [t10495] VALUES (389,'ثور بن يزيد ومنهم القائل بالوعيد أبو خالد ثور بن يزيد رضي الله تعالى عنه كان في القول بالوعيد شاطحا وعرف به فلقب ناطحا','8419','202','0');
INSERT INTO [t10495] VALUES (390,'حدير بن كريب ومنهم حدير بن كريب أبو الزاهرية مخوف العصاة بانتقام القاهرية','8456','202','0');
INSERT INTO [t10495] VALUES (391,'حبيب بن عبيد ومنهم حبيب بن عبيد رضي الله تعالى عنه','8465','202','0');
INSERT INTO [t10495] VALUES (392,'ضمرة بن حبيب ومنهم ضمرة بن حبيب رضي الله تعالى عنه','8473','202','0');
INSERT INTO [t10495] VALUES (393,'ربيعة الجرشي ومنهم ربيعة الجرشي وقيل: ابن عمرو معدود في الصحابة','8484','202','0');
INSERT INTO [t10495] VALUES (394,'أبو عمرو الشيباني ومنهم أبو زرعة يحيى بن أبي عمرو الشيباني','8488','202','0');
INSERT INTO [t10495] VALUES (395,'عثمان بن أبي سودة ومنهم عثمان بن أبي سودة أبو العوام','8496','202','0');
INSERT INTO [t10495] VALUES (396,'أبو زيد الغوثي ومنهم أبو زيد الغوثي رضي الله تعالى عنه','8502','202','0');
INSERT INTO [t10495] VALUES (397,'عبد الرحمن بن ميسرة ومنهم عبد الرحمن بن ميسرة الحضرمي رضي الله تعالى عنه','8504','202','0');
INSERT INTO [t10495] VALUES (398,'عمرو بن قيس الكندي قال الشيخ رحمه الله: ومنهم عمرو بن قيس الكندي رضي الله تعالى عنه','8508','202','0');
INSERT INTO [t10495] VALUES (399,'محمد بن زياد الألهاني قال الشيخ رحمه الله: ومنهم محمد بن زياد الألهاني رضي الله تعالى عنه','8511','202','0');
INSERT INTO [t10495] VALUES (400,'عبدة بن أبي لبابة قال الشيخ رحمه الله: ومنهم عبدة بن أبي لبابة رضي الله تعالى عنه','8515','202','0');
INSERT INTO [t10495] VALUES (401,'راشد بن سعد قال الشيخ رحمه الله: ومنهم راشد بن سعد المقرائي','8538','202','0');
INSERT INTO [t10495] VALUES (402,'هانئ بن كلثوم قال الشيخ رحمه الله: ومنهم هانئ بن كلثوم بن شريك كان قليل الكلام، غزير الحديث أراده عمر بن عبد العزيز على القضاء فاستعفى وأبى','8548','202','0');
INSERT INTO [t10495] VALUES (403,'عروة بن رويم قال الشيخ رحمه الله: ومنهم عروة بن رويم اللخمي','8551','202','0');
INSERT INTO [t10495] VALUES (404,'سعيد بن عبد العزيز قال الشيخ رحمه الله: ومنهم سعيد بن عبد العزيز','8564','202','0');
INSERT INTO [t10495] VALUES (405,'عبد الله بن شوذب قال الشيخ رحمه الله: ومنهم عبد الله بن شوذب','8587','202','0');
INSERT INTO [t10495] VALUES (406,'أبو عمرو الأوزاعي ومنهم العلم المنشور، والحكم المشهور الإمام المبجل، والمقدام المفضل عبد الرحمن بن عمرو أبو عمرو الأوزاعي رضي الله تعالى عنه، كان واحد زمانه، وإمام عصره وأوانه، كان ممن لا يخاف في الله لومة لائم، مقوالا بالحق لا يخاف سطوة العظائم','8612','202','0');
INSERT INTO [t10495] VALUES (407,'[ذكر طوائف من جماهير النساك والعباد]','8650','0','0');
INSERT INTO [t10495] VALUES (408,'حبيب الفارسي فمنهم حبيب أبو محمد الفارسي من ساكني البصرة، كان صاحب المكرمات مجاب الدعوات، وكان سبب إقباله على الآجلة وانتقاله عن العاجلة حضوره مجلس الحسن بن أبي الحسن فوقعت موعظته من قلبه، فخرج عما كان يتصرف فيه ثقة بالله، ومكتفيا بضمانه، فاشترى','8651','407','0');
INSERT INTO [t10495] VALUES (409,'عبد الواحد بن زيد ومنهم المنفلت من القيد المتصيد للصيد عبد الواحد بن زيد. كان عابدا زاهدا وواعظا عن المحاذر زائدا وللقاصد المبادر رائدا','8670','407','0');
INSERT INTO [t10495] VALUES (410,'صالح بن بشير المري ومنهم القارئ الدري، والواعظ التقي أبو بشر صالح بن بشير المري، صاحب قراءة وشجن ومخافة وحزن يحرك الأخيار ويفرك الأشرار','8708','407','0');
INSERT INTO [t10495] VALUES (411,'عمران القصير ومنهم الواعظ البصير المحث على المسير إلى المصير أبو بكر عمران القصير، كان التحفظ من شأنه والتيقظ من مظانه','8757','407','0');
INSERT INTO [t10495] VALUES (412,'غالب القطان ومنهم المتعبد اليقظان غالب بن خطاف القطان كان في عبادة ربه راجحا، ولعبيده وخلقه ناصحا','8783','407','0');
INSERT INTO [t10495] VALUES (413,'سلام بن أبي مطيع ومنهم الشاكر الرفيع، والشاهد السميع سلام بن أبي مطيع، شكر فارتفع وشهد فاستمع وقيل: إن التصوف ارتفاع لازدياد واستماع في استشهاد','8801','407','0');
INSERT INTO [t10495] VALUES (414,'رياح بن عمرو القيسي ومنهم المتخشع البكاء، المتضرع الدعاء أبو المهاجر رياح بن عمرو القيسي','8821','407','0');
INSERT INTO [t10495] VALUES (415,'حوشب بن مسلم منهم السابق المقدم أبو بشر حوشب بن مسلم. كان في العباد عارفا وعن الدنيا عازفا','8844','407','0');
INSERT INTO [t10495] VALUES (416,'سعيد بن إياس الجريري ومنهم الموقن بالمعبود المقيم على رعاية العهود سعيد بن إياس الجريري أبو مسعود','8857','407','0');
INSERT INTO [t10495] VALUES (417,'الفضل بن عيسى الرقاشي ومنهم الواعظ الناصح المنقى من العار الفاضح، كان يلاحظ الإكساب ولا ينشرح للانتحاب، الفضل بن عيسى الرقاشي','8884','407','0');
INSERT INTO [t10495] VALUES (418,'كهمس الدعاء ومنهم الورع البكاء كهمس بن الحسن أبو عبد الله الدعاء','8897','407','0');
INSERT INTO [t10495] VALUES (419,'عطاء السليمي ومنهم ذو الخوف العظيم والقلب السليم عطاء السليمي، أنحله الفزع وأذبله الضرع، فكانت المعرفة ذمامه والمخافة زمامه','8919','407','0');
INSERT INTO [t10495] VALUES (420,'عتبة الغلام ومنهم الحر الهمام، المجلو من الظلام المكلوء بالشهادة والكلام، عتبة بن أبان الغلام، كشف له الغطاء ونظف له الوطاء فخفف عنه البطاء','8975','407','0');
INSERT INTO [t10495] VALUES (421,'بشر بن منصور السليمي ومنهم المتعبد العليم المتوجد السليم بشر بن منصور السليمي رحمه الله استحلى الوحدة والأذكار وسلم من الفتنة والأخطار','9031','407','0');
INSERT INTO [t10495] VALUES (422,'عبد العزيز بن سلمان ومنهم الواله العيمان الوارد العطشان عبد العزيز بن سلمان رحمه الله، الخوف أضناه والرجاء أسلاه','9055','407','0');
INSERT INTO [t10495] VALUES (423,'عبد الله بن ثعلبة ومنهم التائه الكلفي البكاء الدنفي عبد الله بن ثعلبة الحنفي، هيمه الحب وتيمه القرب','9064','407','0');
INSERT INTO [t10495] VALUES (424,'المغيرة بن حبيب ومنهم المسارع اللبيب المغيرة بن حبيب فارق الشهوات وعانق القربات','9070','407','0');
INSERT INTO [t10495] VALUES (425,'حماد بن سلمة ومنهم المجتهد في العبادة المعدود في الإمامة أبو سلمة حماد بن سلمة، كان لخطير الأعمال مصطنعا وبيسير الأقوات مقتنعا','9081','407','0');
INSERT INTO [t10495] VALUES (426,'حماد بن زيد ومنهم الإمام الرشيد الآخذ بالأصل الوكيد المتمسك بالمنهج الحميد، نزل من العلوم بالمحل الرفيع، وتوصل إلى الأصول بالوسيط المنيع، اقتبس الآثار عن الأخيار، وأخذ الأعمال عن الأبرار، أكبر فوائده في الأقضية والأحكام، وأبلغ مواعظه في مراعاة','9123','407','0');
INSERT INTO [t10495] VALUES (427,'زياد بن عبد الله النميري ومنهم القائم المتهجد والصائم المتعبد، ابتدر الفوت وانتظر الموت زياد بن عبد الله النميري','9169','407','0');
INSERT INTO [t10495] VALUES (428,'هشام بن حسان ومنهم المترقب ذو الأحزان المتيقظ ذو الأشجان هشام بن حسان، أكثر كلامه ما أسنده عن أستاذه الحسن بن أبي الحسن، لزمه عشر سنين','9179','407','0');
INSERT INTO [t10495] VALUES (429,'هشام الدستوائي ومنهم المخلص في الرعاية السلس في الرواية كان للذكر أليفا وللخوف حليفا هشام بن أبي عبد الله الدستوائي','9228','407','0');
INSERT INTO [t10495] VALUES (430,'جعفر الضبعي ومنهم الضبعي جعفر بن سليمان صحب العباد ونقل عنهم وعن الزهاد، صحب مالك بن دينار، وثابتا البناني، وأبا عمران الجوني، وأبا التياح، وفرقدا السبخي، وشميط بن عجلان','9269','407','0');
INSERT INTO [t10495] VALUES (431,'ابن برة ومنهم المفيق من الغرة، والمحذر من المضرة والمعرة، المشوق إلى الحبور والمسرة، الربيع بن عبد الرحمن المعروف بابن برة','9315','407','0');
INSERT INTO [t10495] VALUES (432,'عوسجة العقيلي ومنهم عوسجة العقيلي كان مشاهدا مكابدا يحث على المشاهدة والتولي ويدعو إلى الوحدة والتخلي','9329','407','0');
INSERT INTO [t10495] VALUES (433,'خزيمة أبو محمد العابد ومنهم خزيمة أبو محمد العابد كان عن الوضيعة حائدا، وإلى الرفيعة زائدا','9331','407','0');
INSERT INTO [t10495] VALUES (434,'خليفة العبدي ومنهم خليفة العبدي، كان للفكرة والخدمة مستلذا، ومن لوامع العبرة مستمدا رضي الله تعالى عنه','9336','407','0');
INSERT INTO [t10495] VALUES (435,'الربيع بن صبيح ومنهم ذو العقل الرجيح والعمل النجيح الربيع بن صبيح رضي الله تعالى عنه','9340','407','0');
INSERT INTO [t10495] VALUES (436,'علي بن علي الرفاعي ومنهم علي بن علي الرفاعي، كان مالك بن دينار رضي الله تعالى عنه يسميه راهب العرب، وكان شعبة رضي الله تعالى عنه يقول: اذهبوا بنا إلى سيدنا وابن سيدنا علي الرفاعي رضي الله تعالى عنه','9367','407','0');
INSERT INTO [t10495] VALUES (437,'إبراهيم بن عبد الله ومنهم إبراهيم بن عبد الله بن أبي الأسود راوي الرسالة، عن الحسن إلى عمر بن عبد العزيز رضي الله تعالى عنهم','9373','407','0');
INSERT INTO [t10495] VALUES (438,'معاوية بن عبد الكريم ومنهم معاوية بن عبد الكريم رضي الله تعالى عنه','9375','407','0');
INSERT INTO [t10495] VALUES (439,'مالك بن أنس فمنهم إمام الحرمين المشهور في البلدين الحجاز والعراقين المستفيض مذهبه في المغربين والمشرقين مالك بن أنس رضي الله تعالى عنه. كان أحد النبلاء وأكمل العقلاء، ورث حديث الرسول ونشر في أمته علم الأحكام والأصول، تحقق بالتقوى فابتلي بالبلوى','9379','407','0');
INSERT INTO [t10495] VALUES (440,'سفيان الثوري ومنهم الإمام المرضي والورع الدري أبو عبد الله سفيان بن سعيد الثوري رضي الله تعالى عنه. كانت له النكت الرائقة والنتف الفائقة، مسلم له في الإمامة ومثبت به الرعاية، العلم حليفه والزهد أليفه. وقيل: إن التصوف براعة في المعارف وبلاغة في','9587','407','0');
INSERT INTO [t10495] VALUES (441,'شعبة بن الحجاج قال الشيخ رحمه الله تعالى: ومنهم الإمام المشهور , والعلم المنشور , في المناقب مذكور , له التقشف، والتعبد , والتكشف عن الأخبار والتشدد , أمير المؤمنين في الرواية والتحديث , وزين المحدثين في القديم والحديث , أكثر عنايته بتصحيح الآثار ,','10575','407','0');
INSERT INTO [t10495] VALUES (442,'ذكر من حدث وروى عنه شعبة من الأئمة، والأعلام التابعين , ممن أسماؤهم محمد , فمنهم محمد بن المنكدر','10670','441','0');
INSERT INTO [t10495] VALUES (443,'مسعر بن كدام قال الشيخ أبو نعيم رحمه الله تعالى: ومنهم المعظم للمعالي العظام , المعتصم بمنهج الصحابة والأعلام , المسلم مدته بمصاحبة الأعفة الكرام , المخروم لسانه عن الخنا بالأعنة، والفدام , المنظم نصائحه بترك المصاحبة والخصام , أبو سلمة مسعر بن كدام ,','10905','407','0');
INSERT INTO [t10495] VALUES (444,'أسند مسعر عن غير واحد من أعلام التابعين , فممن روى عنهم ممن وافق اسمه اسم المصطفى صلى الله عليه وسلم: محمد بن عبد الله أبي عون الثقفي , سمع جابر بن سمرة، ومحمد بن حاطب','10999','443','0');
INSERT INTO [t10495] VALUES (445,'وروى مسعر، عن إسماعيل السدي، وإسماعيل بن رجاء، وإسماعيل بن عبد الملك , وإسماعيل بن نشيط','11024','443','0');
INSERT INTO [t10495] VALUES (446,'سفيان بن عيينة ومنهم الإمام الأمين , ذو العقل الرصين , والرأي الراجح الركين , المستنبط للمعاني , والمرتبط للمباني , أبو محمد سفيان بن عيينة الهلالي , كان عالما ناقدا , وزاهدا عابدا , علمه مشهور , وزهده معمور','11214','407','0');
INSERT INTO [t10495] VALUES (447,'أسند سفيان بن عيينة عن الجماهير من التابعين , أدرك ستة وثمانين نفسا من أعلام التابعين وأركانهم: كعمرو بن دينار , والزهري , ومحمد بن المنكدر , وعبد الله بن دينار , وزيد بن أسلم , وأبي حازم , ويحيى بن سعيد الأنصاري , ومن الكوفيين: أبو إسحاق , وعبد الملك','11427','407','0');
INSERT INTO [t10495] VALUES (448,'الليث بن سعد ومنهم السري السخي , الملي الوفي , لعلمه عقول , ولماله بذول , أبو الحارث الليث بن سعد. كان يعلم الأحكام مليا , ويبذل الأموال سخيا. وقيل: إن التصوف السخاء والوفاء','11478','407','0');
INSERT INTO [t10495] VALUES (449,'علي، والحسن ومنهم الأخوان التوأمان , الفقيهان العابدان , علي والحسن ابنا صالح بن حيي , رزقا علما وعبادة , وقناعة، وزهادة','11510','407','0');
INSERT INTO [t10495] VALUES (450,'داود بن نصير الطائي ومنهم الفقيه الواعي , البصير الراعي , العابد الطاوي , أبو سليمان داود بن نصير الطائي. أبصر معتبرا , وسبق مبتدرا , تشمر منتصبا , وانتظر مرتقبا , أضناه الفرق , وألهاه القلق. وقيل: إن التصوف تشمر لاستباق , وتضمر للحاق','11553','407','0');
INSERT INTO [t10495] VALUES (451,'أسند داود بن نصير الطائي عن جماعة من التابعين منهم: عبد الله بن عمير , وإسماعيل بن أبي خالد , والأعمش , وحميد الطويل , وأكثر روايته عن الأعمش، أروى الناس عن داود بن صعب بن المقدام. وروى عنه إسماعيل ابن علية , وزافر بن سليمان. توفي داود سنة ست , وقيل','11672','450','0');
INSERT INTO [t10495] VALUES (452,'إبراهيم بن أدهم ومنهم الحازم الأحزم , والعازم الألزم , أبو إسحاق إبراهيم بن أدهم أيد بالمعارف فوجد , وأمد بالملاطف فعبد , كان عن المقطوع، والمرذول وبالمرفوع الموصول متشاغلا , كان شرع الرسول نهجه , واختياره عليه السلام مرجعه , ألف الميمون الموصول , وخالف','11696','407','0');
INSERT INTO [t10495] VALUES (453,'شقيق البلخي ومنهم الرائد العقيق الزاهد الحقيق أبو علي البلخي شقيق. كان شقيق بن إبراهيم البلخي أحد الزهاد من المشرق وكان يقول: تطرح المكاسب والمطالب في الأسباب والمذاهب. قدم للمعاد وتنعم بالوداد زلق بكفالة الوكيل فتوكل واجتهد فيما التزم فاحتمل. وحقيقة','12000','407','0');
INSERT INTO [t10495] VALUES (454,'حاتم الأصم ومنهم الموثر للأدوم والأعم والآخذ بالألزم والأقوم أبو عبد الرحمن حاتم الأصم. توكل فسكن وأيقن فركن. وقيل: إن التصوف التنقي من الشكوك والتوقي في السلوك','12046','407','0');
INSERT INTO [t10495] VALUES (455,'الفضيل بن عياض ومنهم الراحل من المفاوز والقفار إلى الحصون والحياض والناقل من المهالك والسباخ إلى الغصون والرياض , أبو علي الفضيل بن عياض. كان من الخوف نحيفا وللطواف أليفا. وقيل: إن التصوف المبادرة في السفر والمساهرة في الحضر','12079','407','0');
INSERT INTO [t10495] VALUES (456,'وهيب بن الورد ومنهم الورع التقي الضرع الحيي وهيب بن الورد المكي. ظفر بالحيا ونعم بالحيا. وقيل: إن التصوف الأنين من الوضيع , والحنين إلى الربيع','12360','407','0');
INSERT INTO [t10495] VALUES (457,'عبد الله بن المبارك ومنهم السخي الجواد , الممهد للمعاد , المتزود من الوداد , أليف القرآن والحج والجهاد , جاد فساد , وروجع فزاد , ماله مشارك , وفعله مبارك , وقوله مبارك شاها نشاه , عبد الله بن المبارك رضي الله تعالى عنه وقيل: إن التصوف اعتداد لازدياد ,','12461','407','0');
INSERT INTO [t10495] VALUES (458,'عبد العزيز بن أبي رواد ومنهم العابد السجاد , والشاكر العواد أبو عبد الرحمن عبد العزيز بن أبي رواد كان للعبادة مغتنما. وللمصائب والمحن متكتما , وقيل: إن التصوف تعداد العطايا , وكتمان الرزايا','12594','407','0');
INSERT INTO [t10495] VALUES (459,'محمد بن صبيح بن السماك ومنهم زايد النساك وصائد الفتاك وناصب الشباك أبو العباس محمد بن صبيح حدد الشأن وشدد العيان فأوضح البيان وأفصح اللسان. وقيل: إن التصوف التوثق بالأصول للتحقق للوصول','12650','407','0');
INSERT INTO [t10495] VALUES (460,'محمد الحارثي ومنهم محمد بن النضر الحارثي أبو عبد الرحمن، كان من أعبد أهل زمانه وكان بالذكر أنيسا وللحق جليسا، وقيل إن التصوف مذاكرة العهود، ومسامرة الشهود','12714','407','0');
INSERT INTO [t10495] VALUES (461,'محمد بن يوسف الأصبهاني ومنهم ذو الجد والاجتهاد والتشمر والارتياد في التبادر والتسابق إلى المعاد. محمد بن يوسف الأصبهاني عروس الزهاد. وقيل: إن التصوف انتقال وارتحال , انتقال عن اختلال وارتحال عن اعتقال "','12760','407','0');
INSERT INTO [t10495] VALUES (462,'يوسف بن أسباط ومنهم ذو الجد والنشاط والمستبق إلى الصراط , يوسف بن أسباط كان العلم والخوف شعاره والتخلي من فضول الدنيا دثاره. وقيل: إن التصوف التحلي للتراقي والتخلي للتلاقي','12835','407','0');
INSERT INTO [t10495] VALUES (463,'أبو إسحاق الفزاري ومنهم تارك القصور والجواري ونازل الثغور والبراري أبو إسحاق إبراهيم الفزاري. كان لأهل الأثر والسنة إماما وعلى أهل الزيغ والبدعة زماما','12923','407','0');
INSERT INTO [t10495] VALUES (464,'مخلد بن الحسين ومنهم ذو القلب العقول واللسان السؤول , مخلد بن الحسين الواعي للأصول والمداري للجهول','12976','407','0');
INSERT INTO [t10495] VALUES (465,'حذيفة بن قتادة ومنهم العابد المتواضع، الخاضع المتوادع، حذيفة بن قتادة المرعشي، صحب سفيان الثوري وسمع منه.','12987','407','0');
INSERT INTO [t10495] VALUES (466,'أبو معاوية الأسود ومنهم المعرض عن الأرذال، والباحث على الأفضل , اليمان أبو معاوية الأسود','13016','407','0');
INSERT INTO [t10495] VALUES (467,'سعيد بن عبد العزيز ومنهم المتحصن بالحصن الحريز والخوف والبكاء الأزيز أبو محمد سعيد بن عبد العزيز','13029','407','0');
INSERT INTO [t10495] VALUES (468,'سليمان الخواص ومنهم الفطن الغواص سليمان الخواص','13042','407','0');
INSERT INTO [t10495] VALUES (469,'سالم الخواص ومنهم سالم بن ميمون الخواص','13049','407','0');
INSERT INTO [t10495] VALUES (470,'عباد بن عباد الخواص ومنهم الباكي الوباص، الزاكي القناص، أبو عتبة عباد بن عباد الخواص رضي الله تعالى عنه','13067','407','0');
INSERT INTO [t10495] VALUES (471,'عبد الله العمري ومنهم العابد العدوي والزاهد البدوي عبد الله بن عبد العزيز العمري','13072','407','0');
INSERT INTO [t10495] VALUES (472,'أبو حبيب البدوي ومنهم الغريب الشجوي أبو حبيب البدوي','13092','407','0');
INSERT INTO [t10495] VALUES (473,'أحمد الموصلي ومنهم أحمد الموصلي كان شاهدا حاضرا وسابقا مبادرا','13097','407','0');
INSERT INTO [t10495] VALUES (474,'أبو مسعود الموصلي ومنهم المعافى بن عمران أبو مسعود الموصلي كان ذا علم وضياء وبذل وعطاء','13099','407','0');
INSERT INTO [t10495] VALUES (475,'سباع الموصلي ومنهم أبو محمد سباع الموصلي أيس من الفضول فأونس بالوصول. وقيل: إن التصوف تطهير من الأدناس وتشمير للإيناس.','13116','407','0');
INSERT INTO [t10495] VALUES (476,'فتح بن سعيد ومنهم فتح بن سعيد الموصلي المنتقي من اختياره والمبتغي لاختباره','13119','407','0');
INSERT INTO [t10495] VALUES (477,'أسد البجلي ومنهم العابد السجاد المخلص الحماد أسد بن عبيدة البجلي , كوفي عزيز الحديث والكلام','13130','407','0');
INSERT INTO [t10495] VALUES (478,'بشر الآمي ومنهم القانع الرضي والصانع الخفي بشر الآمي','13134','407','0');
INSERT INTO [t10495] VALUES (479,'أبو الربيع السائح ومنهم المبكر الرائح أبو الربيع المعروف بالسائح بكر للحاق وراح للتلاق , رضي الله تعالى عنه','13138','407','0');
INSERT INTO [t10495] VALUES (480,'علي بن فضيل ومنهم الخائف الوجل الذائب النحل علي بن فضيل بن عياض','13145','407','0');
INSERT INTO [t10495] VALUES (481,'بشر بن السري ومنهم الأفوه البصري أبو عمرو بشر بن السري سكن مكة وكان من عبادها','13163','407','0');
INSERT INTO [t10495] VALUES (482,'أبو بكر بن عياش ومنهم القاريء الهشاش العابد البشاش أبو بكر بن عياش كان في العداد واحدا وفي العبادة شاهدا. وقيل: إن التصوف ارتقاء لاقتراب وانتصاب في ارتقاب','13177','407','0');
INSERT INTO [t10495] VALUES (483,'أبو الحكم سيار ومنهم المتعبد الصبار. أبو الحكم سيار كان رباصا ذكارا ولباسا شكارا وقيل: إن التصوف تكشر الظاهر وتكسر الباطن','13228','407','0');
INSERT INTO [t10495] VALUES (484,'شيبان الراعي ومنهم المنيب الواعي شيبان أبو محمد الراعي. كان في العبادة فائقا وبالتوكل على ربه عز وجل واثقا','13244','407','0');
INSERT INTO [t10495] VALUES (485,'صالح بن عبد الجليل ومنهم المستلذ بالطاعة والمجتزى بالبلغة والقناعة صالح بن عبد الجليل','13246','407','0');
INSERT INTO [t10495] VALUES (486,'الحسين بن يحيى الحسني ومنهم المجتهد المهنى الحسين بن يحيى الحسني','13249','407','0');
INSERT INTO [t10495] VALUES (487,'إدريس الخولاني ومنهم العاقل الرباني إدريس بن يحيى الخولاني','13256','407','0');
INSERT INTO [t10495] VALUES (488,'المفضل بن فضالة ومنهم الثابت العدالة القليل الملالة المفضل بن فضالة. كانت له الدعوة المجابة وله الولاية والمهابة','13266','407','0');
INSERT INTO [t10495] VALUES (489,'عبد الله بن وهب ومنهم قتيل الخوف والكرب المحدث المصري عبد الله بن وهب','13282','407','0');
INSERT INTO [t10495] VALUES (490,'يزيد بن عبد الملك ومنهم الخائف الناحل الذاهب الذابل يزيد بن عبد الملك بن موهب','13318','407','0');
INSERT INTO [t10495] VALUES (491,'علي بن أبي الحر ومنهم التارك للتافه المر العابد الناصح علي بن أبي الحر','13331','407','0');
INSERT INTO [t10495] VALUES (492,'عبد العزيز الدوري ومنهم القائم المتهجد الهائم المتعبد عبد العزيز بن أبان الدوري','13333','407','0');
INSERT INTO [t10495] VALUES (493,'داود بن رشيد ومنهم المروح بالهواتف','13335','407','0');
INSERT INTO [t10495] VALUES (494,'عبد الله بن سعيد ومنهم المؤدب بالعتاب والمهذب بالخطاب','13337','407','0');
INSERT INTO [t10495] VALUES (495,'علي بن محمد ومنهم المتوكل المتقاضي المنسوب إلى الضعف وفقد التراضي','13339','407','0');
INSERT INTO [t10495] VALUES (496,'بشر بن الحارث ومنهم من حباه الحق بجزيل الفواتح وحماه عن وبيل الفوادح أبو نصر بشر بن الحارث الحافي المكتفي بكفاية الكافي اكتفى فاشتفى. وقيل: إن التصوف الاكتفاء للاعتلاء والاشتفاء من الابتلاء','13341','407','0');
INSERT INTO [t10495] VALUES (497,'معروف الكرخي ومنهم الملهوف إلى المعروف عن الفاني، مصروف وبالباقي مشغوف وبالتحف محفوف وللطف مألوف , الكرخي أبو محفوظ معروف وقيل إن التصوف التوقي من الأكدار والتنقي من الأقذار','13483','407','0');
INSERT INTO [t10495] VALUES (498,'وكيع بن الجراح ومنهم النصاح والمفهم المفصاح أبو سفيان وكيع بن الجراح','13532','407','0');
INSERT INTO [t10495] VALUES (499,'عبد الرحمن بن محمد ويحيى بن سعيد القطان ومنهم الإمامان القرينان الحافظان على الناس السنن والبيان , عبد الرحمن بن مهدي ويحيى بن سعيد القطان رضي الله تعالى عنهما. كانا للنسك كاتمين وبحقائق الدين عارفين ولصحاح السنن ناقدين ولأهل الزيغ متباغضين وللعباد','13594','407','0');
INSERT INTO [t10495] VALUES (500,'عبد الرحمن بن مهدي ومنهم الإمام الرضي، والزمام القوي ناقد الآثار، وحافظ الأخبار عبد الرحمن بن مهدي. كان للسنن والآثار تابعا، وللآراء والأهواء دافعا','13655','407','0');
INSERT INTO [t10495] VALUES (501,'الإمام الشافعي ومنهم الإمام الكامل العالم العامل ذو الشرف المنيف، والخلق الظريف، له السخاء والكرم، وهو الضياء في الظلم، أوضح المشكلات، وأفصح عن المعضلات، المنتشر علمه شرقا وغربا، المستفيض مذهبه برا وبحرا، المتبع للسنن والآثار، والمقتدي بما اجتمع','13993','407','0');
INSERT INTO [t10495] VALUES (502,'ذكر بيان لصوق نسبه بنسب رسول الله صلى الله عليه وسلم','14002','501','0');
INSERT INTO [t10495] VALUES (503,'ذكر بيان نسبه، ومولده ووفاته','14006','501','0');
INSERT INTO [t10495] VALUES (504,'قال الشيخ رحمة الله تعالى عليه: ذكر الأئمة والعلماء له','14050','501','0');
INSERT INTO [t10495] VALUES (505,'الإمام أحمد بن حنبل قال الشيخ رحمه الله: ومنهم الإمام المبجل والهمام المفضل أبو عبد الله أحمد بن حنبل. لزم الاقتداء، وظفر بالاهتداء، علم الزهاد، وقلم النقاد، امتحن فكان في المحنة صبورا، واجتبي فكان للنعمة شكورا، كان للعلم والحلم واعيا، وللهم','14430','407','0');
INSERT INTO [t10495] VALUES (506,'ذكر نسبه ومولده ووفاته رضي الله تعالى عنه','14431','505','0');
INSERT INTO [t10495] VALUES (507,'ذكر جلالته عند العلماء ونبالته عند المحدثين والفقهاء','14441','505','0');
INSERT INTO [t10495] VALUES (508,'إسحاق بن إبراهيم الحنظلي قال الشيخ أبو نعيم رحمة الله تعالى ورضوانه عليه: ومنهم الإمام الهمام المشهور بالحفظ والفقه مذكور أعلامه في العالم منشور، إسحاق بن إبراهيم الحنظلي، قرين الإمام المعظم المبجل أحمد بن حنبل وخدين الإمام المفضل محمد بن إدريس','14698','407','0');
INSERT INTO [t10495] VALUES (509,'محمد بن أسلم ومنهم السليم الأسلم المذكور بالسواد الأعظم الطوسي أبو الحسن محمد بن أسلم. أحواله مشتهرة مشهورة، وشمائله مسطرة مذكورة. كان بالآثار مقتديا، وعن الآراء منتهيا، أعطي بيانا وبلاغة وزهدا وقناعة، نقض على المخالفين بتبيانه، وأقبل على تصحيح','14718','407','0');
INSERT INTO [t10495] VALUES (510,'أبو سليمان الداراني فمنهم أبو سليمان عبد الرحمن بن أحمد بن عطية العبسي الداراني. وداريا قرية من قرى دمشق، كان سبر الأحوال ليعتبر الأهوال فطهر من الأعلال لمداومته على الدؤوب والكلال','14755','407','0');
INSERT INTO [t10495] VALUES (511,'أحمد بن عاصم الأنطاكي ومنهم القاصم الهاشم اللائم الناقم الأنطاكي أحمد بن عاصم رحمه الله. كان للهوى قاصما ولشرور النفس هاشما، يديم القيام وينقم على اللوام','14950','407','0');
INSERT INTO [t10495] VALUES (512,'محمد بن المبارك ومنهم ذو العقل الوافي والورع الصافي والبيان الشافي أبو عبد الله محمد بن المبارك الصوري رحمه الله','14980','407','0');
INSERT INTO [t10495] VALUES (513,'سعيد بن يزيد ومنهم العجاج الناجي أبو عبد الله الساجي سعيد بن يزيد رحمه الله تعالى. كان يعج من نفسه إلى ربه عجيجا، ويشتاق إليه شاكيا أنينا وضجيجا. وقيل: إن التصوف عرفان الحدود والحقوق ووجدان السكون والوثوق','15018','407','0');
INSERT INTO [t10495] VALUES (514,'علي بن بكار قال الشيخ أبو نعيم رحمه الله تعالى: ومنهم المرابط الصبار المجاهد الكرار علي بن بكار - رحمه الله تعالى. سكن المصيصة مرابطا، صحب إبراهيم بن أدهم وأبا إسحاق الفزاري ومخلد بن الحسين','15063','407','0');
INSERT INTO [t10495] VALUES (515,'القاسم بن عثمان قال الشيخ أبو نعيم رحمة الله تعالى عليه: ومنهم القاسم بن عثمان الجوعي رحمه الله تعالى كانت له الرعاية الوافية فأيد بالقوة الكافية','15085','407','0');
INSERT INTO [t10495] VALUES (516,'مضاء بن عيسى ومنهم مضاء بن عيسى الشامي رحمه الله تعالى، كان من العاملين، اجتذبه الحب واستلبه الخوف','15098','407','0');
INSERT INTO [t10495] VALUES (517,'منصور بن عمار قال الشيخ أبو نعيم رحمه الله تعالى: ومنهم منصور بن عمار رحمه الله تعالى كان لآلاء الله واصفا، وعلى بابه عاكفا يحوش العباد إليه ويلح في المسألة عليه','15106','407','0');
INSERT INTO [t10495] VALUES (518,'ذو النون المصري ومنهم العلم المضي والحكم المرضي الناطق بالحقائق، الفائق للطرائق، له العبارات الوثيقة والإشارات الدقيقة. نظر فعبر وذكر فازدجر أبو الفيض ذو النون بن إبراهيم المصري رحمه الله تعالى','15123','407','0');
INSERT INTO [t10495] VALUES (519,'أحمد بن أبي الحواري ومنهم الزاهد في السراري النابذ للجواري العابد في القفار والبراري أبو الحسن أحمد بن أبي الحواري كان لفضول الدنيا قاليا وعن الملاذ ساليا وفي مكين الأحوال عاليا ولصحيح الآثار حاويا','15318','407','0');
INSERT INTO [t10495] VALUES (520,'أبو يزيد البسطامي قال الشيخ الحافظ أبو نعيم رحمه الله: ومنهم التائه الوحيد الهائم الفريد البسطامي أبو يزيد تاه فغاب وهام فآب غاب عن المحدودات إلى موجد المحسوسات والمعدومات فارق الخلق ووافق الحق فأيد بأخلاء الخير وأمد باستيلاء البر إشارته هانئة وعباراته','15455','407','0');
INSERT INTO [t10495] VALUES (521,'أحمد بن الخضر فمنهم أحمد بن الخضر المعروف بابن خضرويه البلخي شيخ خراسان له الفتوة المشهورة والتجريد الحميد كانت قرينته المكتنية بأم علي من بنات الكبار حللت زوجها أحمد من صداقها على أن يزور بها أبا يزيد البسطامي , فحملها إلى أبي يزيد , فدخلت عليه وقعدت','15489','407','0');
INSERT INTO [t10495] VALUES (522,'إبراهيم الهروي ومنهم أبو إسحاق إبراهيم الهروي يعرف بابن بستنبه صحب إبراهيم بن أدهم من أقران أبي يزيد من المذكورين بالتوكل والتجريد توفي بقزوين وكان أهل هراة يعظمونه فحج متجردا فقيل: إنه كان من دعائه في تلك الحجة أن قال: اللهم اقطع رزقي عن أموال , أهل','15493','407','0');
INSERT INTO [t10495] VALUES (523,'داود البلخي قال الشيخ رحمه الله: ومن متقدمي شيوخ المشرق داود البلخي وإبراهيم بن أدهم وشقيق البلخي , وحاتم الأصم , وقد تقدم ذكرهم غير داود البلخي , فإنه لم ينشر عنه كانتشار إبراهيم , وشقيق وحاتم ولم أر له ذكرا فيما وقع إلينا إلا ما يحكي عنه إبراهيم بن','15497','407','0');
INSERT INTO [t10495] VALUES (524,'أبو تراب النخشبي ومنهم أبو تراب النخشبي كان أحد أعلام المتوكلين وإمام المتجردين تأدب بحاتم الأصم وعلي الرازي المذبوح له الرياضات المشهورة والسياحات المذكورة دخل أصبهان وسمع من عبد الله بن محمد بن زكريا , ومحمد بن عبد الله بن مصعب , وصحبه جدي محمد بن يوسف','15498','407','0');
INSERT INTO [t10495] VALUES (525,'يحيى بن معاذ ومنهم المادح الشكار القانع الصبار الراجي الجآر يحيى بن معاذ الواعظ الذكار لزم الحداد توقيا من العباد واستلذ السهاد تحريا للوداد واحتمل الشداد توصلا إلى العناد','15522','407','0');
INSERT INTO [t10495] VALUES (526,'سعيد بن العباس الرازي ومنهم الواثق بالوصول الناطق بالأصول التارك للفضول له البيان الشافي والكلام الكافي نبذ الآراء وعدد الآلاء عمل على تصفية الباطن فركن إلى لطف الضامن أبو عثمان سعيد بن العباس الرازي','15570','407','0');
INSERT INTO [t10495] VALUES (527,'الحارث بن أسد المحاسبي ومنهم المشاهد المراقبي والمساعد المصاحبي أبو عبد الله الحارث بن أسد المحاسبي كان لألوان الحق مشاهدا ومراقبا ولآثار الرسول عليه السلام مساعدا ومصاحبا، تصانيفه مدونة مسطورة وأقواله مبوبة مشهورة وأحواله مصححة مذكورة كان في علم الأصول','15576','407','0');
INSERT INTO [t10495] VALUES (528,'علي الجرجاني ومنهم المتخلي من الشهوات والمتحلي بالخلوات تخلى من الجزع والهلع واستحلى الفزع والضرع علي الجرجاني، من قدماء المتعبدين','15611','407','0');
INSERT INTO [t10495] VALUES (529,'فديم قال الشيخ: وممن عرف من متقدمي البغداديين بالنسك والتحقيق بالتصوف أبو هاشم فديم جلس إليه سفيان الثوري فحمد طريقته وملازمته للصفاء والوفاء، لا نحفظ من كلامه شيئا إلا ما حكاه عنه الثوري أنه قال: ما زلت أرائي وأنا لا أشعر إلى أن جالست أبا هاشم،','15614','407','0');
INSERT INTO [t10495] VALUES (530,'شريح بن يونس قال الشيخ: ومن المشهورين بتحقيق العبادة والعبودية والانقياد لتعظيم الألهية والربوبية، المأخوذ عنه الآداب الشريفة والمقتبس من الكثير من آثار الشريعة أبو الحارث شريح بن يونس نقل عنه الأحوال السنية وله الآيات البديعة، توفي سنة خمس وثلاثين','15616','407','0');
INSERT INTO [t10495] VALUES (531,'السري السقطي ومنهم العلم المنشور والحكم المذكور شديد الهدى حميد السعي ذو القلب التقي والورع الخفي عن نفسه راحل ولحكم ربه نازل أبو الحسن السري بن المغلس السقطي خال أبي القاسم الجنيد وأستاذه','15628','407','0');
INSERT INTO [t10495] VALUES (532,'إبراهيم بن شماس فممن لم يذكر إبراهيم بن شماس السمرقندي سكن بغداد بالتعبد الدائم مشهور، وفي المحبة هائم مذكور، أسند الحديث','15684','407','0');
INSERT INTO [t10495] VALUES (533,'محمد بن عمرو المغربي ومنهم محمد بن عمرو المغربي كان في التعبد بمشاهدة معبوده طاعما وعن مشاركة المتنعمين غائبا','15686','407','0');
INSERT INTO [t10495] VALUES (534,'بشير الطبري ومنهم بشير الطبري سكن الشام كان محفوظا فيما امتحن به مستسلما فيما ابتلي به','15692','407','0');
INSERT INTO [t10495] VALUES (535,'خزيمة العابد ومنهم خزيمة أبو محمد العابد بصري، كان الغالب عليه من الأحوال ترك اختياره ولزوم عجزه وافتقاره','15694','407','0');
INSERT INTO [t10495] VALUES (536,'قادم الديلمي ومنهم قادم الديلمي صحب الفضيل بن عياض وأقرانه سلك مسلكه في الخضوع والخشوع','15696','407','0');
INSERT INTO [t10495] VALUES (537,'أحمد بن الغمر ومنهم أحمد بن الغمر المحفوظ من اللهو والزمر والمؤيد بالثبات والصبر','15699','407','0');
INSERT INTO [t10495] VALUES (538,'بشر بن بشار ومنهم بشر بن بشار المجاشعي كان من السائحين، مذكور في طبقة القائمين','15701','407','0');
INSERT INTO [t10495] VALUES (539,'مجاهد الصوفي ومنهم مجاهد الصوفي كان من المستأنسين بذكره المستوحشين من غيره','15703','407','0');
INSERT INTO [t10495] VALUES (540,'أبو الأبيض ومنهم المكنى بأبي الأبيض، الوحيد عن الخلق أعرض، وماله قدم وأقرض، وألزم ما الحق عليه أوجب وفرض','15705','407','0');
INSERT INTO [t10495] VALUES (541,'أحمد الميموني، وأحمد الموصلي ومنهم أحمد الميموني وأحمد الموصلي، كانا من عباد الشاميين كانا متواخيين شربا شراب المشتاقين','15707','407','0');
INSERT INTO [t10495] VALUES (542,'عريف اليماني ومنهم عريف اليماني فارق الأشقاص والأشخاص احترازا من الإعراض والانتقاص','15709','407','0');
INSERT INTO [t10495] VALUES (543,'عرفجة الكوفي ومنهم عرفجة الكوفي مشهور في القانتين معروف في العابدين','15711','407','0');
INSERT INTO [t10495] VALUES (544,'عمرو البجلي ومنهم عمرو بن جرير البجلي كان مجذوبا ثم صار محبوبا','15713','407','0');
INSERT INTO [t10495] VALUES (545,'محمد بن أبي القاسم ومنهم محمد بن أبي القاسم الهاشمي مولاهم كان من المؤانسين بذكره والمشهورين بالإجابة في دعوته','15715','407','0');
INSERT INTO [t10495] VALUES (546,'سباع الموصلي ومنهم سباع الموصلي له الحظ النفيس في التمتع برياض التأنيس','15717','407','0');
INSERT INTO [t10495] VALUES (547,'محمد النميري ومنهم محمد بن سباع النميري كان من المشتهرين بذكره والمستأنسين بروحه','15719','407','0');
INSERT INTO [t10495] VALUES (548,'مسكين الصوفي ومنهم مسكين بن عبيد الصوفي صحب أصحاب إبراهيم بن أدهم فسلك مسلكه في التوحيد والزهد','15721','407','0');
INSERT INTO [t10495] VALUES (549,'أبو أيوب ومنهم أبو أيوب مولى بني هاشم صحب الحكماء من العباد وأخذ عنهم عدة المنقلب والمعاد','15723','407','0');
INSERT INTO [t10495] VALUES (550,'أبو عبد الله البراني ومنهم أبو عبد الله البراني من مشاهير المتعبدين معدود في جماهير المعتبرين','15725','407','0');
INSERT INTO [t10495] VALUES (551,'أحمد بن موسى الثقفي ومنهم أحمد بن موسى الثقفي كان شاعرا أديبا فصار صابرا أريبا رغب عن الدنيا بعد أن كان لها وامقا وأقبل على المعاد وصار للتزود عاشقا له الأبيات في ذم الدنيا والمغرورين بها أنشدنيها أبي قال: أنشدني أبو الحسن الفهري قال: أنشدنا أبو بكر','15727','407','0');
INSERT INTO [t10495] VALUES (552,'أبو محرز الطفاوي ومنهم أبو محرز الطفاوي تشمر في العبادة ولحق المتقدمين في الوفادة','15728','407','0');
INSERT INTO [t10495] VALUES (553,'خثيم العجلي ومنهم خثيم بن جحشة العجلي العابد نبه على خدع العاجلة فرغب عنها وجلي له حقيقة الآجلة فبادر إليها فوعظ خطاب الدنيا وذمها','15730','407','0');
INSERT INTO [t10495] VALUES (554,'الحسن الحفري ومنهم المتعبد المقرئ الحسن بن أبي جعفر الحفري أيد في الدءوب والاجتهاد وأمد بمؤانسة مؤمني الجن من العباد','15732','407','0');
INSERT INTO [t10495] VALUES (555,'حازم الحنفي ومنهم حازم الحنفي كان عبدا ذاكرا مغلوبا وكان رأسه من الشجاج معصوبا','15734','407','0');
INSERT INTO [t10495] VALUES (556,'قيس بن السكن ومنهم قيس بن السكن حبس نفسه ولسانه سجنا','15736','407','0');
INSERT INTO [t10495] VALUES (557,'الحكم بن أبان ومنهم الحكم بن أبان كان في سؤدده مجتهدا ومع السابحين مسبحا','15738','407','0');
INSERT INTO [t10495] VALUES (558,'أبو إسحاق التيمي ومنهم أبو إسحاق التيمي القرشي كان بغرور الدنيا عارفا وعنها راحلا وعازفا ولها ذاما وواصفا','15740','407','0');
INSERT INTO [t10495] VALUES (559,'أبو كريمة العبدي ومنهم أبو كريمة العبدي كان بأوقاته ضنينا ويجد لفوتها منه حنينا','15742','407','0');
INSERT INTO [t10495] VALUES (560,'علي بن ثابت ومنهم علي بن ثابت كان من العمال وكان يحث المريدين على رفض الأثقال ونبذ الأشغال','15744','407','0');
INSERT INTO [t10495] VALUES (561,'سليمان بن حيان الأحمر ومنهم الراوي الأنور الموصي أصفياءه بالحظ الأوفر أبو خالد سليمان بن حيان الأحمر','15746','407','0');
INSERT INTO [t10495] VALUES (562,'محمد بن معاوية ومنهم محمد بن معاوية الصوفي التزم نصيحة الحكيم فصفي وعوفي','15748','407','0');
INSERT INTO [t10495] VALUES (563,'مغيث الأسود ومنهم مغيث الأسود الواعظ بالأجود والمذكر بالأوكد','15750','407','0');
INSERT INTO [t10495] VALUES (564,'محمد بن صالح التيمي ومنهم محمد بن صالح التيمي ذو القلب الحاضر واللب الوافر','15752','407','0');
INSERT INTO [t10495] VALUES (565,'علي بن الحسن ومنهم علي بن الحسن بن موسى كان للحكم واعيا ومن العمال راويا','15754','407','0');
INSERT INTO [t10495] VALUES (566,'خطاب العابد ومنهم خطاب العابد، عن الخطايا شارد، وللراحات طارد','15756','407','0');
INSERT INTO [t10495] VALUES (567,'أبو جعفر المحولي ومنهم أبو جعفر المحولي الباكي الشاكي المعولي كان من قدماء العارفين من أهل بغداد سكن باب المحول فنسب إليه كان له الحال الرفيع والقول الصحيح','15758','407','0');
INSERT INTO [t10495] VALUES (568,'عمر الصوفي ومنهم عمر الصوفي قطع البوادي خاليا واعتذر إلى مولاه باكيا','15760','407','0');
