INSERT INTO [t10495] VALUES (572,'الكريم أبو هاشم ومنهم الكريم أبو هاشم للمال قاسم وللبخل قاصم وللغيظ كاظم','15768','407','0');
INSERT INTO [t10495] VALUES (573,'مسعود الجهمي ومنهم مسعود بن الحارث الجهمي العابد المجتهد المرضي','15771','407','0');
