INSERT INTO [t10495] VALUES (575,'محمد بن إسحاق ومنهم المتشمر للحاق المتحرز من الفراق المتجرد للسباق الكوفي أبو عبد الله محمد بن إسحاق , كان على فوت الساعات ضنينا ويجد من فوت وقته أنينا وحسرة وحنينا','15784','407','0');
INSERT INTO [t10495] VALUES (576,'القاسم بن محمد ومنهم القاسم بن محمد بن سلمة الصوفي كان لنفسه حافظا وبحكم الرهبانية لافظا','15790','407','0');
INSERT INTO [t10495] VALUES (577,'يزيد بن يزيد ومنهم الساجد الحميد الحامد الشديد , يزيد بن يزيد','15793','407','0');
INSERT INTO [t10495] VALUES (578,'الخادم ومنهم الخادم المخدوم الحائد عن المعلوم المكتفي بمن يوجد الموجود من المعدوم','15795','407','0');
INSERT INTO [t10495] VALUES (579,'الفرار ومنهم الفرار الجآر الذي لا يقر له قرار خوفا من الغفلة والاغترار','15797','407','0');
INSERT INTO [t10495] VALUES (580,'الديلمي ومنهم الديلمي المأسور المصلوب المحبوس المحبوب الوصيف المكروب','15799','407','0');
INSERT INTO [t10495] VALUES (581,'أمية بن الصامت ومنهم أمية بن الصامت العابد القانت في العوارض ثابت , ولنفسه عاتب ولشيطانه شامت','15801','407','0');
INSERT INTO [t10495] VALUES (582,'هلال بن الوزير ومنهم هلال بن الوزير , المعتدل المستجير إلى مولاه العليم الخبير','15803','407','0');
INSERT INTO [t10495] VALUES (583,'محارب بن حسان ومنهم محارب بن حسان , فتى الفتيان المحفوظ عن النقص والخسران المتحصن بحصن اليقين والإيمان','15805','407','0');
INSERT INTO [t10495] VALUES (584,'أبو عمرو المروزي ومنهم أبو عمرو المروزي الحكيم المفوض أمره إلى السميع العليم','15807','407','0');
INSERT INTO [t10495] VALUES (585,'إبراهيم بن سعد ومنهم المعروف بالآيات , الموصوف بالكرامات إبراهيم بن سعد العلوي له الوصاية النبوية','15809','407','0');
INSERT INTO [t10495] VALUES (586,'أبو محرز ومنهم من سلك مسالك الأكياس أبو محرز الحارس للخواطر والأنفاس','15812','407','0');
INSERT INTO [t10495] VALUES (587,'داود بن هلال ومنهم النصيبي داود بن هلال المنقطع إلى الجبال والتلال كان من المقبلين رافعا ومن فضول الدنيا واضعا','15814','407','0');
INSERT INTO [t10495] VALUES (588,'مسكين الصوفي ومنهم مسكين بن عبيد الصوفي حليف الأحزان الناقل كلام الأئمة والإخوان','15816','407','0');
INSERT INTO [t10495] VALUES (589,'العباس بن المؤمل ومنهم أبو الوليد العباس بن المؤمل الصوفي , امتحن فصبر في محنته فعوفي , راحته في البكاء والأحزان ومفزعه إلى المقابر والجبان','15818','407','0');
INSERT INTO [t10495] VALUES (590,'مغيث الأسود ومنهم مغيث الأسود آثر الأدوم والأجود وحبب إليه الأحمد والأعود','15820','407','0');
INSERT INTO [t10495] VALUES (591,'القلانسي ومنهم المؤانسي أبو عبد الله القلانسي كان بالعهد وافيا فكان الحق له في المعاطب ناجيا','15822','407','0');
INSERT INTO [t10495] VALUES (592,'شبل المدري ومنهم شبل المدري لوحظ باللطف فبرئ','15824','407','0');
INSERT INTO [t10495] VALUES (593,'عبد الله بن دينار ومنهم أبو محمد بن عبد الله بن دينار , صان الأسرار , وحفظ بالأنوار','15826','407','0');
INSERT INTO [t10495] VALUES (594,'مساور المغربي ومنهم مساور المغربي مستوطن الفيافي الأبي','15828','407','0');
INSERT INTO [t10495] VALUES (595,'الفرج بن سعيد ومنهم أبو روح الفرج بن سعيد الصوفي , لزم طريق الأئمة والأوتاد , ونقل عنهم ما يتعالج به العباد','15830','407','0');
INSERT INTO [t10495] VALUES (596,'أبو اليمان ومنهم أبو اليمان قرين الخير الحبر بن سليمان','15832','407','0');
INSERT INTO [t10495] VALUES (597,'حيان الأسود ومنهم حيان الأسود','15834','407','0');
INSERT INTO [t10495] VALUES (598,'أبو الفضل الهاشمي ومنهم أبو الفضل الهاشمي','15836','407','0');
INSERT INTO [t10495] VALUES (599,'إبراهيم المغربي ومنهم إبراهيم المغربي','15838','407','0');
INSERT INTO [t10495] VALUES (600,'أبو تراب الرملي ومنهم أبو تراب الرملي','15840','407','0');
INSERT INTO [t10495] VALUES (601,'سعيد الشهيد ومنهم سعيد الشهيد المقنع في الحديد المشتاق إلى رؤية المنعم المجيد','15842','407','0');
INSERT INTO [t10495] VALUES (602,'سيار النباجي ومنهم سيار النباجي الباكي النائح المناجي','15844','407','0');
INSERT INTO [t10495] VALUES (603,'أحمد بن روح ومنهم أحمد بن روح المستغيث بالمولى من حلول البلوى','15846','407','0');
INSERT INTO [t10495] VALUES (604,'جابر الرحبي ومنهم جابر الرحبي له الأحوال الرفيعة والألطاف البديعة','15848','407','0');
INSERT INTO [t10495] VALUES (605,'عبد الله بن خبيق ومنهم الصادق الواثق المشمر اللاحق عبد الله بن خبيق تذوق بالصفاء وتحقق بالوفاء تخرج على يوسف بن أسباط فأعرض عن الشبهات وأماط , سكن من الثغور أنطاكية','15851','407','0');
INSERT INTO [t10495] VALUES (606,'سهل بن عبد الله فمنهم الشيخ المسكين الناصح الأمين الناطق بالفضل، الرصين أبو محمد سهل بن عبد الله بن يونس بن عيسى بن عبد الله بن رفيع التستري تخرج عن خاله محمد بن سوار ولقي أبا الفيض ذا النون المصري بالحرم كان عامة كلامه في تصفية الأعمال وتنقية الأحوال','15906','407','0');
INSERT INTO [t10495] VALUES (607,'سهل بن عبد الله بن الفرحان قال الشيخ رضي الله تعالى عنه: ومنهم الطاهر المطهر أبو طاهر سهل بن عبد الله الفرحان الأسفهرديري قرية من ربض المدينة مدينة أصبهان رحمة الله تعالى عليه كان مجاب الدعوة، لقي أحمد بن عاصم الأنطاكي، وأحمد بن أبي الحواري، وأبا','15991','407','0');
INSERT INTO [t10495] VALUES (608,'أحمد بن مسروق قال الشيخ: ومنهم المستأنس بالحق المستوحش من الخلق أبو العباس الطوسي أحمد بن محمد بن مسروق من ساكني بغداد صحب الحارث بن أسد المحاسبي ومحمد بن منصور الطوسي، والسري بن المغلس السقطي، ومحمد بن الحسين البرجلاني','15995','407','0');
INSERT INTO [t10495] VALUES (609,'محمد بن منصور ومنهم الطوسي محمد بن منصور رضي الله تعالى عنه كان قلبه باليقين معمورا وفي محبته بمأموله مسرورا وعن كل من سواه مأخوذا ومأسورا','16009','407','0');
INSERT INTO [t10495] VALUES (610,'أبو تراب ومنهم أبو تراب عسكر بن الحصين، وقيل ابن محمد بن الحصيني النخشبي صاحب حاتم الأصم، ولقي أبا حمزة العطار البصري، معروف بالتوكل والسياحة والفتوة توفي بالبادية ونهشته السباع سنة خمس وأربعين ومائتين، صحبه أبو بكر بن أبي عاصم النبيل، وأبو عبد الله','16025','407','0');
INSERT INTO [t10495] VALUES (611,'أبو إسحاق الآجري فمنهم أبو إسحاق الآجري إبراهيم بغدادي له الآيات العجيبة والكرامات اللطيفة','16038','407','0');
INSERT INTO [t10495] VALUES (612,'القاسم الجريري ومنهم القاسم الجريري كان في حاله مسددا ومن أسباب الدنيا مجردا كان بشر بن الحارث يزوره فيما أخبرت عن عبد الله بن مسلم، قال: دخل بشر بن الحارث على القاسم الجريري عائدا في مرضه فوجد تحت رأسه لبنة طارحا نفسه على قطعة بازية خلقة فلما خرج من','16041','407','0');
INSERT INTO [t10495] VALUES (613,'أبو يعقوب الزيات ومن أقرانه أبو يعقوب الزيات: كان مغتنما لوقته مشتغلا بنفسه يراعي خطراته ويشتغل بخلواته، كان جماعة النساك يعظمون حاله','16042','407','0');
INSERT INTO [t10495] VALUES (614,'أبو جعفر بن الكوفي ومنهم أبو جعفر بن الكوفي رحمه الله تعالى سمعت أبا الحسن بن مقسم، يرفع منه جدا وأنه فاق أقرانه في الاجتهاد وكثرة الأوراد، أكثر نساك بغداد تأدبوا به وتوارثوا منه شريف الآداب وحميد الأخلاق','16044','407','0');
INSERT INTO [t10495] VALUES (615,'أبو هاشم الزاهد ومنهم أبو هاشم الزاهد كان إلى الحق وافدا وعن الخلق حائدا وفيما سوى الحق زاهدا، من أقران أبي عبد الله بن أبي جعفر البراثي','16047','407','0');
INSERT INTO [t10495] VALUES (616,'العباس بن مساحق ومنهم العباس بن مساحق المخزومي كان في المحبة محمولا وإلى المحبوب مرتحلا منقولا','16052','407','0');
INSERT INTO [t10495] VALUES (617,'عبيد الله العمري ومنهم المتخلي من الدنيا المتزود فيها للعقبى عبيد الله بن عبد الله العمري','16054','407','0');
INSERT INTO [t10495] VALUES (618,'علي بن معبد ومنهم المعاتب بالعتاب لاستهانته بالتراب، علي بن معبد المنبه بالصواب','16057','407','0');
INSERT INTO [t10495] VALUES (619,'علي بن رزين ومنهم الممكن المكين أبو الحسن علي بن رزين كان عن الأطعمة والأشربة معدولا وفي المشاهدة مقبولا ومحمولا تخرج به أبو عبد الرحمن المغربي أستاذ إبراهيم بن شيبان','16060','407','0');
INSERT INTO [t10495] VALUES (620,'عمرو النيسابوري ومنهم أبو حفص عمرو بن سلمة النيسابوري، وقيل عمر كان أحد المتحققين له الفتوة الكاملة والمروءة الشاملة تخرج به عامة الأعلام النيسابوريين منهم أبو عثمان النيسابوري، وشاه الكرماني، صحب عبيد الله الأباوردي، وكان من رفقاء أحمد بن خضرويه','16062','407','0');
INSERT INTO [t10495] VALUES (621,'حمدون بن أحمد قال الشيخ: ومن أقران أبي حفص من شيوخ نيسابور الشيخ الصالح أبو صالح حمدون بن أحمد بن عمارة، صحب أبا تراب النخشبي وكان فقيها على مذهب الثوري، وهو شيخ الملامتيين','16070','407','0');
INSERT INTO [t10495] VALUES (622,'محمد بن الفضل قال الشيخ: ومن حكماء المشرق من المتأخرين جماعة منهم أبو عبد الله محمد بن الفضل بن العباس بلخي الأصل سكن سمرقند صحب أحمد بن خضرويه المروزي، وسمع الحديث الكثير من قتيبة بن سعيد ومن في طبقته','16075','407','0');
INSERT INTO [t10495] VALUES (623,'محمد بن علي الترمذي ومنهم أبو عبد الله الترمذي محمد بن علي بن الحسن، صحب أبا تراب النخشبي ولقي يحيى بن الجلاء، له التصانيف المشهورة، كتب الحديث، مستقيم الطريقة، يرد على المرجئة وغيرها من المخالفين تابع للآثار','16080','407','0');
INSERT INTO [t10495] VALUES (624,'أبو بكر الوراق ومنهم الحكيم أبو بكر محمد بن عمر الوراق البلخي له الكتب في المعاملات','16087','407','0');
INSERT INTO [t10495] VALUES (625,'شاه الكرماني ومنهم أبو الفوارس الكرماني شاه بن شجاع تعرى من الأغراض تحرزا من الأعراض كان من أبناء الملوك وتشمر للسلوك تخفف للاستباق متحققا بالاشتياق، صحب أبا تراب النخشبي، وأبا عبيد البسري، كان ظريفا في الفتوة عريفا في المروءة','16095','407','0');
INSERT INTO [t10495] VALUES (626,'يوسف الرازي ومنهم المتخلي من رؤية الناس المتحلي بالإخلاص خيفة رب الناس تارك للتزين والتصنع مفارق للتلون والتمتع أبو يعقوب يوسف بن الحسين الرازي، كان وحيدا فريدا وعلى المتنطعين شديدا صحب ذا النون المصري، وأبا تراب النخشبي، وأبا سعيد الخزاز','16105','407','0');
INSERT INTO [t10495] VALUES (627,'سعيد بن إسماعيل ومنهم العارف الفاصح والعابد الناصح، كان بالحكم منطقيا فصيحا وللمريدين شفيقا نصيحا علمهم الآداب الرفيعة ونبههم على ملازمة الشريعة، كان إلى موافقة الحق مجذوبا وعن حظوظ النفس مطهرا مسلوبا أبو عثمان سعيد بن إسماعيل بن سعيد الحيري، رازي','16128','407','0');
INSERT INTO [t10495] VALUES (628,'أحمد بن عيسى ومنهم العارف المعروف الكامل، بالبيان موصوف، له الكتب المذكورة والأجوبة المشهورة أبو سعيد الخزاز أحمد بن عيسى، صحب ذا النون ونظراءه، انتشرت بركاته على أصحابه ومتبعيه، سيد من تكلم في علم الفناء والبقاء','16149','407','0');
INSERT INTO [t10495] VALUES (629,'أحمد النوري ومنهم أبو الحسين أحمد بن محمد المعروف بالنوري أحد الأئمة له اللسان الجاري بالبيان الصافي في أسرار المتوجهين إلى الباري لقي أحمد بن أبي الحواري وصحب سريا السقطي، يعرف بابن البغوي سمعت عبد المنعم بن حيان، يحكي عن أبي سعيد الأعرابي، محنته','16160','407','0');
INSERT INTO [t10495] VALUES (630,'الجنيد بن محمد الجنيد ومنهم المربي بفنون العلم المؤيد بعيون الحلم المنور بخالص الإيقان وثابت الإيمان العالم بمودع الكتاب والعامل بحلم الخطاب، الموافق فيه للبيان والصواب أبو القاسم الجنيد بن محمد الجنيد: كان كلامه بالنصوص مربوطا وبيانه بالأدلة مبسوطا،','16177','407','0');
INSERT INTO [t10495] VALUES (631,'محمد بن يعقوب ومنهم العارف بالأصول العازف عن الفضول له القلب الخاشع والأذن السامع أحكم علم الآثار وأتقنها وألف في المعاملات والأحوال وأوضحها أبو جعفر محمد بن يعقوب بن الفرجي صحب الحارث بن أسد المحاسبي وطبقته، له مصنفات في معاني الصوفية، (كتاب الورع)','16271','407','0');
INSERT INTO [t10495] VALUES (632,'عمرو بن عثمان المكي ومنهم العارف البصير والعالم الخبير له اللسان الشافي، والبيان الكافي، معدود في الأولياء محمود في الأطباء، أحكم الأصول وأخلص في الوصول، أبو عبد الله عمرو بن عثمان المكي، ساح في البلاد وباح بالوداد وصحب الأصفياء من العباد','16281','407','0');
INSERT INTO [t10495] VALUES (633,'رويم بن أحمد ومنهم الفطن المكين، له البيان والتبيين والرأي المتين رويم بن أحمد أبو الحسن الأمين، كان بالقرآن عالما وبالمعاني عارفا وعلى الحقائق عاكفا قلد بفصل الخطاب ولم تؤثر فيه العلل والأسباب، كان سمي جده رويم بن يزيد المقرئ الراوي عن ليث بن سعد،','16288','407','0');
INSERT INTO [t10495] VALUES (634,'أحمد بن محمد بن عطاء ومنهم العامل الظريف والكامل النظيف كان مودع القرآن شعاره وظاهر البيان دثاره له اللسان المبسوط والبيان بالحق مربوط، أوقف على مراتب المأسورين ومقامات أهل البلاء من المأخوذين فتمنى ما خصوا به من الصفاء والاعتلاء فعومل بما تمنى من المحن','16295','407','0');
INSERT INTO [t10495] VALUES (635,'إبراهيم بن السري','16310','407','0');
INSERT INTO [t10495] VALUES (636,'بدر المغازلي وأما بدر المغازلي فأطبقت الألسنة من الحنبلية وأصحاب الحديث أنه كان يعد من البدلاء، عرف له أحوال عجيبة','16313','407','0');
INSERT INTO [t10495] VALUES (637,'القلانسي قال الشيخ: وأما أبو أحمد القلانسي فمخصوص بالتواضع والفتوة والاحتمال وطيبة القلب والابتذال صحب أبا حمزة وتخرج عليه','16315','407','0');
INSERT INTO [t10495] VALUES (638,'خير النساج وأما أبو الحسن خير النساج، كان من أهل سامراء سكن بغداد وصحب أبا حمزة، والسري السقطي، له الحظ الجسيم في الكرامات','16319','407','0');
INSERT INTO [t10495] VALUES (639,'أبو بكر بن مسلم وأما أبو بكر بن مسلم فمن المستأنسين بالله لا ينفك عن مشاهدته، ومذاكرته، كان الجنيد من تلاميذه','16323','407','0');
INSERT INTO [t10495] VALUES (640,'سمنون بن حمزة قال الشيخ: ومنهم سمنون بن حمزة أبو الحسن الخواص، وقيل أبو بكر بصري سكن بغداد ومات قبل الجنيد سمى نفسه سمنون الكذاب وكان سبب ذلك أبياته التي قال فيها: فليس لي في سواك حظ فكيف ما شئت فامتحني، فحصر بوله من ساعته فسمى نفسه سمنون الكذاب','16326','407','0');
INSERT INTO [t10495] VALUES (641,'علي بن الموفق','16337','407','0');
INSERT INTO [t10495] VALUES (642,'أبو عثمان الوراق وأما أبو عثمان الوراق فله العبادة المشهورة، كان الإمام أحمد بن حنبل يحمد سيرته، كان للفقر معتنقا ولا يرى الإمساك والادخار، يتبع آثار ما درج عليه الصدر الأول من صفوة الصحابة وأهل الصفة ويقول بالإيثار والمواساة، أكثر نجوم البغداديين به','16341','407','0');
INSERT INTO [t10495] VALUES (643,'أبو أيوب الحمال وأما أبو أيوب الحمال فمن المجتهدين ومن الأسخياء له كرامات عجيبة','16342','407','0');
INSERT INTO [t10495] VALUES (644,'أبو عبد الله الجلاء وأما أبو عبد الله الجلاء أحمد بن يحيى فهو بغدادي سكن الرملة، صحب ذا النون وأبا تراب، وأباه يحيى الجلاء، له النكت اللطيفة، أحد أئمة القوم، لم يكن بالشام في حاله له شبيه مذكور، تخرج به جماعة من المذكورين','16345','407','0');
INSERT INTO [t10495] VALUES (645,'ابن أبي الورد وأما محمد بن محمد بن أبي الورد، وقيل أحمد، فمن جلة المشايخ وكبارهم، صحب بشرا الحافي، والحارث بن أسد المحاسبي، وسريا السقطي، محله في الورع محل شيوخه وأئمته','16351','407','0');
INSERT INTO [t10495] VALUES (646,'صدقة المقابري وأما صدقة المقابري فمن أقران المتقدمين كبشر بن الحارث وطبقته وكان من التحقق والتحفظ بالمحل العالي','16359','407','0');
INSERT INTO [t10495] VALUES (647,'طاهر المقدسي ومنهم طاهر المقدسي صاحب ذا النون وأعلام النساك من الشاميين وغيرهم','16362','407','0');
INSERT INTO [t10495] VALUES (648,'نصر الصامت ومنهم المبالغ في الرياضة المتابع في السياسة قمع هواه وكفي عناه العابد القانت المعروف بنصر الصامت','16370','407','0');
INSERT INTO [t10495] VALUES (649,'محمد بن إبراهيم البغدادي ومنهم المتوكل السابح والمتجرد الرائح كان لفنون العلم جامعا وكلامه للقلوب نافعا شيخ القوم ولسانهم في المحبة والشوق والأنس والقرب وموارد القلوب ومعاني الخطوب وصفاء الذكر ونقاء السر يحث على تصحيح الأعمال والتخفف عن الأثقال، جالس','16374','407','0');
INSERT INTO [t10495] VALUES (650,'حسن المسوحي ومنهم حسن المسوحي كان من العاملين بالتحقيق والقائمين بالتصديق أحكم علم الأصول وسهل له سبيل الوصول','16380','407','0');
INSERT INTO [t10495] VALUES (651,'أبو عبد الله البراثي ومنهم أبو عبد الله البراثي صاحب النكت المرضية والأحوال الزكية من كبار المشايخ ومتقدميهم','16383','407','0');
INSERT INTO [t10495] VALUES (652,'أبو شعيب البراثي ومنهم أبو شعيب البراثي ذو الأحوال العالية من متقدمي شيوخ بغداد','16386','407','0');
INSERT INTO [t10495] VALUES (653,'بنان البغدادي ومنهم بنان البغدادي، وقيل واسطي سكن مصر كان بالمعروف أمارا وللديان ذكارا أمر أمير مصر ابن طولون بمعروف فوجد عليه فأغراه أبو عبد الله القاضي عليه حتى ضربه سبع درر، وألقاه إلى السبع فدعا على أبي عبيد الله فحبسه ابن طولون بدل كل درة سنة','16388','407','0');
INSERT INTO [t10495] VALUES (654,'إبراهيم الخواص ومنهم المتبتل المتوكل تبتل عن الخلق وتوكل على الحق أبو إسحاق إبراهيم بن أحمد الخواص، له في التوكل الحال المشهور والذكر المنشور','16396','407','0');
INSERT INTO [t10495] VALUES (655,'أبو عبد الله خاقان ومنهم من يسبى بسره الفتيان ويجذب بدعوته من الخسران إلى الرجحان وكان ذا بيان وبرهان أبو عبد الله خاقان','16413','407','0');
INSERT INTO [t10495] VALUES (656,'إبراهيم المارستاني ومنهم المعلم المفهم أبو إسحاق إبراهيم بن أحمد المارستاني كان الجنيد له مؤاخيا وعليه حاميا وحانيا، وذلك أن الجنيد بلغه أن بعض المتأولين زين له تأويلا فمال إليه فكتب إليه الجنيد رسالة','16416','407','0');
INSERT INTO [t10495] VALUES (657,'أبو جعفر المجذوم ومن الأتقياء الأبرياء والضعفاء الأقوياء الأخفياء الأولياء المجذوم أبو جعفر، كان مسكينا خاضعا فكان الحق له معينا صانعا','16419','407','0');
INSERT INTO [t10495] VALUES (658,'أبو عبد الله المغربي ومنهم أبو عبد الله المغربي، وكان من المعمرين، صحب علي بن رزين قيل إنه توفي عن مائة وعشرين سنة وقبره بجبل طور سيناء عند قبر أستاذه علي بن رزين، كان من المحققين له النكت الوثيقة والاستغاثة على الطريقة','16421','407','0');
INSERT INTO [t10495] VALUES (659,'عبد الرحيم بن عبد الملك ومنهم عبد الرحيم بن عبد الملك: كان من المتحققين الواثقين، صحب المتقدمين من أصحاب السري وبشر','16424','407','0');
INSERT INTO [t10495] VALUES (660,'محمد السمين ومنهم الفاتك الأمين القوي المكين المعروف بمحمد السمين','16426','407','0');
INSERT INTO [t10495] VALUES (661,'محمد بن سعيد القرشي ومنهم أبو عبد الله محمد بن سعيد القرشي، ذو البيان الشافي واللسان الوافي','16429','407','0');
INSERT INTO [t10495] VALUES (662,'علي السامري ومنهم القارئ التالي الساري إلى المعالي الموافق للباري علي بن الحسين السامري: ثابت في قصده واف بعهده','16433','407','0');
INSERT INTO [t10495] VALUES (663,'أبو جعفر الحداد ومنهم أبو جعفر الحداد المتشمر في التزود والاجتهاد صحب أبا تراب وأكابر العباد','16435','407','0');
INSERT INTO [t10495] VALUES (664,'أبو جعفر الكبير، وأبو الحسن الصغير ومنهم المعروفان بالمزينين: الكبير أبو جعفر والصغير أبو الحسن، جاورا الحرم سنين عدة وماتا بمكة كانا جميعا من الاجتهاد متمتعين وبالعبادة متنعمين','16439','407','0');
INSERT INTO [t10495] VALUES (665,'أبو أحمد القلانسي ومنهم الحفي المؤانسي أبو أحمد القلانسي كان ذا فتوة كاملة ومروءة شاملة','16444','407','0');
INSERT INTO [t10495] VALUES (666,'أبو سعيد القرشي ومنهم أبو سعيد القرشي، كان بالعلل والآفات عارفا وعنها ناهيا وواقفا','16448','407','0');
INSERT INTO [t10495] VALUES (667,'أبو يعقوب الزيات ومنهم أبو يعقوب الزيات خلع الراحة والسبات احترازا من الفجيعة بالبيات','16451','407','0');
INSERT INTO [t10495] VALUES (668,'أبو جعفر الكتاني ومنهم أبو جعفر الكتاني، كان بذكره متنعما ولساعاته مغتنما جاور الحرم سنين، ومكن من الخدمة للمقام المكين','16454','407','0');
INSERT INTO [t10495] VALUES (669,'أبو بكر الزقاق ومنهم أبو بكر الزقاق كان مؤيدا بالألطاف والأرفاق','16457','407','0');
INSERT INTO [t10495] VALUES (670,'أبو عبد الله الحضرمي ومنهم أبو عبد الله الحضرمي، كان للعلائق مفارقا وبالحقائق ناطقا','16460','407','0');
INSERT INTO [t10495] VALUES (671,'عبد الله الحداد ومنهم أبو محمد عبد الله بن محمد الرازي يعرف بالحداد، كان عن حظه حائدا ولمشهوده شاهدا','16462','407','0');
INSERT INTO [t10495] VALUES (672,'أبو عمرو الدمشقي ومنهم أبو عمرو الدمشقي، مكن في الولاية واتصلت له الرعاية، كان للمكارم فاعلا وعليها حافظا أعرض عن المتروحين إلى الأرواح ونظر إلى صنع مالك الأجسام والأشباح','16464','407','0');
INSERT INTO [t10495] VALUES (673,'أبو نصر المحب ومنهم أبو نصر المحب بغدادي كان للعروض بذولا وعن العوائق محمولا','16469','407','0');
INSERT INTO [t10495] VALUES (674,'أبو سالم الدباغ ومنهم أبو سالم الدباغ كان من المتحققين والمجتهدين، صحب الكبار وكان يعد من الأبرار','16472','407','0');
INSERT INTO [t10495] VALUES (675,'أبو محمد الجريري ومنهم أبو محمد الجريري كان للأثقال حمولا وعن القواطع ذبولا، وكان للحكمة من غير أهلها صائنا وللمدعين والمتكسبين بها شائنا','16474','407','0');
INSERT INTO [t10495] VALUES (676,'ابن الفرغاني ومنهم الواسطي محمد بن موسى أبو بكر المعروف بابن الفرغاني، صحب الجنيد والنوري وانتقل إلى خراسان وسكن مرو، عالم بالأصول والفروع ألفاظه بديعة وإشاراته رفيعة كان يقول: ابتلينا بزمان ليس فيه آداب الإسلام ولا أخلاق الجاهلية ولا أحلام ذوي المروءة','16480','407','0');
INSERT INTO [t10495] VALUES (677,'أبو علي الجورجاني ومنهم الحبر الرباني الحسن بن علي أبو علي الجورجاني له البيان الشافي والكلام الوافي','16486','407','0');
INSERT INTO [t10495] VALUES (678,'أبو عبد الله السجزي ومنهم أبو عبد الله السجزي المعتبر الفكري','16489','407','0');
INSERT INTO [t10495] VALUES (679,'محفوظ بن محمود ومنهم المذعن للمعبود الواثق بالودود النيسابوري محفوظ بن محمود','16492','407','0');
INSERT INTO [t10495] VALUES (680,'ابن طاهر الأبهري ومنهم الأبهري أبو بكر بن طاهر ظهر من حجابه الساتر وغمر في جنابه العامر، رايات الكرام له مرفوعة وطوارق الإياس عنه موضوعة بسط لسانه في وجود الموجود وكرم المنعم المحمود','16495','407','0');
INSERT INTO [t10495] VALUES (681,'أبو بكر الأبهري ومنهم المطوعي أبو بكر بن عيسى الأبهري، كان من المفوضين وتعلو أحواله على السالكين والسائحين','16501','407','0');
INSERT INTO [t10495] VALUES (682,'أبو الحسن الصائغ ومنهم أبو الحسن الصائغ الدينوري سكن مصر، كان في المعاملة مخلصا وعن النظر إلى سوى الحق معرضا','16503','407','0');
INSERT INTO [t10495] VALUES (683,'ممشاد الدينوري ومنهم الدينوري ممشاد حارس همته العالية وغارس خطراته الآتية','16505','407','0');
INSERT INTO [t10495] VALUES (684,'أبو إسحاق القصار ومنهم الرقي إبراهيم بن داود أبو إسحاق القصار، ذو الهم المخزون والبيان الموزون','16507','407','0');
INSERT INTO [t10495] VALUES (685,'أبو عبد الله بن بكر ومنهم الصبيحي أبو عبد الله الحسين بن عبد الله بن بكر، له العقل الرصين والكلام الواضح المبين، وصحبه والدي بالبصرة قبل انتقاله إلى السوس، له المصنفات في أحوال القوم بعبارات لطيفة وإشارات بديعة، وبلغني أنه لزم سريا في داره بالبصرة','16510','407','0');
INSERT INTO [t10495] VALUES (686,'المرتعش ومنهم عبد الله بن محمد أبو محمد المعروف بالمرتعش كانت له المشاهدة باطنة، والمثابرة سابقة','16511','407','0');
INSERT INTO [t10495] VALUES (687,'النهرجوري ومنهم أبو يعقوب إسحاق بن محمد النهرجوري، كان ذا نور زاهر وحضور شاهر','16514','407','0');
INSERT INTO [t10495] VALUES (688,'أبو علي الروذباري ومنهم أبو علي الروذباري أحمد بن محمد بن مقسم له اللسان الفصيح والبيان النجيح، بغدادي انتقل إلى مصر وتوفي بها','16516','407','0');
INSERT INTO [t10495] VALUES (689,'أبو بكرالكتاني ومنهم أبو بكر محمد بن علي بن جعفر الكتاني، بغدادي سكن مكة يعرف بسراج الحرم، صحب الجنيد والخزاز والنوري','16525','407','0');
INSERT INTO [t10495] VALUES (690,'ابن فاتك ومنهم أبو عبد الله بن فاتك، من المراقبين، لزم الثغور ملتزما للشهود والحضور سئل عن المراقبة، فقال: إذا كنت فاعلا فانظر نظر الله إليك وإذا كنت قائلا فانظر سمع الله إليك وإذا كنت ساكتا فانظر علم الله فيك، قال الله تعالى: إنني معكما أسمع وأرى','16530','407','0');
INSERT INTO [t10495] VALUES (691,'ابن علان ومنهم أبو عبد الله بن علان محفوظ عن التلوين والنقلان','16531','407','0');
INSERT INTO [t10495] VALUES (692,'سهل الأنباري ومنهم سهل بن وهبان الأنباري من أقران الجنيد','16533','407','0');
INSERT INTO [t10495] VALUES (693,'عبد الله بن دينار ومنهم عبد الله بن دينار، واعي الخطرات وراعي اللحظات','16535','407','0');
INSERT INTO [t10495] VALUES (694,'أبو علي الوراق ومنهم أبو علي الوراق، عارف بالآفات، مسلم من الشبهات','16537','407','0');
INSERT INTO [t10495] VALUES (695,'ابن الكاتب ومنهم الحسن بن أحمد بن أبي علي المعروف بابن الكاتب، من شيوخ المصريين','16539','407','0');
INSERT INTO [t10495] VALUES (696,'القرميسيني ومنهم القرميسيني مظفر له اللفظ المحبر أحد مشايخ الجبل عرف العلل واحترز من الزلل','16542','407','0');
INSERT INTO [t10495] VALUES (697,'إبراهيم بن شيبان ومنهم القرميسيني إبراهيم بن شيبان أيد باليقين والإيقان وحفظ من التصنع والتزين بالعرفان، كان من المتمسكين بالقرآن والبيان','16548','407','0');
INSERT INTO [t10495] VALUES (698,'أبو الحسين بن بنان ومنهم الواله السكران أبو الحسين بن بنان شيخ مصر مات في التيه والها صحب أبا سعيد الخزاز','16552','407','0');
INSERT INTO [t10495] VALUES (699,'علي الفارسي ومنهم الحاضر الفارسي أبو الحسين علي بن هند الفارسي، صحب عمرا المكي والجنيد وجعفرا الحذاء','16555','407','0');
INSERT INTO [t10495] VALUES (700,'الحسين بن علي بن يزدانيار ومنهم المتمسك بالتنصل والاعتذار أبو بكر الحسين بن علي بن يزدانيار له لسان في لزوم الظواهر وتحقيق بمناجاته ما يعرض من الخواطر في السواتر','16559','407','0');
INSERT INTO [t10495] VALUES (701,'إبراهيم بن أحمد المولد ومنهم المثبت المؤيد إبراهيم بن أحمد المولد، صحب أبا عبد الله الجلاء وإبراهيم بن داود القصار الرقي، وكان يقول: حلاوة الطاعات للمخلص مذهبة لوحشة العجب','16563','407','0');
INSERT INTO [t10495] VALUES (702,'علي بن عبد الحميد ومنهم علي بن عبد الحميد العطائري المجتهد الزائري له الأحوال البديعة والأعمال الرفيعة','16570','407','0');
INSERT INTO [t10495] VALUES (703,'سعيد بن عبد العزيز ومنهم سعيد بن عبد العزيز الحلبي سكن دمشق صحب سريا السقطي، أحد الأوتاد ومن علماء العباد، تخرج له عدة من الأعلام: إبراهيم بن المولد وطبقته ملازم للشرع متبع له','16573','407','0');
INSERT INTO [t10495] VALUES (704,'أبو بكر الشبلي ومنهم المجتذب الولهان المستلب السكران الوارد العطشان، اجتذب عن الكدور والأغيار، واستلب إلى الحضر والأنوار وسقي بالدنان، وارتهن ممتلئا ريان، أبو بكر الشهير بالشبلي','16575','407','0');
INSERT INTO [t10495] VALUES (705,'ابن الأعرابي فمنهم الأغر الأبلج أبو سعيد أحمد بن محمد بن زياد المعروف بابن الأعرابي بصري نزيل مكة توفي سنة إحدى وأربعين وثلاثمائة، له التصانيف المشهورة','16611','407','0');
INSERT INTO [t10495] VALUES (706,'أبو عمرو الزجاجي ومنهم أبو عمرو الزجاجي محمد بن إبراهيم نيسابوري الأصل سكن مكة حج قريبا من ستين حجة لم يتغوط في الحرم أربعين سنة وهو مقيم بها توفي سنة ثمان وأربعين وثلاثمائة','16615','407','0');
INSERT INTO [t10495] VALUES (707,'محمد بن عليان ومنهم محمد بن علي النسوي يعرف بمحمد بن عليان، رفيع الهمة له الكرامات الظاهرة','16618','407','0');
INSERT INTO [t10495] VALUES (708,'أحمد بن أبي سعدان ومنهم أبو بكر أحمد بن محمد بن أبي سعدان بغدادي الأصل، كان ذا لسان وبيان كان في علوم الشرع أحد الأعلام، ينتحل للشافعي وله في علم العمال والعباد اللسان الشافي أقام بطرسوس مدة فبعث رسولا إلى الروم لكمال حاله وبيانه','16620','407','0');
INSERT INTO [t10495] VALUES (709,'أبو الخير الأقطع ومنهم أبو الخير الأقطع التيتاتي له الآيات، توفي بعد الأربعين، كان السباع والهوام يأنسون بمجالسته ويأوون إليه، كان ينسج الخوص بإحدى يديه','16623','407','0');
INSERT INTO [t10495] VALUES (710,'أبو عبد الله البصري ومنهم أبو عبد الله محمد بن أحمد بن سالم البصري صاحب سهل بن عبد الله التستري، وحفظ كلامه سلك مسلك أستاذه سهل وابنه أبي الحسن، أدركته وله أصحاب ينتسبون إليه كان أبو عبد الله يقول: " من عامل الله على رؤية السبق ظهرت عليه الكرامات وكان','16628','407','0');
INSERT INTO [t10495] VALUES (711,'أبو الحسن البوشنجي ومنهم أبو الحسن علي بن أحمد بن الحسن البوشنجي، سكن نيسابور له البيان الشافي في المعارف والتوحيد وله الفتوة والتجريد، توفي سنة ثمان وأربعين وثلاثمائة','16630','407','0');
INSERT INTO [t10495] VALUES (712,'القاسم السياري ومنهم أبو العباس القاسم السياري، الملقن تحف الباري، شيخ المراوزة ومحدثهم وفقيههم توفي سنة اثنين وأربعين','16635','407','0');
INSERT INTO [t10495] VALUES (713,'جعفر الخلدي ومنهم جعفر بن محمد بن نصير الخلدي أبو محمد الخواص السائح اللامح القوام، المزين بالأخلاق الحميدة والآخذ بالوثائق الأكيدة، كتب الآثار وصحب الأخيار: الجنيد والثوري ورويما، حج سنين توفي سنة ثمان وأربعين وثلاثمائة','16638','407','0');
INSERT INTO [t10495] VALUES (714,'أبو بكر الطمستاني ومنهم أبو بكر الطمستاني العالم الرباني صحب الأعلام والأكابر ونبه به الأعلام والأصاغر، قدم أصبهان وخرج منها إلى نيسابور وتوفي بها سنة أربعين وثلاثمائة','16645','407','0');
INSERT INTO [t10495] VALUES (715,'أبو العباس أحمد الدينوري ومنهم أبو العباس أحمد بن محمد الدينوري، صحب يوسف بن الحسين ولقي رويما وأبا العباس بن عطاء','16647','407','0');
INSERT INTO [t10495] VALUES (716,'أحمد بن عطاء ومنهم أبو عبد الله أحمد بن عطاء بن أحمد الروذباري له من فنون العلم الحظ الجزيل توفي بصور سنة تسع وخمسين وثلاثمائة، ورد علينا نعيه وأنا مقيم بمكة','16649','407','0');
INSERT INTO [t10495] VALUES (717,'بندار بن الحسن ومنهم أبو الحسين بندار بن الحسن بن محمد بن المهلب، كان بعلم الأصول مهذبا وفي الحقائق مقربا، كان له القلب العقول واللسان السئول، وكان للمخلصين عضدا وللمريدين مسددا توفي سنة ثلاث وخمسين وثلاثمائة، وحضر مجالسه أبو زرعة الطبري، شيرازي','16652','407','0');
INSERT INTO [t10495] VALUES (718,'ابن حفيف ومنهم أبو عبد الله محمد بن حفيف، الحنيف الظريف، له الفصول في النصول، والتحقق والتثبت في الوصول لقي الأكابر والأعلام، صحب رويما وأبا العباس بن عطاء وطاهرا المقدسي وأبا عمرو الدمشقي، وكان شيخ الوقت حالا وعلما، توفي سنة إحدى وسبعين وثلاثمائة','16655','407','0');
INSERT INTO [t10495] VALUES (719,'النعمان بن عبد السلام فمن المتقدمين الذين ذكرناهم في كتاب طبقات المحدثين والرواة من أهل أصبهان النعمان بن عبد السلام أبو المنذر، كان عبد السلام والده يلي أمر السلطان ومات عن ضيعة نفيسة ومال جم، فترك ذلك كله ورغب عنها زهدا فيها، صحب سفيان الثوري ومالك','16662','407','0');
INSERT INTO [t10495] VALUES (720,'ابن معدان ويليه في الفضل والعلم والعبادة محمد بن يوسف بن معدان بن سليم عروس الزهاد، وقد تقدم ذكره، وكذلك أخواه عبد الرحمن وعبد العزيز، وتوفي محمد بن يوسف بالمصيصة ودفن إلى جنب مخلد بن الحسين، فارق ضياعه زاهدا فيها وكان يقول: لقد خاب من كان حظه من','16664','407','0');
INSERT INTO [t10495] VALUES (721,'عامر بن حمدويه ومنهم عامر بن حمدويه الزاهد، سكن مسيلة، صحب سفيان الثوري وسمعته يروي عنه مسائل','16665','407','0');
INSERT INTO [t10495] VALUES (722,'عصام بن يزيد ومنهم عصام بن يزيد بن عجلان أبو سعيد الملقب بخير، صحب سفيان الثوري ثلاث عشرة سنة وكان رسوله إلى أمير المؤمنين المهدي فعرض عليه المهدي برا ومالا فلم يقبل ثم رجع من عنده إلى سفيان فقال لسفيان: لو أتيتهم؟ فقال: أتراني أخاف هوانهم؟ إنما','16666','407','0');
INSERT INTO [t10495] VALUES (723,'موسى بن مساور ومنهم موسى بن مساور أبو الهيثم الضبي، روى عن سفيان بن عيينة، ووكيع، وكان جيدا فاضلا ترك ما ورثه عن أبيه لإخوته تورعا ولم يتناول منه شيئا لأن أباه كان يتولى السلطان، له الآثار المشهورة في بناء الرباطات وإصلاح الطرق','16667','407','0');
INSERT INTO [t10495] VALUES (724,'محمد بن الوليد ومنهم محمد بن الوليد الأموي من أهل المدينة سمع سفيان بن عيينة، يعد من الأبدال، له الدعوة المجابة','16669','407','0');
INSERT INTO [t10495] VALUES (725,'محمد بن النعمان ومنهم محمد بن النعمان بن عبد السلام صحب وكيعا، وسفيان بن عيينة، وأبا بكر بن عياش، له الورع الثخين والعقل الرصين، كان زيد بن أخرم يسميه عابد أهل أصبهان، كان دأبه المجاهدة والمكابدة الدائمة حتى ضعف وخيف على عقله، ثم رجع إلى الميسور','16670','407','0');
INSERT INTO [t10495] VALUES (726,'صالح بن مهران ومنهم أبو سفيان صالح بن مهران كان يقال له الحكيم، يكتب كلامه قال سليمان الشاذكوني: ما رأيت أورع من أبي سفيان','16673','407','0');
INSERT INTO [t10495] VALUES (727,'عبد الله بن خالد ومنهم عبد الله بن خالد، كان من التعبد والورع بالمحل الرفيع فأكره على قضاء البلد لقي سفيان بن عيينة، وشعيب بن حرب، وإبراهيم بن أبي بكر الشيباني','16676','407','0');
INSERT INTO [t10495] VALUES (728,'رجاء بن صهيب ومنهم أبو غسان رجاء بن صهيب الجرواني أحد المعرضين عن الدنيا الراحلين عنها، وكان يقول: نعم الدار الدنيا طريقا إلى الجنة ومن اتخذ الدنيا طريقا لم يعرج على ما فيها، فالدنيا طريق الأكياس غنموا فيها النفوس ورحلوا بها عنها','16678','407','0');
INSERT INTO [t10495] VALUES (729,'عبد الله بن داود ومنهم عبد الله بن داود سنديلة كان من المتعبدين خيرا فاضلا مجاب الدعوة، أسند الكثير، يحدث عن الحسين بن حفص','16679','407','0');
INSERT INTO [t10495] VALUES (730,'إبراهيم بن عيسى ومنهم إبراهيم بن عيسى الزاهد، صحب معروفا الكرخي وسمع من أبي داود الطيالسي ومحمد بن المقرئ','16681','407','0');
INSERT INTO [t10495] VALUES (731,'عبد الوهاب الضبي ومنهم عبد الوهاب بن المنذر الضبي، فقيه عابد صوام قوام كان له كل يوم ختمة، كان هذا دأبه إلى أن مات، روى عن معتمر بن سليمان سمعت أبي يقول: حكي لي عنه أنه قال: لكل شيء أول، وأول الخير الاستغفار، قال تعالى: استغفروا ربكم إنه كان','16684','407','0');
INSERT INTO [t10495] VALUES (732,'حامد شاذة ومنهم حامد بن المسبور بن الحسين المؤذن مؤذن الجامع يعرف بشاذة، كان يعرف بالدعاء المجاب من الأمناء والنصحاء، حدث عن سليمان بن حرب، وأزهر بن سعيد','16685','407','0');
INSERT INTO [t10495] VALUES (733,'أسيد بن عاصم ومنهم أبو الحسين أسيد بن عاصم بن محمد، كان هو وأخوه محمد بن علي ممن سلكوا مسلك أصحاب سفيان الثوري في العلم والعبادة ومكارم الأخلاق وفواضل الأعمال، يفزع إلى أدعيتهم عند نزول المحن والأغلال فترى الإجابة في الوقت، يقصدون من الديار والنواحي','16687','407','0');
INSERT INTO [t10495] VALUES (734,'أبو جعفر الفريابي ومنهم أحمد بن معاوية بن الهذيل أبو جعفر الفريابي وأخوه الهذيل بن معاوية، كان سمتهما في التعبد والاتباع والاقتداء سمت البدلاء والأولياء، سمعا الحديث من أصحاب الثوري والحسين بن حفص وغيره','16690','407','0');
INSERT INTO [t10495] VALUES (735,'أحمد بن محمد بن إسحاق ومنهم المقرون تعبده وتقشفه بالبذل والسخاء أبو عثمان أحمد بن محمد بن إسحاق بن يزيد بن عجلان ختن ابن رجاء بن صهيب، كانت العبادة عنه مشهورة، والكرم عنه مأثور ومذكور، كان كثير الحديث','16696','407','0');
INSERT INTO [t10495] VALUES (736,'موسى الخزاز ومنهم الناسك النبيه ذو الفضل الكثير أبو عبد الرحمن موسى بن عبد الرحمن الخزاز سمعت أبا محمد بن حيان، يقول: كان له الفضل والعبادة والنسك الكثير وكان تخلى في داره مستأنسا بذكره ومشاهدته، أسند الكثير','16698','407','0');
INSERT INTO [t10495] VALUES (737,'أحمد بن مهدي منهم ذو الدين المتين والمحدث الأمين، أنفق على العلم المال الكثير، المنور المنير آثار الرسول البشير النذير كان ذا سخاء وكرم، راقب المعبود وخدم، حليف العبادة والسهر أليف السنة والأثر أبو جعفر أحمد بن مهدي بن رستم','16700','407','0');
INSERT INTO [t10495] VALUES (738,'محمد بن معروف العطار قال الشيخ: ومن المشهورين بالنسك والعبادة والورع محمد بن معروف العطار المعروف بمؤملة، كان إمام الجامع سمع من يحيى بن سعيد القطان، ويزيد بن هارون، وهو الذي ينسب إليه المسجد، مسجد مؤملة بن معروف','16705','407','0');
INSERT INTO [t10495] VALUES (739,'هارون الراعي ومنهم أبو عبد الرحمن الراعي هارون بن سعيد كان من الزاهدين والسائحين، لقي بالشام أبا سليمان الداراني، ومحمد بن المبارك الصوري وأحمد بن عاصم الأنطاكي، حدث عنه أبو مسعود الرازي في مسنده، سمع من عبد الرحمن بن إبراهيم بن دحيم، ومحمد بن أبي','16707','407','0');
INSERT INTO [t10495] VALUES (740,'العباس بن إسماعيل ومنهم أبو الفضل العباس بن إسماعيل الطامدي كان من العبادة والخلوة بالمحل المكين مع ما كان يرجع إليه من العلم الواسع النافع','16710','407','0');
INSERT INTO [t10495] VALUES (741,'زكريا بن الصلت ومنهم زكريا بن الصلت له الورع الوثيق والقلب الرفيق مشهور بالتعبد والاجتهاد والتوحد والانفراد وكان يقول: ما شافع أشفع للرجل المذنب من الخدمة لرب العالمين وكان يقول: من نظر إلى مبتدع بعينه فقد أعان النظر على العمى ألا فجنبوا أشفار العيون','16714','407','0');
INSERT INTO [t10495] VALUES (742,'الأخوان عبد الله وهمام ومنهم الأخوان أبو بكر عبد الله، وأبو عمرو همام ابنا محمد بن النعمان بن عبد السلام ورثا العلم والعبادة عن أسلافهما المشهورين، الغالب على أبي بكر القدوة والرواية، وعلى أبي عمرو العبادة والرعاية حالهما في العلم والنسك مشهور وفضلهما','16716','407','0');
INSERT INTO [t10495] VALUES (743,'محمد بن الفرج الودنكاني ومنهم المعد في الأبدال المثبت في الأحوال كانت دعوته مجابة صحب أبا عثمان الرازي سعيد بن العباس أبو بكر محمد بن الفرج الودنكاني، وكان الجهاد والرباط ميسرا له، وكان من دعائه: اللهم اقبضني في أحب المواطن إليك، فخرج إلى طرسوس ثلاث','16719','407','0');
INSERT INTO [t10495] VALUES (744,'ابن معدان ومنهم ذو القلب الرجيف، واللب الثاقب الحصيف، والنفس الذائب النحيف عرف مالكه عظيما فخنع وخضع وراقبه عليما، فخشي وخشع ولاحظه كريما فرضي وقنع فابتهل إليه مستغفرا ومفتقرا ولامح صنائعه معتبرا، وتنصل إليه من زلله وهفواته معتذرا موقنا أنه على قبوله','16724','407','0');
INSERT INTO [t10495] VALUES (745,'أبو الحسن بن سهل ومنهم المحبر بالوصل المحفوظ في الفضل أبو الحسن علي بن سهل، كان للحق مجيبا واصلا، وعن النفس مغيبا راحلا','16732','407','0');
INSERT INTO [t10495] VALUES (746,'أحمد بن جعفر بن هانئ ومنهم المملوء من المعاني المكلوء من التواني أحمد بن جعفر بن هانئ كان له الأحوال الرفيعة والاستدلال بالأعمدة المنيعة المتفكر في البراهين والآيات، والمعتبر بالمنصوب من الأدلة والعلامات، كان شأنه السباق والبدار مرتقبا لموارد القلوب من','16739','407','0');
INSERT INTO [t10495] VALUES (747,'محمد بن الحسين الخشوعي ومنهم المزين بالخشوع الممكن للخضوع كانت العبادة حرفته والتلذذ بالعبرة شهوته له الكلام البليغ في تأديب النساك والعباد تخرج به جماعة من السباق والرواد، منهم أبو الحسن علي بن أحمد بن المرزبان الأسواري وطبقته وسليم بن عبد الله بن','16743','407','0');
