INSERT INTO [t10649] VALUES (1048,'ومن نوى الصوم، ثم جن أو أغمي عليه جميع النهار، ولم يفق جزءا منه لم يصح صومه، لا إن نام جميع النهار، ويلزم المغمى عليه القضاء فقط','2390','1035','0');
