INSERT INTO [t10649] VALUES (1136,'وإن لبس ذكر مخيطا فدى','2692','1132','0');
INSERT INTO [t10649] VALUES (1137,'وإن طيب بدنه، أو ثوبه، أو ادهن بمطيب، أو شم طيبا، أو تبخر بعود ونحوه فدى،','2703','1132','0');
INSERT INTO [t10649] VALUES (1138,'وإن قتل صيدا مأكولا بريا أصلا ولو تولد منه ومن غيره، أو تلف في يده فعليه جزاؤه','2707','1132','0');
INSERT INTO [t10649] VALUES (1139,'ولا يحرم حيوان إنسي ولا صيد البحر، ولا قتل محرم الأكل، ولا الصائل','2711','1132','0');
INSERT INTO [t10649] VALUES (1140,'ويحرم عقد نكاح ولا يصح','2717','1132','0');
INSERT INTO [t10649] VALUES (1141,'ولا فدية وتصح الرجعة','2721','1132','0');
INSERT INTO [t10649] VALUES (1142,'وإن جامع المحرم قبل التحلل الأول فسد نسكهما، ويمضيان فيه، ويقضيانه ثاني عام','2722','1132','0');
INSERT INTO [t10649] VALUES (1143,'وتحرم المباشرة، فإن فعل فأنزل لم يفسد حجه وعليه بدنة لكن يحرم من الحل لطواف الفرض','2728','1132','0');
INSERT INTO [t10649] VALUES (1144,'وإحرام المرأة كالرجل إلا في اللباس، وتجتنب البرقع والقفازين، وتغطية وجهها ويباح لها التحلي','2730','1132','0');
INSERT INTO [t10649] VALUES (1145,'باب الفدية','2733','1106','0');
INSERT INTO [t10649] VALUES (1146,'يخير بفدية حلق، وتقليم، وتغطية رأس وطيب ولبس مخيط بين صيام ثلاثة أيام أو إطعام ستة مساكين لكل مسكين مد بر، أو نصف صاع تمر أو شعير أو ذبح شاة','2733','1145','0');
INSERT INTO [t10649] VALUES (1147,'وبجزاء صيد بين مثل إن كان، أو تقويمه بدراهم يشتري بها طعاما، فيطعم كل مسكين مدا، أو يصوم عن كل مد يوما وبما لا مثل له بين إطعام وصيام','2737','1145','0');
INSERT INTO [t10649] VALUES (1148,'وأما دم متعة وقران، فيجب الهدي فإن عدمه فصيام ثلاثة أيام','2740','1145','0');
INSERT INTO [t10649] VALUES (1149,'والأفضل كون آخرها يوم عرفة','2743','1145','0');
INSERT INTO [t10649] VALUES (1150,'وسبعة إذا رجع إلى أهله','2746','1145','0');
INSERT INTO [t10649] VALUES (1151,'والمحصر إذا لم يجد هديا صام عشرة ثم حل ويجب بوطء في فرج في الحج بدنة، وفي العمرة شاة وإن طاوعته زوجته لزمها','2747','1145','0');
INSERT INTO [t10649] VALUES (1152,'فصل','2756','1145','0');
INSERT INTO [t10649] VALUES (1153,'ومن كرر محظورا من جنس، ولم يفد فدى مرة بخلاف صيد، ومن فعل محظورا من أجناس فدى لكل مرة رفض إحرامه أو لا','2756','1145','0');
INSERT INTO [t10649] VALUES (1154,'ويسقط بنسيان فدية لبس وطيب وتغطية رأس','2759','1145','0');
INSERT INTO [t10649] VALUES (1155,'دون وطء وصيد وتقليم','2761','1145','0');
INSERT INTO [t10649] VALUES (1156,'وحلاق','2764','1145','0');
INSERT INTO [t10649] VALUES (1157,'وكل هدي، أو إطعام فلمساكين الحرم','2769','1145','0');
INSERT INTO [t10649] VALUES (1158,'وفدية الأذى، واللبس ونحوهما، ودم الإحصار حيث وجد سببه','2771','1145','0');
INSERT INTO [t10649] VALUES (1159,'ويجزئ الصوم بكل مكان، والدم شاة، أو سبع بدنة وتجزيء عنها بقرة','2774','1145','0');
INSERT INTO [t10649] VALUES (1160,'باب جزاء الصيد','2777','1106','0');
INSERT INTO [t10649] VALUES (1161,'في النعامة بدنة','2777','1160','0');
INSERT INTO [t10649] VALUES (1162,'وحمار الوحش، وبقرته، والأيل، والثيتل والوعل بقرة، والضبع كبش، والغزال عنز، والوبر والضب جدي، واليربوع جفرة','2778','1160','0');
INSERT INTO [t10649] VALUES (1163,'والأرنب عناق، والحمامة شاة','2780','1160','0');
INSERT INTO [t10649] VALUES (1164,'باب صيد الحرم','2781','1106','0');
INSERT INTO [t10649] VALUES (1165,'يحرم صيده على المحرم والحلال','2781','1164','0');
INSERT INTO [t10649] VALUES (1166,'وحكم صيده كصيد المحرم ويحرم قطع شجره وحشيشه الأخضرين إلا الإذخر','2783','1164','0');
INSERT INTO [t10649] VALUES (1167,'ويحرم صيد المدينة، ولا جزاء فيه، ويباح الحشيش للعلف، وآلة الحرث ونحوه وحرمها ما بين عير إلى ثور','2787','1164','0');
INSERT INTO [t10649] VALUES (1168,'باب دخول مكة','2794','1106','0');
INSERT INTO [t10649] VALUES (1169,'يسن من أعلاها، والمسجد من باب بني شيبة فإذا رأى البيت رفع يديه، وقال ما ورد','2794','1168','0');
INSERT INTO [t10649] VALUES (1170,'ثم يطوف مضطبعا يبتدئ المعتمر بطواف العمرة، والقارن والمفرد للقدوم','2796','1168','0');
INSERT INTO [t10649] VALUES (1171,'فيحاذي الحجر الأسود بكله، ويستلمه، ويقبله فإن شق قبل يده، فإن شق اللمس أشار إليه','2798','1168','0');
INSERT INTO [t10649] VALUES (1172,'ويقول ما ورد ويجعل البيت عن يساره','2804','1168','0');
INSERT INTO [t10649] VALUES (1173,'ويطوف سبعا يرمل الأفقي في هذا الطواف ثلاثا ثم يمشي أربعا يستلم الحجر والركن اليماني كل مرة','2807','1168','0');
INSERT INTO [t10649] VALUES (1174,'ومن ترك شيئا من الطواف أو لم ينوه أو نسكه أو طاف على الشاذروان أو جدار الحجر أو عريان أو نجس لم يصح','2815','1168','0');
INSERT INTO [t10649] VALUES (1175,'ثم يصلي ركعتين خلف المقام','2829','1168','0');
INSERT INTO [t10649] VALUES (1176,'فصل','2833','1168','0');
INSERT INTO [t10649] VALUES (1177,'ثم يستلم الحجر، ويخرج إلى الصفا من بابه فيرقاه حتى يرى البيت','2833','1176','0');
INSERT INTO [t10649] VALUES (1178,'ويكبر ثلاثا، ويقول ما ورد، ثم ينزل ماشيا إلى العلم الأول، ثم يسعى شديدا إلى الآخر','2834','1176','0');
INSERT INTO [t10649] VALUES (1179,'ثم يمشي ويرقى المروة ويقول ما قاله على الصفا، ثم ينزل فيمشي في موضع مشيه، ويسعى في موضع سعيه إلى الصفا يفعل ذلك سبعا، ذهابه سعية، ورجوعه سعية، فإن بدأ بالمروة سقط الشوط الأول','2838','1176','0');
INSERT INTO [t10649] VALUES (1180,'وتسن فيه الطهارة والستارة والموالاة ثم إن كان متمتعا لا هدي معه قصر من شعره وتحلل، وإلا حل إذا حج، والمتمتع إذا شرع في الطواف قطع التلبية','2840','1176','0');
INSERT INTO [t10649] VALUES (1181,'باب صفة الحج والعمرة','2847','1106','0');
INSERT INTO [t10649] VALUES (1182,'يسن للمحلين بمكة الإحرام بالحج يوم التروية قبل الزوال منها','2847','1181','0');
INSERT INTO [t10649] VALUES (1183,'ويجزئ من بقية الحرم ويبيت بمنى','2849','1181','0');
INSERT INTO [t10649] VALUES (1184,'فإذا طلعت الشمس سار إلى عرفة وكلها موقف إلا بطن عرنة','2851','1181','0');
INSERT INTO [t10649] VALUES (1185,'ويسن أن يجمع بين الظهر والعصر، ويقف راكبا عند الصخرات وجبل الرحمة ويكثر الدعاء، مما ورد','2856','1181','0');
INSERT INTO [t10649] VALUES (1186,'ومن وقف ولو لحظة من فجر يوم عرفة إلى فجر يوم النحر وهو أهل له صح حجه وإلا فلا','2863','1181','0');
INSERT INTO [t10649] VALUES (1187,'ومن وقف نهارا ودفع قبل الغروب ولم يعد قبله فعليه دم ومن وقف ليلا فقط فلا،','2866','1181','0');
INSERT INTO [t10649] VALUES (1188,'ثم يدفع بعد الغروب إلى مزدلفة بسكينة ويسرع في الفجوة، ويجمع بها بين العشاءين ويبيت بها','2867','1181','0');
INSERT INTO [t10649] VALUES (1189,'وله الدفع بعد نصف الليل وقبله فيه دم، كوصوله إليها بعد الفجر، لا قبله','2872','1181','0');
INSERT INTO [t10649] VALUES (1190,'فإذا صلى الصبح أتى المشعر الحرام فيرقاه، أو يقف عنده، ويحمد الله، ويكبره، ويقرأ: {{فإذا أفضتم من عرفات}} الآيتين ويدعو حتى يسفر','2878','1181','0');
INSERT INTO [t10649] VALUES (1191,'فإذا بلغ محسرا أسرع رمية حجر وأخذ الحصى وعدده سبعون بين الحمص والبندق،','2880','1181','0');
INSERT INTO [t10649] VALUES (1192,'وصل إلى منى، وهي من وادي محسر إلى جمرة العقبة رماها بسبع حصيات متعاقبات يرفع يده حتى يرى بياض إبطه ويكبر مع كل حصاة ولا يجزئ الرمي بغيرها ولا بها ثانيا ولا يقف، ويقطع التلبية قبلها،','2884','1181','0');
INSERT INTO [t10649] VALUES (1193,'ويرمي بعد طلوع الشمس ويجزئ بعد نصف الليل، ثم ينحر هديا إن كان معه، ويحلق أو يقصر من جميع شعره وتقصر منه المرأة قدر أنملة','2892','1181','0');
INSERT INTO [t10649] VALUES (1194,'ثم قد حل له كل شيء إلا النساء والحلاق والتقصير نسك لا يلزم بتأخيره دم، ولا بتقديمه على الرمي والنحر','2895','1181','0');
INSERT INTO [t10649] VALUES (1195,'فصل','2904','1181','0');
INSERT INTO [t10649] VALUES (1196,'ثم يفيض إلى مكة، ويطوف القارن والمفرد بنية الفريضة طواف الزيارة وأول وقته بعد نصف ليلة النحر، ويسن في يومه، وله تأخيره','2904','1181','0');
INSERT INTO [t10649] VALUES (1197,'ثم يسعى بين الصفا والمروة إن كان متمتعا، أو غيره، ولم يكن سعى مع طواف القدوم ثم قد حل له كل شيء','2908','1181','0');
INSERT INTO [t10649] VALUES (1198,'ثم يشرب من ماء زمرم لما أحب ويتضلع منه ويدعو بما ورد','2912','1181','0');
INSERT INTO [t10649] VALUES (1199,'ثم يرجع فيبيت بمنى ثلاث ليالي، فيرمي الجمرة الأولى، وتلي مسجد الخيف بسبع حصيات، ويجعلها عن يساره، ويتأخر قليلا، ويدعو طويلا، ثم الوسطى مثلها','2915','1181','0');
INSERT INTO [t10649] VALUES (1200,'يفعل هذا في كل يوم من أيام التشريق بعد الزوال مستقبل القبلة مرتبا فإن رماه كله في الثالث أجزأه، ويرتبه بنيته فإن أخره عنه، أو لم يبت بها، فعليه دم','2918','1181','0');
INSERT INTO [t10649] VALUES (1201,'ومن تعجل في يومين خرج قبل الغروب، وإلا لزمه المبيت والرمي من الغد فإذا أراد الخروج من مكة لم يخرج حتى يطوف للوداع','2926','1181','0');
INSERT INTO [t10649] VALUES (1202,'فإن أقام، أو اتجر بعده أعاده وإن تركه غير حائض رجع إليه، فإن شق أو لم يرجع فعليه دم','2930','1181','0');
INSERT INTO [t10649] VALUES (1203,'وإن أخر طواف الزيارة فطافه عند الخروج أجزأ عن الوداع','2935','1181','0');
INSERT INTO [t10649] VALUES (1204,'ويقف غير الحائض بين الركن والباب داعيا بما ورد وتقف الحائض ببابه وتدعو بالدعاء','2938','1181','0');
INSERT INTO [t10649] VALUES (1205,'وتستحب زيارة قبر النبي صلى الله عليه وسلم وقبري صاحبيه','2940','1181','0');
INSERT INTO [t10649] VALUES (1206,'وصفة العمرة: أن يحرم بها من الميقات، أو من أدنى الحل من مكي ونحوه','2941','1181','0');
INSERT INTO [t10649] VALUES (1207,'لا من الحرم، فإذا طاف وسعى وقصر حل، وتباح كل وقت، وتجزئ عن الفرض','2942','1181','0');
INSERT INTO [t10649] VALUES (1208,'وأركان الحج: الإحرام، والوقوف، وطواف الزيارة والسعي وواجباته: الإحرام من الميقات المعتبر له، والوقوف بعرفة إلى الغروب والمبيت لغير أهل السقاية والرعاية بمنى ومزدلفة إلى بعد نصف الليل والرمي، والحلاق، والوداع','2946','1181','0');
INSERT INTO [t10649] VALUES (1209,'والباقي سنن، وأركان العمرة: إحرام، وطواف، وسعي، وواجباتها: الحلاق والإحرام من ميقاتها','2963','1181','0');
INSERT INTO [t10649] VALUES (1210,'فمن ترك الإحرام لم ينعقد نسكه ومن ترك ركنا غيره، أو نيته لم يتم نسكه إلا به ومن ترك واجبا فعليه دم، أو سنة فلا شيء عليه','2967','1181','0');
INSERT INTO [t10649] VALUES (1211,'باب الفوات والإحصار','2977','1106','0');
INSERT INTO [t10649] VALUES (1212,'من فاته الوقوف فاته الحج، وتحلل بعمرة ويقضي ويهدي إن لم يكن اشترط','2977','1211','0');
INSERT INTO [t10649] VALUES (1213,'ومن صده عدو عن البيت أهدى ثم حل، فإن فقده صام عشرة أيام ثم حل وإن صد عن عرفة تحلل بعمرة','2981','1211','0');
INSERT INTO [t10649] VALUES (1214,'وإن حصره مرض أو ذهاب نفقة، بقي محرما إن لم يكن اشترط','2984','1211','0');
INSERT INTO [t10649] VALUES (1215,'باب الهدي، والأضحية، والعقيقة','2987','1106','0');
INSERT INTO [t10649] VALUES (1216,'أفضلها: إبل، ثم بقر، ثم غنم ولا يجزئ فيها إلا جذع ضأن،','2987','1215','0');
INSERT INTO [t10649] VALUES (1217,'وثني سواه فالإبل خمس والبقر سنتان، والمعز سنة، والضأن نصفها، وتجزئ الشاة عن واحد، والبدنة والبقرة عن سبعة','2991','1215','0');
INSERT INTO [t10649] VALUES (1218,'ولا تجزئ العوراء، والعجفاء، والعرجاء، والهتماء، والجداء، والمريضة والعضباء بل البتراء خلقة، والجماء، وخصي غير مجبوب وما بأذنه أو قرنه قطع أقل من النصف','2995','1215','0');
INSERT INTO [t10649] VALUES (1219,'والسنة نحر الإبل قائمة معقولة يدها اليسرى فيطعنها بالحربة في الوهدة التي بين أصل العنق والصدر، ويذبح غيرها، ويجوز عكسها','3006','1215','0');
INSERT INTO [t10649] VALUES (1220,'ويقول: بسم الله، والله أكبر اللهم هذا منك ولك','3009','1215','0');
INSERT INTO [t10649] VALUES (1221,'ويتولاها صاحبها، أو يوكل مسلما ويشهدها، ووقت الذبح بعد صلاة العيد أو قدره إلى يومين بعده ويكره في ليلتهما فإن فات قضى واجبه','3021','1215','0');
INSERT INTO [t10649] VALUES (1222,'فصل','3032','1215','0');
INSERT INTO [t10649] VALUES (1223,'ويتعينان بقوله: هذا هدي أو أضحية لا بالنية','3032','1222','0');
INSERT INTO [t10649] VALUES (1224,'وإذا تعينت لم يجز بيعها، ولا هبتها إلا أن يبدلها بخير منها ويجز صوفها ونحوه إن كان أنفع لها','3034','1222','0');
INSERT INTO [t10649] VALUES (1225,'ويتصدق به ولا يعطي جازرها أجرته منها، ولا يبيع جلدها، ولا شيئا منها، بل ينتفع به','3038','1222','0');
INSERT INTO [t10649] VALUES (1226,'وإن تعيبت ذبحها وأجزأته إلا أن تكون واجبة في ذمته قبل التعيين','3041','1222','0');
INSERT INTO [t10649] VALUES (1227,'والأضحية سنة وذبحها أفضل من الصدقة بثمنها ويسن أن يأكل ويهدي ويتصدق أثلاثا وإن أكلها إلا أوقية تصدق بها جاز، وإلا ضمنها','3044','1222','0');
INSERT INTO [t10649] VALUES (1228,'ويحرم على من يضحي أن يأخذ في العشر من شعره أو بشرته شيئا','3051','1222','0');
INSERT INTO [t10649] VALUES (1229,'فصل','3056','1215','0');
INSERT INTO [t10649] VALUES (1230,'تسن العقيقة عن الغلام شاتان، وعن الجارية شاة','3056','1229','0');
INSERT INTO [t10649] VALUES (1231,'تذبح يوم سابعه فإن فات ففي أربعة عشر، فإن فات ففي إحدى وعشرين','3059','1229','0');
INSERT INTO [t10649] VALUES (1232,'تنزع جدولا ولا يكسر عظمها، وحكمها كالأضحية إلا أنه لا يجزئ فيها شرك في دم','3065','1229','0');
INSERT INTO [t10649] VALUES (1233,'ولا تسن الفرعة، ولا العتيرة','3067','1229','0');
INSERT INTO [t10649] VALUES (1234,'كتاب الجهاد','3072','0','0');
INSERT INTO [t10649] VALUES (1235,'*','3072','1234','0');
INSERT INTO [t10649] VALUES (1236,'وهو فرض كفاية ويجب إذا حضره أو حصر بلده عدو أو استنفره الإمام','3073','1235','0');
INSERT INTO [t10649] VALUES (1237,'وتمام الرباط أربعون يوما وإذا كان أبواه مسلمين لم يجاهد تطوعا إلا بإذنهما','3078','1235','0');
INSERT INTO [t10649] VALUES (1238,'ويتفقد الإمام جيشه عند المسير، ويمنع المخذل والمرجف وله أن ينفل في بدايته الربع بعد الخمس، وفي الرجعة الثلث بعده ويلزم الجيش طاعته والصبر معه','3082','1235','0');
INSERT INTO [t10649] VALUES (1239,'ولا يجوز الغزو إلا بإذنه إلا أن يفجأهم عدو يخافون كلبه','3089','1235','0');
INSERT INTO [t10649] VALUES (1240,'وتملك الغنيمة بالاستيلاء عليها في دار الحرب وهي لمن شهد الوقعة من أهل القتال، فيخرج الخمس','3092','1235','0');
INSERT INTO [t10649] VALUES (1241,'ثم يقسم باقي الغنيمة للراجل سهم، وللفارس ثلاثة أسهم، سهم له وسهمان لفرسه','3096','1235','0');
INSERT INTO [t10649] VALUES (1242,'ويشارك الجيش سراياه فيما غنمت، ويشاركونه فيما غنم والغال من الغنيمة يحرق رحله كله إلا السلاح والمصحف','3097','1235','0');
INSERT INTO [t10649] VALUES (1243,'وما فيه روح','3099','1235','0');
INSERT INTO [t10649] VALUES (1244,'إذا غنموا أرضا فتحوها بالسيف خير الإمام بين قسمها ووقفها على المسلمين، ويضرب عليها خراجا مستمرا يؤخذ ممن هي بيده','3100','1235','0');
INSERT INTO [t10649] VALUES (1245,'والمرجع في الخراج والجزية إلى اجتهاد الإمام','3102','1235','0');
INSERT INTO [t10649] VALUES (1246,'ومن عجز عن عمارة أرضه أجبر على إجارتها، أو رفع يده عنها ويجري فيها الميراث، وما أخذ من مال مشرك كجزية وخراج وعشر، وما تركوه فزعا، وخمس خمس الغنيمة، ففيء يصرف في مصالح المسلمين','3104','1235','0');
INSERT INTO [t10649] VALUES (1247,'يقول صاحب الروض المربع [(٣١)]: «فصل في الأمان والهدنة»','3109','1235','0');
INSERT INTO [t10649] VALUES (1248,'باب عقد الذمة وأحكامها','3120','1234','0');
INSERT INTO [t10649] VALUES (1249,'لا يعقد لغير المجوس وأهل الكتابين ومن تبعهم','3122','1248','0');
INSERT INTO [t10649] VALUES (1250,'ولا يعقدها إلا إمام أو نائبه','3126','1248','0');
INSERT INTO [t10649] VALUES (1251,'ولا جزية على صبي ولا امرأة ولا عبد، ولا فقير يعجز عنها،','3128','1248','0');
INSERT INTO [t10649] VALUES (1252,'ومن صار أهلا لها أخذت منه في آخر الحول، ومتى بذلوا الواجب عليهم وجب قبوله وحرم قتالهم، ويمتهنون عند أخذها، ويطال وقوفهم، وتجر أيديهم','3129','1248','0');
INSERT INTO [t10649] VALUES (1253,'فصل','3132','1248','0');
INSERT INTO [t10649] VALUES (1254,'ويلزم الإمام أخذهم بحكم الإسلام في النفس والمال والعرض','3132','1253','0');
INSERT INTO [t10649] VALUES (1255,'وإقامة الحدود عليهم فيما يعتقدون تحريمه دون ما يعتقدون حله','3133','1253','0');
INSERT INTO [t10649] VALUES (1256,'ويلزمهم التميز عن المسلمين، ولهم ركوب غير خيل بغير سرج بإكاف','3135','1253','0');
INSERT INTO [t10649] VALUES (1257,'ولا يجوز تصديرهم في المجالس، ولا القيام لهم','3137','1253','0');
INSERT INTO [t10649] VALUES (1258,'ولا بداءتهم بالسلام','3140','1253','0');
INSERT INTO [t10649] VALUES (1259,'ويمنعون من إحداث كنائس، وبيع، وبناء ما انهدم منها ولو ظلما','3144','1253','0');
INSERT INTO [t10649] VALUES (1260,'ومن تعلية بنيان على مسلم لا من مساواته له','3145','1253','0');
INSERT INTO [t10649] VALUES (1261,'ومن إظهار خمر وخنزير، وناقوس وجهر بكتابهم وإن تهود نصراني أو عكسه لم يقر ولم يقبل منه إلا الإسلام أو دينه','3147','1253','0');
INSERT INTO [t10649] VALUES (1262,'فصل','3152','1248','0');
INSERT INTO [t10649] VALUES (1263,'فإن أبى الذمي بذل الجزية أو التزام حكم الإسلام أو تعدى على مسلم بقتل أو زنا، أو قطع طريق، أو تجسس، أو إيواء جاسوس، أو ذكر الله أو رسوله أو كتابه بسوء انتقض عهده دون نسائه وأولاده، وحل دمه وماله','3152','1262','0');
INSERT INTO [t10649] VALUES (1264,'كتاب البيع','3158','0','0');
INSERT INTO [t10649] VALUES (1265,'*','3158','1264','0');
INSERT INTO [t10649] VALUES (1266,'وهو مبادلة مال ولو في الذمة أو منفعة مباحة كممر في دار بمثل أحدهما على التأبيد غير ربا وقرض','3162','1265','0');
INSERT INTO [t10649] VALUES (1267,'وينعقد بإيجاب وقبول بعده وقبله','3167','1265','0');
INSERT INTO [t10649] VALUES (1268,'متراخيا عنه في مجلسه، فإن اشتغلا بما يقطعه بطل وهي الصيغة القولية وبمعاطاة وهي الفعلية','3170','1265','0');
INSERT INTO [t10649] VALUES (1269,'ويشترط التراضي منهما، فلا يصح من مكره بلا حق','3174','1265','0');
INSERT INTO [t10649] VALUES (1270,'وأن يكون العاقد جائز التصرف فلا يصح تصرف صبي وسفيه بغير إذن ولي','3177','1265','0');
INSERT INTO [t10649] VALUES (1271,'وأن تكون العين مباحة النفع من غير حاجة كالبغل والحمار ودود القز وبزره والفيل وسباع البهائم التي تصلح للصيد إلا الكلب والحشرات والمصحف والميتة والسرجين النجس','3179','1265','0');
INSERT INTO [t10649] VALUES (1272,'والأدهان النجسة ولا المتنجسة','3190','1265','0');
INSERT INTO [t10649] VALUES (1273,'ويجوز الاستصباح بها في غير مسجد','3192','1265','0');
INSERT INTO [t10649] VALUES (1274,'وأن يكون من مالك أو من يقوم مقامه','3194','1265','0');
INSERT INTO [t10649] VALUES (1275,'فإن باع ملك غيره أو اشترى بعين ماله بلا إذنه لم يصح وإن اشترى له في ذمته بلا إذنه ولم يسمه في العقد صح له بالإجازة، ولزم المشتري بعدمها ملكا','3197','1265','0');
INSERT INTO [t10649] VALUES (1276,'ولا يباع غير المساكن مما فتح عنوة كأرض الشام ومصر والعراق بل تؤجر','3202','1265','0');
INSERT INTO [t10649] VALUES (1277,'ولا يصح بيع نقع البئر ولا ما نبت في أرضه من كلأ وشوك، ويملكه آخذه،','3206','1265','0');
INSERT INTO [t10649] VALUES (1278,'وأن يكون مقدورا على تسليمه فلا يصح بيع آبق وشارد وطير في هواء وسمك في ماء، ولا مغصوب من غير غاصبه، أو قادر على أخذه','3209','1265','0');
INSERT INTO [t10649] VALUES (1279,'وأن يكون معلوما برؤية أو صفة فإن اشترى ما لم يره، أو رآه وجهله، أو وصف له بما لا يكفي سلما لم يصح','3215','1265','0');
INSERT INTO [t10649] VALUES (1280,'ولا يباع حمل في بطن، ولبن في ضرع منفردين، ولا مسك في فأرته ولا نوى في تمره وصوف على ظهر، وفجل ونحوه قبل قلعه','3220','1265','0');
INSERT INTO [t10649] VALUES (1281,'ولا يصح بيع الملامسة والمنابذة ولا عبد من عبيده ونحوه','3225','1265','0');
INSERT INTO [t10649] VALUES (1282,'ولا استثناؤه إلا معينا وإن استثنى من حيوان يؤكل رأسه وجلده وأطرافه صح وعكسه الشحم، والحمل ويصح بيع ما مأكوله في جوفه كرمان وبطيخ، وبيع الباقلاء ونحوه في قشره والحب المشتد في سنبله','3229','1265','0');
INSERT INTO [t10649] VALUES (1283,'وأن يكون الثمن معلوما فإن باعه برقمه، أو بألف درهم ذهبا وفضة، أو بما ينقطع به السعر، أو بما باع به زيد وجهلاه أو أحدهما لم يصح','3236','1265','0');
INSERT INTO [t10649] VALUES (1284,'وإن باع ثوبا أو صبرة، أو قطيعا كل ذراع أو قفيز أو شاة بدرهم صح وإن باع من الصبرة كل قفيز بدرهم، أو بمائة درهم إلا دينارا وعكسه','3241','1265','0');
INSERT INTO [t10649] VALUES (1285,'أو باع معلوما ومجهولا يتعذر علمه ولم يقل كل منهما بكذا لم يصح فإن لم يتعذر صح في المعلوم بقسطه','3245','1265','0');
INSERT INTO [t10649] VALUES (1286,'ولو باع مشاعا بينه وبين غيره كعبد أو ما ينقسم عليه الثمن بالأجزاء صح في نصيبه بقسطه وإن باع عبده وعبد غيره بغير إذنه أو عبدا وحرا، أو خلا وخمرا صفقة واحدة، صح في عبده، وفي الخل بقسطه، ولمشتر الخيار إن جهل الحال','3247','1265','0');
INSERT INTO [t10649] VALUES (1287,'فصل','3252','1265','0');
INSERT INTO [t10649] VALUES (1288,'ولا يصح البيع ممن تلزمه الجمعة بعد ندائها الثاني ويصح النكاح وسائر العقود','3252','1287','0');
INSERT INTO [t10649] VALUES (1289,'ولا يصح بيع عصير ممن يتخذه خمرا ولا سلاح في فتنة','3260','1287','0');
INSERT INTO [t10649] VALUES (1290,'ولا عبد مسلم لكافر إذا لم يعتق عليه،','3260','1287','0');
INSERT INTO [t10649] VALUES (1291,'وإن أسلم في يده أجبر على إزالة ملكه، ولا تكفي مكاتبته، وإن جمع بين بيع وكتابة أو بيع وصرف صح في غير الكتابة','3263','1287','0');
INSERT INTO [t10649] VALUES (1292,'ويقسط العوض عليهما ويحرم بيعه على بيع أخيه كأن يقول لمن اشترى سلعة بعشرة أنا أعطيك مثلها بتسعة، وشراؤه على شرائه كأن يقول لمن باع سلعة بتسعة: عندي فيها عشرة ليفسخ ليفسخ ويعقد معه ويبطل العقد فيهما','3267','1287','0');
INSERT INTO [t10649] VALUES (1293,'ومن باع ربويا بنسيئة واعتاض عن ثمنه ما لا يباع به نسيئة أو اشترى شيئا نقدا بدون ما باع به نسيئة لا بالعكس لم يجز وإن اشتراه بغير جنسه، أو بعد قبض ثمنه، أو بعد تغير صفته أو من غير مشتريه، أو اشتراه أبوه أو ابنه جاز','3273','1287','0');
INSERT INTO [t10649] VALUES (1294,'باب الشروط في البيع','3289','1264','0');
INSERT INTO [t10649] VALUES (1295,'منها صحيح، كالرهن وتأجيل الثمن وكون العبد كاتبا، أو خصيا، أو مسلما، والأمة بكرا','3292','1294','0');
INSERT INTO [t10649] VALUES (1296,'ونحو أن يشترط البائع سكنى الدار شهرا وحملان البعير إلى موضع معين، أو شرط المشتري على البائع حمل الحطب، أو تكسيره، أو خياطة الثوب، أو تفصيله،','3298','1294','0');
INSERT INTO [t10649] VALUES (1297,'وإن جمع بين شرطين بطل البيع؛','3301','1294','0');
INSERT INTO [t10649] VALUES (1298,'ومنها فاسد يبطل العقد كاشتراط أحدهما على الآخر عقدا آخر كسلف، وقرض، وبيع، وإجارة، وصرف','3303','1294','0');
INSERT INTO [t10649] VALUES (1299,'وإن شرط أن لا خسارة عليه أو متى نفق المبيع وإلا رده، أو لا يبيع ولا يهبه ولا يعتقه أو إن أعتق فالولاء له','3308','1294','0');
INSERT INTO [t10649] VALUES (1300,'أو أن يفعل ذلك بطل الشرط وحده إلا إذا شرط العتق وبعتك على أن تنقدني الثمن إلى ثلاث وإلا فلا بيع بيننا صح، وبعتك إن جئتني بكذا، أو رضي زيد أو يقول للمرتهن: إن جئتك بحقك وإلا فالرهن لك، لا يصح البيع','3314','1294','0');
INSERT INTO [t10649] VALUES (1301,'وإن باعه وشرط البراءة من كل عيب مجهول لم يبرأ وإن باعه دارا على أنها عشرة أذرع فبانت أكثر أو أقل صح، ولمن جهله وفات غرضه الخيار','3322','1294','0');
INSERT INTO [t10649] VALUES (1302,'باب الخيار','3328','1264','0');
INSERT INTO [t10649] VALUES (1303,'وهو أقسام: الأول خيار المجلس','3328','1302','0');
INSERT INTO [t10649] VALUES (1304,'يثبت في البيع والصلح بمعناه، وإجارة، والصرف، والسلم، دون سائر العقود','3329','1302','0');
INSERT INTO [t10649] VALUES (1305,'ولكل من المتبايعين الخيار ما لم يتفرقا عرفا بأبدانهما وإن نفياه أو أسقطاه سقط وإن أسقطه أحدهما بقي خيار الآخر،','3333','1302','0');
INSERT INTO [t10649] VALUES (1306,'وإذا مضت مدته لزم البيع','3337','1302','0');
INSERT INTO [t10649] VALUES (1307,'الثاني: أن يشترطاه في العقد مدة معلومة ولو طويلة وابتداؤها من العقد','3337','1302','0');
INSERT INTO [t10649] VALUES (1308,'وإذا مضت مدته أو قطعاه بطل ويثبت في البيع والصلح بمعناه، والإجارة في الذمة، أو على مدة لا تلي العقد','3344','1302','0');
INSERT INTO [t10649] VALUES (1309,'وإن شرطاه لأحدهما دون صاحبه صح، وإلى الغد أو الليل يسقط بأوله ولمن له الخيار الفسخ ولو مع غيبة الآخر وسخطه، والملك مدة الخيارين للمشتري','3348','1302','0');
INSERT INTO [t10649] VALUES (1310,'وله نماؤه المنفصل وكسبه، ويحرم ولا يصح تصرف أحدهما في المبيع وعوضه المعين فيها','3352','1302','0');
INSERT INTO [t10649] VALUES (1311,'بغير إذن الآخر بغير تجربة المبيع إلا عتق المشتري وتصرف المشتري فسخ لخياره ومن مات منهما بطل خياره','3357','1302','0');
INSERT INTO [t10649] VALUES (1312,'الثالث: إذا غبن في المبيع غبنا يخرج عن العادة وبزيادة الناجش والمسترسل','3363','1302','0');
INSERT INTO [t10649] VALUES (1313,'الرابع: خيار التدليس، كتسويد شعر الجارية، وتجعيده، وجمع ماء الرحى، وإرساله عند عرضها','3371','1302','0');
INSERT INTO [t10649] VALUES (1314,'الخامس: خيار العيب، وهو ما ينقص قيمة المبيع كمرضه، وفقد عضو أو سن أو زيادتهما،','3377','1302','0');
INSERT INTO [t10649] VALUES (1315,'وزنا الرقيق وسرقته، وإباقه، وبوله في الفراش','3380','1302','0');
INSERT INTO [t10649] VALUES (1316,'فإذا علم المشتري العيب بعد أمسكه بأرشه، وهو قسط ما بين قيمة الصحة والعيب، أو رده وأخذ الثمن','3383','1302','0');
INSERT INTO [t10649] VALUES (1317,'وإن تلف المبيع أو عتق العبد تعين الأرش وإن اشترى ما لم يعلم عيبه بدون كسره كجوز هند، وبيض نعام فكسره فوجده فاسدا فأمسكه فله أرشه وإن رده رد أرش كسره','3386','1302','0');
INSERT INTO [t10649] VALUES (1318,'وإن كان كبيض دجاج رجع بكل الثمن، وخيار عيب متراخ ما لم يوجد دليل الرضا','3388','1302','0');
INSERT INTO [t10649] VALUES (1319,'ولا يفتقر إلى حكم ولا رضا ولا حضور صاحبه وإن اختلفا عند من حدث العيب، فقول مشتر مع يمينه، وإن لم يحتمل إلا قول أحدهما قبل بلا يمين','3390','1302','0');
INSERT INTO [t10649] VALUES (1320,'السادس: خيار في البيع بتخبير الثمن، متى بان أقل أو أكثر، ويثبت في التولية، والشركة، والمرابحة، والمواضعة ولا بد في جميعها من معرفة المشتري رأس المال','3395','1302','0');
INSERT INTO [t10649] VALUES (1321,'وإن اشترى بثمن مؤجل أو ممن لا تقبل شهادته له، أو بأكثر من ثمنه حيلة','3401','1302','0');
INSERT INTO [t10649] VALUES (1322,'أو باع بعض الصفقة بقسطها من الثمن، ولم يبين ذلك في تخبيره بالثمن، فلمشتر الخيار بين الإمساك والرد، وما يزاد في ثمن، أو يحط منه في مدة خيار أو يؤخذ أرشا لعيب، أو جناية عليه يلحق برأس ماله ويخبر به، وإن كان ذلك بعد لزوم البيع لم يلحق به وإن أخبر بالحال','3407','1302','0');
INSERT INTO [t10649] VALUES (1323,'السابع: خيار لاختلاف المتبايعين، فإذا اختلفا في قدر الثمن تحالفا، فيحلف بائع أولا ما بعته بكذا وإنما بعته بكذا، ثم يحلف المشتري ما اشتريته بكذا، وإنما اشتريته بكذا،','3412','1302','0');
INSERT INTO [t10649] VALUES (1324,'ولكل الفسخ إذا لم يرض أحدهما بقول الآخر، فإن كانت السلعة تالفة رجعا إلى قيمة مثلها فإن اختلفا في صفتها فقول مشتر، وإذا فسخ العقد انفسخ ظاهرا وباطنا','3417','1302','0');
INSERT INTO [t10649] VALUES (1325,'وإن اختلفا في أجل أو شرط فقول من ينفيه وإن اختلفا في عين المبيع تحالفا، وبطل البيع،','3422','1302','0');
INSERT INTO [t10649] VALUES (1326,'وإن أبى كل منهما تسليم ما بيده حتى يقبض العوض، والثمن عين، نصب عدل يقبض منهما، ويسلم المبيع ثم الثمن، وإن كان دينا حالا أجبر بائع ثم مشتر إن كان الثمن في المجلس','3426','1302','0');
INSERT INTO [t10649] VALUES (1327,'وإن كان غائبا في البلد حجر عليه في المبيع وبقية ماله حتى يحضره، وإن كان غائبا بعيدا عنها والمشتري معسر، فللبائع الفسخ ويثبت الخيار للخلف في الصفة ولتغير ما تقدمت رؤيته','3429','1302','0');
INSERT INTO [t10649] VALUES (1328,'فصل','3433','1302','0');
INSERT INTO [t10649] VALUES (1329,'ومن اشترى، مكيلا ونحوه صح ولزم بالعقد، ولم يصح تصرفه فيه حتى يقبضه وإن تلف قبل قبضه فمن ضمان البائع، وإن تلف بآفة سماوية بطل البيع، وإن أتلفه آدمي، خير مشتر بين فسخ وإمضاء ومطالبة متلفه ببدله','3433','1328','0');
INSERT INTO [t10649] VALUES (1330,'وما عداه يجوز تصرف المشتري فيه قبل قبضه','3443','1328','0');
INSERT INTO [t10649] VALUES (1331,'وإن تلف ما عدا المبيع بكيل ونحوه فمن ضمانه ما لم يمنعه بائع من قبضه','3447','1328','0');
INSERT INTO [t10649] VALUES (1332,'ويحصل قبض ما بيع بكيل أو وزن أو عد أو ذرع بذلك وفي صبرة، وما ينقل بنقله، وما يتناول بتناوله، وغيره بتخليته والإقالة فسخ، تجوز قبل قبض المبيع بمثل الثمن ولا خيار فيها، ولا شفعة','3452','1328','0');
INSERT INTO [t10649] VALUES (1333,'باب الربا والصرف','3459','1264','0');
INSERT INTO [t10649] VALUES (1334,'يحرم ربا الفضل في كل مكيل وموزون بيع بجنسه، ويجب فيه الحلول والقبض ولا يباع مكيل بجنسه إلا كيلا، ولا موزون بجنسه إلا وزنا ولا بعضه ببعض جزافا، فإن اختلف الجنس جازت الثلاثة','3465','1333','0');
INSERT INTO [t10649] VALUES (1335,'والجنس: ما له اسم خاص يشمل أنواعا؛ كبر ونحوه وفروع الأجناس؛ كالأدقة، والأخباز، والأدهان، واللحم أجناس باختلاف أصوله','3473','1333','0');
INSERT INTO [t10649] VALUES (1336,'وكذا اللبن واللحم والشحم والكبد أجناس، ولا يصح بيع لحم بحيوان من جنسه، ويصح بغير جنسه','3477','1333','0');
INSERT INTO [t10649] VALUES (1337,'ولا يجوز بيع حب بدقيقه ولا سويقه ولا نيئه بمطبوخه، وأصله بعصيره، وخالصه بمشوبه ورطبه بيابسه','3481','1333','0');
INSERT INTO [t10649] VALUES (1338,'ويجوز بيع دقيقه بدقيقه إذا استويا في النعومة، ومطبوخه بمطبوخه، وخبزه بخبزه إذا استويا في النشاف وعصيره بعصيره، ورطبه برطبه، ولا يباع ربوي بجنسه ومعه أو معهما من غير جنسهما','3490','1333','0');
INSERT INTO [t10649] VALUES (1339,'ولا تمر بلا نوى بما فيه نوى، ويباع النوى بتمر فيه نوى، ولبن وصوف بشاة ذات لبن وصوف','3496','1333','0');
INSERT INTO [t10649] VALUES (1340,'ومرد الكيل لعرف المدينة، والوزن لعرف مكة زمن النبي صلى الله عليه وسلم، وما لا عرف له هناك اعتبر عرفه في موضعه','3499','1333','0');
INSERT INTO [t10649] VALUES (1341,'فصل','3503','1333','0');
INSERT INTO [t10649] VALUES (1342,'ويحرم ربا النسيئة في بيع كل جنسين اتفقا في علة ربا الفضل ليس أحدهما نقدا','3503','1341','0');
INSERT INTO [t10649] VALUES (1343,'كالمكيلين والموزونين، وإن تفرقا قبل القبض بطل','3508','1341','0');
INSERT INTO [t10649] VALUES (1344,'وإن باع مكيلا بموزون جاز التفرق قبل القبض والنساء، وما لا كيل فيه ولا وزن؛ كالثياب والحيوان يجوز فيه النساء','3510','1341','0');
INSERT INTO [t10649] VALUES (1345,'ولا يجوز بيع الدين بالدين','3510','1341','0');
INSERT INTO [t10649] VALUES (1346,'فصل','3516','1333','0');
INSERT INTO [t10649] VALUES (1347,'ومتى افترق المتصارفان قبل قبض الكل أو البعض بطل العقد فيما لم يقبض','3516','1346','0');
INSERT INTO [t10649] VALUES (1348,'والدراهم والدنانير تتعين بالتعيين في العقد، فلا تبدل','3518','1346','0');
INSERT INTO [t10649] VALUES (1349,'وإن وجدها مغصوبة بطل','3520','1346','0');
INSERT INTO [t10649] VALUES (1350,'ومعيبة من جنسها أمسك أو رد','3521','1346','0');
INSERT INTO [t10649] VALUES (1351,'ويحرم الربا بين المسلم والحربي، وبين المسلمين مطلقا بدار إسلام وحرب','3522','1346','0');
INSERT INTO [t10649] VALUES (1352,'باب بيع الأصول والثمار','3529','1264','0');
INSERT INTO [t10649] VALUES (1353,'إذا باع دارا شمل أرضها، وبناءها، وسقفها، والباب المنصوب، والسلم، والرف المسمورين، والخابية المدفونة','3530','1352','0');
INSERT INTO [t10649] VALUES (1354,'دون ما هو مودع فيها من كنز وحجر ومنفصل منها كحبل ودلو وبكرة وقفل وفرش ومفتاح','3532','1352','0');
INSERT INTO [t10649] VALUES (1355,'وإن باع أرضا ولو لم يقل بحقوقها شمل غرسها وبناءها وإن كان فيها زرع كبر وشعير فلبائع مبقى،','3534','1352','0');
INSERT INTO [t10649] VALUES (1356,'وإن كان يجز أو يلقط مرارا فأصوله للمشتري، والجزة واللقطة الظاهرتان عند البيع للبائع، وإن اشترط المشتري ذلك صح','3536','1352','0');
INSERT INTO [t10649] VALUES (1357,'فصل','3539','1352','0');
INSERT INTO [t10649] VALUES (1358,'ومن باع نخلا تشقق طلعه فلبائع مبقى إلى الجذاذ إلا أن يشترطه مشتر وكذلك شجر العنب والتوت والرمان وغيره','3539','1357','0');
INSERT INTO [t10649] VALUES (1359,'وما ظهر من نوره كالمشمش والتفاح، وما خرج من أكمامه كالورد والقطن وما قبل ذلك والورق فلمشتر، ولا يباع ثمر قبل بدو صلاحه،','3544','1357','0');
INSERT INTO [t10649] VALUES (1360,'ولا زرع قبل اشتداد حبه، ولا رطبة وبقل، ولا قثاء ونحوه كباذنجان دون الأصل إلا بشرط القطع في الحال،','3547','1357','0');
INSERT INTO [t10649] VALUES (1361,'أو جزة جزة، أو لقطة لقطة والحصاد واللقاط على المشتري وإن باعه مطلقا، أو بشرط البقاء،','3552','1357','0');
INSERT INTO [t10649] VALUES (1362,'أو اشترى ثمرا لم يبد صلاحه بشرط القطع وتركه حتى بدا، أو جزة أو لقطة فنمتا أو اشترى ما بدا صلاحه وحصل آخر واشتبها أو عرية فأتمرت بطل والكل للبائع،','3554','1357','0');
INSERT INTO [t10649] VALUES (1363,'وإذا بدا ما له صلاح في الثمرة واشتد الحب جاز بيعه مطلقا وبشرط التبقية','3559','1357','0');
INSERT INTO [t10649] VALUES (1364,'وللمشتري تبقيته إلى الحصاد والجذاذ ويلزم البائع سقيه إن احتاج إلى ذلك وإن تضرر الأصل','3559','1357','0');
INSERT INTO [t10649] VALUES (1365,'وإن تلفت بآفة سماوية رجع على البائع','3561','1357','0');
INSERT INTO [t10649] VALUES (1366,'وإن أتلفه آدمي خير مشتر بين الفسخ والإمضاء ومطالبة المتلف','3563','1357','0');
INSERT INTO [t10649] VALUES (1367,'وصلاح بعض الشجرة صلاح لها ولسائر النوع الذي في البستان','3563','1357','0');
INSERT INTO [t10649] VALUES (1368,'وبدو الصلاح في ثمر النخل أن تحمر أو تصفر، وفي العنب أن يتموه حلوا','3566','1357','0');
INSERT INTO [t10649] VALUES (1369,'وفي بقية الثمر أن يبدو فيه النضج ويطيب أكله، ومن باع عبدا له مال فماله لبائعه','3566','1357','0');
INSERT INTO [t10649] VALUES (1370,'إلا أن يشترطه المشتري فإن كان قصده المال اشترط علمه وسائر شروط البيع وإلا فلا وثياب الجمال للبائع، والعادة للمشتري','3569','1357','0');
INSERT INTO [t10649] VALUES (1371,'باب السلم','3572','1264','0');
INSERT INTO [t10649] VALUES (1372,'وهو عقد على موصوف في الذمة مؤجل بثمن مقبوض بمجلس العقد ويصح بألفاظ البيع والسلم والسلف','3576','1371','0');
INSERT INTO [t10649] VALUES (1373,'بشروط سبعة: أحدها: انضباط صفاته بمكيل وموزون ومذروع وأما المعدود المختلف كالفواكه والبقول والجلود والرؤوس والأواني المختلفة الرؤوس والأوساط، كالقماقم والأسطال الضيقة الرؤوس، والجواهر والحامل من الحيوان، وكل مغشوش','3580','1371','0');
INSERT INTO [t10649] VALUES (1374,'وما يجمع أخلاطا غير متميزة كالغالية والمعاجين فلا يصح السلم فيه، ويصح في الحيوان والثياب المنسوجة من نوعين، وما خلطه غير مقصود كالجبن وخل التمر والسكنجبين ونحوها','3585','1371','0');
INSERT INTO [t10649] VALUES (1375,'الثاني: ذكر الجنس والنوع وكل وصف يختلف به الثمن ظاهرا وحداثته وقدمه','3587','1371','0');
INSERT INTO [t10649] VALUES (1376,'ولا يصح شرط الأردأ والأجود بل جيد ورديء، فإن جاء بما شرط أو أجود منه من نوعه ولو قبل محله ولا ضرر في قبضه لزمه أخذه','3591','1371','0');
INSERT INTO [t10649] VALUES (1377,'الثالث: ذكر قدره بكيل أو وزن أو ذرع يعلم، وإن أسلم في المكيل وزنا، أو في الموزون كيلا لم يصح','3597','1371','0');
INSERT INTO [t10649] VALUES (1378,'الرابع: ذكر أجل معلوم له وقع في الثمن فلا يصح حالا، ولا إلى الحصاد والجذاذ، ولا إلى يوم إلا في شيء يأخذه منه كل يوم كخبز ولحم ونحوهما','3599','1371','0');
INSERT INTO [t10649] VALUES (1379,'الخامس: أن يوجد غالبا في محله ومكان الوفاء لا وقت العقد، فإن تعذر أو بعضه فله الصبر أو فسخ الكل أو البعض، ويأخذ الثمن الموجود أو عوضه','3602','1371','0');
INSERT INTO [t10649] VALUES (1380,'السادس: أن يقبض الثمن تاما معلوما قدره ووصفه قبل التفرق، وإن قبض البعض ثم افترقا بطل فيما عداه وإن أسلم في جنس إلى أجلين، أو عكسه صح إن بين كل جنس وثمنه وقسط كل أجل','3605','1371','0');
INSERT INTO [t10649] VALUES (1381,'السابع: أن يسلم في الذمة فلا يصح في عين ويجب الوفاء موضع العقد، ويصح شرطه في غيره وإن عقد ببر أو بحر شرطاه ولا يصح بيع المسلم فيه قبل قبضه، ولا هبته ولا الحوالة به ولا عليه ولا أخذ عوضه، ولا يصح الرهن والكفيل به','3607','1371','0');
INSERT INTO [t10649] VALUES (1382,'باب القرض','3617','1264','0');
INSERT INTO [t10649] VALUES (1383,'وهو مندوب، وما يصح بيعه صح قرضه إلا بني آدم ويملك بقبضه فلا يلزم رد عينه بل يثبت بدله في ذمته حالا ولو أجله فإن رده المقترض لزم قبوله،','3618','1382','0');
INSERT INTO [t10649] VALUES (1384,'وإن كانت مكسرة أو فلوسا فمنع السلطان المعاملة بها فله القيمة وقت القرض، ويرد المثل في المثليات والقيمة في غيرها','3626','1382','0');
INSERT INTO [t10649] VALUES (1385,'فإن أعوز المثل فالقيمة إذا','3630','1382','0');
INSERT INTO [t10649] VALUES (1386,'ويحرم كل شرط جر نفعا، وإن بدأ به بلا شرط، أو أعطاه أجود، أو هدية بعد الوفاء جاز، وإن تبرع لمقرضه قبل وفائه بشيء لم تجر عادته به لم يجز، إلا أن ينوي مكافأته أو احتسابه من دينه','3632','1382','0');
INSERT INTO [t10649] VALUES (1387,'وإن أقرضه أثمانا فطالبه بها ببلد آخر لزمته، وفيما لحمله مؤونة قيمته إن لم تكن ببلد القرض أنقص','3639','1382','0');
INSERT INTO [t10649] VALUES (1388,'باب الرهن','3642','1264','0');
INSERT INTO [t10649] VALUES (1389,'يصح في كل عين يجوز بيعها حتى المكاتب مع الحق وبعده بدين ثابت،','3645','1388','0');
INSERT INTO [t10649] VALUES (1390,'ويلزم في حق الراهن فقط ويصح رهن المشاع','3651','1388','0');
INSERT INTO [t10649] VALUES (1391,'ويجوز رهن المبيع غير المكيل والموزون على ثمنه وغيره','3654','1388','0');
INSERT INTO [t10649] VALUES (1392,'وما لا يجوز بيعه لا يصح رهنه إلا الثمرة والزرع الأخضر قبل بدو صلاحهما بدون شرط القطع','3656','1388','0');
INSERT INTO [t10649] VALUES (1393,'ولا يلزم الرهن إلا بالقبض واستدامته شرط، فإن أخرجه إلى الراهن باختياره زال لزومه، فإن رده إليه عاد لزومه إليه','3659','1388','0');
INSERT INTO [t10649] VALUES (1394,'ولا ينفذ تصرف واحد منهما فيه بغير إذن الآخر إلا عتق الراهن فإنه يصح مع الإثم وتؤخذ قيمته رهنا مكانه ونماء الرهن وكسبه وأرش الجناية عليه ملحق به ومؤونته على الراهن وكفنه وأجرة مخزنه','3664','1388','0');
INSERT INTO [t10649] VALUES (1395,'وهو أمانة في يد المرتهن إن تلف من غير تعد منه فلا شيء عليه ولا يسقط بهلاكه شيء من دينه، وإن تلف بعضه فباقيه رهن بجميع الدين، ولا ينفك بعضه مع بقاء بعض الدين، وتجوز الزيادة فيه دون دينه وإن رهن عند اثنين شيئا فوفى أحدهما، أو رهناه شيئا فاستوفى من أحد','3670','1388','0');
INSERT INTO [t10649] VALUES (1396,'ومتى حل الدين وامتنع من وفائه، فإن كان الراهن أذن للمرتهن أو العدل في بيعه باعه ووفى الدين، وإلا أجبره الحاكم على وفائه أو بيع الرهن، فإن لم يفعل باعه الحاكم ووفى دينه','3676','1388','0');
INSERT INTO [t10649] VALUES (1397,'فصل','3679','1388','0');
INSERT INTO [t10649] VALUES (1398,'ويكون عند من اتفقا عليه، وإن أذنا له في البيع لم يبع إلا بنقد البلد، وإن قبض الثمن فتلف في يده فمن ضمان الراهن،','3679','1397','0');
INSERT INTO [t10649] VALUES (1399,'وإن ادعى دفع الثمن إلى المرتهن فأنكره ولا بينة ولم يكن بحضور الراهن ضمن كوكيل','3682','1397','0');
INSERT INTO [t10649] VALUES (1400,'وإن شرط ألا يبيعه إذا حل الدين، أو إن جاءه بحقه في وقت كذا، وإلا فالرهن له لم يصح الشرط وحده','3684','1397','0');
INSERT INTO [t10649] VALUES (1401,'ويقبل قول الراهن في قدر الدين والرهن ورده وكونه عصيرا لا خمرا، وإن أقر أنه ملك غيره، أو أنه جنى قبل على نفسه وحكم بإقراره بعد فكه إلا أن يصدقه المرتهن','3688','1397','0');
INSERT INTO [t10649] VALUES (1402,'فصل','3697','1388','0');
INSERT INTO [t10649] VALUES (1403,'وللمرتهن أن يركب ما يركب ويحلب ما يحلب بقدر نفقته بلا إذن، وإن أنفق على الرهن بغير إذن الراهن مع إمكانه لم يرجع وإن تعذر رجع،','3697','1402','0');
INSERT INTO [t10649] VALUES (1404,'ولو لم يستأذن الحاكم، وكذا وديعة ودواب مستأجرة هرب ربها، ولو خرب الرهن فعمره بلا إذن رجع بآلته فقط','3702','1402','0');
INSERT INTO [t10649] VALUES (1405,'باب الضمان','3706','1264','0');
INSERT INTO [t10649] VALUES (1406,'لا يصح إلا من جائز التصرف ولرب الحق مطالبة من شاء منهما في الحياة والموت','3708','1405','0');
INSERT INTO [t10649] VALUES (1407,'فإن برئت ذمة المضمون عنه برئت ذمة الضامن لا عكسه','3713','1405','0');
INSERT INTO [t10649] VALUES (1408,'ولا يعتبر معرفة الضامن للمضمون عنه','3714','1405','0');
INSERT INTO [t10649] VALUES (1409,'ولا له بل رضا الضامن','3715','1405','0');
INSERT INTO [t10649] VALUES (1410,'ويصح ضمان المجهول إذا آل إلى العلم، والعواري، والمغصوب، والمقبوض بسوم وعهدة مبيع، لا ضمان الأمانات، بل التعدي فيها','3716','1405','0');
INSERT INTO [t10649] VALUES (1411,'فصل','3726','1405','0');
INSERT INTO [t10649] VALUES (1412,'وتصح الكفالة بكل عين مضمونة، وببدن من عليه دين، لا حد ولا قصاص، ويعتبر رضا الكفيل لا مكفول به، فإن مات أو تلفت العين بفعل الله تعالى، أو سلم نفسه برئ الكفيل','3726','1411','0');
INSERT INTO [t10649] VALUES (1413,'باب الحوالة','3734','1264','0');
INSERT INTO [t10649] VALUES (1414,'لا تصح إلا على دين مستقر، ولا يعتبر استقرار المحال به، ويشترط اتفاق الدينين جنسا ووصفا ووقتا وقدرا، ولا يؤثر الفاضل','3735','1413','0');
INSERT INTO [t10649] VALUES (1415,'وإذا صحت نقلت الحق إلى ذمة المحال عليه وبرئ المحيل ويعتبر رضاه لا رضا المحال عليه، ولا المحتال على مليء','3739','1413','0');
INSERT INTO [t10649] VALUES (1416,'وإن كان مفلسا ولم يكن رضي رجع به ومن أحيل بثمن مبيع، أو أحيل به عليه فبان البيع باطلا فلا حوالة، وإذا فسخ البيع لم تبطل ولهما أن يحيلا','3744','1413','0');
INSERT INTO [t10649] VALUES (1417,'باب الصلح','3750','1264','0');
INSERT INTO [t10649] VALUES (1418,'إذا أقر له بدين أو عين فأسقط أو وهب البعض وترك الباقي صح إن لم يكن شرطاه، وممن لا يصح تبرعه','3752','1417','0');
INSERT INTO [t10649] VALUES (1419,'وإن وضع بعض الحال وأجل باقيه صح الإسقاط فقط وإن صالح عن المؤجل ببعضه حالا، أو بالعكس،','3755','1417','0');
INSERT INTO [t10649] VALUES (1420,'أو أقر له ببيت فصالحه على سكناه، أو يبني له فوقه غرفة، أو صالح مكلفا ليقر له بالعبودية، أو امرأة لتقر له بالزوجية بعوض لم يصح','3759','1417','0');
INSERT INTO [t10649] VALUES (1421,'وإن بذلاهما له صلحا عن دعواه صح، وإن قال: أقر بديني وأعطيك منه كذا ففعل، صح الإقرار لا الصلح','3762','1417','0');
INSERT INTO [t10649] VALUES (1422,'فصل','3764','1417','0');
INSERT INTO [t10649] VALUES (1423,'ومن ادعي عليه بعين أو دين فسكت، أو أنكر وهو يجهله ثم صالح بمال صح','3764','1422','0');
INSERT INTO [t10649] VALUES (1424,'وهو للمدعي بيع، يرد معيبه ويفسخ الصلح ويؤخذ منه بشفعة، وللآخر إبراء فلا رد ولا شفعة، وإن كذب أحدهما لم يصح في حقه باطنا وما أخذه حرام','3765','1422','0');
INSERT INTO [t10649] VALUES (1425,'ولا يصح بعوض عن حد سرقة وقذف، ولا حق شفعة ولا ترك شهادة، وتسقط الشفعة والحد','3768','1422','0');
INSERT INTO [t10649] VALUES (1426,'وإن حصل غصن شجرته في هواء غيره أو قراره أزاله، فإن أبى لواه إن أمكن، وإلا فله قطعه','3772','1422','0');
INSERT INTO [t10649] VALUES (1427,'ويجوز في الدرب النافذ فتح الأبواب للاستطراق، لا إخراج روشن وساباط ودكة وميزاب، ولا يفعل ذلك في ملك جار ودرب مشترك بلا إذن المستحق','3777','1422','0');
INSERT INTO [t10649] VALUES (1428,'وليس له وضع خشبه على حائط جاره إلا عند الضرورة إذا لم يمكنه التسقيف إلا به، وكذلك المسجد وغيره','3782','1422','0');
INSERT INTO [t10649] VALUES (1429,'وإذا انهدم جدارهما أو خيف ضرره فطلب أحدهما أن يعمره الآخر معه أجبر عليه، وكذا النهر والدولاب والقناة','3787','1422','0');
INSERT INTO [t10649] VALUES (1430,'باب الحجر','3792','1264','0');
INSERT INTO [t10649] VALUES (1431,'ومن لم يقدر على وفاء شيء من دينه لم يطالب به وحرم حبسه، ومن ماله قدر دينه لم يحجر عليه وأمر بوفائه، فإن أبى حبس بطلب ربه، فإن أصر ولم يبع ماله باعه الحاكم وقضاه ولا يطالب بمؤجل','3792','1430','0');
INSERT INTO [t10649] VALUES (1432,'ومن ماله لا يفي بما عليه حالا وجب الحجر عليه بسؤال غرمائه أو بعضهم ويستحب إظهاره، ولا ينفذ تصرفه في ماله بعد الحجر، ولا إقراره عليه، ومن باعه أو أقرضه شيئا بعده رجع فيه إن جهل حجره وإلا فلا،','3798','1430','0');
INSERT INTO [t10649] VALUES (1433,'وإن تصرف في ذمته، أو أقر بدين أو جناية توجب قودا أو مالا صح ويطالب به بعد فك الحجر عنه، ويبيع الحاكم ماله ويقسم ثمنه بقدر ديون غرمائه،','3806','1430','0');
INSERT INTO [t10649] VALUES (1434,'ولا يحل مؤجل بفلس ولا بموت إن وثق ورثته برهن أو كفيل مليء وإن ظهر غريم بعد القسمة رجع على الغرماء بقسطه ولا يفك حجره إلا حاكم','3810','1430','0');
INSERT INTO [t10649] VALUES (1435,'فصل','3815','1430','0');
INSERT INTO [t10649] VALUES (1436,'ويحجر على السفيه والصغير والمجنون لحظهم ومن أعطاهم ماله بيعا أو قرضا رجع بعينه، وإن أتلفوه لم يضمنوا،','3815','1435','0');
INSERT INTO [t10649] VALUES (1437,'ويلزمهم أرش الجناية، وضمان مال من لم يدفعه إليهم، وإن تم لصغير خمس عشرة سنة،','3818','1435','0');
INSERT INTO [t10649] VALUES (1438,'أو نبت حول قبله شعر خشن أو أنزل، أو عقل مجنون ورشد، أو رشد سفيه زال حجرهم بلا قضاء وتزيد الجارية في البلوغ بالحيض، وإن حملت حكم ببلوغها، ولا ينفك قبل شروطه','3820','1435','0');
INSERT INTO [t10649] VALUES (1439,'والرشد الصلاح في المال بأن يتصرف مرارا فلا يغبن غالبا ولا يبذل ماله في حرام أو في غير فائدة ولا يدفع إليه حتى يختبر قبل بلوغه بما يليق به ووليهم حال الحجر الأب ثم وصيه ثم الحاكم،','3826','1435','0');
INSERT INTO [t10649] VALUES (1440,'ولا يتصرف لأحدهم وليه إلا بالأحظ ويتجر له مجانا وله دفع ماله مضاربة بجزء من الربح ويأكل الولي الفقير من مال موليه الأقل من كفايته أو أجرته مجانا','3830','1435','0');
INSERT INTO [t10649] VALUES (1441,'ويقبل قول الولي والحاكم بعد فك الحجر في النفقة والضرورة والغبطة والتلف ودفع المال وما استدان العبد لزم سيده إن أذن له وإلا ففي رقبته كاستيداعه وأرش جنايته وقيمة متلفه','3837','1435','0');
INSERT INTO [t10649] VALUES (1442,'باب الوكالة','3845','1264','0');
INSERT INTO [t10649] VALUES (1443,'تصح بكل قول يدل على الإذن ويصح القبول على الفور والتراخي بكل قول أو فعل دال عليه ومن له التصرف في شيء فله التوكيل والتوكل فيه','3847','1442','0');
INSERT INTO [t10649] VALUES (1444,'ويصح التوكيل في كل حق آدمي من العقود والفسوخ والعتق، والطلاق، والرجعة،','3851','1442','0');
INSERT INTO [t10649] VALUES (1445,'وتملك المباحات من الصيد والحشيش ونحوه لا الظهار واللعان والأيمان وفي كل حق تدخله النيابة من العبادات والحدود في إثباتها واستيفائها','3854','1442','0');
INSERT INTO [t10649] VALUES (1446,'وليس للوكيل أن يوكل فيما وكل فيه إلا أن يجعل إليه والوكالة عقد جائز وتبطل بفسخ أحدهما وموته وعزل الوكيل وبحجر السفه','3874','1442','0');
INSERT INTO [t10649] VALUES (1447,'ومن وكل في بيع أو شراء لم يبع ولم يشتر من نفسه وولده ولا يبيع بعرض ولا نساء ولا بغير نقد البلد','3884','1442','0');
INSERT INTO [t10649] VALUES (1448,'وإن باع بدون ثمن المثل أو دون ما قدره له أو اشترى له بأكثر من ثمن المثل أو مما قدره له صح وضمن النقص والزيادة','3890','1442','0');
INSERT INTO [t10649] VALUES (1449,'وإن باع بأزيد أو قال: بع بكذا مؤجلا فباع به حالا، أو اشتر بكذا حالا فاشترى به مؤجلا، ولا ضرر فيهما صح وإلا فلا','3893','1442','0');
INSERT INTO [t10649] VALUES (1450,'فصل','3897','1442','0');
INSERT INTO [t10649] VALUES (1451,'وإن اشترى ما يعلم عيبه لزمه إن لم يرض موكله، فإن جهل رده','3897','1450','0');
INSERT INTO [t10649] VALUES (1452,'ووكيل البيع يسلمه ولا يقبض الثمن بغير قرينة ويسلم وكيل المشتري الثمن، فلو أخره بلا عذر وتلف ضمنه','3899','1450','0');
INSERT INTO [t10649] VALUES (1453,'وإن وكله في بيع فاسد فباع صحيحا، أو وكله في كل قليل وكثير، أو شراء ما شاء، أو عينا بما شاء ولم يعين لم يصح','3902','1450','0');
INSERT INTO [t10649] VALUES (1454,'والوكيل في الخصومة لا يقبض والعكس بالعكس واقبض حقي من زيد لا يقبض من ورثته إلا أن يقول: الذي قبله ولا يضمن وكيل الإيداع إذا لم يشهد','3906','1450','0');
INSERT INTO [t10649] VALUES (1455,'فصل','3914','1442','0');
INSERT INTO [t10649] VALUES (1456,'والوكيل أمين لا يضمن ما تلف بيده بلا تفريط ويقبل قوله في نفيه والهلاك مع يمينه','3914','1455','0');
INSERT INTO [t10649] VALUES (1457,'ومن ادعى وكالة زيد في قبض حقه من عمرو، لم يلزمه دفعه إن صدقه ولا اليمين إن كذبه فإن دفعه فأنكر زيد الوكالة حلف وضمنه عمرو، وإن كان المدفوع وديعة أخذها فإن تلفت ضمن أيهما شاء','3918','1455','0');
INSERT INTO [t10649] VALUES (1458,'باب الشركة','3922','1264','0');
INSERT INTO [t10649] VALUES (1459,'وهي اجتماع في استحقاق أو تصرف','3922','1458','0');
INSERT INTO [t10649] VALUES (1460,'وهي أنواع فشركة عنان أن يشترك بدنان بماليهما المعلوم ولو متفاوتا ليعملا فيه ببدنيهما','3924','1458','0');
INSERT INTO [t10649] VALUES (1461,'فينفذ تصرف كل منهما فيهما، بحكم الملك في نصيبه وبالوكالة في نصيب شريكه','3928','1458','0');
INSERT INTO [t10649] VALUES (1462,'ويشترط أن يكون رأس المال من النقدين المضروبين ولو مغشوشين يسيرا وأن يشترطا لكل منهما جزءا من الربح مشاعا معلوما','3930','1458','0');
INSERT INTO [t10649] VALUES (1463,'إن لم يذكرا الربح أو شرطا لأحدهما جزءا مجهولا أو دراهم معلومة أو ربح أحد الثوبين لم تصح وكذا مساقاة ومزارعة ومضاربة','3934','1458','0');
INSERT INTO [t10649] VALUES (1464,'والوضيعة على قدر المال ولا يشترط خلط المالين ولا كونهما من جنس واحد','3937','1458','0');
INSERT INTO [t10649] VALUES (1465,'فصل','3941','1458','0');
INSERT INTO [t10649] VALUES (1466,'الثاني المضاربة لمتجر به ببعض ربحه','3941','1465','0');
INSERT INTO [t10649] VALUES (1467,'فإن قال: والربح بيننا فنصفان، وإن قال: ولي أو لك ثلاثة أرباعه أو ثلثه صح، والباقي للآخر، وإن اختلفا لمن المشروط فلعامل','3942','1465','0');
INSERT INTO [t10649] VALUES (1468,'وكذا مساقاة ومزارعة ومضاربة ولا يضارب بمال لآخر إن أضر الأول ولم يرض','3944','1465','0');
INSERT INTO [t10649] VALUES (1469,'فإن فعل رد حصته في الشركة ولا يقسم مع بقاء العقد إلا باتفاقهما وإن تلف رأس المال أو بعضه بعد التصرف أو خسر جبر من الربح قبل قسمته أو تنضيضه','3949','1465','0');
INSERT INTO [t10649] VALUES (1470,'الثالث: شركة الوجوه أن يشتريا في ذمتيهما بجاههما فما ربحا فبينهما وكل واحد منهما وكيل صاحبه وكفيل عنه بالثمن، والملك بينهما على ما شرطاه، والوضيعة على قدر ملكيهما والربح على ما شرطاه','3953','1465','0');
INSERT INTO [t10649] VALUES (1471,'الرابع: شركة الأبدان أن يشتركا فيما يكتسبان بأبدانهما فما تقبله أحدهما من عمل يلزمهما فعله وتصح في الاحتشاش والاحتطاب وسائر المباحات وإن مرض أحدهما فالكسب بينهما وإن طالبه الصحيح أن يقيم مقامه لزمه','3956','1465','0');
INSERT INTO [t10649] VALUES (1472,'الخامس: شركة المفاوضة، أن يفوض كل منهما إلى صاحبه كل تصرف مالي وبدني من أنواع الشركة، والربح على ما شرطاه والوضيعة بقدر المال فإن أدخلا فيها كسبا، أو غرامة نادرين أو ما يلزم أحدهما من ضمان غصب أو نحوه فسدت','3962','1465','0');
INSERT INTO [t10649] VALUES (1473,'باب المساقاة','3968','1264','0');
INSERT INTO [t10649] VALUES (1474,'تصح على شجر له ثمر يؤكل وعلى ثمرة موجودة وعلى شجر يغرسه ويعمل عليه حتى يثمر بجزء من الثمرة ويشترط أن يكون الجزء معلوما مشاعا','3970','1473','0');
INSERT INTO [t10649] VALUES (1475,'وهي عقد جائز فإن فسخ المالك قبل ظهور الثمرة فللعامل الأجرة، وإن فسخها هو فلا شيء له','3974','1473','0');
INSERT INTO [t10649] VALUES (1476,'ويلزم العامل كل ما فيه صلاح الثمرة من حرث وسقي وزبار وتلقيح وتشميس وإصلاح موضعه وطرق الماء وحصاد ونحوه وعلى رب المال ما يصلحه كسد حائط وإجراء الأنهار والدولاب ونحوه','3978','1473','0');
INSERT INTO [t10649] VALUES (1477,'فصل','3981','1473','0');
INSERT INTO [t10649] VALUES (1478,'وتصح المزارعة بجزء معلوم النسبة مما يخرج من الأرض لربها أو للعامل والباقي للآخر، ولا يشترط كون البذر والغراس من رب الأرض وعليه عمل الناس','3981','1477','0');
INSERT INTO [t10649] VALUES (1479,'باب الإجارة','3991','1264','0');
INSERT INTO [t10649] VALUES (1480,'تصح بثلاثة شروط: معرفة المنفعة كسكنى دار، وخدمة آدمي، وتعليم علم','3992','1479','0');
INSERT INTO [t10649] VALUES (1481,'الثاني: معرفة الأجرة، وتصح في الأجير والظئر بطعامهما وكسوتهما وإن دخل حماما، أو سفينة، أو أعطى ثوبه قصارا، أو خياطا بلا عقد صح بأجرة العادة','3997','1479','0');
INSERT INTO [t10649] VALUES (1482,'الثالث: الإباحة في العين، فلا تصح على نفع محرم، كالزنا، والزمر، والغناء، وجعل داره كنيسة، أو لبيع الخمر','4001','1479','0');
INSERT INTO [t10649] VALUES (1483,'وتصح إجارة حائط لوضع أطراف خشبه عليه ولا تؤجر المرأة نفسها بغير إذن زوجها','4006','1479','0');
INSERT INTO [t10649] VALUES (1484,'فصل','4010','1479','0');
INSERT INTO [t10649] VALUES (1485,'ويشترط في العين المؤجرة: معرفتها برؤية أو صفة في غير الدار ونحوها','4010','1484','0');
INSERT INTO [t10649] VALUES (1486,'وأن يعقد على نفعها دون أجزائها، فلا تصح إجارة الطعام للأكل، ولا الشمع ليشعله، ولا حيوان ليأخذ لبنه إلا في الظئر، ونقع البئر، وماء الأرض يدخلان تبعا','4013','1484','0');
INSERT INTO [t10649] VALUES (1487,'والقدرة على التسليم، فلا تصح إجارة الآبق، والشارد، واشتمال العين على المنفعة، فلا تصح إجارة بهيمة زمنة لحمل،','4018','1484','0');
INSERT INTO [t10649] VALUES (1488,'ولا أرض لا تنبت للزرع، وأن تكون المنفعة للمؤجر أو مأذونا له فيها وتجوز إجارة العين لمن يقوم مقامه، لا بأكثر منه ضررا','4021','1484','0');
INSERT INTO [t10649] VALUES (1489,'وتصح إجارة الوقف، فإن مات المؤجر وانتقل إلى من بعده لم تنفسخ،','4026','1484','0');
INSERT INTO [t10649] VALUES (1490,'وللثاني حصته من الأجرة، وإن أجر الدار ونحوها مدة ولو طويلة يغلب على الظن بقاء العين فيها صح','4028','1484','0');
INSERT INTO [t10649] VALUES (1491,'وإن استأجرها لعمل، كدابة لركوب إلى موضع معين؛ أو بقر لحرث، أو دياس زرع، أو من يدله على طريق، اشترط معرفة ذلك وضبطه بما لا يختلف','4034','1484','0');
INSERT INTO [t10649] VALUES (1492,'ولا تصح على عمل يختص أن يكون فاعله من أهل القربة','4038','1484','0');
INSERT INTO [t10649] VALUES (1493,'وعلى المؤجر كل ما يتمكن به من النفع كزمام الجمل، ورحله، وحزامه، والشد عليه، وشد الأحمال والمحامل، والرفع والحط، ولزوم البعير، ومفاتيح الدار وعمارتها','4047','1484','0');
INSERT INTO [t10649] VALUES (1494,'فأما تفريغ البالوعة والكنيف، فيلزم المستأجر إذا تسلمها فارغة','4049','1484','0');
INSERT INTO [t10649] VALUES (1495,'فصل','4050','1479','0');
INSERT INTO [t10649] VALUES (1496,'وهي عقد لازم، فإن آجره شيئا ومنعه كل المدة أو بعضها فلا شيء له','4050','1495','0');
INSERT INTO [t10649] VALUES (1497,'وإن بدأ الآخر قبل انقضائها فعليه وتنفسخ بتلف العين المؤجرة، وبموت المرتضع والراكب إن لم يخلف بدلا، وانقلاع ضرس أو برئه ونحوه','4053','1495','0');
INSERT INTO [t10649] VALUES (1498,'لا بموت المتعاقدين أو أحدهما، ولا بضياع نفقة المستأجر ونحوه وإن اكترى دارا فانهدمت، أو أرضا لزرع فانقطع ماؤها، أو غرقت، انفسخت الإجارة في الباقي','4057','1495','0');
INSERT INTO [t10649] VALUES (1499,'وإن وجد العين معيبة، أو حدث بها عيب فله الفسخ، وعليه أجرة ما مضى ولا يضمن أجير خاص ما جنت يده خطأ،','4060','1495','0');
INSERT INTO [t10649] VALUES (1500,'ولا حجام، وطبيب، وبيطار لم تجن أيديهم، إن عرف حذقهم، ولا راع لم يتعد','4064','1495','0');
INSERT INTO [t10649] VALUES (1501,'ويضمن المشترك ما تلف بفعله، ولا يضمن ما تلف من حرزه، أو بغير فعله، ولا أجرة له','4068','1495','0');
INSERT INTO [t10649] VALUES (1502,'وتجب الأجرة بالعقد إن لم تؤجل، وتستحق بتسليم العمل الذي في الذمة ومن تسلم عينا بإجارة فاسدة، وفرغت المدة لزمه أجرة المثل','4071','1495','0');
INSERT INTO [t10649] VALUES (1503,'باب السبق','4078','1264','0');
INSERT INTO [t10649] VALUES (1504,'يصح على الأقدام، وسائر الحيوانات، والسفن، والمزاريق','4079','1503','0');
INSERT INTO [t10649] VALUES (1505,'ولا تصح بعوض إلا في إبل، وخيل، وسهام','4084','1503','0');
INSERT INTO [t10649] VALUES (1506,'ولا بد من تعيين المركوبين، واتحادهما، والرماة، والمسافة بقدر معتاد وهي جعالة لكل واحد فسخها','4087','1503','0');
INSERT INTO [t10649] VALUES (1507,'وتصح المناضلة على معينين يحسنون الرمي','4091','1503','0');
INSERT INTO [t10649] VALUES (1508,'باب العارية','4093','1264','0');
INSERT INTO [t10649] VALUES (1509,'هي إباحة نفع عين، تبقى بعد استيفائه','4093','1508','0');
INSERT INTO [t10649] VALUES (1510,'وتباح إعارة كل ذي نفع مباح، إلا البضع، وعبدا مسلما لكافر، وصيدا ونحوه لمحرم، وأمة شابة لغير امرأة أو محرم','4095','1508','0');
INSERT INTO [t10649] VALUES (1511,'ولا أجرة لمن أعار حائطا حتى يسقط، ولا يرد إن سقط إلا بإذنه، وتضمن العارية بقيمتها يوم تلفت، ولو شرط نفي ضمانها، وعليه مؤونة ردها، لا المؤجرة، ولا يعيرها','4100','1508','0');
INSERT INTO [t10649] VALUES (1512,'فإن تلفت عند الثاني استقرت عليه قيمتها، وعلى معيرها أجرتها، ويضمن أيهما شاء','4112','1508','0');
INSERT INTO [t10649] VALUES (1513,'وإن أركب منقطعا للثواب لم يضمن','4115','1508','0');
INSERT INTO [t10649] VALUES (1514,'وإذا قال: أجرتك، قال: بل أعرتني، أو بالعكس عقب العقد قبل قول مدعي الإعارة، وبعد مضي مدة قول المالك بأجرة المثل','4118','1508','0');
INSERT INTO [t10649] VALUES (1515,'وإن قال: أعرتني، أو قال: أجرتني، قال: بل غصبتني، أو قال: أعرتك، قال: بل أجرتني والبهيمة تالفة، أو اختلفا في رد فقول المالك','4121','1508','0');
INSERT INTO [t10649] VALUES (1516,'باب الغصب','4126','1264','0');
INSERT INTO [t10649] VALUES (1517,'وهو الاستيلاء على حق غيره قهرا بغير حق من عقار ومنقول','4126','1516','0');
INSERT INTO [t10649] VALUES (1518,'وإن غصب كلبا يقتنى، أو خمر ذمي ردهما، ولا يرد جلد ميتة','4129','1516','0');
INSERT INTO [t10649] VALUES (1519,'وإتلاف الثلاثة هدر','4132','1516','0');
INSERT INTO [t10649] VALUES (1520,'وإن استولى على حر لم يضمنه، وإن استعمله كرها أو حبسه فعليه أجرته','4133','1516','0');
INSERT INTO [t10649] VALUES (1521,'ويلزم رد المغصوب بزيادته، وإن غرم أضعافه، وإن بنى في الأرض أو غرس لزمه القلع، وأرش نقصها، وتسويتها، والأجرة','4135','1516','0');
INSERT INTO [t10649] VALUES (1522,'ولو غصب جارحا، أو عبدا، أو فرسا، فحصل بذلك صيدا فلمالكه','4141','1516','0');
INSERT INTO [t10649] VALUES (1523,'وإن ضرب المصوغ، ونسج الغزل، وقصر الثوب، أو صبغه، ونجر الخشب ونحوه، أو صار الحب زرعا، أو البيضة فرخا، والنوى غرسا، رده وأرش نقصه، ولا شيء للغاصب، ويلزمه ضمان نقصه','4142','1516','0');
INSERT INTO [t10649] VALUES (1524,'وإن خصى الرقيق رده مع قيمته وما نقص بسعر لم يضمن، ولا بمرض عاد ببرئه','4146','1516','0');
INSERT INTO [t10649] VALUES (1525,'وإن عاد بتعليم صنعة ضمن النقص، وإن تعلم أو سمن فزادت قيمته ثم نسي أو هزل فنقصت ضمن الزيادة كما لو عادت من غير جنس الأول، ومن جنسها لا يضمن إلا أكثرهما','4150','1516','0');
INSERT INTO [t10649] VALUES (1526,'فصل','4153','1516','0');
INSERT INTO [t10649] VALUES (1527,'وإن خلط بما لا يتميز كزيت، أو حنطة بمثلهما، أو صبغ الثوب، أو لت سويقا بدهن، أو عكسه ولم تنقص القيمة ولم تزد، فهما شريكان بقدر ماليهما فيه، وإن نقصت القيمة ضمنها','4153','1526','0');
INSERT INTO [t10649] VALUES (1528,'وإن زادت قيمة أحدهما فلصاحبه ولا يجبر من أبى قلع الصبغ','4155','1526','0');
INSERT INTO [t10649] VALUES (1529,'ولو قلع غرس المشتري أو بناؤه لاستحقاق الأرض، رجع على بائعها بالغرامة وإن أطعمه لعالم بغصبه، فالضمان عليه وعكسه بعكسه','4158','1526','0');
INSERT INTO [t10649] VALUES (1530,'وإن أطعمه لمالكه أو رهنه أو أودعه أو آجره إياه لم يبرأ إلا أن يعلم ويبرأ بإعارته','4159','1526','0');
INSERT INTO [t10649] VALUES (1531,'وما تلف أو تغيب من مغصوب مثلي غرم مثله إذا وإلا فقيمته يوم تعذر، ويضمن غير المثلي بقيمته يوم تلفه، وإن تخمر عصير فالمثل فإن انقلب خلا دفعه ومعه نقص قيمته عصيرا','4163','1526','0');
INSERT INTO [t10649] VALUES (1532,'فصل','4169','1516','0');
INSERT INTO [t10649] VALUES (1533,'وتصرفات الغاصب الحكمية باطلة','4169','1532','0');
INSERT INTO [t10649] VALUES (1534,'والقول في قيمة التالف، أو قدره، أو صفته قوله وفي رده وعدم عيبه قول ربه','4173','1532','0');
INSERT INTO [t10649] VALUES (1535,'وإن جهل ربه تصدق به عنه مضمونا','4176','1532','0');
INSERT INTO [t10649] VALUES (1536,'ومن أتلف محترما، أو فتح قفصا، أو بابا، أو حل وكاء، أو رباطا أو قيدا، فذهب ما فيه، أو أتلف شيئا ونحوه ضمنه،','4183','1532','0');
INSERT INTO [t10649] VALUES (1537,'وإن ربط دابة بطريق ضيق فعثر به إنسان ضمن، كالكلب العقور لمن دخل بيته بإذنه، أو عقره خارج منزله','4186','1532','0');
INSERT INTO [t10649] VALUES (1538,'وما أتلفت البهيمة من الزرع ليلا ضمنه صاحبها وعكسه النهار، إلا أن ترسل بقرب ما تتلفه عادة','4193','1532','0');
INSERT INTO [t10649] VALUES (1539,'وإن كانت بيد راكب أو قائد أو سائق ضمن جنايتها بمقدمها لا بمؤخرها','4199','1532','0');
INSERT INTO [t10649] VALUES (1540,'وباقي جنايتها هدر كقتل الصائل عليه وكسر مزمار وصليب وآنية ذهب وفضة، وآنية خمر غير محترمة','4201','1532','0');
INSERT INTO [t10649] VALUES (1541,'باب الشفعة','4216','1264','0');
INSERT INTO [t10649] VALUES (1542,'وهي استحقاق انتزاع حصة شريكه ممن انتقلت إليه بعوض مالي بثمنه الذي استقر عليه العقد','4216','1541','0');
INSERT INTO [t10649] VALUES (1543,'فإن انتقل بغير عوض، أو كان عوضه صداقا، أو خلعا، أو صلحا عن دم عمد فلا شفعة ويحرم التحيل لإسقاطها','4222','1541','0');
INSERT INTO [t10649] VALUES (1544,'وتثبت لشريك في أرض تجب قسمتها، ويتبعها الغراس، والبناء، لا الثمرة والزرع فلا شفعة لجار','4229','1541','0');
INSERT INTO [t10649] VALUES (1545,'وهي على الفور وقت علمه فإن لم يطلبها إذا بلا عذر بطلت','4235','1541','0');
INSERT INTO [t10649] VALUES (1546,'وإن قال للمشتري: بعني، أو صالحني، أو كذب العدل، أو طلب أخذ البعض سقطت','4238','1541','0');
INSERT INTO [t10649] VALUES (1547,'والشفعة لاثنين بقدر حقيهما، فإن عفا أحدهما أخذ الآخر الكل أو ترك وإن اشترى اثنان حق واحد، أو عكسه، أو اشترى واحد شقصين من أرضين صفقة واحدة فللشفيع أخذ أحدهما','4240','1541','0');
INSERT INTO [t10649] VALUES (1548,'وإن باع شقصا وسيفا، أو تلف بعض المبيع فللشفيع أخذ الشقص بحصته من الثمن ولا شفعة بشركة وقف، ولا غير ملك سابق، ولا لكافر على مسلم','4243','1541','0');
INSERT INTO [t10649] VALUES (1549,'فصل','4249','1541','0');
INSERT INTO [t10649] VALUES (1550,'وإن تصرف مشتريه بوقفه، أو هبته أو رهنه لا بوصية سقطت الشفعة','4249','1549','0');
INSERT INTO [t10649] VALUES (1551,'وببيع فله أخذه بأحد البيعين وللمشتري الغلة، والنماء المنفصل، والزرع، والثمرة الظاهرة فإن بنى أو غرس فللشفيع تملكه بقيمته، وقلعه، ويغرم نقصه، ولربه أخذه بلا ضرر','4253','1549','0');
INSERT INTO [t10649] VALUES (1552,'وإن مات الشفيع قبل الطلب بطلت، وبعده لوارثه، ويأخذ بكل الثمن، فإن عجز عن بعضه سقطت شفعته','4260','1549','0');
INSERT INTO [t10649] VALUES (1553,'والمؤجل يأخذه المليء به، وضده بكفيل مليء','4263','1549','0');
INSERT INTO [t10649] VALUES (1554,'ويقبل في الخلف مع عدم البينة قول المشتري، فإن قال: اشتريته بألف أخذ الشفيع به ولو أثبت البائع أكثر','4265','1549','0');
INSERT INTO [t10649] VALUES (1555,'وإن أقر البائع بالبيع وأنكر المشتري وجبت وعهدة الشفيع على المشتري، وعهدة المشتري على البائع','4268','1549','0');
INSERT INTO [t10649] VALUES (1556,'باب الوديعة','4271','1264','0');
INSERT INTO [t10649] VALUES (1557,'إذا تلفت من بين ماله ولم يتعد ولم يفرط لم يضمن ويلزمه حفظها في حرز مثلها، فإن عينه صاحبها فأحرزها بدونه ضمن، وبمثله أو أحرز فلا','4273','1556','0');
INSERT INTO [t10649] VALUES (1558,'وإن قطع العلف عن الدابة بغير قول صاحبها ضمن وإن عين جيبه فتركها في كمه أو يده ضمن وعكسه بعكسه، وإن دفعها إلى من يحفظ ماله أو مال ربها لم يضمن','4276','1556','0');
INSERT INTO [t10649] VALUES (1559,'وعكسه الأجنبي والحاكم ولا يطالبان إن جهلا وإن حدث خوف أو سفر ردها على ربها، فإن غاب حملها معه إن كان أحرز، وإلا أودعها ثقة','4281','1556','0');
INSERT INTO [t10649] VALUES (1560,'ومن أودع دابة فركبها لغير نفعها، أو ثوبا فلبسه، أو دراهم فأخرجها من محرز ثم ردها، أو رفع الختم، أو خلطها بغير متميز فضاع الكل ضمن','4285','1556','0');
INSERT INTO [t10649] VALUES (1561,'فصل','4290','1556','0');
INSERT INTO [t10649] VALUES (1562,'ويقبل قول المودع في ردها إلى ربها أو غيره بإذنه، وتلفها وعدم التفريط','4290','1561','0');
INSERT INTO [t10649] VALUES (1563,'إن قال: لم تودعني، ثم ثبتت ببينة أو إقرار ثم ادعى ردا، أو تلفا سابقين لجحوده، لم يقبلا ولو ببينة','4293','1561','0');
INSERT INTO [t10649] VALUES (1564,'بل في قوله: ما لك عندي شيء ونحوه، أو بعده بها وإن ادعى وارثه الرد منه أو من مورثه لم يقبل إلا ببينة','4295','1561','0');
INSERT INTO [t10649] VALUES (1565,'وإن طلب أحد المودعين نصيبه من مكيل أو موزون ينقسم أخذه وللمستودع، والمضارب، والمرتهن، والمستأجر مطالبة غاصب العين','4298','1561','0');
INSERT INTO [t10649] VALUES (1566,'باب إحياء الموات','4303','1264','0');
INSERT INTO [t10649] VALUES (1567,'وهي الأرض المنفكة عن الاختصاصات وملك معصوم فمن أحياها ملكها','4303','1566','0');
INSERT INTO [t10649] VALUES (1568,'من مسلم وكافر بإذن الإمام وعدمه، في دار الإسلام وغيرها والعنوة كغيرها ويملك بالإحياء ما قرب من عامر، إن لم يتعلق بمصلحته','4306','1566','0');
INSERT INTO [t10649] VALUES (1569,'ومن أحاط مواتا، أو حفر بئرا فوصل إلى الماء، أو أجراه إليه من عين ونحوها، أو حبسه عنه ليزرع فقد أحياه','4315','1566','0');
INSERT INTO [t10649] VALUES (1570,'ويملك حريم البئر العادية خمسين ذراعا من كل جانب وحريم البدية نصفها','4317','1566','0');
INSERT INTO [t10649] VALUES (1571,'وللإمام إقطاع موات لمن يحييه، ولا يملكه وإقطاع الجلوس في الطرق الواسعة ما لم يضر بالناس، ويكون أحق بجلوسها، ومن غير إقطاع لمن سبق بالجلوس ما بقي قماشه فيها وإن طال','4319','1566','0');
INSERT INTO [t10649] VALUES (1572,'وإن سبق اثنان اقترعا، ولمن في أعلى الماء المباح السقي وحبس الماء إلى أن يصل إلى كعبه ثم يرسله إلى من يليه وللإمام دون غيره حمى مرعى لدواب المسلمين ما لم يضرهم','4324','1566','0');
INSERT INTO [t10649] VALUES (1573,'باب الجعالة','4330','1264','0');
INSERT INTO [t10649] VALUES (1574,'وهي أن يجعل شيئا معلوما لمن يعمل له عملا معلوما، أو مجهولا مدة معلومة أو مجهولة','4330','1573','0');
INSERT INTO [t10649] VALUES (1575,'كرد عبد، ولقطة، وخياطة، وبناء حائط فمن فعله بعد علمه بقوله استحقه، والجماعة يقتسمونه وفي أثنائه يأخذ قسط تمامه','4332','1573','0');
INSERT INTO [t10649] VALUES (1576,'ولكل فسخها، فمن العامل لا يستحق شيئا، ومن الجاعل بعد الشروع للعامل أجرة عمله','4336','1573','0');
INSERT INTO [t10649] VALUES (1577,'ومع الاختلاف في أصله أو قدره يقبل قول الجاعل، ومن رد لقطة، أو ضالة، أو عمل لغيره عملا بغير جعل لم يستحق عوضا، إلا دينارا أو اثني عشر درهما عن رد الآبق ويرجع بنفقته أيضا','4340','1573','0');
INSERT INTO [t10649] VALUES (1578,'باب اللقطة','4345','1264','0');
INSERT INTO [t10649] VALUES (1579,'وهي مال، أو مختص ضل عن ربه، وتتبعه همة أوساط الناس','4345','1578','0');
INSERT INTO [t10649] VALUES (1580,'فأما الرغيف، والسوط ونحوهما فيملك بلا تعريف','4349','1578','0');
INSERT INTO [t10649] VALUES (1581,'وما امتنع من سبع صغير كثور وجمل ونحوهما حرم أخذه','4350','1578','0');
INSERT INTO [t10649] VALUES (1582,'وله التقاط غير ذلك من حيوان وغيره، إن أمن نفسه على ذلك، وإلا فهو كغاصب','4352','1578','0');
INSERT INTO [t10649] VALUES (1583,'ويعرف الجميع في مجامع الناس غير المساجد حولا ويملكه بعده حكما، لكن لا يتصرف فيها قبل معرفة صفاتها','4354','1578','0');
INSERT INTO [t10649] VALUES (1584,'فمتى جاء طالبها فوصفها لزم دفعها إليه','4362','1578','0');
INSERT INTO [t10649] VALUES (1585,'والسفيه والصبي يعرف لقطتهما وليهما ومن ترك حيوانا بفلاة لانقطاعه أو عجز ربه عنه ملكه آخذه ومن أخذ نعله ونحوه ووجد موضعه غيره فلقطة','4364','1578','0');
INSERT INTO [t10649] VALUES (1586,'باب اللقيط','4369','1264','0');
INSERT INTO [t10649] VALUES (1587,'وهو طفل لا يعرف نسبه، ولا رقه، نبذ أو ضل','4369','1586','0');
INSERT INTO [t10649] VALUES (1588,'وأخذه فرض كفاية، وهو حر، وما وجد معه، أو تحته ظاهرا أو مدفونا طريا أو متصلا به كحيوان وغيره، أو قريبا منه، فله وينفق عليه منه','4371','1586','0');
INSERT INTO [t10649] VALUES (1589,'وإلا فمن بيت المال، وهو مسلم، وحضانته لواجده الأمين، وينفق عليه بغير إذن حاكم، وميراثه، وديته، لبيت المال','4374','1586','0');
INSERT INTO [t10649] VALUES (1590,'ووليه في العمد الإمام يخير بين القصاص والدية وإن أقر رجل أو امرأة ذات زوج مسلم أو كافر أنه ولده لحق به،','4378','1586','0');
INSERT INTO [t10649] VALUES (1591,'ولو بعد موت اللقيط، ولا يتبع الكافر في دينه إلا ببينة تشهد أنه ولد على فراشه','4381','1586','0');
INSERT INTO [t10649] VALUES (1592,'وإن اعترف بالرق مع سبق مناف، أو قال: إنه كافر، لم يقبل منه، وإن ادعاه جماعة قدم ذو البينة، وإلا فمن ألحقته القافة به','4383','1586','0');
INSERT INTO [t10649] VALUES (1593,'كتاب الوقف','4388','0','0');
INSERT INTO [t10649] VALUES (1594,'*','4388','1593','0');
INSERT INTO [t10649] VALUES (1595,'وهو تحبيس الأصل وتسبيل المنفعة ويصح بالقول وبالفعل الدال عليه،','4388','1594','0');
INSERT INTO [t10649] VALUES (1596,'وبالفعل الدال عليه، كمن جعل أرضه مسجدا وأذن للناس في الصلاة فيه أو مقبرة وأذن في الدفن فيها','4390','1594','0');
INSERT INTO [t10649] VALUES (1597,'وصريحه وقفت وحبست وسبلت وكنايته تصدقت وحرمت وأبدت','4392','1594','0');
INSERT INTO [t10649] VALUES (1598,'فتشترط النية مع الكناية، أو اقتران أحد الألفاظ الخمسة، أو حكم الوقف','4395','1594','0');
INSERT INTO [t10649] VALUES (1599,'ويشترط فيه المنفعة دائما من معين ينتفع به مع بقاء عينه كعقار وحيوان ونحوهما،','4398','1594','0');
INSERT INTO [t10649] VALUES (1600,'وأن يكون على بر كالمساجد والقناطر والمساكين والأقارب من مسلم وذمي','4402','1594','0');
INSERT INTO [t10649] VALUES (1601,'غير حربي وكنيسة ونسخ التوراة والإنجيل وكتب زندقة','4405','1594','0');
INSERT INTO [t10649] VALUES (1602,'وكذا الوصية والوقف على نفسه','4408','1594','0');
INSERT INTO [t10649] VALUES (1603,'ويشترط في غير المسجد ونحوه أن يكون على معين يملك لا ملك وحيوان وحمل وقبر لا قبوله ولا إخراجه عن يده','4411','1594','0');
INSERT INTO [t10649] VALUES (1604,'فصل','4416','1594','0');
INSERT INTO [t10649] VALUES (1605,'ويجب العمل بشرط الواقف في جمع وتقديم وضد ذلك واعتبار وصف وعدمه وترتيب ونظر وغير ذلك،','4416','1604','0');
INSERT INTO [t10649] VALUES (1606,'فإن أطلق ولم يشترط استوى الغني والذكر وضدهما والنظر للموقوف عليه،','4423','1604','0');
INSERT INTO [t10649] VALUES (1607,'وإن وقف على ولده أو ولد غيره ثم على المساكين فهو لولده، الذكور والإناث بالسوية،','4426','1604','0');
INSERT INTO [t10649] VALUES (1608,'ثم ولد بنيه، دون بناته كما لو قال على ولد ولده، وذريته لصلبه ولو قال: على بنيه أو بني فلان اختص بذكورهم','4427','1604','0');
INSERT INTO [t10649] VALUES (1609,'إلا أن يكونوا قبيلة فيدخل فيه النساء دون أولادهن من غيرهم، والقرابة وأهل بيته وقومه يشمل الذكر والأنثى من أولاده وأولاد أبيه وجده وجد أبيه،','4432','1604','0');
INSERT INTO [t10649] VALUES (1610,'وإن وجدت قرينة تقتضي إرادة الإناث أو حرمانهن عمل بها، وإذا وقف على جماعة يمكن حصرهم وجب تعميمهم والتساوي، وإلا جاز التفضيل والاقتصار على أحدهم','4437','1604','0');
INSERT INTO [t10649] VALUES (1611,'فصل','4440','1594','0');
INSERT INTO [t10649] VALUES (1612,'والوقف عقد لازم لا يجوز فسخه ولا يباع','4440','1611','0');
INSERT INTO [t10649] VALUES (1613,'إلا أن تتعطل منافعه ويصرف ثمنه في مثله، ولو أنه مسجد وآلته وما فضل عن حاجته جاز صرفه إلى مسجد آخر والصدقة به على فقراء المسلمين،','4442','1611','0');
INSERT INTO [t10649] VALUES (1614,'باب الهبة والعطية','4448','1593','0');
INSERT INTO [t10649] VALUES (1615,'وهي التبرع بتمليك ماله المعلوم الموجود في حياته غيره،','4448','1614','0');
INSERT INTO [t10649] VALUES (1616,'وإن شرط فيها عوضا معلوما فبيع','4450','1614','0');
INSERT INTO [t10649] VALUES (1617,'ولا يصح مجهولا، إلا ما تعذر علمه وتنعقد بالإيجاب والقبول والمعاطاة الدالة عليها وتلزم بالقبض بإذن واهب، إلا ما كان في يد متهب','4451','1614','0');
INSERT INTO [t10649] VALUES (1618,'ووارث الواهب يقوم مقامه ومن أبرأ غريمه من دينه بلفظ الإحلال أو الصدقة أو الهبة ونحوها برئت ذمته، ولو لم يقبل وتجوز هبة كل عين تباع، وكلب يقتنى','4455','1614','0');
INSERT INTO [t10649] VALUES (1619,'فصل','4462','1614','0');
INSERT INTO [t10649] VALUES (1620,'يجب التعديل في عطية أولاده بقدر إرثهم،','4462','1619','0');
INSERT INTO [t10649] VALUES (1621,'فإن فضل بعضهم سوى برجوع أو زيادة، فإن مات قبله ثبتت ولا يجوز لواهب أن يرجع في هبته اللازمة إلا الأب','4467','1619','0');
INSERT INTO [t10649] VALUES (1622,'وله أن يأخذ ويتملك من مال ولده ما لا يضره ولا يحتاجه','4474','1619','0');
INSERT INTO [t10649] VALUES (1623,'فإن تصرف في ماله ولو فيما وهبه له ببيع أو عتق أو إبراء أو أراد أخذه قبل رجوعه أو تملكه بقول أو نية وقبض معتبر لم يصح بل بعده','4477','1619','0');
INSERT INTO [t10649] VALUES (1624,'وليس للولد مطالبة أبيه بدين ونحوه إلا بنفقته الواجبة عليه، فإن له مطالبته بها وحبسه عليها','4480','1619','0');
INSERT INTO [t10649] VALUES (1625,'فصل','4484','1614','0');
INSERT INTO [t10649] VALUES (1626,'من مرضه غير مخوف كوجع ضرس وعين وصداع يسير','4484','1625','0');
INSERT INTO [t10649] VALUES (1627,'فتصرفه لازم كالصحيح ولو مات منه، وإن كان مخوفا كبرسام وذات الجنب ووجع قلب ودوام قيام ورعاف وأول فالج وآخر سل والحمى المطبقة والربع،','4485','1625','0');
INSERT INTO [t10649] VALUES (1628,'وما قال طبيبان مسلمان عدلان: إنه مخوف','4489','1625','0');
INSERT INTO [t10649] VALUES (1629,'ومن وقع الطاعون ببلده، ومن أخذها الطلق، لا يلزم تبرعه لوارث بشيء ولا بما فوق الثلث إلا بإجازة الورثة لها إن مات منه، وإن عوفي فكصحيح','4493','1625','0');
INSERT INTO [t10649] VALUES (1630,'ومن امتد مرضه بجذام أو سل أو فالج ولم يقطعه بفراش فمن كل ماله، والعكس بالعكس،','4503','1625','0');
INSERT INTO [t10649] VALUES (1631,'ويعتبر الثلث عند موته ويسوى بين المتقدم والمتأخر في الوصية، ويبدأ بالأول فالأول في العطية','4506','1625','0');
INSERT INTO [t10649] VALUES (1632,'ولا يملك الرجوع فيها، ويعتبر القبول لها عند وجودها ويثبت الملك إذا، والوصية بخلاف ذلك','4510','1625','0');
INSERT INTO [t10649] VALUES (1633,'كتاب الوصايا','4517','0','0');
INSERT INTO [t10649] VALUES (1634,'*','4517','1633','0');
INSERT INTO [t10649] VALUES (1635,'يسن لمن ترك خيرا وهو المال الكثير أن يوصي بالخمس','4517','1634','0');
INSERT INTO [t10649] VALUES (1636,'ولا تجوز بأكثر من الثلث لأجنبي ولا لوارث بشيء إلا بإجازة الورثة لها بعد الموت، فتصح تنفيذا','4521','1634','0');
INSERT INTO [t10649] VALUES (1637,'وتكره وصية فقير وارثه محتاج وتجوز بالكل لمن لا وارث له، وإن لم يف الثلث بالوصايا فالنقص بالقسط','4526','1634','0');
INSERT INTO [t10649] VALUES (1638,'وإن أوصى لوارث فصار عند الموت غير وارث صحت، والعكس بالعكس، ويعتبر القبول بعد الموت وإن طال، لا قبله','4529','1634','0');
INSERT INTO [t10649] VALUES (1639,'ويثبت الملك به عقب الموت، ومن قبلها ثم ردها لم يصح الرد، ويجوز الرجوع في الوصية','4531','1634','0');
INSERT INTO [t10649] VALUES (1640,'وإن قال: إن قدم زيد فله ما أوصيت به لعمرو، فقدم في حياته فله وبعدها لعمرو','4535','1634','0');
INSERT INTO [t10649] VALUES (1641,'ويخرج الواجب كله، من دين وحج وغيره من كل ماله بعد موته وإن لم يوص به،','4537','1634','0');
INSERT INTO [t10649] VALUES (1642,'فإن قال: أدوا الواجب من ثلثي بدئ به، فإن بقي منه شيء أخذه صاحب التبرع، وإلا سقط','4540','1634','0');
INSERT INTO [t10649] VALUES (1643,'باب الموصى له','4542','1633','0');
INSERT INTO [t10649] VALUES (1644,'تصح لمن يصح تملكه','4542','1643','0');
INSERT INTO [t10649] VALUES (1645,'ولعبده بمشاع كثلثه ويعتق منه بقدره ويأخذ الفاضل، وبمائة أو معين لا تصح له، وتصح بحمل ولحمل تحقق وجوده قبلها،','4545','1643','0');
INSERT INTO [t10649] VALUES (1646,'وإذا أوصى من لا حج عليه أن يحج عنه بألف صرف من ثلثه مؤونة حجة بعد أخرى حتى ينفد','4549','1643','0');
INSERT INTO [t10649] VALUES (1647,'ولا تصح لملك وبهيمة وميت، فإن وصى لحي وميت يعلم موته فالكل للحي وإن جهل فالنصف، وإن وصى بماله لابنيه وأجنبي فردا فله التسع','4551','1643','0');
INSERT INTO [t10649] VALUES (1648,'باب الموصى به','4554','1633','0');
INSERT INTO [t10649] VALUES (1649,'تصح بما يعجز عن تسليمه، كآبق وطير في هواء، وبالمعدوم، كبما يحمل حيوانه وشجرته أبدا، أو مدة معينة','4554','1648','0');
INSERT INTO [t10649] VALUES (1650,'، فإن لم يحصل منه شيء بطلت الوصية وتصح بكلب صيد ونحوه، وبزيت متنجس','4555','1648','0');
INSERT INTO [t10649] VALUES (1651,'وله ثلثهما ولو كثر المال إن لم تجز الورثة وتصح بمجهول كعبد وشاة','4557','1648','0');
INSERT INTO [t10649] VALUES (1652,'ويعطى ما يقع عليه الاسم العرفي، وإذا أوصى بثلثه فاستحدث مالا ولو دية دخل في الوصية','4559','1648','0');
INSERT INTO [t10649] VALUES (1653,'ومن أوصي له بمعين فتلف بطلت، وإن تلف المال غيره فهو للموصى له إن خرج من ثلث المال الحاصل للورثة','4562','1648','0');
INSERT INTO [t10649] VALUES (1654,'باب الوصية بالأنصباء والأجزاء','4564','1633','0');
INSERT INTO [t10649] VALUES (1655,'إذا أوصى بمثل نصيب وارث معين فله مثل نصيبه مضموما إلى المسألة، فإذا أوصى بمثل نصيب ابنه وله ابنان فله الثلث، وإن كانوا ثلاثة فله الربع','4564','1654','0');
INSERT INTO [t10649] VALUES (1656,'وإن كان معهم بنت فله التسعان، وإن وصى له بمثل نصيب أحد ورثته ولم يبين كان له مثل ما لأقلهم نصيبا، فمع ابن وبنت ربع ومع زوجة وابن تسع وبسهم من ماله فله سدس، وبشيء أو جزء أو حظ أعطاه الوارث ما شاء','4565','1654','0');
INSERT INTO [t10649] VALUES (1657,'باب الموصى إليه','4568','1633','0');
INSERT INTO [t10649] VALUES (1658,'تصح وصية المسلم إلى كل مسلم مكلف عدل رشيد','4568','1657','0');
INSERT INTO [t10649] VALUES (1659,'ولو عبدا، ويقبل بإذن سيده، وإذا أوصى إلى زيد وبعده إلى عمرو ولم يعزل زيدا اشتركا، ولا ينفرد أحدهما بتصرف لم يجعله له','4571','1657','0');
INSERT INTO [t10649] VALUES (1660,'ولا تصح وصية إلا في تصرف معلوم يملكه الموصي كقضاء دينه وتفرقة ثلثه والنظر لصغاره','4574','1657','0');
INSERT INTO [t10649] VALUES (1661,'ولا تصح بما لا يملكه الموصي، كوصية المرأة بالنظر في حق أولادها الأصاغر ونحو ذلك، ومن وصي في شيء لم يصر وصيا في غيره، وإن ظهر على الميت دين يستغرق تركته بعد تفرقة الوصي لم يضمن وإن قال: ضع ثلثي حيث شئت لم يحل له ولا لولده','4577','1657','0');
INSERT INTO [t10649] VALUES (1662,'ومن مات بمكان لا حاكم به ولا وصي حاز بعض من حضره من المسلمين تركته، وعمل الأصلح حينئذ فيها من بيع وغيره','4581','1657','0');
INSERT INTO [t10649] VALUES (1663,'كتاب الفرائض','4582','0','0');
INSERT INTO [t10649] VALUES (1664,'*','4582','1663','0');
INSERT INTO [t10649] VALUES (1665,'وهي العلم بقسمة المواريث','4582','1664','0');
INSERT INTO [t10649] VALUES (1666,'أسباب الإرث رحم ونكاح وولاء، والورثة ذو فرض وعصبة ورحم','4586','1664','0');
INSERT INTO [t10649] VALUES (1667,'فذو الفرض عشرة: الزوجان، والأبوان، والجد والجدة والبنات، وبنات الابن، والأخوات من كل جهة، والإخوة من الام','4588','1664','0');
INSERT INTO [t10649] VALUES (1668,'فللزوج النصف، ومع وجود ولد أو ولد ابن وإن نزل الربع، وللزوجة فأكثر نصف حاليه فيهما،','4590','1664','0');
INSERT INTO [t10649] VALUES (1669,'ولكل من الأب والجد السدس بالفرض مع ذكور الولد أو ولد الابن، ويرثان بالتعصيب مع عدم الولد وولد الابن، وبالفرض والتعصيب مع إناثهما','4591','1664','0');
INSERT INTO [t10649] VALUES (1670,'فصل','4593','1664','0');
INSERT INTO [t10649] VALUES (1671,'والجد لأب وإن علا مع ولد أبوين أو أب كأخ منهم','4593','1670','0');
INSERT INTO [t10649] VALUES (1672,'فإن نقصته المقاسمة عن ثلث المال أعطيه، ومع ذي فرض بعده الأحظ من المقاسمة، أو ثلث ما بقي، أو سدس الكل','4595','1670','0');
INSERT INTO [t10649] VALUES (1673,'فإن لم يبق سوى السدس أعطيه، وسقط الإخوة إلا في الأكدرية،','4597','1670','0');
INSERT INTO [t10649] VALUES (1674,'ولا يعول ولا يفرض لأخت معه إلا بها، وولد الأب إذا انفردوا معه كولد الأبوين،','4599','1670','0');
INSERT INTO [t10649] VALUES (1675,'فإن اجتمعوا فقاسموه أخذ عصبة ولد الأبوين ما بيد ولد الأب، وأنثاهم تمام فرضها، وما بقي لولد الأب','4600','1670','0');
INSERT INTO [t10649] VALUES (1676,'فصل','4602','1664','0');
INSERT INTO [t10649] VALUES (1677,'وللأم السدس مع ولد أو ولد ابن، أو اثنين من إخوة أو أخوات، والثلث مع عدمهم، والسدس مع زوج وأبوين، والربع مع زوجة وأبوين، وللأب مثلاهما','4602','1676','0');
INSERT INTO [t10649] VALUES (1678,'فصل','4606','1664','0');
INSERT INTO [t10649] VALUES (1679,'ترث أم الأم وام الأب وأم أبي الأب وإن علون أمومة السدس','4606','1678','0');
INSERT INTO [t10649] VALUES (1680,'فإن تحاذين فبينهن، ومن قربت فلها وحدها، وترث ام الأب والجد معهما كالعم، وترث الجدة بقرابتين ثلثي السدس،','4607','1678','0');
INSERT INTO [t10649] VALUES (1681,'فلو تزوج بنت خالته فأتت بولد فجدته أم أم أم ولدهما وأم أم أبيه، وإن تزوج بنت عمته فجدته ام أم امه وأم أبي أبيه','4609','1678','0');
INSERT INTO [t10649] VALUES (1682,'فصل','4610','1664','0');
INSERT INTO [t10649] VALUES (1683,'والنصف فرض بنت وحدها، ثم هو لبنت ابن وحدها، ثم لأخت لأبوين أو لأب وحدها، والثلثان لثنتين من الجميع فأكثر إذا لم يعصبن بذكر','4610','1682','0');
INSERT INTO [t10649] VALUES (1684,'والسدس لبنت ابن فأكثر مع بنت، ولأخت فأكثر لأب مع أخت لأبوين مع عدم معصب فيهما','4613','1682','0');
INSERT INTO [t10649] VALUES (1685,'فإن استكمل الثلثين بنات أو هما سقط من دونهن إن لم يعصبهن ذكر بإزائهن أو أنزل منهن، وكذا الأخوات من الأب مع الأخوات لأبوين إن لم يعصبهن أخوهن،','4615','1682','0');
INSERT INTO [t10649] VALUES (1686,'والأخت فأكثر ترث بالتعصيب ما فضل عن فرض البنت فأزيد، وللذكر أو الأنثى من ولد الأم السدس، ولاثنين فأزيد الثلث بينهم بالسوية','4616','1682','0');
INSERT INTO [t10649] VALUES (1687,'فصل','4620','1664','0');
INSERT INTO [t10649] VALUES (1688,'يسقط الأجداد بالأب، والأبعد بالأقرب، والجدات بالأم، وولد الابن بالابن وولد الأبوين بابن وابن ابن وأب، وولد الأب بهم، وبالأخ لأبوين، وولد الام بالولد وبولد الابن وبالأب وأبيه، ويسقط به كل ابن أخ وعم','4620','1687','0');
INSERT INTO [t10649] VALUES (1689,'باب العصبات','4623','1663','0');
INSERT INTO [t10649] VALUES (1690,'وهم كل من لو انفرد لأخذ المال بجهة واحدة، ومع ذي فرض يأخذ ما بقي، فأقربهم ابن فابنه وإن نزل، ثم الأب ثم الجد وإن علا مع عدم أخ لأبوين أو لأب،','4625','1689','0');
INSERT INTO [t10649] VALUES (1691,'ثم هما، ثم بنوهما أبدا، ثم عم لأبوين، ثم عم لأب ثم بنوهما كذلك، ثم أعمام أبيه لأبوين، ثم لأب، ثم بنوهم كذلك، ثم أعمام جده، ثم بنوهم كذلك، لا يرث بنو أب أعلى مع بني أب أقرب وإن نزلوا،','4627','1689','0');
INSERT INTO [t10649] VALUES (1692,'فأخ لأب أولى من عم وابنه وابن أخ لأبوين، وهو أو ابن أخ لأب أولى من ابن ابن أخ لأبوين، ومع الاستواء يقدم من لأبوين، فإن عدم عصبة النسب ورث المعتق ثم عصبته','4628','1689','0');
INSERT INTO [t10649] VALUES (1693,'فصل','4630','1689','0');
INSERT INTO [t10649] VALUES (1694,'يرث الابن وابنه، والأخ لأبوين أو لأب مع أخته مثليها، وكل عصبة غيرهم لا ترث أخته معه شيئا، وابنا عم أحدهما أخ لأم أو زوج له فرضه والباقي لهما،','4630','1693','0');
INSERT INTO [t10649] VALUES (1695,'ويبدأ بذوي الفروض وما بقي للعصبة، ويسقطون في الحمارية','4631','1693','0');
INSERT INTO [t10649] VALUES (1696,'باب أصول المسائل','4634','1663','0');
INSERT INTO [t10649] VALUES (1697,'الفروض ستة: نصف وربع وثمن وثلثان وثلث وسدس، والأصول سبعة','4634','1696','0');
INSERT INTO [t10649] VALUES (1698,'فنصفان أو نصف وما بقي من اثنين، وثلثان أو ثلث وما بقي أو هما من ثلاثة، وربع أو ثمن وما بقي أو مع النصف من أربعة ومن ثمانية، فهذه أربعة لا تعول، والنصف مع الثلثين أو الثلث','4635','1696','0');
INSERT INTO [t10649] VALUES (1699,'أو السدس أو هو وما بقي من ستة، وتعول إلى عشرة شفعا ووترا، والربع مع الثلثين أو الثلث أو السدس من اثني عشر','4637','1696','0');
INSERT INTO [t10649] VALUES (1700,'وتعول إلى سبعة عشر وترا والثمن مع سدس أو ثلثين من أربعة وعشرين، وتعول إلى سبعة وعشرين وإن بقي بعد الفروض شيء ولا عصبة رد على كل فرض بقدره غير الزوجين','4637','1696','0');
INSERT INTO [t10649] VALUES (1701,'باب التصحيح والمناسخات وقسمة التركات','4647','1663','0');
INSERT INTO [t10649] VALUES (1702,'إذا انكسر سهم فريق عليهم ضربت عددهم إن باين سهامهم، أو وفقه إن وافقه بجزء، كثلث ونحوه، في أصل المسألة، وعولها إن عالت، فما بلغ صحت منه، ويصير للواحد ما كان لجماعته أو وفقه','4647','1701','0');
INSERT INTO [t10649] VALUES (1703,'فصل','4650','1701','0');
INSERT INTO [t10649] VALUES (1704,'إذا مات شخص ولم تقسم تركته حتى مات بعض ورثته، فإن ورثوه كالأول، كإخوة فاقسمها على من بقي،','4650','1703','0');
INSERT INTO [t10649] VALUES (1705,'وإن كان ورثة كل ميت لا يرثون غيره، كإخوة لهم بنون، فصحح الأولى واقسم سهم كل ميت على مسألته، وصحح المنكسر كما سبق، وإن لم يرثوا الثاني كالأول صححت الأولى، وقسمت أسهم الثاني على ورثته، فإن انقسمت صحت من أصلها،','4651','1703','0');
INSERT INTO [t10649] VALUES (1706,'وإن لم تنقسم ضربت كل الثانية أو وفقها للسهام في الأولى، ومن له شيء منها فاضربه فيما ضربته فيها، ومن له من الثانية شيء فاضربه فيما تركه الميت أو وفقه، فهو له، وتعمل في الثالث فأكثر عملك في الثاني مع الأول','4653','1703','0');
INSERT INTO [t10649] VALUES (1707,'فصل','4654','1701','0');
INSERT INTO [t10649] VALUES (1708,'إذا أمكن نسبة سهم كل وارث من المسألة بجزء فله كنسبته','4654','1707','0');
INSERT INTO [t10649] VALUES (1709,'باب ذوي الأرحام','4656','1663','0');
INSERT INTO [t10649] VALUES (1710,'يرثون بالتنزيل الذكر والأنثى سواء، فولد البنات، وولد بنات البنين، وولد الأخوات كأمهاتهم،','4657','1709','0');
INSERT INTO [t10649] VALUES (1711,'وبنات الإخوة والأعمام لأبوين أو لأب، وبنات بنيهم، وولد الإخوة لأم كآبائهم','4660','1709','0');
INSERT INTO [t10649] VALUES (1712,'والأخوال والخالات وأبو الأم كالأم والعمات والعم لأم كأب، وكل جدة أدلت بأب بين أمين هي إحداهما، كأم أبي أم، أو بأب أعلى من الجد، كأم أبي الجد','4661','1709','0');
INSERT INTO [t10649] VALUES (1713,'وأبو أم أب، وأبو أم أم، وأخواهما، وأختاهما بمنزلتهم، فيجعل حق كل وارث لمن أدلى به، فإن أدلى جماعة بوارث واستوت منزلتهم منه بلا سبق، كأولاده، فنصيبه لهم،','4663','1709','0');
INSERT INTO [t10649] VALUES (1714,'فابن وبنت لأخت مع بنت لأخت أخرى، لهذه حق أمها وللأوليين حق أمهما، وإن اختلفت منازلهم منه جعلتهم معه كميت اقتسموا إرثه، فإن خلف ثلاث خالات متفرقات، وثلاث عمات متفرقات','4664','1709','0');
INSERT INTO [t10649] VALUES (1715,'فالثلث للخالات أخماسا، والثلثان للعمات أخماسا، وتصح من خمسة عشر، وفي ثلاثة أخوال متفرقين لذي الأم السدس، والباقي لذي الأبوين','4665','1709','0');
INSERT INTO [t10649] VALUES (1716,'فإن كان معهم أبو أم أسقطهم، وفي ثلاث بنات عمومة متفرقين المال للتي للأبوين، وإن أدلى جماعة بجماعة قسمت المال بين المدلى بهم، فما صار لكل واحد أخذه المدلي به، وإن سقط بعضهم ببعض عملت به، والجهات أبوة، وأمومة، وبنوة','4666','1709','0');
INSERT INTO [t10649] VALUES (1717,'باب ميراث الحمل والخنثى المشكل','4670','1663','0');
INSERT INTO [t10649] VALUES (1718,'من خلف ورثة فيهم حمل فطلبوا القسمة وقف للحمل الأكثر من إرث ذكرين أو أنثيين','4670','1717','0');
INSERT INTO [t10649] VALUES (1719,'فإذا ولد أخذ حقه، وما بقي فهو لمستحقه، ومن لا يحجبه يأخذ إرثه كالجدة، ومن ينقصه شيئا اليقين، ومن سقط به لم يعط شيئا، ويرث ويورث إن استهل صارخا','4672','1717','0');
INSERT INTO [t10649] VALUES (1720,'أو عطس أو بكى أو رضع أو تنفس وطال زمن التنفس، أو وجد دليل حياته غير حركة واختلاج، وإن ظهر بعضه فاستهل ثم مات وخرج لم يرث،','4674','1717','0');
INSERT INTO [t10649] VALUES (1721,'وإن جهل المستهل من التوأمين واختلف إرثهما يعين بقرعة والخنثى المشكل يرث نصف ميراث ذكر ونصف ميراث أنثى','4675','1717','0');
INSERT INTO [t10649] VALUES (1722,'باب ميراث المفقود','4678','1663','0');
INSERT INTO [t10649] VALUES (1723,'من خفي خبره بأسر أو سفر غالبه السلامة كتجارة، انتظر به تمام تسعين سنة منذ ولد،','4678','1722','0');
INSERT INTO [t10649] VALUES (1724,'وإن كان غالبه الهلاك، كمن غرق في مركب، فسلم قوم دون قوم، أو فقد من بين أهله أو في مفازة مهلكة، انتظر به تمام أربع سنين منذ فقد','4679','1722','0');
INSERT INTO [t10649] VALUES (1725,'ثم يقسم ماله فيهما، فإن مات مورثه في مدة التربص أخذ كل وارث إذا اليقين ووقف ما بقي، فإن قدم أخذ نصيبه، وإن لم يأت فحكمه حكم ماله، ولباقي الورثة أن يصطلحوا على ما زاد عن حق المفقود فيقتسمونه','4681','1722','0');
INSERT INTO [t10649] VALUES (1726,'باب ميراث الغرقى','4683','1663','0');
INSERT INTO [t10649] VALUES (1727,'إذا مات متوارثان كأخوين لأب بهدم أو غرق أو غربة أو نار، وجهل السابق بالموت ولم يختلفوا فيه، ورث كل واحد من الآخر من تلاد ماله دون ما ورثه منه؛ دفعا للدور','4683','1726','0');
INSERT INTO [t10649] VALUES (1728,'باب ميراث أهل الملل','4686','1663','0');
INSERT INTO [t10649] VALUES (1729,'لا يرث المسلم الكافر إلا بالولاء، ولا الكافر المسلم إلا بالولاء','4686','1728','0');
INSERT INTO [t10649] VALUES (1730,'ويتوارث الحربي والذمي والمستأمن وأهل الذمة يرث بعضهم بعضا مع اتفاق أديانهم لا مع اختلافها، وهم ملل شتى، والمرتد لا يرث أحدا وإن مات على ردته فماله فيء','4687','1728','0');
INSERT INTO [t10649] VALUES (1731,'ويرث المجوس بقرابتين إن أسلموا، أو تحاكموا إلينا قبل إسلامهم، وكذا حكم المسلم يطأ ذات رحم محرم منه بشبهة ولا إرث بنكاح ذات رحم محرم، ولا بعقد لا يقر عليه لو أسلم','4691','1728','0');
INSERT INTO [t10649] VALUES (1732,'باب ميراث المطلقة','4693','1663','0');
INSERT INTO [t10649] VALUES (1733,'من أبان زوجته في صحته أو مرضه غير المخوف ومات به، أو المخوف ولم يمت به لم يتوارثا بل في طلاق رجعي لم تنقض عدته','4693','1732','0');
INSERT INTO [t10649] VALUES (1734,'وإن أبانها في مرض موته المخوف متهما بقصد حرمانها، أو علق إبانتها في صحته على مرضه، أو على فعل له ففعله في مرضه ونحوه لم يرثها، وترثه في العدة وبعدها ما لم تتزوج أو ترتد','4696','1732','0');
INSERT INTO [t10649] VALUES (1735,'باب الإقرار بمشارك في الميراث','4698','1663','0');
INSERT INTO [t10649] VALUES (1736,'إذا أقر كل الورثة ولو أنه واحد بوارث للميت وصدق، أو كان صغيرا أو مجنونا والمقر به مجهول النسب، ثبت نسبه وإرثه، وإن أقر أحد ابنيه بأخ مثله فله ثلث ما بيده، وإن أقر بأخت فلها خمسه','4698','1735','0');
INSERT INTO [t10649] VALUES (1737,'باب ميراث القاتل، والمبعض، والولاء','4701','1663','0');
INSERT INTO [t10649] VALUES (1738,'من انفرد بقتل مورثه، أو شارك فيه مباشرة، أو سببا بلا حق، لم يرثه إن لزمه قود أو دية أو كفارة، والمكلف وغيره سواء','4701','1737','0');
INSERT INTO [t10649] VALUES (1739,'وإن قتل بحق قودا أو حدا أو كفرا أو ببغي','4705','1737','0');
INSERT INTO [t10649] VALUES (1740,'أو صيالة أو حرابة أو شهادة وارثه، أو قتل العادل الباغي وعكسه ورثه، ولا يرث الرقيق ولا يورث،','4708','1737','0');
INSERT INTO [t10649] VALUES (1741,'ويرث من بعضه حر ويورث، ويحجب بقدر ما فيه من الحرية، ومن أعتق عبدا فله عليه الولاء، وإن اختلف دينهما، ولا يرث النساء بالولاء إلا من أعتقن أو أعتقه من أعتقن','4709','1737','0');
INSERT INTO [t10649] VALUES (1742,'كتاب العتق','4713','0','0');
INSERT INTO [t10649] VALUES (1743,'*','4713','1742','0');
INSERT INTO [t10649] VALUES (1744,'وهو من أفضل القرب، ويستحب عتق من له كسب، وعكسه بعكسه، ويصح تعليق العتق بموت، وهو التدبير','4716','1743','0');
INSERT INTO [t10649] VALUES (1745,'باب الكتابة','4719','1742','0');
INSERT INTO [t10649] VALUES (1746,'وهي بيع عبده نفسه بمال مؤجل في ذمته، وتسن مع أمانة العبد وكسبه وتكره مع عدمه، ويجوز بيع المكاتب، ومشتريه يقوم مقام مكاتبه فإن أدى له عتق، وولاؤه له وإن عجز عاد قنا','4719','1745','0');
INSERT INTO [t10649] VALUES (1747,'باب أحكام أمهات الأولاد','4724','1742','0');
INSERT INTO [t10649] VALUES (1748,'إذا أولد حر أمته، أو أمة له ولغيره، أو أمة لولده خلق ولده حرا، حيا ولد أو ميتا قد تبين فيه خلق الإنسان لا مضغة أو جسم بلا تخطيط صارت أم ولد له، تعتق بموته من كل ماله،','4724','1747','0');
INSERT INTO [t10649] VALUES (1749,'وأحكام أم الولد أحكام الأمة، من وطء وخدمة وإجارة ونحوه، لا في نقل الملك في رقبتها، ولا بما يراد له كوقف وبيع ورهن ونحوها','4727','1747','0');
INSERT INTO [t10649] VALUES (1750,'كتاب النكاح','4729','0','0');
INSERT INTO [t10649] VALUES (1751,'*','4729','1750','0');
INSERT INTO [t10649] VALUES (1752,'وهو سنة','4730','1751','0');
INSERT INTO [t10649] VALUES (1753,'وفعله مع الشهوة أفضل من نوافل العبادة، ويجب على من يخاف زنا بتركه','4731','1751','0');
INSERT INTO [t10649] VALUES (1754,'ويسن نكاح واحدة','4734','1751','0');
INSERT INTO [t10649] VALUES (1755,'دينة','4737','1751','0');
INSERT INTO [t10649] VALUES (1756,'أجنبية','4738','1751','0');
INSERT INTO [t10649] VALUES (1757,'بكر','4739','1751','0');
INSERT INTO [t10649] VALUES (1758,'ولود','4740','1751','0');
INSERT INTO [t10649] VALUES (1759,'بلا أم','4742','1751','0');
INSERT INTO [t10649] VALUES (1760,'وله نظر ما يظهر غالبا، مرارا، بلا خلوة','4743','1751','0');
INSERT INTO [t10649] VALUES (1761,'ويحرم التصريح بخطبة المعتدة من وفاة، والمبانة','4747','1751','0');
INSERT INTO [t10649] VALUES (1762,'دون التعريض','4748','1751','0');
INSERT INTO [t10649] VALUES (1763,'ويباحان لمن أبانها دون الثلاث','4749','1751','0');
INSERT INTO [t10649] VALUES (1764,'كرجعية، ويحرمان منها على غير زوجها','4750','1751','0');
INSERT INTO [t10649] VALUES (1765,'والتعريض: إني في مثلك لراغب','4751','1751','0');
INSERT INTO [t10649] VALUES (1766,'وتجيبه: ما يرغب عنك، ونحوهما، فإن أجاب ولي مجبرة، أو أجابت غير المجبرة لمسلم، حرم على غيره خطبتها','4752','1751','0');
INSERT INTO [t10649] VALUES (1767,'وإن رد أو أذن، أو جهل الحال جاز','4755','1751','0');
INSERT INTO [t10649] VALUES (1768,'ويسن العقد يوم الجمعة مساء','4756','1751','0');
INSERT INTO [t10649] VALUES (1769,'بخطبة ابن مسعود','4757','1751','0');
INSERT INTO [t10649] VALUES (1770,'فصل','4760','1751','0');
INSERT INTO [t10649] VALUES (1771,'وأركانه: الزوجان الخاليان من الموانع، والإيجاب، والقبول','4760','1770','0');
INSERT INTO [t10649] VALUES (1772,'ولا يصح ممن يحسن العربية بغير لفظ زوجت، أو أنكحت','4761','1770','0');
INSERT INTO [t10649] VALUES (1773,'وقبلت هذا النكاح','4765','1770','0');
INSERT INTO [t10649] VALUES (1774,'أو تزوجتها، أو تزوجت، أو قبلت، ومن جهلهما لم يلزمه تعلمهما','4766','1770','0');
INSERT INTO [t10649] VALUES (1775,'وكفاه معناهما الخاص بكل لسان','4768','1770','0');
INSERT INTO [t10649] VALUES (1776,'فإن تقدم القبول لم يصح','4769','1770','0');
INSERT INTO [t10649] VALUES (1777,'وإن تأخر عن الإيجاب صح ما داما في المجلس، ولم يتشاغلا بما يقطعه','4770','1770','0');
INSERT INTO [t10649] VALUES (1778,'وإن تفرقا قبله بطل','4771','1770','0');
INSERT INTO [t10649] VALUES (1779,'فصل','4772','1751','0');
INSERT INTO [t10649] VALUES (1780,'وله شروط: أحدها: تعيين الزوجين','4772','1779','0');
INSERT INTO [t10649] VALUES (1781,'فإن أشار الولي إلى الزوجة، أو سماها، أو وصفها بما تتميز به، أو قال: زوجتك بنتي وله واحدة لا أكثر صح','4773','1779','0');
INSERT INTO [t10649] VALUES (1782,'فصل','4775','1751','0');
INSERT INTO [t10649] VALUES (1783,'الثاني: رضاهما','4775','1782','0');
INSERT INTO [t10649] VALUES (1784,'إلا البالغ المعتوه، والمجنونة، والصغير، والبكر ولو مكلفة','4776','1782','0');
INSERT INTO [t10649] VALUES (1785,'لا الثيب، فإن الأب، ووصيه في النكاح يزوجانهم بغير إذنهم، كالسيد مع إمائه','4784','1782','0');
INSERT INTO [t10649] VALUES (1786,'وعبده الصغير، ولا يزوج باقي الأولياء صغيرة دون تسع، ولا صغيرا، ولا كبيرة عاقلة، ولا بنت تسع إلا بإذنهما، وهو صمات البكر، ونطق الثيب،','4788','1782','0');
INSERT INTO [t10649] VALUES (1787,'فصل','4793','1751','0');
INSERT INTO [t10649] VALUES (1788,'الثالث: الولي،','4793','1787','0');
INSERT INTO [t10649] VALUES (1789,'وشروطه: التكليف، والذكورية، والحرية، والرشد في العقد،','4796','1787','0');
INSERT INTO [t10649] VALUES (1790,'واتفاق الدين سوى ما يذكر، والعدالة،','4799','1787','0');
INSERT INTO [t10649] VALUES (1791,'فلا تزوج امرأة نفسها ولا غيرها، ويقدم أبو المرأة في إنكاحها','4804','1787','0');
INSERT INTO [t10649] VALUES (1792,'ثم وصيه فيه، ثم جدها لأب وإن علا، ثم ابنها، ثم بنوه وإن نزلوا، ثم أخوها لأبوين، ثم لأب، ثم بنوهما كذلك، ثم عمها لأبوين، ثم لأب، ثم بنوهما كذلك، ثم أقرب عصبة نسبا كالإرث،','4806','1787','0');
INSERT INTO [t10649] VALUES (1793,'ثم المولى المنعم، ثم أقرب عصبته نسبا، ثم ولاء، ثم السلطان،','4809','1787','0');
INSERT INTO [t10649] VALUES (1794,'فإن عضل الأقرب، أو لم يكن أهلا، أو غاب غيبة منقطعة لا تقطع إلا بكلفة، ومشقة زوج الأبعد،','4810','1787','0');
INSERT INTO [t10649] VALUES (1795,'وإن زوج الأبعد، أو أجنبي من غير عذر لم يصح','4815','1787','0');
INSERT INTO [t10649] VALUES (1796,'فصل','4818','1751','0');
INSERT INTO [t10649] VALUES (1797,'الرابع: الشهادة','4818','1796','0');
INSERT INTO [t10649] VALUES (1798,'فلا يصح إلا بشاهدين، عدلين، ذكرين، مكلفين، سميعين، ناطقين','4821','1796','0');
INSERT INTO [t10649] VALUES (1799,'وليست الكفاءة ـ وهي دين ومنصب، وهو النسب والحرية ـ شرطا في صحته','4824','1796','0');
INSERT INTO [t10649] VALUES (1800,'فلو زوج الأب عفيفة بفاجر، أو عربية بعجمي، فلمن لم يرض من المرأة، أو الأولياء الفسخ','4826','1796','0');
INSERT INTO [t10649] VALUES (1801,'باب المحرمات في النكاح','4831','1750','0');
INSERT INTO [t10649] VALUES (1802,'تحرم أبدا، الأم، وكل جدة وإن علت، والبنت، وبنت الابن، وبنتاهما من حلال وحرام وإن سفلت،','4831','1801','0');
INSERT INTO [t10649] VALUES (1803,'وكل أخت، وبنتها، وبنت ابنتها، وبنت كل أخ، وبنتها، وبنت ابنه، وبنتها وإن سفلت، وكل عمة، وخالة وإن علتا','4833','1801','0');
INSERT INTO [t10649] VALUES (1804,'والملاعنة على الملاعن','4835','1801','0');
INSERT INTO [t10649] VALUES (1805,'ويحرم بالرضاع ما يحرم بالنسب، إلا أم أخته، وأخت ابنه','4836','1801','0');
INSERT INTO [t10649] VALUES (1806,'ويحرم بالعقد زوجة أبيه، وكل جد، وزوجة ابنه وإن نزل، دون بناتهن، وأمهاتهن، وتحرم أم زوجته، وجداتها بالعقد، وبنتها وبنات أولادها بالدخول','4842','1801','0');
INSERT INTO [t10649] VALUES (1807,'فإن بانت الزوجة، أو ماتت بعد الخلوة أبحن','4851','1801','0');
INSERT INTO [t10649] VALUES (1808,'فصل','4853','1801','0');
INSERT INTO [t10649] VALUES (1809,'وتحرم إلى أمد أخت معتدته، وأخت زوجته، وبنتاهما، وعمتاهما، وخالتاهما','4853','1808','0');
INSERT INTO [t10649] VALUES (1810,'فإن طلقت وفرغت العدة أبحن، وإن تزوجهما في عقد أو عقدين معا بطلا','4857','1808','0');
INSERT INTO [t10649] VALUES (1811,'فإن تأخر أحدهما، أو وقع في عدة الأخرى وهي بائن أو رجعية بطل','4859','1808','0');
INSERT INTO [t10649] VALUES (1812,'وتحرم المعتدة، والمستبرأة من غيره','4860','1808','0');
INSERT INTO [t10649] VALUES (1813,'والزانية حتى تتوب وتنقضي عدتها','4862','1808','0');
INSERT INTO [t10649] VALUES (1814,'ومطلقته ثلاثا حتى يطأها زوج غيره، والمحرمة حتى تحل','4864','1808','0');
INSERT INTO [t10649] VALUES (1815,'ولا ينكح كافر مسلمة، ولا مسلم ولو عبدا كافرة إلا حرة كتابية','4869','1808','0');
INSERT INTO [t10649] VALUES (1816,'ولا ينكح حر مسلم أمة مسلمة، إلا أن يخاف عنت العزوبة، لحاجة المتعة، أو الخدمة، ويعجز عن طول حرة، أو ثمن أمة','4872','1808','0');
INSERT INTO [t10649] VALUES (1817,'ولا ينكح عبد سيدته، ولا سيد أمته، وللحر نكاح أمة أبيه، دون أمة ابنه، وليس للحرة نكاح عبد ولدها،','4875','1808','0');
INSERT INTO [t10649] VALUES (1818,'وإن اشترى أحد الزوجين أو ولده الحر، أو مكاتبه الزوج الآخر أو بعضه انفسخ نكاحهما','4879','1808','0');
INSERT INTO [t10649] VALUES (1819,'ومن حرم وطؤها بعقد حرم بملك يمين إلا أمة كتابية','4881','1808','0');
INSERT INTO [t10649] VALUES (1820,'ومن جمع بين محللة ومحرمة في عقد صح فيمن تحل، ولا يصح نكاح خنثى مشكل قبل تبين أمره','4883','1808','0');
INSERT INTO [t10649] VALUES (1821,'باب الشروط والعيوب في النكاح','4886','1750','0');
INSERT INTO [t10649] VALUES (1822,'إذا شرطت طلاق ضرتها، أو أن لا يتسرى أو لا يتزوج عليها،','4889','1821','0');
INSERT INTO [t10649] VALUES (1823,'يخرجها من دارها أو بلدها، أو شرطت نقدا معينا، أو زيادة في مهرها صح، فإن خالفه فلها الفسخ','4892','1821','0');
INSERT INTO [t10649] VALUES (1824,'وإذا زوجه وليته على أن يزوجه الآخر وليته ففعلا ولا مهر بطل النكاحان','4896','1821','0');
INSERT INTO [t10649] VALUES (1825,'فإن سمي لهما مهر صح، وإن تزوجها بشرط أنه متى حللها للأول طلقها، أو نواه بلا شرط،','4899','1821','0');
INSERT INTO [t10649] VALUES (1826,'أو قال: زوجتك إذا جاء رأس الشهر، أو إن رضيت أمها، أو إذا جاء غد فطلقها، أو وقته بمدة بطل الكل','4903','1821','0');
INSERT INTO [t10649] VALUES (1827,'فصل','4912','1821','0');
INSERT INTO [t10649] VALUES (1828,'وإن شرط أن لا مهر لها، أو لا نفقة،','4912','1827','0');
INSERT INTO [t10649] VALUES (1829,'أو أن يقسم لها أقل من ضرتها أو أكثر، أو شرط فيه خيارا، أو إن جاء بالمهر في وقت كذا، وإلا فلا نكاح بينهما','4915','1827','0');
INSERT INTO [t10649] VALUES (1830,'بطل الشرط وصح النكاح، وإن شرطها مسلمة فبانت كتابية','4919','1827','0');
INSERT INTO [t10649] VALUES (1831,'أو شرطها بكرا، أو جميلة، أو نسيبة، أو نفي عيب لا ينفسخ به النكاح فبانت بخلافه فله الفسخ','4921','1827','0');
INSERT INTO [t10649] VALUES (1832,'وإن عتقت تحت حر فلا خيار لها، بل تحت عبد','4925','1827','0');
INSERT INTO [t10649] VALUES (1833,'فصل','4927','1821','0');
INSERT INTO [t10649] VALUES (1834,'ومن وجدت زوجها مجبوبا، أو بقي له ما لا يطأ به فلها الفسخ، وإن ثبتت عنته بإقراره، أو ببينة على إقراره أجل سنة منذ تحاكمه، فإن وطئ فيها وإلا فلها الفسخ','4927','1833','0');
INSERT INTO [t10649] VALUES (1835,'وإن اعترفت أنه وطئها فليس بعنين، ولو قالت في وقت: رضيت به عنينا سقط خيارها أبدا','4933','1833','0');
INSERT INTO [t10649] VALUES (1836,'فصل','4936','1821','0');
INSERT INTO [t10649] VALUES (1837,'والرتق، والقرن، والعفل، والفتق','4936','1836','0');
INSERT INTO [t10649] VALUES (1838,'واستطلاق بول، ونجو، وقروح سيالة في فرج','4937','1836','0');
INSERT INTO [t10649] VALUES (1839,'وباسور، وناصور، وخصاء، وسل ووجاء، وكون أحدهما خنثى واضحا، وجنون ولو ساعة،','4938','1836','0');
INSERT INTO [t10649] VALUES (1840,'وبرص، وجذام يثبت لكل واحد منهما الفسخ','4940','1836','0');
INSERT INTO [t10649] VALUES (1841,'ولو حدث بعد العقد،','4946','1836','0');
INSERT INTO [t10649] VALUES (1842,'أو كان بالآخر عيب مثله','4947','1836','0');
INSERT INTO [t10649] VALUES (1843,'ومن رضي بالعيب، أو وجدت منه دلالته مع علمه فلا خيار له','4948','1836','0');
INSERT INTO [t10649] VALUES (1844,'ولا يتم فسخ أحدهما إلا بحاكم','4950','1836','0');
INSERT INTO [t10649] VALUES (1845,'فإن كان قبل الدخول فلا مهر','4951','1836','0');
INSERT INTO [t10649] VALUES (1846,'وبعده لها المسمى، ويرجع به على الغار إن وجد،','4952','1836','0');
INSERT INTO [t10649] VALUES (1847,'والصغيرة، والمجنونة، والأمة لا تزوج واحدة منهن بمعيب','4955','1836','0');
INSERT INTO [t10649] VALUES (1848,'فإن رضيت الكبيرة مجبوبا، أو عنينا لم تمنع، بل من مجنون، ومجذوم، وأبرص،','4956','1836','0');
INSERT INTO [t10649] VALUES (1849,'ومتى علمت العيب، أو حدث به لم يجبرها وليها على الفسخ','4958','1836','0');
INSERT INTO [t10649] VALUES (1850,'باب نكاح الكفار','4960','1750','0');
INSERT INTO [t10649] VALUES (1851,'حكمه كنكاح المسلمين، ويقرون على فاسده إذا اعتقدوا صحته في شرعهم، ولم يرتفعوا إلينا، فإن أتونا قبل عقده عقدناه على حكمنا','4960','1850','0');
INSERT INTO [t10649] VALUES (1852,'وإن أتونا بعده أو أسلم الزوجان والمرأة تباح إذن أقرا، وإن كانت ممن لا يجوز ابتداء نكاحها فرق بينهما','4962','1850','0');
INSERT INTO [t10649] VALUES (1853,'وإن وطئ حربي حربية فأسلما، وقد اعتقداه نكاحا أقرا، وإلا فسخ، ومتى كان المهر صحيحا أخذته، وإن كان فاسدا وقبضته استقر، وإن لم تقبضه، ولم يسم فرض لها مهر المثل','4964','1850','0');
INSERT INTO [t10649] VALUES (1854,'فصل','4966','1850','0');
INSERT INTO [t10649] VALUES (1855,'وإن أسلم الزوجان معا، أو زوج كتابية فعلى نكاحهما، فإن أسلمت هي أو أحد الزوجين غير الكتابيين قبل الدخول بطل','4966','1854','0');
INSERT INTO [t10649] VALUES (1856,'فإن سبقته فلا مهر، وإن سبقها فلها نصفه، وإن أسلم أحدهما بعد الدخول وقف الأمر على انقضاء العدة، فإن أسلم الآخر فيها دام النكاح، وإلا بان فسخه منذ أسلم الأول','4968','1854','0');
INSERT INTO [t10649] VALUES (1857,'وإن كفرا أو أحدهما بعد الدخول، وقف الأمر على انقضاء العدة، وقبله بطل','4972','1854','0');
INSERT INTO [t10649] VALUES (1858,'باب الصداق','4975','1750','0');
INSERT INTO [t10649] VALUES (1859,'يسن تخفيفه وتسميته في العقد من أربعمائة درهم إلى خمسمائة','4976','1858','0');
INSERT INTO [t10649] VALUES (1860,'وكل ما صح ثمنا أو أجرة صح مهرا وإن قل','4980','1858','0');
INSERT INTO [t10649] VALUES (1861,'وإن أصدقها تعليم قرآن لم يصح، بل فقه وأدب وشعر مباح معلوم،','4982','1858','0');
INSERT INTO [t10649] VALUES (1862,'وإن أصدقها طلاق ضرتها لم يصح، ولها مهر مثلها، ومتى بطل المسمى وجب مهر المثل','4988','1858','0');
INSERT INTO [t10649] VALUES (1863,'فصل','4992','1858','0');
INSERT INTO [t10649] VALUES (1864,'وإن أصدقها ألفا إن كان أبوها حيا، وألفين إن كان ميتا، وجب مهر المثل','4992','1863','0');
INSERT INTO [t10649] VALUES (1865,'وعلى إن كانت لي زوجة بألفين، أو لم تكن بألف يصح بالمسمى','4993','1863','0');
INSERT INTO [t10649] VALUES (1866,'وإذا أجل الصداق أو بعضه صح','4994','1863','0');
INSERT INTO [t10649] VALUES (1867,'فإن عينا أجلا، وإلا فمحله الفرقة','4996','1863','0');
INSERT INTO [t10649] VALUES (1868,'وإن أصدقها مالا مغصوبا، أو خنزيرا ونحوه وجب مهر المثل','4998','1863','0');
INSERT INTO [t10649] VALUES (1869,'وإن وجدت المباح معيبا خيرت بين أرشه وقيمته، وإن تزوجها على ألف لها وألف لأبيها صحت التسمية','5000','1863','0');
INSERT INTO [t10649] VALUES (1870,'فلو طلق قبل الدخول وبعد القبض رجع بالألف، ولا شيء على الأب لهما، ولو شرط ذلك لغير الأب فكل المسمى لها، ومن زوج بنته ولو ثيبا بدون مهر مثلها صح','5004','1863','0');
INSERT INTO [t10649] VALUES (1871,'وإن زوجها به ولي غيره بإذنها صح، وإن لم تأذن فمهر المثل','5006','1863','0');
INSERT INTO [t10649] VALUES (1872,'وإن زوج ابنه الصغير بمهر المثل أو أكثر صح في ذمة الزوج، وإن كان معسرا لم يضمنه الأب','5008','1863','0');
INSERT INTO [t10649] VALUES (1873,'فصل','5010','1858','0');
INSERT INTO [t10649] VALUES (1874,'وتملك المرأة صداقها بالعقد، ولها نماء المعين قبل القبض، وضده بضده،','5010','1873','0');
INSERT INTO [t10649] VALUES (1875,'وإن تلف فمن ضمانها، إلا أن يمنعها زوجها قبضه فيضمنه، ولها التصرف فيه، وعليها زكاته','5012','1873','0');
INSERT INTO [t10649] VALUES (1876,'وإن طلق قبل الدخول، أو الخلوة فله نصفه حكما دون نمائه المنفصل، وفي المتصل له نصف قيمته بدون نمائه','5015','1873','0');
INSERT INTO [t10649] VALUES (1877,'وإن اختلف الزوجان، أو ورثتهما في قدر الصداق، أو عينه، أو فيما يستقر به فقوله، وفي قبضه فقولها','5020','1873','0');
INSERT INTO [t10649] VALUES (1878,'فصل','5025','1858','0');
INSERT INTO [t10649] VALUES (1879,'يصح تفويض البضع بأن يزوج الرجل ابنته المجبرة، أو تأذن امرأة لوليها أن يزوجها بلا مهر','5025','1878','0');
INSERT INTO [t10649] VALUES (1880,'وتفويض المهر بأن يزوجها على ما يشاء أحدهما أو أجنبي','5026','1878','0');
INSERT INTO [t10649] VALUES (1881,'فلها مهر المثل بالعقد، ويفرضه الحاكم بقدره، وإن تراضيا قبله جاز،','5027','1878','0');
INSERT INTO [t10649] VALUES (1882,'ويصح إبراؤها من مهر المثل قبل فرضه، ومن مات منهما قبل الإصابة والفرض ورثه الآخر، ولها مهر نسائها','5029','1878','0');
INSERT INTO [t10649] VALUES (1883,'وإن طلقها قبل الدخول فلها المتعة بقدر يسر زوجها وعسره، ويستقر مهر المثل بالدخول','5030','1878','0');
INSERT INTO [t10649] VALUES (1884,'وإن طلقها بعده فلا متعة، وإذا افترقا في الفاسد قبل الدخول والخلوة فلا مهر، وبعد أحدهما يجب المسمى، ويجب مهر المثل لمن وطئت بشبهة','5032','1878','0');
INSERT INTO [t10649] VALUES (1885,'أو زنا كرها، ولا يجب معه أرش بكارة','5036','1878','0');
INSERT INTO [t10649] VALUES (1886,'وللمرأة منع نفسها حتى تقبض صداقها الحال، فإن كان مؤجلا، أو حل قبل التسليم، أو سلمت نفسها تبرعا فليس لها منعها','5038','1878','0');
INSERT INTO [t10649] VALUES (1887,'فإن أعسر بالمهر الحال فلها الفسخ، ولو بعد الدخول، ولا يفسخه إلا حاكم','5041','1878','0');
INSERT INTO [t10649] VALUES (1888,'باب وليمة العرس','5043','1750','0');
INSERT INTO [t10649] VALUES (1889,'تسن بشاة فأقل، وتجب في أول مرة إجابة مسلم يحرم هجره إليها إن عينه، ولم يكن ثم منكر','5044','1888','0');
INSERT INTO [t10649] VALUES (1890,'فإن دعاه الجفلى، أو في اليوم الثالث، أو دعاه ذمي كرهت الإجابة','5056','1888','0');
INSERT INTO [t10649] VALUES (1891,'ومن صومه واجب دعا وانصرف، والمتنفل يفطر إن جبر، ولا يجب الأكل،','5059','1888','0');
INSERT INTO [t10649] VALUES (1892,'وإباحته متوقفة على صريح إذن أو قرينة،','5061','1888','0');
INSERT INTO [t10649] VALUES (1893,'وإن علم أن ثم منكرا يقدر على تغييره حضر وغيره، وإلا أبى،','5061','1888','0');
INSERT INTO [t10649] VALUES (1894,'وإن حضر ثم علم به أزاله، فإن دام لعجزه عنه انصرف، وإن علم به ولم يره ولم يسمعه خير','5066','1888','0');
INSERT INTO [t10649] VALUES (1895,'وكره النثار، والتقاطه، ومن أخذه أو وقع في حجره فله، ويسن إعلان النكاح، والدف فيه للنساء','5068','1888','0');
INSERT INTO [t10649] VALUES (1896,'((تتمة)) في آداب الأكل والشرب من الروض المربع','5080','1888','0');
INSERT INTO [t10649] VALUES (1897,'باب عشرة النساء','5104','1750','0');
INSERT INTO [t10649] VALUES (1898,'يلزم الزوجين العشرة بالمعروف','5104','1897','0');
INSERT INTO [t10649] VALUES (1899,'ويحرم مطل كل واحد بما يلزمه للآخر، والتكره لبذله، وإذا تم العقد لزم تسليم الحرة التي يوطأ مثلها في بيت الزوج إن طلبه، ولم تشترط دارها أو بلدها','5110','1897','0');
INSERT INTO [t10649] VALUES (1900,'وإذا استمهل أحدهما أمهل العادة وجوبا، لا لعمل جهاز، ويجب تسليم الأمة ليلا فقط، ويباشرها ما لم يضر بها أو يشغلها عن فرض، وله السفر بالحرة ما لم تشترط ضده','5114','1897','0');
INSERT INTO [t10649] VALUES (1901,'ويحرم وطؤها في الحيض والدبر، وله إجبارها على غسل حيض ونجاسة، وأخذ ما تعافه النفس من شعر، وغيره','5118','1897','0');
INSERT INTO [t10649] VALUES (1902,'ولا تجبر الذمية على غسل الجنابة','5130','1897','0');
INSERT INTO [t10649] VALUES (1903,'فصل','5132','1897','0');
INSERT INTO [t10649] VALUES (1904,'ويلزمه أن يبيت عند الحرة ليلة من أربع، وينفرد إن أراد في الباقي','5132','1903','0');
INSERT INTO [t10649] VALUES (1905,'ويلزمه الوطء إن قدر كل ثلث سنة مرة','5134','1903','0');
INSERT INTO [t10649] VALUES (1906,'وإن سافر فوق نصفها وطلبت قدومه وقدر لزمه، فإن أبى أحدهما فرق بينهما بطلبها','5136','1903','0');
INSERT INTO [t10649] VALUES (1907,'وتسن التسمية عند الوطء، وقول الوارد','5138','1903','0');
INSERT INTO [t10649] VALUES (1908,'ويكره كثرة الكلام، والنزع قبل فراغها، والوطء بمرأى أحد، والتحدث به، ويحرم جمع زوجتيه في مسكن واحد بغير رضاهما، وله منعها من الخروج من منزله،','5140','1903','0');
INSERT INTO [t10649] VALUES (1909,'ويستحب إذنه أن تمرض محرمها، وتشهد جنازته، وله منعها من إجارة نفسها، ومن إرضاع ولدها من غيره إلا لضرورته','5147','1903','0');
INSERT INTO [t10649] VALUES (1910,'فصل','5151','1897','0');
INSERT INTO [t10649] VALUES (1911,'وعليه أن يساوي بين زوجاته في القسم، لا في الوطء','5151','1910','0');
INSERT INTO [t10649] VALUES (1912,'وعماده الليل لمن معاشه النهار، والعكس بالعكس، ويقسم لحائض، ونفساء، ومريضة، ومعيبة، ومجنونة مأمونة وغيرها','5153','1910','0');
INSERT INTO [t10649] VALUES (1913,'وإن سافرت بلا إذنه، أو بإذنه في حاجتها، أو أبت السفر معه، أو المبيت عنده في فراشه، فلا قسم لها، ولا نفقة','5157','1910','0');
INSERT INTO [t10649] VALUES (1914,'ومن وهبت قسمها لضرتها بإذنه أو له فجعله لأخرى جاز، فإن رجعت قسم لها مستقبلا','5159','1910','0');
INSERT INTO [t10649] VALUES (1915,'ولا قسم لإمائه، وأمهات أولاده، بل يطأ من شاء متى شاء، وإن تزوج بكرا أقام عندها سبعا ثم دار، وثيبا ثلاثا، وإن أحبت سبعا فعل وقضى مثلهن للبواقي','5161','1910','0');
INSERT INTO [t10649] VALUES (1916,'فصل','5164','1897','0');
INSERT INTO [t10649] VALUES (1917,'النشوز معصيتها إياه فيما يجب عليها، فإذا ظهر منها أماراته، بأن لا تجيبه إلى الاستمتاع، أو تجيبه متبرمة، أو متكرهة، وعظها،','5164','1916','0');
INSERT INTO [t10649] VALUES (1918,'فإن أصرت هجرها في المضجع ما شاء، وفي الكلام ثلاثة أيام، فإن أصرت ضربها غير مبرح','5166','1916','0');
INSERT INTO [t10649] VALUES (1919,'باب الخلع','5174','1750','0');
INSERT INTO [t10649] VALUES (1920,'من صح تبرعه من زوجة، وأجنبي صح بذله لعوضه','5178','1919','0');
INSERT INTO [t10649] VALUES (1921,'فإذا كرهت خلق زوجها، أو خلقه، أو نقص دينه، أو خافت إثما بترك حقه أبيح الخلع، وإلا كره ووقع','5183','1919','0');
INSERT INTO [t10649] VALUES (1922,'فإن عضلها ظلما للافتداء، ولم يكن لزناها، أو نشوزها، أو تركها فرضا ففعلت، أو خالعت الصغيرة، والمجنونة، والسفيهة، أو الأمة بغير إذن سيدها لم يصح الخلع،','5186','1919','0');
INSERT INTO [t10649] VALUES (1923,'ووقع الطلاق رجعيا إن كان بلفظ الطلاق، أو نيته','5189','1919','0');
INSERT INTO [t10649] VALUES (1924,'فصل','5191','1919','0');
INSERT INTO [t10649] VALUES (1925,'والخلع بلفظ صريح الطلاق، أو كنايته، وقصده طلاق بائن','5191','1924','0');
INSERT INTO [t10649] VALUES (1926,'وإن وقع بلفظ الخلع، أو الفسخ، أو الفداء، ولم ينوه طلاقا، كان فسخا لا ينقص عدد الطلاق','5193','1924','0');
INSERT INTO [t10649] VALUES (1927,'ولا يقع بمعتدة من خلع طلاق ولو واجهها به','5195','1924','0');
INSERT INTO [t10649] VALUES (1928,'ولا يصح شرط الرجعة فيه','5198','1924','0');
INSERT INTO [t10649] VALUES (1929,'وإن خالعها بغير عوض، أو بمحرم لم يصح','5200','1924','0');
INSERT INTO [t10649] VALUES (1930,'ويقع الطلاق رجعيا إن كان بلفظ الطلاق أو نيته، وما صح مهرا صح الخلع به، ويكره بأكثر مما أعطاها','5202','1924','0');
INSERT INTO [t10649] VALUES (1931,'وإن خالعت حامل بنفقة عدتها صح، ويصح بالمجهول، فإن خالعته على حمل شجرتها، أو أمتها،','5205','1924','0');
INSERT INTO [t10649] VALUES (1932,'أو ما في يدها، أو بيتها من دراهم، أو متاع، أو على عبد صح، وله مع عدم الحمل والمتاع والعبد أقل مسماه، ومع عدم الدراهم ثلاثة','5207','1924','0');
INSERT INTO [t10649] VALUES (1933,'فصل','5209','1919','0');
INSERT INTO [t10649] VALUES (1934,'وإذا قال: متى، أو: إذا، أو: إن أعطيتني ألفا فأنت طالق طلقت بعطيته، وإن تراخى','5209','1933','0');
INSERT INTO [t10649] VALUES (1935,'وإن قالت: اخلعني على ألف، أو بألف، أو ولك ألف ففعل بانت واستحقها','5211','1933','0');
INSERT INTO [t10649] VALUES (1936,'وطلقني واحدة بألف فطلقها ثلاثا استحقها، وعكسه بعكسه','5212','1933','0');
INSERT INTO [t10649] VALUES (1937,'إلا في واحدة بقيت، وليس للأب خلع زوجة ابنه الصغير، ولا طلاقها، ولا خلع ابنته بشيء من مالها','5213','1933','0');
INSERT INTO [t10649] VALUES (1938,'ولا يسقط الخلع غيره من الحقوق','5216','1933','0');
INSERT INTO [t10649] VALUES (1939,'وإن علق طلاقها بصفة ثم أبانها فوجدت، ثم نكحها فوجدت بعده طلقت كعتق، وإلا فلا','5217','1933','0');
INSERT INTO [t10649] VALUES (1940,'كتاب الطلاق','5222','0','0');
INSERT INTO [t10649] VALUES (1941,'*','5222','1940','0');
INSERT INTO [t10649] VALUES (1942,'يباح للحاجة،','5225','1941','0');
INSERT INTO [t10649] VALUES (1943,'ويكره لعدمها','5226','1941','0');
INSERT INTO [t10649] VALUES (1944,'ويستحب للضرر','5227','1941','0');
INSERT INTO [t10649] VALUES (1945,'ويجب للإيلاء، ويحرم للبدعة','5229','1941','0');
INSERT INTO [t10649] VALUES (1946,'ويصح من زوج مكلف، ومميز يعقله','5231','1941','0');
INSERT INTO [t10649] VALUES (1947,'ومن زال عقله معذورا لم يقع طلاقه','5234','1941','0');
INSERT INTO [t10649] VALUES (1948,'وعكسه الآثم','5235','1941','0');
INSERT INTO [t10649] VALUES (1949,'ومن أكره عليه ظلما بإيلام له أو لولده، أو أخذ مال يضره، أو هدده بأحدها قادر يظن إيقاعه به فطلق تبعا لقوله لم يقع','5237','1941','0');
INSERT INTO [t10649] VALUES (1950,'ويقع الطلاق في نكاح مختلف فيه،','5241','1941','0');
INSERT INTO [t10649] VALUES (1951,'ومن الغضبان','5243','1941','0');
INSERT INTO [t10649] VALUES (1952,'ووكيله كهو،','5247','1941','0');
INSERT INTO [t10649] VALUES (1953,'ويطلق واحدة ومتى شاء، إلا أن يعين له وقتا وعددا،','5248','1941','0');
INSERT INTO [t10649] VALUES (1954,'وامرأته كوكيله في طلاق نفسها','5250','1941','0');
INSERT INTO [t10649] VALUES (1955,'فصل','5253','1941','0');
INSERT INTO [t10649] VALUES (1956,'إذا طلقها مرة، في طهر لم يجامع فيه، وتركها حتى تنقضي عدتها فهو سنة','5253','1955','0');
INSERT INTO [t10649] VALUES (1957,'فتحرم الثلاث إذا،','5255','1955','0');
INSERT INTO [t10649] VALUES (1958,'وإن طلق من دخل بها في حيض أو طهر وطئ فيه فبدعة','5260','1955','0');
INSERT INTO [t10649] VALUES (1959,'يقع','5263','1955','0');
INSERT INTO [t10649] VALUES (1960,'وتسن رجعتها','5271','1955','0');
INSERT INTO [t10649] VALUES (1961,'ولا سنة ولا بدعة لصغيرة، وآيسة، وغير مدخول بها، ومن بان حملها','5272','1955','0');
INSERT INTO [t10649] VALUES (1962,'وصريحه لفظ الطلاق، وما تصرف منه، غير أمر، ومضار ع، ومطلقة اسم فاعل، فيقع به، وإن لم ينوه جاد أو هازل،','5277','1955','0');
INSERT INTO [t10649] VALUES (1963,'فإن نوى بطالق من وثاق، أو في نكاح سابق منه، أو من غيره، أو أراد طاهرا فغلط لم يقبل حكما، ولو سئل: أطلقت امرأتك؟ فقال: نعم؛ وقع، أو ألك امرأة؟ فقال: لا، وأراد الكذب فلا','5281','1955','0');
INSERT INTO [t10649] VALUES (1964,'فصل','5287','1941','0');
INSERT INTO [t10649] VALUES (1965,'وكناياته الظاهرة نحو: أنت خلية، وبرية، وبائن، وبتة، وبتلة، وأنت حرة، وأنت الحرج','5287','1964','0');
INSERT INTO [t10649] VALUES (1966,'والخفية نحو: اخرجي، واذهبي، وذوقي، وتجرعي، واعتدي، واستبرئي، واعتزلي، ولست لي بامرأة، والحقي بأهلك، وما أشبهه','5290','1964','0');
INSERT INTO [t10649] VALUES (1967,'ولا يقع بكناية ولو ظاهرة طلاق إلا بنية مقارنة للفظ إلا حال خصومة، أو غضب، أو جواب سؤالها،','5292','1964','0');
INSERT INTO [t10649] VALUES (1968,'فلو لم يرده، أو أراد غيره في هذه الأحوال لم يقبل حكما، ويقع مع النية بالظاهرة ثلاث، وإن نوى واحدة، وبالخفية ما نواه','5293','1964','0');
INSERT INTO [t10649] VALUES (1969,'فصل','5296','1941','0');
INSERT INTO [t10649] VALUES (1970,'وإن قال: أنت علي حرام، أو كظهر أمي؛ فهو ظهار، ولو نوى به الطلاق، وكذلك ما أحل الله علي حرام','5296','1969','0');
INSERT INTO [t10649] VALUES (1971,'وإن قال: ما أحل الله علي حرام، أعني به الطلاق طلقت ثلاثا، وإن قال: أعني به طلاقا فواحدة، وإن قال: كالميتة، والدم، والخنزير وقع ما نواه، من طلاق، وظهار، ويمين، وإن لم ينو شيئا فظهار، وإن قال: حلفت بالطلاق، وكذب لزمه حكما','5299','1969','0');
INSERT INTO [t10649] VALUES (1972,'وإن قال: أمرك بيدك ملكت ثلاثا، ولو نوى واحدة، ويتراخى ما لم يطأ أو يطلق أو يفسخ','5301','1969','0');
INSERT INTO [t10649] VALUES (1973,'ويختص اختاري نفسك بواحدة وبالمجلس المتصل ما لم يزدها فيهما، فإن ردت أو وطئ أو طلق أو فسخ بطل خيارها','5303','1969','0');
INSERT INTO [t10649] VALUES (1974,'باب ما يختلف به عدد الطلاق','5306','1940','0');
INSERT INTO [t10649] VALUES (1975,'يملك من كله حر، أو بعضه ثلاثا، والعبد اثنتين','5306','1974','0');
INSERT INTO [t10649] VALUES (1976,'حرة كانت زوجتاهما أو أمة، فإذا قال: أنت الطلاق، أو طالق، أو علي، أو يلزمني، وقع ثلاثا بنيتها، وإلا فواحدة','5307','1974','0');
INSERT INTO [t10649] VALUES (1977,'ويقع بلفظ كل الطلاق، أو أكثره، أو عدد الحصى، أو الريح، أو نحو ذلك ثلاث، ولو نوى واحدة، وإن طلق عضوا، أو جزءا مشاعا، أو معينا، أو مبهما، أو قال: نصف طلقة، أو جزءا من طلقة طلقت، وعكسه الروح، والسن، والشعر، والظفر، ونحوه','5311','1974','0');
INSERT INTO [t10649] VALUES (1978,'وإذا قال لمدخول بها: أنت طالق، وكرره وقع العدد، إلا أن ينوي تأكيدا يصح أو إفهاما','5315','1974','0');
INSERT INTO [t10649] VALUES (1979,'وإن كرره ببل، أو بثم، أو بالفاء، أو قال بعدها، أو قبلها، أو معها طلقة وقع اثنتان، وإن لم يدخل بها بانت بالأولى، ولم يلزمه ما بعدها، والمعلق كالمنجز في هذا','5317','1974','0');
INSERT INTO [t10649] VALUES (1980,'فصل','5321','1974','0');
INSERT INTO [t10649] VALUES (1981,'ويصح منه استثناء النصف فأقل','5321','1980','0');
INSERT INTO [t10649] VALUES (1982,'من عدد الطلاق والمطلقات، فإذا قال: أنت طالق طلقتين إلا واحدة وقعت واحدة، وإن قال: ثلاثا إلا واحدة فطلقتان، وإن استثنى بقلبه من عدد المطلقات صح دون عدد الطلقات، وإن قال: أربعكن إلا فلانة طوالق صح الاستثناء،','5323','1980','0');
INSERT INTO [t10649] VALUES (1983,'ولا يصح استثناء لم يتصل عادة، فلو انفصل وأمكن الكلام دونه بطل، وشرطه النية قبل كمال ما استثني منه','5325','1980','0');
INSERT INTO [t10649] VALUES (1984,'باب الطلاق في الماضي والمستقبل','5329','1940','0');
INSERT INTO [t10649] VALUES (1985,'إذا قال: أنت طالق أمس، أو قبل أن أنكحك، ولم ينو وقوعه في الحال لم يقع، وإن أراد بطلاق سبق منه، أو من زيد، وأمكن قبل،','5329','1984','0');
INSERT INTO [t10649] VALUES (1986,'فإن مات، أو جن، أو خرس قبل بيان مراده لم تطلق، وإن قال: طالق ثلاثا قبل قدوم زيد بشهر فقدم قبل مضيه لم تطلق، وبعد شهر وجزء تطلق فيه يقع، فإن خالعها بعد اليمين بيوم، وقدم بعد شهر ويومين صح الخلع، وبطل الطلاق','5331','1984','0');
INSERT INTO [t10649] VALUES (1987,'وعكسها بعد شهر وساعة، وإن قال: طالق قبل موتي طلقت في الحال، وعكسه معه، أو بعده','5333','1984','0');
INSERT INTO [t10649] VALUES (1988,'فصل','5334','1984','0');
INSERT INTO [t10649] VALUES (1989,'وإن قال: أنت طالق إن طرت، أو صعدت السماء، أو قلبت الحجر ذهبا','5334','1988','0');
INSERT INTO [t10649] VALUES (1990,'ونحوه من المستحيل لم تطلق، وتطلق في عكسه فورا، وهو النفي في المستحيل، مثل: لأقتلن الميت، أو لأصعدن السماء ونحوهما،','5335','1988','0');
INSERT INTO [t10649] VALUES (1991,'وأنت طالق اليوم إذا جاء غد لغو، وإذا قال: أنت طالق في هذا الشهر، أو اليوم طلقت في الحال، وإن قال: في غد، أو السبت، أو رمضان طلقت في أوله، وإن قال: أردت آخر الكل دين وقبل','5337','1988','0');
INSERT INTO [t10649] VALUES (1992,'وأنت طالق إلى شهر طلقت عند انقضائه، إلا أن ينوي في الحال فيقع، وطالق إلى سنة تطلق باثني عشر شهرا، فإن عرفها باللام طلقت بانسلاخ ذي الحجة','5339','1988','0');
INSERT INTO [t10649] VALUES (1993,'باب تعليق الطلاق بالشروط','5341','1940','0');
INSERT INTO [t10649] VALUES (1994,'لا يصح إلا من زوج','5344','1993','0');
INSERT INTO [t10649] VALUES (1995,'فإذا علقه بشرط لم تطلق قبله، ولو قال: عجلته،','5346','1993','0');
INSERT INTO [t10649] VALUES (1996,'وإن قال: سبق لساني بالشرط، ولم أرده وقع في الحال، وإن قال: أنت طالق، وقال: أردت إن قمت، لم يقبل حكما','5347','1993','0');
INSERT INTO [t10649] VALUES (1997,'وأدوات الشرط: إن، وإذا، ومتى، وأي، ومن، وكلما، وهي وحدها للتكرار، وكلها، ومهما','5348','1993','0');
INSERT INTO [t10649] VALUES (1998,'بلا لم أو نية فور، أو قرينة للتراخي، ومع لم للفور إلا إن مع عدم نية فور أو قرينة','5351','1993','0');
INSERT INTO [t10649] VALUES (1999,'فإذا قال: إن قمت، أو إذا، أو متى، أو أي وقت، أو من قامت، أو كلما قمت فأنت طالق فمتى وجد طلقت، وإن تكرر الشرط لم يتكرر الحنث، إلا في كلما، وإن لم أطلقك فأنت طالق؛ ولم ينو وقتا، ولم تقم قرينة بفور، ولم يطلقها، طلقت في آخر حياة أولهما موتا،','5353','1993','0');
INSERT INTO [t10649] VALUES (2000,'ومتى لم، أو إذا لم، أو أي وقت لم أطلقك فأنت طالق، ومضى زمن يمكن إيقاعه فيه ولم يفعل طلقت، وكلما لم أطلقك فأنت طالق، ومضى ما يمكن إيقاع ثلاث مرتبة فيه طلقت المدخول بها ثلاثا، وتبين غيرها بالأولى، وإن قمت فقعدت، أو ثم قعدت، أو إن قعدت إذا قمت، أو إن قع','5355','1993','0');
INSERT INTO [t10649] VALUES (2001,'وبالواو تطلق بوجودهما، ولو غير مرتبين، وبأو بوجود أحدهما','5357','1993','0');
INSERT INTO [t10649] VALUES (2002,'فصل','5359','1993','0');
INSERT INTO [t10649] VALUES (2003,'إذا قال: إن حضت فأنت طالق طلقت بأول حيض متيقن، وإذا حضت حيضة تطلق بأول الطهر من حيضة كاملة، وفي: إذا حضت نصف حيضة تطلق في نصف عادتها','5359','2002','0');
INSERT INTO [t10649] VALUES (2004,'فصل','5360','1993','0');
INSERT INTO [t10649] VALUES (2005,'إذا علقه بالحمل فولدت لأقل من ستة أشهر طلقت منذ حلف، وإن قال: إن لم تكوني حاملا فأنت طالق حرم وطؤها قبل استبرائها بحيضة في البائن','5360','2004','0');
INSERT INTO [t10649] VALUES (2006,'وهي عكس الأولى في الأحكام، وإن علق طلقة إن كانت حاملا بذكر، وطلقتين بأنثى فولدتهما طلقت ثلاثا، وإن كان مكانه: إن كان حملك، أو ما في بطنك لم تطلق بهما','5361','2004','0');
INSERT INTO [t10649] VALUES (2007,'فصل','5363','1993','0');
INSERT INTO [t10649] VALUES (2008,'إذا علق طلقة على الولادة بذكر، وطلقتين بأنثى، فولدت ذكرا ثم أنثى حيا أو ميتا طلقت بالأول، وبانت بالثاني، ولم تطلق به، وإن أشكل كيفية وضعهما فواحدة','5363','2007','0');
INSERT INTO [t10649] VALUES (2009,'فصل','5364','1993','0');
INSERT INTO [t10649] VALUES (2010,'إذا علقه على الطلاق ثم علقه على القيام، أو علقه على القيام ثم على وقوع الطلاق، فقامت، طلقت طلقتين فيهما، وإن علقه على قيامها ثم على طلاقه لها فقامت فواحدة، وإن قال: كلما طلقتك أو كلما وقع عليك طلاقي فأنت طالق فوجدا، طلقت في الأولى طلقتين وفي الثانية','5364','2009','0');
INSERT INTO [t10649] VALUES (2011,'فصل','5365','1993','0');
INSERT INTO [t10649] VALUES (2012,'إذا قال: إذا حلفت بطلاقك فأنت طالق، ثم قال: أنت طالق إن قمت طلقت في الحال، لا إن علقه بطلوع الشمس ونحوه؛ لأنه شرط لا حلف، وإن حلفت بطلاقك فأنت طالق، أو إن كلمتك فأنت طالق، وأعاده مرة أخرى طلقت واحدة، ومرتين فثنتان، وثلاثا فثلاث','5365','2011','0');
INSERT INTO [t10649] VALUES (2013,'فصل','5368','1993','0');
INSERT INTO [t10649] VALUES (2014,'إذا قال: إن كلمتك فأنت طالق فتحققي، أو قال: تنحي، أو اسكتي؛ طلقت، وإن بدأتك بكلام فأنت طالق، فقالت: إن بدأتك به فعبدي حر، انحلت يمينه ما لم ينو عدم البداءة في مجلس آخر','5368','2013','0');
INSERT INTO [t10649] VALUES (2015,'فصل','5369','1993','0');
INSERT INTO [t10649] VALUES (2016,'إذا قال: إن خرجت بغير إذني، أو إلا بإذني، أو حتى آذن لك، أو إن خرجت إلى غير الحمام بغير إذني فأنت طالق، فخرجت مرة بإذنه، ثم خرجت بغير إذنه، أو أذن لها ولم تعلم، أو خرجت تريد الحمام وغيره، أو عدلت منه إلى غيره طلقت في الكل','5369','2015','0');
INSERT INTO [t10649] VALUES (2017,'لا إن أذن فيه كلما شاءت، أو قال: إلا بإذن زيد، فمات زيد ثم خرجت','5370','2015','0');
INSERT INTO [t10649] VALUES (2018,'فصل','5371','1993','0');
INSERT INTO [t10649] VALUES (2019,'إذا علقه بمشيئتها بإن أو غيرها من الحروف لم تطلق حتى تشاء، ولو تراخى، فإن قالت: قد شئت إن شئت، فشاء لم تطلق، وإن قال: إن شئت وشاء أبوك أو زيد، لم يقع حتى يشاءا معا، وإن شاء أحدهما فلا، وأنت طالق أو عبدي حر إن شاء الله وقعا','5371','2018','0');
INSERT INTO [t10649] VALUES (2020,'وإن دخلت الدار فأنت طالق إن شاء الله طلقت إن دخلت، وأنت طالق لرضا زيد أو لمشيئته طلقت في الحال، فإن قال: أردت الشرط قبل حكما، وأنت طالق إن رأيت الهلال، فإن نوى رؤيتها لم تطلق حتى تراه، وإلا طلقت بعد الغروب برؤية غيرها','5373','2018','0');
INSERT INTO [t10649] VALUES (2021,'فصل','5375','1993','0');
INSERT INTO [t10649] VALUES (2022,'وإن حلف لا يدخل دارا أو لا يخرج منها فأدخل أو أخرج بعض جسده، أو دخل طاق الباب، أو لا يلبس ثوبا من غزلها فلبس ثوبا فيه منه، أو لا يشرب ماء هذا الإناء فشرب بعضه لم يحنث، وإن فعل المحلوف عليه ناسيا أو جاهلا حنث في طلاق وعتاق فقط','5375','2021','0');
INSERT INTO [t10649] VALUES (2023,'، وإن فعل بعضه لم يحنث إلا أن ينويه، وإن حلف ليفعلنه لم يبر إلا بفعله كله','5376','2021','0');
INSERT INTO [t10649] VALUES (2024,'باب التأويل في الحلف','5381','1940','0');
INSERT INTO [t10649] VALUES (2025,'ومعناه أن يريد بلفظه ما يخالف ظاهره، فإذا حلف وتأول يمينه نفعه إلا أن يكون ظالما، فإن حلفه ظالم: ما لزيد عندك شيء، وله عنده وديعة بمكان فنوى غيره، أو بـ «ما» الذي','5381','2024','0');
INSERT INTO [t10649] VALUES (2026,'أو حلف ما زيد ها هنا؛ ونوى غير مكانه، أو حلف على امرأته لا سرقت مني شيئا فخانته في وديعة، ولم ينوها لم يحنث في الكل','5385','2024','0');
INSERT INTO [t10649] VALUES (2027,'باب الشك في الطلاق','5387','1940','0');
INSERT INTO [t10649] VALUES (2028,'من شك في طلاق، أو شرطه لم يلزمه،','5387','2027','0');
INSERT INTO [t10649] VALUES (2029,'وإن شك في عدده فطلقة، وتباح له، فإذا قال لامرأتيه: إحداكما طالق طلقت المنوية وإلا من قرعت','5389','2027','0');
INSERT INTO [t10649] VALUES (2030,'كمن طلق إحداهما بائنا ونسيها، وإن تبين أن المطلقة غير التي قرعت ردت إليه ما لم تتزوج أو تكن القرعة بحاكم، وإن قال: إن كان هذا الطائر غرابا ففلانة طالق، وإن كان حماما ففلانة، وجهل لم تطلقا','5392','2027','0');
INSERT INTO [t10649] VALUES (2031,'وإن قال لزوجته وأجنبية اسمهما هند: إحداكما أو هند طالق، طلقت امرأته، وإن قال: أردت الأجنبية لم يقبل حكما، إلا بقرينة، وإن قال لمن ظنها زوجته: أنت طالق طلقت الزوجة، وكذا عكسها','5395','2027','0');
INSERT INTO [t10649] VALUES (2032,'باب الرجعة','5398','1940','0');
INSERT INTO [t10649] VALUES (2033,'من طلق بلا عوض زوجة مدخولا بها أو مخلوا بها دون ما له من العدد فله رجعتها في عدتها','5398','2032','0');
INSERT INTO [t10649] VALUES (2034,'ولو كرهت، بلفظ: راجعت امرأتي ونحوه لا نكحتها ونحوه','5400','2032','0');
INSERT INTO [t10649] VALUES (2035,'ويسن الإشهاد، وهي زوجة، لها وعليها حكم الزوجات، لكن لا قسم لها','5402','2032','0');
INSERT INTO [t10649] VALUES (2036,'وتحصل الرجعة أيضا بوطئها، ولا تصح معلقة بشرط،','5406','2032','0');
INSERT INTO [t10649] VALUES (2037,'فإذا طهرت من الحيضة الثالثة، ولم تغتسل، فله رجعتها،','5409','2032','0');
INSERT INTO [t10649] VALUES (2038,'وإن فرغت عدتها قبل رجعتها بانت وحرمت قبل عقد جديد، ومن طلق دون ما يملك ثم راجع، أو تزوج، لم يملك أكثر مما بقي، وطئها زوج غيره، أو لا','5412','2032','0');
INSERT INTO [t10649] VALUES (2039,'فصل','5415','2032','0');
INSERT INTO [t10649] VALUES (2040,'وإن ادعت انقضاء عدتها في زمن يمكن انقضاؤها فيه، أو بوضع الحمل الممكن، وأنكره فقولها، وإن ادعته الحرة بالحيض في أقل من تسعة وعشرين يوما ولحظة لم تسمع دعواها، وإن بدأته فقالت: انقضت عدتي، فقال: كنت راجعتك، أو بدأها به فأنكرته فقولها','5415','2039','0');
INSERT INTO [t10649] VALUES (2041,'فصل','5420','2032','0');
INSERT INTO [t10649] VALUES (2042,'إذا استوفى ما يملك من الطلاق حرمت عليه حتى يطأها زوج في قبل ولو مراهقا','5420','2041','0');
INSERT INTO [t10649] VALUES (2043,'ويكفي تغييب الحشفة أو قدرها مع جب في فرجها مع انتشار وإن لم ينزل','5423','2041','0');
INSERT INTO [t10649] VALUES (2044,'ولا تحل بوطء دبر، وشبهة، وملك يمين، ونكاح فاسد، ولا في حيض، ونفاس، وإحرام، وصيام فرض، ومن ادعت مطلقته المحرمة ـ وقد غابت ـ نكاح من أحلها، وانقضاء عدتها منه، فله نكاحها إن صدقها وأمكن','5425','2041','0');
INSERT INTO [t10649] VALUES (2045,'كتاب الإيلاء','5432','0','0');
INSERT INTO [t10649] VALUES (2046,'وهو حلف زوج بالله تعالى، أو صفته على ترك وطء زوجته في قبلها أكثر من أربعة أشهر','5432','2045','0');
INSERT INTO [t10649] VALUES (2047,'ويصح من كافر، وقن، ومميز، وغضبان، وسكران، ومريض مرجو برؤه، وممن لم يدخل بها، لا من مجنون، ومغمى عليه','5435','2045','0');
INSERT INTO [t10649] VALUES (2048,'وعاجز عن وطء لجب كامل أو شلل، فإذا قال: والله لا وطئتك أبدا، أو عين مدة تزيد على أربعة أشهر، أو حتى ينزل عيسى، أو يخرج الدجال','5439','2045','0');
INSERT INTO [t10649] VALUES (2049,'أو حتى تشربي الخمر، أو تسقطي دينك، أو تهبي مالك، ونحوه فمول، فإذا مضى أربعة أشهر من يمينه ـ ولو قنا ـ فإن وطئ ولو بتغييب حشفة فقد فاء، وإلا أمر بالطلاق','5442','2045','0');
INSERT INTO [t10649] VALUES (2050,'فإن أبى طلق حاكم عليه واحدة أو ثلاثا أو فسخ، وإن وطئ في الدبر أو دون الفرج فما فاء، وإن ادعى بقاء المدة، أو أنه وطئها، وهي ثيب، صدق مع يمينه، وإن كانت بكرا أو ادعت البكارة وشهد بذلك امرأة عدل صدقت','5445','2045','0');
INSERT INTO [t10649] VALUES (2051,'إن ترك وطأها إضرارا بها، بلا يمين ولا عذر فكمول','5450','2045','0');
INSERT INTO [t10649] VALUES (2052,'كتاب الظهار','5452','0','0');
INSERT INTO [t10649] VALUES (2053,'وهو محرم، فمن شبه زوجته، أو بعضها ببعض، أو بكل من تحرم عليه أبدا بنسب، أو رضاع من ظهر، أو بطن','5453','2052','0');
INSERT INTO [t10649] VALUES (2054,'أو عضو آخر لا ينفصل، بقوله لها: أنت علي، أو معي، أو مني كظهر أمي، أو كيد أختي، أو وجه حماتي ونحوه، أو أنت علي حرام، أو كالميتة والدم فهو مظاهر، وإن قالته لزوجها فليس بظهار، وعليها كفارته ويصح من كل زوجة','5457','2052','0');
INSERT INTO [t10649] VALUES (2055,'فصل','5462','2052','0');
INSERT INTO [t10649] VALUES (2056,'ويصح الظهار معجلا، ومعلقا بشرط، فإذا وجد صار مظاهرا، ومطلقا، ومؤقتا','5462','2055','0');
INSERT INTO [t10649] VALUES (2057,'فإن وطئ فيه كفر، وإن فرغ الوقت زال الظهار، ويحرم قبل أن يكفر وطء ودواعيه ممن ظاهر منها،','5463','2055','0');
INSERT INTO [t10649] VALUES (2058,'ولا تثبت الكفارة في الذمة إلا بالوطء وهو العود، ويلزم إخراجها قبله عند العزم عليه، وتلزمه كفارة واحدة بتكريره قبل التكفير من واحدة، ولظهاره من نسائه بكلمة واحدة، وإن ظاهر منهن بكلمات فكفارات','5466','2055','0');
INSERT INTO [t10649] VALUES (2059,'فصل','5472','2052','0');
INSERT INTO [t10649] VALUES (2060,'وكفارته عتق رقبة، فإن لم يجد صام شهرين متتابعين، فإن لم يستطع أطعم ستين مسكينا','5472','2059','0');
INSERT INTO [t10649] VALUES (2061,'ولا تلزم الرقبة إلا لمن ملكها، أو أمكنه ذلك بثمن مثلها، فاضلا عن كفايته دائما، وكفاية من يمونه، وعما يحتاجه من مسكن وخادم ومركوب','5474','2059','0');
INSERT INTO [t10649] VALUES (2062,'وعرض بذلة وثياب تجمل، ومال يقوم كسبه بمؤنته، وكتب علم ووفاء دين، ولا يجزئ في الكفارات كلها إلا رقبة مؤمنة','5477','2059','0');
INSERT INTO [t10649] VALUES (2063,'ولا أم ولد، ويجزئ المدبر، وولد الزنا، والأحمق، والمرهون، والجاني، والأمة الحامل ولو استثنى حملها','5484','2059','0');
INSERT INTO [t10649] VALUES (2064,'فصل','5487','2052','0');
INSERT INTO [t10649] VALUES (2065,'يجب التتابع في الصوم، فإن تخلله رمضان، أو فطر يجب، كعيد، وأيام تشريق، وحيض، وجنون','5487','2064','0');
INSERT INTO [t10649] VALUES (2066,'ومرض مخوف ونحوه، أو أفطر ناسيا، أو مكرها، أو لعذر يبيح الفطر لم ينقطع','5489','2064','0');
INSERT INTO [t10649] VALUES (2067,'ويجزئ التكفير بما يجزئ في فطرة فقط، ولا يجزئ من البر أقل من مد، ولا من غيره أقل من مدين لكل واحد، ممن يجوز دفع الزكاة إليهم','5491','2064','0');
INSERT INTO [t10649] VALUES (2068,'وإن غدى المساكين أو عشاهم لم يجزئه، وتجب النية في التكفير من صوم وغيره، وإن أصاب المظاهر منها ليلا أو نهارا انقطع التتابع، وإن أصاب غيرها ليلا لم ينقطع','5493','2064','0');
INSERT INTO [t10649] VALUES (2069,'كتاب اللعان','5500','0','0');
INSERT INTO [t10649] VALUES (2070,'يشترط في صحته أن يكون بين زوجين، ومن عرف العربية لم يصح لعانه بغيرها','5502','2069','0');
INSERT INTO [t10649] VALUES (2071,'وإن جهلها فبلغته، فإذا قذف امرأته بالزنا فله إسقاط الحد باللعان، فيقول قبلها أربع مرات: أشهد بالله لقد زنت زوجتي هذه، ويشير إليها، ومع غيبتها يسميها وينسبها','5503','2069','0');
INSERT INTO [t10649] VALUES (2072,'وفي الخامسة: وأن لعنة الله عليه إن كان من الكاذبين، ثم تقول هي أربع مرات: أشهد بالله لقد كذب فيما رماني به من الزنا، ثم تقول في الخامسة: وأن غضب الله عليها إن كان من الصادقين','5507','2069','0');
INSERT INTO [t10649] VALUES (2073,'فإن بدأت باللعان قبله، أو نقص أحدهما شيئا من الألفاظ الخمسة، أو لم يحضرهما حاكم، أو نائبه، أو أبدل لفظة أشهد بأقسم، أو أحلف، أو لفظة اللعنة بالإبعاد، أو الغضب بالسخط لم يصح','5511','2069','0');
INSERT INTO [t10649] VALUES (2074,'فصل','5514','2069','0');
INSERT INTO [t10649] VALUES (2075,'وإن قذف زوجته الصغيرة، أو المجنونة عزر، ولا لعان، ومن شرطه قذفها بالزنا لفظا، كزنيت، أو يا زانية، أو رأيتك تزنين في قبل، أو دبر، فإن قال: وطئت بشبهة، أو مكرهة، أو نائمة،','5514','2074','0');
INSERT INTO [t10649] VALUES (2076,'أو قال: لم تزن، ولكن ليس هذا الولد مني فشهدت امرأة ثقة أنه ولد على فراشه لحقه نسبه ولا لعان، وإذا تم سقط عنه الحد، والتعزير، وثبتت الفرقة بينهما بتحريم مؤبد','5517','2074','0');
INSERT INTO [t10649] VALUES (2077,'فصل','5524','2069','0');
INSERT INTO [t10649] VALUES (2078,'من ولدت زوجته من أمكن كونه منه لحقه','5524','2077','0');
INSERT INTO [t10649] VALUES (2079,'بأن تلده بعد نصف سنة منذ أمكن وطؤه، أو دون أربع سنين منذ أبانها، وهو ممن يولد لمثله كابن عشر','5527','2077','0');
INSERT INTO [t10649] VALUES (2080,'ولا يحكم ببلوغه إن شك فيه، ومن اعترف بوطء أمته في الفرج، أو دونه فولدت لنصف سنة، أو أزيد لحقه ولدها، إلا أن يدعي الاستبراء ويحلف عليه،','5531','2077','0');
INSERT INTO [t10649] VALUES (2081,'وإن قال: وطئتها دون الفرج، أو فيه ولم أنزل، أو عزلت لحقه، وإن أعتقها، أو باعها بعد اعترافه بوطئها، فأتت بولد لدون نصف سنة لحقه، والبيع باطل','5534','2077','0');
INSERT INTO [t10649] VALUES (2082,'كتاب العدد','5538','0','0');
INSERT INTO [t10649] VALUES (2083,'*','5538','2082','0');
INSERT INTO [t10649] VALUES (2084,'تلزم العدة كل امرأة فارقت زوجا، خلا بها، مطاوعة، مع علمه بها، وقدرته على وطئها، ولو مع ما يمنعه منهما، أو من أحدهما حسا، أو شرعا، أو وطئها، أو مات عنها','5539','2083','0');
INSERT INTO [t10649] VALUES (2085,'حتى في نكاح فاسد فيه خلاف، وإن كان باطلا وفاقا لم تعتد للوفاة، ومن فارقها حيا قبل وطء وخلوة، أو بعدهما، أو بعد أحدهما، وهو ممن لا يولد لمثله، أو تحملت بماء الزوج أو قبلها أو لمسها بلا خلوة فلا عدة','5541','2083','0');
INSERT INTO [t10649] VALUES (2086,'فصل','5551','2083','0');
INSERT INTO [t10649] VALUES (2087,'والمعتدات ست: الحامل، وعدتها من موت وغيره إلى وضع كل الحمل بما تصير به أمة أم ولد','5551','2086','0');
INSERT INTO [t10649] VALUES (2088,'فإن لم يلحقه لصغره، أو لكونه ممسوحا، أو ولدت لدون ستة أشهر منذ نكحها ونحوه وعاش لم تنقض به، وأكثر مدة الحمل أربع سنين، وأقلها ستة أشهر، وغالبها تسعة أشهر، ويباح إلقاء النطفة قبل أربعين يوما بدواء مباح','5555','2086','0');
INSERT INTO [t10649] VALUES (2089,'فصل','5564','2083','0');
INSERT INTO [t10649] VALUES (2090,'الثانية: المتوفى عنها زوجها بلا حمل منه، قبل الدخول أو بعده','5564','2089','0');
INSERT INTO [t10649] VALUES (2091,'للحرة أربعة أشهر وعشرة، وللأمة نصفها، فإن مات زوج رجعية في عدة طلاق سقطت، وابتدأت عدة وفاة منذ مات','5565','2089','0');
INSERT INTO [t10649] VALUES (2092,'وإن مات في عدة من أبانها في الصحة لم تنتقل، وتعتد من أبانها في مرض موته الأطول من عدة وفاة وطلاق','5568','2089','0');
INSERT INTO [t10649] VALUES (2093,'ما لم تكن أمة، أو ذمية، أو جاءت البينونة منها فلطلاق لا غير، وإن طلق بعض نسائه مبهمة، أو معينة ثم أنسيها، ثم مات قبل قرعة اعتد كل منهن سوى حامل الأطول منهما','5570','2089','0');
INSERT INTO [t10649] VALUES (2094,'الثالثة: الحائل ذات الأقراء ـ وهي الحيض ـ المفارقة في الحياة، فعدتها إن كانت حرة أو مبعضة ثلاثة قروء كاملة، وإلا قرءان','5573','2089','0');
INSERT INTO [t10649] VALUES (2095,'الرابعة: من فارقها حيا ولم تحض لصغر، أو إياس، فتعتد حرة ثلاثة أشهر، وأمة شهرين، ومبعضة بالحساب، ويجبر الكسر','5577','2089','0');
INSERT INTO [t10649] VALUES (2096,'الخامسة: من ارتفع حيضها ولم تدر سببه، فعدتها سنة، تسعة أشهر للحمل، وثلاثة للعدة، وتنقص الأمة شهرا، وعدة من بلغت ولم تحض، والمستحاضة الناسية، والمستحاضة المبتدأة ثلاثة أشهر، والأمة شهران، وإن علمت ما رفعه من مرض أو رضاع، أو غيرهما، فلا تزال في عدة حت','5580','2089','0');
INSERT INTO [t10649] VALUES (2097,'السادسة: امرأة المفقود تتربص ما تقدم في ميراثه ثم تعتد للوفاة، وأمة كحرة في التربص، وفي العدة نصف عدة الحرة، ولا تفتقر إلى حكم حاكم بضرب المدة، وعدة الوفاة، وإن تزوجت فقدم الأول قبل وطء الثاني فهي للأول، وبعده له أخذها زوجة بالعقد الأول، ولو لم يطلق','5588','2089','0');
INSERT INTO [t10649] VALUES (2098,'فصل','5597','2083','0');
INSERT INTO [t10649] VALUES (2099,'ومن مات زوجها الغائب، أو طلقها اعتدت منذ الفرقة، وإن لم تحد، وعدة موطوءة بشبهة، أو زنا، أو بعقد فاسد كمطلقة','5597','2098','0');
INSERT INTO [t10649] VALUES (2100,'وإن وطئت معتدة بشبهة أو نكاح فاسد فرق بينهما، وأتمت عدة الأول، ولا يحتسب منها مقامها عند الثاني، ثم اعتدت للثاني، وتحل له بعقد بعد انقضاء العدتين،','5600','2098','0');
INSERT INTO [t10649] VALUES (2101,'وإن تزوجت في عدتها لم تنقطع حتى يدخل بها، فإذا فارقها بنت على عدتها من الأول، ثم استأنفت العدة من الثاني، وإن أتت بولد من أحدهما انقضت منه عدتها به، ثم اعتدت للآخر، ومن وطئ معتدته البائن بشبهة استأنفت العدة بوطئه، ودخلت فيها بقية الأولى،','5604','2098','0');
INSERT INTO [t10649] VALUES (2102,'وإن نكح من أبانها في عدتها ثم طلقها قبل الدخول بها بنت','5607','2098','0');
INSERT INTO [t10649] VALUES (2103,'فصل','5610','2083','0');
INSERT INTO [t10649] VALUES (2104,'يلزم الإحداد','5610','2103','0');
INSERT INTO [t10649] VALUES (2105,'مدة العدة كل متوفى عنها زوجها في نكاح صحيح، ولو ذمية','5612','2103','0');
INSERT INTO [t10649] VALUES (2106,'أو أمة، أو غير مكلفة، ويباح لبائن من حي، ولا يجب على رجعية وموطوءة بشبهة، أو زنا، أو في نكاح فاسد، أو باطل، أو ملك يمين','5616','2103','0');
INSERT INTO [t10649] VALUES (2107,'والإحداد: اجتناب ما يدعو إلى جماعها ويرغب في النظر إليها،','5620','2103','0');
INSERT INTO [t10649] VALUES (2108,'الزينة، والطيب، والتحسين، والحناء، وما صبغ للزينة، وحلي، وكحل أسود، لا توتيا ونحوها ولا نقاب، وأبيض، ولو كان حسنا','5621','2103','0');
INSERT INTO [t10649] VALUES (2109,'فصل','5627','2083','0');
INSERT INTO [t10649] VALUES (2110,'وتجب عدة الوفاة في المنزل حيث وجبت، فإن تحولت خوفا، أو قهرا، أو بحق، انتقلت حيث شاءت، ولها الخروج لحاجتها نهارا لا ليلا، وإن تركت الإحداد أثمت، وتمت عدتها بمضي زمانها','5627','2109','0');
INSERT INTO [t10649] VALUES (2111,'باب الإستبراء','5631','2082','0');
INSERT INTO [t10649] VALUES (2112,'من ملك أمة يوطأ مثلها من صغير، وذكر، وضدهما حرم عليه وطؤها ومقدماته قبل استبرائها، واستبراء الحامل بوضعها، ومن تحيض بحيضة، والآيسة والصغيرة بمضي شهر','5631','2111','0');
INSERT INTO [t10649] VALUES (2113,'كتاب الرضاع','5638','0','0');
INSERT INTO [t10649] VALUES (2114,'يحرم من الرضاع ما يحرم من النسب','5639','2113','0');
INSERT INTO [t10649] VALUES (2115,'والمحرم خمس رضعات','5644','2113','0');
INSERT INTO [t10649] VALUES (2116,'والسعوط، والوجور، ولبن الميتة، والموطوءة بشبهة، أو بعقد فاسد، أو باطل، أو بزنا محرم،','5654','2113','0');
INSERT INTO [t10649] VALUES (2117,'وعكسه البهيمة، وغير حبلى، ولا موطوءة، فمتى أرضعت امرأة طفلا صار ولدها في النكاح، والنظر، والخلوة، والمحرمية','5657','2113','0');
INSERT INTO [t10649] VALUES (2118,'وولد من نسب لبنها إليه بحمل، أو وطء','5659','2113','0');
INSERT INTO [t10649] VALUES (2119,'ومحارمه محارمه، ومحارمها محارمه، دون أبويه، وأصولهما، وفروعهما، فتباح المرضعة لأبي المرتضع وأخيه من النسب، وأمه وأخته من النسب لأبيه وأخيه، ومن حرمت عليه بنتها فأرضعت طفلة حرمتها عليه','5663','2113','0');
INSERT INTO [t10649] VALUES (2120,'وفسخت نكاحها منه إن كانت زوجة، وكل امرأة أفسدت نكاح نفسها برضاع قبل الدخول فلا مهر لها، وكذا إن كانت طفلة فدبت فرضعت من نائمة،','5665','2113','0');
INSERT INTO [t10649] VALUES (2121,'وبعد الدخول مهرها بحاله، وإن أفسده غيرها فلها على الزوج نصف المسمى قبله، وجميعه بعده، ويرجع الزوج به على المفسد، ومن قال لزوجته: أنت أختي لرضاع بطل النكاح،','5667','2113','0');
INSERT INTO [t10649] VALUES (2122,'فإن كان قبل الدخول وصدقته فلا مهر، وإن كذبته فلها نصفه، ويجب كله بعده، وإن قالت هي ذلك وأكذبها فهي زوجته حكما، وإذا شك في الرضاع أو كماله أو شكت المرضعة ولا بينة فلا تحريم','5669','2113','0');
INSERT INTO [t10649] VALUES (2123,'كتاب النفقات','5674','0','0');
INSERT INTO [t10649] VALUES (2124,'*','5674','2123','0');
INSERT INTO [t10649] VALUES (2125,'يلزم الزوج نفقة زوجته قوتا، وكسوة، وسكناها بما يصلح لمثلها، ويعتبر الحاكم ذلك بحالهما عند التنازع','5674','2124','0');
INSERT INTO [t10649] VALUES (2126,'فيفرض للموسرة تحت الموسر قدر كفايتها من أرفع خبز البلد، وأدمه، ولحما عادة الموسرين بمحلهما، وما يلبس مثلها من حرير وغيره، وللنوم فراش ولحاف وإزار ومخدة، وللجلوس حصير جيد وزلي','5675','2124','0');
INSERT INTO [t10649] VALUES (2127,'وللفقيرة تحت الفقير من أدنى خبز البلد، وأدم يلائمه، وما يلبس مثلها، ويجلس عليه، وللمتوسطة مع المتوسط، والغنية مع الفقير، وعكسها ما بين ذلك عرفا، وعليه مؤونة نظافة زوجته دون خادمها لا دواء، وأجرة طبيب','5677','2124','0');
INSERT INTO [t10649] VALUES (2128,'فصل','5680','2124','0');
INSERT INTO [t10649] VALUES (2129,'ونفقة المطلقة الرجعية، وكسوتها، وسكناها، كالزوجة، ولا قسم لها','5680','2128','0');
INSERT INTO [t10649] VALUES (2130,'والبائن بفسخ، أو طلاق لها ذلك إن كانت حاملا،','5681','2128','0');
INSERT INTO [t10649] VALUES (2131,'والنفقة للحمل لا لها من أجله، ومن حبست ولو ظلما، أو نشزت، أو تطوعت بلا إذنه بصوم','5687','2128','0');
INSERT INTO [t10649] VALUES (2132,'أو حج، أو أحرمت بنذر حج أو صوم، أو صامت عن كفارة أو قضاء رمضان مع سعة وقته، أو سافرت لحاجتها ولو بإذنه سقطت، ولا نفقة ولا سكنى لمتوفى عنها، ولها أخذ نفقة كل يوم من أوله لا قيمتها','5690','2128','0');
INSERT INTO [t10649] VALUES (2133,'ولا عليها أخذها، فإن اتفقا عليه، أو على تأخيرها، أو تعجيلها مدة طويلة أو قليلة جاز، ولها الكسوة كل عام مرة في أوله، وإذا غاب ولم ينفق لزمته نفقة ما مضى، وإن أنفقت في غيبته من ماله فبان ميتا غرمها الوارث ما أنفقته بعد موته','5698','2128','0');
INSERT INTO [t10649] VALUES (2134,'فصل','5703','2124','0');
INSERT INTO [t10649] VALUES (2135,'ومن تسلم زوجته، أو بذلت نفسها، ومثلها يوطأ وجبت نفقتها، ولو مع صغر زوج، ومرضه، وجبه، وعنته، ولها منع نفسها حتى تقبض صداقها الحال، فإن سلمت نفسها طوعا، ثم أرادت المنع لم تملكه،','5703','2134','0');
INSERT INTO [t10649] VALUES (2136,'وإذا أعسر بنفقة القوت، أو الكسوة، أو ببعضها، أو المسكن، فلها فسخ النكاح، فإن غاب ولم يدع لها نفقة، وتعذر أخذها من ماله، واستدانتها عليه، فلها الفسخ بإذن الحاكم','5707','2134','0');
INSERT INTO [t10649] VALUES (2137,'باب نفقة الأقارب والمماليك والبهائم','5715','2123','0');
INSERT INTO [t10649] VALUES (2138,'تجب، أو تتمتها لأبويه وإن علوا، ولولده وإن سفل حتى ذوي الأرحام منهم','5715','2137','0');
INSERT INTO [t10649] VALUES (2139,'حجبه معسر أو لا، ولكل من يرثه بفرض، أو تعصيب لا برحم، سوى عمودي نسبه، سواء ورثه الآخر، كأخ أو لا، كعمة وعتيق، بمعروف، مع فقر من تجب له، وعجزه عن تكسب','5717','2137','0');
INSERT INTO [t10649] VALUES (2140,'إذا فضل عن قوت نفسه وزوجته ورقيقه يومه وليلته وكسوة وسكنى من حاصل أو متحصل، لا من رأس مال وثمن ملك، وآلة صنعة، ومن له وارث غير أب فنفقته عليهم على قدر إرثهم، فعلى الأم الثلث، والثلثان على الجد،','5722','2137','0');
INSERT INTO [t10649] VALUES (2141,'وعلى الجدة السدس، والباقي على الأخ، والأب ينفرد بنفقة ولده، ومن له ابن فقير وأخ موسر فلا نفقة له عليهما، ومن أمه فقيرة، وجدته موسرة، فنفقته على الجدة، ومن عليه نفقة زيد فعليه نفقة زوجته كظئر لحولين','5725','2137','0');
INSERT INTO [t10649] VALUES (2142,'ولا نفقة مع اختلاف دين إلا بالولاء، وعلى الأب أن يسترضع لولده ويؤدي الأجرة، ولا يمنع أمه إرضاعه، ولا يلزمها إلا لضرورة كخوف تلفه، ولها طلب أجرة المثل، ولو أرضعه غيرها مجانا، بائنا كانت أو تحته، وإن تزوجت آخر فله منعها من إرضاع ولد الأول ما لم يضطر إل','5729','2137','0');
INSERT INTO [t10649] VALUES (2143,'فصل','5739','2137','0');
INSERT INTO [t10649] VALUES (2144,'وعليه نفقة رقيقه طعاما، وكسوة، وسكنى، وأن لا يكلفه مشقا كثيرا، وإن اتفقا على المخارجة جاز، ويريحه وقت القائلة، والنوم، والصلاة، ويركبه في السفر عقبة، وإن طلب نكاحا زوجه، أو باعه، وإن طلبته أمة وطئها، أو زوجها، أو باعها','5739','2143','0');
INSERT INTO [t10649] VALUES (2145,'فصل','5744','2137','0');
INSERT INTO [t10649] VALUES (2146,'وعليه علف بهائمه، وسقيها، وما يصلحها، وأن لا يحملها ما تعجز عنه ولا يحلب من لبنها ما يضر ولدها، فإن عجز عن نفقتها أجبر على بيعها، أو إجارتها، أو ذبحها، إن أكلت','5744','2145','0');
INSERT INTO [t10649] VALUES (2147,'باب الحضانة','5749','2123','0');
INSERT INTO [t10649] VALUES (2148,'تجب لحفظ صغير، ومعتوه، ومجنون، والأحق بها أم، ثم أمهاتها القربى فالقربى، ثم أب، ثم أمهاته كذلك، ثم جد، ثم أمهاته كذلك','5749','2147','0');
INSERT INTO [t10649] VALUES (2149,'ثم أخت لأبوين ثم لأم، ثم لأب، ثم خالة لأبوين، ثم لأم، ثم لأب، ثم عمات كذلك، ثم خالات أمه، ثم خالات أبيه، ثم عمات أبيه، ثم بنات إخوته وأخواته، ثم بنات أعمامه وعماته، ثم بنات أعمام أبيه، وبنات عمات أبيه، ثم لباقي العصبة، الأقرب فالأقرب، فإن كانت أنثى ف','5751','2147','0');
INSERT INTO [t10649] VALUES (2150,'أو كان غير أهل انتقلت إلى من بعده، ولا حضانة لمن فيه رق، ولا لفاسق، ولا لكافر ولا لمزوجة بأجنبي من محضون من حين عقد، فإن زال المانع رجع إلى حقه، وإن أراد أحد أبويه سفرا طويلا إلى بلد بعيد ليسكنه، وهو وطريقه آمنان فحضانته لأبيه، وإن بعد السفر لحاجة، أ','5754','2147','0');
INSERT INTO [t10649] VALUES (2151,'فصل','5763','2147','0');
INSERT INTO [t10649] VALUES (2152,'وإذا بلغ الغلام سبع سنين عاقلا خير بين أبويه، فكان مع من اختار منهما، ولا يقر بيد من لا يصونه ويصلحه، وأبو الأنثى أحق بها بعد السبع، ويكون الذكر بعد رشده حيث شاء، والأنثى عند أبيها حتى يتسلمها زوجها','5763','2151','0');
INSERT INTO [t10649] VALUES (2153,'كتاب الجنايات','5768','0','0');
INSERT INTO [t10649] VALUES (2154,'*','5768','2153','0');
INSERT INTO [t10649] VALUES (2155,'وهي عمد يختص القود به بشرط القصد، وشبه عمد','5769','2154','0');
INSERT INTO [t10649] VALUES (2156,'وخطأ، فالعمد أن يقصد من يعلمه آدميا معصوما فيقتله بما يغلب على الظن موته به','5771','2154','0');
INSERT INTO [t10649] VALUES (2157,'مثل أن يجرحه بما له مور في البدن، أو يضربه بحجر كبير ونحوه','5773','2154','0');
INSERT INTO [t10649] VALUES (2158,'أو يلقي عليه حائطا، أو يلقيه من شاهق، أو في نار، أو ماء يغرقه ولا يمكنه التخلص منهما، أو يخنقه أو يحبسه ويمنع عنه الطعام أو الشراب فيموت من ذلك في مدة يموت فيها غالبا','5775','2154','0');
INSERT INTO [t10649] VALUES (2159,'أو يقتله بسحر، أو بسم، أو شهدت عليه بينة بما يوجب قتله، ثم رجعوا، وقالوا: عمدنا قتله، ونحو ذلك، وشبه العمد أن يقصد جناية لا تقتل غالبا ولم يجرحه بها، كمن ضربه في غير مقتل بسوط، أو عصا صغيرة','5778','2154','0');
INSERT INTO [t10649] VALUES (2160,'أو لكزه، ونحوه [(١٤)]، والخطأ أن يفعل ما له فعله، مثل أن يرمي ما يظنه صيدا، أو غرضا، أو شخصا، فيصيب آدميا لم يقصده، وعمد الصبي والمجنون','5781','2154','0');
INSERT INTO [t10649] VALUES (2161,'فصل','5787','2154','0');
INSERT INTO [t10649] VALUES (2162,'تقتل الجماعة بالواحد، وإن سقط القود أدوا دية واحدة،','5787','2161','0');
INSERT INTO [t10649] VALUES (2163,'ومن أكره مكلفا على قتل مكافئه فقتله فالقتل أو الدية عليهما','5789','2161','0');
INSERT INTO [t10649] VALUES (2164,'وإن أمر بالقتل غير مكلف أو مكلفا يجهل تحريمه','5792','2161','0');
INSERT INTO [t10649] VALUES (2165,'أو أمر به السلطان ظلما من لا يعرف ظلمه فيه فقتل، فالقود أو الدية على الآمر، وإن قتل المأمور المكلف عالما بتحريم القتل فالضمان عليه دون الآمر، وإن اشترك فيه اثنان لا يجب القود على أحدهما منفردا لأبوة أو غيرها فالقود على الشريك','5793','2161','0');
INSERT INTO [t10649] VALUES (2166,'فإن عدل إلى طلب المال لزمه نصف الدية','5796','2161','0');
INSERT INTO [t10649] VALUES (2167,'باب شروط القصاص','5797','2153','0');
INSERT INTO [t10649] VALUES (2168,'وهي أربعة: عصمة المقتول، فلو قتل مسلم، أو ذمي حربيا أو مرتدا لم يضمنه بقصاص ولا دية','5799','2167','0');
INSERT INTO [t10649] VALUES (2169,'الثاني: التكليف، فلا قصاص على صغير ولا مجنون','5800','2167','0');
INSERT INTO [t10649] VALUES (2170,'الثالث: المكافأة، بأن يساويه في الدين، والحرية، والرق، فلا يقتل مسلم بكافر، ولا حر بعبد، وعكسه يقتل، ويقتل الذكر بالأنثى، والأنثى بالذكر','5801','2167','0');
INSERT INTO [t10649] VALUES (2171,'الرابع: عدم الولادة، فلا يقتل أحد الأبوين وإن علا بالولد وإن سفل، ويقتل الولد بكل منهما','5805','2167','0');
INSERT INTO [t10649] VALUES (2172,'باب استيفاء القصاص','5809','2153','0');
INSERT INTO [t10649] VALUES (2173,'يشترط له ثلاثة شروط: أحدها: كون مستحقه مكلفا، فإن كان صبيا، أو مجنونا لم يستوف، وحبس الجاني إلى البلوغ والإفاقة','5809','2172','0');
INSERT INTO [t10649] VALUES (2174,'الثاني: اتفاق الأولياء المشتركين فيه على استيفائه، وليس لبعضهم أن ينفرد به، وإن كان من بقي غائبا، أو صغيرا، أو مجنونا، انتظر القدوم، والبلوغ، والعقل، الثالث: أن يؤمن في الاستيفاء أن يتعدى الجاني، فإذا وجب على حامل أو حائل فحملت لم تقتل حتى تضع الولد','5811','2172','0');
INSERT INTO [t10649] VALUES (2175,'ثم إن وجد من يرضعه، وإلا تركت حتى تفطمه، ولا يقتص منها في الطرف حتى تضع، والحد في ذلك كالقصاص','5813','2172','0');
INSERT INTO [t10649] VALUES (2176,'فصل','5817','2172','0');
INSERT INTO [t10649] VALUES (2177,'ولا يستوفى قصاص إلا بحضرة سلطان أو نائبه، وآلة ماضية، ولا يستوفى في النفس إلا بضرب العنق بسيف، ولو كان الجاني قتله بغيره','5817','2176','0');
INSERT INTO [t10649] VALUES (2178,'باب العفو عن القصاص','5820','2153','0');
INSERT INTO [t10649] VALUES (2179,'يجب بالعمد القود، أو الدية، فيخير الولي بينهما، وعفوه مجانا أفضل،','5820','2178','0');
INSERT INTO [t10649] VALUES (2180,'فإن اختار القود، أو عفا عن الدية فقط فله أخذها والصلح على أكثر منها','5823','2178','0');
INSERT INTO [t10649] VALUES (2181,'وإن اختارها، أو عفا مطلقا، أو هلك الجاني، فليس له غيرها','5825','2178','0');
INSERT INTO [t10649] VALUES (2182,'وإذا قطع إصبعا عمدا فعفا عنها، ثم سرت إلى الكف أو النفس، وكان العفو على غير شيء فهدر، وإن كان العفو على مال فله تمام الدية','5827','2178','0');
INSERT INTO [t10649] VALUES (2183,'وإن وكل من يقتص ثم عفا، فاقتص وكيله ولم يعلم فلا شيء عليهما،','5829','2178','0');
INSERT INTO [t10649] VALUES (2184,'وإن وجب لرقيق قود، أو تعزير قذف، فطلبه وإسقاطه إليه، فإن مات فلسيده','5829','2178','0');
INSERT INTO [t10649] VALUES (2185,'باب ما يوجب القصاص فيما دون النفس','5833','2153','0');
INSERT INTO [t10649] VALUES (2186,'من أقيد بأحد في النفس أقيد به في الطرف والجراح، ومن لا فلا، ولا يجب إلا بما يوجب القود في النفس','5833','2185','0');
INSERT INTO [t10649] VALUES (2187,'وهو نوعان: أحدهما: في الطرف، فتؤخذ العين، والأنف، والأذن، والسن، والجفن، والشفة، واليد، والرجل، والإصبع، والكف، والمرفق، والذكر، والخصية، والألية، والشفر، كل واحد من ذلك بمثله','5835','2185','0');
INSERT INTO [t10649] VALUES (2188,'وللقصاص في الطرف شروط: الأول: الأمن من الحيف بأن يكون القطع من مفصل، أو له حد ينتهي إليه، كمارن الأنف، وهو ما لان منه','5837','2185','0');
INSERT INTO [t10649] VALUES (2189,'الثاني: المماثلة في الاسم والموضع','5840','2185','0');
INSERT INTO [t10649] VALUES (2190,'فلا تؤخذ يمين بيسار، ولا يسار بيمين، ولا خنصر ببنصر، ولا أصلي بزائد، ولا عكسه، ولو تراضيا لم يجز','5841','2185','0');
INSERT INTO [t10649] VALUES (2191,'الثالث: استواؤهما في الصحة والكمال، فلا تؤخذ صحيحة بشلاء، ولا كاملة الأصابع بناقصة، ولا عين صحيحة بقائمة، ويؤخذ عكسه ولا أرش','5842','2185','0');
INSERT INTO [t10649] VALUES (2192,'فصل','5846','2185','0');
INSERT INTO [t10649] VALUES (2193,'النوع الثاني: الجراح، فيقتص في كل جرح ينتهي إلى عظم، كالموضحة،','5846','2192','0');
INSERT INTO [t10649] VALUES (2194,'وجرح العضد، والساق، والفخذ، والقدم، ولا يقتص في غير ذلك من الشجاج والجروح غير كسر سن، إلا أن يكون أعظم من الموضحة، كالهاشمة، والمنقلة، والمأمومة، فله أن يقتص موضحة، وله أرش الزائد','5847','2192','0');
INSERT INTO [t10649] VALUES (2195,'وإذا قطع جماعة طرفا أو جرحوا جرحا يوجب القود فعليهم القود','5849','2192','0');
INSERT INTO [t10649] VALUES (2196,'وسراية الجناية مضمونة في النفس فما دونها وسراية القود مهدورة، ولا يقتص من عضو وجرح قبل برئه، كما لا تطلب له دية','5851','2192','0');
INSERT INTO [t10649] VALUES (2197,'كتاب الديات','5854','0','0');
INSERT INTO [t10649] VALUES (2198,'*','5854','2197','0');
INSERT INTO [t10649] VALUES (2199,'كل من أتلف إنسانا بمباشرة، أو سبب لزمته ديته،','5854','2198','0');
INSERT INTO [t10649] VALUES (2200,'فإن كانت عمدا محضا ففي مال الجاني حالة وشبه العمد والخطأ على عاقلته، وإن غصب حرا صغيرا فنهشته حية، أو أصابته صاعقة، أو مات بمرض، أو غل حرا مكلفا وقيده، فمات بالصاعقة، أو الحية وجبت الدية','5857','2198','0');
INSERT INTO [t10649] VALUES (2201,'فصل','5863','2198','0');
INSERT INTO [t10649] VALUES (2202,'وإذا أدب الرجل ولده، أو سلطان رعيته، أو معلم صبيه، ولم يسرف لم يضمن ما تلف به، ولو كان التأديب لحامل فأسقطت جنينا ضمنه المؤدب،','5863','2201','0');
INSERT INTO [t10649] VALUES (2203,'وإن طلب السلطان امرأة لكشف حق الله تعالى، أو استعدى عليها رجل بالشرط في دعوى له فأسقطت ضمنه السلطان والمستعدي، ولو ماتت فزعا لم يضمنا','5868','2201','0');
INSERT INTO [t10649] VALUES (2204,'ومن أمر شخصا مكلفا أن ينزل بئرا، أو يصعد شجرة فهلك به لم يضمنه','5871','2201','0');
INSERT INTO [t10649] VALUES (2205,'ولو أن الآمر سلطان، كما لو استأجره سلطان أو غيره','5873','2201','0');
INSERT INTO [t10649] VALUES (2206,'باب مقادير ديات النفس','5879','2197','0');
INSERT INTO [t10649] VALUES (2207,'دية الحر المسلم مائة بعير، أو ألف مثقال ذهبا، أو اثنا عشر ألف درهم فضة، أو مائتا بقرة، أو ألفا شاة','5879','2206','0');
INSERT INTO [t10649] VALUES (2208,'هذه أصول الدية، فأيها أحضر من تلزمه لزم الولي قبوله، ففي قتل العمد وشبهه خمس وعشرون بنت مخاض، وخمس وعشرون بنت لبون، وخمس وعشرون حقة، وخمس وعشرون جذعة','5881','2206','0');
INSERT INTO [t10649] VALUES (2209,'وفي الخطإ تجب أخماسا، ثمانون من الأربعة المذكورة، وعشرون من بني مخاض','5885','2206','0');
INSERT INTO [t10649] VALUES (2210,'ولا تعتبر القيمة في ذلك، بل السلامة، ودية الكتابي نصف دية المسلم','5888','2206','0');
INSERT INTO [t10649] VALUES (2211,'ودية المجوسي والوثني ثمانمائة درهم، ونساؤهم على النصف كالمسلمين','5891','2206','0');
INSERT INTO [t10649] VALUES (2212,'ودية قن قيمته، وفي جراحه ما نقصه بعد البرء، ويجب في الجنين ذكرا كان أو أنثى عشر دية أمه غرة','5895','2206','0');
INSERT INTO [t10649] VALUES (2213,'وعشر قيمتها إن كان مملوكا وتقدر الحرة أمة، وإن جنى رقيق خطأ أو عمدا لا قود فيه، أو فيه قود واختير فيه المال، أو أتلف مالا بغير إذن سيده تعلق ذلك برقبته، فيخير سيده بين أن يفديه بأرش جنايته، أو يسلمه إلى ولي الجناية فيملكه، أو يبيعه ويدفع ثمنه','5900','2206','0');
INSERT INTO [t10649] VALUES (2214,'باب ديات الأعضاء ومنافعها','5904','2197','0');
INSERT INTO [t10649] VALUES (2215,'من أتلف ما في الإنسان منه شيء واحد، كالأنف، واللسان، والذكر، ففيه دية النفس','5904','2214','0');
INSERT INTO [t10649] VALUES (2216,'وما فيه منه شيئان، كالعينين، والأذنين، والشفتين، واللحيين، وثديي المرأة، وثندؤتي الرجل، واليدين، والرجلين، والأليتين، والأنثيين، وإسكتي المرأة ففيهما الدية، وفي أحدهما نصفها','5905','2214','0');
INSERT INTO [t10649] VALUES (2217,'وفي المنخرين ثلثا الدية، وفي الحاجز بينهما ثلثها، وفي الأجفان الأربعة الدية، وفي كل جفن ربعها، وفي أصابع اليدين الدية كأصابع الرجلين، وفي كل أصبع عشر الدية، وفي كل أنملة ثلث عشر الدية، والإبهام مفصلان، وفي كل مفصل نصف عشر الدية كدية السن','5908','2214','0');
INSERT INTO [t10649] VALUES (2218,'فصل','5912','2214','0');
INSERT INTO [t10649] VALUES (2219,'وفي كل حاسة دية كاملة، وهي السمع، والبصر، والشم، والذوق وكذا في الكلام والعقل، ومنفعة المشي، والأكل، والنكاح، وعدم استمساك البول، أو الغائط','5912','2218','0');
INSERT INTO [t10649] VALUES (2220,'وفي كل واحد من الشعور الأربعة الدية، وهي شعر الرأس، واللحية، والحاجبين، وأهداب العينين فإن عاد فنبت سقط موجبه، وفي عين الأعور الدية كاملة','5916','2218','0');
INSERT INTO [t10649] VALUES (2221,'وإن قلع الأعور عين الصحيح المماثلة لعينه الصحيحة عمدا فعليه دية كاملة ولا قصاص وفي قطع يد الأقطع نصف الدية كغيره','5918','2218','0');
INSERT INTO [t10649] VALUES (2222,'باب الشجاج وكسر العظام','5923','2197','0');
INSERT INTO [t10649] VALUES (2223,'الشجة: الجرح في الرأس والوجه خاصة، وهي عشر: الحارصة التي تحرص الجلد، أي: تشقه قليلا ولا تدميه، ثم البازلة الدامية الدامعة وهي التي يسيل منها الدم، ثم الباضعة وهي التي تبضع اللحم، ثم المتلاحمة وهي الغائصة في اللحم، ثم السمحاق وهي ما بينها وبين العظم ق','5923','2222','0');
INSERT INTO [t10649] VALUES (2224,'وفي الموضحة وهي ما توضح اللحم وتبرزه خمسة أبعرة، ثم الهاشمة وهي التي توضح العظم وتهشمه وفيها عشرة أبعرة، ثم المنقلة وهي ما توضح العظم وتهشمه وتنقل عظامها، وفيها خمس عشرة من الإبل، وفي كل واحدة من المأمومة والدامغة ثلث الدية، وفي الجائفة ثلث الدية،','5925','2222','0');
INSERT INTO [t10649] VALUES (2225,'وهي التي تصل إلى باطن الجوف، وفي الضلع وكل واحدة من الترقوتين بعير، وفي كسر الذراع وهو الساعد الجامع لعظمي الزند، والعضد، والفخذ والساق إذا جبر ذلك مستقيما بعيران','5927','2222','0');
INSERT INTO [t10649] VALUES (2226,'وما عدا ذلك من الجراح وكسر العظام ففيه حكومة، والحكومة: أن يقوم المجني عليه كأنه عبد لا جناية به، ثم يقوم وهي به قد برئت، فما نقص من القيمة فله مثل نسبته من الدية','5931','2222','0');
INSERT INTO [t10649] VALUES (2227,'كأن قيمته عبدا سليما ستون، وقيمته بالجناية خمسون، ففيه سدس ديته، إلا أن تكون الحكومة في محل له مقدر فلا يبلغ بها المقدر','5932','2222','0');
INSERT INTO [t10649] VALUES (2228,'باب العاقلة وما تحمل','5934','2197','0');
INSERT INTO [t10649] VALUES (2229,'عاقلة الإنسان عصباته كلهم من النسب والولاء، قريبهم وبعيدهم حاضرهم وغائبهم، حتى عمودي نسبه','5934','2228','0');
INSERT INTO [t10649] VALUES (2230,'ولا عقل على رقيق وغير مكلف، ولا فقير، ولا أنثى، ولا مخالف لدين الجاني، ولا تحمل العاقلة عمدا محضا، ولا عبدا، ولا صلحا، ولا اعترافا لم تصدقه به، ولا ما دون ثلث الدية التامة','5937','2228','0');
INSERT INTO [t10649] VALUES (2231,'فصل','5946','2228','0');
INSERT INTO [t10649] VALUES (2232,'من قتل نفسا محرمة خطأ مباشرة، أو تسببا بغير حق فعليه الكفارة','5946','2231','0');
INSERT INTO [t10649] VALUES (2233,'باب القسامة','5955','2197','0');
INSERT INTO [t10649] VALUES (2234,'وهي أيمان مكررة في دعوى قتل معصوم من شرطها اللوث','5955','2233','0');
INSERT INTO [t10649] VALUES (2235,'وهو العداوة الظاهرة، كالقبائل التي يطلب بعضها بعضا بالثأر، فمن ادعي عليه القتل من غير لوث حلف يمينا واحدة وبرئ','5960','2233','0');
INSERT INTO [t10649] VALUES (2236,'ويبدأ بأيمان الرجال من ورثة الدم، فيحلفون خمسين يمينا، فإن نكل الورثة، أو كانوا نساء حلف المدعى عليه خمسين يمينا وبرئ','5963','2233','0');
INSERT INTO [t10649] VALUES (2237,'كتاب الحدود','5968','0','0');
INSERT INTO [t10649] VALUES (2238,'*','5968','2237','0');
INSERT INTO [t10649] VALUES (2239,'لا يجب الحد إلا على بالغ','5970','2238','0');
INSERT INTO [t10649] VALUES (2240,'عاقل، ملتزم، عالم بالتحريم، فيقيمه الإمام أو نائبه في غير مسجد','5974','2238','0');
INSERT INTO [t10649] VALUES (2241,'ويضرب الرجل في الحد قائما بسوط لا جديد ولا خلق، ولا يمد، ولا يربط، ولا يجرد، بل يكون عليه قميص، أو قميصان','5979','2238','0');
INSERT INTO [t10649] VALUES (2242,'ولا يبالغ بضربه بحيث يشق الجلد، ويفرق الضرب على بدنه، ويتقى الرأس والوجه والفرج والمقاتل','5981','2238','0');
INSERT INTO [t10649] VALUES (2243,'والمرأة كالرجل فيه إلا أنها تضرب جالسة، وتشد عليها ثيابها، وتمسك يداها لئلا تنكشف،','5983','2238','0');
INSERT INTO [t10649] VALUES (2244,'وأشد الجلد جلد الزنا، ثم القذف، ثم الشرب، ثم التعزير، ومن مات في حد فالحق قتله، ولا يحفر للمرجوم في الزنا','5985','2238','0');
INSERT INTO [t10649] VALUES (2245,'باب حد الزنا','5991','2237','0');
INSERT INTO [t10649] VALUES (2246,'إذا زنا المحصن رجم حتى يموت','5991','2245','0');
INSERT INTO [t10649] VALUES (2247,'والمحصن: من وطئ امرأته المسلمة أو الذمية في نكاح صحيح، وهما بالغان، عاقلان، حران، فإن اختل شرط منها في أحدهما فلا إحصان لواحد منهما','5995','2245','0');
INSERT INTO [t10649] VALUES (2248,'وإذا زنا الحر غير المحصن جلد مائة جلدة وغرب عاما','5998','2245','0');
INSERT INTO [t10649] VALUES (2249,'ولو امرأة، والرقيق خمسين جلدة ولا يغرب، وحد لوطي كزان','6000','2245','0');
INSERT INTO [t10649] VALUES (2250,'ولا يجب الحد إلا بثلاثة شروط: أحدها: تغييب حشفته الأصلية كلها في قبل، أو دبر أصليين حراما محضا من آدمي حي','6007','2245','0');
INSERT INTO [t10649] VALUES (2251,'الثاني: انتفاء الشبهة، فلا يحد بوطء أمة له فيها شرك، أو لولده، أو وطئ امرأة ظنها زوجته أو سريته، أو في نكاح باطل اعتقد صحته أو نكاح أو ملك مختلف فيه، ونحوه، أو أكرهت المرأة على الزنا','6011','2245','0');
INSERT INTO [t10649] VALUES (2252,'الثالث: ثبوت الزنا، ولا يثبت إلا بأحد أمرين: أحدهما: أن يقر به أربع مرات في مجلس، أو مجالس، ويصرح بذكر حقيقة الوطء، ولا ينزع عن إقراره حتى يتم عليه الحد','6019','2245','0');
INSERT INTO [t10649] VALUES (2253,'الثاني: أن يشهد عليه في مجلس واحد بزنا واحد يصفونه أربعة ممن تقبل شهادتهم فيه، سواء أتوا الحاكم جملة، أو متفرقين، وإن حملت امرأة لا زوج لها، ولا سيد لم تحد بمجرد ذلك','6031','2245','0');
INSERT INTO [t10649] VALUES (2254,'باب حد القذف','6041','2237','0');
INSERT INTO [t10649] VALUES (2255,'إذا قذف المكلف محصنا جلد ثمانين جلدة إن كان حرا، وإن كان عبدا أربعين','6041','2254','0');
INSERT INTO [t10649] VALUES (2256,'والمعتق بعضه بحسابه، وقذف غير المحصن يوجب التعزير، وهو حق للمقذوف','6047','2254','0');
INSERT INTO [t10649] VALUES (2257,'والمحصن هنا: الحر المسلم العاقل العفيف الملتزم الذي يجامع مثله، ولا يشترط بلوغه','6049','2254','0');
INSERT INTO [t10649] VALUES (2258,'وصريح القذف: يا زان، يا لوطي ونحوه، وكنايته: يا قحبة، يا فاجرة، يا خبيثة، فضحت زوجك، أو نكست رأسه، أو جعلت له قرونا ونحوه، وإن فسره بغير القذف قبل، وإن قذف أهل بلد، أو جماعة لا يتصور منهم الزنا عادة عزر، ويسقط حد القذف بالعفو، ولا يستوفى بدون الطلب','6051','2254','0');
INSERT INTO [t10649] VALUES (2259,'باب حد المسكر','6056','2237','0');
INSERT INTO [t10649] VALUES (2260,'كل شراب أسكر كثيره فقليله حرام وهو خمر من أي شيء كان، ولا يباح شربه للذة ولا لتداو ولا عطش ولا غيره، إلا لدفع لقمة غص بها ولم يحضره غيره','6059','2259','0');
INSERT INTO [t10649] VALUES (2261,'، وإذا شربه المسلم مختارا عالما أن كثيره يسكر فعليه الحد، ثمانون جلدة مع الحرية، وأربعون مع الرق','6066','2259','0');
INSERT INTO [t10649] VALUES (2262,'باب التعزير','6070','2237','0');
INSERT INTO [t10649] VALUES (2263,'وهو التأديب، وهو واجب','6070','2262','0');
INSERT INTO [t10649] VALUES (2264,'في كل معصية لا حد فيها ولا كفارة','6073','2262','0');
INSERT INTO [t10649] VALUES (2265,'كاستمتاع لا حد فيه، وسرقة لا قطع فيها، وجناية لا قود فيها، وإتيان المرأة المرأة، والقذف بغير الزنا ونحوه، ولا يزاد في التعزير على عشر جلدات','6075','2262','0');
INSERT INTO [t10649] VALUES (2266,'ومن استمنى بيده بغير حاجة عزر','6081','2262','0');
INSERT INTO [t10649] VALUES (2267,'باب القطع في السرقة','6086','2237','0');
INSERT INTO [t10649] VALUES (2268,'إذا أخذ الملتزم نصابا، من حرز مثله، من مال معصوم، لا شبهة له فيه، على وجه الاختفاء قطع','6088','2267','0');
INSERT INTO [t10649] VALUES (2269,'فلا قطع على منتهب، ولا مختلس، ولا غاصب، ولا خائن في وديعة، أو عارية','6090','2267','0');
INSERT INTO [t10649] VALUES (2270,'وغيرها، ويقطع الطرار الذي يبط الجيب أو غيره ويأخذ منه ويشترط أن يكون المسروق مالا محترما،','6093','2267','0');
INSERT INTO [t10649] VALUES (2271,'فلا قطع بسرقة آلة لهو ولا محرم كالخمر، ويشترط أن يكون نصابا وهو ثلاثة دراهم، أو ربع دينار، أو عرض قيمته كأحدهما','6095','2267','0');
INSERT INTO [t10649] VALUES (2272,'وإذا نقصت قيمة المسروق، أو ملكها السارق لم يسقط القطع','6100','2267','0');
INSERT INTO [t10649] VALUES (2273,'وتعتبر قيمتها وقت إخراجها من الحرز، فلو ذبح فيه كبشا، أو شق فيه ثوبا فنقصت قيمته عن نصاب، ثم أخرجه، أو أتلف فيه المال لم يقطع، وأن يخرجه من الحرز، فإن سرقه من غير حرز فلا قطع، وحرز المال ما العادة حفظه فيه','6101','2267','0');
INSERT INTO [t10649] VALUES (2274,'ويختلف باختلاف الأموال، والبلدان، وعدل السلطان، وجوره، وقوته، وضعفه، فحرز الأموال، والجواهر، والقماش في الدور، والدكاكين، والعمران وراء الأبواب، والأغلاق الوثيقة، وحرز البقل وقدور الباقلاء ونحوهما وراء الشرائج إذا كان في السوق حارس','6104','2267','0');
INSERT INTO [t10649] VALUES (2275,'وحرز الحطب والخشب الحظائر، وحرز المواشي الصير، وحرزها في المرعى بالراعي ونظره إليها غالبا','6108','2267','0');
INSERT INTO [t10649] VALUES (2276,'وأن تنتفي الشبهة، فلا قطع بالسرقة من مال أبيه وإن علا، ولا من مال ولده وإن سفل، والأب والأم في هذا سواء، ويقطع الأخ وكل قريب بسرقة مال قريبه، ولا يقطع أحد من الزوجين بسرقته من مال الآخر، ولو كان محرزا عنه','6109','2267','0');
INSERT INTO [t10649] VALUES (2277,'وإذا سرق عبد من مال سيده، أو سيد من مال مكاتبه','6115','2267','0');
INSERT INTO [t10649] VALUES (2278,'أو حر مسلم من بيت المال، أو من غنيمة لم تخمس، أو فقير من غلة وقف على الفقراء، أو شخص من مال فيه شركة له، أو لأحد ممن لا يقطع بالسرقة منه لم يقطع، ولا يقطع إلا بشهادة عدلين','6117','2267','0');
INSERT INTO [t10649] VALUES (2279,'أو إقرار مرتين، ولا ينزع عن إقراره حتى يقطع،','6121','2267','0');
INSERT INTO [t10649] VALUES (2280,'وأن يطالب المسروق منه بماله،','6124','2267','0');
INSERT INTO [t10649] VALUES (2281,'وإذا وجب القطع قطعت يده اليمنى من مفصل الكف وحسمت، ومن سرق شيئا من غير حرز ثمرا كان أو كثرا أو غيرهما أضعفت عليه القيمة ولا قطع','6126','2267','0');
INSERT INTO [t10649] VALUES (2282,'باب حد قطاع الطريق','6131','2237','0');
INSERT INTO [t10649] VALUES (2283,'وهم الذين يعرضون للناس بالسلاح في الصحراء، أو البنيان، فيغصبونهم المال مجاهرة لا سرقة','6131','2282','0');
INSERT INTO [t10649] VALUES (2284,'فمن منهم قتل مكافيا، أو غيره، كالولد، والعبد، والذمي، وأخذ المال، قتل ثم صلب حتى يشتهر، وإن قتل ولم يأخذ المال قتل حتما ولم يصلب، وإن جنوا بما يوجب قودا في الطرف تحتم استيفاؤه،','6133','2282','0');
INSERT INTO [t10649] VALUES (2285,'وإن أخذ كل واحد من المال قدر ما يقطع بأخذه السارق ولم يقتلوا قطع من كل واحد يده اليمنى، ورجله اليسرى في مقام واحد، وحسمتا، ثم خلي، فإن لم يصيبوا نفسا ولا مالا يبلغ نصاب السرقة نفوا، بأن يشردوا فلا يتركون يأوون إلى بلد،','6136','2282','0');
INSERT INTO [t10649] VALUES (2286,'ومن تاب منهم قبل أن يقدر عليه سقط عنه ما كان لله، من نفي، وقطع، وصلب، وتحتم قتل','6142','2282','0');
INSERT INTO [t10649] VALUES (2287,'وأخذ بما للآدميين من نفس وطرف ومال، إلا أن يعفى له عنها، ومن صال على نفسه، أو حرمته، أو ماله آدمي أو بهيمة فله الدفع عن ذلك بأسهل ما يغلب على ظنه دفعه به، فإن لم يندفع إلا بالقتل فله ذلك ولا ضمان عليه','6146','2282','0');
INSERT INTO [t10649] VALUES (2288,'فإن قتل فهو شهيد، ويلزمه الدفع عن نفسه وحرمته دون ماله، ومن دخل منزل رجل متلصصا فحكمه كذلك','6148','2282','0');
INSERT INTO [t10649] VALUES (2289,'باب قتال أهل البغي','6158','2237','0');
INSERT INTO [t10649] VALUES (2290,'إذا خرج قوم لهم شوكة ومنعة على الإمام بتأويل سائغ فهم بغاة','6158','2289','0');
INSERT INTO [t10649] VALUES (2291,'وعليه أن يراسلهم فيسألهم ما ينقمون منه فإن ذكروا مظلمة أزالها، وإن ادعوا شبهة كشفها، فإن فاؤوا، وإلا قاتلهم،','6161','2289','0');
INSERT INTO [t10649] VALUES (2292,'وإن اقتتلت طائفتان لعصبية أو رئاسة فهما ظالمتان، وتضمن كل واحدة ما أتلفت على الأخرى','6167','2289','0');
INSERT INTO [t10649] VALUES (2293,'باب حكم المرتد','6170','2237','0');
INSERT INTO [t10649] VALUES (2294,'وهو الذي يكفر بعد إسلامه،','6170','2293','0');
INSERT INTO [t10649] VALUES (2295,'فمن أشرك بالله، أو جحد ربوبيته، أو وحدانيته','6171','2293','0');
INSERT INTO [t10649] VALUES (2296,'أو صفة من صفاته، أو اتخذ لله صاحبة أو ولدا، أو جحد بعض كتبه','6175','2293','0');
INSERT INTO [t10649] VALUES (2297,'أو رسله','6181','2293','0');
INSERT INTO [t10649] VALUES (2298,'أو سب الله أو رسوله فقد كفر،','6184','2293','0');
INSERT INTO [t10649] VALUES (2299,'ومن جحد تحريم الزنا، أو شيئا من المحرمات الظاهرة المجمع عليها بجهل عرف ذلك، وإن كان مثله لا يجهله كفر','6187','2293','0');
INSERT INTO [t10649] VALUES (2300,'فصل','6200','2293','0');
INSERT INTO [t10649] VALUES (2301,'فصل','6207','2293','0');
INSERT INTO [t10649] VALUES (2302,'فمن ارتد عن الإسلام وهو مكلف مختار رجل أو امرأة دعي إليه ثلاثة أيام','6207','2301','0');
INSERT INTO [t10649] VALUES (2303,'وضيق عليه، فإن لم يسلم قتل بالسيف،','6216','2301','0');
INSERT INTO [t10649] VALUES (2304,'ولا تقبل توبة من سب الله، أو رسوله، ولا من تكررت ردته، بل يقتل بكل حال','6219','2301','0');
INSERT INTO [t10649] VALUES (2305,'وتوبة المرتد وكل كافر إسلامه، بأن يشهد أن لا إله إلا الله، وأن محمدا رسول الله، ومن كان كفره بجحد فرض ونحوه فتوبته مع الشهادتين إقراره بالمجحود به، أو قوله: أنا بريء من كل دين يخالف الإسلام','6225','2301','0');
INSERT INTO [t10649] VALUES (2306,'كتاب الأطعمة','6234','0','0');
INSERT INTO [t10649] VALUES (2307,'*','6234','2306','0');
INSERT INTO [t10649] VALUES (2308,'الأصل فيها الحل،','6234','2307','0');
INSERT INTO [t10649] VALUES (2309,'فيباح كل طاهر لا مضرة فيه','6237','2307','0');
INSERT INTO [t10649] VALUES (2310,'من حب وثمر وغيرهما، ولا يحل نجس، كالميتة، والدم، ولا ما فيه مضرة كالسم ونحوه','6241','2307','0');
INSERT INTO [t10649] VALUES (2311,'وحيوانات البر مباحة إلا الحمر الأهلية','6243','2307','0');
INSERT INTO [t10649] VALUES (2312,'وما له ناب يفترس به غير الضبع','6245','2307','0');
INSERT INTO [t10649] VALUES (2313,'كالأسد، والنمر، والذئب، والفيل، والفهد، والكلب، والخنزير، وابن آوى، وابن عرس، والسنور، والنمس، والقرد، والدب، وما له مخلب من الطير يصيد به،','6247','2307','0');
INSERT INTO [t10649] VALUES (2314,'كالعقاب، والبازي، والصقر، والشاهين، والباشق، والحدأة، والبومة، وما يأكل الجيف كالنسر، والرخم، واللقلق، والعقعق، والغراب الأبقع، والغداف، وهو أسود صغير أغبر، والغراب الأسود الكبير','6249','2307','0');
INSERT INTO [t10649] VALUES (2315,'وما يستخبث كالقنفذ، والنيص، والفأرة، والحية، والحشرات كلها، والوطواط، وما تولد من مأكول وغيره كالبغل','6251','2307','0');
INSERT INTO [t10649] VALUES (2316,'فصل','6257','2307','0');
INSERT INTO [t10649] VALUES (2317,'وما عدا ذلك فحلال، كالخيل','6257','2316','0');
INSERT INTO [t10649] VALUES (2318,'وبهيمة الأنعام، والدجاج، والوحشي من الحمر','6259','2316','0');
INSERT INTO [t10649] VALUES (2319,'والبقر والضب والظباء والنعامة والأرنب وسائر الوحش، ويباح حيوان البحر كله','6261','2316','0');
INSERT INTO [t10649] VALUES (2320,'إلا الضفدع، والتمساح، والحية، ومن اضطر إلى محرم ـ غير السم ـ حل له منه ما يسد رمقه','6263','2316','0');
INSERT INTO [t10649] VALUES (2321,'ومن اضطر إلى نفع مال الغير مع بقاء عينه لدفع برد، أو استسقاء ماء، ونحوه، وجب بذله له مجانا','6264','2316','0');
INSERT INTO [t10649] VALUES (2322,'ومن مر بثمر بستان في شجرة، أو متساقط عنه، ولا حائط عليه، ولا ناظر، فله الأكل منه مجانا من غير حمل','6273','2316','0');
INSERT INTO [t10649] VALUES (2323,'وتجب ضيافة المسلم المجتاز به في القرى يوما وليلة','6277','2316','0');
INSERT INTO [t10649] VALUES (2324,'باب الذكاة','6282','2306','0');
INSERT INTO [t10649] VALUES (2325,'لا يباح شيء من الحيوان المقدور عليه بغير ذكاة، إلا الجراد، والسمك، وكل ما لا يعيش إلا في الماء','6282','2324','0');
INSERT INTO [t10649] VALUES (2326,'ويشترط للذكاة أربعة شروط: أهلية المذكي، بأن يكون عاقلا، مسلما أو كتابيا، ولو مراهقا، أو امرأة، أو أقلف، أو أعمى،','6286','2324','0');
INSERT INTO [t10649] VALUES (2327,'ولا تباح ذكاة سكران، ومجنون، ووثني، ومجوسي، ومرتد','6294','2324','0');
INSERT INTO [t10649] VALUES (2328,'الثاني: الآلة: فتباح الذكاة بكل محدد ولو مغصوبا من حديد وحجر وقصب وغيره، إلا السن والظفر','6296','2324','0');
INSERT INTO [t10649] VALUES (2329,'الثالث: قطع الحلقوم والمريء، فإن أبان الرأس بالذبح لم يحرم المذبوح، وذكاة ما عجز عنه من الصيد، والنعم المتوحشة، والواقعة في بئر ونحوها، بجرحه في أي موضع كان من بدنه، إلا أن يكون رأسه في الماء ونحوه فلا يباح','6301','2324','0');
INSERT INTO [t10649] VALUES (2330,'الرابع: أن يقول عند الذبح: باسم الله، لا يجزيه غيرها، فإن تركها سهوا أبيحت لا عمدا','6306','2324','0');
INSERT INTO [t10649] VALUES (2331,'ويكره أن يذبح بآلة كالة وأن يحدها والحيوان يبصره، وأن يوجهه إلى غير القبلة، وأن يكسر عنقه، أو يسلخه قبل أن يبرد','6320','2324','0');
INSERT INTO [t10649] VALUES (2332,'باب الصيد','6327','2306','0');
INSERT INTO [t10649] VALUES (2333,'لا يحل الصيد المقتول في الاصطياد إلا بأربعة شروط: أحدها: أن يكون الصائد من أهل الذكاة، الثاني: الآلة وهي نوعان: محدد يشترط فيه ما يشترط في آلة الذبح، وأن يجرح، فإن قتله بثقله لم يبح، وما ليس بمحدد كالبندق، والعصا، والشبكة، والفخ لا يحل ما قتل به','6329','2332','0');
INSERT INTO [t10649] VALUES (2334,'الثالث: إرسال الآلة قاصدا، فإن استرسل الكلب أو غيره بنفسه لم يبح، إلا أن يزجره فيزيد في عدوه في طلبه فيحل','6337','2332','0');
INSERT INTO [t10649] VALUES (2335,'الرابع: التسمية عند إرسال السهم أو الجارحة، فإن تركها عمدا أو سهوا لم يبح، ويسن أن يقول معها: الله أكبر، كالذكاة','6340','2332','0');
INSERT INTO [t10649] VALUES (2336,'كتاب الأيمان','6344','0','0');
INSERT INTO [t10649] VALUES (2337,'*','6344','2336','0');
INSERT INTO [t10649] VALUES (2338,'واليمين التي تجب بها الكفارة إذا حنث هي اليمين بالله، أو صفة من صفاته، أو بالقرآن، أو بالمصحف، والحلف بغير الله محرم، ولا تجب به كفارة','6347','2337','0');
INSERT INTO [t10649] VALUES (2339,'ويشترط لوجوب الكفارة ثلاثة شروط','6354','2337','0');
INSERT INTO [t10649] VALUES (2340,'الأول: أن تكون اليمين منعقدة، وهي التي قصد عقدها على مستقبل ممكن','6354','2337','0');
INSERT INTO [t10649] VALUES (2341,'فإن حلف على أمر ماض كاذبا عالما فهي الغموس','6359','2337','0');
INSERT INTO [t10649] VALUES (2342,'ولغو اليمين الذي يجري على لسانه بغير قصد، كقوله: لا والله، وبلى والله، وكذا يمين عقدها يظن صدق نفسه فبان بخلافه، فلا كفارة في الجميع','6360','2337','0');
INSERT INTO [t10649] VALUES (2343,'الثاني: أن يحلف مختارا، فإن حلف مكرها لم تنعقد يمينه','6363','2337','0');
INSERT INTO [t10649] VALUES (2344,'الثالث: الحنث في يمينه، بأن يفعل ما حلف على تركه، أو يترك ما حلف على فعله مختارا ذاكرا، فإن فعله مكرها أو ناسيا فلا كفارة، ومن قال في يمين مكفرة: إن شاء الله لم يحنث','6365','2337','0');
INSERT INTO [t10649] VALUES (2345,'ويسن الحنث في اليمين إذا كان خيرا','6373','2337','0');
INSERT INTO [t10649] VALUES (2346,'ومن حرم حلالا سوى زوجته، من أمة، أو طعام، أو لباس، أو غيره لم يحرم، وتلزمه كفارة يمين إن فعله','6377','2337','0');
INSERT INTO [t10649] VALUES (2347,'فصل','6385','2337','0');
INSERT INTO [t10649] VALUES (2348,'يخير من لزمته كفارة يمين بين إطعام عشرة مساكين، أو كسوتهم، أو عتق رقبة','6385','2347','0');
INSERT INTO [t10649] VALUES (2349,'فمن لم يجد فصيام ثلاثة أيام متتابعة','6395','2347','0');
INSERT INTO [t10649] VALUES (2350,'ومن لزمته أيمان قبل التكفير موجبها واحد فعليه كفارة واحدة','6398','2347','0');
INSERT INTO [t10649] VALUES (2351,'وإن اختلف موجبها، كظهار، ويمين بالله لزماه، ولم يتداخلا','6400','2347','0');
INSERT INTO [t10649] VALUES (2352,'باب جامع الأيمان','6402','2336','0');
INSERT INTO [t10649] VALUES (2353,'يرجع في الأيمان إلى نية الحالف إذا احتملها اللفظ','6402','2352','0');
INSERT INTO [t10649] VALUES (2354,'فإن عدمت النية رجع إلى سبب اليمين وما هيجها','6403','2352','0');
INSERT INTO [t10649] VALUES (2355,'فإن عدم ذلك رجع إلى التعيين، فإذا حلف لا لبست هذا القميص، فجعله سراويل، أو رداء، أو عمامة، ولبسه، أو لا كلمت هذا الصبي فصار شيخا، أو زوجة فلان هذه، أو صديقه فلانا، أو مملوكه سعيدا، فزالت الزوجية والملك والصداقة ثم كلمهم','6405','2352','0');
INSERT INTO [t10649] VALUES (2356,'أو لا أكلت لحم هذا الحمل، فصار كبشا، أو هذا الرطب فصار تمرا، أو دبسا أو خلا، أو هذا اللبن فصار جبنا، أو كشكا، أو نحوه، ثم أكله حنث في الكل، إلا أن ينوي ما دام على تلك الصفة','6407','2352','0');
INSERT INTO [t10649] VALUES (2357,'فصل','6410','2352','0');
INSERT INTO [t10649] VALUES (2358,'فإن عدم ذلك رجع إلى ما يتناوله الاسم، وهو ثلاثة: شرعي، وحقيقي، وعرفي','6410','2357','0');
INSERT INTO [t10649] VALUES (2359,'فالشرعي ما له موضوع في الشرع، وموضوع في اللغة','6411','2357','0');
INSERT INTO [t10649] VALUES (2360,'فالمطلق ينصرف إلى الموضوع الشرعي الصحيح، فإذا حلف لا يبيع أو لا ينكح، فعقد عقدا فاسدا لم يحنث','6412','2357','0');
INSERT INTO [t10649] VALUES (2361,'وإن قيد يمينه بما يمنع الصحة، كأن حلف لا يبيع الخمر أو الحر حنث بصورة العقد،','6413','2357','0');
INSERT INTO [t10649] VALUES (2362,'والحقيقي هو الذي لم يغلب مجازه على حقيقته كاللحم، فإن حلف لا يأكل اللحم فأكل شحما، أو مخا، أو كبدا، أو نحوه لم يحنث، وإن حلف لا يأكل أدما، حنث بأكل البيض، والتمر، والملح، والخل، والزيتون','6415','2357','0');
INSERT INTO [t10649] VALUES (2363,'ونحوه، وكل ما يصطبغ به، ولا يلبس شيئا فلبس ثوبا، أو درعا، أو جوشنا، أو نعلا حنث','6417','2357','0');
INSERT INTO [t10649] VALUES (2364,'وإن حلف لا يكلم إنسانا حنث بكلام كل إنسان، ولا يفعل شيئا فوكل من فعله حنث، إلا أن ينوي مباشرته بنفسه','6419','2357','0');
INSERT INTO [t10649] VALUES (2365,'والعرفي ما اشتهر مجازه فغلب الحقيقة، كالراوية والغائط، ونحوهما، فتتعلق اليمين بالعرف، فإذا حلف على وطء زوجته، أو وطء دار، تعلقت يمينه بجماعها، وبدخول الدار','6421','2357','0');
INSERT INTO [t10649] VALUES (2366,'وإن حلف لا يأكل شيئا فأكله مستهلكا في غيره، كمن حلف لا يأكل سمنا فأكل خبيصا فيه سمن لا يظهر فيه طعمه، أو لا يأكل بيضا فأكل ناطفا لم يحنث، وإن ظهر طعم شيء من المحلوف عليه حنث','6424','2357','0');
INSERT INTO [t10649] VALUES (2367,'فصل','6428','2352','0');
INSERT INTO [t10649] VALUES (2368,'وإن حلف لا يفعل شيئا، ككلام زيد، ودخول دار ونحوه، ففعله مكرها لم يحنث، وإن حلف على نفسه أو غيره ممن يقصد منعه كالزوجة، والولد، ألا يفعل شيئا، ففعله ناسيا، أو جاهلا حنث في الطلاق والعتاق فقط','6428','2367','0');
INSERT INTO [t10649] VALUES (2369,'وعلى من لا يمتنع بيمينه من سلطان وغيره ففعله حنث مطلقا، وإن فعل هو أو غيره ممن قصد منعه بعض ما حلف على كله لم يحنث، ما لم تكن له نية','6432','2367','0');
INSERT INTO [t10649] VALUES (2370,'باب النذر','6436','2336','0');
INSERT INTO [t10649] VALUES (2371,'لا يصح إلا من بالغ عاقل ولو كافرا، والصحيح منه خمسة أقسام المطلق مثل أن يقول: لله علي نذر، ولم يسم شيئا فيلزمه كفارة يمين','6436','2370','0');
INSERT INTO [t10649] VALUES (2372,'الثاني نذر اللجاج والغضب، وهو تعليق نذره بشرط يقصد المنع منه، أو الحمل عليه، أو التصديق أو التكذيب، فيخير بين فعله، وكفارة يمين','6440','2370','0');
INSERT INTO [t10649] VALUES (2373,'الثالث نذر المباح كلبس ثوبه، وركوب دابته فحكمه كالثاني، وإن نذر مكروها من طلاق أو غيره استحب أن يكفر ولا يفعله','6442','2370','0');
INSERT INTO [t10649] VALUES (2374,'الرابع نذر المعصية كشرب خمر، وصوم يوم الحيض، والنحر، فلا يجوز الوفاء به ويكفر','6443','2370','0');
INSERT INTO [t10649] VALUES (2375,'الخامس نذر التبرر مطلقا أو معلقا، كفعل الصلاة والصيام والحج ونحوه، كقوله: إن شفى الله مريضي أو سلم مالي الغائب، فلله علي كذا، فوجد الشرط لزمه الوفاء به، إلا إذا نذر الصدقة بماله كله، أو بمسمى منه يزيد على ثلث الكل فإنه يجزئه قدر الثلث','6447','2370','0');
INSERT INTO [t10649] VALUES (2376,'وفيما عداها يلزمه المسمى، ومن نذر صوم شهر لزمه التتابع، وإن نذر أياما معدودة لم يلزمه إلا بشرط أو نية','6458','2370','0');
INSERT INTO [t10649] VALUES (2377,'كتاب القضاء','6464','0','0');
INSERT INTO [t10649] VALUES (2378,'*','6464','2377','0');
INSERT INTO [t10649] VALUES (2379,'وهو فرض كفاية يلزم الإمام أن ينصب في كل إقليم قاضيا','6465','2378','0');
INSERT INTO [t10649] VALUES (2380,'ويختار أفضل من يجده علما، وورعا، ويأمره بتقوى الله، وأن يتحرى العدل، ويجتهد في إقامته فيقول: وليتك الحكم، أو قلدتك ونحوه، ويكاتبه في البعد','6486','2378','0');
INSERT INTO [t10649] VALUES (2381,'وتفيد ولاية الحكم العامة الفصل بين الخصوم، وأخذ الحق لبعضهم من بعض، والنظر في أموال غير المرشدين، والحجر على من يستوجبه لسفه أو فلس، والنظر في وقوف عمله ليعمل بشرطها، وتنفيذ الوصايا، وتزويج من لا ولي لها، وإقامة الحدود، وإمامة الجمعة والعيد، والنظر ف','6492','2378','0');
INSERT INTO [t10649] VALUES (2382,'ويجوز أن يولى عموم النظر في عموم العمل','6497','2378','0');
INSERT INTO [t10649] VALUES (2383,'وأن يولى خاصا فيهما أو في أحدهما','6498','2378','0');
INSERT INTO [t10649] VALUES (2384,'ويشترط في القاضي عشر صفات، كونه بالغا، عاقلا، ذكرا، حرا، مسلما، عدلا، سميعا، بصيرا، متكلما، مجتهدا، ولو في مذهبه','6500','2378','0');
INSERT INTO [t10649] VALUES (2385,'وإذا حكم اثنان بينهما رجلا يصلح للقضاء نفذ حكمه في المال والحدود واللعان وغيرها','6513','2378','0');
INSERT INTO [t10649] VALUES (2386,'باب آداب القاضي','6518','2377','0');
INSERT INTO [t10649] VALUES (2387,'ينبغي أن يكون قويا من غير عنف، لينا من غير ضعف، حليما ذا أناة وفطنة، وليكن مجلسه في وسط البلد فسيحا، ويعدل بين الخصمين في لحظه، ولفظه، ومجلسه، ودخولهما عليه','6518','2386','0');
INSERT INTO [t10649] VALUES (2388,'ويحرم القضاء وهو غضبان كثيرا، أو حاقن، أو في شدة جوع، أو عطش، أو هم، أو ملل، أو كسل، أو نعاس، أو برد مؤلم، أو حر مزعج','6527','2386','0');
INSERT INTO [t10649] VALUES (2389,'ويستحب أن لا يحكم إلا بحضرة الشهود، ولا ينفذ حكمه لنفسه، ولا لمن لا تقبل شهادته له، ومن ادعى على غير برزة لم تحضر وأمرت بالتوكيل، وإن لزمها يمين أرسل من يحلفها وكذا المريض','6538','2386','0');
INSERT INTO [t10649] VALUES (2390,'باب طريق الحكم وصفته','6541','2377','0');
INSERT INTO [t10649] VALUES (2391,'إذا حضر إليه خصمان قال: أيكما المدعي، فإن سكت حتى يبدأ جاز، فمن سبق بالدعوى قدمه، فإن أقر له حكم له عليه','6541','2390','0');
INSERT INTO [t10649] VALUES (2392,'وإن أنكر قال للمدعي: إن كان لك بينة فأحضرها إن شئت، فإن أحضرها سمعها وحكم بها، ولا يحكم بعلمه،','6543','2390','0');
INSERT INTO [t10649] VALUES (2393,'وإن قال المدعي: ما لي بينة، أعلمه الحاكم أن له اليمين على خصمه، على صفة جوابه، فإن سأل إحلافه أحلفه وخلى سبيله، ولا يعتد بيمينه قبل مسألة المدعي، وإن نكل قضى عليه، فيقول: إن حلفت وإلا قضيت عليك، فإن لم يحلف قضى عليه، وإن حلف المنكر ثم أحضر المدعي بين','6547','2390','0');
INSERT INTO [t10649] VALUES (2394,'فصل','6555','2390','0');
INSERT INTO [t10649] VALUES (2395,'ولا تصح الدعوى إلا محررة، معلومة المدعى به إلا ما نصححه مجهولا كالوصية وبعبد من عبيده مهرا ونحوه','6555','2394','0');
INSERT INTO [t10649] VALUES (2396,'وإن ادعى عقد نكاح أو بيع أو غيرهما فلا بد من ذكر شروطه، وإن ادعت امرأة نكاح رجل لطلب نفقة، أو مهر، أو نحوهما سمعت دعواها، وإن لم تدع سوى النكاح لم تقبل،','6559','2394','0');
INSERT INTO [t10649] VALUES (2397,'وإن ادعى الإرث ذكر سببه، وتعتبر عدالة البينة ظاهرا وباطنا','6563','2394','0');
INSERT INTO [t10649] VALUES (2398,'ومن جهلت عدالته سئل عنه، وإن علم عدالته عمل بها، وإن جرح الخصم الشهود كلف البينة به وأنظر له ثلاثا إن طلبه، وللمدعي ملازمته، فإن لم يأت ببينة حكم عليه، وإن جهل حال البينة طلب من المدعي تزكيتهم','6568','2394','0');
INSERT INTO [t10649] VALUES (2399,'ويكفي فيها عدلان يشهدان بعدالته، ولا يقبل في الترجمة والتزكية والجرح والتعريف والرسالة إلا قول عدلين، ويحكم على الغائب إذا ثبت عليه الحق، وإن ادعى على حاضر بالبلد غائب عن مجلس الحكم، وأتى ببينة لم تسمع الدعوى، ولا البينة','6570','2394','0');
INSERT INTO [t10649] VALUES (2400,'باب كتاب القاضي إلى القاضي','6587','2377','0');
INSERT INTO [t10649] VALUES (2401,'يقبل كتاب القاضي إلى القاضي في كل حق حتى القذف، لا في حدود الله، كحد الزنا ونحوه،','6588','2400','0');
INSERT INTO [t10649] VALUES (2402,'ويقبل فيما حكم به لينفذه، وإن كان في بلد واحد، ولا يقبل فيما ثبت عنده ليحكم به، إلا أن يكون بينهما مسافة قصر','6591','2400','0');
INSERT INTO [t10649] VALUES (2403,'ويجوز أن يكتب إلى قاض معين، وإلى كل من يصل إليه كتابه من قضاة المسلمين، ولا يقبل إلا أن يشهد به القاضي الكاتب شاهدين، فيقرؤه عليهما، ثم يقول: اشهدا أن هذا كتابي إلى فلان ابن فلان ثم يدفعه إليهما','6593','2400','0');
INSERT INTO [t10649] VALUES (2404,'باب القسمة','6597','2377','0');
INSERT INTO [t10649] VALUES (2405,'لا تجوز قسمة الأملاك التي لا تنقسم إلا بضرر، أو رد عوض، إلا برضا الشركاء، كالدور الصغار، والحمام، والطاحون الصغيرين','6597','2404','0');
INSERT INTO [t10649] VALUES (2406,'والأرض التي لا تتعدل بأجزاء، ولا قيمة، كبناء، أو بئر في بعضها، فهذه القسمة في حكم البيع، ولا يجبر من امتنع من قسمتها، وأما ما لا ضرر، ولا رد عوض في قسمته، كالقرية، والبستان، والدار الكبيرة، والأرض، والدكاكين الواسعة، والمكيل، والموزون من جنس واحد، كا','6601','2404','0');
INSERT INTO [t10649] VALUES (2407,'وهذه القسمة إفراز لا بيع، ويجوز للشركاء أن يتقاسموا بأنفسهم، وبقاسم ينصبونه، أو يسألوا الحاكم نصبه، وأجرته على قدر الأملاك، فإذا اقتسموا، أو اقترعوا لزمت القسمة، وكيف اقترعوا جاز','6604','2404','0');
INSERT INTO [t10649] VALUES (2408,'باب الدعاوى والبينات','6611','2377','0');
INSERT INTO [t10649] VALUES (2409,'المدعي من إذا سكت ترك، والمدعى عليه من إذا سكت لم يترك،','6611','2408','0');
INSERT INTO [t10649] VALUES (2410,'ولا تصح الدعوى والإنكار إلا من جائز التصرف','6611','2408','0');
INSERT INTO [t10649] VALUES (2411,'وإذا تداعيا عينا بيد أحدهما فهي له مع يمينه، إلا أن تكون له بينة فلا يحلف، وإن أقام كل واحد بينة أنها له، قضي للخارج ببينته، ولغت بينة الداخل','6615','2408','0');
INSERT INTO [t10649] VALUES (2412,'كتاب الشهادات','6618','0','0');
INSERT INTO [t10649] VALUES (2413,'*','6618','2412','0');
INSERT INTO [t10649] VALUES (2414,'تحمل الشهادة في غير حق الله فرض كفاية، وإن لم يوجد إلا من يكفي تعين عليه، وأداؤها فرض عين على من تحملها، متى دعي إليه،','6620','2413','0');
INSERT INTO [t10649] VALUES (2415,'وقدر بلا ضرر في بدنه، أو عرضه، أو ماله، أو أهله، وكذا في التحمل، ولا يحل كتمانها','6625','2413','0');
INSERT INTO [t10649] VALUES (2416,'ولا أن يشهد إلا بما يعلمه برؤية، أو سماع، أو باستفاضة فيما يتعذر علمه بدونها، كنسب، وموت، وملك مطلق، ونكاح، ووقف، ونحوها، ومن شهد بنكاح أو غيره من العقود فلا بد من ذكر شروطه،','6628','2413','0');
INSERT INTO [t10649] VALUES (2417,'وإن شهد برضاع، أو سرقة، أو شرب، أو قذف فإنه يصفه، ويصف الزنا بذكر الزمان، والمكان، والمزني بها','6636','2413','0');
INSERT INTO [t10649] VALUES (2418,'ويذكر ما يعتبر للحكم، ويختلف به في الكل','6641','2413','0');
INSERT INTO [t10649] VALUES (2419,'فصل','6642','2413','0');
INSERT INTO [t10649] VALUES (2420,'وشروط من تقبل شهادته ستة: البلوغ، فلا تقبل شهادة الصبيان','6642','2419','0');
INSERT INTO [t10649] VALUES (2421,'الثاني: العقل، فلا تقبل شهادة مجنون، ولا معتوه، وتقبل ممن يخنق أحيانا في حال إفاقته','6644','2419','0');
INSERT INTO [t10649] VALUES (2422,'الثالث: الكلام، فلا تقبل شهادة الأخرس، ولو فهمت إشارته، إلا إذا أداها بخطه','6645','2419','0');
INSERT INTO [t10649] VALUES (2423,'الرابع: الإسلام، الخامس: الحفظ، السادس: العدالة: ويعتبر لها شيئان: الصلاح في الدين: وهو أداء الفرائض بسننها الراتبة، واجتناب المحارم بأن لا يأتي كبيرة، ولا يدمن على صغيرة، فلا تقبل شهادة فاسق','6648','2419','0');
INSERT INTO [t10649] VALUES (2424,'الثاني: استعمال المروءة، وهو فعل ما يجمله، ويزينه، واجتناب ما يدنسه ويشينه','6653','2419','0');
INSERT INTO [t10649] VALUES (2425,'ومتى زالت الموانع، فبلغ الصبي، وعقل المجنون، وأسلم الكافر، وتاب الفاسق قبلت شهادتهم','6658','2419','0');
INSERT INTO [t10649] VALUES (2426,'باب موانع الشهادة وعدد الشهود','6664','2412','0');
INSERT INTO [t10649] VALUES (2427,'لا تقبل شهادة عمودي النسب بعضهم لبعض، ولا شهادة أحد الزوجين لصاحبه، وتقبل عليهم، ولا من يجر إلى نفسه نفعا، أو يدفع عنها ضررا، ولا عدو على عدوه','6664','2426','0');
INSERT INTO [t10649] VALUES (2428,'كمن شهد على من قذفه، أو قطع الطريق عليه، ومن سره مساءة شخص، أو غمه فرحه فهو عدوه','6672','2426','0');
INSERT INTO [t10649] VALUES (2429,'فصل','6675','2426','0');
INSERT INTO [t10649] VALUES (2430,'ولا يقبل في الزنا والإقرار به إلا أربعة، ويكفي على من أتى بهيمة رجلان، ويقبل في بقية الحدود، والقصاص، وما ليس بعقوبة ولا مال، ولا يقصد به المال، ويطلع عليه الرجال غالبا، كنكاح، وطلاق، ورجعة، وخلع، ونسب، وولاء، وإيصاء إليه يقبل فيه رجلان','6675','2429','0');
INSERT INTO [t10649] VALUES (2431,'ويقبل في المال وما يقصد به كالبيع، والأجل، والخيار فيه، ونحوه رجلان، أو رجل وامرأتان، أو رجل ويمين المدعي','6679','2429','0');
INSERT INTO [t10649] VALUES (2432,'وما لا يطلع عليه الرجال، كعيوب النساء تحت الثياب، والبكارة والثيوبة، والحيض، والولادة، والرضاع، والاستهلال ونحوه تقبل فيه شهادة امرأة عدل والرجل فيه كالمرأة، ومن أتى برجل وامرأتين، أو شاهد ويمين فيما يوجب القود لم يثبت به قود ولا مال، وإن أتى بذلك ف','6685','2429','0');
INSERT INTO [t10649] VALUES (2433,'فصل','6691','2426','0');
INSERT INTO [t10649] VALUES (2434,'ولا تقبل الشهادة على الشهادة، إلا في حق يقبل فيه كتاب القاضي إلى القاضي، ولا يحكم بها إلا أن تتعذر شهادة الأصل بموت، أو مرض، أو غيبة مسافة قصر، ولا يجوز لشاهد الفرع أن يشهد إلا أن يسترعيه شاهد الأصل،','6692','2433','0');
INSERT INTO [t10649] VALUES (2435,'فيقول: اشهد على شهادتي بكذا، أو يسمعه يقر بها عند الحاكم، أو يعزوها إلى سبب، من قرض، أو بيع، أو نحوه، وإذا رجع شهود المال بعد الحكم لم ينقض، ويلزمهم الضمان دون من زكاهم، وإن حكم بشاهد ويمين، ثم رجع الشاهد غرم المال كله','6695','2433','0');
INSERT INTO [t10649] VALUES (2436,'باب اليمين في الدعاوى','6702','2412','0');
INSERT INTO [t10649] VALUES (2437,'لا يستحلف في العبادات، ولا في حدود الله، ويستحلف المنكر في كل حق لآدمي، إلا النكاح، والطلاق، والرجعة، والإيلاء، وأصل الرق، والولاء، والاستيلاد، والنسب، والقود، والقذف','6702','2436','0');
INSERT INTO [t10649] VALUES (2438,'واليمين المشروعة اليمين بالله ولا تغلظ إلا فيما له خطر','6708','2436','0');
INSERT INTO [t10649] VALUES (2439,'كتاب الإقرار','6712','0','0');
INSERT INTO [t10649] VALUES (2440,'يصح من مكلف، مختار، غير محجور عليه، ولا يصح من مكره، وإن أكره على وزن مال فباع ملكه لذلك صح، ومن أقر في مرضه بشيء فكإقراره في صحته، إلا في إقراره بالمال لوارث فلا يقبل، وإن أقر لامرأته بالصداق فلها مهر المثل بالزوجية لا بإقراره،','6712','2439','0');
INSERT INTO [t10649] VALUES (2441,'ولو أقر أنه كان أبانها في صحته لم يسقط إرثها وإن أقر لوارث فصار عند الموت أجنبيا لم يلزم إقراره لا أنه باطل، وإن أقر لغير وارث أو أعطاه صح، وإن صار عند الموت وارثا،','6721','2439','0');
INSERT INTO [t10649] VALUES (2442,'وإن أقرت امرأة على نفسها بنكاح ولم يدعه اثنان قبل، وإن أقر وليها المجبر بالنكاح، أو الذي أذنت له صح، وإن أقر بنسب صغير أو مجنون مجهول النسب أنه ابنه ثبت نسبه، فإن كان ميتا ورثه، وإذا ادعى على شخص بشيء فصدقه صح','6725','2439','0');
INSERT INTO [t10649] VALUES (2443,'فصل','6733','2439','0');
INSERT INTO [t10649] VALUES (2444,'إذا وصل بإقراره ما يسقطه، مثل أن يقول: له علي ألف لا تلزمني ونحوه لزمه الألف، وإن قال: كان له علي وقضيته فقوله بيمينه ما لم تكن بينة، أو يعترف بسبب الحق، وإن قال: له علي مائة ثم سكت سكوتا يمكنه الكلام فيه، ثم قال زيوفا أو مؤجلة لزمه مائة جيدة حالة','6733','2443','0');
INSERT INTO [t10649] VALUES (2445,'وإن أقر بدين مؤجل، فأنكر المقر له الأجل فقول المقر مع يمينه، وإن أقر أنه وهب أو رهن وأقبض، أو أقر بقبض ثمن أو غيره، ثم أنكر القبض، ولم يجحد الإقرار، وسأل إحلاف خصمه فله ذلك','6737','2443','0');
INSERT INTO [t10649] VALUES (2446,'وإن باع شيئا، أو وهبه أو أعتقه، ثم أقر أن ذلك كان لغيره لم يقبل قوله ولم ينفسخ البيع، ولا غيره، ولزمته غرامته','6743','2443','0');
INSERT INTO [t10649] VALUES (2447,'وإن قال: لم يكن ملكي ثم ملكته بعد وأقام بينة قبلت، إلا أن يكون قد أقر أنه ملكه، أو أنه قبض ثمن ملكه لم يقبل','6745','2443','0');
INSERT INTO [t10649] VALUES (2448,'فصل','6747','2439','0');
INSERT INTO [t10649] VALUES (2449,'إذا قال: له علي شيء، أو كذا، قيل له: فسره، فإن أبى حبس حتى يفسره، فإن فسره بحق شفعة أو بأقل مال قبل، وإن فسره بميتة أو خمر أو قشر جوزة لم يقبل','6747','2448','0');
INSERT INTO [t10649] VALUES (2450,'ويقبل بكلب مباح نفعه، أو حد قذف، وإن قال: له علي ألف رجع في تفسير جنسه إليه، فإن فسره بجنس أو أجناس قبل منه، وإن قال: له علي ما بين درهم وعشرة لزمه ثمانية، وإن قال: ما بين درهم إلى عشرة، أو من درهم إلى عشرة لزمه تسعة','6751','2448','0');
INSERT INTO [t10649] VALUES (2451,'وإن قال: له علي درهم أو دينار لزمه أحدهما وإن قال: له علي تمر في جراب، أو سكين في قراب، أو فص في خاتم، ونحوه فهو مقر بالأول، والله سبحانه وتعالى أعلم','6753','2448','0');
