INSERT INTO [t10668] VALUES (105,'الباب الخامس والثمانون النصيحة، والموعظة، والزجر عن القبيح والشفقة، والرحمة، وما يجري مجراها','2094','95','0');
INSERT INTO [t10668] VALUES (106,'الباب السادس والثمانون النعمة وشكرها، والإشادة بذكرها، وغمطها وكفرانها، والامتنان بها، وما شابه ذلك','2101','95','0');
INSERT INTO [t10668] VALUES (107,'الباب السابع والثمانون النوم، والاحتلام، والسهر، والرؤيا وما جاء من عجائب التأويلات، وما يتعلق بذلك','2114','95','0');
INSERT INTO [t10668] VALUES (108,'الباب الثامن والثمانون الوفاء، وحسن العهد، ورعاية الذمم، والأمانة والثقة، وكتمان الأسرار، وما أشبه ذلك','2124','95','0');
INSERT INTO [t10668] VALUES (109,'الباب التاسع والثمانون الوقاحة، والسفاهة، والجسارة، وقلة المبالاة وذكر الغوغاء والحشوة، ونحو ذلك','2134','95','0');
INSERT INTO [t10668] VALUES (110,'الباب التسعون الهدية، والرشوة، وما جاء في الاهداء والاستهداء، وذكر من ارتشى في الحكم وغيره','2139','95','0');
INSERT INTO [t10668] VALUES (111,'الباب الحادي والتسعون اليأس، والقناعة، والرضا بما رزق الله، والتوكل على الله، والتفويض إليه، والنزاهة عن المطمع','2151','95','0');
INSERT INTO [t10668] VALUES (112,'الباب الثاني والتسعون الخيل، والبغال، والحمير، وذكر الفروسية، وما اتصل بذلك','2172','95','0');
INSERT INTO [t10668] VALUES (113,'الباب الثالث والتسعون الابل، والبقر، والغنم، وما يتصل بها وينسب إليها','2189','95','0');
INSERT INTO [t10668] VALUES (114,'الباب الرابع والتسعون الوحوش من السباع وغيرها، وذكر أحوالها، وما يصطاد منها ويتألف، وما أشبه ذلك','2195','95','0');
INSERT INTO [t10668] VALUES (115,'الباب الخامس والتسعون دواب البحر من السمك وسائر الحيوان المختلف فيه وما وضع الله فيها العجائب','2219','95','0');
INSERT INTO [t10668] VALUES (116,'الباب السادس والتسعون الطيور وما أوتيت من أعاجيب الإلهام في حضنها ورزقها ورفرفتها على فراخها وتدبير أمرها','2225','95','0');
INSERT INTO [t10668] VALUES (117,'الباب السابع والتسعون البعوض، والهمج، والذبان، والفراش والزنابير، والجراد، والجنادب، وما أشبه ذلك','2242','95','0');
INSERT INTO [t10668] VALUES (118,'الباب الثامن والتسعون الحشرات والهوام ونحوها من دواب الأرض، وما اتصل بها وذكر منها','2248','95','0');
