INSERT INTO [t10777] VALUES (1,'مقدمة: الرئاسة العامة لإدارات البحوث العلمية والإفتاء والدعوة والإرشاد.','2','0','0');
INSERT INTO [t10777] VALUES (2,'مقدمة: أحمد محمد شاكر','5','0','0');
INSERT INTO [t10777] VALUES (3,'ترجمة الإمام الطحاوي صاحب العقيدة:','9','0','0');
INSERT INTO [t10777] VALUES (4,'مقدمة النشر في الطبعة الأولى- بالمطبعة السلفية، بمكة المكرمة:','12','0','0');
INSERT INTO [t10777] VALUES (5,'مقدمة الشارح','14','0','0');
INSERT INTO [t10777] VALUES (6,'قوله: (نقول في توحيد الله معتقدين بتوفيق الله: إن الله واحد لا شريك له).','23','0','0');
INSERT INTO [t10777] VALUES (7,'قوله: (ولا شيء مثله).','49','0','0');
INSERT INTO [t10777] VALUES (8,'قوله: (ولا شيء يعجزه).','58','0','0');
INSERT INTO [t10777] VALUES (9,'قوله: (ولا إله غيره).','61','0','0');
INSERT INTO [t10777] VALUES (10,'قوله: (لا يفنى ولا يبيد).','65','0','0');
INSERT INTO [t10777] VALUES (11,'قوله: (ولا يكون إلا ما يريد).','65','0','0');
INSERT INTO [t10777] VALUES (12,'قوله: (لا تبلغه الأوهام، ولا تدركه الأفهام).','70','0','0');
INSERT INTO [t10777] VALUES (13,'قوله: (ولا يشبه الأنام).','70','0','0');
INSERT INTO [t10777] VALUES (14,'قوله: (حي لا يموت قيوم لا ينام).','73','0','0');
INSERT INTO [t10777] VALUES (15,'قوله: (خالق بلا حاجة، رازق بلا مؤنة).','75','0','0');
INSERT INTO [t10777] VALUES (16,'قوله: (مميت بلا مخافة، باعث بلا مشقة).','76','0','0');
INSERT INTO [t10777] VALUES (17,'قوله: (ما زال بصفاته قديما قبل خلقه، لم يزدد بكونهم شيئا لم يكن قبلهم من صفته، كما كان بصفاته أزليا، كذلك لا يزال عليها أبديا).','76','0','0');
INSERT INTO [t10777] VALUES (18,'قوله: (ليس بعد خلق الخلق استفاد اسم"الخالق"، ولا بإحداثه البرية استفاد اسم"الباري").','84','0','0');
INSERT INTO [t10777] VALUES (19,'قوله: (له معنى الربوبية ولا مربوب، ومعنى الخالق ولا مخلوق).','89','0','0');
INSERT INTO [t10777] VALUES (20,'قوله: (وكما أنه محيي الموتى بعدما أحيا، استحق هذا الاسم قبل إحيائهم، كذلك استحق اسم الخالق قبل إنشائهم).','89','0','0');
INSERT INTO [t10777] VALUES (21,'قوله: (ذلك بأنه على كل شيء قدير، وكل شىء إليه فقير، وكل أمر إليه يسير، لا يحتاج إلى شيء، ليس كمثله شيء، وهو السميع البصير).','90','0','0');
INSERT INTO [t10777] VALUES (22,'قوله: (خلق الخلق بعلمه)','95','0','0');
INSERT INTO [t10777] VALUES (23,'قوله: (وقدر لهم أقدارا).','96','0','0');
INSERT INTO [t10777] VALUES (24,'قوله: (وضرب لهم آجالا)','97','0','0');
INSERT INTO [t10777] VALUES (25,'قوله: (ولم يخف عليه شيء قبل أن يخلقهم، وعلم ما هم عاملون قبل أن يخلقهم).','99','0','0');
INSERT INTO [t10777] VALUES (26,'قوله: (وأمرهم بطاعته، ونهاهم عن معصيته).','100','0','0');
INSERT INTO [t10777] VALUES (27,'قوله: (وكل شيء يجري بتقديره ومشيئته، ومشيئته تنفذ، لا مشيئة للعباد، إلا ما شاء لهم، فما شاء لهم كان، وما لم يشأ لم يكن)','100','0','0');
INSERT INTO [t10777] VALUES (28,'قوله: (يهدي من يشاء، ويعصم ويعافي فضلا. ويضل من يشاء، ويخذل ويبتلي عدلا).','103','0','0');
INSERT INTO [t10777] VALUES (29,'قوله: (وكلهم يتقلبون في مشيئته، بين فضله وعدله).','104','0','0');
INSERT INTO [t10777] VALUES (30,'قوله: (وهو متعال عن الأضداد والأنداد).','104','0','0');
INSERT INTO [t10777] VALUES (31,'قوله: (لا راد لقضائه، ولا معقب لحكمه، ولا غالب لأمره).','104','0','0');
INSERT INTO [t10777] VALUES (32,'قوله: (آمنا بذلك كله، وأيقنا أن كلا من عنده)','104','0','0');
INSERT INTO [t10777] VALUES (33,'قوله: (وإن محمدا عبده المصطفى، ونبيه المجتبى، ورسوله المرتضى).','105','0','0');
INSERT INTO [t10777] VALUES (34,'قوله: (وأنه خاتم الأنبياء)','115','0','0');
INSERT INTO [t10777] VALUES (35,'قوله: (وإمام الأتقياء)','116','0','0');
INSERT INTO [t10777] VALUES (36,'قوله: (وسيد المرسلين)','116','0','0');
INSERT INTO [t10777] VALUES (37,'قوله: (وحبيب رب العالمين).','120','0','0');
INSERT INTO [t10777] VALUES (38,'قوله: (وكل دعوى النبوة بعده فغي وهوى)','121','0','0');
INSERT INTO [t10777] VALUES (39,'قوله: (وهو المبعوث إلى عامة الجن وكافة الورى، بالحق والهدى، وبالنور والضياء).','122','0','0');
INSERT INTO [t10777] VALUES (40,'قوله: (وإن القرآن كلام الله، منه بدا بلا كيفية قولا، وأنزله على رسوله وحيا، وصدقه المؤمنون على ذلك حقا، وأيقنوا أنه كلام الله تعالى بالحقيقة','124','0','0');
INSERT INTO [t10777] VALUES (41,'قوله"بلا كيفية"','141','0','0');
INSERT INTO [t10777] VALUES (42,'قوله: "وصدقه المؤمنون على ذلك حقا"','143','0','0');
INSERT INTO [t10777] VALUES (43,'قوله: وأيقنوا أنه كلام الله تعالى بالحقيقة ليس بمخلوق ككلام البرية.','143','0','0');
INSERT INTO [t10777] VALUES (44,'قوله: (ومن سمعه، وقال: إنه كلام البشر، فقد كفر)','148','0','0');
INSERT INTO [t10777] VALUES (45,'قوله: (ولا يشبه قول البشر)','148','0','0');
INSERT INTO [t10777] VALUES (46,'قوله: (ومن وصف الله بمعنى من معاني البشر، فقد كفر. من أبصر هذا اعتبر. وعن مثل قول الكفار انزجر. وعلم أن الله بصفاته ليس كالبشر).','149','0','0');
INSERT INTO [t10777] VALUES (47,'قوله: (والرؤية حق لأهل الجنة،','150','0','0');
INSERT INTO [t10777] VALUES (48,'قوله: "بغير إحاطة ولا كيفية"','161','0','0');
INSERT INTO [t10777] VALUES (49,'قوله: "فإنه ما سلم في دينه إلا من سلم لله عز وجل ولرسوله صلى الله عليه وسلم، ورد علم ما اشتبه عليه إلى عالمه".','162','0','0');
INSERT INTO [t10777] VALUES (50,'قوله: (ولا تثبت قدم الإسلام إلا على ظهر التسليم والاستسلام).','165','0','0');
INSERT INTO [t10777] VALUES (51,'قوله: (فمن رام علم ما حظر عنه علمه، ولم يقنع بالتسليم فهمه، حجبه مرامه عن خالص التوحيد، وصافي المعرفة، وصحيح الإيمان).','167','0','0');
INSERT INTO [t10777] VALUES (52,'قوله: (فيتذبذب بين الكفر والإيمان، والتصديق والتكذيب، والإقرار والإنكار، موسوسا تائها، شاكا، لا مؤمنا مصدقا، ولا جاحدا مكذبا).','174','0','0');
INSERT INTO [t10777] VALUES (53,'قوله: (ولا يصح الإيمان بالرؤية لأهل دار السلام لمن اعتبرها منهم بوهم،','177','0','0');
INSERT INTO [t10777] VALUES (54,'قوله: "أو تأولها بفهم"','179','0','0');
INSERT INTO [t10777] VALUES (55,'قوله: (ومن لم يتوق النفي والتشبيه، زل ولم يصب التنزيه).','184','0','0');
INSERT INTO [t10777] VALUES (56,'قوله: (فإن ربنا جل وعلا موصوف بصفات الوحدانية، منعوت بنعوت الفردانية، ليس في معناه أحد من البرية).','185','0','0');
INSERT INTO [t10777] VALUES (57,'وله: (وتعالى عن الحدود والغايات، والأركان والأعضاء والأدوات، لا تحويه الجهات الست كسائر المبتدعات).','186','0','0');
INSERT INTO [t10777] VALUES (58,'قوله: (والمعراج حق، وقد أسري بالنبي صلى الله عليه وسلم وعرج بشخصه في اليقظة، إلى السماء. ثم إلى حيث شاء الله من العلا وأكرمه الله بما شاء، وأوحى إليه','192','0','0');
INSERT INTO [t10777] VALUES (59,'قوله: (والحوض - الذي أكرمه الله تعالى به غياثا لأمته - حق).','196','0','0');
INSERT INTO [t10777] VALUES (60,'قوله: (والشفاعة التي ادخرها لهم حق، كما روي في الأخبار).','199','0','0');
INSERT INTO [t10777] VALUES (61,'قوله: (والميثاق الذي أخذه الله تعالى من آدم وذريته حق).','211','0','0');
INSERT INTO [t10777] VALUES (62,'قوله: (وقد علم الله تعالى فيما لم يزل عدد من يدخل الجنة، وعدد من يدخل النار، جملة واحدة، فلا يزاد في ذلك العدد ولا ينقص منه. وكذلك أفعالهم فيما علم منهم أن يفعلوه).','220','0','0');
INSERT INTO [t10777] VALUES (63,'قوله: (وكل ميسر لما خلق له، والأعمال بالخواتيم، والسعيد من سعد بقضاء الله، والشقي من شقي بقضاء الله).','221','0','0');
INSERT INTO [t10777] VALUES (64,'قوله: (وأصل القدر سر الله تعالى في خلقه، لم يطلع على ذلك ملك مقرب، ولا نبي مرسل،','222','0','0');
INSERT INTO [t10777] VALUES (65,'قوله: "والتعمق والنظر في ذلك ذريعة الخذلان"','233','0','0');
INSERT INTO [t10777] VALUES (66,'قوله: "فالحذر كل الحذر من ذلك نظرا وفكرا ووسوسة"-','233','0','0');
INSERT INTO [t10777] VALUES (67,'قوله: "فمن سأل: لم فعل؟ فقد رد حكم الكتاب، ومن رد حكم الكتاب كان من الكافرين".','235','0','0');
INSERT INTO [t10777] VALUES (68,'قوله: (فهذا جملة ما يحتاج إليه من هو منور قلبه من أولياء الله تعالى','237','0','0');
INSERT INTO [t10777] VALUES (69,'قوله"وهي درجة الراسخين في العلم"','237','0','0');
INSERT INTO [t10777] VALUES (70,'قوله: (ونؤمن باللوح والقلم، وبجميع ما فيه قد رقم).','238','0','0');
INSERT INTO [t10777] VALUES (71,'قوله: (فلو اجتمع الخلق كلهم على شيء كتبه الله تعالى أنه كائن، ليجعلوه غير كائن - لم يقدروا عليه. ولو اجتمعوا كلهم على شيء لم يكتبه الله تعالى فيه، ليجعلوه كائنا - لم يقدروا عليه. جف القلم بما هو كائن إلى يوم القيامة).','239','0','0');
INSERT INTO [t10777] VALUES (72,'قوله: (وما أخطأ العبد لم يكن ليصيبه، وما أصابه لم يكن ليخطئه).','243','0','0');
INSERT INTO [t10777] VALUES (73,'قوله: (وعلى العبد أن يعلم أن الله قد سبق علمه في كل كائن من خلقه، فقدر ذلك تقديرا محكما مبرما، ليس فيه ناقض، ولا معقب ولا مزيل ولا مغير، ولا ناقص ولا زائد من خلقه في سماواته وأرضه).','243','0','0');
INSERT INTO [t10777] VALUES (74,'قوله: (وذلك من عقد الإيمان وأصول المعرفة والاعتراف بتوحيد الله تعالى وربوبيته، كما قال تعالى في كتابه: {وخلق كل شيء فقدره تقديرا}. وقال تعالى: {وكان أمر الله قدرا مقدورا}','245','0','0');
INSERT INTO [t10777] VALUES (75,'قوله: (فويل لمن صار قلبه في القدر قلبا سقيما، لقد التمس بوهمه في فحص الغيب سرا كتيما، وعاد بما قال فيه أفاكا أثيما).','248','0','0');
INSERT INTO [t10777] VALUES (76,'وقوله: (والعرش والكرسي حق).','251','0','0');
INSERT INTO [t10777] VALUES (77,'قوله: (ونقول: إن الله اتخذ إبراهيم خليلا، وكلم الله موسى تكليما، إيمانا وتصديقا وتسليما).','270','0','0');
INSERT INTO [t10777] VALUES (78,'قوله: (ونؤمن بالملائكة والنبيين، والكتب المنزلة على المرسلين، ونشهد أنهم كانوا على الحق المبين).','273','0','0');
INSERT INTO [t10777] VALUES (79,'قوله: (ونسمي أهل قبلتنا مسلمين مؤمنين، ما داموا بما جاء به النبي صلى الله عليه وسلم معترفين، وله بكل ما قاله وأخبر مصدقين).','289','0','0');
INSERT INTO [t10777] VALUES (80,'قوله: (ولا نخوض في الله، ولا نماري في دين الله).','289','0','0');
INSERT INTO [t10777] VALUES (81,'قوله: (ولا نجادل في القرآن، ونشهد أنه كلام رب العالمين،','290','0','0');
INSERT INTO [t10777] VALUES (82,'قوله: (نزل به الروح الأمين)،','292','0','0');
INSERT INTO [t10777] VALUES (83,'قوله: (ولا نكفر أحدا من أهل القبلة بذنب، ما لم يستحله، ولا نقول لا يضر مع الإيمان ذنب لمن عمله).','293','0','0');
INSERT INTO [t10777] VALUES (84,'قوله: (ونرجو للمحسنين من المؤمنين أن يعفو عنهم ويدخلهم الجنة برحمته، ولا نأمن عليهم، ولا نشهد لهم بالجنة، ونستغفر لمسيئهم، ونخاف عليهم، ولا نقنطهم).','303','0','0');
INSERT INTO [t10777] VALUES (85,'قوله: (والأمن [والإياس ينقلان] عن ملة الإسلام، وسبيل الحق بينهما لأهل القبلة).','309','0','0');
INSERT INTO [t10777] VALUES (86,'قوله: (ولا يخرج العبد من الإيمان إلا بجحود ما أدخله فيه).','310','0','0');
INSERT INTO [t10777] VALUES (87,'قوله: (والإيمان: هو الإقرار باللسان، والتصديق بالجنان. وجميع ما صح عن رسول الله صلى الله عليه وسلم من الشرع والبيان كله حق. والإيمان واحد، وأهله في أصله سواء، والتفاضل بينهم بالخشية والتقى، ومخالفة الهوى، وملازمة الأولى).','311','0','0');
INSERT INTO [t10777] VALUES (88,'قوله: "وجميع ما صح عن رسول الله صلى الله عليه وسلم من الشرع والبيان كله حق".','337','0','0');
INSERT INTO [t10777] VALUES (89,'قوله: (والمؤمنون كلهم أولياء الرحمن).','342','0','0');
INSERT INTO [t10777] VALUES (90,'قوله: (وأكرمهم عند الله أطوعهم وأتبعهم للقرآن).','346','0','0');
INSERT INTO [t10777] VALUES (91,'قوله: (والإيمان: هو الإيمان بالله، وملائكته، وكتبه، ورسله، واليوم الآخر، والقدر، خيره وشره، وحلوه ومره، من الله تعالى).','347','0','0');
INSERT INTO [t10777] VALUES (92,'قوله: (ونحن مؤمنون بذلك كله، لا نفرق بين أحد من رسله، ونصدقهم كلهم على ما جاءوا به).','356','0','0');
INSERT INTO [t10777] VALUES (93,'قوله: (وأهل الكبائر من أمة محمد صلى الله عليه وسلم في النار لا يخلدون، إذا ماتوا وهم موحدون','356','0','0');
INSERT INTO [t10777] VALUES (94,'قوله"وإن لم يكونوا تائبين"-','360','0','0');
INSERT INTO [t10777] VALUES (95,'قوله: "بعد أن لقوا الله تعالى عارفين"-','360','0','0');
INSERT INTO [t10777] VALUES (96,'قوله"وهم في مشيئة الله وحكمه، إن شاء غفر لهم وعفا عنهم بفضله"،','360','0','0');
INSERT INTO [t10777] VALUES (97,'"ذلك أن الله مولى أهل معرفته"-','361','0','0');
INSERT INTO [t10777] VALUES (98,'وقوله: "اللهم يا ولي الإسلام وأهله مسكنا بالإسلام"وفي نسخة"ثبتنا على الإسلام حتى نلقاك به"-','361','0','0');
INSERT INTO [t10777] VALUES (99,'قوله: (ونرى الصلاة خلف كل بر وفاجر من أهل القبلة، وعلى من مات منهم).','362','0','0');
INSERT INTO [t10777] VALUES (100,'قوله: (ولا ننزل أحدا منهم جنة ولا نارا).','367','0','0');
INSERT INTO [t10777] VALUES (101,'قوله: (ولا نشهد عليهم بكفر ولا بشرك ولا بنفاق، ما لم يظهر منهم شيء من ذلك، ونذر سرائرهم إلى الله تعالى).','368','0','0');
INSERT INTO [t10777] VALUES (102,'قوله: (ولا نرى [القتل] على أحد من أمة محمد صلى الله عليه وسلم إلا من وجب عليه السيف).','368','0','0');
INSERT INTO [t10777] VALUES (103,'قوله: (ولا نرى الخروج على أئمتنا وولاة أمورنا، وإن جاروا، ولا ندعو عليهم، ولا ننزع يدا من طاعتهم، ونرى طاعتهم من طاعة الله عز وجل فريضة، ما لم يأمروا بمعصية','368','0','0');
INSERT INTO [t10777] VALUES (104,'قوله: (ونتبع السنة والجماعة، ونجتنب الشذوذ والخلاف والفرقة).','371','0','0');
INSERT INTO [t10777] VALUES (105,'قوله: (ونحب أهل العدل والأمانة، ونبغض أهل الجور والخيانة).','373','0','0');
INSERT INTO [t10777] VALUES (106,'قوله: (ونقول: الله أعلم، فيما اشتبه علينا علمه).','374','0','0');
INSERT INTO [t10777] VALUES (107,'قوله: (ونرى المسح على الخفين، في السفر والحضر، كما جاء في الأثر).','376','0','0');
INSERT INTO [t10777] VALUES (108,'قوله: (والحج والجهاد ماضيان مع أولي الأمر من المسلمين، برهم وفاجرهم، إلى قيام الساعة، لا يبطلهما شيء ولا ينقضهما).','378','0','0');
INSERT INTO [t10777] VALUES (109,'قوله: (ونؤمن بالكرام الكاتبين، فإن الله قد جعلهم علينا حافظين).','379','0','0');
INSERT INTO [t10777] VALUES (110,'قوله: (ونؤمن بملك الموت، الموكل بقبض أرواح العالمين).','381','0','0');
INSERT INTO [t10777] VALUES (111,'قوله: (وبعذاب القبر لمن كان له أهلا، وسؤال منكر ونكير في قبره عن ربه ودينه ونبيه، على ما جاءت به الأخبار عن رسول الله صلى الله عليه وسلم، وعن الصحابة رضوان الله عليهم. و','388','0','0');
INSERT INTO [t10777] VALUES (112,'قوله: (ونؤمن بالبعث','398','0','0');
INSERT INTO [t10777] VALUES (113,'قوله: "وجزاء الأعمال"-','407','0','0');
INSERT INTO [t10777] VALUES (114,'قوله: "والعرض والحساب، وقراءة الكتاب، والثواب والعقاب"-','408','0','0');
INSERT INTO [t10777] VALUES (115,'قوله"والصراط"-','411','0','0');
INSERT INTO [t10777] VALUES (116,'قوله: "والميزان"-','413','0','0');
INSERT INTO [t10777] VALUES (117,'قوله (والجنة والنار مخلوقتان،','417','0','0');
INSERT INTO [t10777] VALUES (118,'قوله: "لا تفنيان أبدا ولا تبيدان"-','421','0','0');
INSERT INTO [t10777] VALUES (119,'قوله: "وخلق لهما أهلا"-','428','0','0');
INSERT INTO [t10777] VALUES (120,'قوله: "فمن شاء منهم إلى الجنة فضلا منه، ومن شاء منهم إلى النار عدلا منه"','429','0','0');
INSERT INTO [t10777] VALUES (121,'قوله: (والاستطاعة التي يجب بها الفعل، من نحو التوفيق الذي لا يجوز أن يوصف المخلوق به - تكون مع الفعل. وأما الاستطاعة من جهة الصحة والوسع، والتمكين وسلامة الآلات - فهي قبل الفعل، وبها يتعلق الخطاب، وهو كما قال تعالى: {لا يكلف الله نفسا إل','430','0','0');
INSERT INTO [t10777] VALUES (122,'قوله: (وأفعال العباد خلق الله وكسب من العباد).','435','0','0');
INSERT INTO [t10777] VALUES (123,'قوله: (ولم يكلفهم الله تعالى إلا ما يطيقون، ولا يطيقون إلا ما كلفهم.','445','0','0');
INSERT INTO [t10777] VALUES (124,'قوله: "وكل شيء يجري بمشيئة الله وعلمه وقضائه وقدره".','448','0','0');
INSERT INTO [t10777] VALUES (125,'قوله: "يفعل ما يشاء، وهو غير ظالم أبدا"-','450','0','0');
INSERT INTO [t10777] VALUES (126,'قوله: (وفي دعاء الأحياء وصدقاتهم منفعة للأموات).','455','0','0');
INSERT INTO [t10777] VALUES (127,'[قوله]: (والله تعالى يستجيب الدعوات، ويقضي الحاجات).','463','0','0');
INSERT INTO [t10777] VALUES (128,'قوله: (ويملك كل شيء، ولا يملكه شيء. ولا غنى عن الله تعالى طرفة عين، ومن استغنى عن الله طرفة عين، فقد كفر وصار من أهل الحين).','468','0','0');
INSERT INTO [t10777] VALUES (129,'قوله: (والله يغضب ويرضى، لا كأحد من الورى).','468','0','0');
