INSERT INTO [t1094] VALUES (1,'مقدمة','1','0','0');
INSERT INTO [t1094] VALUES (2,'من قوله (الحمد لله رب العالمين) إلى قوله (ويدينون به رب العالمين)','5','0','0');
INSERT INTO [t1094] VALUES (3,'قوله: نقول في توحيد الله معتقدين بتوفيق الله: أن الله واحد لا شريك له، ولا شيء مثله','7','0','0');
INSERT INTO [t1094] VALUES (4,'قوله: ولا شيء مثله','12','0','0');
INSERT INTO [t1094] VALUES (5,'قوله: ولا شيء يعجزه','13','0','0');
INSERT INTO [t1094] VALUES (6,'قوله: ولا إله غيره','15','0','0');
INSERT INTO [t1094] VALUES (7,'قوله: قديم بلا ابتداء، دائم بلا انتهاء','18','0','0');
INSERT INTO [t1094] VALUES (8,'قوله: لا يفنى ولا يبيد','20','0','0');
INSERT INTO [t1094] VALUES (9,'قوله: ولا يكون إلا ما يريد','21','0','0');
INSERT INTO [t1094] VALUES (10,'قوله: لا تبلغه الأوهام، ولا تدركه الأفهام','24','0','0');
INSERT INTO [t1094] VALUES (11,'قوله: ولا يشبه الأنام','25','0','0');
INSERT INTO [t1094] VALUES (12,'الأسئلة','26','0','0');
INSERT INTO [t1094] VALUES (13,'قوله: حي لا يموت، قيوم لا ينام','28','0','0');
INSERT INTO [t1094] VALUES (14,'قوله: خالق بلا حاجة، رازق بلا مؤنة','29','0','0');
INSERT INTO [t1094] VALUES (15,'قوله: مميت بلا مخافة، باعث بلا مشقة','30','0','0');
INSERT INTO [t1094] VALUES (16,'قوله: ما زال بصفاته قديما قبل خلقه، لم يزدد بكونهم شيئا لم يكن قبلهم من صفته، وكما كان بصفاته أزليا كذلك لا يزال عليها أبديا','31','0','0');
INSERT INTO [t1094] VALUES (17,'قوله: ليس بعد خلق الخلق استفاد اسم "الخالق"، ولا بإحداث البرية استفاد اسم "الباري','36','0','0');
INSERT INTO [t1094] VALUES (18,'قوله: له معنى الربوبية ولا مربوب ومعنى الخالق ولا مخلوق','37','0','0');
INSERT INTO [t1094] VALUES (19,'قوله: وكما أنه محيي الموتى بعد ما أحيا استحق هذا الاسم قبل إحيائهم كذلك استحق اسم الخالق قبل إنشائهم','38','0','0');
INSERT INTO [t1094] VALUES (20,'قوله: ذلك بأنه على كل شيء قدير','39','0','0');
INSERT INTO [t1094] VALUES (21,'قوله: ذلك بأنه على كل شيء قدير، وكل شيء إليه فقير، وكل أمر عليه يسير','40','0','0');
INSERT INTO [t1094] VALUES (22,'قوله: لا يحتاج إلى شيء','41','0','0');
INSERT INTO [t1094] VALUES (23,'قوله: خلق الخلق بعلمه','42','0','0');
INSERT INTO [t1094] VALUES (24,'قوله: وقدر لهم أقدارا','43','0','0');
INSERT INTO [t1094] VALUES (25,'الأسئلة','45','0','0');
INSERT INTO [t1094] VALUES (26,'قوله: وقدر لهم أقدارا وضرب لهم آجالا','48','0','0');
INSERT INTO [t1094] VALUES (27,'ولم يخف عليه شيء قبل أن يخلقهم وعلم ما هم عاملون قبل أن يخلقهم','49','0','0');
INSERT INTO [t1094] VALUES (28,'قوله: وأمرهم بطاعته ونهاهم عن معصيته','50','0','0');
INSERT INTO [t1094] VALUES (29,'قوله: وكل شيء يجري بتقديره ومشيئته، ومشيئته تنفذ لا مشيئة للعباد إلا ما شاء لهم، فما شاء لهم كان وما لم يشأ لم يكن','51','0','0');
INSERT INTO [t1094] VALUES (30,'قوله: يهدي من يشاء، ويعصم ويعافي فضلا، ويضل من يشاء ويخذل ويبتلي عدلا','52','0','0');
INSERT INTO [t1094] VALUES (31,'قوله: وكلهم يتقلبون في مشيئته بين فضله وعدله','56','0','0');
INSERT INTO [t1094] VALUES (32,'قوله: وهو متعال عن الأضداد والأنداد','57','0','0');
INSERT INTO [t1094] VALUES (33,'قوله: لا راد لقضائه، ولا معقب لحكمه، ولا غالب لأمره','58','0','0');
INSERT INTO [t1094] VALUES (34,'قوله: آمنا بذلك كله، وأيقنا أن كلا من عنده','59','0','0');
INSERT INTO [t1094] VALUES (35,'قوله: وإن محمدا عبده المصطفى ونبيه المجتبى','60','0','0');
INSERT INTO [t1094] VALUES (36,'الأسئلة','65','0','0');
INSERT INTO [t1094] VALUES (37,'قوله: وإنه خاتم الأنبياء','67','0','0');
INSERT INTO [t1094] VALUES (38,'قوله: وإمام الأتقياء','68','0','0');
INSERT INTO [t1094] VALUES (39,'قوله: وسيد المرسلين','69','0','0');
INSERT INTO [t1094] VALUES (40,'قوله: وحبيب رب العالمين','71','0','0');
INSERT INTO [t1094] VALUES (41,'قوله: وكل دعوى النبوة بعده فغي وهوى','73','0','0');
INSERT INTO [t1094] VALUES (42,'قوله: وهو المبعوث إلى عامة الجن','74','0','0');
INSERT INTO [t1094] VALUES (43,'قوله: وكافة الورى بالحق والهدى وبالنور والضياء','76','0','0');
INSERT INTO [t1094] VALUES (44,'قوله: وإن القرآن كلام الله','77','0','0');
INSERT INTO [t1094] VALUES (45,'الأسئلة','80','0','0');
INSERT INTO [t1094] VALUES (46,'تابع قوله: وإن القرآن كلام الله','83','0','0');
INSERT INTO [t1094] VALUES (47,'الأسئلة','93','0','0');
INSERT INTO [t1094] VALUES (48,'تابع قوله (وإن القرآن كلام الله)','95','0','0');
INSERT INTO [t1094] VALUES (49,'قوله: منه بدا بلا كيفية قولا وأنزله على رسوله وحيا','103','0','0');
INSERT INTO [t1094] VALUES (50,'قوله: وصدقه المؤمنون على ذلك حقا','104','0','0');
INSERT INTO [t1094] VALUES (51,'قوله: وأيقنوا أنه كلام الله -تعالى- بالحقيقة','105','0','0');
INSERT INTO [t1094] VALUES (52,'قوله: ليس بمخلوق ككلام البرية','106','0','0');
INSERT INTO [t1094] VALUES (53,'قوله: فمن سمعه فزعم أنه كلام البشر فقد كفر','107','0','0');
INSERT INTO [t1094] VALUES (54,'قوله: وقد ذمه الله وعابه وأوعده بسقر حيث قال تعالى: {سأصليه سقر (٢٦) }','108','0','0');
INSERT INTO [t1094] VALUES (55,'قوله: فلما أوعد الله بسقر لمن قال {إن هذا إلا قول البشر (٢٥) } علمنا وأيقنا أنه قول خالق البشر، ولا يشبه قول البشر.','109','0','0');
INSERT INTO [t1094] VALUES (56,'قوله: ومن وصف الله بمعنى من معاني البشر فقد كفر','110','0','0');
INSERT INTO [t1094] VALUES (57,'قوله: فمن أبصرها فاعتبر وصمت لقول الكفار انزجر وعلم أنه بصفاته ليس كالبشر','111','0','0');
INSERT INTO [t1094] VALUES (58,'الأسئلة','112','0','0');
INSERT INTO [t1094] VALUES (59,'قوله: والرؤية حق لأهل الجنة بغير إحاطة ولا كيفية','114','0','0');
INSERT INTO [t1094] VALUES (60,'الأسئلة','129','0','0');
INSERT INTO [t1094] VALUES (61,'قوله: كما نطق به كتاب ربنا {وجوه يومئذ ناضرة (٢٢) إلى ربها ناظرة (٢٣) }','133','0','0');
INSERT INTO [t1094] VALUES (62,'قوله: وتفسيره على ما أراده الله -تعالى- وعلمه "، تفسيره على ما أراده الله -تعالى- وعلمه','134','0','0');
INSERT INTO [t1094] VALUES (63,'قوله: وكل ما جاء في ذلك من الحديث الصحيح عن الرسول صلى الله عليه وعلى آله وسلم فهو كما قال: " ومعناه على ما أراد ".','135','0','0');
INSERT INTO [t1094] VALUES (64,'قوله: لا ندخل في ذلك متأولين بآرائنا ولا متوهمين بأهوائنا','136','0','0');
INSERT INTO [t1094] VALUES (65,'قوله: فإنه ما سلم في دينه إلا من سلم لله عز وجل ولرسوله -صلى الله عليه وعلى آله وسلم-، ورد علم ما اشتبه عليه إلى عالمه','137','0','0');
INSERT INTO [t1094] VALUES (66,'قوله: ولا تثبت قدم الإسلام إلا على ظهر التسليم والاستسلام','138','0','0');
INSERT INTO [t1094] VALUES (67,'قوله: فمن رام علم ما حظر عنه علمه ولم يقنع بالتسليم فهمه حجبه مرامه عن خالص التوحيد وصافي المعرفة وصحيح الإيمان','140','0','0');
INSERT INTO [t1094] VALUES (68,'قوله: فيتذبذب بين الكفر والإيمان والتصديق والتكذيب والإقرار والإنكار موسوسا تائها شاكا لا مؤمنا مصدقا ولا جاحدا مكذبا','141','0','0');
INSERT INTO [t1094] VALUES (69,'قوله: ولا يصح الإيمان بالرؤية لأهل دار السلام لمن اعتبرها منهم بوهم أو تأولها بفهم','142','0','0');
INSERT INTO [t1094] VALUES (70,'قوله: إذ كان تأويل الرؤية وتأويل كل معنى يضاف إلى الربوبية بترك التاويل ولزوم التسليم عليه دين المسلمين','143','0','0');
INSERT INTO [t1094] VALUES (71,'قوله: ومن لم يتوق النفي والتشبيه زل ولم يصب التنزيه','144','0','0');
INSERT INTO [t1094] VALUES (72,'قوله: فإن ربنا -جلا وعلا- موصوف بصفات الوحدانية منعوت بنعوت الفردانية ليس في معناه أحد من البرية','145','0','0');
INSERT INTO [t1094] VALUES (73,'قوله: وتعالى عن الحدود والغايات والأركان والأعضاء والأدوات لا تحويه الجهات الست كسائر المبتدعات','146','0','0');
INSERT INTO [t1094] VALUES (74,'قوله: والمعراج حق وقد أسري بالنبي صلى الله عليه وعلى آله وسلم وعرج بشخصه في اليقظة إلى السماء ثم إلى حيث شاء الله من العلا وأكرمه الله بما شاء وأوحى إليه ما أوحى ما كذب الفؤاد ما رأى فصلى الله عليه وسلم في الآخرة والأولى','150','0','0');
INSERT INTO [t1094] VALUES (75,'قوله: والحوض الذي أكرمه الله -تعالى- به غياثا لأمته حق','155','0','0');
INSERT INTO [t1094] VALUES (76,'قوله: والشفاعة التي ادخرها لهم حق كما روي في الأخبار','159','0','0');
INSERT INTO [t1094] VALUES (77,'قوله: والميثاق الذي أخذه الله تعالى من آدم وذريته حق','164','0','0');
INSERT INTO [t1094] VALUES (78,'قوله: وقد علم الله تعالى فيما لم يزل عدد من يدخل الجنة وعدد من يدخل النار جملة واحدة فلا يزداد في ذلك العدد ولا ينقص منه','167','0','0');
INSERT INTO [t1094] VALUES (79,'قوله: والسعيد من سعد بقضاء الله والشقي من شقي بقضاء الله','173','0','0');
INSERT INTO [t1094] VALUES (80,'قوله: وأصل القدر سر الله تعالى في خلقه','174','0','0');
INSERT INTO [t1094] VALUES (81,'قوله: والتعمق والنظر في ذلك ذريعة الخذلان وسلم الحرمان ودرجة الطغيان','175','0','0');
INSERT INTO [t1094] VALUES (82,'قوله: فالحذر كل الحذر من ذلك نظرا وفكرا ووسوسة','176','0','0');
INSERT INTO [t1094] VALUES (83,'قوله: فإن الله -تعالى- طوى علم القدر عن أنامه','177','0','0');
INSERT INTO [t1094] VALUES (84,'قوله: ونهاهم عن مرامه','178','0','0');
INSERT INTO [t1094] VALUES (85,'قوله: فمن سأل لما فعل فقد رد حكم الكتاب ومن رد حكم الكتاب كان من الكافرين','179','0','0');
INSERT INTO [t1094] VALUES (86,'قوله: فهذا جملة من يحتاج إليه من هو منور قلبه من أولياء الله -تعالى-','180','0','0');
INSERT INTO [t1094] VALUES (87,'قوله: وهي درجة الراسخين في العلم','181','0','0');
INSERT INTO [t1094] VALUES (88,'قوله: لأن العلم علمان: علم في الخلق موجود وعلم في الخلق مفقود','182','0','0');
INSERT INTO [t1094] VALUES (89,'قوله: فإنكار العلم الموجود كفر وادعاء العلم المفقود كفر','183','0','0');
INSERT INTO [t1094] VALUES (90,'قوله: ولا يثبت الإيمان إلا بقبول العلم الموجود وترك طلب العلم المفقود','184','0','0');
INSERT INTO [t1094] VALUES (91,'قوله: ونؤمن باللوح والقلم وبجميع ما فيه قد رقم','185','0','0');
INSERT INTO [t1094] VALUES (92,'قوله: فلو اجتمع الخلق كلهم على شيء كتبه الله -تعالى- فيه أنه كائن ليجعلوه غير كائن لم يقدروا عليه ولو اجتمعوا كلهم على شيء لم يكتبه الله -تعالى- فيه ليجعلوه كائنا لم يقدروا عليه','187','0','0');
INSERT INTO [t1094] VALUES (93,'قوله: جف القلم بما هو كائن إلى يوم القيامة','188','0','0');
INSERT INTO [t1094] VALUES (94,'قوله: وما أخطأ العبد لم يكن ليصيبه وما أصابه لم يكن ليخطئه','189','0','0');
INSERT INTO [t1094] VALUES (95,'قوله: وعلى العبد أن يعلم أن الله قد سبق علمه في كل كائن من خلقه','190','0','0');
INSERT INTO [t1094] VALUES (96,'قوله: ليس فيه ناقض ولا معقب','191','0','0');
INSERT INTO [t1094] VALUES (97,'قوله: ولا مزيل ولا مغير','192','0','0');
INSERT INTO [t1094] VALUES (98,'قوله: وذلك من عقد الإيمان وأصول المعرفة','193','0','0');
INSERT INTO [t1094] VALUES (99,'قوله: والاعتراف بتوحيد الله تعالى وربوبيته','194','0','0');
INSERT INTO [t1094] VALUES (100,'قوله: كما قال تعالى في كتابه: {وخلق كل شيء فقدره تقديرا (٢) }','195','0','0');
INSERT INTO [t1094] VALUES (101,'قوله: وقال تعالى: {وكان أمر الله قدرا مقدورا (٣٨) }','196','0','0');
INSERT INTO [t1094] VALUES (102,'قوله: فويل لمن صار لله تعالى في القدر خصيما','197','0','0');
INSERT INTO [t1094] VALUES (103,'قوله: لقد التمس بوهمه في فحص الغيب سرا كتيما','198','0','0');
INSERT INTO [t1094] VALUES (104,'قوله: وعاد بما قال فيه أفاكا أثيما','199','0','0');
INSERT INTO [t1094] VALUES (105,'قوله: والعرش والكرسي حق، وهو مستغن عن العرش وما دونه محيط بكل شيء وفوقه وقد أعجز عن الإحاطة خلقه','200','0','0');
INSERT INTO [t1094] VALUES (106,'قوله: ونقول: إن الله اتخذ إبراهيم خليلا، وكلم الله موسى تكليما إيمانا وتصديقا وتسليما','210','0','0');
INSERT INTO [t1094] VALUES (107,'قوله: ونؤمن بالملائكة والنبيين والكتب المنزلة على المرسلين','211','0','0');
INSERT INTO [t1094] VALUES (108,'قوله: ونسمي أهل قبلتنا مسلمين مؤمنين، ما داموا بما جاء به النبي صلى الله عليه وعلى آله وسلم معترفين، وله بكل ما قاله وأخبر مصدقين','215','0','0');
INSERT INTO [t1094] VALUES (109,'قوله: ولا نخوض في الله، ولا نماري في دين الله','216','0','0');
INSERT INTO [t1094] VALUES (110,'قوله: ولا نجادل في القرآن','217','0','0');
INSERT INTO [t1094] VALUES (111,'قوله: ونشهد أنه كلام رب العالمين نزل به الروح الأمين، فعلمه سيد المرسلين محمدا -صلى الله عليه وعلى آله وسلم','218','0','0');
INSERT INTO [t1094] VALUES (112,'قوله: ولا نقول بخلقه ولا نخالف جماعة المسلمين','219','0','0');
INSERT INTO [t1094] VALUES (113,'قوله: ولا نكفر أحدا من أهل القبلة بذنب ما لم يستحله','220','0','0');
INSERT INTO [t1094] VALUES (114,'قوله: ولا نقول لا يضر مع الإيمان ذنب لمن عمله','224','0','0');
INSERT INTO [t1094] VALUES (115,'نرجو للمحسنين من المؤمنين أن يعفوا عنهم','225','0','0');
INSERT INTO [t1094] VALUES (116,'قوله: ويدخلهم الجنة برحمته','226','0','0');
INSERT INTO [t1094] VALUES (117,'قوله: ولا نأمن عليهم، ولا نشهد لهم بالجنة، ونستغفر لمسيئهم، ونخاف عليهم ولا نقنطهم','229','0','0');
INSERT INTO [t1094] VALUES (118,'الأسئلة','230','0','0');
INSERT INTO [t1094] VALUES (119,'قوله: والأمن والإياس ينقلان عن الملة وسبيل الحق بينهما لأهل القبلة','232','0','0');
INSERT INTO [t1094] VALUES (120,'قوله: ولا يخرج العبد من الإيمان إلا بجحود ما أدخله فيه','235','0','0');
INSERT INTO [t1094] VALUES (121,'قوله: والإيمان هو الإقرار باللسان والتصديق بالجنان','237','0','0');
INSERT INTO [t1094] VALUES (122,'قوله: وجميع ما صح عن رسول الله -صلى الله عليه وعلى آله وسلم- من الشرع والبيان كله حق','242','0','0');
INSERT INTO [t1094] VALUES (123,'قوله: والإيمان واحد وأهله في أصله سواء','244','0','0');
INSERT INTO [t1094] VALUES (124,'قوله: والتفاضل بينهم بالخشية والتقي ومخالفة الهوي وملازمة الأولى','245','0','0');
INSERT INTO [t1094] VALUES (125,'قوله: والمؤمنون كلهم أولياء الرحمن، وأكرمهم عند الله أطوعهم وأتبعهم للقرآن','249','0','0');
INSERT INTO [t1094] VALUES (126,'قوله: والإيمان هو الإيمان بالله وملائكته وكتبه ورسله واليوم الآخر والقدر خيره وشره وحلوه ومره من الله تعالى','253','0','0');
INSERT INTO [t1094] VALUES (127,'قوله: ونحن مؤمنون بذلك كله لا نفرق بين أحد من رسله','254','0','0');
INSERT INTO [t1094] VALUES (128,'قوله: وأهل الكبائر من أمة محمد -صلى الله عليه وعلى آله وسلم- في النار، لا يخلدون إذا ماتوا وهم موحدون','255','0','0');
INSERT INTO [t1094] VALUES (129,'قوله: إذا ماتوا وهم موحدون وإن لم يكونوا تائبين','257','0','0');
INSERT INTO [t1094] VALUES (130,'قوله: وإن لم يكونوا تائبين بعد أن لقوا الله عارفين مؤمنين','258','0','0');
INSERT INTO [t1094] VALUES (131,'قوله: وهم في مشيئته وحكمه، إن شاء غفر لهم وعفا عنهم بفضله كما ذكر عز وجل في كتابه: {ويغفر ما دون ذلك لمن يشاء}','259','0','0');
INSERT INTO [t1094] VALUES (132,'قوله: وإن شاء عذبهم في النار بعدله','260','0','0');
INSERT INTO [t1094] VALUES (133,'قوله: ثم يخرجهم منها برحمته وشفاعة الشافعين من أهل طاعته','261','0','0');
INSERT INTO [t1094] VALUES (134,'قوله: ثم يبعثهم إلي جنته','262','0','0');
INSERT INTO [t1094] VALUES (135,'قوله: ولم يجعلهم في الدارين كأهل نكرته','263','0','0');
INSERT INTO [t1094] VALUES (136,'قوله: ولم ينالوا من ولايته','264','0','0');
INSERT INTO [t1094] VALUES (137,'قوله: اللهم يا ولي الإسلام وأهله ثبتنا على الإسلام حتى نلقاك به','265','0','0');
INSERT INTO [t1094] VALUES (138,'قوله: ونرى الصلاة خلف كل بر وفاجر من أهل القبلة','266','0','0');
INSERT INTO [t1094] VALUES (139,'قوله: ونرى الصلاة خلف كل بر وفاجر من أهل القبلة، وعلى من مات منهم','270','0','0');
INSERT INTO [t1094] VALUES (140,'قوله: ولا نشهد عليهم بكفر، ولا بشرك، ولا بنفاق، ما لم يظهر منهم شيء من ذلك، ونذر سرائرهم إلى الله تعالى','271','0','0');
INSERT INTO [t1094] VALUES (141,'قوله: ولا نرى السيف على أحد من أمة محمد -صلى الله عليه وعلى آله وسلم- إلا من وجب عليه السيف','272','0','0');
INSERT INTO [t1094] VALUES (142,'قوله: ولا نرى الخروج على أئمتنا وولاة أمرنا، وإن جاروا. ولا ندعو عليهم، ولا ننزع يدا من طاعتهم، ونرى طاعتهم من طاعة الله عز وجل فريضة ما لم يأمروا بمعصية، وندعوا لهم بالصلاح والمعافاة','273','0','0');
INSERT INTO [t1094] VALUES (143,'قوله: ونرى طاعتهم من طاعة الله عز وجل فريضة ما لم يأمروا بمعصية','277','0','0');
INSERT INTO [t1094] VALUES (144,'قوله: وندعوا لهم بالصلاح والمعافاة','278','0','0');
INSERT INTO [t1094] VALUES (145,'قوله: ونتبع السنة والجماعة، ونجتنب الشذوذ والخلاف والفرقة','279','0','0');
INSERT INTO [t1094] VALUES (146,'قوله: ونحب أهل العدل والأمانة، ونبغص أهل الجور والخيانة','281','0','0');
INSERT INTO [t1094] VALUES (147,'قوله: ونقول: "الله أعلم" فيما اشتبه علينا علمه','282','0','0');
INSERT INTO [t1094] VALUES (148,'قوله: ونرى المسح على الخفين في السفر والحضر كما جاء في الأثر','283','0','0');
INSERT INTO [t1094] VALUES (149,'قوله: والحج والجهاد ماضيان مع أولي الأمر من المسلمين، برهم وفاجرهم إلى قيام الساعة، لا يبطلهما شيء، ولا ينقضهما','285','0','0');
INSERT INTO [t1094] VALUES (150,'قوله: ونؤمن بالكرام الكاتبين؛ فإن الله قد جعلهم علينا حافظين','287','0','0');
INSERT INTO [t1094] VALUES (151,'قوله: ونؤمن بملك الموت الموكل بقبض أرواح العالمين','288','0','0');
INSERT INTO [t1094] VALUES (152,'قوله: وبعذاب القبر لمن كان له أهلا، وسؤال منكر، ونكير في قبره عن ربه ودينه، ونبيه على ما جاءت به الأخبار عن رسول الله صلى الله عليه وسلم وعلى آله وسلم، وعن الصحابة رضوان الله عليهم','296','0','0');
INSERT INTO [t1094] VALUES (153,'قوله: ونؤمن بالبعث، وجزاء الأعمال يوم القيامة، والعرض، والحساب، وقراءة الكتاب، والثواب، والعقاب، والصراط، والميزان','304','0','0');
INSERT INTO [t1094] VALUES (154,'قوله: والجنة والنار مخلوقتان لا تفنيان أبدا، ولا تبيدان','315','0','0');
INSERT INTO [t1094] VALUES (155,'قوله: فمن شاء منهم إلى الجنة فضلا منه، ومن شاء منهم إلى النار عدلا منه','319','0','0');
INSERT INTO [t1094] VALUES (156,'قوله: وكل يعمل لما قد فرغ له وصائر إلى ما خلق له','320','0','0');
INSERT INTO [t1094] VALUES (157,'قوله: والخير والشر مقدران على العباد','321','0','0');
INSERT INTO [t1094] VALUES (158,'قوله: والاستطاعة التي يجب بها الفعل من نحو التوفيق، الذي لا يجوز أن يوصف المخلوق به، فهي مع الفعل، وأما الاستطاعة من جهة الصحة والوسع والتمكن، وسلامة الآلات، فهي قبل الفعل، وبها يتعلق الخطاب، وهو كما قال -تعالى- {لا يكلف الله نفسا إلا','322','0','0');
INSERT INTO [t1094] VALUES (159,'قوله: وأما الاستطاعة من جهة الصحة والوسع والتمكن وسلامة الآلات، فهي قبل الفعل، وبها يتعلق الخطاب','325','0','0');
INSERT INTO [t1094] VALUES (160,'قوله: وهو كما قال تعالى {لا يكلف الله نفسا إلا وسعها}','326','0','0');
INSERT INTO [t1094] VALUES (161,'قوله: وأفعال العباد خلق الله وكسب من العباد','327','0','0');
INSERT INTO [t1094] VALUES (162,'قوله: ولم يكلفهم الله -تعالى- إلا ما يطيقون','330','0','0');
INSERT INTO [t1094] VALUES (163,'قوله: ولا يطيقون إلا ما كلفهم','331','0','0');
INSERT INTO [t1094] VALUES (164,'قوله: وهو تفسير لا حول ولا قوة إلا بالله','332','0','0');
INSERT INTO [t1094] VALUES (165,'قوله: نقول لا حيلة لأحد، ولا حركة لأحد ولا تحول لأحد عن معصية الله إلا بمعونة الله','333','0','0');
INSERT INTO [t1094] VALUES (166,'قوله: وكل شيء يجري بمشيئة الله تعالى وعلمه وقضائه وقدره','334','0','0');
INSERT INTO [t1094] VALUES (167,'قوله: غلبت مشيئته المشيئات كلها','335','0','0');
INSERT INTO [t1094] VALUES (168,'قوله: وغلب قضائه الحيل كلها','336','0','0');
INSERT INTO [t1094] VALUES (169,'قوله: يفعل ما يشاء وهو غير ظالم أبدا','337','0','0');
INSERT INTO [t1094] VALUES (170,'قوله: تقدس عن كل سوء وحين','339','0','0');
INSERT INTO [t1094] VALUES (171,'قوله: وتنزه عن كل عيب وشين','340','0','0');
INSERT INTO [t1094] VALUES (172,'قوله: {لا يسأل عما يفعل وهم يسألون}','341','0','0');
INSERT INTO [t1094] VALUES (173,'قوله: وفي دعاء الأحياء وصدقاتهم منفعة للأموات','342','0','0');
INSERT INTO [t1094] VALUES (174,'قوله: والله تعالى يستجيب الدعوات ويقضى الحاجات','348','0','0');
INSERT INTO [t1094] VALUES (175,'قوله: ويملك كل شيء ولا يملكه شيء','351','0','0');
INSERT INTO [t1094] VALUES (176,'قوله: ولا غنى عن الله تعالى طرفة عين','352','0','0');
INSERT INTO [t1094] VALUES (177,'قوله: ومن استغنى عن الله طرفة عين فقد كفر وصار من أهل الحين','353','0','0');
INSERT INTO [t1094] VALUES (178,'قوله: والله يغضب ويرضى لا كأحد من الورى','354','0','0');
INSERT INTO [t1094] VALUES (179,'قوله: ونحب أصحاب رسول الله -صلى الله عليه وعلى آله وسلم- ولا نفرط في حب أحد منهم','356','0','0');
INSERT INTO [t1094] VALUES (180,'قوله: ولا نتبرأ من أحد منهم ونبغض من يبغضهم وبغير الخير يذكرهم ولا نذكرهم إلا بخير','357','0','0');
INSERT INTO [t1094] VALUES (181,'قوله: ونثبت الخلافة بعد رسول الله -صلى الله عليه وعلى آله وسلم- أولا لأبي بكر الصديق - رضي الله عنه - تفضيلا له وتقديما على جميع الأمة ثم لعمر بن الخطاب - رضي الله عنه - ثم لعثمان - رضي الله عنه - ثم لعلي بن أبي طالب - رضي الله عنه - وهم الخلفاء الرا','360','0','0');
INSERT INTO [t1094] VALUES (182,'قوله: وأن العشرة الذين سماهم رسول الله -صلى الله عليه وعلى آله وسلم- وبشرهم بالجنة نشهد لهم بالجنة على ما شهد لهم رسول الله -صلى الله عليه وعلى آله وسلم- وقوله الحق: وهم أبو بكر، وعمر وعثمان وعلي وطلحة، والزبير وسعد وسعيد، وعبد الرحمن بن عوف، وأبو','364','0','0');
INSERT INTO [t1094] VALUES (183,'قوله: وعلماء السلف من السابقين ومن بعدهم من التابعين أهل الخير والأثر وأهل الفقه والنظر لا يذكرون إلا بالجميل','367','0','0');
INSERT INTO [t1094] VALUES (184,'قوله: ومن ذكرهم بسوء فهو على غير السبيل','368','0','0');
INSERT INTO [t1094] VALUES (185,'قوله: ولا نفضل أحد من الأولياء على أحد من الأنبياء -عليهم السلام- ونقول نبي واحد أفضل من جميع الأولياء','369','0','0');
INSERT INTO [t1094] VALUES (186,'الأسئلة','373','0','0');
INSERT INTO [t1094] VALUES (187,'قوله: ونؤمن بما جاء من كراماتهم وصح عن الثقات من رواياتهم','376','0','0');
INSERT INTO [t1094] VALUES (188,'قوله: ونؤمن بأشراط الساعة من خروج الدجال، ونزول عيسى بن مريم -عليه السلام- من السماء، ونؤمن بطلوع الشمس من مغربها، وخروج دابة الأرض من موضعها','382','0','0');
INSERT INTO [t1094] VALUES (189,'قوله: ولا نصدق كاهنا ولا عرافا ولا من يدعي شيئا يخالف الكتاب والسنة وإجماع الأمة','385','0','0');
INSERT INTO [t1094] VALUES (190,'قوله: ونرى الجماعة حقا وصوابا والفرقة زيعا وعذابا','392','0','0');
INSERT INTO [t1094] VALUES (191,'قوله: ودين الله في الأرض والسماء واحد، وهو دين الإسلام، قال الله تعالى: {إن الدين عند الله الإسلام} وقال تعالى: {ورضيت لكم الإسلام دينا} وهو بين الغلو والتقصير، وبين التشبيه والتعطيل وبين الجبر والقدر وبين الأمن والإياس','395','0','0');
INSERT INTO [t1094] VALUES (192,'قوله: وهو بين الغلو والتقصير','397','0','0');
INSERT INTO [t1094] VALUES (193,'قوله: وبين التشبيه والتعطيل','398','0','0');
INSERT INTO [t1094] VALUES (194,'قوله: وبين الجبر والقدر','399','0','0');
INSERT INTO [t1094] VALUES (195,'قوله: وبين الأمن والإياس','400','0','0');
INSERT INTO [t1094] VALUES (196,'قوله: فهذا ديننا واعتقادنا ظاهرا وباطنا','401','0','0');
INSERT INTO [t1094] VALUES (197,'قوله: ونحن برآء إلى الله من كل من خالف الذي ذكرناه وبيناه','402','0','0');
INSERT INTO [t1094] VALUES (198,'قوله: ونسأل الله تعالى أن يثبتنا على الإيمان ويختم لنا به','403','0','0');
INSERT INTO [t1094] VALUES (199,'قوله: ويعصمنا من الأهواء المختلفة والآراء المتفرقة والمذاهب الردية مثل المشبهة والمعتزلة والجهمية والجبرية والقدرية وغيرهم','404','0','0');
INSERT INTO [t1094] VALUES (200,'قوله: ونحن منهم براء','414','0','0');
INSERT INTO [t1094] VALUES (201,'قوله: وهم عندنا ضلال وأردياء','415','0','0');
INSERT INTO [t1094] VALUES (202,'قوله: وبالله العصمة والتوفيق','416','0','0');
