INSERT INTO [t11030] VALUES (1,'من كان يؤمن بالله واليوم الآخر فليكرم ضيفه','1','0','0');
INSERT INTO [t11030] VALUES (2,'من كان يؤمن بالله واليوم الآخر فليكرم ضيفه، جائزة الضيف يوم وليلة، والضيافة ثلاثة أيام،','2','0','0');
INSERT INTO [t11030] VALUES (3,'جائزته يوم وليلة، والضيافة ثلاثة أيام، لا يحل لأحد أن يقيم عند أخيه حتى يؤثمه، قيل: وكيف','3','0','0');
INSERT INTO [t11030] VALUES (4,'من كان يؤمن بالله واليوم الآخر فليكرم ضيفه','4','0','0');
INSERT INTO [t11030] VALUES (5,'أول من ضيف الضيف إبراهيم عليه السلام','5','0','0');
INSERT INTO [t11030] VALUES (6,'إبراهيم أول من أضاف الضيف','6','0','0');
INSERT INTO [t11030] VALUES (7,'إبراهيم عليه السلام يكنى أبا الضيفان وكان لقصره أربعة أبواب قال أبو أسامة: فزادني معلى بن','7','0','0');
INSERT INTO [t11030] VALUES (8,'ضيف إبراهيم المكرمين} [الذاريات: ٢٤] قال: خدمته إياهم، خدمهم بنفسه','8','0','0');
INSERT INTO [t11030] VALUES (9,'إذا أراد أن يتغدى خرج ميلا أو ميلين يلتمس من يتغدى معه','9','0','0');
INSERT INTO [t11030] VALUES (10,'ويؤثرون على أنفسهم ولو كان بهم خصاصة} [الحشر: ٩] قال: نزلت في رجل من الأنصار أرسل النبي','10','0','0');
INSERT INTO [t11030] VALUES (11,'لقد عجب الله عز وجل البارحة منكم ومن ضيفكم وأنزلت فيه الآية {ويؤثرون على أنفسهم ولو كان','11','0','0');
INSERT INTO [t11030] VALUES (12,'إذا نزل بنا ضيف، لم نأكل حتى يشبع أو حتى يأكل','12','0','0');
INSERT INTO [t11030] VALUES (13,'سلي الطارق المعتر يا أم خالد ... إذا ما رأيني بين ناري ومجزري أأبذل وجهي إنه أول القرى','13','0','0');
INSERT INTO [t11030] VALUES (14,'إنك يا ابن جعفر نعم الفتى ... ونعم مأوى طارق إذا أتى ورب ضيف طرق الحي سرى ... صادف زادا','14','0','0');
INSERT INTO [t11030] VALUES (15,'ابغوني شفرة، فناولناه الشفرة، فوجأ في لبتها، وقال: شأنكم، قال: وأقمنا اليوم الثاني، وإذا','15','0','0');
INSERT INTO [t11030] VALUES (16,'إلي بوأك الله مساكن الأبرار، قال: فأعجب بمنطقها، فتحول إلى باب الخيمة، فألقت له وسادة من','16','0','0');
INSERT INTO [t11030] VALUES (17,'فجاء بناقة، فضرب عرقوبيها، ثم قال: دونكم، فانحروها، قال: فنحرناها فأصبنا من أطيابها، فلما','17','0','0');
INSERT INTO [t11030] VALUES (18,'كان قيس بن سعد يستدين ويطعمهم، فقال أبو بكر وعمر رضي الله تعالى عنهما: إن تركنا هذا الفتى','18','0','0');
INSERT INTO [t11030] VALUES (19,'إنه في بيت جود يعني في غزوة الخبط','19','0','0');
INSERT INTO [t11030] VALUES (20,'إذا أمسوا انطلق الرجل بالرجل، والرجل بالرجلين، والرجل بالخمسة، فأما سعد بن عبادة فإنه كان','20','0','0');
INSERT INTO [t11030] VALUES (21,'اللهم ارزقني مالا أستعين به على فعالي، فإنه لا يصلح الفعال إلا المال','21','0','0');
INSERT INTO [t11030] VALUES (22,'من أحب شحما ولحما فليأت سعد بن عبادة، ثم أدركت ابنه بمثل ذلك يدعو به','22','0','0');
INSERT INTO [t11030] VALUES (23,'من أراد الشحم واللحم فليأت دار دليم، فمات دليم فنادى منادي عبادة مثل ذلك، ثم مات عبادة','23','0','0');
INSERT INTO [t11030] VALUES (24,'لأهدينها إلى الكعبة فكان يهديها','24','0','0');
INSERT INTO [t11030] VALUES (25,'كل العرب أجود مني، ثم أنشأ يحدث قال: نزلت على غلام من العرب يتيم ذات ليلة، وكانت له مائة','25','0','0');
INSERT INTO [t11030] VALUES (26,'العرب تضيف أحسن ما يكون من الضيافة، فنبحه كلب، فقصد نحوه، حتى انتهى إلى الكلب، فخرج الكلب','26','0','0');
INSERT INTO [t11030] VALUES (27,'لقد مكثت سنة ما أنام بليل حتى أصبح، ولا رأيت نارا رفعت بليل إلا ظننت أن نفسي ستخرج، أذكر','27','0','0');
INSERT INTO [t11030] VALUES (28,'أتعب الناس آبي اللحم الغفاري حيث يقول: [البحر الطويل] لقد علمت عرسي قتيلة أنني ... طويل','28','0','0');
INSERT INTO [t11030] VALUES (29,'وكلما رفعت صحفة وضعت على دكان في الدار حتى فرغوا فتح الباب، فأدخل من كان من مسكين وغيره،','29','0','0');
INSERT INTO [t11030] VALUES (30,'فما يقوم أحد من عنده إلا وهو كظيظ','30','0','0');
INSERT INTO [t11030] VALUES (31,'إذا أمعن القوم حسر عن ذراعيه وخوى تخوية الظليم واستأنف الأمر استئنافا قال محمد: قال أبي:','31','0','0');
INSERT INTO [t11030] VALUES (32,'رهقتك الجمعة؟، قال: قميصي يجف، قلت: وما لك إلا قميص واحد؟، قال يزيد: ما له إلا قميص،','32','0','0');
INSERT INTO [t11030] VALUES (33,'أمر أبو قفاص بمساكين الحي فنصبت لهم الموائد، فأجلسوا عليها، وقام أبو قفاص وولده عليهم حتى','33','0','0');
INSERT INTO [t11030] VALUES (34,'يجلس بفناء داره وينصب مائدته، فلا يجوز أحد إلا أجلسه معه','34','0','0');
INSERT INTO [t11030] VALUES (35,'ما ساد منا إلا سخي على الطعام','35','0','0');
INSERT INTO [t11030] VALUES (36,'إنى لأطعم كل يوم ستة وثلاثين ألفا من الأعراب من تمر وسويق','36','0','0');
INSERT INTO [t11030] VALUES (37,'فقام إلى فرسه فوجأ بحربته في لبته، ثم قدح زنده وأورى ناره، ثم جاء بمدية فكشط عن جلده ثم','37','0','0');
INSERT INTO [t11030] VALUES (38,'إذا نزل بك ضيف فلا تكلف له ما لا تطيق، وأطعمه من إطعام أهلك، والقه بوجه طلق، فإنك إن تكلف','38','0','0');
INSERT INTO [t11030] VALUES (39,'عوى آيسا شبه الجنون وما به ... جنون ولكن كيد أمر يحاوله فأثقبت ناري ثم أبرزت ضوءها ...','39','0','0');
INSERT INTO [t11030] VALUES (40,'ضاف حاتما رجل في سنة فلم يقدر له على شيء فطلب من بني عمه قراه فلم يقدر على شيء، وله ناقة','40','0','0');
INSERT INTO [t11030] VALUES (41,'ومستفتح باب الصدا يستنبهه ... فتاه وجوف الليل مضطرب الكسر رفعت له نارا ثقوبا زنادها ...','41','0','0');
INSERT INTO [t11030] VALUES (42,'ومجتنب أهل الثرى يبتغي القرى ... أتانا وخرق دوننا متنازح أتانا وقد بلته شهباء حرجف ...','42','0','0');
INSERT INTO [t11030] VALUES (43,'ومستنبح يبغي المبيت ودونه ... من الليل سجفا ظلمة وكسورها رفعت له ناري فلما اهتدى بها ...','43','0','0');
INSERT INTO [t11030] VALUES (44,'ومستنبح بعد الهدوء دعوته ... وقد حان من ساري السماء طروق تألق في عين من المزن وادق ... له','44','0','0');
INSERT INTO [t11030] VALUES (45,'لأنحرن كل يوم جزورين','45','0','0');
INSERT INTO [t11030] VALUES (46,'للخير أسرع إلى البيت الذي يطعم فيه من الشفرة إلى سنام البعير','46','0','0');
INSERT INTO [t11030] VALUES (47,'إن الله عز وجل يحب أهل البيت الخصب','47','0','0');
INSERT INTO [t11030] VALUES (48,'إن الله يحب أن يرى أثر نعمته على عبده في مأكله ومشربه','48','0','0');
INSERT INTO [t11030] VALUES (49,'أحب الطعام إلى الله عز وجل ما كثرت عليه الأيدي','49','0','0');
INSERT INTO [t11030] VALUES (50,'إذا جمع أربعا فقد كمل كل شيء من شأنه، إذا كان أوله حلالا، وذكر اسم الله عليه حين يوضع،','50','0','0');
INSERT INTO [t11030] VALUES (51,'من السنة أن يمشي الرجل مع ضيفه إلى باب الدار','51','0','0');
INSERT INTO [t11030] VALUES (52,'أطعت الله وعصيت الشيطان','52','0','0');
INSERT INTO [t11030] VALUES (53,'الضيافة ثلاثة أيام، فما زاد فهو صدقة، وعلى الضيف أن يتحول بعد ثلاثة','53','0','0');
INSERT INTO [t11030] VALUES (54,'ليلة الضيف حق على كل مسلم، فإن أصبح بفنائه فهو عليه دين، فإن شاء اقتضى وإن شاء','54','0','0');
INSERT INTO [t11030] VALUES (55,'كفى بالمرء شرا أن يتسخط ما قرب إليه','55','0','0');
INSERT INTO [t11030] VALUES (56,'هلاك بالرجل يدخل عليه الرجل من إخوانه فيحتقر ما في بيته أن يقدمه، وهلاك بالقوم أن يحقروا','56','0','0');
INSERT INTO [t11030] VALUES (57,'فقدم إلينا مرقا وليس فيه لحم','57','0','0');
INSERT INTO [t11030] VALUES (58,'فقدم إلينا إحدى عشرة بسرة حمراء، وقال: هذا الجهد من أخيكم والله','58','0','0');
INSERT INTO [t11030] VALUES (59,'ثلاث ليس فيهن انتظار: الجنازة إذا وجدت من يحملها، والأيم إذا أصابت لها كفؤا، والضيف إذا','59','0','0');
INSERT INTO [t11030] VALUES (60,'إنما تقاطع الناس بالتكلف','60','0','0');
INSERT INTO [t11030] VALUES (61,'إذا أتاك ضيف فلا تنتظر به ما ليس عندك، وتمنعه ما عندك، قدم إليه ما حضر، وانتظر به ما بعد','61','0','0');
INSERT INTO [t11030] VALUES (62,'ليس شيء أضر بالضيف من أن يكون رب البيت شبعان','62','0','0');
INSERT INTO [t11030] VALUES (63,'أخرج السلة من تحت السرير، فيخرجها فإذا فيها رطب، فيقول: إنما ادخرته','63','0','0');
INSERT INTO [t11030] VALUES (64,'تناول السلة من تحت السرير، فأتناولها وفيها خبيص، فيقول: إني لست آكله، ولكني أصنعه','64','0','0');
INSERT INTO [t11030] VALUES (65,'سأطعمكم شيئا لا أراه في بيوتكم، فجاء بشهدة فكان يقطع بالسكين ويلقمنا','65','0','0');
INSERT INTO [t11030] VALUES (66,'إن زكاة الرجل في داره أن يجعل فيها بيتا للضيافة','66','0','0');
INSERT INTO [t11030] VALUES (67,'هكذا اصنعوا بالقراء','67','0','0');
