INSERT INTO [t11192] VALUES (151,'س١٣٢: بين معاني ما يلي من الكلمات: الله أكبر، أشهد أن لا إله إلا الله، أشهد أن محمدا رسول الله، حي على الصلاة، حي على الفلاح، الصلاة خير من النوم، الشفع، الوتر، الترسل، الحدر.','66','144','0');
INSERT INTO [t11192] VALUES (152,'س١٣٣: ما الدليل على أفضلية الأذان من الكتاب والسنة؟','67','144','0');
INSERT INTO [t11192] VALUES (153,'س١٣٤: بين حكم الأذان في حق المسافر، واذكر ما تستحضره من خلاف.','67','144','0');
INSERT INTO [t11192] VALUES (154,'س١٣٥: بين حكم الأذان قبل دخول الوقت، وجلوس المؤذن بعد الأذان. واذكر الخلاف.','68','144','0');
INSERT INTO [t11192] VALUES (155,'س١٣٦: ما حكم رفع الصوت بالأذان؟ وما دليل الحكم؟','69','144','0');
INSERT INTO [t11192] VALUES (156,'س١٣٧: بين حكم ما يلي: التثويب في أذان الفجر بعد الحيعلة، الإسراع في الإقامة، جعل المؤذن أصبعيه في أذنيه، التفاته في الحيعلة يمينا وشمالا غير مستدير وكونه على علو، وكونه متطهرا قائما مترسلا، وكونه أول الوقت, وكون المؤذن عالما بالوقت، بصيرا حسن الصوت.','70','144','0');
INSERT INTO [t11192] VALUES (157,'س١٣٨:من الأولى أن يتولى الأذان والإقامة؟ واذكر الدليل على ما تقول.','71','144','0');
INSERT INTO [t11192] VALUES (158,'س١٣٩: ما حكم أخذ الأجرة على الأذان والإقامة؟ وما دليل الحكم؟','72','144','0');
INSERT INTO [t11192] VALUES (159,'س١٤٠: بين من المقدم عند تشاح المؤذنين؟ واذكر الدليل على ما تقول.','72','144','0');
INSERT INTO [t11192] VALUES (160,'س١٤١: إذا جمع أو قضى فوائت، فما الحكم؟ وكم يؤذن؟ وكم يقيم؟ وضح ذلك.','73','144','0');
INSERT INTO [t11192] VALUES (161,'س١٤٢: ما المسنون قوله عند سماع الأذان؟ وضحه مع ذكر الدليل.','73','144','0');
