INSERT INTO [t11192] VALUES (185,'س١٥٨: بين وقت الاختيار ووقت الكراهة أو الضرورة.','83','183','0');
INSERT INTO [t11192] VALUES (186,'س١٥٩: متى يؤمر الصبي بالصلاة؟ ومتى يضرب على تركه؟ وهل الثواب له؟','84','183','0');
