INSERT INTO [t11192] VALUES (231,'س٢٠١: ما المسنون قوله إذا دخل المسجد وإذا خرج منه؟','107','218','0');
INSERT INTO [t11192] VALUES (232,'س٢٠٢: ما المكروه فعله في حق من خرج إلى الصلاة أو جلس ينتظر الصلاة؟','108','218','0');
INSERT INTO [t11192] VALUES (233,'س٢٠٣: بين حكم الكلام في أمر الدنيا في المسجد، واذكر الدليل على ما تقول؟','108','218','0');
INSERT INTO [t11192] VALUES (234,'س٢٠٤: ما الذي ينبغي أن يشتغل فيه من أقام في المسجد؟','108','218','0');
INSERT INTO [t11192] VALUES (235,'س٢٠٥: بين حكم تحية المسجد لمن دخله، واذكر دليل الحكم؟','109','218','0');
INSERT INTO [t11192] VALUES (236,'باب صفة الصلاة','109','162','0');
INSERT INTO [t11192] VALUES (237,'س٢٠٦: اذكر صفة الصلاة بوضوح تام.','109','236','0');
INSERT INTO [t11192] VALUES (238,'أركان الصلاة','113','162','0');
INSERT INTO [t11192] VALUES (239,'س٢٠٧: ما هي أركان الصلاة؟','113','238','0');
INSERT INTO [t11192] VALUES (240,'س٢٠٨: ما الدليل على أن القيام في صلاة الفرض ركن من أركان الصلاة؟','113','238','0');
INSERT INTO [t11192] VALUES (241,'س٢٠٩: ما الدليل على أن تكبيرة الإحرام ركن من أركان الصلاة؟','113','238','0');
INSERT INTO [t11192] VALUES (242,'س٢١٠: ما الدليل على أن قراءة الفاتحة ركن من أركان الصلاة في كل ركعة؟ وماذا يعمل من لا يحسنها ولا شيئا منها ولا من غيرها؟','114','238','0');
INSERT INTO [t11192] VALUES (243,'س٢١١: ما الدليل على أن الركوع ركن إلا الركوع بعد ركوع أول في صلاة كسوف فسنة؟','115','238','0');
INSERT INTO [t11192] VALUES (244,'س٢١٢: ما الدليل على أن الرفع من الركوع ركن من أركان الصلاة؟ وكذلك الاعتدال قائما، واذكر أدلتهما بوضوح.','115','238','0');
INSERT INTO [t11192] VALUES (245,'س٢١٣: ما الدليل على أن السجود على الأعضاء السبعة ركن من أركان الصلاة؟ وأن الرفع منه ركن؟ وأن الجلسة بين السجدتين ركن؟','115','238','0');
INSERT INTO [t11192] VALUES (246,'س٢١٤: ما هي الطمأنينة؟ وما الدليل على أنها ركن من أركان الصلاة؟','116','238','0');
INSERT INTO [t11192] VALUES (247,'س٢١٥: ما الدليل على أن التشهد الأخير ركن من أركان الصلاة؟','117','238','0');
INSERT INTO [t11192] VALUES (248,'س٢١٦: ما الدليل على أن الجلوس للتشهد الأخير ركن من أركان الصلاة؟ وما الدليل على أن الصلاة على النبي صلى الله عليه وسلم في الصلاة ركن من أركانها؟','118','238','0');
INSERT INTO [t11192] VALUES (249,'س٢١٧: ما الدليل على أن الترتيب بين أركان الصلاة ركن من أركانها؟','118','238','0');
INSERT INTO [t11192] VALUES (250,'س٢١٨: ما الدليل على أن التسليمتين ركن من أركان الصلاة؟','118','238','0');
INSERT INTO [t11192] VALUES (251,'س٢١٩: كم واجبات الصلاة؟ وما هي؟ وما الفرق بينها وبين الأركان؟','119','238','0');
INSERT INTO [t11192] VALUES (252,'س٢٢٠: ما الدليل على أن التكبير غير تكبيرة الإحرام واجب من واجبات الصلاة؟','119','238','0');
INSERT INTO [t11192] VALUES (253,'س٢٢١: ما الدليل على أن قول سبحان ربي العظيم في الركوع، وقول سبحان ربي الأعلى في السجود واجب من واجبات الصلاة؟','120','238','0');
INSERT INTO [t11192] VALUES (254,'س٢٢٢: ما الدليل على أن قول سمع الله لمن حمده للإمام والمنفرد واجب من واجبات الصلاة؟ وأن قول ربنا ولك الحمد للإمام والمنفرد والمأموم واجب من واجبات الصلاة؟ وهل لهم أن يزيدوا على ذلك؟','120','238','0');
INSERT INTO [t11192] VALUES (255,'س٢٢٣: ما الدليل على أن قول رب اغفر لي بين السجدتين واجب من واجبات الصلاة؟ وهل له أن يزيد على ذلك؟ وضح ذلك.','121','238','0');
INSERT INTO [t11192] VALUES (256,'س٢٢٤: ما الدليل على أن التشهد الأول واجب من واجبات الصلاة؟ وأن الجلوس له واجب أيضا من واجباتها؟','121','238','0');
INSERT INTO [t11192] VALUES (257,'س٢٢٥: اذكر ما تستحضره من سنن الأقوال مقرونا بالأدلة.','122','238','0');
INSERT INTO [t11192] VALUES (258,'سنن الأفعال','125','162','0');
INSERT INTO [t11192] VALUES (259,'س٢٢٦: اذكر ما تستحضره من سنن الأفعال مقرونا بالدليل.','125','258','0');
INSERT INTO [t11192] VALUES (260,'ما يكره في الصلاة','129','162','0');
INSERT INTO [t11192] VALUES (261,'س٢٢٧: ما الذي يكره في الصلاة؟ وما دليله؟ اذكرهما بوضوح.','129','260','0');
INSERT INTO [t11192] VALUES (262,'س٢٢٨: ما الدليل على كراهة رفع البصر إلى السماء في الصلاة؟ وكراهة الالتفات فيها؟','129','260','0');
INSERT INTO [t11192] VALUES (263,'س٢٢٩: ما الدليل على كراهة ابتداء الصلاة وهو تائق إلى طعام ونحوه؟','129','260','0');
INSERT INTO [t11192] VALUES (264,'س٢٣٠: ما الدليل على كراهة افتراش الذراعين حال السجود وكراهة التخصر؟','130','260','0');
INSERT INTO [t11192] VALUES (265,'س٢٣١: اذكر الدليل على كراهة العبث، والتروح، والتمطي، واستقبال الصورة وما يلهي، ولماذا خصت الجبهة بالكراهة دون غيرها؟','130','260','0');
INSERT INTO [t11192] VALUES (266,'س٢٣٢: ما الدليل على كراهة مس الحصى، أو مسحه، أو تسوية التراب؟ والإنسان في الصلاة أو مسح أثر السجود وهو يصلي؟','130','260','0');
INSERT INTO [t11192] VALUES (267,'س٢٣٣: ما الدليل على كراهة فرقعة الأصابع، وتشبيكها، ومس اللحية، وكف ثوبه، والاستناد بلا حاجة، وعقص الشعر وكفه وفرقعة الأصابع؟','131','260','0');
INSERT INTO [t11192] VALUES (268,'س٢٣٤: ما هو الإقعاء؟ وما الدليل على كراهته؟ وما الدليل على كراهة نقر الصلاة واستقبال نار؟','132','260','0');
INSERT INTO [t11192] VALUES (269,'من ما يبطل الصلاة','132','162','0');
INSERT INTO [t11192] VALUES (270,'س٢٣٥: اذكر ما تستحضره من ما يبطل الصلاة؟ وما الأدلة الدالة على ما تذكر؟','132','269','0');
INSERT INTO [t11192] VALUES (271,'س٢٣٦: بين أحكام ما يلي: جمع سورتين في ركعة، تكرار سورة في ركعتين، ملازمة سورة بعينها، واذكر دليل كل حكم.','133','269','0');
INSERT INTO [t11192] VALUES (272,'س٢٣٧: بين حكم قراءة أواخر السور وأوساطها، واذكر الدليل على ما تقول.','134','269','0');
INSERT INTO [t11192] VALUES (273,'س٢٣٨: بين أحكام ما يلي: اتخاذ سترة للمصلي، مقدارها، قربه منها، رد المار بين يدي المصلي، واذكر الدليل على ما تذكر موضحا.','134','269','0');
INSERT INTO [t11192] VALUES (274,'س٢٣٩: إذا لم يجد شاخصا وتعذر عليه غرر عصا ونحوها، فما الحكم؟','135','269','0');
INSERT INTO [t11192] VALUES (275,'س٢٤٠: ما الذي يقطع الصلاة ومتى يقطعها؟ وضح ذلك مع ذكر الدليل.','135','269','0');
INSERT INTO [t11192] VALUES (276,'س٢٤١: هل سترة الإمام سترة لمن خلفه؟ وضح ذلك مع ذكر الدليل والتعليل.','136','269','0');
INSERT INTO [t11192] VALUES (277,'س٢٤٢: بين أحكام ما يلي: اتخاذ السترة للمأموم، إذا مر بين يدي المأمومين شيء مما يقطع الصلاة، أو بين يدي الإمام، المرور بين يدي المصلي؟','136','269','0');
INSERT INTO [t11192] VALUES (278,'س٢٤٣: اذكر الصور التي لا يرد فيها المصلي من مر بين يديه، وحكم صلاة من صلى في موضع يحتاج فيه إلى المرور، وهل تنقص بذلك صلاته؟','137','269','0');
INSERT INTO [t11192] VALUES (279,'س٢٤٤: بين حكم ما يلي: إمام ارتج عليه أو غلط، من رأى بقربه حية أو عقربا وهو يصلي، من انحل كور عمامته أو إزاره، وهو يصلي، وسق ما تستحضره من الأدلة على ما تذكر.','137','269','0');
INSERT INTO [t11192] VALUES (280,'س٢٤٥: ما حكم رد المعصوم عن بئر ونحوه وإنقاذ الغريق والحريق، ونحوهما في حق المصلي؟ وماذا يعمل من نابه شيء مثل سهو إمامه أو نحوه؟','138','269','0');
INSERT INTO [t11192] VALUES (281,'س٢٤٦:هل للمصلي أن يدعو إذا مر بآية رحمة، ويتعوذ إذا مر بآية وعيد؟','138','269','0');
INSERT INTO [t11192] VALUES (282,'س٢٤٧: ماذا يعمل من غلبه تثاؤب، أو بدره بصاق أو مخاط أو نخامة؟','139','269','0');
INSERT INTO [t11192] VALUES (283,'باب سجود السهو','140','162','0');
INSERT INTO [t11192] VALUES (284,'س٢٤٨: ما حكم سجود السهو؟ وما أسبابه؟ وما الأصل في مشروعيته؟','140','283','0');
INSERT INTO [t11192] VALUES (285,'س٢٤٩: بين متى يسن؟ ومتى يباح؟ ومتى يجب؟ واذكر ما تستحضره من الأدلة.','140','283','0');
INSERT INTO [t11192] VALUES (286,'س٢٥٠: بين حكم صلاة مصل زاد ركعة ثم ذكر، وهل يحتسب المسبوق بالركعة الزائدة؟ وهل يدخل مع الإمام القائم لركعة زائدة؟','141','283','0');
INSERT INTO [t11192] VALUES (287,'س٢٥١: ماذا يلزم المأمومين إذا سهى على إمامهم؟ وماذا يلزمه؟ وما هو الدليل على ما تذكر؟ وضح ذلك وعلل لما يحتاج إلى تعليل.','141','283','0');
INSERT INTO [t11192] VALUES (288,'س٢٥٢: إذا ذكر قريبا عرفا من سلم قبل إتمامها سهوا أنه لم يتمها، فما الحكم؟','142','283','0');
INSERT INTO [t11192] VALUES (289,'س٢٥٣: إذا انحرف عن القبلة من سلم قبل إتمامها، أو خرج من المسجد أو لم يذكر حتى قام من مصلاه، فما الحكم؟ وما دليل الحكم؟ اذكرهما بوضوح.','142','283','0');
INSERT INTO [t11192] VALUES (290,'س٢٥٤: ما حكم سجود السهو في حق من ترك واجبا، أو شك في زيادة وقت فعلها؟','143','283','0');
INSERT INTO [t11192] VALUES (291,'س٢٥٥: هل تبطل الصلاة بتعمد ترك سجود السهو؟ وما الدليل على ما تقول؟','143','283','0');
INSERT INTO [t11192] VALUES (292,'س٢٥٦: هل على مأموم سها دون إمامه سجود سهو؟ وإذا سها إمامه فهل يتابعه؟','144','283','0');
INSERT INTO [t11192] VALUES (293,'س٢٥٧: تكلم عن حكم من نسي ركنا من أركان الصلاة؟','144','283','0');
INSERT INTO [t11192] VALUES (294,'س٢٥٨: تكلم عمن نسي التشهد الأول وحده أن نسيه مع جلوس له؟ واذكر الدليل على ما تذكر.','144','283','0');
INSERT INTO [t11192] VALUES (295,'س٢٥٩: تكلم عما يلي: مصل شك في عدد الركعات، مأموم شك هل أدرك الركوع مع الإمام أم لا؟ مأموم شك هل دخل مع الإمام في الأولى أم الثانية؟ آخر شك هل دخل معه في الركعة الثانية أو الثالثة؟','145','283','0');
INSERT INTO [t11192] VALUES (296,'س٢٦٠: ما معنى تحري الصواب فيه؟ وإذا شك في ترك ركن أو ترك واجب فما الحكم؟ وإذا تكرر السهو في الصلاة فكم يسجد؟ وجاوب عن الإيرادات على ما تقول.','146','283','0');
INSERT INTO [t11192] VALUES (297,'س٢٦١: إذا اجتمع سهوان: أحدهما قبل السلام والآخر بعده، فأيهما يغلب؟ وما الذي يقال في سجود السهو بين السجدتين؟ وضح ذلك.','146','283','0');
INSERT INTO [t11192] VALUES (298,'س٢٦٢: ما محل سجود السهو؟ وضح ذلك مع ذكر الدليل.','146','283','0');
INSERT INTO [t11192] VALUES (299,'باب صلاة التطوع','147','162','0');
INSERT INTO [t11192] VALUES (300,'س٢٦٣: ما هو التطوع لغة واصطلاحا؟ وما أفضل ما يتطوع به؟','147','299','0');
INSERT INTO [t11192] VALUES (301,'س٢٦٤: ما أفضل صلاة تطوع؟ وما آكدها؟ وما أقل الوتر؟ وما أكثر؟','148','299','0');
INSERT INTO [t11192] VALUES (302,'س٢٦٥: متى وقت الوتر؟ وما حكم القنوت فيه؟ وما دعاء القنوت فيه؟ وما هو الدليل؟','148','299','0');
INSERT INTO [t11192] VALUES (303,'س٢٦٦: ماذا يعمل بعد ما يخلص من دعاء القنوت؟ واذكر الدليل لما تقول.','149','299','0');
INSERT INTO [t11192] VALUES (304,'س٢٦٧: ما حكم القنوت لغير الوتر؟','150','299','0');
INSERT INTO [t11192] VALUES (305,'س٢٦٨: كم عدد الرواتب؟ وما هو الدليل على عددها؟','150','299','0');
INSERT INTO [t11192] VALUES (306,'س٢٦٩: ما آكد الرواتب؟ وما الدليل على آكديتها؟ وما الذي ينبغي أن يقرأ فيها؟','151','299','0');
INSERT INTO [t11192] VALUES (307,'س٢٧٠: بين حكم قضاء الرواتب مقرونا بالدليل، وحكم قضاء الوتر كذلك؟','151','299','0');
INSERT INTO [t11192] VALUES (308,'س٢٧١: ما حكم الفصل بين الفرض والنفل؟ وحدد وقت الراتبة وأيهما أفضل؟ فعل النافلة في البيت أو في المسجد؟ واذكر الدليل على ما تقول؟','152','299','0');
INSERT INTO [t11192] VALUES (309,'س٢٧٢: ما الدليل على سنية صلاة الليل؟ ولما كانت صلاة الليل أفضل من صلاة النهار فأيهما أفضل: الصلاة في أول الليل أو آخره؟ وضح ذلك.','152','299','0');
INSERT INTO [t11192] VALUES (310,'س٢٧٣: متى يكون التهجد؟ وما الذي يسن للمتهجد أن يفتتح تهجده به؟ وما حكم النية في حق مريد التهجد؟ وضح ذلك مقرونا بالدليل.','152','299','0');
INSERT INTO [t11192] VALUES (311,'س٢٧٤: ما حكم صلاة الضحى؟ وما أقلها وما أكثرها؟ ومتى وقتها؟ واذكر دليل كل مما تقدم.','153','299','0');
INSERT INTO [t11192] VALUES (312,'س٢٧٥: تكلم عن أحكام ما يلي: صلاة الاستخارة، صلاة الحاجة، الصلاة عقب الوضوء، واذكر ما تستحضره من الأدلة.','154','299','0');
INSERT INTO [t11192] VALUES (313,'سجود التلاوة','155','162','0');
INSERT INTO [t11192] VALUES (314,'س٢٧٦: ما حكم سجود التلاوة؟ ولمن يسن؟ وهل يتابع المأموم الإمام فيه؟','155','313','0');
INSERT INTO [t11192] VALUES (315,'س٢٧٧: ما الذي يقال في سجود التلاوة؟ بينه مع ذكر الدليل.','156','313','0');
INSERT INTO [t11192] VALUES (316,'س٢٧٨: ما حكم سجود الشكر؟ وضح وقته ودليله، ومثل لما يحتاج إلى تمثيل.','156','313','0');
INSERT INTO [t11192] VALUES (317,'أوقات النهي','157','162','0');
INSERT INTO [t11192] VALUES (318,'س٢٧٩: ما هي أوقات النهي؟ وما أدلتها؟','157','317','0');
INSERT INTO [t11192] VALUES (319,'س٢٨٠: ما الذي يجوز فعله في أوقات النهي؟ وما هي أدلته؟','158','317','0');
INSERT INTO [t11192] VALUES (320,'باب صلاة الجماعة وأحكامها','161','162','0');
INSERT INTO [t11192] VALUES (321,'س٢٨١: ما حكم صلاة الجماعة؟ واذكر الدليل على ما تقول.','161','320','0');
INSERT INTO [t11192] VALUES (322,'س٢٨٢: ما هي الحكمة في مشروعية صلاة الجماعة؟','161','320','0');
INSERT INTO [t11192] VALUES (323,'س٢٨٣: بين ما أقل الجماعة؟ واذكر الدليل على ما تذكر، وماذا يعمل مع تاركها؟','162','320','0');
INSERT INTO [t11192] VALUES (324,'س٢٨٤: أين تفعل صلاة الجماعة؟ وكم تفضل الصلاة في جماعة على صلاة المنفرد؟ وما هو الدليل على ما تذكر؟','162','320','0');
INSERT INTO [t11192] VALUES (325,'س٢٨٥: بين هل ينقص أجر من ترك الجماعة لعذر؟ واذكر الدليل على ذلك.','163','320','0');
INSERT INTO [t11192] VALUES (326,'س٢٨٦: ما هو الثغر؟ وما هو المستحب لأهله؟ التعدد أم الاجتماع في مسجد واحد؟','163','320','0');
INSERT INTO [t11192] VALUES (327,'س٢٨٧: ما الأفضل لغير أهل الثغر؟ اذكر ذلك مرتبا مع ما تستحضره من دليل أو تعليل.','163','320','0');
INSERT INTO [t11192] VALUES (328,'س٢٨٨: بين حكم الإمامة في مسجد له إمام راتب، وإذا تأخر فماذا يعمل؟','164','320','0');
INSERT INTO [t11192] VALUES (329,'س٢٨٩: بين ما تستحضره من دليل على ما تقدم؟','165','320','0');
INSERT INTO [t11192] VALUES (330,'س٢٩٠:ما حكم الشروع في النفل بعد إقامة الصلاة؟ وماذا يعمل من شرع','165','320','0');
INSERT INTO [t11192] VALUES (331,'س٢٩١: ما الذي تدرك به الجماعة؟ وما الدليل على ذلك؟','166','320','0');
INSERT INTO [t11192] VALUES (332,'س٢٩٢: ما الذي تدرك به الركعة؟ وهل تجزى تكبيرة الإحرام عن تكبيرة الركوع؟','166','320','0');
INSERT INTO [t11192] VALUES (333,'س٢٩٣: ما الأولى لمن أدرك الإمام بعد الركوع؟ وماذا يلزم من قام قبل التسليمة الثانية؟ وضح ذلك مع ذكر الدليل.','167','320','0');
INSERT INTO [t11192] VALUES (334,'س٢٩٤: إذا أدرك في سجود السهو بعد السلام، فهل يدخل معه؟ وإذا فاتته الجماعة، فما المسنون في حقه؟ وضح ذلك.','167','320','0');
INSERT INTO [t11192] VALUES (335,'س٢٩٥: هل تجب القراءة على المأموم إذا سمع قراءة الإمام؟ اذكر الدليل على ما تقول.','167','320','0');
INSERT INTO [t11192] VALUES (336,'س٢٩٦: متى تسن القراءة للمأموم؟ وما محل سكتات الإمام؟ وما دليلها؟','168','320','0');
INSERT INTO [t11192] VALUES (337,'س٢٩٧: هل يستفتح المأموم فيما يجهر فيه الإمام؟','168','320','0');
INSERT INTO [t11192] VALUES (338,'س٢٩٨: متى يشرع المأموم في أفعال الصلاة؟ وما حكم موافقة الإمام؟ وما حكم مسابقته؟ وضح ذلك مع ذكر الدليل.','169','320','0');
INSERT INTO [t11192] VALUES (339,'س٢٩٩: بين حكم ما يلي: مأموم ركع أو سجد قبل إمامه، مأموم ركع ورفع قبل إمامه، مأموم ركع ورفع قبل ركوع ثم سجد قبل رفعه، وضح ذلك توضيحا شافيا.','169','320','0');
INSERT INTO [t11192] VALUES (340,'س٣٠٠: بين حكم تخلف المأموم عن الإمام بركن أو بركنين؟','170','320','0');
INSERT INTO [t11192] VALUES (341,'س٣٠١: اذكر شيئا مما يسن في حق الإمام مقرونا بالدليل؟','171','320','0');
INSERT INTO [t11192] VALUES (342,'من الأحكام التي تتعلق بالمرأة','172','162','0');
INSERT INTO [t11192] VALUES (343,'س٣٠٢ [كذا في الأصل] : إذا استأذنت امرأة إلى المسجد ليلا أو نهارا أكره لزوج وسيد منعها إذا خرجت تفلة غير مزينة ولا مطيبة، لما ورد عن أبي هريرة أن النبي صلى الله عليه وسلم قال: «لا تمنعوا إماء الله مساجد الله وليخرجن تفلات» رواه أحمد وأبو داود، وعن ابن عمر عن النبي صلى الله عليه وسلم قال: «إذا استأذنتكم نساؤكم بالليل إلى المسجد فأذنوا لهن» رواه الجماعة إلا ابن ماجه، وفي لفظ: «لا تمنعوا النساء أن يخرجن إلى المساجد وبيوتهن خير لهن» رواه أحمد وأبو داود. وله منعها من الخروج إلى المسجد إن خشي بخروجها إليه فتنة أو ضررا، وكذا الأب مع ابنته إذا استأذنت في الخروج إلى المسجد كره له منعها إلا أن يخشى فتنة أو ضررا، وله منعها من الانفراد عنه؛ لأنه لا يؤمن من دخول من يفسدها ويلحق العار بها وبأهلها. قال أحمد: والزوج أملك من الأب؛ فإن لم يكن أب فأولياؤها المحارم لقيامهم مقامه.','172','342','0');
INSERT INTO [t11192] VALUES (344,'س٣٠٣: ما الدليل على أن الخروج للنساء إلى المساجد إنما يجوز إذا لم يصحب ذلك ما فيه فتنة؟','172','342','0');
INSERT INTO [t11192] VALUES (345,'س٣٠٤: بين أيهما أفضل للمرأة: الصلاة في المسجد أم في بيتها؟ واذكر الدلل على ما تقول والحكمة في ذلك.','173','342','0');
