INSERT INTO [t11281] VALUES (1,'مقدمة','1','0','0');
INSERT INTO [t11281] VALUES (2,'المدخل','14','0','0');
INSERT INTO [t11281] VALUES (3,'الفصل الأول: الصمانات العامة لحقوق المرأة الزوجية','29','0','0');
INSERT INTO [t11281] VALUES (4,'المبحث الأول: (الضمان الأول) : أن حقوق المرأة الزوجية ثابتة بأحكام شرعية توعد الله من اعتدى عليها، أو قصر في أدائها.','29','3','0');
INSERT INTO [t11281] VALUES (5,'المبحث الثاني: (الضمان الثاني) : أن من الحقوق الزوجية، ما لا يمكن التنازل عنه شرعا.','30','3','0');
INSERT INTO [t11281] VALUES (6,'المبحث الثالث: (الضمان الثالث) : أن الشريعة أبطلت التنازل عن الحقوق الزوجية إذا كان مشتملا على الإكراه أو الغرر.','32','3','0');
INSERT INTO [t11281] VALUES (7,'المبحث الرابع: (الضمان الرابع) : أن الشريعة أبطلت التنازل عن الحقوق الزوجية مسبقا','36','3','0');
INSERT INTO [t11281] VALUES (8,'المبحث الخامس: (الضمان الخامس) : أن الشريعة حرمت الأنكحة التي فيها ضرر مادي أو معنوي للمرأة.','40','3','0');
INSERT INTO [t11281] VALUES (9,'المبحث السادس: (الضمان السادس) : أن الشريعة ألغت تصرفات الزوج الضارة بالمرأة مما كان سائدا في الجاهلية، وعاقبت عليها، كالظهار، والإيلاء.','56','3','0');
INSERT INTO [t11281] VALUES (10,'الفصل الثاني: الضمانات الخاصة لحقوق زوجية معينة','69','0','0');
INSERT INTO [t11281] VALUES (11,'المبحث الأول: (الضمان السابع) : أن الشريعة منعت الولي من عضل موليته إن أرادت أن تنكح','69','10','0');
INSERT INTO [t11281] VALUES (12,'المبحث الثاني: (الضمان الثامن) : أن الولاية تتحول من الولي الأقرب إلى الولي الأبعد','75','10','0');
INSERT INTO [t11281] VALUES (13,'المبحث الثالث: (الضمان التاسع) : أن الشريعة جعلت لها الحق في أن تشترط من الحقوق المادية والمعنوية ما فيه مصلحتها','83','10','0');
INSERT INTO [t11281] VALUES (14,'المبحث الرابع: (الضمان العاشر) : أن المرأة لو زوجت من غير رضاها كان لها حق الفسخ','85','10','0');
INSERT INTO [t11281] VALUES (15,'المبحث الخامس: (الضمان الحادي عشر) : أن الشريعة الإسلامية ضمنت للمرأة مهرها، بأوجه عدة','88','10','0');
INSERT INTO [t11281] VALUES (16,'المبحث السادس: (الضمان الثاني عشر) : أن الشريعة جعلت أمر الطلاق بيد الرجل.','96','10','0');
INSERT INTO [t11281] VALUES (17,'المبحث السابع: (الضمان الثالث عشر) : أن الشريعة جعلت أمر الطلاق بيدها بالاشتراط.','100','10','0');
INSERT INTO [t11281] VALUES (18,'المبحث الثامن: (الضمان الرابع عشر) : أن الشريعة جعلت لها الخيار، إذا لحقها ضرر مادي أو معنوي من قبل الزوج','105','10','0');
INSERT INTO [t11281] VALUES (19,'المبحث التاسع: (الضمان الخامس عشر) : أن الشريعة أباحت للمرأة طلب الخلع إن لم تطق العيش مع زوجها','136','10','0');
INSERT INTO [t11281] VALUES (20,'المبحث العاشر: (الضمان السادس عشر) : أن الشريعة أمرت ببعث الحكمين للإصلاح بين الزوجين','144','10','0');
INSERT INTO [t11281] VALUES (21,'المبحث الحادي عشر: (الضمان السابع عشر) : أن الشريعة فرضت لها الميراث ولو طلقت طلاقا بائنا، إذا اتهم زوجها بقصد حرمانها من الميراث.','149','10','0');
INSERT INTO [t11281] VALUES (22,'مصادر ومراجع','160','0','0');
