INSERT INTO [t11385] VALUES (200,'وبه فيه (الصيام ٥٧: ٢، وأيضا في الشمائل ٤٤: ٢) : سئل عن صوم النبي صلى الله عليه وسلم، فقال: كان يصوم من الشهر. . . . الحديث، وقال: حسن صحيح.','783','93','0');
INSERT INTO [t11385] VALUES (201,'وبه في الزهد (٥٠: ١) : قال: جاء رجل إلى النبي صلى الله عليه وسلم فقال: متى قيام الساعة؟ . . . . الحديث، وقال: حسن صحيح.','784','93','0');
INSERT INTO [t11385] VALUES (202,'وبه في الدعوات (٧١) : أن النبي صلى الله عليه وسلم كان يدعو اللهم! إني أعوذ بك من الكسل. . . . الحديث، وقال: صحيح.','785','93','0');
INSERT INTO [t11385] VALUES (203,'وبه في الجهاد (١٧: ٤) لغدوة في سبيل الله أو روحة خير من الدنيا وما فيها. . . . الحديث، وقال: صحيح.','786','93','0');
INSERT INTO [t11385] VALUES (204,'وبه فيه (الجهاد ١٣: ٥) ما من عبد يموت له عند الله خير يحب أن يرجع إلى الدنيا. . . . الحديث، وقال: صحيح.','787','93','0');
INSERT INTO [t11385] VALUES (205,'وبه في القدر (٨: ٣) إذا أراد الله بعد خيرا استعمله. . . . الحديث، وقال صحيح.','788','93','0');
INSERT INTO [t11385] VALUES (206,'وبه في المناقب (٥٣: ٢) دخلت الجنة فإذا أنا بقصر من ذهب. . . . الحديث، وقال: صحيح ١','789','93','0');
INSERT INTO [t11385] VALUES (207,'وبه في الصلاة (٤٨: ٢) أن رجلا أتى النبي صلى الله عليه وسلم فسأله عن وقت صلاة الغداة. . . . الحديث.','791','93','0');
INSERT INTO [t11385] VALUES (208,'وبه فيه (الصلاة ٦٥٢: ١) : كان لأهل الجاهلية يومان من كل سنة يلعبون فيهما، فلما قدم النبي صلى الله عليه وسلم المدينة. . . . الحديث.','792','93','0');
INSERT INTO [t11385] VALUES (209,'وبه فيه (الصلاة ٢٠٠) : آخر صلاة صلاها رسول الله صلى الله عليه وسلم مع القوم صلاها في ثوب. . . . الحديث.','793','93','0');
INSERT INTO [t11385] VALUES (210,'وبه فيه (الصلاة ٢٢٠) أقبل علينا النبي صلى الله عليه وسلم بوجهه حين قام إلى الصلاة قبل أن يكبر، ثم قال: أقيموا صفوفكم. . . . الحديث.','794','93','0');
