INSERT INTO [t11385] VALUES (1351,'حديث غلظ القلوب والجفاء في أهل المشرق، والإيمان في أهل الحجاز. م في الإيمان (٢٢: ١٣) عن إسحاق بن إبراهيم، عن عبد الله بن الحارث المخزومي، عنه به.','3840','893','0');
INSERT INTO [t11385] VALUES (1352,'حديث من استجمر فليوتر. م في الطهارة (٨: ٦) عن إسحاق بن إبراهيم ومحمد بن رافع، كلاهما عن عبد الرزاق، عنه به.','3843','893','0');
