INSERT INTO [t11385] VALUES (2879,'وبه في الشرب (١: ١) أتى النبي صلى الله عليه وسلم بقدح فشرب منه، وعن يمينه غلام أصغر القوم، والأشياخ عن يساره. . . الحديث.','6811','2848','0');
INSERT INTO [t11385] VALUES (2880,'وبه في الهبة (٣: ١) : أن النبي صلى الله عليه وسلم أرسل إلى امرأة من المهاجرين١ - وكان لها غلام نجار - قال: مري غلامك فليعمل لنا أعواد المنبر. . . الحديث.','6812','2848','0');
INSERT INTO [t11385] VALUES (2881,'وبه في الاعتصام (١٧: ١٣) : أنه كان بين جدار المسجد مما يلي القبلة وبين المنبر ممر الشاة.','6813','2848','0');
