INSERT INTO [t11385] VALUES (4325,'وبه فيه (الصلاة ١٢٣: ٥) : صليت مع رسول الله صلى الله عليه وسلم قبل الظهر سجدتين، وبعدها سجدتين، وبعد المغرب وبعد العشاء وبعد الجمعة.','11169','3840','0');
