INSERT INTO [t11385] VALUES (4327,'وبه فيه (الصلاة ١١٠: ٣) : صلى رسول الله صلى الله عليه وسلم بمنى ركعتين، وأبو بكر بعده، وعمر بعد أبي بكر، وعثمان صدرا من خلافته. . . الحديث.','11171','3840','0');
INSERT INTO [t11385] VALUES (4328,'وبه في الزكاة (٥: ٢) : فرض رسول الله صلى الله عليه وسلم زكاة الفطر صاعا من تمر وصاعا من شعير، على كل حر أو عبد صغير أو كبير.','11172','3840','0');
INSERT INTO [t11385] VALUES (4329,'وبه في الصوم (٢: ٢) : أن رسول الله صلى الله عليه وسلم ذكر رمضان، فضرب بيده فقال: الشهر هكذا وهكذا وهكذا. . . الحديث.','11173','3840','0');
INSERT INTO [t11385] VALUES (4330,'وبه فيه (الصوم ١٩: ٧) : أن أهل الجاهلية كانوا يصومون يوم عاشوراء، فلما افترض رمضان. . . الحديث.','11174','3840','0');
INSERT INTO [t11385] VALUES (4331,'وبه في الحج (٦٨: ٤) : دخل رسول الله صلى الله عليه وسلم البيت - ومعه أسامة وبلال وعثمان بن طلحة. . . الحديث. وقد ذكرناه في مسند بلال - (ح ٢٠٣٧) .','11175','3840','0');
INSERT INTO [t11385] VALUES (4332,'وبه فيه (الحج ٩٤: ٦) صلاة في مسجدي هذا أفضل من ألفي صلاة فيما سواه إلا المسجد الحرام.','11176','3840','0');
INSERT INTO [t11385] VALUES (4333,'وبه فيه (الحج ٩٧: ٢) : كان رسول الله صلى الله عليه وسلم يأتي مسجد قباء راكبا وماشيا.','11177','3840','0');
INSERT INTO [t11385] VALUES (4334,'وبه فيه (الحج ٧٦: ١) : كان رسول الله صلى الله عليه وسلم إذا قفل من الجيوش أو السرايا أو الحج أو العمرة، إذا أوفي على ثنية أو فدفد كبر ثلاثا، ثم قال: لا إله إلا الله وحده لا شريك له. . . الحديث - آيبون تائبون.','11178','3840','0');
INSERT INTO [t11385] VALUES (4335,'وبه في البيوع (٣١: ٢) : أن رسول الله صلى الله عليه وسلم أمر بقتل الكلاب فأرسل في أقطار المدينة أن تقتل.','11179','3840','0');
INSERT INTO [t11385] VALUES (4336,'وبه في العتق (؟) والنذور (والأيمان ١٦: ٢) إن العبد إذا نصح سيده وأحسن عبادة ربه فله أجره مرتين.','11180','3840','0');
INSERT INTO [t11385] VALUES (4337,'وبه في اللعان (١: ١٠) : لاعن رسول الله صلى الله عليه وسلم بين رجل من الأنصار وامرأته، وفرق بينهما.','11181','3840','0');
INSERT INTO [t11385] VALUES (4338,'وبه في المغازي (٧٨: ٢) : أن رسول الله صلى الله عليه وسلم سابق بالخيل التي قد أضمرت من الحفياء. . . الحديث.','11182','3840','0');
INSERT INTO [t11385] VALUES (4339,'وبه فيه (المغازي ٦: ١) يرفع لكل غادر لواء. . . الحديث.','11183','3840','0');
INSERT INTO [t11385] VALUES (4340,'وبه في الفرائض١ (لا، بل في الهبات ١: ٦) أن عمر حمل على فرس في سبيل الله فوجده يباع. . . الحديث.','11184','3840','0');
INSERT INTO [t11385] VALUES (4341,'وبه في الأطعمة (١٧: ٢) المؤمن يأكل في معي واحد، والكافر يأكل في سبعة أمعاء.','11185','3840','0');
INSERT INTO [t11385] VALUES (4342,'وبه في اللباس (١: ٢) : أن عمر رأى حلة سيراء عند باب المسجد، فقال: لو اشتريت هذه. . . . . . الحديث.','11186','3840','0');
INSERT INTO [t11385] VALUES (4343,'أأ وبه في الاستئذان (٢١: ٢) لا يقيم الرجل الرجل من مقعده ثم يجلس فيه، ولكن تفسحوا وتوسعوا.','11187','3840','0');
INSERT INTO [t11385] VALUES (4344,'وبه في الفتن (٢٠: ١) : أن رسول الله صلى الله عليه وسلم ذكر الدجال، فقال: إن الله ليس بأعور. . . الحديث.','11188','3840','0');
INSERT INTO [t11385] VALUES (4345,'وبه في التوبة (١٣: ٢٠) مثل المنافق كمثل الشاة العائرة بينا الغنمين، تعير إلى هذه مرة وإلى هذه مرة.','11189','3840','0');
INSERT INTO [t11385] VALUES (4346,'حماد بن زيد بن درهم البصري، عن عبيد الله بن عمر، عن نافع، عن ابن عمر','11196','3840','0');
INSERT INTO [t11385] VALUES (4347,'حماد بن سلمة بن دينار البصري، عن عبيد الله بن عمر، عن نافع، عن ابن عمر','11198','3840','0');
INSERT INTO [t11385] VALUES (4348,'حماد بن مسعدة البصري، عن عبيد الله بن عمر، عن نافع، عن ابن عمر','11201','3840','0');
INSERT INTO [t11385] VALUES (4349,'خالد بن الحارث الهجيمي البصري، عن عبيد الله بن عمر، عن نافع، عن ابن عمر','11203','3840','0');
INSERT INTO [t11385] VALUES (4350,'وبه في العتق (الكبرى ١٠: ١٠) من كان له شرك في عبد فأعتقه فقد عتق، فإن كان له مال قوم عليه. . . الحديث.','11212','3840','0');
INSERT INTO [t11385] VALUES (4351,'وبه في عشرة النساء (الكبرى ٣٣ - ألف: ٧) : أن عمر سأل رسول الله صلى الله عليه وسلم فقال: أيرقد أحدنا - وهو جنب -؟ قال: نعم، إذا توضأ.','11213','3840','0');
INSERT INTO [t11385] VALUES (4352,'زائدة بن قدامة الثقفي الكوفي، عن عبيد الله بن عمر، عن نافع، عن ابن عمر','11214','3840','0');
INSERT INTO [t11385] VALUES (4353,'زهير بن معاوية الجعفي الكوفي، عن عبيد الله بن عمر، عن نافع، عن ابن عمر','11218','3840','0');
INSERT INTO [t11385] VALUES (4354,'زيد بن أبي أنيسة الجزري، عن عبيد الله بن عمر، عن نافع، عن ابن عمر','11220','3840','0');
INSERT INTO [t11385] VALUES (4355,'سفيان بن حسين الواسطي، عن عبيد الله بن عمر، عن نافع، عن ابن عمر','11222','3840','0');
INSERT INTO [t11385] VALUES (4356,'سفيان بن سعيد الثوري، عن عبيد الله بن عمر، عن نافع، عن ابن عمر','11224','3840','0');
INSERT INTO [t11385] VALUES (4357,'سفيان بن عيينة الهلالي، عن عبيد الله بن عمر، عن نافع، عن ابن عمر','11232','3840','0');
INSERT INTO [t11385] VALUES (4358,'سليم بن أخضر البصري، عن عبيد الله بن عمر، عن نافع، عن ابن عمر','11237','3840','0');
INSERT INTO [t11385] VALUES (4359,'سليمان بن حيان أبو خالد الأحمر، عن عبيد الله بن عمر، عن نافع، عن ابن عمر','11240','3840','0');
INSERT INTO [t11385] VALUES (4360,'سيف بن عمر التميمي الكوفي، عن عبيد الله بن عمر، عن نافع، عن ابن عمر','11246','3840','0');
INSERT INTO [t11385] VALUES (4361,'شريك بن عبد الله النخعي القاضي، عن عبيد الله بن عمر، عن نافع، عن ابن عمر','11248','3840','0');
INSERT INTO [t11385] VALUES (4362,'شعبة بن الحجاج العتكي، عن عبيد الله بن عمر، عن نافع، عن ابن عمر','11251','3840','0');
INSERT INTO [t11385] VALUES (4363,'شعيب بن إسحاق الدمشقي، عن عبيد الله بن عمر، عن نافع، عن ابن عمر','11253','3840','0');
INSERT INTO [t11385] VALUES (4364,'عباد بن عباد المهلبي، عن عبيد الله بن عمر، عن نافع، عن ابن عمر','11257','3840','0');
INSERT INTO [t11385] VALUES (4365,'عبد الله بن إدريس الأودي، عن عبيد الله بن عمر، عن نافع، عن ابن عمر','11260','3840','0');
INSERT INTO [t11385] VALUES (4366,'عبد الله بن رجاء المكي، عن عبيد الله بن عمر، عن نافع، عن ابن عمر','11266','3840','0');
INSERT INTO [t11385] VALUES (4367,'عبد الله بن المبارك المروزي، عن عبيد الله بن عمر، عن نافع، عن ابن عمر','11270','3840','0');
INSERT INTO [t11385] VALUES (4368,'وبه في المزارعة (والحرث ١١) : أن رسول الله صلى الله عليه وسلم أعطى خيبر اليهود على أن يعملوها ويزرعوها، ولهم شطر ما خرج منها.','11273','3840','0');
INSERT INTO [t11385] VALUES (4369,'وبه في النذور (والأيمان ٢٩) : أن عمر قال: يا رسول الله! إني نذرت في الجاهلية أن أعتكف ليلة في المسجد الحرام، قال: أوف بنذرك.','11274','3840','0');
INSERT INTO [t11385] VALUES (4370,'عبد الله بن نمير الهمداني الكوفي، عن عبيد الله بن عمر، عن نافع، عن ابن عمر','11280','3840','0');
INSERT INTO [t11385] VALUES (4371,'وبه فيه (الصلاة ٩٥: ٨) صلاة الرجل في الجماعة تزيد على صلاته وحده بضعا وعشرين درجة. وفي حديث أبي بكر: سبعا وعشرين.','11304','3840','0');
INSERT INTO [t11385] VALUES (4372,'وبه فيه (الصلاة ٧٠: ٢) من أكل من هذه البقلة فلا يقربن مساجدنا حتى يذهب ريحها - يعني الثوم.','11305','3840','0');
INSERT INTO [t11385] VALUES (4373,'وبه في الزكاة (٥: ٢) : فرض رسول الله صلى الله عليه وسلم زكاة الفطر صاعا من تمر أو صاعا من شعير، على كل حر أو عبد، صغير أو كبير.','11306','3840','0');
INSERT INTO [t11385] VALUES (4374,'وبه في الصوم (١١: ٢) : أن رسول الله صلى الله عليه وسلم واصل في رمضان، فواصل الناس فنهاهم. . . الحديث.','11307','3840','0');
INSERT INTO [t11385] VALUES (4375,'وبه فيه (الصوم ١٩: ٦) : أن أهل الجاهلية كانوا يصومون يوم عاشوراء. وأن رسول الله صلى الله عليه وسلم صامه والمسلمون قبل] أن [يفترض رمضان. . . الحديث.','11308','3840','0');
INSERT INTO [t11385] VALUES (4376,'وبه في الحج (٣٧: ١) أن رسول الله صلى الله عليه وسلم كان يخرج من طريق الشجرة، ويدخل من طريق المعرس، وإذا دخل مكة دخل من الثنية العلياء، ويخرج من الثنية السفلى.','11309','3840','0');
INSERT INTO [t11385] VALUES (4377,'وبه فيه (الحج ٣٩: ١) : أن رسول الله صلى الله عليه وسلم كان إذا طاف بالبيت الطواف الأول، خب ثلاثا ومشى أربعا، وكان يسعى ببطن المسيل إذا طاف بين الصفا والمروة - وكان ابن عمر يفعل ذلك.','11310','3840','0');
INSERT INTO [t11385] VALUES (4378,'وبه فيه (الحج ٧٤: ٢) لا تسافر المرأة ثلاثة أيام - وفي حديث أبي بكر: فوق ثلاث - إلا ومعها ذو محرم.','11311','3840','0');
INSERT INTO [t11385] VALUES (4379,'وبه في العتق (؟) والنذور (والأيمان ١٦: ٢) إن العبد إذا نصح لسيده وأحسن عبادة ربه فله أجره مرتين.','11312','3840','0');
INSERT INTO [t11385] VALUES (4380,'وبه في المغازي (٧٩: ٢) الخيل في نواصيها الخير إلى يوم القيامة.','11313','3840','0');
INSERT INTO [t11385] VALUES (4381,'وبه في الاستئذان (٢٥: ٢) لا يتناجى اثنان دون الثالث. . . الحديث.','11314','3840','0');
INSERT INTO [t11385] VALUES (4382,'وبه في الصلاة (١١١: ٢) عن ابن عمر: أنه نادى بالصلاة في ليلة ذات برد وريح، فقال في آخر ندائه: ألا! صلوا في رحالكم. قال: إن رسول الله صلى الله عليه وسلم كان يأمر المؤذن إذا كانت ليلة باردة أو ذات مطر في السفر، أن يقول: ألا! صلوا في رحالكم.','11316','3840','0');
INSERT INTO [t11385] VALUES (4383,'وبه فيه (الصلاة ١١٢: ١) : أن رسول الله صلى الله عليه وسلم كان يصلي سبحته حيثما توجهت به ناقته.','11317','3840','0');
INSERT INTO [t11385] VALUES (4384,'وبه فيه (الصلاة ٣٠: ٣) لا تمنعوا إماء الله مساجد الله.','11318','3840','0');
INSERT INTO [t11385] VALUES (4385,'وبه فيه (الصلاة ١٢٨: ٨) اجعلوا آخر صلاتكم بالليل وترا.','11319','3840','0');
INSERT INTO [t11385] VALUES (4386,'وبه فيه (الصلاة ٦٩: ٤) إذا وضع عشاء أحدكم وأقيمت الصلاة فابدؤا بالعشاء. . . الحديث.','11320','3840','0');
INSERT INTO [t11385] VALUES (4387,'وبه فيه (الصلاة ١٤٠: ٤) إنما مثل صاحب القرآن كمثل الإبل المعقلة. . . الحديث.','11321','3840','0');
INSERT INTO [t11385] VALUES (4388,'وبه في الصوم (٢: ٣) : أن رسول الله صلى الله عليه وسلم ذكر رمضان، فقال: الشهر هكذا وهكذا. . . الحديث.','11322','3840','0');
INSERT INTO [t11385] VALUES (4389,'وبه في الحج (٢٦: ٣) : أراد ابن عمر الحج حين نزل الحجاج بابن الزبير. . . الحديث. وفي آخره: وكان يقول: من جمع الحج والعمرة كفاه طواف واحد، ولم يحل حتى يحل منهما جميعا.','11323','3840','0');
INSERT INTO [t11385] VALUES (4390,'وبه في الطلاق (١: ٣) : طلقت امرأتي على عهد رسول الله صلى الله عليه وسلم - وهي حائض - فذكر ذلك عمر لرسول الله صلى الله عليه وسلم، فقال: مره فليراجعها. . . الحديث.','11324','3840','0');
INSERT INTO [t11385] VALUES (4391,'وبه في اللعان (١: ١٠) : لاعن رسول الله صلى الله عليه وسلم بين رجل من الأنصار وامرأته، وفرق بينهما.','11325','3840','0');
INSERT INTO [t11385] VALUES (4392,'وبه في البيوع (٢٢: ٣) : أن رسول الله صلى الله عليه وسلم عامل أهل خيبر بشطر ما يخرج منها من زرع أو ثمر. . . الحديث.','11326','3840','0');
INSERT INTO [t11385] VALUES (4393,'وبه فيه (البيوع ٥: ١) : أن رسول الله صلى الله عليه وسلم نهى أن تتلقى السلع حتى تبلغ الأسواق.','11327','3840','0');
INSERT INTO [t11385] VALUES (4394,'وبه فيه (البيوع ١٣: ٢) عن النبي صلى الله عليه وسلم: أنه نهى عن بيع الثمرة حتى يبدو صلاحها، نهى البائع والمبتاع.','11328','3840','0');
INSERT INTO [t11385] VALUES (4395,'وبه فيه (البيوع ١٠: ٢) البيعان بالخيار ما لم يتفرقا.','11329','3840','0');
INSERT INTO [t11385] VALUES (4396,'وبه فيه (البيوع ١٥: ٢) أيما نخل اشترى أصولها - وقد أبرت - فإن ثمرها الذي أبرها، إلا أن يشترط الذي اشتراها.','11330','3840','0');
INSERT INTO [t11385] VALUES (4397,'وبه في الفرائض (لا، بل في الهبات ١: ٦) أن عمر حمل على فرس في سبيل الله. . . الحديث.','11331','3840','0');
INSERT INTO [t11385] VALUES (4398,'وبه في العتق (١: ٢) والنذرو (والأيمان ١٧: ٢) من أعتق شركا له في مملوك، فعليه عتقه كله إن كان له مال يبلغ ثمنه. . . الحديث.','11332','3840','0');
INSERT INTO [t11385] VALUES (4399,'وبه فيه (الأيمان والنذور ٦: ٥) عن رسول الله صلى الله عليه وسلم أنه أدرك عمر بن الخطاب في ركب يحلف بأبيه. . . الحديث.','11333','3840','0');
INSERT INTO [t11385] VALUES (4400,'وبه في الحدود (١٢: ١٠) : أن رسول الله صلى الله عليه وسلم قطع في مجن ثمنه ثلاثة دراهم.','11334','3840','0');
INSERT INTO [t11385] VALUES (4401,'وبه في القضاء (لا، بل في اللقطة ٣: ٢) لا يحلبن أحد ماشية أحد إلا بإذنه. . . الحديث.','11335','3840','0');
INSERT INTO [t11385] VALUES (4402,'وبه في المغازي (٥٨: ٥) كلكم راع وكلكم مسئول عن رعيته. . . الحديث.','11336','3840','0');
INSERT INTO [t11385] VALUES (4403,'وبه في (المغازي ٦١: ١٩) على المرء المسلم السمع والطاعة فيما أحب وكره، إلا أن يؤمر بمعصية. . . الحديث.','11337','3840','0');
INSERT INTO [t11385] VALUES (4404,'وبه فيه (المغازي ٦: ١) إذا جمع الله الأولين والآخرين يوم القيامة، يرفع لكل غادر لواء، فقيل: هذه غدرة فلان بن فلان.','11338','3840','0');
INSERT INTO [t11385] VALUES (4405,'وبه فيه (المغازي ١٩: ٢) : أن رسول الله صلى الله عليه وسلم قسم للفرس سهمين وللراجل سهما.','11339','3840','0');
INSERT INTO [t11385] VALUES (4406,'وبه في الذبائح (والصيد ٧: ٣) سأل رجل رسول الله صلى الله عليه وسلم - وهو على المنبر عن أكل الضب؟ فقال: لا آكله ولا أحرمه.','11340','3840','0');
INSERT INTO [t11385] VALUES (4407,'وبه في الأشربة (٦: ٢٣) : أن رسول الله صلى الله عليه وسلم خطب الناس فأقبلت نحوه، فانصرف قبل أن أبلغه، فسألت ماذا قال؟ فقالوا: نهى أن ينبذ في الدباء والمزفت.','11341','3840','0');
INSERT INTO [t11385] VALUES (4408,'وبه في اللباس (٢٥: ٢٤) إن الذين يصنعون هذه الصور يعذبون يوم القيامة، يقال لهم: أحيوا ما خلقتم.','11342','3840','0');
INSERT INTO [t11385] VALUES (4409,'وبه في الفضائل (٩: ١٩) إن أمامكم حوضا كما بين جرباء وأذرح. قال عبيد الله: فسألته؟ فقال: قريتين بالشأم، بينهما مسيرة ثلاث ليال.','11343','3840','0');
