INSERT INTO [t11385] VALUES (7700,'وبه في الجمعة (الصلاة - ٣١٨ - مطول، وفي التوحيد - ٣٥: ٥ - وفي الطهارة - ٧٢ - جميعا مختصر) نحن الآخرون السابقون يوم القيامة، بيد أنهم. . . الحديث.','20064','7481','0');
INSERT INTO [t11385] VALUES (7701,'وبه في علامات النبوة (المناقب ٢٥: ٧) لا تقوم الساعة حتى تقاتلوا قوما نعالهم الشعر، وحتى تقاتلوا الترك صغار الأعين، وتجدون من خير الناس أشدهم كراهية لهذا الأمر، والناس معادن، وليأتين على أحدكم زمان لأن يرأني أحب إليه. . . الحديث.','20066','7481','0');
INSERT INTO [t11385] VALUES (7702,'وبه في الفتن (٢٦: ٢) لا تقوم الساعة حتى تقتتل فئتان عظيمتان دعواهما واحدة، وحتى يبعث دجالون كذابون، وحتى يقبض العلم، وتظهر الزلازل (ويتقارب الزمان، ويكثر الهرج - وهو القتل -) ، وحتى يكثر فيكم المال فيفيض، وحتى يتطاول الناس في البنيان، وحتى يمر','20067','7481','0');
INSERT INTO [t11385] VALUES (7703,'وبه في الرقاق (٤٠) لا تقوم الساعة حتى تطلع الشمس من مغربها، ولتقومن الساعة وقد نشر الرجلان ثوبهما. كذلك (ح ١٣٧٤٧) .','20069','7481','0');
INSERT INTO [t11385] VALUES (7704,'وبه في الزكاة (٨: ٢) لا تقوم الساعة حتى يكثر فيكم المال فيفيض. كذلك (ح ١٣٧٤٧) .','20070','7481','0');
INSERT INTO [t11385] VALUES (7705,'وبه فيه (الزكاة ٢٨: ١) مثل البخيل والمنفق كمثل رجلين عليهما جنتان. . . الحديث.','20071','7481','0');
INSERT INTO [t11385] VALUES (7706,'وبه في النكاح (١٢) خير نساء ركبن الإبل نساء قريش. . . الحديث.','20073','7481','0');
INSERT INTO [t11385] VALUES (7707,'وبه في الأشربة (١٢: ٦) نعم اللقحة الصفى منحة والشاة الصفى منحة تغدو. . . الحديث.','20074','7481','0');
INSERT INTO [t11385] VALUES (7708,'وبه في الجهاد (٩٩) : قدم الطفيل بن عمرو الدوسي (وأصحابه) على النبي صلى الله عليه وسلم فقالوا: إن دوسا قد عصت. . . الحديث.','20075','7481','0');
INSERT INTO [t11385] VALUES (7709,'وبه في المغازي (٤٩: ٤) منزلنا - إن شاء الله - إذا فتح الله الخيف حيث تقاسموا على الكفر. . . الحديث٣.','20076','7481','0');
INSERT INTO [t11385] VALUES (7710,'وبه فيه (المغازي ٧٥: ٧) أتاكم أهل اليمن أضعف قلوبا. . . الحديث.','20077','7481','0');
