INSERT INTO [t11385] VALUES (7712,'وبه في النذور (والأيمان ٢٦: ٣) لا يأتي ابن آدم النذر بشيء لم أكن قدرته له. . . الحديث.','20079','7481','0');
INSERT INTO [t11385] VALUES (7713,'وبه في الديات (١٥: ١) لو اطلع رجل في بيتك ولم تأذن له] فخذفته [. . . الحديث.','20080','7481','0');
INSERT INTO [t11385] VALUES (7714,'وبه في الأدب (١١٤: ١) أخنى الأسماء عند الله يوم القيامة رجل تسمى ملك الأملاك. (قال د فيه - الأدب ٧٠: ١٠ تعليقا -: رواه شعيب بن أبي حمزة، عن أبي الزناد بلفظ أخنى) .','20081','7481','0');
INSERT INTO [t11385] VALUES (7715,'وبه فيه (الأدب؟) وفي صفة الجنة (٨: ٧) أول زمرة تدخل الجنة على صورة القمر. . . الحديث.','20082','7481','0');
INSERT INTO [t11385] VALUES (7716,'وبه في صفة الجنة (٥١: ٢١) لا يدخل أحدكم الجنة إلا أري مقعده من النار لو أساء، ليزداد شكرا. . . الحديث.','20083','7481','0');
