INSERT INTO [t11385] VALUES (7730,'وبه (في التمني ٩: ٧) لولا الهجرة لكنت أمرا من الأنصار، ولو سلك الناس واديا. . . الحديث. ذكره خلف وحده، قال أبو القاسم: لم أجده، ولا ذكره أبو مسعود.','20097','7481','0');
INSERT INTO [t11385] VALUES (7731,'وبه (؟) إني لأطمع أن يكون حوضي - إن شاء الله - أوسع مما أيلة إلى الكعبة. ذكره يحيى بن منده فيما استدركه على أبي مسعود في الأطراف.','20098','7481','0');
