INSERT INTO [t11385] VALUES (8180,'وبه في الطهارة (٢٦: ٣) إذا استيقظ أحدكم من نومه فلا يغمس يده في الإناء حتى يغسلها ثلاثا. . . الحديث.','21384','7481','0');
