INSERT INTO [t11385] VALUES (9402,'جد حجاج بن أبي منيع، عن الزهري، عن عروة، عن عائشة - واسمه عبيد الله بن أبي زياد الرصافي الشأمي في الحديث الثاني من ترجمة الأوزاعي، عن الزهري، عن عروة، عن عائشة - (ح ١٦٥١٢) .','24373','8999','0');
