INSERT INTO [t11385] VALUES (9423,'حديث إن الله وملائكته يصلون على الذين يصلون الصفوف ومن سد فرجة رفعه الله بها درجة. ق في الصلاة (٨١: ٤) عن هشام بن عمار، عن إسماعيل بن عياش به.','24401','8999','0');
